INSERT INTO `feedback` (`ID`, `userID`, `name`, `rank`, `type`, `feedback`, `date`, `isActive`, `isRead`, `imageSrc`) VALUES
(1, 1, '方同學', 'student', 'loan', NULL, '2020-01-01', 'on', '1', '/upload/2019/11/學生_-1.jpg'),
(3, 1, '宋同學', 'student', 'loan', '有比較過其他平台，普匯不管是在審核，或到最後的放款，我覺得跟其他平台相對來說是最快的，平台的手續費也收最低，真的是很仁慈的一件事!', '2020-01-01', 'on', '1', '/upload/2019/11/學生_2.jpg'),
(4, 1, '洪同學', 'student', 'loan', '這次借款是為了繳交校外的房租，看到廣告就決定來申請看看，我覺得對於學生非常方便也輕鬆還，謝謝有普匯這個平台。', '2020-01-01', 'on', '1', '/upload/2019/11/學生_3.jpg'),
(6, 1, '康同學', 'student', 'loan', '先前有比較過各個P2P借貸，覺得普匯不僅撥款速度快，使用介面及流程也很清楚好用，尤其有貼心又專業的客服可以詢問，謝謝普匯的貼心服務。', '2020-01-01', 'on', '1', '/upload/2019/11/學生_4.jpg'),
(7, 1, '羅同學', 'student', 'loan', '我覺得平台對於像我這種臨時急需用錢的人很方便，而且不用面對太多的人，整個借款流程讓我很放心。', '2020-01-01', 'on', '1', '/upload/2019/11/學生_5.jpg'),
(9, 1, '王小姐', 'officeWorker', 'loan', '謝謝普匯和投資人，讓我即時取得貸款解決急需,對於不方便上班跑銀行的人真的很便利,過程迅速,也不會有人知道,還款方便，對於急需用錢的人來說是一大福音。', '2020-01-01', 'on', '1', '/upload/2019/11/上班族_1.jpg'),
(10, 1, '李小姐', 'officeWorker', 'loan', '畢業不到一年的新鮮人，在銀行根本貸不到款，到台北工作生活大不易,普匯讓我貸到足夠的錢安置生活,很感謝普匯。', '2020-01-01', 'on', '1', '/upload/2019/11/上班族_2.jpg'),
(11, 1, '張先生', 'officeWorker', 'loan', '為了給老婆一個完美的婚禮，需要多點錢才能應付得來,在Dcard看到大家推薦普匯,就來試試看,真的很快就拿到款項了!美好的婚禮讓我終身難忘~!', '2020-01-01', 'on', '1', '/upload/2019/11/上班族_3.jpg'),
(12, 1, '古小姐', 'officeWorker', 'loan', '在銀行工作大概三年，一直有出國留學的夢想。銀行雖然有優惠利率,但時間來不及申請,且同事嘴雜也很煩人,用普匯隱私沒人知道又快,真的很便利又好用~以後銀行員要失業了O-O', '2020-01-01', 'on', '1', '/upload/2019/11/上班族_4.jpg'),
(13, 1, '趙小姐', 'officeWorker', 'loan', '機車故障維修費需現金,等薪水下來,最近的交通問題很麻煩,到普匯申請雖然整理需要的資料花了一兩天,但上傳成功到撥款一天就完成,真的太厲害了!以後有需求還會使用。', '2020-01-01', 'on', '1', '/upload/2019/11/上班族_5.jpg'),
(16, 82, '謝先生', 'officeWorker', 'invest', '投資真是太棒拉', '2020-11-17', 'off', '1', 'images/officer_m.png');
