INSERT INTO `interview` (`ID`, `type`, `post_title`, `feedback`, `video_link`, `category`, `post_modified`, `rank`, `imageSrc`, `isRead`, `isActive`, `amount`, `rate`, `period_range`, `spend_day`) VALUES
(1, '【投資人專訪】', '台北投資顧問 葉先生', '普匯跟其他保本型的商品比起來，投報率真的相對較高，也比較不需要花太多時間去關注，就可以獲得被動收入。', 'https://www.youtube.com/embed/6DbX2r2cR-o', 'invest', '2021-07-05 02:08:38', 'officeWorker', NULL, '0', 'on', '0', '0', '0', '0'),
(2, '【投資人專訪】', '台北科技業 劉先生', '在普匯投資除了可以有穩定的收益外，還可以幫助到有夢的大學生，所以覺得這個平台還滿特別的', 'https://www.youtube.com/embed/4RqbVmH6aHU', 'invest', '2020-12-09 06:59:42', 'officeWorker', NULL, '1', 'on', '0', '0', '0', '0'),
(3, '【投資人專訪】', '台北設計業 陳先生', '有比較過其他平台，其他的債權金額都比較大，普匯可以小額投資，還可以進行債權轉讓，讓我可以即時的拿回投資的錢', 'https://www.youtube.com/embed/we16JV8Hc1o', 'invest', '2020-12-09 06:59:42', 'officeWorker', NULL, '1', 'on', '0', '0', '0', '0'),
(4, '【投資人專訪】', '台北服飾業 何小姐', '在普匯上投資，平均都可以獲得12%的年化報酬率 和以往投資的經驗比較起來， 報酬率跟風險管控都更加穩健！ 否則平常上班也無法一直盯盤注意市場變化', 'https://www.youtube.com/embed/tsd-YTxzRyI', 'invest', '2020-12-09 06:59:41', 'officeWorker', NULL, '1', 'on', '0', '0', '0', '0'),
(5, '【借款人專訪】', '公部門 陳先生', '整個貸款辦理過程都很快速，對於整個流程都很滿意，加上每個程序在完成之後，都會有訊息即時跳出來做提醒。從認證資料的認證上傳，到資料的更新，整個過程都蠻快的，所以我覺得用AI做這些文件然後檔案處理是很方便的。', 'https://www.youtube.com/embed/VTJB7c1fS-4', 'loan', '2020-12-30 07:54:03', 'officeWorker', NULL, '1', 'on', '0', '0', '0', '0'),
(6, '【借款人專訪】', '銀行員 林小姐', '普匯真的是一個快速、簡單又方便的平台，真的很好用而且也很快 ! 如果我之後還有這類需求的話，一定還會再來使用。同時也想介紹給我身邊無法在銀行申請貸款的朋友們', 'https://www.youtube.com/embed/YkZyp5iSgxs', 'loan', '2020-12-30 07:54:05', 'officeWorker', NULL, '1', 'on', '0', '0', '0', '0'),
(7, '【借款人專訪】', '陳同學', '在學校看到出國遊學的計畫，很想要參加又不想花到家裡的錢，很謝謝有普匯這個平台，讓我快速又方便的申請到。', 'https://www.youtube.com/embed/M70n4r87KZE', 'loan', '2021-02-03 00:00:00', 'officeWorker', NULL, '1', 'off', '0', '0', '0', '0'),
(8, '【借款人專訪】', '方同學', '有比較過其他的平台， 普匯不管是在審核，或到最後的放款 我覺得跟其他平台相對來說是最快的， 平台的手續費也收最低，真的是很仁慈的一件事， 真的很棒，很適合學生', 'https://www.youtube.com/embed/6DbX2r2cR-o', 'loan', '2020-12-30 07:53:28', 'student', NULL, '1', 'on', '0', '0', '0', '0'),
(9, 'loan', '方同學', '覺得普匯是一個很安心的平台，個資都很安全，不會有其他人打電話來，也不用讓學生冒著生命危險，很像是海裡面找到一塊浮木吧!', NULL, 'loan', '2021-01-13 00:00:00', 'student', '/upload/2019/11/學生_-1.jpg', '1', 'on', '0', '0', '0', '0'),
(10, '【借款人專訪】', '宋同學', '有比較過其他平台，普匯不管是在審核，或到最後的放款，我覺得跟其他平台相對來說是最快的，平台的手續費也收最低，真的是很仁慈的一件事!', NULL, 'loan', '2020-12-09 06:59:35', 'student', '/upload/2019/11/學生_2.jpg', '1', 'on', '0', '0', '0', '0'),
(11, '【借款人專訪】', '洪同學', '這次借款是為了繳交校外的房租，看到廣告就決定來申請看看，我覺得對於學生非常方便也輕鬆還，謝謝有普匯這個平台。', NULL, 'loan', '2020-12-09 06:59:35', 'student', '/upload/2019/11/學生_3.jpg', '1', 'on', '0', '0', '0', '0'),
(12, '【借款人專訪】', '康同學', '先前有比較過各個P2P借貸，覺得普匯不僅撥款速度快，使用介面及流程也很清楚好用，尤其有貼心又專業的客服可以詢問，謝謝普匯的貼心服務。', NULL, 'loan', '2020-12-09 06:59:34', 'student', '/upload/2019/11/學生_4.jpg', '1', 'on', '0', '0', '0', '0'),
(13, '【借款人專訪】', '羅同學', '我覺得平台對於像我這種臨時急需用錢的人很方便，而且不用面對太多的人，整個借款流程讓我很放心。', NULL, 'loan', '2020-12-09 06:59:34', 'student', '/upload/2019/11/學生_5.jpg', '1', 'on', '0', '0', '0', '0'),
(14, '【借款人專訪】', '王小姐', '謝謝普匯和投資人，讓我即時取得貸款解決急需,對於不方便上班跑銀行的人真的很便利,過程迅速,也不會有人知道,還款方便，對於急需用錢的人來說是一大福音。', NULL, 'loan', '2020-12-09 06:59:33', 'officeWorker', '/upload/2019/11/上班族_1.jpg', '1', 'on', '0', '0', '0', '0'),
(15, '【借款人專訪】', '李小姐', '畢業不到一年的新鮮人，在銀行根本貸不到款，到台北工作生活大不易,普匯讓我貸到足夠的錢安置生活,很感謝普匯。', NULL, 'loan', '2020-12-09 06:59:33', 'officeWorker', '/upload/2019/11/上班族_2.jpg', '1', 'on', '0', '0', '0', '0'),
(16, '【借款人專訪】', '張先生', '為了給老婆一個完美的婚禮，需要多點錢才能應付得來,在Dcard看到大家推薦普匯,就來試試看,真的很快就拿到款項了!美好的婚禮讓我終身難忘~!', NULL, 'loan', '2020-12-09 06:59:32', 'officeWorker', '/upload/2019/11/上班族_3.jpg', '1', 'on', '0', '0', '0', '0'),
(17, '【借款人專訪】', '古小姐', '在銀行工作大概三年，一直有出國留學的夢想。銀行雖然有優惠利率,但時間來不及申請,且同事嘴雜也很煩人,用普匯隱私沒人知道又快,真的很便利又好用~以後銀行員要失業了O-O', NULL, 'loan', '2020-12-09 06:59:31', 'officeWorker', '/upload/2019/11/上班族_4.jpg', '1', 'on', '0', '0', '0', '0'),
(18, '【借款人專訪】', '趙小姐', '機車故障維修費需現金,等薪水下來,最近的交通問題很麻煩,到普匯申請雖然整理需要的資料花了一兩天,但上傳成功到撥款一天就完成,真的太厲害了!以後有需求還會使用。', NULL, 'loan', '2020-12-09 06:59:31', 'officeWorker', '/upload/2019/11/上班族_5.jpg', '1', 'on', '0', '0', '0', '0');
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(1760, 1, NULL, NULL, '<p>上次跟大家介紹了K線圖不知道大家對股市是否多了些興趣呢？今天我們要介紹的是成交量，成交量常和K線圖搭配使用，也是新手必學的常識之一喔！</p><p> </p><h3>成交量介紹</h3><p>成交量即為VOL，代表當日所有交易的成交張數，其中一張相當於1000股。通常我們會用紅綠柱狀條來表示成交量，當日收盤價為漲幅則呈現紅色，若為跌幅則呈現綠色，平盤也為紅色。(顏色根據不同券商的app可能會有些許差異，平盤有些券商會額外選定顏色來表示)</p><p>那麼成交量和股價又有什麼關聯呢？通常若多方和空方對股票的看法一致，成交量少，股價便不會有太大波動，反之多空雙方看法出現歧異時，一方大量買進一方大量賣出，那麼股價的波動就會比較激烈。特別注意，每支個股的成交量差異很大，對中小型個股來說，一天成交量達到3000張就可以稱之為大量，但對大型個股來說，一天成交量要達到50000張才能說是大量，投資前可以看看股票的歷史成交量來判斷到底要成交多少量才算是大量喔！</p><h3>範例講解</h3><p>接下來我們會介紹幾個K線圖和VOL配合的關係圖提供大家初步判斷量價關係的方式，那麼就讓我們開始吧！</p><p>在開始前，我們先來看一下途中包含了哪些資訊。圖的上半部是K線圖，下半部則是該股的量。這支股票是聯電(2303)，屬於每日成交量都有萬張以上的大型股(股本100億元以上，股票在外流通張數多)，在這段觀察時間內它的量在8萬以下我們會稱為低量，超過17萬便會稱為大量。</p><p> </p><h3><img src=\"https://www.influxfin.com/upload/2020/05/1-1.jpg\" alt=\"VOL\" width=\"653\" height=\"320\" /></h3><h3>1.低量低價</h3><p>低量低價表示該股的成交量和價格皆處於低檔，這個時候股價不會有太大的波動，進入「盤整」的階段，通常會出現在股價從高檔跌落後，可用於判斷該股特定時間的底價。</p><p>低量低價的出現，只是說明股價階段性底部形成的可能性大大增強，而不能作為買入股票的依據。投資者還應在研究該股基本面是否良好、否具有投資價值等情況後，才能做出投資決策。</p><h3>2.量縮價跌</h3><p>量縮價跌表示個股在成交量減少的同時股價也同步下跌。量縮價跌現象既可以出現在下跌行情的中期，也可能出現在上升行情的中期，但它們的研判過程和結果是不一樣。</p><p>在下跌行情中，當股價開始從高檔下跌後，由於市場預期不佳，一些獲利投資者紛紛出逃，而大多數投資者選擇觀望，市場承接乏力，因此造成股價下跌、成交萎縮的量縮價跌現象。這種量縮價跌現象的出現，預示著股價仍將繼續下跌。下跌行情中的量縮價跌，表明投資者在出貨以後不再做「空頭回補」，股價還將維持下跌方向，投資者應以觀望為主。</p><p>在上升行情中，當股價上升到一定高度時，市場成交量開始減少，股價也隨之小幅下跌，呈現出一種量縮價跌現象，而這種量縮價跌是對前期上升行情的一個主動調整過程，股價在此時的跌幅不會過深，投資者也會因此產生惜售心理而繼續持有個股，造成量縮價跌的現象，但在整理完成後價格便會繼續向上，特別注意若在量縮價跌時跌價太多，有可能是主力預備拋售的資訊，投資人因審慎評估再選擇要逢低加碼或拋售個股。</p><p>除了上述兩種量價關係外還有不少量價關係可供投資人參考，這裡我們稍微提一下量價背離，所謂量價背離分為兩種，分別是：</p><p>1.價升量減，被認為是下跌的前兆。</p><p>2.價跌量增，但不被認為是上升的前兆。</p><p>造成這兩項差異的原因是因為股價在上升時需要量的支持，但在下跌時卻未必需要量能，這點投資朋友們能在股海中慢慢驗證喔！</p><p>量價背離進一步表明當前的量價關係與之前的量價關係發生了改變，一般量價背離會產生一種新的趨勢，但也可能只是上升中的調整或下跌中的反彈，要多加注意。</p><p> </p><p><img src=\"https://www.influxfin.com/upload/2020/05/2.jpg\" alt=\"VOL\" width=\"610\" height=\"299\" /></p><p> </p><h3>成交量與股價關係的深度剖析</h3><p>一隻底部成交量放大的股票就像在火箭在升空前必須要有充足的燃料一樣，必須具有充分的底部動力才能將股價推升到極高的地步。因此，一隻狂漲的股票必須在底部出現大的成交量，在上漲的初期成交量必須持續遞增、量價配合，主升段之後往往出現價漲量縮的所謂無量狂升的強勁走勢。<br /><br />從實際的圖可以看出，一隻會大漲的股票必須具備充足的底部動力才得以將股價推高。這裡所說的充足的巨量是相對過去的微量而言，也就是說，當一隻股票成交量極度萎縮後，再出現連續的大量才能將股價推高。成交量是衡量買壓和賣壓的工具，它能對股價的走向有所指引，因此，精明的投資人對於底部出現巨大成交量的股票必須追蹤，因為當一隻股票的供需關係發生極大變化時，將決定股價的走向，投資者絕對不可以忽略這種變化發生時股價與量的關係，一旦價量搭配起來，介入之後股價將必然如自己預期的那樣急速上揚。<br /><br />成交量的形態改變將是趨勢反轉的前兆。個股上漲初期，其成交量與股價的關係是價少量增，而成交量在不斷持續放大，股價也隨著成交量的放大而揚升;一旦進入強勢的主升段時，則可能出現無量狂升的情況;最後末升段的時候，出現量增價跌，量縮價升的背離走勢。一旦股價跌破十日均線，則顯示強勢已經改變，將進入中期整理的階段。因此，當你握有一隻強勢股的時候，最好是緊緊盯住股價日K線圖。在日K線一直保持在十日均線之上，可以一路持有;一旦股價以長印線或盤勢跌破十日均線，應立即出貨，考慮換股操作。<br /><br />盤整完成的股票要特別注意，理由是其機會大於風險。盤整的末期成交量為萎縮，代表拋盤力量的消竭。.基本上，量縮是一種反轉信號，量縮才有止跌的可能，下跌走勢中，成交量必須逐漸縮小才有反彈的機會。但是，量縮之後還可能再持續縮小，到底何時才是底部呢?只有等到量縮之後又是到量增的那一天才能確認底部。如果此時股價已經站在十日均線之上，就更能確認其漲勢已經開始了。所以,基本上我們應重視的角度是量縮之後的量增,只有量增才能反映出供求關係的改變,只有成交量增大才能使該股具有上升的底部動量。<br /><br /> 在盤局的尾段,股價走勢具有以下特徵</p><p>1.波動幅度逐漸縮小</p><p>2.量縮到極點</p><p>3.量縮之後是量增,突然有一天量大增,且盤出中陽線,突破股票盤局,股價站在10日均線之上</p><p>4.成交量持續放大,且收中陽線,加上離開底價三天為原則</p><p>5.突破之後,均線開始轉為多頭排列,而盤整期間均線疊合在一起</p><p> </p><h3>普匯金融專員私心推薦，買股看指標，買債看心情</h3><p>普匯金融專員：「從剛出社會接觸股市開始，學習基本面、技術面、籌碼面以來，長期投資報酬率增長說真的非常有限，散戶是有可能抓到機會跟上大盤漲勢的，但不會是把把都贏，一但被套牢就損失了大量的流動性，俗稱住套房，況且每日都要花很心思去研究各種指標，著實會花了大量的時間卻成效不彰。買債與買股其實是一體兩面的事情，大部份剛接觸金融市場的投資者都認為購買債權是一種風險低報酬率也低的事情，購買股票人們會研究公司的好壞並且有各種不同的評價方式，但購買債權只需要確認好違約風險即可而不用花太多心力去持續追蹤；普匯金融科技是所有P2P產業裡風控模組建立最好的一間公司，在疫情發生時著實也不太會受到影響，畢竟都是走全線上化的，根本不用面對面接觸到人，只要您認真研究一番的話，很容易就會發現，買債權真的只要看心情就好」。</p><h2><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">今天心情好！買個債權創造被動收入吧&gt;&gt;</a></h2>', '2018-12-07 14:47:31', '【普匯觀點】股市基本常識-成交量(VOL)', 'article', 'publish', '2020-05-14 12:13:33', '', 0),
(2467, 1, '/upload/2018/12/20181226.jpg', NULL, '最近大陸頻傳商家拒收人民幣的事件，以至於中國人民銀行近期進行大規模整治。為了要讓「拒收現金違法」觀念深植人心，中國人民銀行發布公告，該國的法定貨幣為人民幣，任何單位與個人不得以條款或聲明等方式拒收現金。不過，包括電子商務或自助服務等不具備現金收取條件的業務則能使用非現金支付工具。\r\n\r\n<hr />\r\n\r\n中國移動支付為何如此盛行?\r\n在大陸念書的小編有著深刻的體會~\r\n1.提款機實在是太遠啦!每次要找到提款機真的要花個20分鐘到市區。\r\n2.跨行提款的手續費偏貴，跨省的轉帳更是不便宜。\r\n3.人民幣面額最高是一百，帶人民幣一萬元就厚厚一大疊，實在是怕被偷。\r\n強大支付的背後隱憂從個人的層面來說，對於那些無法在中國開設銀行帳戶的遊客和出差人，生活變得更加麻煩，因為他們沒法把手機當錢包用。從企業上來說，外國企業和本地商業的日子過的也非常艱難。外國企業想要賣給中國遊客的物品如今必須在阿里巴巴或者騰訊上販賣，否則就要冒著無法支付的風險。\r\n\r\n<hr />\r\n\r\n台灣、中國支付環境的不同1. 超商、ATM覆蓋率\r\n台灣超商密度全球第二，僅次南韓，在超商店員的十八般武藝裡，就包含了各種繳費，例如：水電、瓦斯、信用卡、電話費等，台灣民眾皆可在距離不遠的超商完成，加上密度全球第一的ATM，讓台灣人無論日常繳費、領鈔都不成問題。\r\n2. 紙幣汙損、假鈔問題嚴重\r\n再來便是中國的紙幣汙損與嚴重的假鈔問題。在紙鈔與零錢的設計上不甚理想，民眾無論是在使用或攜帶上都不方便。\r\n3. 中國信用文化不牢固VS台灣人最愛信用卡若台灣行動支付的確「大大落後」中國，但中國也有遠遠不及台灣的一點，那就是台灣是個「信用社會」，中國的行動支付之所以迅速發展，是由於它的「後發優勢」，因為沒有牢固的信用卡文化，反而能快速地從現金支付跳躍至行動支付階段。\r\n\r\n<hr />\r\n\r\n結論\r\n記得小編第一次到大陸時，帶著現金在便利商店結帳時，可是被店員大大的白眼一番。提醒大家下次為了方便，可要先準備好行動支付在去大陸阿!應該商家也要提供多元化支付方式來保障消費者選擇的權利。大家選擇將流動資產放置在移動支付或是現金上，不妨考慮普匯的小額貸款投資，普匯提供債權轉讓的服務讓您的資金更能靈活的運用!', '2018-12-26 17:29:57', '【時事分享】中國行動支付取代人民幣?', 'article', 'publish', '2018-12-26 17:31:06', '', 0),
(2493, 1, '/upload/2018/12/20181227.jpg', NULL, '不理川普施壓，美國聯準會（Fed）下周將調升短期利率1碼， Fed同時看好美\r\n國經濟至少還能維持3年擴張榮景，未來3年內通膨率盤旋在2％附近。從2015年\r\n開始已經第八次升息，每次升息1碼(0.25%)，也是今年第四次升息了。\r\n\r\n<hr />\r\n\r\n川普反對升息\r\n川普對此相當不滿意，當被問到Fed下周升息一事時，他不客氣的說，我認為很\r\n愚蠢，但我能說什麼呢？川普強調，正當他與中國或甚至其他國家進行貿易戰\r\n之際，他需要有可以調降利率的彈性。他說：「各位必須了解，我們正在打貿\r\n易戰，而且勝利即將在望，不過在同時我也需要貨幣寬鬆。」\r\n\r\n<hr />\r\n\r\n那麼升息會帶來什麼影響呢?\r\n「息」指的是「基準利率」，也就是中央銀行對各個銀行存放款的利率。各國央行會做出「升息」的決策，通常表示對於經濟前景充滿信心，並預期可能出現通貨膨脹。為了調節市場，避免經濟過熱，適度降低民眾的投資跟消費，讓民眾將錢留存在銀行。而對於跟銀行有貸款的人來說，因為貸款利率提高，就必須開始考慮提早還款。主要好處可以控制通貨膨脹，抑制資產泡沫化以及景氣熱過頭的現象。但也有一些負面的效果就是市場上的錢會流向銀行儲蓄，導致經濟活動下降。貸款利率也會隨之提升。通常升息之後會伴隨著貨幣變得強勢，原因是世界的資金會往利率高的地方流動，造成出口貿易相對較不利。房市債市股市都會造成一定的影響。\r\n\r\n<hr />\r\n\r\n結論\r\n當美國升息，台灣會是正面受益者。原因是台灣主要是由出口產業帶動GDP成長，且MSCI台灣指數中，有六成都是出口為導向的科技產業類股。小編建議面對瞬息萬變的投資環境，需要聰明的做調整。美國銀行的活期存款也不到1.5%的年利率，明明Fed升息，美銀行業存款利率卻未調升。相對普匯債權投資有高額的利差，債權投資的報酬率更高達年利率15%以上。', '2018-12-27 11:34:55', '【時事分享】 川普反升息VS美國聯準會升息', 'article', 'publish', '2018-12-27 14:44:56', '', 0),
(2535, 1, '/upload/2018/09/S__41820218.jpg', NULL, '<p><b>人物介紹 &nbsp;Arthur Yao</b></p>\n<p><span style=\"font-weight: 400;\">「觀念、態度、努力，決定你的人生。」— 普匯金融科技 Founder/CEO</span></p>\n<p></p>\n<p><span style=\"font-weight: 400;\">Arthur Yao 姚木川，於兩岸金融機構服務超過二十年，曾任大眾（現併入元大）、台新、復華（今元大）、中租等集團之專業經理人，期間亦陸續投資服飾、餐飲、通訊、進出口、演藝、唱片、娛樂等產業，近年毅然決然卸下南粵銀行總經理的職位創業，放棄千萬年薪投入台灣金融科技發展，現為 普匯金融科技 CEO／亞設國際投資 CEO。</span></p>\n<p></p>\n<hr>\n<p></p>\n<p><b>Q：為什麼想創立普匯？</b></p>\n<p><span style=\"font-weight: 400;\">顧名思義，普匯希望能夠幫助更多銀行服務不到的客戶。</span></p>\n<p><span style=\"font-weight: 400;\">加上科技產業正在快速發展，結合本身豐富的金融歷練，決定投入 Fintech 創業，自我挑戰、讓自己活得更精彩！</span></p>\n<p></p>\n<hr>\n<p></p>\n<p><b>Q：對於台灣金融科技發展的看法？</b></p>\n<p><span style=\"font-weight: 400;\">台灣曾歷經數次金融風暴，應該有足夠的基礎與韌性發展 Fintech。如果能一改台灣金融產業長年累積的保守風氣，搭配法規上的調整，利用科技快速推進，台灣的 Fintech 會發展得很好。</span></p>\n<p></p>\n<hr>\n<p></p>\n<p><b>Q：為何在金融科技產業之中選擇了 P2P Lending？</b></p>\n<p><span style=\"font-weight: 400;\">由於過往在金融機構主要負責法金企業放款項目，不只對相關運作模式與規範有一定了解，更能確保普匯不存在吸金、觸法等問題。</span></p>\n<p><span style=\"font-weight: 400;\">另外，借貸衍生支付、支付衍生其他金融行為；未來也會繼續探尋 Fintech 中的其他可能性，只是目前要先打穩基礎，才能向上發展。</span></p>\n<p></p>\n<hr>\n<p></p>\n<p><b>Q：如何同時兼顧多項投資事業？</b></p>\n<p><span style=\"font-weight: 400;\">睡眠時間比別人短，維持正常的生活起居，養成運動習慣訓練體力。</span></p>\n<p><span style=\"font-weight: 400;\">懂得自我約束，搭配時間管理，就能達成目標。</span></p>\n<p></p>\n<hr>\n<p></p>\n<p><b>Q：擔任高階主管、甚至創業，需要什麼特質？</b></p>\n<p><span style=\"font-weight: 400;\">比別人更努力，比別人更細心，保持虛心不斷學習。</span></p>\n<p><span style=\"font-weight: 400;\">心態要正面積極、樂觀進取，做事要捨我其誰、當仁不讓。</span></p>\n<p></p>\n<hr>\n<p></p>\n<p><b>Q：給予想進入金融業的年輕人一些建議？</b></p>\n<p><span style=\"font-weight: 400;\">若想進入金融業，需要花額外的時間鑽研相關知識，加上產業特性，圈子較為封閉、狹小，因此一旦選擇了金融業，就要下定決心。</span></p>\n<p><span style=\"font-weight: 400;\">至於金融科技業，則得拋棄金融體制內的固有想法，若能畢業後直接進入 Fintech，也許可以學習得更快、更沒有包袱。</span></p>\n<p><span style=\"font-weight: 400;\">但總體來說，建議年輕人一定要有夢想、有野心，勇於挑戰、衝刺、冒險，在各行各業都會成功。</span></p>\n<p></p>\n<hr>\n<p></p>\n<p><span style=\"font-weight: 400;\">人物相關報導：</span></p>\n<p><a href=\"https://mp.weixin.qq.com/s/lwL2vhJvFAlKBIHWUHINIA\"><span style=\"font-weight: 400;\">https://mp.weixin.qq.com/s/lwL2vhJvFAlKBIHWUHINIA</span></a></p>\n<p></p>\n<p></p>\n\n<!-- wp:button {\"textColor\":\"very-light-gray\",\"customBackgroundColor\":\"#255069\"} -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link has-text-color has-very-light-gray-color has-background\" href=\"https://lend.influxfin.com/welcome\" style=\"background-color:#255069\">點我去投資</a></div>\n<!-- /wp:button -->', '2018-09-12 10:29:48', '【名人堂】普匯金融科技 CEO姚木川：觀念、態度、努力 決定你的人生', 'article', 'publish', '2019-04-26 12:06:46', '', 0),
(2541, 1, '/upload/2018/09/未命名-1.jpg', NULL, '<blockquote>\n<h6>「年輕人需要的是更多的資金管道、更多元化的理財工具，同時也應該具有正確的理財觀念。」— 普匯金融科技 CRO / Roger</h6>\n</blockquote>\n<p> Roger郭基永，過去在大陸金融機構服務多年，除了曾在大陸銀行業工作外，還到了民間金融公司(第二大房屋抵押貸公司)發展多年，工作內容就是作風險管控以及相關個案審核，對於大陸的金融體系具有相當的了解，近年毅然決然放下優渥的工作，與普匯金融CEO一同投入台灣金融科技業發展，現為普匯金融科技CRO。</p>\n<p>註：CRO — Chief Risk Officer，首席風險官，主要職責為擬定風險管理戰略、提出風險管理政策以及監控程序實施。</p>\n<p> </p>\n<hr />\n<p> </p>\n<p><strong>Q</strong><strong>：</strong> <strong>為什麼想加入普匯</strong><strong> ?</strong></p>\n<p>A： 起初在大陸工作時P2P正發展得相當火熱，在與現今普匯金融科技的CEO姚木川先生討論的過程中，我們認為台灣的金融體制其實是很成熟的，但是科技相較於大陸卻不是最先進的，加上台灣的法律發展較慢，許多科技發展相應的法規都還沒有很完善，金融海嘯過後態度多半轉向保守，民間能提供資金的管道不多導致Fintech的發展受限居多相當可惜。</p>\n<p>中國在這塊金融科技領域發展得相當快速，反觀台灣卻多半仍處於傳統銀行業的形式，但是我們認為台灣應該要有這樣的產業、平台，因此我們決定放下當時的工作返台進行創業，把這樣的科技應用帶回台灣。</p>\n<p> </p>\n<hr />\n<p> </p>\n<p><strong>Q</strong><strong>：在普匯工作的理念、初衷？</strong></p>\n<p>A：在與老闆創立普匯金融科技公司時，我們認為年輕人是最需要資金的一個族群，但是台灣目前除了銀行能進行借貸款外，鮮少有其他民間金融公司能提供小額貸款，因此在借貸款方面受限居多，所以我們希望建立一個平台來幫助更多年輕人，同時我們也致力於帶給年輕人正確的理財觀念，提供他們最完善的金融服務。</p>\n<p> </p>\n<hr />\n<p> </p>\n<p><strong>Q</strong><strong>：</strong><strong>對於普匯平台風險的管控，有什麼是能讓大眾了解的？</strong></p>\n<p>A：風險管控涉及於多方層面，要有一套完整的系統需要的是強大的邏輯設計。在平台上，人的真實性是需要謹慎評估的，對於反欺詐的制度更需要嚴謹的管控。</p>\n<p>傳統銀行在辨別系統上是使用身分證和帳戶號碼，但是P2P平台是除了個人基本證件外，另外再加上其他證件以及銀行帳戶進行交叉驗證，這樣子的驗證率可近乎是百分之百，中間並不會有甚麼錯誤產生。而風險管控機制就是根據個人的特質行為來計算信用分數，當個人給予平台越透明的資訊，分數就能在更短的時間內計算得越準確，平台上進行的是小額分散風險機制，最低額度可至一千元。</p>\n<p> </p>\n<hr />\n<p> </p>\n<p><strong>Q</strong><strong>：對於台灣</strong><strong>P2P</strong><strong>的發展有何看法</strong><strong> ? </strong><strong>未來可能有甚麼走向或風險存在？</strong></p>\n<p>A：現階段因為大陸P2P業者〝暴雷〞的影響導致許多投資借款人人心惶惶，但是會發生這樣的局面主要歸咎於大陸法規當時的不嚴謹，加上業者的不法商業行為才使P2P在大陸紛紛倒閉，但在洗盤之後發展應該漸趨好轉。</p>\n<p> </p>\n<hr />\n<p> </p>\n<p>反觀台灣在制定法規上是相當嚴格以及縝密的，雖然大家對於這個產業的信心相對較低，但這樣的科技平台是一種去仲介化平台，除了本身立意是良好外，還能提供許多投資人在除了傳統銀行外的交易平台上進行放款、小額投資，同時服務到更多銀行業服務不到的客戶，因此對於台灣之後的發展我認為是相當有空間及樂觀的！而民眾對於這樣子的平台存在是否能建立起相當的信賴感是未來需要面對的挑戰及課題。</p>\n<p> 延伸閱讀：【名人堂】普匯金融科技 CEO：觀念、態度、努力 決定你的人生</p>\n<p>https://www.pu-hey.com/%E3%80%90%E5%90%8D%E4%BA%BA%E5%A0%82%E3%80%91-ceo-%E8%A8%AA%E5%95%8F/</p>\n\n<!-- wp:button {\"textColor\":\"very-light-gray\",\"customBackgroundColor\":\"#104e69\"} -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link has-text-color has-very-light-gray-color has-background\" href=\"https://lend.influxfin.com/welcome\" style=\"background-color:#104e69\">點我去投資</a></div>\n<!-- /wp:button -->', '2018-09-26 10:58:37', '【名人堂】普匯CRO郭基永：年輕人需要的是更多的資金管道、更多元化的理財工具', 'article', 'publish', '2019-04-26 12:01:23', '', 0),
(2568, 1, '/upload/2018/12/未命名-1.jpg', NULL, '<p>大家好，前陣子看到在網路瘋狂轉發的文章「閨蜜慶祝10周年，兩年存下10萬元」，文章描述三人閨蜜為了慶祝友誼10周年，決定每一次的聚會各自掏出1000元，存進「夢想基金盒子」裡，由於其中一人本身比較會花錢，因此決定每次存2000元並都持續兩年，最後他們存到了將近10萬元！</p>\n<p>看完這篇文章我們認為這樣子的儲蓄法並不是聰明的理財方式，若今日你是單純的年輕學生或許這樣做是上策，但站在有一定資產上班族的角度來看勢必有更聰明的理財方法，讓錢不是只放在那慢慢累積！</p>\n<p> </p>\n<hr />\n<p> </p>\n<h2>√<strong> 首先，先以原本三人一般的存款法計算：</strong></h2>\n<p>每個月三人共$4000，固定兩年共24個月，4000x24=96,000</p>\n<p>沒錯，很簡單的儲蓄法累積下來兩年共有96,000元。</p>\n<p> </p>\n<p>但今日若選擇低風險的債權滾錢法有甚麼不一樣?</p>\n<p>股市風險大，光是投資就得花一大筆錢，放在銀行定存最保守但利率又是低的沒話說，再扣掉通貨膨脹你放的錢可能都被銀行默默吃掉了！</p>\n<p>今天利用小額投資法，將每月三人投資總額4000元投入普匯債權進行小額定期定額投資，優點有甚麼?</p>\n<ol>\n<li>根據借款人的信用評等不同，你投資的報酬率也會跟著有變化，普匯平台的報酬率在5%~20%不等。</li>\n<li>借款期限長短- 普匯平台可以自行選擇投資金額、期望報酬區間以及借款期限，借款期限越長，本利和複利的累積金額將越多，投資報酬率越高。</li>\n</ol>\n<p> </p>\n<hr />\n<p> </p>\n<h2><strong>√ 那麼來算算用普匯債權投資兩年能賺多少：</strong></h2>\n<p>若將三人每月投入總額4,000元定期每月投資持續兩年，將每個月的利息再投入，並以平台平均年利率16%來計算，所以到了第48期才能收完所有回款，因此我們計算第48期後金錢會增值多少來比較。</p>\n<p>(以下皆先不考慮平台1%手續費)</p>\n<p> </p>\n<table width=\"563\">\n<tbody>\n<tr>\n<td width=\"188\">金錢增值</td>\n<td width=\"188\">閨蜜儲蓄法</td>\n<td width=\"188\">普匯債權滾錢法</td>\n</tr>\n<tr>\n<td width=\"188\">第24期</td>\n<td width=\"188\">96,000</td>\n<td width=\"188\">0 (因持續將利息再投入)</td>\n</tr>\n<tr>\n<td width=\"188\">第25 ~ 48期</td>\n<td width=\"188\">維持96,000</td>\n<td width=\"188\">96,000 → 126,751</td>\n</tr>\n</tbody>\n</table>\n<p> </p>\n<p>你沒看錯，同樣都是$96000創造出來的價值卻如此不一樣！</p>\n<p>到了第48期後資產增值到了$126,751！成長了32%！！</p>\n<p> </p>\n<p>相信還是有不少人不懂複利意義，簡單告訴大家，第一期期初投入4000元，那麼第一期收的的本息回款是$355，但我們不拿回，選而加上每月再投入的$4000一同投進第二個月，依此類推錢滾錢持續兩年，這就是所謂「複利」「利滾利」的概念。現今很少上班族具備此種理財觀念，不斷用舊有保守的儲蓄觀便永遠也生不了「大錢」。</p>\n<p>或許有人也會質疑，我乖乖每個月存$4000毫無風險，若我選擇投資普匯，借款人逾期或違約怎麼辦?風險比乖乖存錢還高！這裡我們想說，目前平台違約率只接近快10%，若進行小額分散投資，你投給10個人的風險計算下來也就一個人「可能」逾期或違約，你仍然可收到另外九個人的利息，如此何樂而不為?</p>\n<p>若真有逾期違約狀況平台本身也都有保障，除了走法律保障程序平台也提供債權轉讓機制讓投資人不承擔風險的可能，可說是保本的投資理財法！</p>\n<p>一般儲蓄法沒有不好，但若能用更聰明的方法為自己累積錢財何不嘗試? 總體下來的資產遠遠超過一般儲蓄法！</p>\n<p>以上是我們的小小淺見~ 並沒有認為一般儲蓄法不好，最重要的還是依照自己的投資偏好來做最適選擇！希望大家錢滾錢~發大財！</p>\n<p> </p>\n\n<!-- wp:button {\"textColor\":\"very-light-gray\",\"customBackgroundColor\":\"#334b86\",\"align\":\"left\",\"className\":\"is-style-default\"} -->\n<div class=\"wp-block-button alignleft is-style-default\"><a class=\"wp-block-button__link has-text-color has-very-light-gray-color has-background\" href=\"https://lend.influxfin.com/welcome\" style=\"background-color:#334b86\"><strong>點我前往 >></strong></a></div>\n<!-- /wp:button -->\n\n<!-- wp:paragraph {\"align\":\"left\",\"textColor\":\"very-dark-gray\",\"customFontSize\":30} -->\n<p style=\"font-size:30px;text-align:left\" class=\"has-text-color has-very-dark-gray-color\"><strong> 開始我的聰明儲蓄法</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', '2018-12-28 15:43:58', '【時事分享】 想學閨密存錢法?網驚:還有普匯這招!', 'article', 'publish', '2019-01-09 12:25:37', '', 0),
(2585, 1, '/upload/2019/01/02.jpg', NULL, '每年的10月一到，就會聯想到全世界最瘋狂的節日-德國慕尼黑啤酒節！被譽為德國醉大盛事的啤酒節，不僅能在短短的17日內吸引將近600萬的觀光人潮，也創造了德國大量的消費，根據當地光觀事務局的統計，2015年慕尼黑啤酒節590萬的人潮，平均喝掉730萬公升的啤酒，也就是相當於3個游泳競賽標準池的水，是不是很驚人呢？\r\n\r\n一年一度的慕尼黑啤酒節對德國的文化、人文精神以及經濟發展上都有重大的影響，觀光產業、啤酒產業是最為主要的獲益者，隨著創新科技的發展，德國的百威啤酒也開始運用區塊鏈的技術拓展新的業務範圍，就讓我們來揭開德國慕尼黑啤酒節的神秘面紗，以及其中的科技運用新體驗吧！\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n<h2><strong>啤酒節的由來</strong></h2>\r\n「慕尼黑啤酒節」( Oktoberfest )，又稱「十月節」，當地人稱之為\" Wiesn\" ，是巴伐利亞語中「草地」的意思。這個節慶源自 1810 年 10 月 12 日，巴伐利亞王儲<u>路德威希一世</u>與<u>特雷希亞公主</u>的皇室婚禮，這場盛世婚禮在特雷希亞草地廣場舉行，並免費供應啤酒美食與國民同樂。\r\n\r\n德國的 9月、10 月正是大麥和啤酒花豐收的時節，辛苦的採收過程結束後，人們總是喜歡歡聚在一起唱歌、跳舞、喝酒，以表達對於豐收的喜悅。啤酒節中完整地保留了巴伐利亞的民間風采和習俗，慕尼黑政府更於 1995 年將慕尼黑啤酒節推向國際化，每年總吸引超過600萬名遊客共襄盛舉，慕尼黑也因此獲得了「啤酒之都」的美名。\r\n\r\n今年是第185屆的啤酒節，預計在9月22日舉行開幕，到10月7日為止為期17天，來自世界各地的觀光客會趁著這段期間到德國體驗這「最瘋狂的節日」，並到周邊進行觀光，替德國的經濟成長貢獻一份心力。\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n<h2><strong>啤酒節替德國帶來的經濟效益</strong></h2>\r\n節慶期間的經濟消費來源，我們主要分為「飲食」、「住宿」以及「入場券」三方面來看。\r\n\r\n【飲食】\r\n\r\n首先是啤酒以及肉類製品的消費，隨著德國經濟的發展，物價上升的同時也帶動啤酒價格的成長，但每年十月節期間的遊客數量卻不減反增，根據統計，在短短的17日內，啤酒以及肉雞的銷售量分別高達700多萬和上百多萬隻，而且數量正在逐年遞增當中！\r\n\r\n【住宿】\r\n\r\n除了飲食，來到慕尼黑當地的觀光客在狂歡後也需要歇息片刻，不管是主打美食的小餐館、湖畔的酒店，還是方便背包客住宿的青年旅館，在節慶期間總能看見不同膚色與臉孔的人們，也因為遊客的高消費和注重享受的生活態度，讓德國的商家鎖定市場上未被滿足的需求，在滿足消費者慾望的同時，也賺得金銀滿缽。\r\n\r\n【入場券】\r\n\r\n啤酒節設置的遊樂設施都需要購買入場券，從刺激逼真的地震、颶風與火山場景體驗，到適合親子同樂的大轉輪、旋轉木馬等娛樂設施，還有數不清的特色雜耍表演、大轉盤活動，點綴著整個遊樂園區。園區也有販售許多紀念品，像是巴伐利亞傳統服飾、手杖、精美卡片或是精緻的瓷器，這些看似價格不高的小商品，卻積累了相當可觀的消費量！\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n<h2><strong>啤酒產業在區塊鏈的技術應用</strong></h2>\r\n啤酒製造商安海斯-布希公司收購的百威英博公司參與了一個區塊鏈試驗，測試這項技術在全球航運中的應用。該公司是區塊鏈聯盟成員之一，其他聯盟成員還包括航運集裝箱公司APL、物流公司Kuehne+Nagel和一個未具名的歐洲海關組織。該聯盟在試驗Accenture諮詢公司開發的技術項目中，涉及到的公司使用了Accenture的解決方案，這些貨物都被送到了不同的管轄區，每個管轄區都有自己的具體監管要求。Accenture說，研究結果證實了區塊鏈技術可以降低運營成本，增加供應鏈的可見度。\r\n\r\n百威英博國際物流副總裁戴尼洛·菲格雷多(Danillo Figueiredo)表示： 「區塊鏈技術將改變我們的業務的發展。它能減少錯誤，將信息數字化，並改進供應鏈流程，使我們能夠專注於我們的核心業務：為消費者釀造最好的啤酒。」\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n<h2><strong>區塊鏈啤酒的創新推出</strong></h2>\r\n上述提到的百威英博還有其他在區塊鏈方面的應用成就，它和舊金山的初創公司Civic合作，推出全球首例的啤酒自動販賣機。該販賣機採用區塊鏈技術，藉由手機app的身份驗證與販賣機進行連接，用來限制未成年購買酒品，並以加密的虛擬貨幣作為交易媒介(比特幣就是加密虛擬貨幣的一種)。此種運用區塊鏈技術的販賣機在推出支出就廣為被討論，一方面驚艷於AI技術的擴大應用，卻也有購買流程過於繁瑣的疑慮，不過大眾注目焦點並不在於一瓶區塊鏈啤酒，而是區塊鏈在年齡限制上的運用，此項技術在AI產業中屬於一大突破，並期待未來在更多領域中被良好運用，讓企業在身負妥善管理責任的同時，也讓消費者能更安心地享受金融科技所帶來的便利。', '2018-10-01 13:04:37', '【時事分享】德國慕尼黑啤酒節背後的觀光商機與區塊鏈技術', 'article', 'publish', '2019-01-02 13:07:29', '', 0),
(2590, 1, '/upload/2019/01/01-1.jpg', NULL, '<h2>前言</h2>\r\n當生活日常中的各項互動漸漸虛擬化後，消費、金融等行為缺少了真實的人參與其中，你又該相信誰？\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n<h2>現況</h2>\r\n根據行政院主計處統計，民國105年網路銷售家數92,667家，占全體企業家數的7.1%，與100年必較增加了38.6%；105年網路銷售金額為3兆5,395億元，較100年及104年分別成長73.8%及11.4%，其中網路銷售予個人（家庭）(B2C)規模更是高達5,257億元。\r\n\r\n社會大眾更習慣於線上進行消費，輔以新型態支付平台、支付的方式以及多種金融上的創新，民眾終於實踐一鍵完成生活大小事的夢想，但貿然地暴露自己的各項私人資訊、甚至是銀行及信用卡資訊於網路上，安全勢必是一項重要的議題，傳統人與人面對面交流而產生的信任感，又該怎麼在商業創新的市場上延續？\r\n\r\n&nbsp;\r\n<h2>信任的提供者</h2>\r\n有關當局：政府訂定的法規勢必為最有效果的一道防線，法規能以公權力規範廠商，讓商業行為的對方也就是消費者，足以信賴符合法規的廠商所提供的相關服務。國際準則的規範亦能提供廠商內部控制或作業性控制上執行的依歸，讓服務或金融產品符合準則要求，例如：SOC l、SOC ll、SOC lll等。\r\n\r\n第三方認證評價機構：透過具有公信力的第三方機構，委託該機構去審核公司的服務或產品並出具有效報告，提供消費者對於該公司安全性取得合理的確信。相關認證的標章有：SGS食品安全、ISO9001品質管理、ISO27001資訊安全管理系統以及BS10012個人資料保護等。\r\n\r\n社會大眾：當某企業建立了正面的口碑及品牌形象，並且讓多數消費者對其取得相當程度的信賴時，社會大眾透過彼此的交流提供另一種穩固的信任來源。\r\n\r\n&nbsp;\r\n<h2>小結</h2>\r\n信任是人與人互動的根本，也是各項商業創新的基石，在如今科技化、虛擬化的時代，消費者藉由信任的提供者辨認優質的廠商；對於企業也不會是危機，而是一種轉機更是商機。\r\n\r\n&nbsp;\r\n<h5>參考資料</h5>\r\n<a href=\"https://www.dgbas.gov.tw/ct.asp?xItem=42804&amp;ctNode=5624\">行政院主計處電子商務統計結果</a>', '2018-10-02 13:07:36', '【普匯觀點】虛擬社會的試金石-信任', 'article', 'publish', '2019-01-02 13:09:54', '', 0),
(2594, 1, '/upload/2019/01/03.jpg', NULL, '<span style=\"font-weight: 400;\">最近幾年來Fintech蓬勃發展，金融科技也成為大家耳熟能詳的新名詞，不過大</span><span style=\"font-weight: 400;\">家知道怎麼樣的服務叫做金融科技嗎？Fintech有各種不同的應用方式，在無形</span><span style=\"font-weight: 400;\">中漸漸滲透人們的生活，也讓大眾習慣它的存在，讓我們來簡單介紹一下</span><span style=\"font-weight: 400;\">Fintech的六種商業模式吧！</span>\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n<h2><span style=\"font-weight: 400;\">➜商業模式1｜P2P網路貸款</span></h2>\r\n<span style=\"font-weight: 400;\">P2P是peer to peer的縮寫，peer是個人的意思，因此P2P是一種個人對個人的</span><span style=\"font-weight: 400;\">信貸模式，投資人可以透過第三方網路平台的媒合，出借閒置資金並獲得報酬</span><span style=\"font-weight: 400;\">，而借款人也能在平台的媒合下獲得資金，只要支付少許利息，就能夠運用資</span><span style=\"font-weight: 400;\">金實現夢想，而這也是目前普匯金融科技所代表的商業模式。</span>\r\n\r\n&nbsp;\r\n\r\n<span style=\"font-weight: 400;\">深入了解P2P網路借貸傳送門&gt;&gt;</span>\r\n<h2><span style=\"font-weight: 400;\">【人物專訪】普匯CRO 郭基永：帶給年輕人更多元的理財工具！</span></h2>\r\n<a href=\"https://goo.gl/V2BMPY\"><span style=\"font-weight: 400;\">https://goo.gl/V2BMPY</span></a>\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n<h2><span style=\"font-weight: 400;\">➜商業模式2｜虛擬貨幣</span></h2>\r\n<span style=\"font-weight: 400;\">虛擬貨幣就是不存在實體形象的貨幣，我們使用的點數儲值就是虛擬貨幣最常</span><span style=\"font-weight: 400;\">見的應用，像是遊戲點數、Apple Store點數等等，他們在特定的虛擬社群開發</span><span style=\"font-weight: 400;\">與控制下，被特定的虛擬社群成員使用。還有一種虛擬貨幣是藉由區塊鏈的應</span><span style=\"font-weight: 400;\">用而產生，它不由任何機構發行與控制，單純依靠網路點對點產生的協議信任</span><span style=\"font-weight: 400;\">而存在，大家耳熟能詳的比特幣就是最好的例子。</span>\r\n\r\n<span style=\"font-weight: 400;\">前一陣子比特幣的大漲讓許多投資者開始注意到虛擬貨幣這一塊的投資，區塊鏈相關的產業也因此蓬勃發展，不過大家要記得，高報酬就一定會有高風險，虛擬貨幣的投資漲跌幅範圍廣，在這領域的投資需要有足夠的知識背景，如果沒有事先做好功課就貿然進入市場，小心因小失大。</span>\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n<h2><span style=\"font-weight: 400;\">➜商業模式3｜第三方支付</span></h2>\r\n<span style=\"font-weight: 400;\">第三方支付是目前Fintech最廣為運用的商業模式，從中國大陸崛起並蓬勃發展</span><span style=\"font-weight: 400;\">的支付寶、微信支付是最典型的例子，它透過網際網路連結使用者、第三方支</span><span style=\"font-weight: 400;\">付公司以及銀行，協助使用者實現行動付款，並且擁有資金結算、代收代付等</span><span style=\"font-weight: 400;\">功能，現在甚至連路邊的停車費都能夠利用第三方支付繳款哦！</span>\r\n\r\n&nbsp;\r\n\r\n<span style=\"font-weight: 400;\">深入了解第三方支付傳送門&gt;&gt;</span>\r\n<h2><span style=\"font-weight: 400;\">【influx小學堂│Fintech再創新！】LINE代幣經濟即將上線！</span></h2>\r\n<a href=\"https://goo.gl/4GuRm2\"><span style=\"font-weight: 400;\">https://goo.gl/4GuRm2</span></a>\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n<h2><span style=\"font-weight: 400;\">➜商業模式4｜群眾募資</span></h2>\r\n<span style=\"font-weight: 400;\">群眾募資在資金的籌募上具有很大的影響力，他利用網路平台散播並展示計畫</span><span style=\"font-weight: 400;\">內容或是創作作品的訊息，讓受眾提供資金給自己支持的計畫以及作品，只要</span><span style=\"font-weight: 400;\">獲得足夠的金額便能夠付諸執行，專輯的發行、新創產品的推出都常利用這種</span><span style=\"font-weight: 400;\">模式來籌募資金。</span>\r\n\r\n<span style=\"font-weight: 400;\">許多社創企業都是依靠群眾募資創業的，Kickstarter是目前使用率最高的群眾募資平台，平台上充滿了新奇創業的有趣產品，如果你心中有滿滿的鬼點子、覺得一定能夠大受歡迎的新產品，但是卻沒有資金能夠付出行動，就可以到Kickstarter平台上尋求網友的協助，不僅能夠知道自己的點子在市場上的反應狀況，也能讓認同你理念的人資助你完成產品的推出，依靠大眾的力量讓命想成真！</span>\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n<h2><span style=\"font-weight: 400;\">➜商業模式5｜P2P匯兌</span></h2>\r\n<span style=\"font-weight: 400;\">P2P匯兌，顧名思義就是個人與個人之間的貨幣兌換，可以讓使用者利用比銀</span><span style=\"font-weight: 400;\">行還要低的匯率，透過平台找到對換貨幣的對象，出國換錢不但不用花時間比</span><span style=\"font-weight: 400;\">較各地的匯率，也能夠進行小額的交易，讓資金的運用更加靈活。</span>\r\n\r\n<span style=\"font-weight: 400;\">P2P匯兌平台的理念為「跳過銀行的匯率才合理」，TransferWise是2010年成立的跨國轉帳服務平台，宗旨為讓人們以更方便省錢的方式完成國際轉帳，不同於銀行的自訂利率來收取利潤，匯兌平台採用中期市場利率，也就是買賣匯率的中間值來作為匯率標準，和大家在google上看到的匯率是相同的，這才是平台所認為的「合理且公平的匯率」。使用方法也很簡單明瞭，捨去了銀行間跨國轉帳的繁瑣程序，如同一般網銀的轉帳功能，只要在網站上輸入自己和對方的銀行賬戶以及匯款金額，TransferWise就會自動幫你完成轉帳了！</span>\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n\r\n&nbsp;\r\n<h2><span style=\"font-weight: 400;\">➜商業模式6｜機器人理財</span></h2>\r\n<span style=\"font-weight: 400;\">目前許多銀行機構都爭相推出機器人理財的功能，也因為其「懶人投資」的稱</span><span style=\"font-weight: 400;\">號深受忙碌的小資族群歡迎，平台背後的演算機器人根據客戶不同的投資偏好</span><span style=\"font-weight: 400;\">與投資金額，制定出個人投資組合，只要支付平台少許的費用，就能夠成功達</span><span style=\"font-weight: 400;\">成懶人投資的目標！</span>\r\n\r\n<span style=\"font-weight: 400;\">雖然說是懶人經濟，但在利用此項功能的時候也要閱讀一下公開說明書，清楚了解目前投資標的以及報酬的詳細狀況，才不會在申購與贖回的過程中受到不明的扣款。也要清楚地了解自己的投資風險偏好，才能夠做出最有利的投資選擇。</span>\r\n\r\n&nbsp;\r\n\r\n<span style=\"font-weight: 400;\">Fintech的各種商業模式都是近幾年才開始興盛的投資模式，由於內容創新、報酬可觀，讓許多人趨之若鶩，大家在享受金融科技的便利時也不要忘記存在背後的風險，千萬不要忘記「高報酬、高風險」，投資的時候維持理性思維，才能夠做出最正確的判斷。</span>', '2018-10-03 13:10:32', '【普匯觀點】一次帶你搞懂Fintech的六種商業模式', 'article', 'publish', '2019-01-02 13:15:34', '', 0),
(2599, 1, '/upload/2019/01/04.jpg', NULL, '前言：身為年輕人的我們講求效率跟便利性，讓我們從一天的生活中來聊聊現今年輕世代需要的金融科技吧！\r\n\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n<h2></h2>\r\n<h2>一早起床</h2>\r\n一睜開眼，兩眼昏花盯著天花板摸著空空如也的肚子，拿起床邊的手機打開外送軟體的App，點選自己喜歡的店家跟商品，在付款時出現了以下支付方法：\r\n<ul>\r\n 	<li>手機電子錢包支付：隨著智慧型手機挾帶各種安全性驗證科技，行動支付功能已經普遍適用在智慧型手機，每個人可以透過指紋、臉部等辨識方式進行付款，手機中的電子錢包透過與銀行、信用卡公司的綁定，快速地驗證進行付款。</li>\r\n 	<li>線上信用卡付款：透過線上的支付介面，以手動輸入卡號等信用卡資訊後，線上與銀行、信用卡公司資訊鏈結，完成付款的動作。</li>\r\n</ul>\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n<h2>中午吃飯</h2>\r\n你與你的閨蜜或哥兒們約在餐廳吃飯，大家有說有笑聊得愉快至極，但每到了要結帳的時候又是個傷腦筋的難題，此時劇情可能有以下發展：\r\n\r\n一個金主出現說道：「我先付吧！」並且拿出手機來跟店員說：「您好，我要用XXX Pay」\r\n<ul>\r\n 	<li>手機NFC行動支付：NFC的中文是「近距離無線通訊」技術簡稱，允許電子裝置之間進行非接觸式點對點資料交換，透過此技術，手機中電子錢包內綁定的信用卡，與店家的刷卡機進行資訊交換，輔以智慧型手機安全性的身份驗證，立即地進行付款。</li>\r\n</ul>\r\n此時秉持著「親兄弟明算帳」的精神，一群人又討論著該如何分賬呢？\r\n<ul>\r\n 	<li>手機對手機的付款：透過當今資訊傳輸的通訊軟體或者其他軟體平台，透過QR code、條碼等訊息傳輸技術來交換彼此付款資訊，進而互相付款</li>\r\n</ul>\r\n&nbsp;\r\n\r\n<hr />\r\n\r\n&nbsp;\r\n<h2>下課/下班後</h2>\r\n傍晚時分，踏著疲憊的步伐邁向回家的路上，看到路邊的廣告打著「小島度假」、「賞楓之旅」或者「經典球鞋復刻上市」想著要是我有閒錢那該多好！\r\n<ul>\r\n 	<li>線上借貸平台：線上的平台商提供媒合平台，透過會員資訊的認證，讓資金的需求者跟投資者能夠搓合，使資金最有效的流通，解決年輕人小額貸款跟傳統銀行不願主動放款的市場需求，如今平台商更透過人工智慧讓借貸雙方快速地配對，更便捷的取得或放貸資金。</li>\r\n</ul>\r\n如今年輕人視支付工具的金融科技創新最為需要，在各種生活中的應用也最為廣泛，其中線上借貸平台商的服務日益完備，在消費者逐漸接受這樣的金融創新下，便利的資金流動也勢必受年輕人們歡迎。', '2018-10-04 13:18:45', '【普匯觀點】年輕人需要的金融科技服務', 'article', 'publish', '2019-01-02 13:24:59', '', 0),
(2611, 1, '/upload/2018/10/未命名-1.jpg', NULL, '<!-- wp:paragraph -->\n<p>撰寫人：Joe</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>引言</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>金融監理沙盒從法案通過、Fintech創新中心成立到如今通過了第一件審核案件已過半個年頭，本文從沙盒的介紹、相關議題探討至介紹第一件案件，讓你輕鬆了解沙盒的遊戲規則，快速跟上台灣金融創新的發展腳步！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>正文</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>立法院在2017年底三讀通過「金融科技創新實驗條例草案」，金融監理沙盒正式能在台上路，台灣也成為世界上第五個擁有監理沙盒的國家，金管會主委更在9/18宣佈了第一件通過審核的案件，就讓我們快速了解一下沙盒的運作吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>兩分鐘搞懂台灣的金融監理沙盒</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>沙盒這個名詞最早出現在電腦工程的術語，它是一個軟體測試的環境，讓程式、模型能夠被獨立地執行以便衡量、監控或測試。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>將這個沙盒的概念運用在Fintech上，杜克大學法學院教授Lawrence\nBaxter表示：監理沙盒不僅是政府扶植新創業者的方式，也是監管者學習Regtech（Regulation\nTechnology監管科技）的地方，也就是說，業者透過沙盒學習商業模式、軟體運行或相關法規的適用，而政府也可以就此學習監管並對於未來科技有先一步的因應，監理沙盒創造了一個共同實驗的環境。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>台灣現行的金融監理沙盒有以下特型：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>申請對象：金融業者與非金融業者</li><li>審查時間：60日內完成審查及作成核准或駁回創新實驗之決定</li><li>實驗期限：以一年為限，得申請延長一次不超過六個月；但涉及應正修法律時，延長不限一次，最長不逾三年。</li><li>相關規定：金融監理沙盒的業者在最終上線前，有相關法規命令的豁免權，但是然仍須遵守洗錢防制、資恐防治等法條的限制。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2>專家學者建言</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>但綜合的觀察下，專家學者對於目前的金融監理沙盒還是有提出幾點注意事項：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>創新實驗結束後仍須適用金融法規規範：本條例雖明定創新實驗結束後，主管機關如認為有修正法律的必要，應提出修法草案，但實驗結束後若未能完成修法，業者即須回歸適用一般金融法令。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>公告創新實驗內容可能洩漏商業機密：發展條例中的第十一條規定業者申請時要提供「創新實驗內容」並由金管會公告。李慈國際科技法律事務所創辦人蔡玉玲指出「既然創新、就一定有營業秘密，」並建議金管會可以不要要求太詳細的報告，避免讓新創的營業秘密外露。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>對此，金管會副主委鄭貞茂表示：這點金管會有考量到，會保護商業機密，公告內容不會寫到太細。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>申請變更流程繁冗導致喪失創新時間點：發展條例中的第十條規定業者在送出實驗內容後「重要事項不得變更，非重要事項可經申請後變更」，但是既然是創新想必會需要多項的調整，若是重大事項得調整還得等上一個月的審核期。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>因應此疑慮，金管會副主委鄭貞茂針對重大事項的判斷做舉例：若本來實驗對象是一百人改為一萬人，或將業務經手金額上限拉高，這就是比較實質的變更，但像變更地址這類不影響參與者權益的則不屬於重大變更。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>上路後的核准首案</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>金管會主委顧立雄在9月18日宣布凱基銀行與中華電信的合作案已經核准進行實驗，透過手機門號進行身份的驗證，結合電信業者徵信、電話費繳款記錄以及Big Data大數據交換等科技，用來辦理新戶線上信貸及信用卡業務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>對凱基銀而言，可拓展既有金融市場以外的客戶，對中華電信而言則是為未來發展純網銀奠定基礎，該合作案由凱基銀行設計信用評分模式，且不必額外付費給中華電信，但中華電信可取得凱基銀行實驗後的相關分析，及後續雙方合作。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此案件提供了幾項創新優點：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>解決年輕人信貸需求：銀行傳統的信用評估模式為「信用養信用」的方式，也就是透過基礎信用的培養才能取得自己未來較大的信用，但剛出社會或者在學中的年輕人沒有工作記錄下，信用記錄不足，這些「白紙」的信用狀況讓多數銀行比較難以貸放款給這樣的客群。但透過與電信業者的資料主檔配合，這些年輕人可以透過電信費的繳款狀況，反映他們的信用狀況，彌補以往傳統銀行對此客群的不足信用記錄。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>提供比傳統更即時的信用狀況更新：曾擔任金融聯合徵信中心董事、現任東吳大學講座教授兼金融科技中心執行長蔡宗榮則認為，以往銀行業辦理授信最頭痛的莫過於沒有即時資料，而聯徵中心所取得的財務資料多是落後資訊，反觀電信業者所持有的電信帳單是每個月都有，「甚至比水電費資料還來的即時」，因此，就可從客戶帳單內容進一步分析，實際觀察客戶現階段的消費行為</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>排除個資疑慮：中華電信所提供的客戶資料必須徵得客戶同意，身分資料才可攜入凱基銀辦理申請，並不會擅自地將資料進行流通。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2>結語</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>隨著金管會通過第一件的審核實驗合作案，台灣正式宣告金融監理沙盒的實際運轉，這半年來我們看得出政府針對金融科技創新領域投入不少重視及心力，也期盼台灣金融創新的市場能就此日漸蓬勃，不論是線上支付、行動支付、線上借貸、區塊鏈應用或者Big Data大數據。對於此次凱基銀行與中華電信的合作內容，其實台灣也已經存在很多P2P線上貸款的平台，不僅讓年輕人可以有個安全取得貸款的地方，更多了個將資金共享的投資選擇。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>參考資料</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金融管理委員會</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&lt; Fintech專欄：我國監理沙盒面臨的挑戰&gt;，谷湘儀，2018/02/11</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&lt; FinTech大鳴大放？金融監理沙盒上路後的4道難題，政府真的準備好了嗎&gt;，張庭瑜，2018/01/17</p>\n<!-- /wp:paragraph -->', '2018-10-05 15:39:02', '【時事分享】金融監理沙盒首實驗案核准了！', 'article', 'publish', '2019-01-02 15:50:43', '', 0),
(2619, 1, '/upload/2018/10/未命名-1-1.jpg', NULL, '<!-- wp:paragraph -->\n<p>文案撰寫人：Ava</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>中秋連假後，緊接著就是國慶假期啦！今年的國慶假日只有一天，深藏在口袋的旅遊名單也許沒有辦法實現，不過對於想把握假期充實自我，或是小小娛樂的人來說，這卡在一週中間的「小確幸」可是有大大的價值的哦！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>從經濟學角度看單一假期</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>我們從幾個簡單的經濟學基本理論來分析。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>首先是邊際效用遞減，邊際效用在經濟學理論中所描述的是，「當同一物品消費數量漸增時，每增加一單位的物品，同單位物品對於消費者的效用會遞減。」解釋太文言文聽不懂嗎？沒關係，簡單來說，假如你是個非常喜歡吃泡麵的人，現在你非常飢餓，吃了一碗泡麵後得到最大的滿足，這時候如果再給你一碗泡麵，你還是會吃，但可能覺得沒有第一碗泡麵來的美味，再給你第三碗的時候，你可能看到就想吐了，這就是邊際效用遞減的概念。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>那要怎麼用來解釋單一假期呢？大學生的暑假有將近三個月，將近90天的假期如果多了一天的假日感覺上沒有太大的愉悅，但是在幾乎都是工作日的10月，國慶日僅僅一天的休假，忙裡偷閒的感覺，讓人有滿滿的「小確幸」！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>自我充實的效率提升</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>很多人會在暑假安排自我充實計畫，但礙於天數過長，總會有「今天不做，明天補回來就好」的拖延症想法，結果到開學前才發現又虛度了一個暑假。不過如果在單一假期安排短短的課程，在「我只有今天」的潛意識催促下，就能夠順利達成自己訂下的目標，學習與做事的效率也大幅提升！那要做什麼事情才能在達到最高邊際效益的同時，也有良好的效率呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如果你是個喜好安靜的人，這個假日不妨閱讀一本你很想看但平時沒有時間看的書，或是安排一堂語言課程，找間咖啡廳坐下，享受這個與文字共處的悠閒假期，在舒適悠閒的環境讀書，能夠有效地提升效率哦！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如果你平時是個工作狂，生活缺少娛樂，那就出外走走，給自己一天放鬆的假期吧！不論是到市區血拼購物還是到郊區踏青郊遊，適時地給予身心放鬆，舒緩平時工作積累的壓力，也是提高工作效率的小撇步哦！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>節慶放假看台灣經濟</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>由於假期只有一天，很多人可能會選擇在市區或是近郊的娛樂活動，KTV、電影院在假日更是人滿為患，有研究指出「小確幸」能夠增加台灣消費量，政治人物也紛紛推出「放假拚經濟」的口號，但是人們在假日所做的娛樂行為，真的能替台灣的經濟成長帶來貢獻嗎？那是不是意味著放假越多，GDP成長就越快呢？「放假經濟學」自提出以來，眾說紛紜。在現實生活中，小確幸這種生活中微小卻很確實的幸福的確讓一般民眾有感，消費也確實在假期期間有所增加，不過卻不是放越多假就能帶來越高的經濟成長率，以企業的長期經營來看，過多的假期可能造成企業競爭力的衰退，國家GDP不僅僅由民間支出組成，投資、政府支出、淨出口也是構成GDP的重要元素，光從消費面拚經濟是政策執行上的盲點，台灣經濟需要成長，但也不能光從單一角度來思考政策的執行。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>不過綜合來說，人民是處在生活中的，節慶放假是在忙碌生活中偷閒</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>最有感的小確幸，享受這天假日，把握時間充實自我、來點簡單的小娛樂，在增加國家人力資本競爭力的同時，也替台灣GDP有小小的貢獻，何樂而不為呢？快讓自己的國慶假日達到效益最大化吧！</p>\n<!-- /wp:paragraph -->', '2018-10-08 15:43:12', '【節慶特輯】今年國慶沒有連假？只有一天也能夠很享受的「小確幸經濟學」', 'article', 'publish', '2019-01-02 15:57:16', '', 0),
(2622, 1, '/upload/2018/10/未命名-1-4.jpg', NULL, '<!-- wp:paragraph -->\n<p>文案撰寫人：sandy</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在我們的日常生活中，銀行的服務無所不在，然而在科技日益創新的時代下，創新技術擴散的速度越來越快，面對於這樣的局勢，傳統的銀行業勢必面臨轉型來應對快速變化的市場及客戶需求，那麼中國的銀行發展趨勢又是如何？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>金融革命下的變遷</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 在互聯網金融的衝擊以及金融多元化的角逐競爭下，傳統商業銀行無不需要改變自身的業務模式來應對市場急速的變化，現今中國的多數銀行打破了以往依賴網點以及櫃位的傳統經營模式，開啟了網路金融的產業革命。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 從以前的高櫃、低櫃，到現在演變成智慧銀行、手機線上的網路銀行又或是接下來邁入的無人銀行，中國大陸在大數據的變革下不斷地與時俱進，銀行業科技的進步提高了服務的品質，也為傳統銀行業的轉型奠下根基。有人說中國的銀行發展就是網絡時代的變遷，現今他們主要發展趨勢可能為三大項：智慧銀行、輕型銀行以及國際銀行。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>智慧銀行的興起</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 智慧型零售網點這個概念首先在日本提出的，而在中國已經接近實現全面運營，相對於傳統網點，其主要特徵為社會化、智能化和多樣化，這種新模式提供了更高的靈活性、便利性和移動性。現在銀行的智慧服務，早已超出了存取款的限制，在智慧銀行的設備上，使用者可以通過觸摸顯螢幕進入網路銀行，辦理融資、貸款等業務，同時智慧平臺也支援登錄非本行的網路平臺。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 智慧銀行除了智慧的特性外，在面對客戶所需服務之時也須及時調整業務結構型態，智能銀行的構建貫穿銀行的前中後台，透過前中後台業務流程的整合和自動化以及渠道的相關媒合，體現以實現客戶為中心的銀行業務。再透過優化的系統處理和技術的革新來保證服務的全面高效性以及客戶的最佳體驗。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>中國建設銀行智慧櫃員機</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;舉中國的建設銀行為例，客戶在進入智慧銀行後，透過人臉辨識及身分證檢驗就能把個人資訊傳輸到櫃員的作業系統，利用屏幕在智慧櫃員機上進行多項產品的買賣，不管是辦理金融卡、貸款、存取款……等，都能一次搞定！經由大數據分析和智能AI的技術，智慧桌面為客戶帶來全新的互動方式，客戶經理現場設計理財方案，輕輕一揮，方案就能傳輸到客戶的手機上帶回家。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 在金融超市，只要在貨架上拿起感興趣的產品卡片，旁邊的螢幕就會自動播放該產品的動漫介紹。在移動金融場景運用區，可通過二維碼支付、刷卡支付或是建行自行推出的龍支付在現場購買飲料，體驗便捷支付方式的魅力。在遠端銀行VTM，客戶可以在遠端櫃員的視頻協助下自助辦理開戶、充值繳費等各項業務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 對於這樣的發展，智慧銀行不僅僅解決了大量人力的問題，為社會大眾省去不少的時間成本，客户对银行需要的不再是渠道，而是一种生活场景。哪家银行能够跟上时代的潮流，取得客户的信任，取决于银行如何在世代的變遷下繼續前進、發展新的業務模式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"http://news.163.com/15/0401/15/AM4IRKMH00014Q4P.html\">http://news.163.com/15/0401/15/AM4IRKMH00014Q4P.html</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://mp.weixin.qq.com/s/WxvfkFfNd_3AnpuZH34zLg\">https://mp.weixin.qq.com/s/WxvfkFfNd_3AnpuZH34zLg</a></p>\n<!-- /wp:paragraph -->', '2018-10-09 15:48:33', '【Fintech】大陸銀行業發展沿革', 'article', 'publish', '2019-01-02 16:42:44', '', 0),
(2629, 1, '/upload/2018/10/未命名-1-3.jpg', NULL, '<!-- wp:paragraph -->\n<p>文案撰寫人：sandy</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>   傳統金融科技式微，現今金融科技的發展與應用不容小覷，而 P2P網路借貸正是現今火熱的一項趨勢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>   P2P是一種全球性的金融創新服務，P2P是英文 peer to peer的縮寫，意即「個人對個人」，是一種金融民主化的精神。但是在台灣並沒有很多人真正了解P2P到底功用何在，聽到的也都有可能只是片面的解釋，然而在現今大數據的時代下，使用區塊鏈的技術發展網路交易是一大趨勢。因此此篇文章將帶大家深入探討P2P在台灣的發展與沿革。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading -->\n<h2><strong>P2P是什麼？</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 只要網上google搜尋就會有很多相關介紹，但是密密麻麻的文字總還是讓我們對於P2P很陌生，簡單來說：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>  P2P就是一個無形的網路平台，當現在有個需要資金的借款人提出借貸申請後，平台就會開始尋找可以媒合的投資人進行配對、搓合，再用更簡單的話來講，P2P就像是一個中間媒介，為借貸雙方進行金錢的流動處理，因此我們可以是借款人也可以是提供資金的投資人。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading -->\n<h2><strong>台灣</strong><strong>P2P</strong><strong>起源與規範</strong><strong></strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>2016年3月，<a href=\"https://zh.wikipedia.org/wiki/%E9%84%89%E6%B0%91%E8%B2%B8\">鄉民貸</a>在台灣成立，是台灣首家網路借貸&nbsp;平台。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2016年4月，矽谷創投家陳五福先生宣布投資台灣P2P借貸平台，<a href=\"https://zh.wikipedia.org/wiki/LnB%E4%BF%A1%E7%94%A8%E5%B8%82%E9%9B%86\">LnB信用市集</a>。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2018年5月，<a href=\"https://zh.wikipedia.org/wiki/LnB%E4%BF%A1%E7%94%A8%E5%B8%82%E9%9B%86\">LnB信用市集</a>攜手<a href=\"https://zh.wikipedia.org/wiki/%E6%B8%A3%E6%89%93%E9%8A%80%E8%A1%8C\">渣打銀行</a>合作，推動完全數位化線上信貸體驗。此為全台第一個和銀行合作之P2P借貸服務，在全球間也僅為少數。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> 台灣版的P2P相較於各國起步晚，初期有鄉民貸、信用市集，雖然發展較晚，雖然現在還沒有法律專法進行規範，但透過「經濟部」的監管以及金管會等相關機關的管控，除了規定P2P業者不得吸金、融資，亦不可以發行理財商品，再加上與金管會合作建立的「金融監理沙盒機制」 (點選進行更多了解)，台灣 P2P 的發展漸趨成熟。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading -->\n<h2><strong>是否存在風險？</strong><strong></strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 然而，大家一定會想，在這種網路交易的平台上我的資金及隱私是否安全、是否在借錢出去後能安全收回款項？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 其實台灣的金融信用體制是很完善的，我們擁有國家級的信用資料庫機構去判斷個人或企業的信用分數，裡面的資訊不僅完整更在多數P2P平台中使用。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 用較淺顯易懂的話語來說，假設今日我要進行貸款，當我填寫好所有資料以及進行相關認證後，P2P平台就會依據我過去的工作或是收支狀況為我個人計算出一個分數，系統再依照分數尋找適合我的投資人！加上媒合的期間個人資料都不會被公布在平台上，因此發生風險的機率也大大降低！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://zh.wikipedia.org/wiki/%E7%BD%91%E7%BB%9C%E5%80%9F%E8%B4%B7\">https://zh.wikipedia.org/wiki/%E7%BD%91%E7%BB%9C%E5%80%9F%E8%B4%B7</a></p>\n<!-- /wp:paragraph -->', '2018-10-10 16:00:13', '【Fintech】P2P在台灣', 'article', 'publish', '2019-01-02 16:36:40', '', 0),
(2644, 1, '/upload/2019/01/12.png', 'https://www.youtube.com/embed/IjZnrAXAwgY', '<!-- wp:core-embed/youtube {\"url\":\"https://youtu.be/IjZnrAXAwgY\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://youtu.be/IjZnrAXAwgY\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:image {\"id\":2649} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/1.jpg\" alt=\"\" class=\"wp-image-2649\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>小睡眠 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>大一新生進入一個陌生的住宿環境，一開始容易睡不好。這款專門針對睡眠問題的app，裏面有上百種白噪音，優質的冥想音頻，還有明星鬧鈴，可以幫助你快速入眠，即使在新宿舍裏可以有一個安甜的睡眠。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2656} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/2.png\" alt=\"\" class=\"wp-image-2656\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>有道雲筆記&nbsp;&nbsp; </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>強大的筆記APP，最厲害的是它可以識別圖片、PDF等掃描文檔中的文字，並將其轉化成可編輯的文檔。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2657} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/3.jpg\" alt=\"\" class=\"wp-image-2657\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>超級課程表&nbsp;&nbsp; </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>大學生的課程多且雜，有專業課、公共基礎課、選修課等。上課地點也都不同。因此可以下載這款APP，讓你準時出現在每一次課堂。還可以順利旁聽別系的課程。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2658} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/4.png\" alt=\"\" class=\"wp-image-2658\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>滴答清單 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>身為一個大學生，學會時間管理是成功第一步。可分天/周/月進行計劃的安排，提醒功能讓你克服拖延，提高學習效率！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2659} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/5.jpg\" alt=\"\" class=\"wp-image-2659\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>藏書館 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>上了大學後要多讀書來提升自己，這款app可以在上面閱讀書籍，借閱電子書，和你志同道合的書友進行交流，還可以通過它購買新書，聯通其他數字圖書館借閱圖書，完成自我知識的積累。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2660} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/6.png\" alt=\"\" class=\"wp-image-2660\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>TIMI時光記賬</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>學生錢有限，當然要好好分配啦。要試算不清楚錢到底花在哪兒。一款手機記賬app幫助理清自己的消費情況。這款app主打時光記賬，以時間軸、快拍記賬為特色，強調支出控制，力求簡潔的記賬模板</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2661} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/7.png\" alt=\"\" class=\"wp-image-2661\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;黑白校園&nbsp;&nbsp; </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這款超酷的APP ，黑白校園主打的是分時社交的概念：以早上7點和晚上9點為時間界限。白天實名，晚上匿名。它既不像人人那樣公開，又不像微信那樣私密，很好地選擇了一個適宜的中間地帶。白天，用戶可以正常上傳照片，發表狀態;晚上，全部用戶都變成匿名狀態，各種更加直白感性的話題也能毫無顧忌地被討論。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2662} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/8.jpg\" alt=\"\" class=\"wp-image-2662\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>小恩愛</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>美好的大學生活少不了談戀愛，擁有小恩愛，情侶間的溝通更有樂趣，豐富二人生活。你們在小恩愛擁有最私密的二人世界，免費發送短信、語音、圖片等，只有彼此看得到；除了多種貼心的私密功能之外，你們還可以在公共社區與志趣相投的情侶們交流分享；邀請朋友光臨情侶空間；和心愛的人一起玩雙人遊戲等。<br><br><br><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2664} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/10.png\" alt=\"\" class=\"wp-image-2664\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Any.do</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有簡單強大的日曆，任何想法都可以記錄下來，生活組織與日常規劃的最棒夥伴 。Any.Do是一個界面簡潔的跨平台免費日程管理應用，提供Android、iOS客戶端，以及Chrome瀏覽器擴展、網頁版，允許登陸用戶同步日程,並與用戶的Google Tasks同步。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2665} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/11.jpg\" alt=\"\" class=\"wp-image-2665\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>Zepeto</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>近很多人都在曬一種可愛的3D形象，乍看和蘋果的Memoji有點像，不過動作更豐富，還可以和別人進行合照。這個有趣的App叫做ZEPETO，是一款社交軟體。它最大的亮點就是可以進行捏臉，做出可愛的3D形象，然後和別人互動，並製作各種有趣的貼圖。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2666} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/12.png\" alt=\"\" class=\"wp-image-2666\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>D card</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>推薦指數：★★★★★</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>使用者可以藉由Dcard的網站或是App來透過瀏覽、發文、回應他人文章，討論區也依照內容不同分作不同討論版，目前共有「有趣」、「感情」、「心情」等多個不同的版，除此之外每個學校還有自己的個版，稱作「校版」，只有該校學生可以在上面發文及留言。Dcard官方有時也會因為當今流行的話題等因素開放特定討論版，例如「寶可夢版」就是在寶可夢開始流行時設立的；「世大運版」則是在2017世界夏季大學運動會後因為使用者的踴躍參與而改名為「大型賽事版」並予以持續開放。2018年花蓮地震開設「地震回報版」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>總結</strong><strong>:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>兩岸大學生在喜愛app上不外乎就是喜愛社交占了重要的因素，另外重視的就是生活上的輔助小工具啦!小編認為同類型的app只有使用上的些微不同，基本上大學生地喜愛都很相近。大家也不用擔心到內地沒有好用的app，大陸的app可是豐富且多樣呢!</p>\n<!-- /wp:paragraph -->', '2019-01-02 16:38:00', '【時事分享】兩岸大學生最近夯什麼APP?', 'video', 'publish', '2019-01-17 17:53:52', '', 0),
(2674, 1, '/upload/2019/01/555bf2b08afcaabcdc6e3d1698e26f0b9a415fd1c988b-FYVj0p_fw658.jpg', NULL, '<!-- wp:paragraph -->\n<p>互聯網發展以來，區塊鏈是最能顛覆我們日常生活應用的一種技術創新，科技不斷在創新、進步</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>你，知道區塊鏈到底是如何運作及應用的嗎？來看看你到底對他了解多少！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>區塊鏈 (BLOCKCHAIN)是由比特幣等多種數位貨幣的架構所發展出來的創新技術，本質上是一種去中心化的全球性資料庫，它無法被竄改，同時具有高安全性及保密性還能大幅降低金錢上的成本，不但能記錄龐大的金融交易，也能記錄我們生活中任何能以程式來表現的東西\n。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading -->\n<h2><strong>演進與機制</strong><strong></strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>區塊鏈這項技術主要可以分為四個階段：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li><strong>加密數字貨幣</strong>：數位貨幣與支付系統去中心化</li><li><strong>智慧資產、智慧契約</strong>：市場去中心化，可做貨幣以外的數位資產轉移</li><li><strong>金融應用、資料庫</strong>：代幣應用崛起，資料庫區塊鏈技術擴大並結合AI應用</li><li><strong>更多元的智慧合約</strong>：涉及層面擴大至科學、藝術、醫療、政府等領域</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>區塊鏈是一種全球性分散帳本，是一個開放型的加密架構。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>用更淺顯易懂的表達方式來說，當我們在網路上的任一處發生一筆交易時，這筆交易會和十分鐘內一起發生的交易被記錄在一個小區塊內，在經過資料的查驗、清算後儲存到大型的資料庫，隨後這些新的區塊會和原本被驗證過的區塊串再一起，累積下來便是一條〝區塊鏈〞，每一個相連的區塊都必須搭上線才被認定為有效。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>因此，如果你想破壞、侵入這項系統，你必須重新改寫所有過往發生過的交易紀錄，這在實務上相對是不可能發生的，畢竟所有擁有這些帳本的持有者分散世界各處，只要其中一個區塊一有發生錯誤就會馬上被察覺！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading -->\n<h2><strong>金融應用服務</strong><strong></strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>區塊鏈對於企業的交易模式可以產生巨大的改變與衝擊，為了因應潮流，許多行業的商務模式都開始產生了變革，而對於社會經濟最具影響力的金融業也積極佈局區塊鏈技術的應用，目前應用的範疇包含：跨國貿易、證券交易、資料轉換及儲存……等。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading -->\n<h2><strong>普匯金融科技區塊鏈應用</strong><strong></strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>經由前面整合性的介紹後，大家是否也疑惑〝普匯金融科技〞到底用了甚麼樣的區塊鏈技術？馬上就讓我們來為您解惑！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Influx 普匯金融科技平台提供完全線上化借貸款服務，不同於傳統銀行，普匯運用區塊鏈中智能合約(Smart\nContract)的技術打造無人化融資平台，主要運作模式是利用網路程式驅動以及自動執行的機制，在沒有第三方中介機構下進行可信交易，能自動儲存、發送訊息並進行驗證、清算，在無需依賴第三方驗證與聯繫個人資料安全的情況下，智能合約技術具有兩大主要優點：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>能大幅降低成本並加速整體流程效率，借貸款雙方能直接在線上或手機進行申貸辦理，讓交易過程更優化也更快速。</li><li>因交易資料在區塊鏈中不能隨意被竄改，因此具有相當的安全及保障性。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>普匯致力於服務往往因資歷或金錢額度不足而無法向銀行借款的年輕族群，提供創新的金融借貸服務，建立互利互信制度讓借貸雙方都能獲得更好的體驗。如此詳細的解說還讓你不心動嗎? 讓普匯帶著你一同學會投資理財，放膽追逐自己的夢想！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://www.ithome.com.tw/news/123145\">https://www.ithome.com.tw/news/123145</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://wiki.mbalib.com/zh-tw/%E5%8C%BA%E5%9D%97%E9%93%BE\">https://wiki.mbalib.com/zh-tw/%E5%8C%BA%E5%9D%97%E9%93%BE</a></p>\n<!-- /wp:paragraph -->', '2018-10-11 17:52:26', '【普匯觀點】區塊鏈金融應用(上)', 'article', 'publish', '2019-01-02 17:55:03', '', 0),
(2681, 1, '/upload/2019/01/01.jpg', NULL, '<!-- wp:heading -->\n<h2>引言</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>上個禮拜我們講到台灣金融監理沙盒終於核准了首案實驗，過了幾天台灣金融科技產業又迎來另一個好消息：台灣與英國簽訂了金融科技創新合作備忘錄（MOU）</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>正文</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>在9月28日台灣金融總會與英國在台辦事處簽署金融科技創新合作備忘錄，此備忘錄除了象徵兩國對於金融科技產業更進一步的合作，更能帶來兩國相關廠商相互學習合作的契機，此篇文章從簡介英國金融行為監理局（FCA）、例舉英國金融科技相關業者以及此備忘錄對台灣金融科技市場的影響。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>兩分鐘了解英國金融科技監管機構</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>監理主管機關：金融行為監理局（Financial Conduct Authority）</li><li>申請對象：必須符合以下五項標準的新創企業<ul><li>金融服務範疇</li></ul><ul><li>真正創新性</li></ul><ul><li>消費者利益</li></ul><ul><li>必要性</li></ul><ul><li>已達試驗階段</li></ul></li><li>實驗期限：測試期間為3~6個月，需要做客戶人數、客戶選擇、客戶保護、資訊揭露、資料測試等計畫</li><li>相關規定：企業可以暫時豁免相關法規的適用，藉此降低相關法規遵循上的風險，加快新創企業產品的上架時程，FCA也會與通過測試審核的企業進行密切合作，並給予個別的指導。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2>英國監理沙盒通過的案例</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>Chynge：為跨國匯款系統，該系統建置了監控的機制，透過虛擬人工智慧的機器人去更有效率防止洗錢、財務金融恐怖攻擊及詐騙的發生。</li><li>First Direct and Bud：為一個手機應用軟體，他能透過學習客戶的交易屬性及人口資料，去辨識出該公司、匯豐銀行或其他外部組織在市場上的金融產品及非金融產品。</li><li>Orca：此為P2P線上貸款的聚集平台，此平台主要是加速投資多項來自其他線上P2P網貸平台的組合標的。它的概念就像是你購買基金一樣，能夠一次投資包含多項股票標的的組合，只是這裡的投資標的來自於各個線上借貸平台。</li><li>Rebank：此為一整合客戶多個銀行賬戶登陸作業的平台，透過可預期性的演算法，提供企業能夠更有效率地處理銀行業務及付款行為。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2>此合作案對台灣金融創新的影響</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>此次的金融科技創新合作備忘錄比較屬於民間的交流，金管會主委顧立雄對此表示：「金融科技（FinTech）MOU重要的不是『監理』這塊而是『交流』的這塊，提供雙方業者能互相取經的機會，這是與其他監理MOU不一樣的地方，監理MOU會涉及主管機關的資訊、聯合金檢這些具有高度公權力的議題，但FinTech的MOU沒有涉及公權力的行使，純粹就是多交朋友、多取得相關資訊，多理解對方的做法。」因此在這樣相較自由、互信且對等的合作環境下，相信兩國業者能夠向彼此學習更多的科技及金融應用。此外，英國市場也發展了很多在台灣尚未開發的金融創新服務，想必也能因此刺激更多台灣企業發展出更多元服務。</li><li>新創公司每每想要追求讓自己成為那個「獨角獸」英國已經有四家發展完成的獨角獸企業，並且還有七家尚屬於發展中的階段，台灣屬於剛起步的階段，透過英國的實例借鏡，相信我國能較有效率地培植這樣的企業誕生。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>對於台灣的新創公司是也能拼出一隻獨角獸的問題，金管會主委也表示：「新創公司還沒成為公發公司就長那麼大，這跟整個國家經濟體的規模有很大關連性，像中國、美國經濟體就很大，我國的話，國發會也已訂下目標。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>英國的監管機構近期邀集了來自澳大利亞、阿布達比、巴林、杜拜、香港、新加坡和加拿大等11個國家同行合作，他們想要打造一個以「全球監理沙盒」為基礎的網路，該聯盟稱該網路為「全球金融創新網路GFIN（Global Financial Innovation Network）」將沙盒的應用拓展到了國際的層級，相信在台灣與英國逐漸培養起的合作關係下，台灣也能在這個全球化的金融科技創新市場，取得一席之地。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>隨著台灣金融科技創新市場逐步地發展，更多元的商業模式、科技應用及經營理念透過與他國的合作進行直接的交流，相信在這樣正向的合作中，台灣的金融科技市場能夠更快速地朝向成熟市場邁進，提供台灣民眾更全面且安全的金融創新服務。</p>\n<!-- /wp:paragraph -->', '2018-10-12 18:29:47', '【時事分享】台英簽署金融科技創新合作備忘錄', 'article', 'publish', '2019-01-02 18:33:05', '', 0),
(2684, 1, '/upload/2019/01/02-1.jpg', NULL, '<!-- wp:paragraph -->\n<p>文案撰寫人：Tommy </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>升大三的這個夏天，和眾多學生一樣，我滿懷熱血的去找實習，試著想要和職場接軌，當時不知道要去哪一間比較好，但心裡其實一直有個信念:「新創、金融、大數據」，後來尋找這類型的公司時，普匯這個名子很快就映入眼簾，老闆是拋棄銀行千萬年薪的主管，帶著頂尖的技術和金融經驗再度創業，只為了讓Fintech在台灣生根並扎根，當時看完這些資訊就馬上決定要應徵了，有經驗又有團隊，相信加入了就可以獲得很大的成長，而最後我也成功加入普匯。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯是一間Fintech新創公司，由具有豐富金融業經驗的管理層帶領著年輕人所組成的團隊，充滿著活力與各式各樣的腦力激盪，扁平化的組織中，在遇到問題時，除了能和其他大學的intern交流外，更能直接面對面和主管或老闆討論、學習，假如當時我選的不是新創公司，我根本不會有這種機會。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在普匯擔任intern的日子以來，時間是最大的挑戰，新創的步調總是特別快，除了學習力快之外還要做事快，一個不小心就會跟不上大家的腳步，面臨淘汰的邊緣，平時必須負責許多產業以及新聞調查研究，目的是要了解整個金融市場的現況，這點我一直以為應該和學校上課教的差不多，但是其實差的可多了，真實世界的運作模式遠遠超多我們的想像，顛覆你我的認知；</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>然而最讓我印象深刻的莫過於我所負責的「市場探勘」專案，從蒐集資料到報告產出，用自身的力量去做出一份報告，雖然常常得熬夜趕工，但是在做出來的時候、和老闆面對面報告的當下，那感覺真的很不一樣，像軟硬實力都同時得到了大幅提升，也因此我現在才能更熟捻於程式分析的應用、判讀數據背後的insight，幫助自己也幫助公司！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>三個月過後我在看三個月前的自己，很開心能夠加入這個大家庭，三個月就徹底改變了我對金融世界的看法，但也體會到必須隨時積極學習的現實，在Fintech的浪潮下，我們不去改變自己，就注定等著被別人改變。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>我是來自政大經濟的Tommy，</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>很幸運地在普匯獲得的實習經驗。</p>\n<!-- /wp:paragraph -->', '2018-10-15 18:37:25', '【時事分享】為甚麼我選Fintech公司來磨練自己', 'article', 'publish', '2019-01-02 18:38:05', '', 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(2687, 1, '/upload/2018/10/02.jpg', NULL, '<!-- wp:paragraph -->\n<p>撰寫人：Joe</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>前言</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>在先前文章中我們介紹了AI的發展過程，現在讓我們以AI處理的分類資訊，對這項科技有更完整的了解。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>AI處理的資訊及應用</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>結構化資訊：具有明確欄位、鏈結、或屬性等特性的資訊，此種資訊能夠最為便捷的被應用，只要使用者下出明確的指令，系統則能完成任務。更生活化來說，我們以國外採買為例：假如你到了一個國度，並且不熟悉其使用的語言，有人給你一張用中文寫成的採買流程，請你先去A店買香蕉、再到隔壁B 店買便當與C點買飲料，雖然買的物品不同，但是你能有依據地快速採買完成。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此種AI不涉及自我學習，且現階段較為廣泛被企業使用，例如：Robotic Process Automation (RPA)。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>具規律的非結構化資訊：不具有結構化資訊的特性，AI可以辨識其中的規律，可能是文字、電子郵件或者Excel活頁簿裡多型態資訊，進而連結其他檔案甚至做出預測，同樣以國外採買為例：現在那張流程文件使用你不熟悉的語言，透過幾個猜得出來的文字或符號，你能最終完成預期的採買任務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此種AI已經開始進入學習的領域，商業的使用有：線上商場或郵件分類。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>不具規律的非結構化資訊：同樣屬非結構資訊，只是資訊的規律更難預測，此類資訊可能是影像或者語音，AI處理此種資訊需要更進階的學習，進而最佳化預測結果。同例：今天你的委託人用不熟悉的語言口頭告訴你要如何採買，你可能花很多次經驗得知他要你做甚麼以及哪間店的產品才是他想要的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>生活中的例子：目前有智慧型手機能夠透過鏡頭辨認花、狗等生物或物品。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>情境式資訊：此處的資訊最為抽象複雜且附情境資訊，AI被給予的指令可能是：幫我把車倒出車庫或者幫我預約明天的剪髮，在未給出明確指令下，AI必須單純透過觀察來完成任務。最後用同一個例子，今天國外委託人用不熟悉的語言告訴你：「我餓了」，接下來你只能透過一次次的經驗，學習完成採買這個任務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這個技術是現階段開發的主要範疇。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>參考資料</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://www.capgemini.com/consulting-de/2018/01/ai-concept-frame/\">〈How different Kinds of Artificial Intelligence Will Shape\nIndustries – the Case of Smart Production in Life Science〉，Fruth, Florian David，January 3,\n2018</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"http://www.netadmin.com.tw/article_content.aspx?sn=1112140001\">〈MBC找出非結構性資料的關聯價值〉，高偉超，NetAdmin網管人，2012年1月2日</a></p>\n<!-- /wp:paragraph -->', '2018-10-16 18:38:26', '【Fintech】AI有幾種？讓我們從AI處理的資訊淺談', 'article', 'publish', '2019-01-02 18:41:56', '', 0),
(2690, 1, '/upload/2018/10/01.jpg', NULL, '<!-- wp:heading -->\n<h2>前言：</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>金融科技方興未艾地席捲全球，台灣的金融科技業者在這股浪潮下也逐步的發展，身為老大哥的「金管會」又是怎麼想呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>本文：</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>金管會在2016年5月發佈「金融科技發展策略白皮書」作為發展的指導原則，並且提出其他10項金融科技發展計畫；2017年底，立法院三讀通過&lt;金融科技發展與創新實驗條例&gt;，而金管會也於9月初發表「金融科技發展推動情形」報告。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>報告內容</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>金管會以正向積極的態度擬定政策並推動金融科技在台發展，該報告明示了金管會三個政策面向「金融業升級」「金融科技創新發展」與「資安與監理」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>金融業升級：除了放寬金融機構投資金融科技的法規也讓投資支出能申請投資抵減；鼓勵申請專利並且推動產學合作，並設立了金融科技創新基地來輔導創新團隊、開設課程以及在大學成立研究中心；除了跨部會繼續推動行動支付外，純網路銀行的開放也被提及。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>金融科技創新發展：設立金融科技創新園區並且協助釐清科技創新者在法規上的疑慮，也提供其他諮詢輔導的服務，而該新創園區目前有37家廠商通過審核進駐並且與他國建立新創資源交換合作協議。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>資安與監理：成立金融資安資訊分享與分析中心(F-ISAC)，進行情資研判分析、資安資訊分享、協助資案事件應變等相關服務，強調資安為金融科技創新的基礎，並強化網路風險防範。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2>P2P網貸業務</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>近期中國大陸的網貸P2P市場發生了一連的風險性事件，有鑒於台灣市場屬於起步的階段，社會大眾必然會對此次事件投下關注，而根據媒體報導，金管會副主委黃天牧也針對台灣P2P網路融資平台業務表示：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「我國會開放P2P網路融資平台業務，但會審慎審查及監理，不會因大陸發生倒閉潮因噎廢食，畢竟兩岸監理方式不同。若只是充當資金借貸撮合平台，提供訊息，則平台業者本身並沒有風險；有的型態是平台保證一定收益，那樣風險較大。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>小結</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>綜觀金管會對於台灣當今金融創新科技發展的業務，主管單位以實際的法規及政策展現樂觀推動的態度，至於P2P網貸平台的發展，在相關條件下更是明確肯定了政府的意願。</p>\n<!-- /wp:paragraph -->', '2018-10-17 18:42:09', '【時事分享】金管會對金融創新的態度', 'article', 'publish', '2019-01-02 18:46:02', '', 0),
(2693, 1, '/upload/2019/01/02-2.jpg', NULL, '<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>歌手巴布狄倫有一句歌詞這麼唱，「你不需要氣象員也知道，風要吹向何方」（You\ndon\'t need a weather man to know which way the wind blows）現在職場吹的是什麼風，相信不需要專家指點，人人都能感覺得到，在未來，讀財金的人不能再只會看數字，更要能夠懂得程式語言；而工程師也不再因為會寫程式而吃香，還需要跨領域的專業知識，才能夠在茫茫人海中突出。而這過程中，能夠同時進行金融與科技交流的「跨界人才」絕對是炙手可熱的企業新寵。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>資料分析</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>金融科技服務主要是以線上化、便捷化為基礎，奠基於使用者的網路使用習慣，然而這部分是傳統銀行所缺乏的，因此必須找來各領域的金融人才，包括社群經營、用戶體驗來設計出符合客戶使用需求的創新金融服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>然而這部分必須透過應用資料分析的人才幫助設計者更精準地了解客戶需求，在對的時間提供對的產品服務，因此，培育資料分析人才更是銀行業壯大的重點工程；我們不能錯過任何培養專業的機會，假如你是一名大學，應該試著兼修資訊科技的相關課程培養coding的能力，如果覺得學校教的不夠全面時，也可利用線上廣泛的學習資源自學，讓自己成為一名炙手可熱的資料分析人才。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>科技創新</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>除了資料分析能力，金融科技人才也應該從「原始數據使用」進步到「改善數據獲得方式、增加用戶體驗」，要常問自己\n有沒有方法可以獲得更具判斷性的data、有沒有方式可以更佳優化用戶體驗，透過這種模式，找出潛在商機，打造出不一樣的商業模式，創造更多新的金融服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>數位行銷</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>資料分析與科技創新，都屬於技術開發層次的規劃；然而當實際一項新的金融商品或服務被開發出來時，要如何推廣給使用者知道才是大重點；新一代的金融科技人才必須具備「同理心」用不一樣的思維觀察大眾的想法、聆聽使用者的心聲，才能知道如何行銷自身的產品，讓服務與產品能夠打動人心。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>參考資料：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://www.bnext.com.tw/article/38928/fintech-talents-need-3-abilities\">https://www.bnext.com.tw/article/38928/fintech-talents-need-3-abilities</a></p>\n<!-- /wp:paragraph -->', '2018-10-18 18:46:43', '【Fintech】如何成為一位搶手的金融科技人才？', 'article', 'publish', '2019-01-02 18:54:04', '', 0),
(2697, 1, '/upload/2019/01/02-3.jpg', NULL, '<!-- wp:paragraph -->\n<p>每個月等著信用卡帳單時的你心情是如何？拿到帳單後還要抽空在繁忙的生活中繳納又是多麼不方便的一件事。但是！現在我們不用再為這些瑣事煩惱了！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>所有東西一「掃」而定</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Visa及萬事達卡兩大信用卡組織，在台積極推動QR code掃碼支付服務，優化消費者帳單支付過程，讓台灣消費者能夠在安全及便利兼顧的前提下使用行動支付，預估今年11月即將上線！多數持卡人將在未來直接使用手機掃描帳單上的QR code，以連結Visa金融卡或萬事達卡Debit金融卡，繳納該信用卡帳單，如此便利快速的服務勢必能造福廣大民眾！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Visa與財金公司合作，首波已有22家發卡機構同意加入信用卡帳單QR code繳費，其中16家Visa金融卡發卡行將提供QR code手機app予消費者綁定Visa金融卡，進行掃碼支付。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>萬事達卡方面，則是全面支援聯合信用卡處理中心（NCCC）與財金公司（FISC）兩大QR Code平台。未來萬事達卡Debit 金融卡與多家銀行完成串接後，持卡人即可使用手上的Debit 金融卡，進行全方位QR Code支付服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>小結</strong><strong></strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>隨著現今行動支付越來越普及化的趨勢，許多因應的金融服務也雨後春筍般的浮上檯面，愈來愈多的優化服務使我們可以在日常生活中利用行動裝置進行更多的交易行為，不管是繳費、轉帳抑或是購物，每件事都能省去更多的時間和力氣！而在未來又會有甚麼令人為之一嘆的服務？讓我們拭目以待！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"http://www.cna.com.tw/news/afe/201808300300.aspx\">http://www.cna.com.tw/news/afe/201808300300.aspx</a></p>\n<!-- /wp:paragraph -->', '2018-10-19 18:54:09', '【時事分享】QR code 掃碼支付即將上線！', 'article', 'publish', '2019-01-02 18:58:28', '', 0),
(2705, 1, '/upload/2018/10/1023.jpg', NULL, '<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><br> 在2010年尾聲，一種叫做”Fintech”的東西悄悄誕生，短短十年不到，奪走銀行<br> 的利基、逐漸擴大金融市場佔有率，使得銀行不得不開始和Fintech合作創造出<br> 新興的合作模式。<br> 文案撰寫人：Tommy<br> 銀行一開始對Fintech是保持不削一顧的態度，直到Fintech開始剝削他們的客戶<br> ，展現出更快、更敏捷的商業模式時，銀行才開始不得不合作。<br> 與其相爭，不如共榮共生。<br> 知名編輯 Alessandro Hatami依照傳統銀行與Fintech在品牌合作和銀行投資<br> Fintech的程度做為考量，將合作模式分成四種樣貌。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>通路型(The Channel)： Fintech透過銀行的管道進行銷售<br>\n銀行在不提供用戶資料的情況下將銷售管道提供給Fintech做為使用。<br>\n這對銀行的益處是可以利用相對低成本的方式提供新服務給原先客戶(伸長<br>\n自身觸手)，另外也可以試水溫了解客戶對這項新服務的接受度為何，銀行<br>\n也隨時可以選擇隨時離開這個市場、自己開設Fintech部門或加強與Fintech合<br>\n作關係，具有三種選擇，進可攻、退可守。<br>\n而對於Fintech而言，他們可以直接對新的客戶提供服務，與大銀行合作提升<br>\n自身的品牌形象，擴展自己的業務範圍。<br>\n然而這項策略的缺點是，銀行等於是在販售自己的通路交換市場情報<br>\n，Fintech隨時可以抽手離開。<br>\n例子：<br>\n超商提供通路給廠商販售生鮮產品，透過自己的通路，在低成本自身不用做<br>\n的情況下，了解消費者對於生鮮產品的接受度如何，同時廠商也利用通路提<br>\n升自己的能見度。</li><li>輔助型(The Supplier)： Fintech的服務包覆在銀行的產品中<br>\n和通路型的不同地方在於，銀行與Fintech在品牌上具有高度的合作，雖然通<br>\n路型也可以測試客戶對Fintech的使用習慣，但比較起來，如果合作成功，銀<br>\n行可以和客戶建立起更緊密的互動關係，品牌信賴度也會急速攀升。<br>\n對於Fintech而言，Fintech的品牌受到很大的保護，當產品發生問題時，由於</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>消費者都認為是銀行推出的產品，銀行做為品牌本身必須出面負責，而<br>\nFintech品牌本身就不會受到太大的衝擊。<br>\n另外輔助型最大的特徵在於Fintech的非唯一性，由於Fintech只提供服務，而<br>\n非品牌，可以同時與多家銀行進行合作，這使得銀行本身需有心理準備。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>天線型(The Satellite)： Fintech被高度投資<br>\n這是協作型的進階版，銀行高度投資Fintech避免其提供產品給與其他銀行(<br>\n排他性)並保障原先Fintech團隊的獨立性(因為沒有合併)，對於Fintech而言，<br>\n他可以透過銀行投資和直接使用銀行的用戶資料來擴展業務，而銀行則透過<br>\n低品牌合作、高投資的方式來驗證Fintech的商業模式，同時不損害到自身已<br>\n存在的業務。<br>\n這個模式的壞處有兩個，Fintech公司的創辦人可能因為有大量投資，導致做<br>\n事起來覺得綁手綁腳，第二個是，假如Fintech失敗，銀行就要賠得一屁股。</li><li>併購型(The Merger)： Fintech直接被吃掉<br>\n通常銀行會這樣做代表本身業務和Fintech並無重疊，這種方式的好處在於銀<br>\n行可以隨心所欲，可以利用自身的力量加強創新，也可以利用自身的客戶數<br>\n創造更高的價值。但壞處也相當明顯，在金融業相當封閉的環境裡，創新、<br>\n開放的Fintech通常彼此公司文化、營運模式會大相逕庭，銀行使用這種方法<br>\n的最大困難點就是管理不易或理想不同。<br>\n在四種模式中，沒有最好的，也沒有最壞的，只有審時度勢，才能立於不敗之<br>\n地。<br>\n然而可以預見的是，未來銀行與Fintech的合作將會越來越多，GAFA(谷狗、蘋<br>\n果、臉書和亞馬遜)都已經嘗試在金融服務上下手，蘋果的apple pay、谷狗支<br>\n持的android pay 、Facebook試驗的P2P支付，如果他們開始積極地踏入金融領<br>\n域，代表其實金融界的真正戰鬥才正要開始。</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>參考資源：<br>\nBank &amp; Fintech Cooperation Model ---http://fintechnews.hk/6363/various/bank-<br>\nfintech-collaboration-models-how-big-banks-plan-fight-the-big-tech-challenge/</p>\n<!-- /wp:paragraph -->', '2018-10-23 15:52:39', '【Fintech】傳統銀行和Fintech的商業模式', 'article', 'publish', '2019-01-03 16:09:09', '', 0),
(2706, 1, '/upload/2019/01/未命名-1-1.jpg', NULL, '<!-- wp:heading -->\n<h2>引言</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>履歷是讓你成功取得面試機會的一項重要考核，但千萬不要因為犯了以下錯誤，讓你的履歷表出現在公司垃圾桶裡！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>五個千萬別犯的錯</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>開始讀文章前請融入以下情境：假設你只有三分鐘，必須閱讀完一段不熟悉的文字500字，並且用兩三句話總結那段文字。你會如何閱讀？著重在哪些部分？又或者什麼樣文章的陳列會讓你更容易的閱讀？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>企業的人事部門一天肯定處理不下百封的求職邀約，做個簡單的計算：一封履歷至少500字，一百封履歷就得看上50,000個字，就算每封只花3分鐘，你也得花上五小時看完。在有限的時間下換作是你，你覺得你會花多少時間看一份履歷？還有你會怎樣看一份履歷？！因此，衡量我們寫履歷的目標：讓公司對我的經歷親睞，還有我們的有限資源：人事部門閱讀履歷的時間，我們應該避免以下錯誤，來增加自己履歷被接受的可能性。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>文字、語法錯誤：雖然說小錯不傷大雅，但從小錯可以透露公司你對職缺的態度，並強烈的影響繼續閱讀的意願，這點可以從兩方面思考：<ol><li>讓履歷的閱讀者反覆地讀你使用錯誤的語法句子，這無疑是讓他們感覺浪費時間而已，設身處地的想一下，當你閱讀完你朋友的履歷後不知其所以然，你會覺得你閱讀能力不好，還是朋友寫得爛？！</li></ol></li></ol>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>小錯可能表示撰寫者沒有仔細檢視自己的履歷，某方面給閱讀者一種做事不專一、不認真的反應，當這些錯誤在幾百份履歷中成為少數的個案，自然就有可能變成篩選的標準。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>錯誤示範：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯金融科技股份有限公司&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2018年10月開始任職</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>展現於市場分析之能力，看目標客群進行習慣行為及心理分析，符合客群的文案吉網站編排，成功讓公司暴光度上升50%並且提升粉絲團人數100人每月。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>正確示範：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯金融科技股份有限公司&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2018年10月開始任職</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>展現市場分析能力，針對目標客群進行行為及心理分析，設計符合客群的文案及網站編排，成功讓公司曝光度上升50%並且提升粉絲團人數每月100人。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>過於冗長的內容：時間就是商業的金錢，雖然你花費了不下幾個夜晚苦練成幾頁的文字，但實在抱歉，閱讀的人真的沒太多時間，同樣一件經歷能用一句話表達何必用到三句呢？重質而不重量啊！</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>但是如何叫做有效率地表達呢？可以參考一下的思路：C.A.R</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>Challenge（挑戰）：同一個經歷一定會讓你學到某些技能或成長，而往往這些事情都是來自於遇到的挑戰，因此從挑戰發想每個經歷的寫作方向，能提供你明確的思路，也方便確定完整地表達自己透過各經歷而學到的技能。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>Action（對策）：此過程反映出撰寫履歷者的能力，以及藉此具體地說服履歷閱讀者你真的經歷過這些事項，並且顯示你能夠克服各項難關。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>Result（結果）：此處最好能以數字化的方式述說結果，與其描述相較軟實力的成長，業績上升率、成交量、獲利率等能數據化的資料能較抓住讀者目光，並且快速地被吸收。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>錯誤示範：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>我從2018年10月開始任職普匯金融科技股份有限公司，在任職期間我展現市場分析能力，發揮學校所長及自我興趣針對目標客群進行行為及心理分析，並且我還設計了符合客群的文案及網站的編排設計，最後終於成功地讓公司曝光度上升非常多並且明顯的提升粉絲團人。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>正確示範：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯金融科技股份有限公司&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2018年10月開始任職</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>展現市場分析能力，針對目標客群進行行為及心理分析，設計符合客群的文案及網站編排，成功讓公司社群媒體曝光度上升50%並且提升粉絲團人數每月100人。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>格式複雜非簡單一至：不要忘了剛開始我們的情境設定，你只有三分鐘！如果又因為必須熟悉格式讓閱讀者多花了幾秒，這樣真正閱讀內容的時間又變少了，反而促成達到目的的反效果，當然若你是設計、創意等較需設計軟實力的職位，適當展現出你的長才確實有加分效果，但若你是一般的企業應徵，用種謹慎的方式處理格式，把重心放在履歷內容相對比較有效果。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>不適當的基本資料：履歷其實充斥至少三分之一的版面在講述自己的基本資料，但你有沒有想過，其實這些基本資料會被閱讀的條件是，其他三分之二的內容吸引到履歷的閱讀者，以下就不適當的基本資料分別做個介紹：<ul><li>過多的篇幅撰寫基本資料：履歷重點在經歷的撰寫，取得閱讀者深刻的印象，只需要有關鍵的基本資料（ex:手機號碼、電子信箱）公司自然能找得到你做進一步資料的索取。</li></ul></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>不適合的照片：自拍、修圖過多或者太過私密的照片不適合放在履歷表上，當然不放上學士服照片能夠降低整體履歷嚴肅的程度，讓閱讀者感覺較親近。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>錯誤的基本資料：務必嚴加檢查基本資料的正確性，萬一真的成功進到了下一關的審核，公司也會因為聯繫不上你而向隅。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:list -->\n<ul><li>資料造假：千萬千萬不能犯了這條大忌！人與人互動最重要的是講求誠信，因此不要妄想一時的隱瞞能造就美滿的職涯，當東窗事發而眾叛親離，你將會被自己的欺騙反噬，搞不好終生不得平反。就算你想透過換工作洗刷，有些雇主會聯絡前任雇主了解你之前的工作態度及經驗，屆時你也無力反駁。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>履歷是一張紀錄自己過去並開啟你未來職涯重要的紀錄，因此避免些履歷的錯誤能讓你職涯走得更加順遂，但切記千萬別被履歷綁死，不要為了增加履歷的豐富度而恣意地一直換工作，工作中的學習才是履歷表的真諦，徒留表面的經歷，你的人生不會如你寫的那樣精彩。</p>\n<!-- /wp:paragraph -->', '2018-10-22 15:51:33', '【普匯觀點】大學畢業生看這邊！你的履歷絕對不要這樣寫', 'article', 'publish', '2019-01-03 15:53:49', '', 0),
(2711, 1, '/upload/2019/01/未命名-1-2.jpg', NULL, '<!-- wp:heading -->\n<h2>前言：</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>AI後浪推前浪，前浪躺在沙灘上。怎樣的人會是躺在沙灘上的前浪，而什麼樣特質的人又不會呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>本文</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>麥肯錫全球研究院（McKinsey\nGlobal Institute）於2017年1月發表〈未來工作：自動化、就業與生產力〉，報告指出在800個職業中的2,000個工作項目內，將有5%能被自動化科技（AI科技的一種應用）完全取代，在未來60%的職業別中，更有30%的工作項目能實現自動化，半數現今的工作內容也將在2055年被AI取代。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>現在，讓我們來一窺什麼樣的職業可能被AI取代吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>最可能被取代的工作</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>重複可流程化勞力的工作：從事連續性、可流程化的勞力工作。例如：搬運工人、物流人員、生產線等製造業人員、農業人員、計程車及大眾運輸司機甚至是交通警察。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>阿里巴巴在杭州推行「城市大腦」計畫，推出AI巡邏系統「天曜」，透過全市的路口攝影機以圖像辨識查獲違規，20秒內自動報警，且擁有高達95%的準確率，傳統靠警察人工舉發的交通違規勤務將可被替代。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>服務工作：現今許多餐飲、飯店等服務業為了提供專一的顧客體驗，都會將服務內容流程化，應付任何狀況也都可以遵照訂定的SOP。例如：飯店服務生、送餐服務生又或者部分廚師。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>在台中逢甲商圈的鵲絲旅店，主打完全無人化的服務，透過自助報到機，客戶可以從連線的設備進行check-in、付款、證件拍照並領取房卡，自動化的手臂也提供客人寄放行李，24小時的線上訂餐系統，將會有專門的機器人從大廳送餐點到客戶房門。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>校稿工作：單純的檢查文字內容，聽打或者關鍵字查詢、擷取及整理的工作。例如：律師助理、文字記者或者書記官。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>Ross intelligence起初是基於IBM Watson所開發的職務，線上的查詢平台，打入關鍵字，透過NLP（自然語言處理）技術，系統會提供法條、判例以及重點段落，宛若律師就在面前，目前該服務也正與各大律師事務所合作。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>會計記帳工作：單純的紀錄日記帳、匯總總帳以及編製相關報表的工作，基礎的審計業務執行也包含在內。例如：初階會計事務所人員、公司會計財務部員工。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>目前四大會計師事務所EY、Deloitte、PwC、KPMG都表明將會導入AI在他們的審計、稅務或顧問等相關部門。舉Deloitte 為例，他們有幾項不錯的AI計畫分別是：Tax-I（歐洲判決視覺化匯總及分析）、Sonar（偵測資料庫中可能的錯誤）、Chatbot（聊天機器人回答大量客戶提問）、Alice the care robot（老年照護機器人）</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>財務服務及分析交易工作：監控多項指數、計算獲利及做出交易決策的工作，匯總各項指數、生成財務報告並作出財務建議的工作，又或者銀行臨櫃提供的金融服務。例如：股市、債券交易員、理專以及銀行行員。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>在2016年，紐約有一家公司Sigmoidal發展顧問服務，主要讓AI導入財經、營建、軍事等客戶，該公司也宣稱使用AI演算交易於股票市場，透過統計模型機器高速決策及進行市場交易。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>被取代的工作特性</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>比較一下上述的工作項目，我們可以歸納出最可能（抑或最快）會被AI取代的工作性質有：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>可預測（重複性）工作項目</li><li>整理匯總資訊的工作</li><li>蒐集資訊的工作</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2>最不容易被取代的特質</h2>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li><strong>創新力</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>李開復先生在台大的畢業典禮上曾說過：「AI主要是優化每一個領域的精確度，但是AI是不會創新的」我們的機會是在於創新和發明前所未有的技術，開拓不同的領域才是未來真正的競爭力所在。又好比已經有人透過AI成功地模仿莫內的畫作，但是如果給AI一張白紙要他畫一幅作品，AI是辦不到的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>整合力</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>如今取得資訊及匯總資訊的工作如上述已經能被AI逐漸取代，AI在這方面領域能夠做得比多數人類快又好，但匯總完成的資訊若無法被有效使用，它始終無法發揮價值，因此如何在眾多匯總資訊裡辨認決策相關的資訊並且加以整合，是人類未來的優勢，當然這裡整合的內容不僅是數據化的資料也包含無法數據化的各項考量。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>社交能力</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>人與人的交流是有感情、溫度的，AI不懂美也不懂幽默，或許聊天機器能夠辨識某些字眼進而做出相關回覆，但AI不懂眼神及肢體語言、無法辨認出跟他交流的人內心究竟在想什麼，當然就更不可能完成一場有溫度的社交談話。先前Google Duplex展示的AI語音助理能夠自行預約理髮，雖然任務是達成了，但事實上Google 語音助理的語調跟真人還是有段差距，只是在電話裡不容易被分辨，由此顯示，要讓AI比照真人進行溝通，還是有難度的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>專業領域上的精進</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>李開復先生在同一個演講裡也說道：「專家包括了各垂直領域的頂尖專家和各行各業專業人士。雖然我認為大部分記者會被 AI 取代，尤其是財經新聞、體育新聞，AI 今天已經比大部分記者寫得更快、更好，而且不會犯錯。但是 AI 肯定無法寫出比高希均教授更好的經濟評論。雖然AI 已經開始寫小說，但是 AI 肯定寫不出比龍應台更好的散文。」頂尖的專家扮演的是描繪藍圖、開拓專業領域及複雜性思考的角色，這些都是AI所不能企及的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>管理能力/最終決策權</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>管理能力其實是種綜合的能力，它包含上述所有的能力，並藉此達到管理的功效，因此在AI無法擁有那些能力的前提下，人類當然也就無法輕易地被取代。雖然 AI可以進行最優化的投資建議或者提供演算後的最佳解答，但就法律層面、社會層面，AI還是沒有辦法取得最終的職權及負起相關責任。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>結語</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>富比士雜誌曾經下了個標題「Artificial Intelligence Will\nReplace Tasks, Not Jobs」意思是：「AI取代的是工作的內容，而不是職業本身」也就是說，我們不必擔憂將來因為AI而失去工作，只是工作內容會有所轉變罷了。你可能會擔心自己是否能勝任未來的職場環境，但其實只要抓住身為「人」的優勢，培養上述AI所不能及的軟實力，懂的思考並自我充實，就能在這個AI興起的時代脫穎而出，況且距離MGI報告指出的2055年還那麼久，機會一直都在，只怕你不開始行動。</p>\n<!-- /wp:paragraph -->', '2018-10-24 15:54:29', '【Fintech】在AI時代下，未來我們的工作在哪？', 'article', 'publish', '2019-01-03 15:57:28', '', 0),
(2713, 2, '/upload/2019/01/1101-01.png', NULL, '<!-- wp:heading -->\n<h2><strong>引言</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>第一桶金看似遙不可及，投資又感覺好難。但各位大學生們，請務必學會這三個心法，讓你比同儕更快更穩健地賺進人生的第一桶金！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>本文</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>在開始本文前，讓我們先看清一下事實，假如小匯他是一個剛出社會的上班族月領25,000元，在台北租個小套房月租8,000元、電話費499元、伙食費8,000元、通勤費 1,280，這樣他一個月還剩多少閒錢：25,000- 8,000*2- 499- 1,280 = </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong><em>7,221</em></strong>，不算跟朋友聚餐、出去玩或者買衣服、手機，血淋淋的事實是否有如當頭棒喝？！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>我們的日常開銷隨著物價的上漲逐漸增加，剛出社會或者打工賺來的薪水又這麼的「求溫飽」而已，每個月多餘的閒錢雖然放在銀行裡，但也不知道能有什麼投資的選擇，深怕一個不小心辛苦賺來的血淚就這樣付之一炬。但各位夥伴們，其實只要你只要知道這三項投資原則，便可以相較穩健地取得比銀行利息更好的報酬。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>分散風險</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>老話一句「雞蛋不要放在同一個籃子裡」，這句話就是在講風險的控制，在介紹分散風險的概念前，我們把投資會遇到的風險基本分成兩項：<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>系統性風險：此種風險是市場全面性的風險，他是指整個投資環境會面臨的風險，基本上這樣的風險比較難預防也相較無法透過分散進行有效降低，但一般系統性的風險會比大部分的非系統性風險還要低。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>舉個生活中的比喻：如果把投資想像成搶五X天的跨年演唱會門票，那系統性風險就像是訂票網站被駭入或者全台大停電，它無法透過開團搶票防止，可是它會發生的機率也不高。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>非系統性風險：此種風險就可以透過分散的方法進行有效的抑制，也因為他可以透過分散防止，這也是我們主要關注的風險對象。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>舉同一個例子：這時候的非系統性風險比較像是你想要的票種被搶光了，但是你可以透過多人聯合搶票來防止這個悲劇，而且其實沒搶到票的可能性也相對的機率高很多。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>回到分散風險的議題，每個投資項目都會有自己的非系統性風險，一件事件、新聞或財報消息都會對不同金融商品的價值有不一樣的影響，舉個市場規律：一般股票市場的報酬率跟債券市場屬於反向關係，因此如果你同時擁有這兩項投資標的，市場消息造成的價格變動較能相互抵消，而這就是分散風險的目的。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>小額投資</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>既然我們年輕人手上的閒錢也不多，依照剛剛分散風險的概念，我們當然不可能把資金大筆梭哈在投資上又或者單一投資標的上，小額投資使你在能掌控的情況下，爭取額外的獲利報酬。雖然小額投資所取得的投資報酬絕對值可能低，但是投資看中的其實是報酬「率」，當你累積越多的資金，報酬率換算成報酬金額也會越可觀，一千塊的5%是五十塊，十萬塊的5%&nbsp;就是五千塊，因此由小額投資做起，將來你就能累積越多的財富。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>市場上也有很多值得留意的小額投資選擇：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>基金投資、高價股的零股、P2P線上借貸平台、股票權證、選擇權等</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>重視複利</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>股神巴菲特的名言：「人生就像滾雪球，最重要的事是發現濕濕的雪和長長的山坡」濕濕的雪就是指不錯的報酬率投資標的，而長長的山坡則是指需要足夠的時間進行投資，也就是指長時間的複利所帶來可觀的報酬。更清楚地解釋複利的效果，我們用以下的表格進行說明，投資標的A的投入金額為10,000且報酬率5%，投入5年；投資標的B投入金額為10,000且報酬率5%，但投入10年，我們來驗證一下金額會因為五年的複利差多少。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>期數</td><td>0</td><td>1</td><td>2</td><td>3</td><td>4</td><td>5</td><td>6</td><td>7</td><td>8</td><td>9</td><td>10</td><td>報酬率</td></tr><tr><td>A</td><td>10,000</td><td>10,500</td><td>11,025</td><td>11,576</td><td>12,155</td><td>12,763</td><td>停止</td><td>28%</td></tr><tr><td>B</td><td>10,000</td><td>10,500</td><td>11,025</td><td>11,576</td><td>12,155</td><td>12,763</td><td>13,401</td><td>14,071</td><td>14,775</td><td>15,513</td><td>16,289</td><td>63%</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>報酬率的公式分別為：A投資標的：[(12,763 – 10,000)/ 10,000]*100 ≈ <strong>28%</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Ｂ投資標的：[(16,289 – 10,000)/ 10,000]*100 ≈ <strong>63%</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>由此可知時間越長，產生的複利效果就會更加的明顯，就算兩者的投資報酬率剛開始都只有5%，但經過不同期數後，竟然得出了驚人的28%以及63%報酬率。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>回到文章開始時小匯的故事，假設小匯一年只要拿出10,000的錢，這樣10年後他可以賺得16,289這麼多，如果小匯勤儉地一年拿出 40,000元投資，則10年後則是65,200，何況這只是一年而已，如果每年小匯都做了同樣10,000元的投資，則連續10年後，小匯在第10年底總共會取得高達132,068元！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如果今天小匯投資的是小額債權的標的，連續五個月下，每個月固定投入1,000元、標的年利率5％、每個月複利、期數5期也就是五個月，而且每期收到的本金及利息再繼續投資相同特性的小額債權，投資統計資料如下：（考量每個月收取款項時1%手續費）</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>年利率5%換算每月利率 = 5%/12</li><li>以第一個月的標的計算每期收款 = 1,000*5%/12 ≈ 202 (a)，考慮1%手續費後約為200(b)</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>期數</td><td>每月本金</td><td>每月回收</td><td>每月投入</td><td>第ㄧ標的每月收款</td><td>第二標的每月收款</td><td>第三標的每月收款</td><td>第四標的每月收款</td><td>第五標的每月收款</td></tr><tr><td>0</td><td>1,000</td><td>1,000</td></tr><tr><td>1</td><td>1,000</td><td>200(b)</td><td>1,200</td><td>202(a)</td></tr><tr><td>2</td><td>1,000</td><td>441</td><td>1,441</td><td>202(a)</td><td>243</td></tr><tr><td>3</td><td>1,000</td><td>730</td><td>1,730</td><td>202(a)</td><td>243</td><td>292</td></tr><tr><td>4</td><td>1,000</td><td>1,077</td><td>2,077</td><td>202(a)</td><td>243</td><td>292</td><td>350</td></tr><tr><td>5</td><td>1,493</td><td>1,493</td><td>202(a)</td><td>243</td><td>292</td><td>350</td><td>421</td></tr><tr><td>6</td><td>1,293</td><td>1,293</td><td>243</td><td>292</td><td>350</td><td>421</td></tr><tr><td>7</td><td>1,052</td><td>1,052</td><td>292</td><td>350</td><td>421</td></tr><tr><td>8</td><td>763</td><td>763</td><td>350</td><td>421</td></tr><tr><td>9</td><td>416</td><td>416</td><td>421</td></tr><tr><td>標的總報酬</td><td>7,540</td><td>1,012</td><td>1,215</td><td>1,495</td><td>1,751</td><td>2,103</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>投資回收金流：5,017 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;原始投資現值：4,958.68</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>投資報酬率：[(5,017 – 4,958.68)/ 4,958.68] * 2.4 ≈ 2.85%<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但投報率必須經過比較才更有目的性，以台灣銀行公告的定存一年期約莫0.8%，而美國國庫券一年期的投報率為2.55％左右。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>定存0.8% V.S. 美國國債2.55% V.S. 普匯小額債權2.85%</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以投報率的角度來看，小額債權的投資然能在10個月的時間內，穩健的擁有如此複利表現，相對定存來說高了不少，再加上相較安全的特性，對於小匯這樣年輕的上班族可以說是更好的投資選擇。近期興起的P2P線上借貸平台，也提供了這類型的投資機會，讓我們年輕人能藉由小額投資進行分散且複利特性的投資，而這類型的平台也會有相關風險的管控以及法律上的保障，因此相對也比較安全。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>小結</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>每個人的人生起跑點本來就不同，如果你不是已經很有錢，那請你儘早開始你的投資生涯，大學生處於人生開始衝刺的階段，雖然我們取得的資本額少，但是學會如何投資將會讓你的財富與同儕在未來會有很大的差別。提早開始投資，你的財富會像複利般在未來拉開差距。但也切記，在還未充分準備下千萬不要過分的操做高槓桿項目，選擇相較穩定的小額債權投資也能藉此掌控投資組合內的風險。<br></p>\n<!-- /wp:paragraph -->', '2018-11-01 12:05:07', '【普匯觀點】大學生投資心法', 'article', 'publish', '2019-01-03 16:07:46', '', 0),
(2719, 1, '/upload/2019/01/1025.jpg', NULL, '<!-- wp:paragraph -->\n<p>歷經資安疑慮、用戶流失嚴重的Facebook正走在社群事業的下坡，然而其於<br>\n2012年收購的子公司instagram(IG)卻逆風高飛，順勢承接Facebook流失的人<br>\n潮，逐漸成為社群網站的龍頭，更打算推出線上支付一舉跨足電商<br>\nInstagram內付款，不再跳轉頁面<br>\n已擁有超越10億的活躍用戶人數、800萬企業品牌帳號的instagram，透過簡潔的<br>\n介面、一指化的操作流程打下社群軟體的一片天，其中更有高達8成的用戶天天<br>\n觸及品牌帳號，在龐大的用戶基礎後，隱藏著數以百億計的商機。<br>\n其實早在2016時，Instagram就推出過購物標籤(#shoppable)的功能，使用者在點<br>\n擊商品標籤後，即會自動跳轉到該品牌的網站讓顧客下訂單，但其實這種模式<br>\n有很大的缺陷，原因在於用戶常常在滑手機時誤觸標籤，IG馬上跳轉到該品牌<br>\n網站，用戶就必須再多按好幾次按鍵回到APP內，對用戶來講是一種很大的操<br>\n作負擔，因此Instagram的新支付功能，將能讓使用者從看到商品、下好訂單到<br>\n完成購物流程都不離開App，提升購物效率也降低使用者操作難度。<br>\n日前instagram宣布，目前已有部分餐廳及美髮店能使用此服務，未來將會推廣<br>\n更多的支付情境，像是直接購買電影票等等，另外未來也將於探索介面中啟用<br>\n購物的專屬頁面，提供使用者個人化的商品推薦，將會在全球46個國家中陸續<br>\n推出。<br>\n為甚麼Instagram有機會成為電商霸主？<br>\n其實Instagram優勢主要有三個，「和母公司Facebook擁有龐大的用戶基數、<br>\n本身是高度追求個人興趣的平台、年輕用戶將近8成」。<br>\n第一，截至最近，Facebook台灣區用戶數達1900萬，佔台灣人口8成<br>\n，Instagram用戶則超過750萬，佔台灣人口的3成，以用戶基數來看，不管是臉<br>\n書還是IG都早已遙遙領先其他社群軟體，未來跨足電商的IG、Facebook，不需<br>\n要特別打廣告，用自己既有的龐大用戶數，就可以輕鬆處及用戶、打造一個嶄</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>新的電商王國；<br>\n第二，instagram比起Facebook走的路線完全不同，IG是一個非常高度個人化的<br>\n平台，用戶幾乎只追蹤自己喜歡的品牌、廠商，不同於使用Facebook接收全方<br>\n位的資訊，站在電商的角度來看，簡直就是完美的客群分析，能夠更精準的投<br>\n放廣告給有需求的潛在消費者。<br>\n越來越多的髮型設計師、自由工作者、廠商喜歡將自己的作品呈現在instagram<br>\n上，除了既有的功能外，加上即將開放的線上支付改善購物流程，未來顧客將<br>\n不必離開App就能下單，一指完成所有操作。<br>\n而在百家爭鳴的電商市場，Instagram的加入，勢必將掀起另一波電商風潮。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>資料來源：<br>\nhttps://www.dgcovery.com/2018/04/15/2017-social-media-analysis-report/<br>\nhttps://cnews.com.tw/005180505a01/<br>\nhttp://cacafly.com/?p=25897</p>\n<!-- /wp:paragraph -->', '2018-10-25 16:00:41', '【Fintech】電商喪鐘？instagram開放線上支付跨足電商', 'article', 'publish', '2019-01-03 16:03:02', '', 0),
(2722, 1, '/upload/2019/01/1029.jpg', NULL, '<!-- wp:paragraph -->\n<p>面試是讓你取得夢幻實習、領到夢想公司員工證的必備過程，快讓我們學習三點面試技巧，大大增加你的成功錄取率吧！<br /><br /></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>面試是最直接與企業互動的方式，個人的人格特質、談吐習慣甚至是臨場反應都是面試官在面試過程中考量的因素，因此本文將教你：如何在短短幾分鐘內的互動讓面試官印象深刻，為你打造面試場上的不敗之身！</p>\n<!-- /wp:paragraph --><!-- wp:list {\"ordered\":true} -->\n<ol>\n<li>充分瞭解面試企業</li>\n</ol>\n<!-- /wp:list --><!-- wp:paragraph -->\n<p> 瀏覽企業官方網站或粉絲團：如今的企業重視形象的建立與宣傳，因此從這些線上的網站幾乎都能取得足夠企業資訊。首先，了解企業的經營理念及願景，這不僅是認識公司的第一步也是衡量自己是否適合該公司的重要管道；其二，了解企業的產品或服務項目，這方法能最直接地了解公司的營運內容，對於未來工作的想像也能夠更具體，面試答題時當然也更有建設性。<br /> 瀏覽企業相關報導：新聞報導的即時性有時候比官網更新的速度還要來得快，如果是正面評價的新聞，可以在面試回答中適時的融入以增加回答豐富度及深度；若是負面的新聞，則可以預想新聞報導的正確性，並針對該事件提出自身的觀點。<br /> 搜尋該企業的職缺方向：企業公開徵求的職業別，在某方面顯示公司的發展的方向，它們不會平白擴編，因此透過這樣的觀察，能讓面試官瞭解你的洞察能力以及對於該職缺的適任能力。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>2.不要表現你會什麼，而是你能為公司做什麼</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>這當然不是指你完全不用審視自己的經歷及能力，而是要將這些能力整合，並且連結到公司職缺上的需求。想想看，面試官一天面試了無數的求職者，若將應試者一個個配對到待補的職缺，過程將會相當繁瑣耗時，這時你若貼心地提供明確的線索給面試官，當然大大地增加錄取可能性。我們可以從三個層面著手：<br /> 彙整自己過去的人生經歷：既然要完備的連結自己的能力與公司職缺的需求，第一步當然是匯總自己的能力，先表列出自己過去社團、學業、校外比賽甚至作品集，再歸納出這些經驗分別讓你獲得了什麼能力或體悟。當然若有相關學術文章、作品集及相關數據化的成果，更可以主動地提供給公司，讓主管在你的專業度表現留下好印象。<br /> 你為什麼適合這間公司：這問題可以從前面第一點了解到的公司願景思考，屬於精神層面的準備，從公司願景發想自己的觀點並動之以情，增加面試官的共鳴，用事實配合自己的人生經歷，營造一種與企業理念契合的精神。<br /> 你為什麼適合這個工作：此屬執行力、適任能力的準備，需要更具體地把自己的能力與職缺的要求做直接連線，配合你匯總出來過去的人生經歷，呈現地越明確越好！若能更進一步地與企業未來營運計畫連結，也會有加分的效果哦！</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>3.自信</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>相信自己！自信能轉變你的心態、增強你散發出的個人魅力、完備展現你事前的準備以及穩定你臨危的反應。換個角度想，當你/妳跟曖昧對象第一次約會時，你會希望對方是什麼樣的人？沒錯！自信能讓你變成你想要的那個樣子！很多人沒有自信是因為害怕結果，但是仔細思考一下，面試前的你還沒取得該職位，而面試後最差不過也跟現在一樣不是嗎？就此多學得經驗搞不好還取得了工作，因此自信地放手一博感覺是不是很划算！<br /><br /></p>\n<p>普匯觀點</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>充分掌握好這三點技巧，相信你一定能在面試場上無往不利，當然千萬不要有過度的得失心，面試結果並不是絕對的評量，也許只是不適合這間公司或職位，當然若你成功被錄取了，也不要忘了當初進公司的理想，好好奮鬥吧！</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<h3><strong><a style=\"color: #ff0000;\" href=\"https://www.104.com.tw/company/1a2x6bkhzg\">歡迎加入普匯大家庭！</a></strong></h3>\n<!-- /wp:paragraph -->', '2018-10-29 16:03:09', '【普匯觀點】面試技巧讓你知，掌握三要點提升面試成功率', 'article', 'publish', '2020-05-26 16:33:54', '', 0),
(2725, 1, '/upload/2019/01/未命名-1-4.jpg', NULL, '<!-- wp:paragraph -->\n<p>在現今的時代，許多學生還有社會新鮮人都懷有創業夢，也許是想圓自己一個夢，也許是不想替別人工作，根據調查顯示，將近80%的工作者都有過自行創業的念頭，創業的理由有很多，案例更是數不清。跨出創業的第一步看似容易，真正執行起來卻是難如登天，除了要有對的方法，也要知道創業的風險，尤其在台灣這個創業精神全球第一，創業成功率卻只有1%的國家，更是要審慎評估，以下就讓我們來個創業淺談吧！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>如何開始創業行動</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>想要創業但卻不知道從何開始？跟著以下四個步驟來釐清自己的思緒：寫下來、讀資料、深入瞭解、取得回饋。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>【寫下來】</h2>\n<!-- /wp:heading -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>1. 定義自我</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>不能只是閱讀相關的書籍文章，而是要跟著做出行動。在&lt;你可以不遷就&gt;書中，作者強調「誰」優先於「什麼」，要先清楚知道自己是誰，才能夠決定要做什麼，因此在構想創業前，一定要知道自己心中最想要的是什麼。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>2. Brainstorming</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>拿一張白紙寫下任何想到的點子，想到什麼就寫上去，不管這個點子有多荒謬或多不可能完成，這就是腦力激盪的過程，之後也許真正有辦法執行的就是那些你當初認為荒謬的事情。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>3. 撰寫履歷</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>寫一份自己最新的履歷，檢視自己這段時間來到底做了什麼，也許能夠獲得創業靈感。如果你已經做同一件事做了很久，只是礙於受僱於人無法盡情發揮才能，那就可以考慮以創業來做相同的工作，例如獨立會計師、顧問、律師、設計師等等，然後把點子也寫下來。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>4. 列出清單</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>以上的方法如果還是沒有讓你有任何靈感，那就試試丹尼爾・品克的方法吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>（a）列出你擅長的五件事</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>（b）另外列出五件你最喜歡做的事</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>（c）把前面兩張清單的交集部份列出來</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>（d）看看第三張清單，問自己：「有誰會付我錢去做這些事？」。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如果自己沒有辦法有效率地釐清思緒，也可以找些有相同理念的朋友共同腦力激盪，很多時候一間企業的誕生都是從日常閒聊開始的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>【讀資料】</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>理想狀態下，經過以上的思考還有實作步驟，應該可以歸納出屬於自己的創業方向了，最好可以想出三種方向：最具有熱情的、有趣的、可以執行的。決定好方向後要開始研究所有創業的優缺點，利用網際網路資源或是相關書籍都能夠幫你分析或是解答，再決定最後的創業走向。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>【深入瞭解】</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>到了這個階段，你應該對於要做什麼事情有些想法了，不過台灣的低創業成功率不是沒有原因的，如果要避免失敗，最快速有效的方法就是和做相同行業的人談談，探索最適合的創業模式，這個過程有三個步驟，我們在這邊簡單總結成A-B=C的公式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>?A清單</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>透過搜尋引擎、LinkedIn、黃頁、商會、各種手機應用程式，尋找面談對象，確認成功創業所需的能力、知識與經驗，建議不要用信件的模式進行交流，不然很多重要的細節都不會被提到。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>?B清單</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>列出自己目前擁有的能力、知識與經驗，將上一步驟所做的履歷拿出來查看，並清楚理解目前自己的定位。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>?C清單</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>把A清單減去B清單，得到C清單-你目前沒有，但是創業必須必備的知識能力，可以藉由自我進修、雇用員工以及尋找有能力的合夥人來滿足目標，例如你目前想要投身音樂產業，但你本身不是音樂科系出身，對大眾音樂的敏銳度也不高，這時你能做的就是雇用一個相關產業出身的人來幫忙。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>創業本身就需要長時間的付出與投入，多花點時間在事前籌備上，公司真正成立後能少拐很多彎，不論想做什麼，都一定能找到相符的A-B=C公式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>讓我們來舉個簡單的例子吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>假如今天花媽想要自行創業在夜市賣包子，那他的A-B=C可能如下：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>A：包子攤販需要的能力</td><td>B：花媽有的能力</td><td>C：花媽需要加強的</td></tr><tr><td>包子好吃的秘方</td><td>包子好吃的秘方</td></tr><tr><td>包子手作技術</td><td>包子手作技術</td></tr><tr><td>攤位視覺設計</td><td>攤位視覺設計</td></tr><tr><td>招攬客人的能力</td><td>招攬客人的能力</td></tr><tr><td>攤位地點分析能力</td><td>攤位地點分析能力</td></tr><tr><td>愛好美食、享受烹飪</td><td>愛好美食、享受烹飪</td></tr><tr><td>食材批發管道</td><td>食材批發管道</td></tr><tr><td>會計</td><td>會計</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>在確定要創業後，花媽就會知道他缺少的是C清單上的能力，這時他可能會請柚子負責攤位視覺設計、請橘子擔任會計職位、請花爸來提供手作包子技術，並尋找擁有地點分析能力與食材批發管道的人力來給予創業協助。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>【取得回饋】</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>經過以上的步驟後，最後要問自己一個問題：「我適合做這種工作嗎？」傾聽內心的聲音，確定這一切是不是自己想要的，或是詢問家人朋友的意見，看看他人眼中的自己是否適合這個工作，並以此省思自身，也可以藉助「自我創業評估」等工具來判斷合適度。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>創業一開始只是個念頭，真正執行有非常多的細節需要思考以及彙整，除了確定自己的熱情所在之外，也要擁有足以創業的知識能力，在經過以上幾個創業步驟後，希望能讓想創業的你有個基本的藍圖，並勇於踏出創業的第一步。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>下一集我們將告訴你最新的創業概念與自行創業的風險，有興趣深入了解的讀者請繼續追蹤普匯金融科技的粉絲專頁還有官網哦！<br></p>\n<!-- /wp:paragraph -->', '2018-10-30 16:04:08', '【時事分享】大學生想創業？你不可以不知道的創業大小事(上)', 'article', 'publish', '2019-01-03 16:07:14', '', 0),
(2735, 2, '/upload/2019/01/1102.png', NULL, '<!-- wp:paragraph -->\n<p>前言：<br>提款時常常會擔心手續費的問題嗎？隨著金融科技的發展，就連ATM跨行手續費的成本也逐漸降低！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>小額提款手續費降低<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>目前所有銀行的ATM，，都一律採跨行提款手續費5元、跨行轉帳每筆15元的手續費用，且由民眾自行負擔。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但日前金管會正在研議，希望能大幅調降ATM跨行交易的手續費。銀行局則表示，有請財金公司持續收集相關資料，目前是針對「小額」交易轉帳或提款，例如1000元或3000元，不管是網路銀行或ATM交易，請財金公司提出報告看是否要調降，財金公司仍研議中。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金管會主委顧立雄今天受訪透露，方向上是已經確定，但要調降到多少未出爐，也要考量到比例原則，再訂出一個標準，目前還沒有定案，會就交易金額分出不同級距，但這級距要怎麼分還在研究中，最快11月底可以確定。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>小結：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在Fintech的浪潮下，數家銀行早已推出數位金融卡主打跨行提款限次數免費的政策，在金融科技大環境的前提下，未來銀行業的營運成本必然會逐漸降低，進而將利潤還給大眾，而ATM手續費降低，只是這趨勢的其中一角，未來肯定會有更進一步的發展！<br></p>\n<!-- /wp:paragraph -->', '2018-11-02 10:11:21', '【時事分享】ATM跨行手續費可望調降', 'article', 'publish', '2019-01-03 16:11:53', '', 0),
(2737, 1, '/upload/2019/01/1105.jpg', NULL, '<!-- wp:paragraph -->\n<p>每年網路購物在11/11前後可說是媲美情人節、聖誕節般精彩，在淘寶、天貓屢屢創下驚人銷售金額下，裡頭不乏需要金融、科技及物流的備齊，這就讓我們一起來揭秘吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>光棍節的由來</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>光棍節的由來來自於何處或何時，每個人都說法都不一致，目前廣為認可的是源於中國大陸的南京大學。根據網路上流傳的說法，在1993年南京大學「名草無主」寢室4名大四生每晚舉行「臥談」，主題大都關於如何脫離單身的狀態，4個大男生談著談著閃過一個念頭，決定以當時即將來臨的11月11日作為「光棍節」，並據此舉辦活動。從此以後，光棍節逐漸成為南京乃至中國大陸各地大學的校園趣味文化，且隨著一批批學子陸續從學校畢業，這個節日漸漸被帶入社會，並在網路和媒體的傳播推動下流行起來。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>網路購物結合</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>從2009年的光棍節開始，淘寶和旗下的天貓為首，將這一天行銷成為網路購物節，採用瘋狂降價的行銷策略，激發那些仍舊單身的「光棍們」鼓勵雖然沒對象也要對自己好的單身男女，讓他們給自己買禮物，藉此對應「情人節」傳統，偶後其他電商也紛紛加入這個戰局。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://lh3.googleusercontent.com/aicWSxv3BHjPIN_OxZ-lP8uw4onNFF7bUnfAwnzB_jakb9y9Pb6-S6v-nz3-FFoq784awpOONCpZchzOXdatr1nxqsUFlKzbZvGqr392QzsqeswArKmzjEfPjv_VJsVEHb_yoi5syNgcu1gaqA\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>這個行銷策略顯然非常的成功且貼近大眾，在中國社會風氣漸開放、自我意識抬頭且相關科技逐步成熟的前提下，每年光棍節檔期淘寶及天貓的銷售額屢屢創下驚人的商機（詳見下圖），1,682億人民幣換算結果更是高達8千多億台幣！此外光棍節的市場更成功超越美國黑色星期五的銷售金額。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>揭秘銷售背後的科技</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>試想一下你平常上網購物時候的劇情：進入購物平台網站後，點選自己喜歡的商品，並且加入購物車，然後用各種方式結帳，之後就是等待送貨員送貨了，偶爾太心急還要一直狂刷物流公司送貨進度。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但這些總總貌似理所當然，其實裡頭充滿的各種科技以及金融上的突破創新，分別舉例一下其中幾個步驟：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>將商品加入購物車：這個你認為僅應該耗費不到一秒的動作，在光棍節這樣每秒鐘需要處理不下百萬筆交易的規模，其實牽涉到巨量的平台後端軟體運作，更需要突破性的技術及大把公司資源做到這件事。亞馬遜在Prime Day購物日的活動中，便有過客人無法將商品放進購物車的窘境。</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>簡單介紹一下其中隱含的科技，一般的網路購物平台使用MySQL垂直式的資料庫系統，但此相較屬於傳統的技術，當有爆多流量突然出現，仍會有無法突破的極限，因此需要這種大量資訊流的平台會使用橫向擴展SQL的技術，相較經濟且可行。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>線上付款：目前能夠用於線上購物網站的付款方式有：手機內建的物理辨識系統支付、線上刷卡、第三方支付軟體（Line、微信、支付寶等）還有網路銀行轉帳等，這些方式需要的是平台商與付款方的配合、銀行與信用卡公司的合作、手機業者硬體的開發、銀行系統的升級、資料庫安全的提升等，才能達到如此便利的結果。舉例而言，手機支付與線上刷卡透過數據連線，將交易資料加密之後從手機到信用卡公司再到銀行完成付款，比起傳統實體刷卡的運作方式，手機就像是刷卡機一樣；而支付寶像是個資金中轉機構連結買賣方，買家先把交易額放入支付寶內，滿意收貨的商品後支付寶才會將交易款轉給賣方。</li><li>巨量物流配送：「資訊數位化及自動化」會是物流業者應付如此大量包裹的途徑，透過數據自動化調度運送員及規劃運送途徑，並藉由行動派遣系統與送貨員行動裝置或車上機連線，將效率大幅提升且每單位的運送成本拉低；即時的包裹資訊必須仰賴資訊化的系統流程還有完整建置的企業資訊系統，在不同的運送階段設置內部控制的節點，才能一方面確保物流順暢以及提供客戶即時更新的配送進度。</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:heading -->\n<h2><strong>小結</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>很快今年的光棍節也快到了！但相信你讀完本篇文章後，能在瘋狂搶購中喚醒一些理智，想到原來這麼多科技被運用其中啊！（然後以一種幫助科技進步的大愛繼續衝高銷售額）台灣的網路賣場平台近期也搭上光棍節的商機推出了許多折扣活動，相信在這樣的趨勢下，台灣在這方面相關的科技及金融創新也會越發成熟。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當然，如果想要上網買一些高單價商品（例如某水果牌手機）也可以結合P2P網貸平台，輕鬆迅速取得資金，讓你既安全又方便（不用賣腎或賣眼角膜）的把握光棍節折扣！<br></p>\n<!-- /wp:paragraph -->', '2018-11-05 16:09:21', '【普匯觀點】光棍節—網路平台銷售屢創佳績下的科技含量', 'article', 'publish', '2019-01-03 16:15:48', '', 0),
(2740, 2, '/upload/2019/01/1106.png', NULL, '<!-- wp:paragraph -->\n<p>創業說來容易做來難，在上一篇文章裡跟大家介紹了創業前的思考模式與流程，今天則要來告訴大家，在決定創業後，有什麼最新的概念是需要了解的呢？台灣的創業率高達86%，但是創業成功率卻只有個位數，在背後是否有什麼風險被我們遺忘了呢？有了好的概念與謹慎的風險評估，加上已經在腦中盤旋多遍的創業計畫，資金在手，就可以開啟夢想的旅程了！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>MVP 最小可行性產品</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>MVP是目前創業的最新概念，MVP ( Minimum Viable Product ) 的概念就是利用最低成本設計出產品，然後馬上放到市場上測試大眾的接受程度。在評估商品與市場的過程中，「目標市場」、「最小可行性產品」、「用戶訊息回饋」、「優化修正」是創業者需要釐清的四大概念。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>1. 目標市場</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>你必須要確定產品的目標市場在哪裡？哪種類型的顧客會願意消費？消費的理由是什麼？購買產品的管道是什麼？你的產品跟同類型的產品有什麼不一樣？在開發產品之前，這些問題都是需要想清楚的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>2. 最小可行性產品</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>最小的概念不是最便宜，而是用最低的成本（可能需要幾十甚至數百萬），快速的完成一個能夠獲得利潤、有消費者需求，且可以被市場接受的產品。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>3. 用戶訊息回饋</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>MVP需要在短時間內優化產品，而大量的「顧客訊息回饋」是改善產品的關鍵，在設計MVP時，可以直接把訊息回饋機制安裝在產品內，能夠得到最快速且有效的使用者資訊，在搜集資料的過程中，不用特地訪問消費者的心得，而是要藉由觀察消費者的行為來做出更好的判斷。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>4. 優化修正</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>透過顧客回饋數據，進行不停的優化。在產品修正的過程中，要讓消費者能夠不用付出多於的成本就能無痛進行優化、更新、升級，把自動更新優化的機制放到產品中是最好的選擇。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>讓我們來模擬一下情境吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>目標市場</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>今天瘦老媽想在捷運站出口擺攤賣炸雞，那他的目標市場就是「通勤族」，「愛吃速食、受不了香味誘惑」的人會是願意消費的顧客，消費的理由是「嘴饞或是肚子餓」，購買管道是經過捷運出口旁的「瘦老媽攤位」，產品的特色是「獨門老媽醬料」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>最小可行性產品</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>最小可行性產品就是「塗滿獨家醬料的炸雞」，酥脆的雞肉加上老媽特製的醬汁，讓經過的人都不禁駐足圍觀購買。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>用戶訊息回饋</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在剛開幕的時候採用「消費填問卷送薯條」的活動，讓消費者能夠及時反應食物的口味，薯條兌換卷的下次使用也能增加回購次數。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>優化修正</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據問卷得到的消費者調查作「炸雞口味的調整」，口感以及新鮮度都能「在下一次的販售立刻改善」，達到無痛優化。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>風險評估必須搞清楚的四件事</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>創業的變數太多，要考慮的面向也很多，但最最重要的四件事情務必要深思熟慮：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>1. 估算表的製作</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>利用Excel表格做一份支出與收入的明細，創業的想法往往不如現實中美好，需要花到錢的部分也不計其數，表格不用過於複雜，只要自己看得懂、便於計算就可以，在記錄時將成本寫的高一些、營收相對少一些，未來的計畫才會比較有彈性，算好每個月的開銷花費然後評估以目前的資金狀況可以維持多久，進而訂定每月的營收目標。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>讓我們以瘦老媽的炸雞店為例：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>項目</td><td>支出</td><td>收入</td></tr><tr><td>食材成本(100份/月)</td><td>-2,000</td></tr><tr><td>炸鍋(固定資本)</td><td>-5,000/台</td></tr><tr><td>炸雞收入(100份/月)</td><td>7,000</td></tr><tr><td>扣除固定資本後收入</td><td>7,000-2,000</td><td>5,000/月</td></tr><tr><td>固定資本每月攤銷</td><td>-5,000/10= -500(月)</td><td>4,500/月</td></tr><tr><td>總計</td><td>4,500</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p><strong>2. 對行業的了解</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如果你對想創業的所屬產業還沒有認知，那最好可以先到相關的機構工作累積一些經驗，除了學習產業的技能之外，親自深入了解顧客的需求，甚至是進一步培養自己的客戶也很重要，另外也能藉此確定自己在這個產業的合適度。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>3. 資金預備</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>至少半年至一年以上的資金預備是必須的，基本上半年的時間就夠能判斷一項創業計畫是否成功，如果因為資金不足而無法維持到成功的那一刻那就相當可惜了。另外，生活的資金也要記得一併納入考量。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>4. 退場承擔</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這應該是最重要的一項考量了，如果真的創業失敗了，失去的時間、金錢，還有捨棄的原有工作前途是自己可以承受的嗎？不要因為許多創業成功案例就讓自己一頭熱的開始計畫，他不全然都是美好的，失敗的可能性我們在開始創業前也要進行評估，否則到最後只會賠了夫人又折兵。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>相信大家看完創業大小事的上下集後，對創業有一定的認識以及評估，也能更清楚地知道未來的目標為何。在創業這件事情上，「資金」我認為是最簡單卻也卻困難的部分，普匯金融科技提供有創業夢的年輕人貸款機會，最高貸款金額上限為12萬，提早開始投資理財，圓夢計畫馬上展開。在計劃創業的你如果萬事俱備，只欠東風，那普匯願意做你的那一陣風，把你吹往夢想的未來。<br></p>\n<!-- /wp:paragraph -->', '2018-11-06 17:33:56', '【時事分享】大學生想創業？你不可以不知道的創業大小事(下)', 'article', 'publish', '2019-01-03 16:17:53', '', 0),
(2744, 1, '/upload/2019/01/1107.jpg', NULL, '<!-- wp:paragraph -->\n<p>經濟日報、政大金融科技國際產學聯盟、金融總會於10月3日共同舉辦「<br>\n金融科技」校園座談，主題以「金融科技發展與政策趨勢」為主，與學生<br>\n們共同探討Fintech相關議題！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>三大發展策略<br>\n我國在金融科技領域的推動主要以三大策略指標進行分層。<br>\n1、 金融業升級<br>\n◆ 三面向建構金融科技創新應用環境 — 產品研發、人才培育、發展環境<br>\n對於人才培育計畫，金總會秘書長表示，金融科技發展下，銀行不會消失<br>\n，但人才需求會跟著轉變，存款及外匯人員需求將降低，但FinTech人才<br>\n（行動支付、區塊鏈、大數據）、業務人才（財富理財、個金業務、企業<br>\n金融）、法遵人才、風控人才，還有海外及資訊人員，需求都會增加。金<br>\n融科技創新園區不僅打造金融科技國際聚落，也鼓勵學生加入金融創新行<br>\n動！<br>\n2、 金融科技創新發展<br>\n◆ 創新實驗機制、創新園區、台北金融科技展<br>\n3、 資安與監理合作<br>\n◆ 金融資安資訊分享與分析中心、深化國際監理合作<br>\n其中小編想向大家多介紹即將在年底舉辦的「台北金融科技展」，此次<br>\n的展覽將邀請Bank 3.0金融科技創新教父Brett King專題演講。舉辦目的<br>\n是希望透過這樣的交流平台匯集到國內外金融科技產、官、學、研多方資源<br>\n，透過多方的體驗與交流打造台灣最大金融科技創新交流平台，讓台灣成為實<br>\n驗創新樞紐，見證FinTech發展潛力，更同時讓這樣的展覽茁壯成跨國合作的不<br>\n朽典範。<br>\n全球政策六大趨勢<br>\n一、成立專責輔導及政策推動單位<br>\n◆ 台灣金融科技發展與創新中心<br>\n二、建立金融監理沙盒制度— 全球已有18個國家開放申請監理沙盒實驗，也<br>\n有不少國家推動進階版的沙盒，如：<br>\n◆ 一國多個金融業務沙盒：像香港跟泰國，分別推出銀行、證券、保險業沙盒</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>◆ 跨境或區域沙盒：像新加坡建立東協金融創新網路等<br>\n三、打造金融科技實體聚落<br>\n◆ 台灣金融科技創新園區、香港數碼港等。<br>\n四、建立資安資訊共享機制、強化資安防護<br>\n◆ 台灣金融資安資訊分享與分析中心（F-ISAC），目前有310家金融機構參加。<br>\n5、 簽署金融技合作協議<br>\n◆ 為尋求進入外國市場的金融科技公司提供轉介服務，加速金融科技創新與監<br>\n理進行資訊交流。<br>\n6、 推動設立純網銀<br>\n◆ 純網銀可提供更便利且價格更優惠的金融服務，提升金融普及性。</p>\n<!-- /wp:paragraph -->', '2018-11-07 16:15:50', '【普匯觀點】校園金融大講堂 (上) —Fintech趨勢與發 展', 'article', 'publish', '2019-01-03 16:19:03', '', 0),
(2747, 2, '/upload/2019/01/1106_01.png', NULL, '<!-- wp:paragraph -->\n<p>在《遠見》今年FinTech大調查中，20～49歲族群最常使用的行動支付品牌，前三名依序為LINE Pay、街口支付與Apple Pay，分別有61％、41.1％與31.6％的人經常使用。成立三年，台灣本土的「街口支付」在行動支付品牌中近來終於殺出重圍，從線下走入線上並成功打入現金市場獲受好評，但是對於支付浪潮的興起與激烈的競爭街口支付所面臨的挑戰相對也不小！面對Line這樣強大的勁敵，街口支付又是怎麼應對的呢?讓小編來跟你說個明白！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>多元服務，走入現金市場，搶下新據點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>街口支付目前累積活躍用戶數量約80萬，起初他們走訪難以攻破的現金市場，例如四大超商、百貨、夜市商圈、連鎖手搖店等，目前合作簽約的商家已達七萬多家！現在走在路上已經可以看到很多店家可提供街口支付功能，除了讓民眾不用再戴著厚重的錢包出門外，也為店家省去很多收銀系統上的成本問題！這樣的便利性真是一大福音！街口支付提供的服務也很多元化，除了能掃碼支付還能叫車、美食餐廳訂位、繳費……等，整合的行銷策略建立起超機能的街口生活圈讓生活變得更便利！除此之外街口支付甚至在App內建「收款」功能，我們只需出示手機QR Code就能輕鬆收款，生活上大大小小的事只要利用掃碼就能一切搞定，這對小編來說真的非常有吸引力！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>線下走入線上，合作電商陸續上線</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>街口支付在今年的通路策略中也邁向下一個階段進行，今年串接的電商平台達22家，包括momo購物網、、好吃市集、遊戲業者Garena、金石堂網路書店等22家電商平台，雖然還有很多還未商討完畢，但街口支付在上個禮拜的雙11歡慶中就已創下單日破億元的交易紀錄！執行長胡亦嘉表示，街口支付在線上網購單價上面比交易客多出4~4.5倍，可看出線上交易的金額可為他們衝破更多的交易紀錄，大型電商平台如PC home線上購物、Yahoo奇摩拍賣、蝦皮購物都已簽約，預期明年將陸續上線，我們只需要點入APP裡的「網購專區」，不需再輸入信用卡號或到ATM轉帳，只要在結帳頁面掃碼，即可完成付款。街口支付除了提供年輕世代另一種支付的管道外，更為線上電商拉攏了更多新用戶。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>小結</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>街口支付在今年原本還期望推出有「台灣餘額寶」之稱的「託付帳戶」，主打「儲值就能享利息」，讓用戶能享有更多元的金融服務，但基於法規的限制被迫停線上架，不過這樣的計畫仍在如火如荼推動中，期望未來能看到更多街口支付的功能，小編也為大家整理的電子支付相關的優惠方案，敬請期待！<br></p>\n<!-- /wp:paragraph -->', '2018-11-06 16:22:32', '【時事分享】街口支付搶攻據點！', 'article', 'publish', '2019-01-03 16:23:07', '', 0),
(2749, 1, '/upload/2019/01/1109.jpg', NULL, '<!-- wp:paragraph -->\n<p>金融資訊帶著走，客戶選擇更自由</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金管會主委顧立雄25日透露，正在請銀行公會研議Open Banking（開放銀行計畫），評估要採英國式，還是新加坡或香港版本，即在客戶同意下，銀行要將金融資料交給第三方，進行相關金融分析或理財建議。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>也就是說，未來客戶可以選擇將自己的金融資訊，如房貸、信用卡紀錄等金融資料，提供給第三方計算，一次來估算出哪裡有最適合自己的貸款、或金融商品；比起傳統必須跑遍銀行一間一間詢問的方式來的更為方便。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>各國做法並不相同</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>開放銀行的概念源自於歐洲，要求銀行打開客戶資料庫，提供API讓外界可存取相關資料；英國即是要求其9大銀行將過去視為機密的客戶資料，變成開放的API，有個統一的具有公權力或處分效果的特定平台，負責接收銀行交出的訊息，訂定統一格式等。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>另一模式就是新加坡及香港模式，即不具強制性，由銀行自行尋找合作夥伴，在取得客戶書面同意後，將客戶的資料交給此平台運用，銀行甚至可以成立子公司，進行相關智慧運算提供Open Banking的服務，顧立雄說，這部分可能就不需要修法，因為沒有強制且統一提供，銀行的反彈會相對較小。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>目前銀行公會才開始評估，顧立雄說尚未給研究期限，同時國際上也算是新的觀念，也要關注國外發展情況</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>另外，金管會也正在評議開放金融雲，即未來無論純網銀或一般銀行的資料，隨著客戶愈來愈多、法規要求保存期限等，資料庫需求會更大，因此業者爭取開放雲端存取，但目前許多都是放在Google、Micrasoft、Amazon的公眾雲之中，這對金融資料安全性恐有疑慮。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>小結：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>簡單、快速、客製化是目前金融科技的整體大方向，在這個方向下，相信未來還會有更多更為創新的金融服務，畢竟誰能夠主動地知道用戶在想甚麼、並且把她想要的東西送在他眼前，那麼他就是個最大贏家！<br></p>\n<!-- /wp:paragraph -->', '2018-11-09 16:19:08', '【時事分享】金管會鬆口 Open Banking研議開放', 'article', 'publish', '2019-01-03 16:22:15', '', 0),
(2755, 2, '/upload/2019/01/1108.png', NULL, '<!-- wp:paragraph -->\n<p><em>身為年輕學子的我們，想自己賺一桶金來買房、買車都是一件難如登天的事，想靠每個月不高不低的薪水打拼根本是遙不可及的夢想，然而！我們仍然能藉由投資理財來幫自己的人生規劃打下最穩健的基礎！</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>投資有賺有賠，一定會有風險，就算是再厲害的投資人抑或是股神巴菲特也無法完完全全排除投資風險，因此要如何有效控制風險便是一個重要的課題！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在可承受風險的範圍內追求到最高報酬的核心概念之一便是「分散投資」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>分散投資</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「分散投資」是在追求長期財務目標並降低風險時最重要的要素之一，其中我們在投資期間會遇到的風險主要為系統性風險、非系統性風險，而這兩者的差異性小編已在上篇<strong>〈大學生投資心法〉</strong>中詳細介紹。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>因此我們可以依照風險的特性將我們的投資進行最優的組合配置。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舉例來說，如果將所有的資產都放入同一產業如電子業，當今天公司運營不當導致股票開始下跌時，您的投資價值也會跟著受到影響及波動，一時之間所有的金錢投注將會血本無歸。然而，如果在投資組合中加了其他產業如鋼鐵業、航空業……等，如此一來總體資產只會有一部份受到負面影響，因此投資標的的關聯性越低，越能達到分散投資的目的及效果。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但是小編認為最重要的一點！身為投資人對投資標的公司的基本資料像是財務狀況、運營績效……等，一定要有深入的了解與認知，市場上的風險無法全部消弭，因此在知識與經驗有限的情況下我們就要找出自己能接受也最適當的平衡點做漸進式的調整。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>長期投資</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>相較於短期投資，長期投資對於投資人來說相對較具有保障性，不需要時時刻刻緊盯著大盤，不受非理性的因素影響做頻繁的買入賣出交易，更能捨去多次的交易手續費成本，並且比起主動投資需要專業的知識、大量的技術分析和策略，長期投資需要的是謹慎選擇穩健、前景良好的標的公司，並持有一年以上才能得到相對的高收益！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>以普匯為例，</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當然，利用普匯金融科技的平台我們就能做最簡單的分散以及長期投資，進而達到最高的報酬！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舉例來說，今天我擁有一筆資金想要進行投資，如果將總投資金額分成好幾等分分別投注到不同的借款人，由於每個借款端的利率都不同，因此投資人能夠依不同的利率收取不同的利息，進而錢滾錢賺取更高的收益，就算其中一位借款者沒有在期限內還款對整體的收益並不會有太大的影響。藉由每一次收取的利息在投入下一個標的，你擁有的金錢將越滾越大，投資報酬率高的讓你嘖嘖稱奇！往前回看小編為大家描繪的普匯高投報率解析表！！&gt;&gt; 大學生投資心法\n\n</p>\n<!-- /wp:paragraph -->', '2018-11-08 11:23:34', '【普惠觀點】投資理財大補貼', 'article', 'publish', '2019-01-03 16:27:33', '', 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(2760, 2, '/upload/2019/01/1112.png', NULL, '<!-- wp:paragraph -->\n<p>印度在2016年底為了打擊黑錢開始實施「廢鈔令」，開啟了印度數位金融的大門，在民間與政府的共同推動之下，電子錢包以及相關的金融科技應用正在蓬勃發展，在2018年的印度已經有超過1/4的人口使用電子錢包的服務，就連具有百年歷史的老店都能夠看見第三方支付的QR code代碼。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>印度電子錢包龍頭Paytm的崛起</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在印度的第三方支付市場中，有「印度支付寶」之稱的Paytm為產業龍頭，雖然在2010年就已經創立，但由於電子支付的概念尚未在國內被接受，直到2016年政府大刀闊斧執行廢鈔政策，將近86%的現金失去流動性，民眾別無選擇之下才開始使用電子支付的功能，在廢鈔後的一個月內Paytm的每日交易筆數成長3.5倍，日增用戶更是高達50萬人，未來的發展深受中國電商龍頭阿里巴巴看好，並投資持有40％的股份。至今，平台的年交易量達到290億美元，大約700萬個實體店家接受Paytm付款，且開始與Uber、Foodpanda等企業合作，期望創造更多的商機。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>電子支付習慣的養成</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2016年底廢鈔令開始執行後，印度的現金流量瞬間被砍半，近幾年來電子錢包的普及與相關措施的推動才讓現金流量回到原本的水準，數位支付比率也因此成長超過70%，現金支付減少了將近20%，政府以政策領導印度從目前的「少現金社會」邁向「無現金社會」，推出「JAM三位一體」政策，鼓勵民眾改變金融消費習慣。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>改變民眾金融習慣的JAM政策 </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>J，是「金融普惠政策」，鼓勵民眾在銀行開戶，曾締造一週開啟超過1800萬帳戶的金氏世界紀錄，目前銀行帳戶總數已經達到3.1億。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Ａ，是「數位身分認證系統」，是一組代表自身的身分認證號碼，由12個數字組成，目前有超過12.1億組號碼，受「單一身份認證局」管理，除了推出指紋辨識外，未來還計畫導入虹膜與臉部辨識，加強金融服務認證。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>M，是「印度手機滲透率」，目前已累積約11.9億張手機晶片卡。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>印度政府強迫民眾將手機門號、身分號碼與銀行帳號三者綁定，只要透過手機中的「統一支付介面」就能互相轉帳，收款與支付。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>金融科技相關商業模式的相繼崛起</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>目前印度的金融科技主要聚焦在行動支付領域(電子錢包)，但像是另類借貸、資產管理與保險等項目市場也相當看好其發展性，其中另類借貸的市場各大廠商已經磨刀霍霍，2017年就有超過225個相關業者在印度進軍。目前印度13億人口只有不到2%的人擁有信用卡，但有信用需求的人卻高達23%，市場仍具有強大的成長動能，這也是LazyPay會推出的原因，LazyPay的功能與信用卡相同，除了還款利率比銀行來得低之外，最大的差別就是沒有實體的卡片，根據消費者的客戶適格性調查、線上消費行為以及還款記錄作為個人信用額度的依據，可用於日常生活中的小額消費，並制定消費上限為10萬盧比。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>結語</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>目前，印度FinTech的重鎮在原本即是金融中心的孟買，但由於孟買的發展成本過高，且位於南方的科技大城邦加羅爾享有人力資源的優勢，有不少新創團隊選擇在南方發展。至今為止的統計，印度金融科技產業年均投資報酬率達29％，高於全球平均的優異表現吸引許多國外資金流入，阿里巴巴、日本軟銀、Whatsapp、Google等國際大廠都相繼來印度投資，讓原本有將近93％人口無法觸及傳統金融體系的印度，在民間與政策的帶領下，在金融科技領域發展更多的可能性。相較之下，台灣FinTech產業的發展卻顯得有些緩慢，但原因是什麼呢？下一篇文章會替大家介紹台灣目前的金融環境以及FinTech的發展現況，請大家持續鎖定PuHey!小學堂的文章分享哦！<br></p>\n<!-- /wp:paragraph -->', '2018-11-12 13:30:03', '【普匯觀點】印度的金融科技已經崛起，台灣還在等什麼？(上)', 'article', 'publish', '2019-01-03 16:34:45', '', 0),
(2763, 1, '/upload/2019/01/1115.jpg', NULL, '<!-- wp:paragraph -->\n<p>「銀行借錢好麻煩，要一堆認證文件還要簽合約！」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「銀行從申請到放款程序好冗，要至少兩個星期才能拿到錢QQ」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「銀行貸款都要直接去分行辦理，可是我就是懶得出門啊！」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>很多有資金需求的人會第一個尋找銀行借款，不過銀行信用貸款的程序是出了名的繁瑣，從開始申請到實際拿到現金的期間更是高達14天，在家裡銀行間奔波也是許多貸款人的困擾！沒關係，大家的困擾普匯都聽見了！普匯應用最新的科技打造P2P借貸平台，全面線上化且無人化的借款流程，解決你向銀行貸款時的困擾，15分鐘審核，3小時放款，資金需求馬上補足，讓你不用再看行員臉色，痴痴等待銀行放款！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>銀行準備文件</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>於各家銀行申請信用貸款時，必須親自到分行臨櫃辦理，並且備妥身分證明文件，如身分證、工作證明、專業執照等，以及財力文件如最新扣繳憑單、報稅證明影本、主要往來存摺6個月至1年影本，審核期間一天至一星期不等，審核通過後還需要花一天時間再至臨櫃核准對保，準備雙證件、印章與銀行簽約，完成以上步驟後才能夠獲得銀行的撥款進帳。因為銀行要確保的事項過多，過程顯得有些繁瑣複雜，且前往銀行辦理相關業務的皮鞋成本與時間成本過高，常常讓人耐不住性子等待。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>普匯借款免煩惱</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>PuHey！利用人工AI技術以及大數據分析，導入信用評等機制，我們只需要拿起手機輕鬆點入手機ATM介面，快速註冊會員，輸入各項基本資料，，你的信用分數能拿到幾分、最多能貸款多少金額最快15分鐘內就能審核完畢，3小時搓合成功入帳，是不是非常神奇？連小編都為這種速度感到驚訝無比呀！透過AI、線上化、無人化的技術，我們不再需要向傳統金融低頭，不受他人干擾，也不需擔心銀行照會聯絡家人，馬上點進普匯快速貸款吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>銀行普匯比一比：</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td><strong>項目</strong></td><td><strong>一般銀行</strong></td><td><strong>普匯借貸平台</strong></td></tr><tr><td>放款對象</td><td>工作滿6個月之社會人士</td><td>在學學生</td></tr><tr><td>放款金額</td><td>依薪資所得與信用評等評估，十萬至百萬不等</td><td>依在學學校與可支配所得等風險評估，可借貸5000至12萬元不等</td></tr><tr><td>所需證件</td><td>雙證件、工作證明、薪資證明、報稅證明、印章、與銀行合約等</td><td>身分證正反面與健保卡正面、學生證、金融卡資訊<br></td></tr><tr><td>撥款期間</td><td>至少兩週</td><td>最快3小時入帳WIN</td></tr><tr><td>臨櫃辦理</td><td>需要</td><td>借貸過程線上化無人化WIN</td></tr><tr><td>還款利率</td><td>年利率1.5%~5%不等</td><td>年利率5%~10%不等</td></tr><tr><td>手續費</td><td>2,000元~10,000元不等</td><td>500元~1,200元不等WIN</td></tr></tbody></table>\n<!-- /wp:table -->', '2018-11-15 16:31:01', '【普匯觀點】AI貸款有多快？ 銀行貸款要兩周，普匯只要三小時', 'article', 'publish', '2019-01-03 16:34:00', '', 0),
(2766, 1, '/upload/2019/01/1119.jpg', NULL, '<!-- wp:paragraph -->\n<p>一年一度的感恩節又到來了！除了慶祝這歡愉的節慶外，你知道這麼重要的節<br>\n日背後也有運用到新興區塊鏈的技術嗎？沒錯，別懷疑，馬上就來看看內部祕<br>\n辛！<br>\n感恩節食物與習俗<br>\n感恩節 (Thanksgiving Day)，是美國的全國節日，為感謝上帝賜予一年度豐收的<br>\n一種祝福，是美國人民獨創的一個古老節日，也是美國人合家歡聚的節日，定<br>\n於每年11月的第四個星期四。<br>\n那麼感恩節是怎麼來的呢？<br>\n感恩節和美洲古代的印地安人有關，在15世紀中期一批英國清教徒因宗教迫害<br>\n搭乘了「五月花號」駛往美洲。在飄泊的六十幾天當中他們忍肉著寒冷、飢餓<br>\n以及疾病，最後終於到達一處北美的殖民地。當時正值嚴酷的冬季，不僅氣候<br>\n嚴寒農作物也歉收，但當地的印地安人不僅慷慨地拿出貯藏的玉米和豆類食品<br>\n來溫飽他們，更教導他們如何飼養火雞，因此他們建立了親密的友誼，隔年秋<br>\n季豐收他們舉辦了豐盛的感恩會，用烤火雞和玉米糕點來感謝印地安人的款待<br>\n，長久以來這種感恩會變成為了慣例。<br>\n每逢感恩節這一天，美國舉國上下都非常熱鬧，城鄉市鎮到處舉行化裝遊行、<br>\n戲劇表演和體育比賽等，學校和商店也都按規定放假休息。孩子們還會模仿當<br>\n年印第安人的穿著打扮，畫上臉譜或戴上面具到街上唱歌、吹喇叭。當天教堂<br>\n里的人也格外多，按習俗人們在這裡都要做感恩祈禱。另外每家的家庭成員也<br>\n都會從世界各地歸來，一同享受家庭團圓的喜樂並大嚼美味烤火雞，暢談往事<br>\n，讓人備感溫馨、親切。<br>\n火雞區塊鏈應用<br>\n在物聯網、大數據、AI等創新科技的時代，結合生活環境和資訊科技，人們研<br>\n發出更多新技術應用在日常生活中，進而掀起智慧城市建設的熱潮，除了之前<br>\n小編提到的德國啤酒節，你知道像感恩節這樣重大的節日竟然也有應用到區塊<br>\n鏈技術嗎？<br>\n近日，美國嘉吉公司運用區塊鏈技術來記錄每一隻火雞從農場運送到市場的情<br>\n況，同時也幫助消費者明確辨認自己購買的火雞是從哪裏運送過來，只要在官<br>\n網輸入包裝袋上的代碼，消費者即可查詢到這隻火雞的來源，更有趣的是農民<br>\n們也能透過區塊鏈平台輸入飼養的數據，包括火雞的健康狀態、農場相關訊息<br>\n等，讓公司在管理食品供應鏈訊息上面更有效率及整合性。這項技術對食品產<br>\n業未來的發展是有利可圖且具有潛大的開發優勢！除了迎合消費者的心態更同<br>\n時增加自身的競爭力。沒錯，沒有小編這樣的介紹相信很少人知道感恩節背後<br>\n的祕辛！是不是很有趣呢！<br>\nhttps://www.jinse.com/news/blockchain/99364.html</p>\n<!-- /wp:paragraph -->', '2018-11-19 16:34:05', '【普匯觀點】感恩節也能應用區塊鏈？！', 'article', 'publish', '2019-01-03 16:37:39', '', 0),
(2769, 2, '/upload/2019/01/1114.png', NULL, '<!-- wp:paragraph -->\n<p><em>你能想像全世界有將近25億成年人沒有使用銀行服務嗎？他們沒有活期帳戶、無法辦信用卡，加上銀行也不想去他們居住的地方增設據點，因此在全球金融體系下他們是幾乎被隔絕的，但是他們並非遙不可及，目前電子貨幣正在這些開發中國家開拓商機！</em><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>開發中國家金融體系</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金融體系在開發中國家普遍效能不彰，資金的流動率相對於已開發國家也是落後了一大截，然而金融數位化卻在這些國家中嶄露無限商機，為什麼呢？<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舉電子加密貨幣為例，已開發國家人民對於信用卡的信賴度通常很高，不用掏現金、預繳費用、免去交易手續費……等都是他們喜愛使用的原因之一，因此除非是信用卡遇到相當棘手的問題，否則他們不會使用加密電子貨幣來解決問題。但是在開發中國家卻反倒不是如此，因為銀行服務的不足，加上沒有設立實體分行基礎的建設和安全防護，法治的薄弱以及不健全使這些國家的人民缺乏參與全球經濟體系的基礎，也限制了他們的現金交易程度，於是電子化、數位化的金融便能滿足他們的需求！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但很多人會提出疑問：首先，貧困國家的人民識字程度不一，無法應付類似比特幣複雜的新科技。第二則是他們沒有完善的電訊系統做這樣的用途。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>事實上這幾點反倒成為商機成熟的原因！全世界識字率其實比十幾年前高出許多，一波波受過教育的年輕人進入職場，絕對有能力處理與接收數位貨幣這類的工作，其實他們能做到的遠比我們想像中的還要多很多！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>印度Fintech崛起</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「無現金社會是目標，但『普惠金融』才是商機之所在！」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>基地在孟買的PayU印度總經理庫柏塔（Jitendra Gupta）表示，九成以上的印度人口無法觸及金融體系、太多的需求沒有被滿足，好在三位一體的政策，正好可以補足消費者服務的最後一哩路。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>JAM政策 改變平民金融行為</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>J，是金融普惠政策（Jan Dhan Yojana），零元也能開戶，鼓勵民眾開立銀行帳戶，曾締造一週開啟超過1800萬帳戶的金氏世界紀錄，目前總帳戶數已逾3.1億。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Ａ（Aadhaar），是「數位身分認證系統」，每一個住民都能申請一組12碼、代表自身的獨特身分認證號碼。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>M（mobile phone），是指印度手機滲透率，已有11.9億張手機晶片卡。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>印度政府幾乎是強迫式地，要民眾將這三者：手機門號、Aadhaar身分號碼與銀行帳號綁在一起。如此一來，用戶只要透過手機中的「統一支付介面」（UPI, Unified Payment Interface），即能互相轉帳，收款與支付。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>堪稱FinTech金童的庫柏塔表示，最大的金融服務缺口之一，就是「信用」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>印度13億人口中只有2500萬人有信用卡，但至少3億人有信用需求，市場潛力極大！！看來，原本93％人口無法觸及傳統金融體系的印度，在「金融普惠」政策的帶領下，將不只是「無現金」，印度FinTech還很有看頭，讓我們繼續看下去！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>參考資料</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:embed {\"url\":\"https://www.gvm.com.tw/article.html?id=45364\"} -->\n<figure class=\"wp-block-embed\"><div class=\"wp-block-embed__wrapper\">\nhttps://www.gvm.com.tw/article.html?id=45364\n</div></figure>\n<!-- /wp:embed -->', '2018-11-14 17:55:26', '【Fintech】開發中國家—無現金社會！', 'article', 'publish', '2019-01-03 16:41:58', '', 0),
(2772, 1, '/upload/2019/01/1121.jpg', NULL, '<!-- wp:paragraph -->\n<p>繼上一次金管會聯合經濟日報在政大舉辦校園巡迴講堂後，這次金管會主委顧<br>\n立雄前往臺北大學與學生面對面暢談金融議題，以「普惠金融」為主題發表演<br>\n講！趕緊來看看這次的內容有哪些是你沒有catch到的！<br>\n普惠金融之定義及目標<br>\n聯合國於2006年的「普惠金融體系」藍皮書<br>\n 普惠金融的目標是在健全的政策、法律和監管框架下，每一個國家都應有一<br>\n整套的金融建構體系，共同為所有層面的人口提供合適的金融產品和服務。<br>\n普惠金融(Financial Inclusion)也稱包容性金融，其初衷強調在透過金融基礎設施<br>\n的不斷提升，以有效的方式提高金融服務的可及性並惠及每一個人，尤其是那<br>\n些被傳統金融忽視的農村、城鄉貧困地區的弱勢團體。<br>\n核心定義可劃分為三大層面：<br>\n 可及性(Access)：確保消費者可取得金融服務、消除弱勢和低所得民眾及微<br>\n中小企業所面臨之困境，尤其<br>\n 實用性(Usage)：強化金融服務使用頻率，並提升消費者能力以充分運用金融<br>\n服務、隨著金融創新科技發展，可提供民眾多元化及較低成本之金融服務。<br>\n 品質(Quality)：使金融服務符合民眾需要及金融資訊取得，如推廣金融知<br>\n識普及之教育和金融消費者保護措施等。<br>\n重要措施<br>\n 推動中小企業放款<br>\n 推動新創重點產業放款<br>\n 提供金融服務與欠缺地區：偏鄉申設時數不設限<br>\n 提升小額投資人便利性：推動定期定額買股、成立基金網路銷售平台<br>\n 推動微型保險<br>\n 發展行動支付<br>\n 落實社會宣導、深化校園根基、結合社會關懷與公益<br>\nPuHey!普匯金融科技，提供用心金融服務！<br>\n普匯金融科技也是以「普惠金融」為願景，使無法享受銀行服務的人都能受惠<br>\n，尤其是學生、新鮮人或青年創業家及中小企業家因缺乏和銀行來往的資歷，<br>\n往往被列為違約高風險的族群造成在貸款時備受阻饒與困境，因此PuHey！利用<br>\n高端科技建立了投資借款的雙向P2P平台，導入人工智能信用評等機制，只要使<br>\n用手機註冊會員並輸入資料就能快速申辦資金！小編認為真的是非常方便又快<br>\n速！<br>\n對於大學生而言，不僅僅能解決金錢不足的問題，如果你是一個懂得理財的高<br>\n手也能成為投資人，幫助別人之餘更能用小額投資的特性為自己累積財富，錢<br>\n滾錢畢業之時就能贏在起跑點上！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>小結<br> 根據「世界銀行」調查，至今全球仍有20億人尚未擁有銀行服務，目前已有許<br> 多國家包含東南亞各國都陸續推動沙盒來實現普惠金融目標，其中馬來西亞的<br> 新創My cash推出的應用程式使東南亞移工們也能不受限使用線上金融服務。小<br> 編認為目前台灣普惠金融的發展仍然有很大的空間需要前進，行動支付、數位<br> 金融或是網路銀行的推動往往受金融法規限制，受限於監管金融業者被要求配<br> 合產業發展，若未能跳脫這樣的舊思維勢必無法帶動數位經濟的發展趨勢，期望未來台灣能看到更多元化的金融環境！</p>\n<!-- /wp:paragraph -->', '2018-11-21 16:37:41', '【普匯觀點】校園金融大講堂 (下)— 普惠金融', 'article', 'publish', '2019-01-03 16:42:43', '', 0),
(2777, 1, '/upload/2019/01/1123.jpg', NULL, '<!-- wp:paragraph -->\n<p>金管會自上周五(11/16)開始接受純網銀的申設，為了避免韓國當初在設立純網<br>\n銀所面臨的窘境，金管會特別提出了幾項規定。<br>\n南韓純網銀剛設立的窘境<br>\n南韓目前有兩家純網路銀行，提供用戶線上辦理匯款或是信用貸款的服務。南<br>\n韓第一家純網銀K Bank因為祭出高存款利率，開業四天就吸收超過十萬名客戶<br>\n；一年後，K Bank卻面臨增資不利的生死關頭。因為ＩＴ系統建置及行銷成本過<br>\n高，營業未滿一年南韓的純網銀就因為虧損嚴重必須增資，但由於韓國「產金<br>\n分離」規定過於嚴苛，有錢的人無法出資才導致資金的週轉出現困難。南韓政<br>\n府針對這個問題已提出解方，放寬非金融業的持股上限。<br>\n金管會借鏡韓國經驗提出預防方案<br>\n金管會在規定純網銀相關規範時，為了確保純網銀在增資時不會遭遇困難，特<br>\n別提出了持有純網銀股票達25%以上的大股東必須提出財務支援承諾書。對於<br>\n金融業則規範若持股達25%以上的金融業要出售股票時，必須找到其他金融業<br>\n接手方能出售。透果這些管制，金管會期望台灣的純網銀在進行第一次增資時<br>\n不會遇到資金周轉不靈的問題。<br>\n純網銀所面臨的未來挑戰<br>\n純網銀對於台灣來說是較新的概念，對於老年人來說親自到銀行辦理各項事務<br>\n已經是不可改變的習慣，在這項限制下純網銀只能以年輕人及壯年人為目標提<br>\n供服務。另外純網銀的資安和洗錢疑慮也是不少人擔心的重點，若是能透過變<br>\n換IP以及偽造資料開辦人頭帳戶，那麼純網銀便會成為有心人是洗錢的最佳平<br>\n台。<br>\n總結<br>\n雖然近幾年台灣對於金融科技的認知有逐步提高，但純網路銀行的開辦還是面<br>\n臨了不少挑戰，縱使參考了各國經驗，然而會不會在執行時遇到突發狀況也值<br>\n得觀察。但不可否認的是FinTech已經成為了世界的潮流，要如何應對這股潮流<br>\n也只能端看各國的法令限制是否能跟上變化了。</p>\n<!-- /wp:paragraph -->', '2018-11-23 16:42:46', '【時事分享】純網銀申請設立開始 金管會嚴格把關', 'article', 'publish', '2019-01-03 16:46:26', '', 0),
(2778, 2, '/upload/2019/01/1120.png', NULL, '<!-- wp:paragraph -->\n<p> 隨著科技快速發展，互聯網以及各式平台app的推出，我們似乎已經熟悉這個網路科技的時代，Airbnb、Uber、Tinder等不同領域的媒合平台已經滲透我們的生活，普匯作為P2P網路借貸平台也是以信任為基礎在維持投資人與借款人雙方的關係。不過是什麼原因讓我們能夠無端地去相信網路背後那未曾謀面的陌生人呢？我們相信的是平台，還是人呢？Rachel Botsman在TEDSummit中剖析人們產生信任的原因，並且運用信任機制的建立與發展來解釋目前人們使用互聯網的狀況，其中也有提到許多大家習以為常的應用程式，讓我們來了解一下平台運作的重要基礎「信任」吧！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>信任是什麼？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;信任是個難以捉模的概念，但是沒有信任生活中很多事情都沒有辦法被完成。老闆聘請員工做事，讓公司得以運作，是信任; 每天安全地搭捷運上下班，是信任，我們在無形中對他人產生信任，但卻沒有深入思考他究竟是怎麼形成的，也沒有想過他在人生中扮演的是怎麼樣的角色。一般人會將信任定義成「事情能順利解決的機率的風險評估」，但是講者認為這樣讓信任顯得理性而且可以預測，沒有辦法捕捉人的本質，以及信任是如何讓我們與彼此相連結。Rachel將信任定義成：「與未知事物建立的自信關係(a confident relationship to unknow)」，因為這樣才能解釋信任為何擁有讓我們應付不確定事物的能力，讓我們相信陌生人，並讓我們持續向前邁進。Rachel也提到，人們在進行信任大躍進的時候表現地相當出色，不知道大家是否還記得你第一次將信用卡資訊輸入網站時的情景呢？<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>信任階層</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;研究數百個網路和市場的結果顯示，&nbsp;大家都會遵循一個共同的信任建立模式，Rachel稱之為「信任階層」，共有三個層次，分別是信任這個想法、對平台有信心、用少少的資訊決定另一端的陌生人是否值得信任，以Uber為例，我們要先相信共享汽車這個想法是可行且安全的，然後相信Uber平台能夠在你有困難的時候給予協助，最後是判斷平台另一端的駕駛是否可以信任。當我們第一次爬上信任階層的時候，會感到奇怪甚至危險，但是最後我們會習慣這樣的模式以及日常，行為往往轉變地非常快速，也可以說信任促使了改變還有創新。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>信任的進化階段：本地→體制→分配屬性</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;其實在人類的歷史上，信任只有三個顯著的進化階段：本地階段、體制階段，以及我們即將進入的分配屬性階段。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;在18世紀中葉，信任是圍繞著緊密關係而建立的，假設我住在只有5個人的小村落，大家彼此都熟識，而我今天要向A居民借錢，如果我沒有還他錢，大家都會知道我這個人沒有信譽，以後也不會想要跟我有金錢上的來往。這個時期的信任具有本地屬性，以問責為基礎。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;19世紀中葉社會發生了巨大的改變，人們開始往發展快速得城市移居，不熟悉個人狀況的大集團取代了本地的銀行家與人們進行互動，我們開始信任權力機關的黑箱體制(black box)，例如法律、監管規則和保險，這不是我們一般了解的黑箱，而是指人對人的信任開始經由看不見的黑色箱子建立，我們相信的是機構，而不是直接相信人。這個時期的信任具有體制屬性，並以委託為基礎。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;不過長期以來，人民對於體制以及公司的信任持續下降，體制的信任失效使人們開始重新反思，現在數位時代所發生的種種並不能藉由質疑體制規模還有結構來解決，而最終我們了解到對體制的信任不是為現在我們所處的時代而設立的，信任的模式正在被顛覆、被摧毀，然後重新建立。信任在世界的流通方式已經改變，而這也使信任的體制轉向適合21世紀的分配屬性模式，信任的本質回到人際的分配，並且以問責為基礎。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Airbnb的分配屬性信任</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;我們舉Airbnb為例子，有位國際飯店品牌的執行長提到在與Rachel的對談中提到，他難以理解Airbnb這個依靠陌生人願意互相信任的公司為什麼會在191個國家良好地運作，而Rachel卻告訴他：「我在一般的飯店用完毛巾都懶得掛起來，但若是身為Airbnb的顧客我就不會這樣做。」因為房東會在事後給予房客評分，而這評分影響到顧客日後的交易權利，信任在無形中改變我們現實世界的行為，並且以我們從來沒有想過的方式來讓我們變得更有責任感。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>科技建立的信任</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;Uber還有中國的滴滴出行相信大家都有所耳聞，它們以共享汽車平台打入市場，是以科技建立人際信任最強而有力的例子，在美國每天會有500萬人進行信任大跳躍搭乘Uber，而滴滴出行在中國每天的共乘次數達到1,100萬次，這兩個例子讓我們看見科技如何以意想不到的方式和規模建立人與人之間的信任。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;普匯金融科技打造P2P借貸平台，希望藉由人際間的信任來達到投資借貸的目的，以信任為基礎，透過平台的媒合進行小額投資與放款。要讓人們願意從「知」走向「未知」需要以信任為跳板，普匯平台作為最堅強的跳板，藉由信用分數的建立以及實名認證的機制，透過平台給予大眾信任來推行投資業務，讓大家對金融科技的恐懼不再存在。<br></p>\n<!-- /wp:paragraph -->', '2018-11-20 15:42:58', '【普匯觀點】無形中我們開始相信陌生人了？談談信任的建立', 'article', 'publish', '2019-01-03 16:48:12', '', 0),
(2781, 1, '/upload/2019/01/1129.jpg', NULL, '<!-- wp:paragraph -->\n<p>最近有一個新名詞「斜槓青年」正在流行，斜槓這個詞彙其實是來自《紐約<br>\n時報》專欄作家麥瑞克·阿爾伯撰寫的書籍《雙重職業》，從英文的「Slash」翻<br>\n譯而來。現在越來越多人因為興趣不同、喜好挑戰，不再只從事單一職業，因<br>\n此在自我介紹的時候會用「斜槓/」來區分自己不同的身份，所以才會稱擁有多<br>\n重工作身份的青年族群為「斜槓青年」。不過現在有些斜槓青年不是為了興趣<br>\n以及熱情而「斜槓」，反而是因為想追求更高的收入才讓自己同時擁有多份工<br>\n作，今天撰寫這篇文章是想要告訴大家，想要提早財富自由擁有退休財富不一<br>\n定要當個斜槓青年，利用「斜槓理財」我們也能創造更多的財富！<br>\n台灣人的儲蓄主要用於退休、投資、旅遊<br>\n根據萬事達卡 2017 年「消費者購買傾向調查－金錢管理」的調查報告顯示，<br>\n台灣受訪者中每個月都會撥出平均24%的收入用來儲蓄，比例高於亞太地區平<br>\n均值18%，而且有將近87%的人表示未來的儲蓄金額只增不減，主要是為退休<br>\n(55%)、投資(54%)及國外旅遊(50%)做準備，若有額外收入，也會優先考量作為<br>\n投資或退休金用途，有超過9成的受訪者認為投資應趁早，早賺錢就能提早累積<br>\n財富。<br>\n台灣人對App理財的接受度居亞太地區之首<br>\n隨著生活習慣改變，目前有將近1/3的受訪者表示考慮使用行動裝置 App 進行<br>\n金錢管理，顯示智慧行動裝置的發展已經逐漸改變台灣消費者的理財習慣。根<br>\n據某數位化應用平台所頒發的 2017 最佳 App 下載排行榜，記帳 App 已經登上<br>\n亞太地區的熱門榜，顯示行動裝置技術發展已影響消費者行為，民眾已經開始<br>\n體會到用行動裝置進行金錢管理的便利。調查中也發現，習慣用行動裝置管理<br>\n金錢的台灣消費者(26%)較去年同期成長 5%，也有37%的消費者未來會考慮以行<br>\n動裝置進行金錢管理，比例高居大中華區之冠，顯示台灣消費者擁抱新事物的<br>\n傾向。<br>\n台灣投資人的資產配置偏保守<br>\n由下圖可以看見台灣、日本、美國、歐盟以及中國目前的金融資產配置情形，各國<br>\n的投資基本款有存款/保險養老金/股票/債券，不如美國的低儲蓄率，台灣將超過4成的<br>\n資金放置在銀行定存等儲蓄項目，這其實是累積財富最沒有效率的配置，台灣一年的<br>\n通貨膨脹率在2.6%左右，銀行定存年利率大約1%，也就是說扣除通貨膨漲的影響，我<br>\n們存在銀行的錢一年後會不增反減，利息收入根本不夠支付通貨膨脹造成的物價上漲<br>\n。投資人視股票為投資收益最高的管道，台灣的股票投資配置有將近4成的權重，但許<br>\n多人都忽略了股市背後的高風險，那些利用股票一夕致富的人終究是少數，背後不知<br>\n道還有多少血流成河的案例，在享有高收益的同時也承擔了大量的風險。<br>\n我們還可以看到，在擁有固定收益的債券投資上，台灣的配置比重是偏低的，美國<br>\n有將近6%、歐盟將近4％的資金用來投資債券，債券投資比起股票有比較低的風險，<br>\n雖然收益沒有股市來得吸引人，但穩定的報酬以及低風險的性質卻非常直得當作投資<br>\n標的，除了中國前陣子因為國企債務問題使得民眾不敢投資債券，筆者認為收益較股<br>\n票市場來的低不該是投資人不投資債券的原因，穩定的收入以及低風險的環境可藉由<br>\n長期投資來累積財富，建議各國投資人可以增加在債券市場投資的比例。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯債權投資，報酬率低不再是你不想投資的藉口！<br>\n上述有提到，投資人不願進入債券市場是因為債券的投資報酬率相較於股票<br>\n市場沒有那麼吸引人，綜合台灣人對理財App的接受程度日漸上升以及資產配<br>\n置習慣，現在市面上有了一項符合時代趨勢又能有效率投資的產品！<br>\n普匯金融科技是一個P2P借貸平台，P2P就是peer to peer的意思，點對點或是個<br>\n人對個人的連結，藉由平台來媒合資金供給與需求雙方，讓資金供給者能因此<br>\n賺取高報酬，資金需求者也能藉此緩解金錢需要。大家一定很好奇筆者所謂的<br>\n高報酬是什麼，我們接下來以台灣上班族平均的薪資收入以及儲蓄偏好來計算<br>\n給大家看：<br>\n目前台灣的平均薪資的中位數30,000元來看，儲蓄率為24%，也就是每個月會有<br>\n7,200元的儲蓄，如果將這筆儲蓄拿來做定期定額的普匯債券投資，投資標的的<br>\n年均報酬率落在12%左右，並將每月取得的利息收入加上該月的定額轉投資至<br>\n下一期，長期投資五年，總投資報酬率有98%，換算下來每年可以有將近20%的<br>\n報酬率，比起將薪水存在銀行定存的1%利率，債權投資是不是相對有效率的多<br>\n呢？<br>\n銀行定存：<br>\n一個月存入7,200元，以複利計算儲蓄5年，總共投資432,000元，在投資期間結<br>\n束後可獲得445,134元，利息收入13,134元，5年的總投資報酬率約3%左右，1年<br>\n看下來只有不到1%的投資報酬率。<br>\n普匯債權投資：<br>\n期數 每月本金 每月回款 每月投<br>\n入</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>期數 每月本金 每月回款 每月投入<br>\n1 7,200 7,200 9 7,200 3,203 10,403<br>\n2 7,200 339 7,539 10 7,200 3,692 10,892</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3 7,200 694 7,894 11 7,200 4,205 11,405<br>\n4 7,200 1,065 8,265 12 7,200 4,742 11,942<br>\n5 7,200 1,454 8,654 13 7,200 5,304 12,504<br>\n6 7,200 1,862 9,062 14 7,200 5,893 13,093<br>\n7 7,200 2,288 9,488 15 7,200 6,509 13,709<br>\n8 7,200 2,735 9,935 16 7,200 7,155 14,355<br>\n(以此類推，累計至5年後，共計60期)<br>\n每月定期定額投資7,200元，下個月的利息收入馬上轉投資下一期標的，複利計<br>\n算在5年後可收回642,203元，投資淨值為323,676元，額外的投資收益為318,527<br>\n元，5年的總投資報酬率約98%，1年看下來有將近20%的投資報酬率！<br>\n結語<br>\n看到債權投資的年報酬率，是不是興起一股想開始理財的慾望呢？除了投資報<br>\n酬率高之外，普匯平台背後的風險控管團隊也致力於營造低風險的投資環境，<br>\n讓投資人可以輕鬆無負擔地享受高報酬，投資風險由我們來控管，並跟上目前<br>\n的科技趨勢，所有的投資步驟只要在智慧型裝置上就可以快速完成，不用親自<br>\n跑銀行，給你最方便快速的理財體驗！</p>\n<!-- /wp:paragraph -->', '2018-11-29 16:47:34', '【普匯觀點】做個斜槓青年才能提早財富自由？斜槓理財才是提早退休的關鍵！', 'article', 'publish', '2019-01-03 16:49:41', '', 0),
(2784, 2, '/upload/2019/01/1122.png', NULL, '<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;今年一月，歐洲的銀行業掀起了一陣風暴，政府要求銀行要將過去視為內部機密、層層保護的資料，透過開放API，提供給授權的第三方業者使用，那麼開放API到底是什麼意思呢？<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>透過API可以在一個頁面完成許多事情</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;API是Application Programming Interface（應用程式介面）的縮寫，API是讓廠商能授權第三方開發者開發額外應用程式來強化他們的商品所給出的系統介面服務，廠商的開發人員在進行編成的時候不需要了解該程式如何運作，便可透過API進行衍生開發。說了長長一串可能還是有些模糊，我們就舉幾個常見的API應用吧！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.在各類新聞網站能透過FB案讚、分享或留言。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.店家在網頁中嵌入Google Map，讓消費者能精確知道店家位置。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.在IG上傳照片能透過分享鍵將照片分享至各個平台。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>銀行開放API融入消費者生活</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;近年來全球的金融業都在力拼數位轉型，透過網路來發掘潛在客戶將會是各家銀行的重點策略，此時開放API便是不可或缺的手段之一。透過開放API給合作的第三方業者，消費者能在瀏覽網頁時不用特別連接到銀行網站，直接在購買商品的網頁進行付款、轉帳甚至信貸的功能。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>銀行開放API會危害個資嗎？</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據國際數據資訊(IDC)預估，至2018年底，50%的全球領先銀行將開放至少5項API，領域包括財務訊息、信貸相關訊息、忠誠與獎勵相關訊息、非財務訊息、交易相關訊息、位置相關訊息、產品訊息等七大類。傳統銀行所持有的客戶資料將透過第三方網站傳遞，不少人對於這樣的流程感到憂心，擔心自己的個資受到不肖業者盜取。台灣在這塊的法規尚未完善，金管會於今年7月表示會分四個階段開放不同的API項目，來確保API不會發展過快以致法規跟不上變化。但主管機關仍可以參照在歐洲訂立的歐盟支付服務指令修正案(PSD2)，要求更嚴格的資安措施和驗證來降低開放API遭盜用的風險。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>台灣各銀行開放API的未來發展</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;台灣各家銀行對於API已經陸續有所行動，現在我們上網能直接透過開放API輸入信用卡號付款就是銀行與消費平台的API串聯服務。但透過API申請信貸、放款對於台灣的各銀行來說仍是需要評估的部分。即使開放API在銀行貸款的部分尚未成熟，近幾年新興的借貸方式(如普匯的P2P借貸)也迫使銀行業面臨各種挑戰，未來幾年傳統銀行將面臨那些挑戰，又會祭出那些解決方案就讓我們拭目以待吧！<br></p>\n<!-- /wp:paragraph -->', '2018-11-22 12:48:50', '【普匯觀點】銀行開放API你了解多少？', 'article', 'publish', '2019-01-03 16:53:48', '', 0),
(2787, 1, '/upload/2019/01/1127.jpg', NULL, '<!-- wp:paragraph -->\n<p>2018G20高峰會將在11月30日至12月1號舉行，這次舉辦地點為阿根廷首都布宜<br>\n諾斯艾利斯，由財政部長及德國聯邦財政部長共同協商合作，本屆會議將討論<br>\n全球經濟增長面臨的威脅，特別是貿易戰對新興國家的負面影響和所帶來的風<br>\n險，因此這次的高峰會有可能成為中美貿易大戰是否停火的關鍵，白宮經濟顧<br>\n問柯德洛表示，中美雙方確實正在商討高峰會會談一事，各國無不都緊張關注<br>\n雙方互動。<br>\nG20是甚麼?<br>\n首先，還是有很多人不知道G20到底是甚麼，會議的用途以及對我們<br>\n的影響是甚麼，現在就讓小編來為大家一一解惑！<br>\n二十國集團(Group of Twenty）是一個國際經濟合作論壇，於1999在德國柏林<br>\n成立，由七國集團（美國、英國、法國、德國、義大利、加拿大、日本），金磚五<br>\n國（中國、印度、巴西、俄羅斯、南非），七個重要經濟體（澳洲、墨西哥、韓國<br>\n、土耳其、印尼、沙烏地阿拉伯、阿根廷），以及歐盟組成。<br>\n二十國集團的GDP總量約佔全球GDP的85％，貿易佔全球貿易總額的80%以上<br>\n，人口約佔全球人口的2/3。<br>\nG20原先是八大工業國組織，由美國財政部長提出，隨著工業發展及架構的漸<br>\n趨成熟，在2008年二十國集團領導人宣布取代八國集團，其目的是防止類似亞<br>\n洲金融風暴的重演，對於工業化國家和新興市場國家之間實質性的問題進行開<br>\n放及有建設性的討論和研究，相關國家也會就國際經濟及貨幣政策舉行非正式<br>\n的對話，以求合作並促進國際金融的穩定和增長。按照以往慣例，國際貨幣基<br>\n金組織與世界銀行列席該組織的會議。<br>\n對我們有甚麼影響?<br>\n每年的高峰會都會依照當下國際政治局勢的現況來提出相關議題進行談話，涉<br>\n及的層面跨多領域，像是環境、金融、政治、貿易…等都是曾提出過的問題。<br>\n例如去年在德國漢堡舉辦的高峰會中，各國領導人就世界經濟、打擊恐怖主義<br>\n和氣候等內容展開討論，對於熱點問題「全球暖化」也有宏觀性的政策協調及<br>\n建立各國共識，全球股市也可能因高峰會的決策而受波盪，就像是2009年在英<br>\n國倫敦舉辦的高峰會，當年的會議結論對全球經濟都帶來重要的成果，其中包<br>\n括金融監理方面決定嚴格管制「避稅天堂」，加強管制「對沖基金」、提供國<br>\n際貨幣基金會總共七千五百億美元的緊急基金（目前僅有二千五百億美元），<br>\n來強化援助陷入金融危機國家的能力、管制避稅天堂，加強打擊洗錢…等，這<br>\n個重大利多消息激勵了各國股市，美國道瓊指數上漲了百分之三點六，歐洲股<br>\n市普遍勁揚百分之五；台灣股市雖然只漲了五十六點，但卻是創了六個月來的<br>\n新高！經濟可能復甦的預期也導致油價上漲了百分之九，但G20掌握的是大方<br>\n向，因此對短期市場的影響不大，近年來也有相關人士爭議是否該限縮議題的<br>\n內容，但整體來說所有提出來的內容都和我們生活息息相關。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>中美貿易戰的停火?<br>\n中美貿易戰進行至今影響加劇，世界各國各個密切關注貿易糾紛何時能化解，<br>\n為什麼明明是兩國的戰火卻對全球造成如此大的影響?<br>\n中美兩大國若持續在貿易談判上互不相讓，彼此使出更狠的絕招與手段，將讓<br>\n世界金融體系增加更多不穩定性及不確定性，尤其是開發中國家的衝擊更是巨<br>\n大，面對相互的高關稅，全球產業鏈也深受牽連，全球經濟動盪不安，人民身<br>\n受其害，而這次的高峰會會是美中貿易戰的「關鍵點」，中美雙方可能在峰會<br>\n期間達成「停火」協議緩和目前緊張情勢，究竟川普與習近平兩人的談判會不<br>\n會擦出甚麼令人咋舌的火花呢? 讓我們繼續看下去吧！</p>\n<!-- /wp:paragraph -->', '2018-11-27 16:50:46', '【普匯觀點】G20高峰會—中美貿易大戰的停哨站', 'article', 'publish', '2019-01-03 16:53:40', '', 0),
(2792, 2, '/upload/2019/01/1126.png', NULL, '<!-- wp:paragraph -->\n<p>在經過雙11的大肆採買後，緊接而來的另一個購物節-黑色星期五也即將來臨啦！黑色星期五是每一年感恩節的後一天，被美國人視為聖誕購物期的正式開始，也是每年零售業者在聖誕假期期間銷售額的指標日，是一年之中各個商家最看重也最繁忙的一天。在這個買買買的聖誕節，我們也來了解一下背後的小故事吧！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>黑色星期五的由來</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>為什麼黑色星期五會叫做黑色星期五呢？這個節日在感恩節的後一天，感恩節是每年11月的第四的星期四，在隔天，也就是星期五，美國的人們會開始聖誕節的大採購，很多商店因此有大額進帳，以前都用不同顏色的墨水來記帳，黑色代表有盈餘，紅色代表虧損，所以我們才會把這一天叫做黑色星期五，商家的員工也會戲稱真的是「黑色」星期五，因為會忙得要死(笑<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>演變到如今的購物節，大部分的商店在星期五的凌晨五、六點就會開門，並且在感恩節前就在報紙上刊登廣告，發放優惠券來吸引客人，美國民眾也會把這一天看作準備聖誕節禮物或是撿便宜的好時機，商家也利用「限量」來吸引顧客，很多熱門商店在前一天晚上就會大排長龍。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>電商市場的AI應用</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>淘寶在雙11替阿里巴巴帶來的營收相當可觀，在互聯網發達的現在，電商已經成為零售業不可忽視的一個區塊，也是各大廠商掌握商機的重要媒介，現在只要想到雙11購物節就會直覺聯想到淘寶，那麼黑色星期五在美國造成的炫風，電商是怎麼來應對的呢？讓我們來看一下吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>現在的電商平台會用AI來做兩件事情：預測需求、個人化服務</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>預測需求</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>平台會利用資料分析來調整庫存，並預測消費者的最高願付價格以利定價，並管理物流相關的業務，在出清存貨的同時也能讓消費者搶購最新商品。以美國的最大零售電商平台Amazon為例，他們依據各地消費者的購買習慣準備適當庫存量，並節省運輸費用，使銷售的總成本下降，同時讓消費者享有較低的價格，帶來雙贏的局面。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>個人化服務</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>個人化服務是由AI中的機器學習所提供的，以人工神經網路模擬人腦的學習過程，檢視不同「物件」間的相關性。舉例來說，平台可以透過消費者平時的購物習慣來歸納「你可能喜歡的物品」，或是以購物偏好做差別定價，並藉由搜集消費者的偏好與支出慣性，來影響消費者決策，方便平台業者做銷售策略的制定。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>結語</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>AI的誕生與應用讓整體的消費體驗更美好，網路購物的盛行也讓消費者能夠在家輕鬆享受購物節的優惠與購物的樂趣，大家再買買買的過程中也不妨想想背後的科技應用，看到「你可能喜歡的物品」時可以認真想一想這是不是真的符合自己的需求，體驗一下人工智慧的厲害吧！<br></p>\n<!-- /wp:paragraph -->', '2018-11-26 18:00:41', '【時事分享】美國最大購物節-黑色星期五', 'article', 'publish', '2019-01-03 17:06:49', '', 0),
(2794, 2, '/upload/2019/01/0103.jpg', NULL, '<!-- wp:paragraph -->\n<p>全球的銀行在近幾年都在進行轉型，無疑是為了配合科技的進步所需進行的改革，今天將在這篇文章介紹Brett King所撰寫的《銀行X.0》系列，對銀行的演變進行詳細介紹。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>銀行1.0</strong><strong>：大眾熟知的銀行</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這是銀行最初的型態，銀行的服務以地區做為區分並經辦各項業務，也就是我們日常生活中會看到的各式分行。民眾對於銀行服務的認知還停留在一定要去分行才能辦理相關業務，也因為這個原因，銀行無法進行分工的專業化，繁雜且耗時的流程常常耗費掉許多人力與時間。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>銀行2.0</strong><strong>：網路銀行開始出現</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這個時期銀行開始尋求變革，網路銀行便是其成果的體現。但網路銀行仍未成為主流，一般大眾仍習慣到分行辦理各項業務，且銀行業務還是存在著侷限性。舉例來說，當一個新顧客在線上完成開戶後，在大多數銀行，IT系統仍然在記錄客戶是被「分配」或「聯繫」到客戶首次開戶的網點，簡單來說，銀行雖然透過電子型態擴張，但這仍不會成為銀行的著重的重點，只是眾多宣傳方式的其中一種。<br>\n<br>\n</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>銀行3.0</strong><strong>：銀行不再是一個地方，而是一種行為</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>銀行3.0可以視為銀行2.0的擴展，這不單純只是科技上的進步而帶來更多的服務，銀行開始著眼於未來消費者的消費習慣與偏好，尋求新的風險管理模式、降低金融行為中間的行為成本和流程。並隨著科技發展及資訊傳播的進步，銀行開始重視網路上面的服務，這個時期的消費者在進行抉擇時，比起商品內容更相信口碑，各式行動支付和P2P也在此成為銀行的競爭對手或合作夥伴，銀行將在這個階段應對數位轉型的衝擊，傳統銀行的存在開始岌岌可危。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>銀行4.0</strong><strong>：金融服務將無所不在</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在銀行4.0中，銀行會更加重視「應用情境」，並且由AI來建議人們的消費行為。你可以在任何地方得到來自AI的建議。舉書中的例子來說，你在家中跟智慧音箱助理說你想購買一個XBOX來當作聖誕禮物，但AI卻告訴你若是進行這項非預期消費，可能會使下個月旅行的費用出現短絀，這個警告讓你重新思考是否真的需要進行這項預期外的消費。這些AI理財顧問能深入民眾的生活給予建議，彷彿有個理財專員24小時伴隨身邊。對於銀行方面則會有內部的流程自動化（RPA）改造，另外現實增強（AR）、語音辨識設備、穿戴智慧設備、無人駕駛、5G通信、區塊鏈都會是銀行需要適應的新技術。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>消費者行為改變的四個破壞性階段</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>隨著科技的進步，傳統金融服務必定會面臨變革，Brett King將這些區分為四個破壞性階段，探討消費者和銀行的行為在這些階段會進行哪些改變。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>階段一：消費者有主控權和多元選擇</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>網路的普及讓消費者不必像以前以樣任何事都必須親臨銀行，消費者可以選擇在自己喜歡的時段用自己喜歡的方式來完成絕大多數的服務，並且群眾會開始以社群的方式去檢視傳統銀行，傳統銀行不再擁有所有金融服務的主導權，他們必須參照「民意」提出服務，否則就必須面臨被輿論公審的壓力，這個階段將顧客與銀行的上下關係進行了對調，並考驗著傳統銀行的應對能力。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>階段二：透過螢幕隨時隨地取得金融服務</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這個階段與階段一的後期大幅重疊，並且是現在進行式。人們透過螢幕直接使用各項金融平台服務，這個階段民眾正在與這些新的技術磨合，當民眾都習慣透過手機操作各項金融服務時，現金或信用卡的存在會逐漸降低，並開始進入第三階段。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>階段三：無現金、無卡的行動支付</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在這個階段，行動支付會逐漸取代信用卡，而傳統的支票、旅行支票等等會快速被民眾所棄用。使用手機完成一切的付費行動會成為多數人的選擇，而銀行帳戶在這個階段也轉變為資金存放的中繼站，在人們及廠商體認到在支付程式裡的餘額和在銀行的餘額不存在區別性時，銀行便會失去它的市場區隔性，社會將走向最後的階段。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>階段四：人人都是銀行，銀行無所不在</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當人們與各式支付平台建立信任關係後，業者會推出新的存款方式來取代銀行存款的功能，同時各式的金融平台整合會改變人們借貸的習慣，P2P借貸會是一種常態，當然其他不透過銀行的金融服務也會普及。傳統銀行已經無力回天，只能尋求與業者合作來挽留剩下的顧客。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>結論</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>銀行的演變速度十分快速，而台灣的銀行還處在銀行2.0的階段，現在各銀行雖然有在為了轉變進行改革，但是礙於法規限制以及部分思想僵化使的推出的東西並不盡人意，台灣的銀行要如何因應這些難關還有待觀察。</p>\n<!-- /wp:paragraph -->', '2019-01-03 17:09:39', '【普匯觀點】從銀行1.0到銀行4.0(上)', 'article', 'publish', '2019-01-03 17:12:15', '', 0),
(2797, 2, '/upload/2019/01/1128.png', NULL, '<!-- wp:paragraph -->\n<p>Fintech一詞炙手可熱，多數人對Fintech都抱有些距離感，但你可曾想過，Fintech也可以結合遊戲，讓你透過遊戲來賺取現實中的金錢？美國的Long Game公司推出這項服務，讓Fintech融入你的生活！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如何構築遊戲和Fintech間的橋梁</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;Long Game的創辦人Lindsay Holden觀察到美國深陷「M型社會」的危機，她查閱了相關資料，發現 70% 美國人民的儲蓄少於 1000 美元，而 63% 的美國人民無法負擔超過 500 美元的預期外支出。而在此同時，另一項資料則指出美國福利彩券每年的營收竟然接近 700 億美元，平均每個家庭每年就花費 540 美元購買彩券。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;透過這兩項資料，Holden認為美國人儲蓄方式影響了他們的生活習慣，在沒有經濟基礎之下，大多數的民眾都不願意承擔太多風險，多數民眾都只為了餬口而工作。因此Holden想透過學習彩券行，以遊戲的方式鼓勵民眾儲蓄來改善貧富問題，於是，Holden 創立了 Long Game，並和美國本土銀行 Blue Ridge 合作，讓使用者可以一邊存錢，一邊玩遊戲，一邊贏獎金。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Long Game的運作方式</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;首先要下載Long Game的app並註冊帳號，在註冊時Long Game會要求使用者提供銀行帳號作為存款時的資金匯款帳戶。之後每次登入Long Game就可以選擇新增存款，範圍由最低5美元至最高5000美元，根據你存的金額Long Game會給你相應的代幣，這些代幣無法兌成現金，但可以用來玩Long Game裡面的小遊戲。這些小遊戲很多是仿照彩券行而設計，像是刮刮樂或是樂透等等，所有遊戲都會設定現金獎，當你獲得現金獎後金額便會匯至你的帳戶中，讓你邊玩遊戲的同時也有機會賺錢！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>更多關於Long Game的細節</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;看完上面的介紹，想必不少人還是有些疑惑，在這邊我們提供三個Long Game官網整理出來的常見問題與回答提供大家參考。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Q1. 我在Long Game能領到多少利息？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A1. 只要將錢透過Long Game存放到銀行，除了Long Game將提供相應代幣外，銀行也將支付0.01%的年息，當然你也有機會透過遊戲獲得額外金錢甚至是比特幣或以太幣！目前最高額的現金獎是類似台灣「威力彩」的樂透，選定6個號碼和一個表情符號，只要全部中獎便可獲得一百萬美元的獎金！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Q2. 使用Long Game需要付任何錢嗎？我的錢放在Long Game裡是安全的嗎？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A2. &nbsp;Long Game是完全免費的，你在Long Game的存款都是經過FDIC綁帳並存放在我們的合作銀行中，當然你可以在任何時間將裡面的存款提出來！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Q3. 那Long Game是怎麼獲利的？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>A3. 每當有新用戶透過Long Game開設銀行帳戶並存款時，Long Game的合作銀行便會給付一定的獎勵金，而Long Game會將這些獎勵金用於小遊戲的現金獎以及營運支出和投資。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>結語</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> &nbsp;&nbsp;&nbsp;Long Game因為它的遊戲性和可獲利性吸引了不少年輕用戶註冊，Holden 透露說，Long Game 的使用者幾乎每天都會登入它們的系統，而使用者每週也會在 App 內玩 24 次的小遊戲。這讓 Long Game 有別與其他金融業 App，讓使用者對它產生極高的黏著性。透過Long Game我們可以發現Fintech的應用十分廣泛並和我們的生活息息相關， 可惜Long Game是美國的應用程式並且要綁定美國指定銀行帳號，台灣的朋友可能無法享受這項服務，但我們可以期待未來有一天台灣也引進這項概念，為我們的投資方式提供更多的選擇！<br></p>\n<!-- /wp:paragraph -->', '2018-11-28 15:07:03', '【普匯觀點】美國新創Fintech讓你玩遊戲也能賺錢', 'article', 'publish', '2019-01-03 17:18:19', '', 0),
(2816, 2, '/upload/2019/01/0104.jpg', NULL, '<!-- wp:paragraph -->\n<p>上篇為大家提供了銀行的演變，這篇則要從一個案例中介紹民眾思想改變的困難之處。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>改變的困難：習慣與惰性</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當然，在這些金融改革進行時總會遇到一些難關，像是肯定有人對於這些東西感到不信任，認為傳統的做法比較有保障，但真的是如此嗎？我們在這邊舉一個書中的例子：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當我們處於一個沒有人會寄送紙本對帳單，一切都已電子化進行的世界(或許10年後)，一個行員對法尊部門提出以寄信取代電子帳單的做法，對話如下：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>行員：我們可以每個月寄送對帳單給客戶，如此一來我們的顧客可以永遠保存這些資料，並且在信裡附上我們最新的活動和其他促銷訊息，可以提高銷售機會，我想這個措施的投資報酬率一定會很棒的！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>法遵人員：所以這是完全安全的對吧？你寄出的信應該不會被盜取吧？還有客戶一定要簽收就像收受掛號或快遞對吧？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>行員：那個...其實不是欸，如果要這樣處理會超出預算太多，我們只是要用平信寄送，並且用一般的信封就好，這是目前最便宜的方式了。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>法遵人員：好，先不管電子寄送會比寄送紙本更便宜這件事。這封信在交到顧客手上前，可能會經過十多個人之手，並且在寄到信箱後還要擔心會不會被偷走，你不覺得這計畫簡直是天方夜譚嗎？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>從上面對話我們不難看出紙本對帳單的缺點，但在現今社會中我們卻因為習慣了紙本寄送而忽略了這些問題，我們不會去懷疑現在收到的信是否遭到人調換，甚至連沒收到信我們都不一定會知道，這便是習慣帶給我們的錯誤認知。因此我們會懷疑電子寄送會被駭客入侵、洩漏個資等等，儘管我們知道有心人士從你家信封拿一封信與此相比是多麼地容易。另一個問題便是惰性了，額外去申請電子帳單的動作對多數人來說還是太麻煩，於是很多人選擇維持現狀，儘管他們覺得紙本沒效率且不安全。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>結語</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>從Brett King的《銀行X.0》系列中我們能知道銀行未來的走向和面臨的挑戰，在這些階段中一般大眾要面臨哪些習慣上的改變，要如何讓消費者接受新型態的商品也是各家業者要多著墨的重點，或許銀行會在不久的將來式微，如何順應潮流的變換，就要看個人或機構有沒有相應的應對能力了。</p>\n<!-- /wp:paragraph -->', '2019-01-04 16:26:30', '【普匯觀點】從銀行1.0到銀行4.0(下)', 'article', 'publish', '2019-01-04 16:26:32', '', 0),
(2826, 2, '/upload/2019/01/未命名-2.jpg', 'https://www.youtube.com/embed/EuUgJMGBH-I', '<!-- wp:core-embed/youtube {\"url\":\"https://youtu.be/EuUgJMGBH-I\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://youtu.be/EuUgJMGBH-I\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:paragraph {\"customFontSize\":15} -->\n<p style=\"font-size:15px\">最近大家都在瘋台中的谷關空氣，在蝦皮一瓶谷關空氣更是有高於1,000元的定<br> 價，其他地區也紛紛推出具有地方特色的「空氣」搭上這一波空氣熱潮。小編<br> 也是趁著這波熱潮發現，原來區塊鏈的應用中，竟然也有「空氣幣」的存在！<br> 究竟什麼是空氣幣呢？讓小編來告訴你最新鮮的區塊鏈應用吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#1583d2\",\"customFontSize\":23} -->\n<p style=\"color:#1583d2;font-size:23px\" class=\"has-text-color\"><strong>什麼是空氣幣</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>近幾年來虛擬貨幣的發展可以說是如火如荼，虛擬貨幣背後所創造出來的驚人<br>收益讓一些投機份子想藉此大賺一筆，大概在2017年，這些投機的人自己產生<br>了一些沒有實體項目支撐也沒有發佈項目代碼進度的「貨幣」進入市場，他們<br>的目的就是在網路上吹噓區塊鏈的各種好處，忽悠投資者進入市場投資以獲得<br>巨額資金，說白了點就是沒有實際的任何東西，單純用來騙錢的貨幣。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#1583d2\",\"customFontSize\":23} -->\n<p style=\"color:#1583d2;font-size:23px\" class=\"has-text-color\"><br><strong>空氣幣的特質</strong> </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.用創始人的背景和各種榮譽作為貨幣的代言。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.完全不重視技術，只顧營銷宣傳。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.白皮書上不會有實質性的技術路線說明。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.以在交易所上市為主要目的，把幣價作為衡量項目的唯一標準。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5.幣價暴漲的原因並非技術層次高或是項目有進展，而是通過營銷手段炒作。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#1583d2\",\"customFontSize\":23} -->\n<p style=\"color:#1583d2;font-size:23px\" class=\"has-text-color\"><br><strong> 空氣幣帶來的影響</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>講到虛擬貨幣大家第一個想到的一定都是比特幣，但是其實目前市面上的虛擬<br>貨幣高達2,000多種，而其中有一大部分都屬於沒有實際價值的「空氣幣」，市<br>場上也將空氣幣的存在視為虛擬貨幣的「泡沫化」，若是投資人不具有識別能<br>力以及提高自身的風險意識，非常容易因此而血本無歸。不要以為在虛擬空間<br>交易就不會有風險存在，事實上虛擬貨幣和區塊鏈的技術相輔相成，屬於新興<br>科技的應用，在近期虛擬貨幣交易價格暴漲暴跌的情況下，許多投機詐騙的人<br>也開始加入市場，再加上區塊鏈能夠跨國發展的特性，更是叫人防不慎防，因<br>此需要仰賴業者的自律以及投資人的意識來防範。投資行為有賺有賠，有高報<br>酬就一定有相對的風險，在面對新科技時除了勇於嘗試也要具有洞察能力，避<br>免因小失大，讓好不容易累積的財富都變成空氣！(哭</p>\n<!-- /wp:paragraph -->', '2019-01-07 17:08:18', '【普匯觀點】台中有谷關空氣，區塊鏈也有空氣幣！', 'video', 'publish', '2019-01-07 17:23:25', '', 0),
(2893, 1, '/upload/2019/01/180830.jpg', NULL, '<!-- wp:paragraph -->\n<p>薪薪學子們總是為了學雜費、生活費而困擾嗎?</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>為了不造成家裡的負擔許多學生每天下課都得去打工賺取費用，在求學的路上不僅無法專心向學更對身心造成了壓力，因此政府對於經濟弱勢之家庭的學生提供「就學貸款」的方案，雖然對於學生來說就學貸款方案是個非常優惠的幫助。但是優惠的學貸需為低收入戶才能申請，但需要幫助的學生卻並非人人符合資格；加上過多繁雜的手續及申請資格限制其實對許多人來說不是如此便利呢！</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>不過沒關係~ 普匯金融科技平台提供的「學生貸」輕鬆解決繁雜程序的困擾！不多說~ 小編馬上為大家做個簡單比較表，馬上來看看普匯有多便利！</p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":2894} -->\n\n<p><img alt=\"\" src=\"/upload/2019/01/1.png\" /></p>\n<!-- /wp:image --><!-- wp:paragraph -->\n\n<p>普匯隨時可申貸，提供大專院校學子最即時的協助，而且不須舟車勞頓，只需使用手機就能輕鬆完成！</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>同時普匯擁有人工智能高端科技保，能確實保障學生個人隱私安全，給予學生高信賴以及有溫度的服務。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>心動不如馬上行動！</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>馬上試算 &gt;&gt; <a href=\"https://goo.gl/9x6NAk\" target=\"_blank\">https://event.influxfin.com/R/url?p=web</a></p>\n<!-- /wp:paragraph -->', '2019-01-10 17:04:19', '【普匯報你知】只知道就學貸款? 還有這項超方便學生貸款!', 'article', 'publish', '2021-01-19 01:39:38', NULL, 0),
(2905, 2, '/upload/2019/01/111.jpg', NULL, '<!-- wp:paragraph -->\n<p>上次我們介紹了K線圖和VOL指標提供剛接觸股市的人如何看個股的價量變動，但只參考K線圖和VOL指標還是會遇到種種的狀況，因此今天我們要介紹另外幾個常用的技術指標給大家參考囉！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"customFontSize\":18} -->\n<p style=\"font-size:18px\" class=\"has-text-color has-vivid-cyan-blue-color\"><strong>KD指標</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp;\nKD指標中文譯為隨機指標，其中分為K值和D值，是用來判斷股票強弱勢以及尋找價格轉折點的常用指標。那麼我們該如何計算KD指標呢？在導入公式前我們需要先知道幾件事：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1. KD指標通常以9天為一個範圍計算</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2. RSV(未成熟隨機值)用來判斷9日內股票收盤價的強弱，公式為：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2908} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/1-1.png\" alt=\"\" class=\"wp-image-2908\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>式中，C<sub>t</sub>為第n日收盤價；L<sub>n</sub>為n日內的最低價；H<sub>n</sub>為n日內的最高價。RSV值始終在1~100間波動。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在知道這以上兩點後，我們就可以開始計算KD值了！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>K值的公式為：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>今日K</strong><strong>值 = 2/3</strong><strong>昨日K</strong><strong>值 + 1/3 RSV</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>D值的公式為：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>今日D</strong><strong>值 = 2/3</strong><strong>昨日D</strong><strong>值 + 1/3</strong><strong>今日K</strong><strong>值</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>從公式我們可以發現K值受RSV影響變動，而D值則受K值影響，並且K值的波動的幅度會較D值激烈。若要為K值和D值定位的話，K值是代表短時間變動的值，D值是代表一個波段變動的值。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"customFontSize\":18} -->\n<p style=\"font-size:18px\" class=\"has-text-color has-vivid-cyan-blue-color\"><strong>KD的常見用法</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp; 在學完KD值的計算方式後，我們來看如何應用KD值來判斷何時買進股票。因為K值較為敏感，因此當K值向上穿越D值時，我們可以判斷短期趨勢是向上的，稱為黃金交叉(下圖紅圈處)，為買進的信號；而當K值向下穿越D值時，我們可以判斷短期趨勢是向下的稱為死亡交叉(下圖綠圈處)，是賣出的信號。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2906} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/2-1.png\" alt=\"\" class=\"wp-image-2906\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp; 另外KD指標若超過80或低於20超過3日，我們將其分別稱為高檔鈍化和低檔鈍化。若高檔鈍化很久的股票我們可以判斷其漲勢十分強勁，這個時候要獲利出場或是等待時機加碼權端看投資人的風險容忍能力。但若是長期處於低檔鈍化的個股則建議少碰為妙，這類股票處於低檔的時間過久，若是沒有特別的利多消息或主力拉抬是很難有起色的，也很容易出現套牢的情況喔！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"customFontSize\":18} -->\n<p style=\"font-size:18px\" class=\"has-text-color has-vivid-cyan-blue-color\"><strong>MACD指標</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>MACD中文譯為指數平滑異同移動平均線，由於中文名稱很饒舌因此我們都以英文代稱，也是常見的技術分析指標，通常用來判斷中短期的個股趨勢。其公式較為複雜因此不在本篇贅述，接下來會主要介紹MACD指標的應用和趨勢判斷，若是有興趣的朋友可以自行上網搜尋MACD指標的計算方式喔！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>MACD指標由兩條線和柱狀圖組成，其代表的意義分別為：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>DIF(黃線): 計算12天平均線和26天平均線的差</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>MACD(DEM或DEA)&nbsp;(藍線): 計算MACD 9日均值</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Histogram&nbsp;(柱)(紅柱或綠柱): 計算MACD與DIF的差值</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":2907} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/01/3-512x1024.png\" alt=\"\" class=\"wp-image-2907\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><br>\n所以當你點進股市APP時選擇MACD的技術分析會看到類似下面的圖</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>首先我們來看柱體的方面：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1. 當紅柱體逐漸變大時，代表處於多頭的階段，可以買進，但當紅柱體不再變大甚至開始縮小時，代表股價即將反轉。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2. 綠柱體和紅柱體相反，綠柱體變大時代表處於空頭，而當綠柱體不再變大甚至開是縮小時，代表股價即將反轉，可以把握時機買進。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>再來是關於兩條線的判讀：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1. 當DIF和MACD線數值皆處於0以上時，代表此股處於相對高檔，若兩線形成的開口向上，則此股呈多頭的趨勢，可以找機會買進；反之若兩線都在0以下便屬於相對低檔，若兩線形成開口向下，此股則呈空頭趨勢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2. DIF的變動速度較MACD快速，與K線和D線相同，當DIF向上穿越MACD時稱為黃金交叉，反之則為死亡交叉，黃金交叉和死亡交叉皆為轉折的重要訊號要多加留意。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"customFontSize\":18} -->\n<p style=\"font-size:18px\" class=\"has-text-color has-vivid-cyan-blue-color\"><strong>結語</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;&nbsp;&nbsp; 今天分享的KD和MACD都是很實用的技術指標，兩者也常被拿來相互比較。由於MACD的參數週期較長，因此會被用來研判趨勢，而在確認趨勢後，會用KD來判斷買點。但技術指標通常都會延後或參雜一些難以判斷的雜訊，新手們要切記不要盲目跟隨技術指標，將技術指標當作一種參考並蒐集資訊多加評估才能提高勝率喔！</p>\n<!-- /wp:paragraph -->', '2019-01-11 16:54:57', '【普匯觀點】股市中的技術指標-KD和MACD', 'article', 'publish', '2019-01-14 12:03:02', '', 0),
(2931, 2, '/upload/2019/01/未命名-3.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"customFontSize\":35} -->\n<p style=\"font-size:35px\" class=\"has-text-color has-vivid-cyan-blue-color\"><strong>化紀律為自律</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>聯強的CEO杜書伍認為，「紀律」不是表面上的服從、說一動做一動，也不是壓迫性的命令所形成的，而是深思熟慮後的自我要求，確認心中所要追求的，然後把這項追求變成自己的習慣，將「紀律」養成「自律」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-color has-large-font-size has-vivid-cyan-blue-color\"><strong>自律不是犧牲，而是選擇</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「自律」不是不要做什麼，而是決定自己到底要做什麼。我們往往都把自律當成是「不要做什麼」的一種限制，然後把它當成逃避的藉口，於是自律變成只有少數人才能做到的事。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>那「自律」到底可以怎麼解釋呢？把原本問自己「我到底應該做什麼」的想法，轉變「應該」的角度，去思考兩個問題：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>我想要怎麼樣？</li><li>而我決定怎麼做最好？</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>不要一開始就想著犧牲、克制與紀律，而是轉個想法，從「選擇去投注一件能實現自我價值的事情」開始做起，自律不是犧牲，而是「選擇」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>自律的練習，不是列出不能做的事，而是「先列出所有要做的事」，然後在每個時間做出要做什麼事的選擇，選擇了，這個時間就做好了交換。是一種選擇的練習，而非犧牲的練習。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-color has-large-font-size has-vivid-cyan-blue-color\"><strong>自律不是克制，而是確認價值</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>所謂選擇，並非是要我們克制欲望的選擇，只是我們的欲望、想要很多，時間卻只有一個，所以這時候，自律要做的事情並非去克制欲望，而是去確認在那麼多欲望中，在當下這個時間，哪個欲望最能實現自我價值，也就是在有限的資源內，實現你最想完成的那一件事。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>自律，就是練習去確認每一個欲望的價值，然後選擇自己的價值。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>自律是建立「實現自我價值的手段」</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>康德有句名言說：「自律即自由」。他想表達的是，真正的自由，並非想到什麼就做什麼的自由，也不是屈從於單純欲望的自由，而是要「為自己立法」，如果我有一個非常想要實現的自我價值，我就要建立規則去實現它。價值不會因為我想想就實現，我需要建立一份自己的規則，讓我逐步地實現這個價值。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>自律，就是為自己找出實現價值的手段，並且去執行它。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-color has-large-font-size has-vivid-cyan-blue-color\"><strong>當公司紀律遇上自律</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>既然自律不是犧牲，而是「選擇」、是「確認價值」、是「實現自我價值的手段」，那麼面對職場上的紀律，我們又該如何自處呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>首先，必須先認同任職公司的文化。打從心裡地相信公司文化是自己想追求的，才能夠做出選擇，並在選擇中確認自己目前做的事情是能夠實現自我價值的，最後以「自律」為手段，達成職場上的目標。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>也就是說，在「紀律」的框架中尋找自由發展的方向，並以「自律」達成符合公司期望的自我追求。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"large\"} -->\n<p class=\"has-text-color has-large-font-size has-vivid-cyan-blue-color\"><strong>速度、紀律與績效</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯的企業文化：速度、紀律、績效，在這瞬息萬變的金融互聯網產業，員工們必須先意識到速度、紀律、績效三者的重要性，並在此框架中尋求自我價值與突破，於每日工作項目中做出選擇，並以「自律」為手段，營造出公司與員工互利互惠的雙贏局面。</p>\n<!-- /wp:paragraph -->', '2019-01-15 17:28:34', '【普匯觀點】PuHey!精神文化-紀律與自律', 'article', 'publish', '2019-01-15 17:32:03', '', 0),
(2975, 1, '/upload/2019/01/未命名-1-6.jpg', NULL, '<!-- wp:paragraph -->\n<p>由於借款端和投資端對於P2P剛性需求持續增強，2019年P2P的市場規模仍將處於穩定的上升通道。未來，P2P細分領域也將出現更好的發展機遇。接連的事，隨著P2P市場日益壯大，第三方電子簽名行業也跟著萌芽! </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>1.</strong><strong>電子簽名是什麼</strong><strong>?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>與電子文件有關，用來辨識及確認電子文件的簽署人身分、資格及電子文件真偽者。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>2.</strong><strong>電子簽章法的目的</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>建立安全及可信賴之網路環境，確保資訊在網路傳輸過程中不易遭到偽造、竄改或竊取，且能鑑別交易雙方之身分，並防止事後否認已完成交易之事實，是電子化政府及電子商務能否全面普及的關鍵。為推動安全的電子交易系統，政府及民間企業正致力於利用現代密碼技術，建置各領域之電子認證體系，提供身分認證及交易認證服務，以增進使用者之信心。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>3.</strong><strong>電子簽章法的立法原則</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(一)&nbsp; 技術中立原則：任何可確保資料在傳輸或儲存過程中之完整性及鑑別使用者身分的技術，皆可用來製作電子簽章。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(二)&nbsp; 契約自由原則：對於民間的電子交易行為，應該在契約自由原則下，由交易雙方當事人自行約定採行何種適當的安全技術、程序及方法作成的電子簽章或電子文件，作為雙方共同信賴及遵守的依據，並作為事後相關法律責任的基礎，不應該以政府公權力介入交易雙方的契約原則；交易雙方可以自行約定共同信守的技術作成電子簽章或電子文件。另憑證機構與其使用者之間，也可以契約方式規範雙方之權利及義務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(三)&nbsp; 市場導向原則：政府對於憑證機構的管理及電子認證市的發展，應以最低必要的規範為限。今後電子認證機制的建立及電子認證市場的發展，應由民間主導發展各項電子交易所需的電子認證服務及相關標準。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>4.</strong><strong>透過第三方電子簽名幫助大家投資更有保障</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>過去電子簽證為人詬病的地方在於電子資料容易被篡改以及刪除。一旦有人惡意篡改數據，無論出借人保存與否，後續的追訴都會面臨重重困難。所以，電子合同信息的妥善保存，是金融消費者保護中非常重要的一環。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>與平台自行保管數據相比，上線第三方電子合同存證後，出借人的交易記錄及合同信息將由可信的第三方進行保管，杜絕了篡改風險，為後續的權益維護和資金追討提供了基礎和前提。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>5.</strong><strong>透過影像電子簽章確立簽署人</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>影像電子簽章(SelfieSign)結合電子簽名以及自拍錄影，利用電子壓力偵測筆跡粗細，辨別當事人簽名書寫習慣，並且錄下簽署過程的影像，明確記錄了和約簽署當事人的意願，以及簽署時間、地點等有利證據。更重要的是，萬一碰到糾紛或是有問題的時候，都可以追溯這些證據，正所謂「凡走過必留下痕跡，凡簽過必留下證據!」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>結論:在有了許多科技的發明相繼出現，許多過往人力無法處理的問題，都可以被完全改變，各式各樣的信用問題逐漸能夠安全且更透明的被解決!</p>\n<!-- /wp:paragraph -->', '2019-01-16 15:11:07', '【Fintech】你還在用傳統簽名?第三方電子簽名行業正在崛起!', 'article', 'publish', '2019-01-19 09:21:43', '', 0),
(3040, 1, '/upload/2019/01/未命名-1.png', 'https://www.youtube.com/embed/02xltnhLgd0', '<!-- wp:core-embed/youtube {\"url\":\"https://youtu.be/02xltnhLgd0\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<div class=\"wp-block-embed__wrapper\">全球各地相勁將區塊鏈、AI技術融入金融產業，根據美國調查預測分析，區塊鏈技術在未來能為更多沒有銀行賬戶(unbanked)或無法得到銀行服務(unbanked)的人提供機會，進而達到普惠金融的目標，到2020年底，無銀行賬戶的人數將從17億減少到10億以下，未來大量能受惠的人都源於移動銀行普及化的區域。</div>\n<!-- /wp:core-embed/youtube --><!-- wp:paragraph --><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>為什麼非洲會有發展金融科技的潛力?</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>根據美國G2 CROWD資料顯示，非洲80%的人口(326百萬成人)是無法取得銀行服務或是沒有帳戶的，其次則是中東有67%，更進一步資料顯示，非洲無法獲得金融服務的人口有70%來自鄉村地區，46%來自貧窮區域，這樣子的數據顯示出金融科技的驅動力在於市場的規模。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>隨著西方在移動銀行上建立新的標準，非洲各國不需要再採用過去的技術如ATM、線上辦理金融業務等，他們直接跳到新的、無摩擦性的移動基礎設施，如今沒有傳統銀行帳戶的人皆可以透過移動銀行解決支付的問題，雖然非洲80％以上的人無法使用傳統銀行業務，但超過2.2億人是註冊的移動用戶。而且，雖然只有63％的非洲人口能夠獲得自來水，但93％的人口擁有移動設備。沒錯，契機的源頭就在這。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>以肯亞為例，超過3700萬的人口都有一隻具有發信功能的手機，但回頭看看ATM甚至是筆記型電腦的數量相較下來顯得後濟無力，沒錯這便是發展的一大契機！最早發展的是M-Pesa在2007推出的移動式錢包，他們為數百萬非洲人提供數字金融服務。大多數住在肯亞的人都住在鄉村地區，但幾乎所有的工作和銀行都在市中心，為了將薪水送回家中每個勞工者都必須將他的薪水放入信封然後付錢請郵差送件，過程中長則可能數天最快也要好幾小時，因此M-Pesa 開始將過程數字化讓低收入的勞工也有能力在沒有任何實體運輸風險的狀況下將金錢馬上送到家。因為可以使用手機進行支付且手機的距離比銀行分行短，所以通過M-Pesa匯款的費用大約是其他正式國內匯款服務的一半，不僅如此他們還為無銀行帳戶家庭提供金融服務使交易更為便利。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>但是當一個問題解決後，就會有更大的需求產生，M-Pesa使國內支付變得更加便利，但當需要跨境支付的時候交易費用可能飆升至31%，隨之而來的目標便是連結整個非洲人口的移動性基礎設施。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>金融科技隨之來的影響? </strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>iGDP是衡量互聯網對整體GDP貢獻的指標。儘管非洲的iGDP目前處於較低水平，但麥肯錫預測到2025年非洲的iGDP應該增長至少5％至6％，與瑞典等主要經濟體相比達到6.3％，台灣和英國的增長率則為5.4％。非洲國家，肯亞以及塞內加爾在iGDP方面處於領先地位。塞內加爾的iGDP為3.3％，肯尼亞為2.9％，摩洛哥為2.3％，南非為1.4％。如果互聯網發展的速度和手機普及的速度相當時，預計iGDP將佔GDP總量的10％，即3000億美元，並同時會帶來經濟和社會發展的增長。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>非洲蓬勃的城市化人口發展或是零售業者移動能力的提高都是有助於非洲電子商務創新的特徵，到2025年，技術將使60％以上的非洲人獲得銀行服務，超過90％的人使用移動錢包進行日常交易和匯款。金融科技可能為非洲帶來無限轉型的可能。</p>\n<!-- /wp:paragraph -->', '2019-01-18 16:09:57', '【Fintech】金融科技如何翻轉非洲?', 'video', 'publish', '2020-06-04 07:41:33', '', 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(3069, 2, '/upload/2019/01/債轉海報-1.jpg', NULL, '<!-- wp:core-embed/youtube {\"url\":\"https://www.youtube.com/watch?v=zjDJ2E6RCBg\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://www.youtube.com/watch?v=zjDJ2E6RCBg\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>債權轉讓</strong>：出讓人依合約將持有的債權轉移給受讓人。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>申請流程</strong>：出讓人上架債權 -&gt; 受讓人認購 -&gt; 出讓人取得現金</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>對於許多投資人來說，還是有很多人對於「債權轉讓」抱有疑問，為什麼要做債權轉讓?債權轉讓對於投資人來說的益處是甚麼?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>投資市場裡多樣化的產品給予投資人有許多理財的好選擇，但是對股市而言，世界各國的動盪、企業的營運都會對股市造成極大的影響，收益高時相對風險也高，有可能一夕之間就會血本無歸，在這樣子資金調度受限制的狀況下，普匯投資端的「債權轉讓」就提供了另一種選擇！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"customFontSize\":31} -->\n<p style=\"font-size:31px\" class=\"has-text-color has-vivid-cyan-blue-color\"><strong>債權轉讓優點</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>出讓人</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.增加債權流動性</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 普匯平台提供債權轉讓服務讓投資人可以隨時隨地轉讓債權，使債權流動性大幅提升，遇到臨時需要資金的情況也不須煩惱！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.靈活規劃理財方法</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 透過將債權轉讓取得現金後，投資人可以選擇將現金再進行投資或是做為其他用途使用。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>受讓人</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.期數較短方便理財</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 受讓人認購的債權與相同條件的一般債權相比期數較短，到期日也更近，讓理財規畫更加靈活。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.選擇還款正常的債權</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 在選擇標的時可以查看還款人前幾期是否逾期，並從中選擇還款情況正常的標的進行認購。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.便利靈活的智能收購</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; 透過普匯的智能收購功能，受讓人可以篩選適合自己的債權，以提高投資效率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>看了這麼多~ 馬上按 &lt;了解更多&gt; 試試看吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {\"textColor\":\"very-light-gray\",\"customBackgroundColor\":\"#2b3d50\"} -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link has-text-color has-very-light-gray-color has-background\" href=\"https://www.pu-hey.com/%E5%82%B5%E6%AC%8A%E8%BD%89%E8%AE%93/\" style=\"background-color:#2b3d50\">了解更多</a></div>\n<!-- /wp:button -->', '2019-01-21 17:23:36', '【普匯觀點】到底什麼是債權轉讓？', 'article', 'publish', '2019-01-21 17:54:26', '', 0),
(3090, 2, '/upload/2019/01/job46-s15-kung-0181.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>什麼是基金?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>基金就是集合一群人的資金，交由專家或是經理人集中管理投資，這些專業基金經理人透過本身的研究，將這筆資金投資在各種不同的股票、債券、貨幣等項目上，因為集合較大的資金規模，可以進行的操作方法更有彈性，統一管理創造更高的投資報酬率，當然也要付出管理成本。 <br>基金可分為很多種類型，例如：股票型、債券型、平衡型、組合型……等。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>投資基金特色</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>優點：</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>1.省去研究時間</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>&nbsp;</strong>&nbsp; 很多人沒有時間研究投資，若直接交由專業人管理便可省去不少時間成本。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>2.接觸到更多投資標的物</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>基金的種類非常多，可以很容易的接觸到各國股票、債券、原物料、房地產等等。一般其他投資方式很難像基金一樣把投資多樣化。<strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>缺點：</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>1.對投資的掌控度低</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.<strong>交易成本比較高</strong><br>比起自己操作，基金還有額外申購費、基金費、管理費等等費用。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>投資基金種類</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>ü</strong><strong> </strong><strong>定期定額</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>- 每隔一段時間，通常是每一個月，投資固定金額於固定的基金上，不必在乎進場時點，也不必在意市場價格起伏，時間到了就固定投資。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>- 最低為3,000元</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>- 建議投資時間為2~3年</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>- 較不受市場波動影響，風險相對較小</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>ü 單一投資</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>- 一次拿出一筆金額，選擇適當時點買進基金，並在市場高點時獲利了結</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>- 最低為10,000元</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>- 投資依市場趨勢進出</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>- 收益及風險相對較高</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>基金獲利來源</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>ü</strong><strong> </strong><strong>價差</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>基金就是一堆投資標的的集合，例如股票、債券、貨幣….等，基金價值會隨著這些標的的漲跌而浮動，如果投資人的買入價格低於賣出價格，就可以從中賺到差價。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>ü</strong><strong> </strong><strong>&nbsp;</strong><strong>利息</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有些基金像股票一樣，如果投資有一定收益，就會以基金總資產分配一定比率的利息到投資人帳戶。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>&nbsp;</strong><strong>ü</strong><strong> </strong><strong>貨幣匯率差</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>買海外基金（以外國貨幣單位計價）才有可能有此獲利。如果此國家的貨幣價值提升，當你賣出基金時，兌換成台幣後就可以賺取此匯差。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>投資基金風險</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>雖然投資基金相對股票風險較小，但還是有不少潛在風險。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>ü 價格風險 ü 通脹風險 ü 信貸風險 ü 利率、匯率風險</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>投資基金報酬vs.普匯10年債權投資</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舉一個富蘭克林的基金為例 <strong>：</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>全球債券基金<strong>(</strong><strong>本基金之配息來源可能為本金</strong><strong>)</strong>-月配息-美元計價</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在期初直接投入12萬，經過10年後</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>\n  <strong>總回收</strong><strong></strong>\n  </td><td>\n  <strong>183600</strong>\n  </td></tr><tr><td>\n  <strong>總成本</strong><strong></strong>\n  </td><td>\n  <strong>120000</strong>\n  </td></tr><tr><td>\n  <strong>純收益</strong><strong></strong>\n  </td><td>\n  <strong>63600</strong>\n  </td></tr><tr><td>\n  <strong>總報酬率</strong><strong></strong>\n  </td><td>\n  <strong>53%</strong>\n  </td></tr><tr><td>\n  <strong>年化報酬率</strong><strong></strong>\n  </td><td>\n  <strong>4%</strong>\n  </td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p><strong>與普匯債權做為比較後：</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>\n  &nbsp;\n  </td><td>\n  基金\n  </td><td>\n  普匯債權投資\n  </td></tr><tr><td>\n  總投入金額\n  </td><td>\n  120,000\n  </td><td>\n  120,000\n  (每個月1,000)\n  </td></tr><tr><td>\n  利率\n  </td><td>\n  X\n  </td><td>\n  以年利率12%計算\n  </td></tr><tr><td>\n  期數\n  </td><td>\n  10年\n  </td><td>\n  10年\n  </td></tr><tr><td>\n  總回收\n  </td><td>\n  183,600\n  </td><td>\n  250,921 \n  </td></tr><tr><td>\n  投資年化報酬率\n  </td><td>\n  4%\n  </td><td>\n  14%\n  </td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p><strong>→</strong><strong> </strong><strong>普匯債權投報率的計算為：</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.總回收= 自第121期(即不再投入後)後的回款。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.總成本=每期投入的1000以年利率10%折現到第一期後的加總</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.淨收益= 總回收-總成本</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5.年化報酬率以有投入的期間(10年)計算</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>→ 投資股票型基金長期大概就是 6~8%左右，債券型則是 3~4%左右</strong>。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>結論</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>不論是單筆投資還是定期定額投資都各有優缺點，若能互相搭配，相輔相成也能達到符合自己的理財目標，但是相較於小額債權投資法，風險及各項交易成本都還是比較高，除了需觀察市場的波動性外，風險也要較高，報酬更較低，真正要購買基金的時候，還是要做好功課並篩選。<strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>依照自己的風險偏好來做資產配置才能使投資更有效率！普匯的小額投資債權提供小資族另一種理財的工具，若有興趣就趕進註冊來看看吧！</p>\n<!-- /wp:paragraph -->', '2019-01-23 17:03:43', '【普匯觀點】投資懶人包—基金vs.普匯債權', 'article', 'publish', '2019-01-23 17:03:47', '', 0),
(3112, 1, '/upload/2019/01/2-3.png', 'https://www.youtube.com/embed/80P1q95l894', '<!-- wp:core-embed/youtube {\"url\":\"https://www.youtube.com/watch?v=80P1q95l894\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://www.youtube.com/watch?v=80P1q95l894\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:paragraph -->\n<p>今天要來跟大家介紹一本書：<strong>《錢意識：借錢、花錢、存錢、賺錢，你最愛哪一件？不做哪一件？變有錢的人怎麼處理錢？跟你想的不一樣》</strong>現在大家對於借款還款的既定印象可能都是「欠錢不好」、「高利貸」等等，但是你們知道有錢的人其實有大概一半的錢都是借來的嗎？這本書裡面作者將改變我們的刻板印象，借錢不全然是不好的，而是看我們將借來的錢用在什麼地方，只要投資觀念對了，就能夠提早開始享受人生！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>人們對於財富的刻板印象</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>小時後爸媽總是告訴我們要養成存錢的好習慣，不可以亂花錢，好好讀書找到好的工作有一份好的薪水，然後把錢都存起來，才可以買很多你想買的東西。想要成為富裕的人，過著自己心目中的生活真的只能靠存錢嗎？現在有多少人的房子、車子都是依靠貸款而來的，人們只想到借錢要繳交「利息」，而沒有思考到通貨膨脹的因素。「通貨膨脹」會讓我們的錢越來越不值錢，也就是說我們從銀行借越多的錢，在未來欠的錢會越來越少，這聽起來似乎是件很划算的事，不過為什麼大家都還是不願意欠錢呢？因為大家都把借錢行為的焦點放在貸款的利息上面，而且在想不出如何增加財富的前提下，只好儘早還完利息讓自己有「我累積的財富變多了」的感覺。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>提前消費，在最美好的歲月讓幸福提前</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>作者在書中提到，在這個世界上，除了按部就班的生活方式，還需要勇氣和遠見的生活方式。「提前消費，在最美好的歲月讓幸福提前。」我們需要把這樣的訊息裝進自己的潛意識裡。但這也不完全是鼓勵大家借錢過度消費，作者要傳達的是「合理的借錢，是富裕的開始」這個概念，透過借貸來的資金投資自己、投資自己的生活，讓自己有更雄厚的資本去追求更加富有的人生，這樣一來等到自己老去的那一天，不但可以把帳都還完，人生也會因此而更豐富。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>不是鼓勵過度消費，而是創造價值</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>作者撰寫這本書的目的卻也不是要大家從此開始盲目借錢來花，而是在自己未來可以承擔的範圍內將借錢消費這件事情當作是一種投資，是為了以後能夠獲得更大的財富，不僅僅只是享受當下的即時性消費行為而已。藉由投資行為替自己的生活創造更多的價值，不受限於「負債」、「還利息」的負面刻板印象中，才是真正的投資。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>書籍簡介</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>作者在書中從四個方面，用親身經歷和身邊案例告訴你：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>借錢、花錢、存錢、賺錢這四件事，有錢人最愛哪一件？不做哪一件？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>不會借錢，你永遠不會成為有錢人</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>敢借，可以激發「賺錢的潛能」，該怎麼借？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>你怕借錢嗎？你覺得有負債讓自己很累嗎？如果答案是肯定的，</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>那麼，有錢生活對你來說注定只能是一種奢想，一個遙遠的夢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>為什麼作者說，要敢借錢，但不要跟「人」借錢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>合理的借錢，是富裕的開始</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有錢人的身家一大半都因借而來的。而且有錢人一直在借，也一直在還。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>買車、買房時，若你有足夠的錢支付，你會選擇一次付清還是抵押貸款？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有錢人怎麼做？你又會怎麼做？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>跟風的人賺不了大錢</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>「做得好」，就是商機，而不是「誰誰誰都在做」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「我看看哪家店賺錢，加盟一下」<br>\n「我先考察一下市場，看看商機在哪？」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當你在說這些話時，就註定了你是一個跟風的人。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>也許，跟風的人不會太失敗，但肯定不會很成功。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>膽小是成為有錢人的首要阻礙</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>現在有A項目和B項目，A項目有一萬個人做，B項目至今還沒有人做。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>請問你做A項目，還是B項目？想做有錢人，就得做有錢人的行為，</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>窮人存錢補貼富人</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>窮得只剩一個金額龐大的存摺</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有錢人用錢買時間，而窮人則是用時間換錢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>存錢能讓存摺的數字不斷增加，但你知道嗎？<br>\n<strong>省下大筆存款的人，最快變窮人。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>存錢是起點，不能是目的</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>人們愛存錢是為了尋找安全感，但錢存的時間拉得越長，貶值的風險就越高。怎麼避免？有錢人會怎麼做？。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>窮人花錢是純花錢，有錢人花錢是為了投資自己。</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>有錢人怎麼處理錢？跟你想的不一樣。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>做有錢人該做的事，才會越來越有錢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>參考資料：關鍵評論-《錢意識》：有錢人的身家一大半都因「借」而來的</p>\n<!-- /wp:paragraph -->', '2019-01-25 16:55:57', '【普匯觀點】存錢不會讓你變有錢，有錢人總是一直在還錢', 'video', 'publish', '2019-01-25 16:55:59', '', 0),
(3128, 2, '/upload/2019/01/未命名-3.jpg', NULL, '<!-- wp:paragraph -->\n<p><strong>原文： https://adespresso.com/blog/fintech-ads-examples/</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>你是否總是盯著螢幕看並想著：這些Fintech公司到底在做什麼？這些Fintech廣告範例又要如何幫助我的事業成長？<br>對於這些困惑我們並不意外，因為全美國只有14%的民眾聽過Fintech公司並清楚了解他們在做什麼。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如果我們告訴你Fintech是數位付款、替代金融或個人金融你可能會一頭霧水，那如果我們告訴你彼特幣就是Fintech的其中一種方式呢？那麼你很快就能進入狀況了。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Fintech是一個使用科技改善金融相關活動的新興產業，Fintech產業主要是由一些開發新科技方式提供網路或手機付款、替代金融和財務管理的小型新創公司組成的。<br>在坐擁廣大的金融背景以及雄厚的資金下，這些公司擁有充足的預算並且可以聘請具有創意的高階人才，並以結果導向籌備相關企劃！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"width\":416,\"height\":217} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"https://lh5.googleusercontent.com/isqCGzq6MffgI7wUP7VxsDhDbSA3fww-9sWCXr42Ka4G9laB6Ux3roixC9gDLk22ITHhrrYS86heUs8xS9dlhcTRHM2hfbGSjr4Bo9jR-V3On3uFDiWSNue6poNPuKXc-QvYT7qLosbv42zp8w\" alt=\"https://webcdn-adespressoinc.netdna-ssl.com/wp-content/uploads/2018/09/best-fintech-ads-1024x536.jpg\" width=\"416\" height=\"217\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>這邊提供幾個我們從市場研究者發布的精選能抓住群眾目光的Fintech廣告例子給大家參考。很多行銷人不會馬上在金融產業尋求創新的廣告發想，「老式流行」、「正式」等概念常常取而代之。但隨著需要吸引更多年輕人以及觸及更多受眾的需求增長，部分的Fintech公司開始推行引領潮流且各個產業都能複製的FB、IG和google廣告模式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>像Ellevest(女性財務健康評斷)和Betterment(幫助退休者和千禧世代找到長期目標)這樣的公司也都正在產出挑戰極限並強調實際成果的廣告文案。<br>不論你的目標是哪種產業或哪些受眾，這些公司所採用的策略都是應用層面很廣，而且可以涵蓋FB、IG和google廣告的。我們所挑選的這些範例都是精華中的精華，並且能立即套用到你的企畫上喔！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Fintech ads on Facebook</strong> 臉書上的Fintech廣告</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在FB上投放廣告一直是能獲得廣大關注且最有效的方法之一。FB擁有超過一億每日活躍用戶，而FB提供的廣告讓你能鎖定特定群眾並最大化你的觸擊率。這邊有幾個Fintech公司的例子，他們精心的設計為他們在FB上找到了相當多的潛在用戶。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":19} -->\n<p style=\"font-size:19px\"><strong>1. Wealthsimple</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Wealthsimple是一家加拿大的線上投資管理服務平台，他們主要的客群是千禧世代，這些年輕人不但是Wealthsimple裡面最大的核心客群，也是FB使用者的最大群體。不少千禧世代發先他們必須同時面對複數的責任，因此Wealthsimple提供他們以下這些選擇來處理他們的問題：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.簡單快速的操作介面</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.自動化資產平衡分配</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.即時性的利息再投資</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>透過這些方式，年輕的顧客不必花太多的時間區擔心該如何管理他們的投資組合。Wealthsimple FB部分的關鍵字廣告和他們的發文內容都是用戶談論他們人生的經驗以及投資方式。下方的FB廣告與出演熱門電視劇 Girls 的演員Alex Karpovsky 合作，便是這種模式的例子。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"width\":397,\"height\":448} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"https://lh5.googleusercontent.com/SFdATUSVyupYuyvBkDqsOBstUdJqDLAe2ESRtWTw8cxDaVLQJCRqYc9Nh3R5mDvAtq3uaZ9yJhI4qKF2fAz_B_iaDh8ndrVVthvGGk7u_yBZpvDkEsGIasPuGPyf2_oeq9vrkSrKsAfDxj4LuA\" alt=\"Wealthsimple Facebook ad\" width=\"397\" height=\"448\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>廣告小秘訣：</strong>在上千個具有影響力臉書名人中要如何找到適合你目標客群的人呢？查看他們的跟隨者並選擇組成與你現有客群最相近的名人吧！更進一步來做，Wealthsimple會讓這些名人談論他們的個人經歷，比起找名人純粹推廣自家商品，這個做法能大大增加可信度喔！<br><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":19} -->\n<p style=\"font-size:19px\"><strong>2. Ellevest</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Ellevest是一個針對女性的金錢管理平台。這家公司的誕生是為了讓女性更為容易接觸投資，因為傳統的投資選擇通常並不會迎合他們的需求。(ex：與男性長久以來的薪資差距造成投資選擇不同) Ellevest的廣告能獲得成功主要是因為公司不怕挑戰極限且讓議題具有些許的爭議性。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舉例來說，他們的川普廣告採行傳統宣傳方式，大量張貼廣告在紐約的電話亭，這些廣告十分熱門而且輕易地被大眾所接受。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"width\":333,\"height\":443} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"https://lh6.googleusercontent.com/lLQ1X-Xd-N_eD45fLbGPcDxL7xscih9t_nCq6iGd8OMOF0rJ5ahTajKkj8DyUXUWcPSmJglxkuhDN00dKLFbsGCzwfYCa4SomxxMVEkuGpjNTkWLCXhJwgDaQPE2Q6qPy9Qau6u87-p4J8sHOQ\" alt=\"Ellevest advertising\" width=\"333\" height=\"443\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>許多婦女都在IG上傳與廣告的自拍來顯示他們的支持。Ellevest的線上宣傳便以鎖定特定詞彙為主，「像個女人一樣投資」，Ellevest就利用這類的廣告詞去鼓舞他們投資人的信心，並讓他們以不同的角度看待投資。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>廣告小秘訣： </strong>在你的FB廣告上講述特定議題，並讓你的目標客群與之接觸，這是你向他們顯示你是所有選擇中最好的機會。解釋你的產品和其他產品有何不同並描述你產品的使用者能得到什麼回饋，重點是對他們的痛處提供解決方案！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"width\":315,\"height\":559} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"https://lh6.googleusercontent.com/ED9Bqa4aG-VwC6jgbFvwNYpQrxTmPGbagy99hcG3bQ2dLctT7-d15_j8AMR-_G8_PQu8qRCvKbQVQmRIvh1U8qjkZKrRfC83pnVNkRELveMBhqE9oxenVGlpQvn6KBMkoD1PD5s5Ef3vRh6ujQ\" alt=\"Ellevest Facebook ad\" width=\"315\" height=\"559\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"customFontSize\":19} -->\n<p style=\"font-size:19px\"><strong>3. Betterment</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Betterment是一間線上投資公司。他們其中一個主要服務在管理顧客為了長遠目標(如養老、創業)所進行的投資。Betterment的FB廣告是面向他們的目標客群的，他們說明就算沒有金融背景的人也能輕鬆投資。在加上這則廣告又以輕鬆幽默的步調取解釋科技該念，讓他們的受眾就算不懂金融也能樂在其中。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"width\":493,\"height\":311} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"https://lh4.googleusercontent.com/wuPaLHyZGu8-TCZjrxRq12Usamzu6BiHSN0_OZIo1XPsRW4d2hGmUtG4I7W_hJx5YZ74vdU2mvlogpHFZwH-c1D6XN3BumWxGnponmd9J8haGZnvassUVjSV3YE6mBTUHXjeixxKWP9YQLeTvw\" alt=\"Betterment video Facebook ad\" width=\"493\" height=\"311\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>廣告小秘訣：</strong>當你越了解你的受眾時，你越有機會找出哪些內容會真正吸引到他們。關鍵是讓你的FB廣告看起來有趣，特別是當你的產品或服務很複雜時，引起你受眾的興趣就能激發他們點擊網頁其他部分來了解更多。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":19} -->\n<p style=\"font-size:19px\"><strong>4. Wealthfront</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Wealthfront是一個自動化的投資服務。不少民眾對於開始一項新投資組合感到畏懼，事實上很多服務跟投資一點相關都沒有。Wealthfront藉由提供每個顧客各自的投資計劃並教導他們使用網站的可用資源進行管理有多麼簡單來抓住顧客的目光。Wealthfront實際的FB廣告是使用與Business Insider(美國知名財經科技網站)的合作來產生社會認同。Business Insider對金融、科技和策略的主題提供閱讀者深入的分析，而Business Insider也花費時間使用各種工具來挑起閱讀者的興趣。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這則FB廣告也提到AI的運作，會對他們目標讀者閱讀者產生共鳴的關鍵字來鼓勵閱讀者點擊進去。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"width\":491,\"height\":356} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"https://lh4.googleusercontent.com/MSEmE6BGbp_wCzH_vwEagmmRo8CMpkPY94ipUcZnlgzTsBClxy-dPW3i9wZScz3UwWdro1ctmxUeL50CmwCGkvzDEmd__MlB_k0ugY3HEPlXr3Kvuo0-yE7IbNXshNFUY8NmGMfb0RU5y-aVcg\" alt=\"Wealthfront Facebook ad\" width=\"491\" height=\"356\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p><strong>廣告小秘訣：</strong>讓你的FB廣告保持簡單，就像上面的範例一樣。不需要太長的描述，只要知道你的受眾想要什麼並專注在該議題上，滿足他們的需求並減少無意義的篇幅。遵照這個方法你會更容易抓到受眾的注意力並讓他們點擊你的廣告。</p>\n<!-- /wp:paragraph -->', '2019-01-28 17:00:57', '【普匯觀點】(翻譯)八個Fintech廣告社群行銷方式(上)', 'article', 'publish', '2019-01-28 17:15:23', '', 0),
(3143, 2, '/upload/2019/01/banner-4.png', NULL, '<!-- wp:paragraph -->\n<p> 過年佳節是資金籌措、流動的高峰期，隨著金融科技的發展，各大銀行開始在數位金融產品上做創新的改變，銀行小額信貸就是其中之一，部分銀行標榜低於1%以下的貸款利率，但大家借款前務必要注意的是，部分銀行小額信貸通常採取「前低後高」的計息方式，許多人忽略了高額手續費、提前清償違約金等費用，總體計算下來總借款金額幾乎沒有任何優惠可言！那麼，到底申請信貸該注意甚麼事? </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>總費用年百分率才是你該看的點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;許多銀行都沒有明確標出開辦費用、手續費計算或是綁約年限，單純以超低利率的字眼吸引民眾，但APR才是你該看的點！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>APR又稱作貸款的實質年利率，把貸款收的手續費、多階段利率等的成本計算進去，然後再回算成每年的貸款利率，所以很多民眾都容易落入低率的陷阱，卻不自知在過程中開辦費或是手續費已成為信貸另一項高支出。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>馬上來試驗看看：</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>\n  &nbsp;\n  </td><td>\n  貸款金額\n  </td><td>\n  貸款利率\n  </td><td>\n  貸款期數\n  </td><td>\n  手續費\n  </td><td>\n  APR\n  </td></tr><tr><td>\n  銀行A\n  </td><td>\n  100,000\n  </td><td>\n  2.58%\n  </td><td>\n  24期\n  </td><td>\n  3,000\n  </td><td>\n  3.08%\n  </td></tr><tr><td>\n  銀行B\n  </td><td>\n  100,000\n  </td><td>\n  1.98%\n  </td><td>\n  24期\n  </td><td>\n  9,000\n  </td><td>\n  7%\n  </td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>比較看看以上兩家銀行，一個標榜利率1.98%，但其實加上高額手續費後APR來到了7%！雖然第一時間和2.58%相比下來看似優惠，但其實整體計算下來根本不划算。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>前低後高? 精準細算，保障權益！</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp;渣打銀行的「理想貸」前兩期借款利率 0.1%，第三期起利率 2.99%(機動計息)，帳管費4,000元，可借額度最高 350 萬，但條件是該方案必須綁約 12 個月，若於撥款日後12個月(含)內於約定攤還金額外提前償還部分或全部本金時，應支付提前償還金額之3.5%為提前償還違約金。換言之，借款人想要在一年內提前清償貸款省利息，勢必得多繳提前清償的違約金。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Ø舉例來說，銀行推出二階段利率貸款方案，</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第一階段：前三個月利率優惠1%</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第二階段：第三個月後，年利率2~15%</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但此方案若以總費用年百分率（APR）表示，總費用年百分率大概會介於5~20%，較接近真正能貸到的利率。比較挑選信貸利率不能只看表面上的借款利率，銀行才是決定利率的決策者，加上開辦費用的年化利率，通常平均借款利率一定高於廣告宣傳的利率價格。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>多比較，普匯貸借平台也是你的好選擇</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>說到借款當然不能趁機告訴大家普匯借貸平台的好處是甚麼?到底和銀行的區別在哪? 直接整理讓你一目了然!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>\n  &nbsp;\n  </td><td>\n  銀行信貸\n  </td><td>\n  普匯借貸\n  </td></tr><tr><td> 適用<br> 對象   </td><td>\n  多半限本行用戶，且有限制年收入\n  </td><td>\n  目前供學生申請，未來持續推出上班族、新鮮人方案\n  </td></tr><tr><td>  利率   </td><td>\n  前低後高，機動計息\n  </td><td>\n  固定利率，依個人信用評分\n  </td></tr><tr><td> 手續費   </td><td>\n  最低大約$2,000 (可能還有開辦費用)\n  </td><td>\n  本金的3%\n  </td></tr><tr><td>  申請  流程   </td><td>\n  最快1天撥款\n  </td><td>\n  最快15分鐘放款\n  </td></tr><tr><td>  逾期<br>  處理   </td><td>\n  背上信用不良紀錄，上個人徵信\n  </td><td>\n  有產品轉換做選擇\n  </td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>沒錯! 普匯不僅提供投資借貸雙贏的媒合平台，服務上也具備專業、貼心，別再說只能和銀行借錢啦~ 來普匯，給你更多的選擇！</p>\n<!-- /wp:paragraph -->', '2019-01-30 17:39:27', '銀行小額信貸背後隱藏的殺機?', 'article', 'publish', '2019-01-31 12:21:57', '', 0),
(3168, 2, '/upload/2019/02/2.png', NULL, '<!-- wp:core-embed/youtube {\"url\":\"https://www.youtube.com/watch?v=80P1q95l894\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://www.youtube.com/watch?v=80P1q95l894\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:paragraph -->\n<p>今天要來跟大家介紹一本書：<strong>《錢意識：借錢、花錢、存錢、賺錢，你最愛哪一件？不做哪一件？變有錢的人怎麼處理錢？跟你想的不一樣》</strong>現在大家對於借款還款的既定印象可能都是「欠錢不好」、「高利貸」等等，但是你們知道有錢的人其實有大概一半的錢都是借來的嗎？這本書裡面作者將改變我們的刻板印象，借錢不全然是不好的，而是看我們將借來的錢用在什麼地方，只要投資觀念對了，就能夠提早開始享受人生！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>人們對於財富的刻板印象</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> 小時後爸媽總是告訴我們要養成存錢的好習慣，不可以亂花錢，好好讀書找到好的工作有一份好的薪水，然後把錢都存起來，才可以買很多你想買的東西。想要成為富裕的人，過著自己心目中的生活真的只能靠存錢嗎？現在有多少人的房子、車子都是依靠貸款而來的，人們只想到借錢要繳交「利息」，而沒有思考到通貨膨脹的因素。「通貨膨脹」會讓我們的錢越來越不值錢，也就是說我們從銀行借越多的錢，在未來欠的錢會越來越少，這聽起來似乎是件很划算的事，不過為什麼大家都還是不願意欠錢呢？因為大家都把借錢行為的焦點放在貸款的利息上面，而且在想不出如何增加財富的前提下，只好儘早還完利息讓自己有「我累積的財富變多了」的感覺。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>提前消費，在最美好的歲月讓幸福提前</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>作者在書中提到，在這個世界上，除了按部就班的生活方式，還需要勇氣和遠見的生活方式。「提前消費，在最美好的歲月讓幸福提前。」我們需要把這樣的訊息裝進自己的潛意識裡。但這也不完全是鼓勵大家借錢過度消費，作者要傳達的是「合理的借錢，是富裕的開始」這個概念，透過借貸來的資金投資自己、投資自己的生活，讓自己有更雄厚的資本去追求更加富有的人生，這樣一來等到自己老去的那一天，不但可以把帳都還完，人生也會因此而更豐富。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>不是鼓勵過度消費，而是創造價值</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>作者撰寫這本書的目的卻也不是要大家從此開始盲目借錢來花，而是在自己未來可以承擔的範圍內將借錢消費這件事情當作是一種投資，是為了以後能夠獲得更大的財富，不僅僅只是享受當下的即時性消費行為而已。藉由投資行為替自己的生活創造更多的價值，不受限於「負債」、「還利息」的負面刻板印象中，才是真正的投資。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>書籍簡介</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>作者在書中從四個方面，用親身經歷和身邊案例告訴你：借錢、花錢、存錢、賺錢這四件事，有錢人最愛哪一件？不做哪一件？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>不會借錢，你永遠不會成為有錢人</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>敢借，可以激發「賺錢的潛能」，該怎麼借？你怕借錢嗎？你覺得有負債讓自己很累嗎？如果答案是肯定的，那麼，有錢生活對你來說注定只能是一種奢想，一個遙遠的夢。為什麼作者說，要敢借錢，但不要跟「人」借錢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>合理的借錢，是富裕的開始</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有錢人的身家一大半都因借而來的。而且有錢人一直在借，也一直在還。買車、買房時，若你有足夠的錢支付，你會選擇一次付清還是抵押貸款？有錢人怎麼做？你又會怎麼做？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>跟風的人賺不了大錢</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>「做得好」，就是商機，而不是「誰誰誰都在做」「我看看哪家店賺錢，加盟一下」「我先考察一下市場，看看商機在哪？」當你在說這些話時，就註定了你是一個跟風的人。也許，跟風的人不會太失敗，但肯定不會很成功。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>膽小是成為有錢人的首要阻礙</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>現在有A項目和B項目，A項目有一萬個人做，B項目至今還沒有人做。請問你做A項目，還是B項目？想做有錢人，就得做有錢人的行為，</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>窮人存錢補貼富人</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>窮得只剩一個金額龐大的存摺，有錢人用錢買時間，而窮人則是用時間換錢。存錢能讓存摺的數字不斷增加，但你知道嗎？<br><strong>省下大筆存款的人，最快變窮人。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>存錢是起點，不能是目的</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>人們愛存錢是為了尋找安全感，但錢存的時間拉得越長，貶值的風險就越高。怎麼避免？有錢人會怎麼做？。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>窮人花錢是純花錢，有錢人花錢是為了投資自己。</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>有錢人怎麼處理錢？跟你想的不一樣。 做有錢人該做的事，才會越來越有錢。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>參考資料：關鍵評論-《錢意識》：有錢人的身家一大半都因「借」而來的</p>\n<!-- /wp:paragraph -->', '2019-02-01 17:30:41', '【普匯觀點】存錢不會讓你變有錢，有錢人總是一直在還錢', 'article', 'publish', '2019-02-01 13:51:25', '', 0),
(3179, 2, '/upload/2019/02/1.png', NULL, '<!-- wp:core-embed/youtube {\"url\":\"https://youtu.be/hH-CMblrH7I\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://youtu.be/hH-CMblrH7I\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:paragraph -->\n<p>今天是除夕夜，在這裡普匯向大家說聲：新年快樂！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>新年想到紅包，紅包想到金錢，金錢就想到……沒錯，賺錢！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但是富可敵國的李嘉誠說過:有錢卻沒有智慧的人，越努力賺錢，越努力投資反而會越窮。但相對的，若你只有小錢但有智慧，那麼小錢一滾再滾也能滾出大雪球！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>年節將至， <strong>小編在商周上看到一個有趣的問題想問問大家</strong><strong>:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>當今天你拿到了辛苦一年的年終獎金，你會怎麼做</strong><strong>?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>將獎金規劃式的運用，一部分投資，一部分花費</li><li>全部拿去投資</li><li>全部犒賞自己花掉</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>你想到答案了嗎?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>其實，<strong>不管選哪個答案都不會讓你在未來變成很有錢的人。</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>為什麼呢?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據商周上作者的評論，不管你選擇甚麼答案都不會讓你變得更有錢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>原因很簡單，<strong>選</strong><strong>A</strong><strong>的人</strong>，所用的資金切割使用策略，根本對你的人生資產和理財軌跡沒有什麼影響。你只不過把年終獎金分散花完而己。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>即使一部分是拿去存款或投資，但金額實在太小，而且一年才一次，除非你的年終獎有百千萬以上，否則根本只算雞皮蒜毛，充其量只是讓你花得安心，以為自己有智慧。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>選</strong><strong>B</strong><strong>的人</strong>，就是李嘉誠說的，有錢卻沒有智慧的人。<br>\n最主要的原因在於，若根本不懂理財專業知識，那麼無疑只是把錢漫無目的石沉大海。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>選</strong><strong>C</strong><strong>的人</strong>，應該不用多說了! </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以上是商周作者的評論，更多細節內容大家想了解的話可以點選此連結</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&gt;&gt; https://www.businessweekly.com.tw/article.aspx?id=24925&amp;type=Blog</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>對於以上的評論，小邊也有一些看法。現在的社會物價高，薪水低，大家不斷追求更高的收益報酬，有些人選擇放入定存，有些人選擇基金，更多的是投入股市，沒有一個是錯誤的投資法，端看個人資產多寡以及風險屬性來決定，但現今網路發達，各大投資平台及網站都充斥著投資理財方法，但不幸的是，有許多人沒有李嘉誠所說的智慧，大部分的人都盲從於網路上各大雜燴資訊，卻理不出一個最適合自己的投資理財法，也沒有為未來10年做實際的規劃。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>前幾日小編發佈了多部投資人的採訪影片，相較於其他投資產品，普匯債權投資可以說是簡單上手又單純！想為財富做長遠的計劃，那麼勢必要把眼光放得更長遠，就算只有小錢，只要合理地進行分散並利用複利的特性，年復一年也能滾出可觀的收益！拿了紅包就趕快行動吧！普匯在這裡祝大家豬年行大運，財富滾滾來喔~</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(以下不考慮平台手續費)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>\n  年數\n  </td><td>\n  總投資報酬率\n  </td><td>\n  年化報酬率\n  </td></tr><tr><td>\n  1\n  </td><td>\n  19.21%\n  </td><td>\n  19.21%\n  </td></tr><tr><td>\n  3\n  </td><td>\n  44.14%\n  </td><td>\n  12.96%\n  </td></tr><tr><td>\n  5\n  </td><td>\n  75.64%\n  </td><td>\n  11.92%\n  </td></tr><tr><td>\n  7\n  </td><td>\n  111.81%\n  </td><td>\n  11.31%\n  </td></tr><tr><td>\n  10\n  </td><td>\n  188.67%\n  </td><td>\n  11.18%\n  </td></tr></tbody></table>\n<!-- /wp:table -->', '2019-02-04 18:49:00', '【普匯觀點】年終獎金你怎麼用? 普匯跟大家拜個年！', 'article', 'publish', '2019-02-04 18:49:02', '', 0),
(3186, 2, '/upload/2019/02/banner.jpg', NULL, '<!-- wp:paragraph {\"customFontSize\":32} -->\n<p style=\"font-size:32px\"><strong>一、什麼是期貨?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>期貨合約（英語：Futures contract），簡稱期貨（英語：Futures），是一種跨越時間的交易方式。買賣雙方透過簽訂合約，同意按指定的時間、價格與其他交易條件，交收指定數量的現貨。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":32} -->\n<p style=\"font-size:32px\"><strong>二、期貨種類</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>期貨又分成『商品期貨』和『金融期貨』，期貨演變至今天，可以交易的可不是只有買商品而已！像是股票市場上的許多金融指數也都可以變成期貨做交易。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>商品期貨包含：農產品、金屬類、能源類。金融商品則有：股票、公債、指數(台指期貨、金融期貨、電子期貨)。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":32} -->\n<p style=\"font-size:32px\"><strong>三、期貨特色</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.財務槓桿效應大:由於大多數期貨商品保證金不到15%，100萬的期貨，15萬就能買到。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.交易的限制少、投資較靈活:可以在同一天同時進行買空，賣空。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.有避險功能: 期貨通常要避免的風險是「價格風險」，會先買進或賣出期貨，確保未來價格。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.交易成本低:期貨的交易成本比股票低很多！我們用股票和期貨交易稅做比較股票：交易稅為賣出時的\n0.3%，期貨交易稅：為買進、賣出時按契約總值的 0.002% 。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":32} -->\n<p style=\"font-size:32px\"><strong>四、如何運作?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>今天我買了一口紅豆期貨 →&nbsp;建倉</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>我持續抱著這口期貨一年 →&nbsp;持倉</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>到期當天，拿契約換紅豆 →&nbsp;交割</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>到期日前將期貨又賣別人 →&nbsp;平倉</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":32} -->\n<p style=\"font-size:32px\"><strong>五、期貨風險</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>期貨的風險取決於你放多少保證金</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舉一個簡單的案例：<br>\n1口大台(台指期)，放10萬保證金與50萬保證金差多少?<br>\n事實上同樣的操作，風險高低取決於自己放入多少保證金。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":32} -->\n<p style=\"font-size:32px\"><strong>六、結論</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如果你的資金只到<strong>最低保證金</strong>的門檻，那小編建議你千萬不要碰期貨。反之如果的資金足夠，而且想尋求更低手續費的買賣方法，期貨就是一個很好用的工具。<strong>期貨適合頻繁操作、多空操作的人</strong>，期貨最大的好處就是手續費等交易成本極低、槓桿也能自行控制，因此頻繁操作的情況下會省掉很多交易成本。而期貨做空也很容易，沒有任何複雜的操作。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>&nbsp;</h3>\n<!-- /wp:heading -->', '2019-02-11 17:00:29', '【普匯觀點】投資懶人包— 期貨篇', 'article', 'publish', '2019-02-18 18:01:37', '', 0),
(3233, 2, '/upload/2019/02/未命名-2.jpg', NULL, '<!-- wp:paragraph -->\n<p>2018歷經多次重大事件的變革，中美兩大經濟體各個劍拔弩張，貿易戰火加劇令投資人無不戰戰兢兢、英國脫歐、聯準會升息、金融科技的不斷升級改變我們的生活模式…等， 10年前全球金融海嘯席捲全世界，而如今又過了一個新的十年，有許多經濟學家預測2019是經濟成長持緩的一年，國際政經格局遭逢巨變，誰都不敢斷定金融海嘯是否會再次吞噬整個市場，斗轉星移，一年結束了，借鑑過去的同時，2019的”新經濟”局勢正猛烈地向”舊經濟”發攻！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>互聯網發展的幾十年以來，市場技術也趨近成熟，人工智能、大數據、共享經濟、金融科技等互聯網思維下的產物不管是對市場抑或是我們的生活都產生極大影響，因應如此驅動，傳統的經濟模式也正式邁向新的里程碑。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以下提出幾點2019經濟新趨勢！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>趨勢1:</strong> <strong>川習纏鬥，全球供應鏈、國際秩序大亂</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>糾纏亂鬥的中美貿易戰為期了不短的時間，2019可能是全新的競爭時代，從國家、企業到個人都必須做好全新的準備。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以貿易戰為起點，這場新競爭的範圍，橫跨了政經制度、產業政策、企業、科技、軍事、國際事務等方面。雖說是中美兩國關稅之間的戰火，卻能對全世界的經濟、新興國家市場造成莫大損失，甚至是台灣也有可能隨時受到衝擊。                          根據資料調查顯示，最快在2030年前中國經濟規模將超越美國，隨著數位化、網路化，開發中國家一一崛起，西方舊權勢力也飽受威脅，國際經貿秩序也有可能被重塑，所有事情都將無可預料，未來我們將處在一個動盪的交接時期。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>趨勢2:</strong> <strong>人工智能的再次爆發</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>隨著AI走向商業化後，民眾的消費模式產生巨大的改變，我們可以看到人工智慧滲透了我們的生活，從自動化的體現一直到智慧化，AI提供我們更精細的服務，突破原有的模式發展出更具個性化的整合服務，本質上協助我們、提升工作效率，讓我們的生活變得更便利，全球科技龍頭也相勁從醫療、生物語音辨識邁向更智慧化，為各大產業注入新的活水，但相對AI也可能產生失業、貧富差距等問題，這些狀況在未來會更加顯著，政府、國家都必須負更大的責任，但是我們也不應排斥它，人類和AI的關係是共生，人工智慧取代重複性的工作，卻也能讓解放的勞動力創造更多具有新價值的工作。2019年，人工智慧生態將日漸成熟，服務面越來越“精”細化。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>趨勢3: 物聯網時代，資安危機意識崛起</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>物聯網IOT的時代，讓居家產品在網路及人工智慧的加持下發展出與人互動的智慧，但是威脅卻也悄悄前進你的周遭:手機。家電、行動支付、指紋、人臉、語音辨識、線上購物…等都是駭客的新戰場。不同於以往的傳統網路節點，現今的資安防護網不像電腦時代一樣單純，滲透資料庫竊取隱私，擷取企業的高機密文件，阻擋交易…種種狀況都是駭客能從中獲利的商業行為。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\">不管2019會發展成甚麼樣子，你我都必須做好航向新航道的準備，各大行業的生態圈模式仍然依照消費者的需求在變化，就讓我們看看未來會朝哪裏發展吧！</p>\n<!-- /wp:paragraph -->', '2019-02-13 17:00:43', '【時事分享】2019經濟未來趨勢? 這些大事你不能不知道！', 'article', 'publish', '2019-02-13 15:51:33', '', 0),
(3247, 2, '/upload/2019/02/未命名-2-2.jpg', NULL, '<!-- wp:paragraph {\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\">文案撰寫人：gallon</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> 「別將雞蛋全部放進同一個籃子裡。」這句話每個人都聽過也都知道其含義，<br> 但是一般民眾在進行投資時都會忽略了這個動作，或者是誤解了他的意思，這<br> 篇文章將簡單地介紹投資上的避險，讓投資人對避險有更準確的認知。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>避險意義</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>避險是規避風險的簡稱，屬於應對風險的一種手段。投資人會透過各種方式來<br> 降低或是消除潛在的風險因子來確保風險發生時資產收到的損失能最小化。在<br> 國際貿易中，避險是專指進出口商為了規避匯率風險進行遠期外匯買賣的對沖<br> ，也稱為對沖或是套利保值，針對不同的避險方式也有不同的計算方式。<br> </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>投資組合理論</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>一般投資人要進行避險時，最好的作法是將資產分配到不同的投資產品上，例<br> 如50%的資金購買債權，50%的資金投資股票。但是有人會問說那麼50%的資金<br> 投入在A個股，剩下投資在B個股算是避險嗎？答案是算，但是功用不大。A、B<br> 個股雖然是不同的公司，但他們仍處於同一個系統下，換句話說投資人若採用<br> 這樣的避險方式時，能規避非系統性風險(公司個別風險)但不能規避系統性風險<br> (整體風險)，當股市崩跌時這種避險方式無法有效降低損失。<br> 那麼投資人要如何對自己的投資商品進行避險呢？在投資學中，我們可以透過<br> 公式計算兩種投資產品的相關係數，當相關係數越低時，代表兩種投資商品互<br> 相影響的效果越小，反之當相關係數越高時，兩者相互影響的效果就越大，舉<br> 例來說，台股和美股呈現高度正相關(相關係數大)，當美股崩跌時台股有很高的<br> 機率也會崩盤，因為台灣和美國時差有八小時，因此不少投資人晚上時都會觀<br> 察美股的動向，藉此來判斷台股明天開盤的走向。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>效率前緣理論</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>那麼在投資時要如何進行適當的資產配置呢？這邊建議新手投資人可以選擇一<br> 個無風險投資商品和中高風險商品並依自己期望的報酬率進行比例分配，例如<br> 說美國公債80%，股票20%。但有人會問說既然都選擇無風險投資商品了，為什<br> 麼我不將所有的資金全部投入穩穩賺票面和配息報酬就好呢？這是個好問題，<br> 在投資學中我們有個經典理論叫效率前緣理論，以下讓我們來簡單介紹一下這<br> 個理論在做些什麼。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://lh6.googleusercontent.com/mG48dlwWlKqWWvncIwGu-IZ7G6SmdI0a15Ds9aEkwrata_60YNN5e89cnOy0qUmSLsFOlm-8az46BhH5F1ozzAx7LqxHEo9moSJERYB3ADCrOXq2qvdLNTtA2Cvff9nZI7E8b8Bx-KOIiqFsiw\" alt=\"\"/><figcaption><br><br></figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p> 這是將資產分配在股票和債卷的效率前緣曲線，橫軸預期年報酬標準差可以視作風險程度，當標準差越大時代表風險越大，縱軸預期年報酬如字面意思，是投資人計算出的預期收益。詳細的標準差算法和效率前緣曲線繪製法有興趣的投資人可以自行上網搜尋喔！<br>那麼我們來看看若將資金全部分配在無風險資產會產生什麼結果，可以看到標準差約10%，預期報酬約9%，但是若加入股票後再25%錢整個投資組合的風險會降低，報酬會提高，在分配約40%在股票時，與全部投資債券的風險程度相同，但報酬卻上升不少，當股票分配比例越來越高時，風險和預期報酬都會隨之增長。<br>那麼在繪製出效率前緣曲線後投資人該如何決定資產分配比例呢？我們依上面得圖進行講解，紅色圓圈處是曲線的最突出點，在其之下的投資分配都有更佳的資產配置(同樣風險但報酬率更高)，因此這些投資組合我們都不考慮。至於紅色圓圈處以上的投資金額分配則端看投資人的風險偏好程度而定，並沒有特別的優劣之分，投資人自己拿捏即可。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>結論</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>避險是每個投資人都要做的功課，很多投資人認為自己都有做到避險的行為，但卻不一定能有效減免損失。也有很多投資人對於該投資哪種商品感到困惑，普匯債權提供投資人安全、快速且收益合理的投資選擇，各位不妨檢視自己現在的資產配置，並進行適當管理，當風險發生時才能將損失降到最低喔！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><br></p>\n<!-- /wp:paragraph -->', '2019-02-15 17:16:01', '【普匯觀點】淺談避險', 'article', 'publish', '2019-02-15 17:16:03', '', 0),
(3258, 2, '/upload/2019/02/未命名-1.png', 'https://www.youtube.com/embed/Uq_Gxcxlau0', '<!-- wp:core-embed/youtube {\"url\":\"https://youtu.be/Uq_Gxcxlau0\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://youtu.be/Uq_Gxcxlau0\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:paragraph -->\n<p>一年一度的元宵節又到來了！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>大家都知道在除夕這一天家家戶戶貼紅春聯、放鞭炮，是為了要趕走可怕的年獸；那麼大家知道為什麼元宵節要提燈籠、猜燈謎嗎?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>元宵起源</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>正月是農曆的元月，古人稱夜為「宵」，所以稱正月十五為元宵節。正月十五日既是一年中第一個月圓之夜，也是一元復始，大地回春的夜晚，人們對此加以慶祝，而實際上也是慶賀新春的延續。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>相傳唐朝唐太宗時為提倡讀書風氣，那時到處私塾林立，每年元宵節（正月十五日）是私塾開學日，所有的學生都必須準備一個精巧的花燈拿到學校來，稱為「開學燈」並請老師在燈上提些鼓勵字詞後即點燃燈裡燭光，象徵前途光明的意思。 這樣的習俗隨著時代演變成今日的「提燈籠遊行」、「掛花燈」、「燈謎會」等等活動。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>傳統習俗</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":18} -->\n<p style=\"font-size:18px\"><strong>吃元宵</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":17} -->\n<p style=\"font-size:17px\">正月十五吃元宵，是在中國由來己久的習俗，元宵即「湯圓」，它的做法成份風味各異，但是吃元宵代表的意義卻相同。代表著團團圓圓，日子越過越紅火。俗語有句話叫和氣生財。家庭的和睦以及家人的團圓對於一個完整的家庭來講是多麼重要的因素。因此，在元宵節一定要和家人吃上「元宵」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":18} -->\n<p style=\"font-size:18px\"> <strong>送花燈</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「送花燈」簡稱為「送燈」，其實質意義就是送孩兒燈。即在元宵節前，娘家送花燈給新嫁女兒家，或一般親友送給新婚不育之家，以求添丁吉兆，因為「燈」與「丁」諧音。表示希望女兒婚後吉星高照、早生麟子；如女兒懷孕，則除大宮燈外，還要送一兩對小燈籠，祝願女兒孕期平安。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":17} -->\n<p style=\"font-size:17px\"><strong>舞獅子</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舞獅，又稱「獅子舞」、「獅燈」、「舞獅子」，多在年節和喜慶活動中表演。獅子在中國人心目中為瑞獸，象徵著吉祥如意，從而在舞獅活動中寄託著民眾消災除害、求吉納福的美好意願。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":17} -->\n<p style=\"font-size:17px\">  <strong>猜燈謎</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>每逢元宵節，各個地方都打出燈謎，希望今年能喜氣洋洋的，平平安安的。因為謎語能啟迪智慧又饒有興趣，所以流傳過程中深受社會各階層的歡迎。唐宋時燈市上開始出現各式雜耍技藝。明清兩代的燈市上除有燈謎與百戲歌舞之外，又增設了戲曲表演的內容。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customFontSize\":17} -->\n<p style=\"font-size:17px\"><strong>燃蜂炮</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>台南鹽水的「南蜂炮」是每年元宵節一道亮麗的風景。「蜂炮」有數萬支沖天炮製作而成，而每一個的沖天炮的炮芯都連接在一起，只要點燃其中一個沖天炮，就會帶動邊上其他沖天炮轟炸，霎時間，多個沖天炮連續射出，十分響亮。據說，每年燃放「蜂炮」有乞求風調雨順、平平安安的象徵。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>參考網址：<a href=\"https://kknews.cc/zh-tw/culture/b2z865n.html\">https://kknews.cc/zh-tw/culture/b2z865n.html</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"customFontSize\":17} -->\n<p style=\"font-size:17px\" class=\"has-text-color has-vivid-cyan-blue-color\"><strong>元宵商機，大陸網路理財招數層出不窮！</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>大陸電子商務業者的網路理財新招層出不窮，不甘被騰訊春節期間搶紅包蓋過風頭，阿里巴巴支付寶14日推1年期「元宵理財」。自去年底開始發酵的網路理財服務，在過年期間的搶紅包大戰中達到高峰，利用網路和手機小額轉帳、買基金，已經成為大陸人民的日常生活。最新的網路理財產品是支付寶的元宵理財，元宵理財號稱可以保本保底，資金1年後才能贖回，年收益率達到7%，幫用戶加薪。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"customFontSize\":17} -->\n<p style=\"font-size:17px\" class=\"has-text-color has-vivid-cyan-blue-color\"><strong>最後猜猜幾個燈謎吧！</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.錢的媽媽是誰? </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金&nbsp;&nbsp; —因為(錢)前世(金)今生</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.哪個刀最長?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>屠龍刀 —因為屠龍(too\nlong)！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3. 哪個動物最容易懷孕? </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這題的答案小邊就不透漏了! 大家有興趣的話可以上網查查或是在粉專底下留言讓小邊告訴你XD</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><br> </p>\n<!-- /wp:paragraph -->', '2019-02-19 11:12:27', '【時事分享】元宵特輯— 你是猜燈謎高手嗎?', 'video', 'publish', '2019-02-19 11:12:29', '', 0),
(3268, 2, '/upload/2019/02/未命名-2.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>真實案例</strong><strong>:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>蔡先生買了1000萬的投資型保單，其中400萬是房貸，600萬是靠保單貸款出來的，利息6%。投資標的是<strong>某間公司的收益成長基金-南非幣</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>結果美股大跌，他的基金4個月虧損了300萬。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>首先什麼是投資型保單</strong><strong>?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>投資型保單本質其實還是一種保險，不過增加了投資的功能。投資標的有很多種，投資的風險還是客戶自行承擔，屬於風險較高的保險。優點是保費跟保額繳交較傳統型保險彈性，資金運用可以辦理保險單借款或是部分贖回。每人每年有24,000元的保險列舉扣除額。一般而言保單貸款利率4~7%。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>保單貸款的條件</strong><strong>:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.需要具有保價金的保單才可以貸款，像是終生險、癌症險</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.貸款利率:通常申請保單借款，保險公司不會收取額外的手續費用，也沒有限定還款期限，但要注意的是，保單貸款利率是按月計息，而保險公司每半年寄出一次繳息單據，如果保戶未繳利息，未繳的利息就會計入本金中，造成借款利息增加，對借款人來說就會相當不划算。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.可以貸金額最高九成</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.理賠效益: 申請保單借款後有未還款的金額，若發生保險事故，保險金會先扣除貸款本息，剩下的金額再給付給受益人。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>出了許多問題導致金管會已對投資型保單改制:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>改制項目如下</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.賣給70歲以上的要錄影錄音。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.公平待客原則</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.保險業實施股票逆景氣循環措施</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>結論</strong><strong>:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>房屋抵押貸款: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>假設是用二胎房貸下去貸款的</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>年利率15%，貸款手續費1%來說</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>一年所要付的利息是房貸400萬的15%，是60萬加上手續費4萬</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>保單貸款的利率是6% ，一年的利息是600X6%=36萬</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>也就是說你在一年可是要付出高達100萬的利息錢，除此之外保險給付的金額可是會大打折扣!這些都是保險公司沒有詳細說明的潛在危機! </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>所以在投資決策上一定要做好功課，了解其投資工具的特性，才不會損失鉅額！</p>\n<!-- /wp:paragraph -->', '2019-02-20 17:00:12', '【普匯觀點】投資型保單潛在的風險', 'article', 'publish', '2019-02-20 16:00:11', '', 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(3288, 1, '/upload/2019/02/工作區域-3.png', 'https://www.youtube.com/embed/YO-BODCKYSw', '<!-- wp:core-embed/youtube {\"url\":\"https://www.youtube.com/watch?v=MxjZebzAOcc\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<div class=\"wp-block-embed__wrapper\">股票的高風險、基金的複雜資訊對許多投資觀較保守的人來說都不是個好選擇，市場商品琳瑯滿目所以通常銀行定期存款、定期儲蓄存款往往是許多人的首選，但是以目前銀行提供的利率來看：定存三年以上為1.065%，定儲為約莫1.09%，不管是選擇哪個進行儲蓄理財其實生出來的利息真的都很低！尤其當我們考慮通膨後，你拿回的錢往往都被銀行吃掉了！網路上公布的通膨率其實都是盡量以最低的數字呈現出來，央行印製鈔票的速度才是真正的通膨率，那麼到底有甚麼投資方法是低風險又能快速滾錢的?今日小編就來好好告訴大家！</div>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>首先，做比較前我們先簡單了解銀行定儲有哪些種類，與定存有何差別以及利息又是如何計算。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>我們先來探討定期存款和定期儲蓄存款的差別</p>\n<!-- /wp:paragraph --><!-- wp:list {\"ordered\":true} -->\n\n<ol>\n	<li>定期存款：計算利率方式是月單利，每個月產生的利息不滾入下個月的本金，每個月的利息為本金呈上年利率在除以12。</li>\n	<li>定期儲蓄存款：計算利率方式為月複利，通常以月為一期，每個月的利息會在滾入下個月的本金一起生息，本金和=本金*(1+月利率)^期數，月利率=年利率/12。</li>\n</ol>\n<!-- /wp:list --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n\n<p><strong>定期儲蓄存款可分為：整存整付、存本取息、零存整付。</strong></p>\n<!-- /wp:paragraph --><!-- wp:list {\"ordered\":true} -->\n\n<ol>\n	<li>整存整付：將本金一次存入，每月產生的利息會滾入本金成為本金的一部分，利息採月複利計算，以固定利率或機動利率計息，通常本金最少為$10,000，這是一般民眾最熟知的存款方式。</li>\n	<li>存本取息：將本金一次存入，其後每月領取利息，於到期時取回期初所存入之本金，存款取單利方式計息，通常退休族群較偏好此種每月領息方式。</li>\n	<li>零存整付：每個月接固定存入一定金額之本金至銀行，直到約定期間為止，第一個月存入之本金會以月複利的方式計息，並且將當月本金加入利息滾入下一個月而成為第二個月的本金之一，再加上第二個月薪存入之固定本金，以此類推。此存款方式類似基金以及小額債權定期定額的投資方式，對於小資族來說是較好的選擇。</li>\n</ol>\n<!-- /wp:list --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n\n<p><strong>普匯債權投資比較</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>現在市場上有另一種因金融科技興起而誕生的金融投資產品，是位於P2P借貸平台上的債權投資，是一種追求高報酬但風險相對較低的投資。普匯金融科技提供了不同的投資產品給予廣大投資大眾，讓我們來試算一下投資報酬率！</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>以下為零存整付與普匯債權投資之比較：</p>\n<!-- /wp:paragraph --><!-- wp:table -->\n\n<table class=\"wp-block-table\">\n	<tbody>\n		<tr>\n			<td>&nbsp;</td>\n			<td>銀行定存(零存整付)</td>\n			<td>普匯債權投資</td>\n		</tr>\n		<tr>\n			<td>每月投入金額</td>\n			<td>20,000</td>\n			<td>20,000</td>\n		</tr>\n		<tr>\n			<td>利率</td>\n			<td>以1%計算</td>\n			<td>以年利率10%計算 違約率0%(每期都能回收)。</td>\n		</tr>\n		<tr>\n			<td>期數</td>\n			<td>10年</td>\n			<td>10年</td>\n		</tr>\n		<tr>\n			<td>總利息收入</td>\n			<td>125,100</td>\n			<td>2,879,158</td>\n		</tr>\n		<tr>\n			<td>10年投資報酬率</td>\n			<td>5%</td>\n			<td>總投報率189% (年均報酬率達11.17%)</td>\n		</tr>\n		<tr>\n			<td>優點</td>\n			<td>幾乎零風險，隨時可動用資金</td>\n			<td>報酬率高，比起其他投資工具相較投入金額小，風險降低許多。</td>\n		</tr>\n		<tr>\n			<td>缺點</td>\n			<td>低報酬 通貨膨脹風險</td>\n			<td>借款人違約風險 平台信用風險</td>\n		</tr>\n	</tbody>\n</table>\n<!-- /wp:table --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n\n<p><strong>收益計算方式</strong>：</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>1.總回收=自不再投入資金後的回款。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>2.總成本=每期投入的20000以年利率10%折現到第一期後的加總</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>3.淨收益=總回收-總成本</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>4.投報率=淨收益/總成本</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>5.年化報酬率以有投入的期間(10年)計算</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>年化報酬率(%) = (總報酬率+1)^(1/年數) -1</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>(以下不考慮平台手續費)</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>跟銀行存款比起來，普匯債權投資的收益明顯高出至少好幾倍！投資報酬率真的讓你看得不敢置信！但你一定會想若借款人逾期跑掉怎麼辦? 目前P2P借貸平台上的債權違約率不高，就算真的倒帳了也還有平台公司與法律作為保障，加上平台提供「債權轉讓」的機制，就算借款人逾期也能透過轉讓取回現金，屬於低風險投資！這樣子的小額分散定期定額投資對於投資人來說是很好的理財工具，趕緊來普匯金融科技找投資標的就對啦！</p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":3293,\"width\":451,\"height\":337} -->\n\n<p><img alt=\"\" class=\"wp-image-3293\" src=\"/upload/2019/02/未命名-1_0-1024x765.jpg\" style=\"height:337px; width:451px\" /></p>\n<!-- /wp:image --><!-- wp:image {\"id\":3294,\"width\":449,\"height\":335} -->\n\n<p><img alt=\"\" class=\"wp-image-3294\" src=\"/upload/2019/02/工作區域-2-1024x765.png\" style=\"height:335px; width:449px\" /></p>\n\n<p>&nbsp;</p>\n<!-- /wp:image --><!-- wp:image {\"id\":3295,\"width\":450,\"height\":336} -->\n\n<p><img alt=\"\" class=\"wp-image-3295\" src=\"/upload/2019/02/工作區域-3-1024x765.png\" style=\"height:336px; width:450px\" /></p>\n<!-- /wp:image --><!-- wp:image {\"id\":3292,\"width\":451,\"height\":337} -->\n\n<p><img alt=\"\" class=\"wp-image-3292\" src=\"/upload/2019/02/4-1024x765.jpg\" style=\"height:337px; width:451px\" /></p>\n<!-- /wp:image -->', '2019-02-26 16:53:30', '【普匯觀點】理財大小事—銀行定存 vs. 普匯債權', 'video', 'publish', '2020-06-08 07:54:10', '', 0),
(3309, 2, '/upload/2019/03/未命名-1.jpg', NULL, '<!-- wp:paragraph -->\n<p>隨著科技浪潮，全球相勁將AI融入各大產業發展新的服務，使各項產業的發展變得更有效率，成長動能更是深不可測，尤其在創新的浪潮下，金融產業的業務模式也開始受到了衝擊進而產生改變，近年來人們開始提出了疑問：AI會取代人類嗎?人工智能是個非常魔術的領域，大想將之應用於一個系統的開發除了光鮮亮麗的成功外，背後勢必也伴隨無數的失敗，大多數的人對於其本質其實還是一知半解，以下就來討論智能金融對我們到底有甚麼影響、我們對於AI是否存有太多迷思！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>人工智能的本質就是標準化</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>人工智能有三大分支，一是神經網路，現在叫深度學習，二是機器學習，另一個則是邏輯或知識系統，能應用的範圍很廣泛。有人開玩笑說智能金融就像 Teenage Sex “ everyone’s talking about it, only a few know how to do it, they all think everyone else is at it and so pretend they are too.” 每個人都在討論它，只是很少人知道該怎麼做，每個人都假設別人在做，所以假設自己也在做。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當大家紛紛投入AI懷抱的同時卻很少人認真思考過人工智能是否真的如此神? 過去我們的生活不斷的產生變化，歷經工業革命再從電氣化開始演變成計算機化，到了近十年來互聯網的興起帶動網絡化，最後進階到數據化，以金融IT進步的歷程來看: 信息化→大數據化→自動化→智能化。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以上種種的階段其實都是自動化的體現、自動化的不斷升級，而AI給予我們的就是自動化上的協助，從技術上改變我們的生活。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> 重複性工作項目</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> 整理匯總資訊的工作</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> 蒐集資訊的工作</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以上三種特性的工作利用AI能大幅提升我們工作的效率及產值。傳統的數據庫已經不敷使用，已經無法符合現今要求的規模以及質量了。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>你認為AI會取代人類嗎?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>富比士雜誌曾經下了個標題「Artificial\nIntelligence Will Replace Tasks, Not Jobs」意思是：「AI取代的是工作的內容，而不是職業本身」也就是說，我們不必太擔憂將來因為AI而失去工作，只是工作內容會有所轉變罷了，智能金融的核心作用不是取代而是輔助。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>人的記憶、數據分析甚至是邏輯思維的能力都有限，機器智慧能夠幫助我們，協助我們在大數據爆炸的時代下蒐集資料、分析、儲存。他們能幫我們解決金融業務上的痛點問題，如個性化、客製化的需求，依據客戶過去的交易行為加上網絡時代演變的社群網路關聯性進行演算，提供用戶最合適的資產配置建議，以減少過去人力成本上大量的花費，他們將複雜的數據簡單化並建立模組，相輔相成下提供線上及線下的服務擴展市場上不同的客群，對於金融業來說可以變得更加有彈性，服務也能更靈活。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>顛覆式的創新該如何走?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>因應AI的趨勢，金融產業相爭做出創新化的變革，銀行開始上線許多創新化的數位金融產品，從物理分行網點到移動銀行，傳統銀行貸款到網路線上借貸，種種結合科技的服務使整個金融業開始你爭我奪，想將AI融入系統開發出更吸引人的產品，但不是一股腦兒的投入複雜系統應該從最簡單的事情開始做起並發揮: 零件→工具→解決方案→系統，想要長久不衰應該從小事做起，從解決方案做起，再去追求通用化。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>蓋爾定律提到，一個切實可行的複雜系統勢必是從一個切實可行的簡單系統發展而來的。想在產業間脫穎而出? 別只是試圖想用機器做出更繁瑣的事情花費更多的時間成本！ 金融科技的根本仍然是金融，但由於其電子化、虛擬化等特點，延展了金融業風險管理內涵，使風險更具隱蔽性。下一步應進一步完善法律體系，加強監管的框架和標準以及對金融消費者的保護。智能金融的發展涉及科技公司、金融機構、監管當局、消費者、中介機構和自律組織等多方主體，這些主體應密切協作，共同打造好金融科技生態。</p>\n<!-- /wp:paragraph -->', '2019-03-05 17:00:02', '【Fintech】智能金融的破局', 'article', 'publish', '2019-03-05 17:05:08', '', 0),
(3329, 1, '/upload/2019/03/未命名-1-2.png', NULL, '<!-- wp:paragraph -->\n<p>以往在銀行申請信貸的過程中有大於九成的民眾並不滿意，不僅手續較為繁雜、資訊不透明，還會時常接到不堪其擾的電話，「請問有借貸需求嗎?我們有優惠的借貸方案！」這一類的問題總是不斷的上演，為了省去繁瑣的過程，給予有資金需求的民眾更具優化的服務，「AlphaLoan實貸比較網」主打用數據幫助個人更便利取得信用貸款，讓民眾可以在最短的時間內獲得各家銀行的真實核貸利率，以及利用「人工智慧風控機器人」結合社群、電商與行動資料等其他外部資料，讓銀行對申請者能有更全面的瞭解。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>為何從市場研發出AlphaLoan?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>創辦人發現銀行業無論對內或是對外都對數據有極大的需求，尤其金融科技趨勢當在，各大銀行紛紛推出線上小額借貸服務，雖然現今已發展到方便又快速，但是對外宣稱的利率往往和實際核貸的利率與金額有所差別，過件率也可能不到五成，造成多數民眾覺得陷阱重重與資訊不透明，加上過去個人以及微型企業的資料都太過於零散、資料獲取成本也高，因此這些族群往往在申貸的過程中被忽略、拒絕，也無從評估信用狀況。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但隨著網路普及以及數據工具的發展，獲取資訊的管道越變越多，取得成本也逐漸降低，銀行能夠透過掌握更多的資訊進而創造個人以及微型企業的信用貸款市場，因此AlphaLoan團隊利用資料科學，研發了一套能夠預測各家銀行信貸實際額度、利率及核准機率的系統。這套系統就叫「實貸比較網」，使用者只要輸入自己的狀況，就能利用這個系統得到專屬自己的報告，作為選擇銀行信貸的參考。他們想做的就是第三方的協助，透過數據以及人工智慧提供有效評估的信用資料。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>數據的累積，推動自動化</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>AlphaLoan團隊在累積數據的過程中也碰壁了不少次，他們舉辦多場座談會並邀請有貸款經驗的民眾參加，從分享申貸的過程及經驗中擷取資料並累積真實數據 ，也進而對使用者的需求有更深的了解。 第一版系統上線後該團隊利用使用者回饋線上機制加速數據累積的速度，經過三番兩次的模型修正，和使用者搭起信任的橋樑，他們終於研發出更精準的預測模型並且完全自動化，加上正面回饋建立起的口碑，現今民眾使用比較網的現象已更為普遍。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>使用者分析</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>隨著有越來越多人在申請信貸前使用比較網進行試算，AlphaLoan 在2018做了一份數據報告。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.目前使用者已突破15萬人，每年台灣申請信貸需求的人數高達120萬，也就是將近13%的人會先使用比較神器做有效率的試算</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.使用者年齡分布落在25~34歲，反映出年輕族群對於新型工具的接受度較高，做決策前資訊透明化已成為重要的指標</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.申請信貸男女比例不同，男性占高達70%</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.替用戶省下60%的時間</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5. 申請信貸的企業主多數屬於管理的是資本額小、創立時間較短的小型企業，以零售和網路購物居多</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>→ 在這裡也藉機和大家說，普匯的借款平台也有試算功能，輸入幾項基本資料就可以約莫了解自已可能申貸的金額喔！科技在走，普匯也一樣與時俱進，致力於提供使用者最貼近的服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>想申貸?現在網路資源眾多、技術進步，唯一要警惕的就是自身的隱私是否安全，決不要變成任人宰割的羔羊！</p>\n<!-- /wp:paragraph -->', '2019-03-08 16:45:15', '【時事分享】新創分享— AlphaLoan實貸比較網', 'article', 'publish', '2019-05-08 17:02:39', '', 0),
(3350, 1, '/upload/2019/03/8個fintech下.jpg', NULL, '<!-- wp:paragraph -->\n<p>延伸閱讀：<a href=\"https://www.pu-hey.com/%E3%80%90%E6%99%AE%E5%8C%AF%E8%A7%80%E9%BB%9E%E3%80%91%E7%BF%BB%E8%AD%AF%E5%85%AB%E5%80%8Bfintech%E5%BB%A3%E5%91%8A%E7%A4%BE%E7%BE%A4%E8%A1%8C%E9%8A%B7%E6%96%B9%E5%BC%8F%E4%B8%8A/\">【普匯觀點】(翻譯)八個Fintech廣告社群行銷方式(上)</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"cyan-bluish-gray\"} -->\n<p class=\"has-text-color has-cyan-bluish-gray-color\"><strong>Instagram上的Fintech廣告</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>IG每日擁有超過5億的活躍用戶，而且這個數字仍持續上升中。 <br>這個數字持續增長的原因在於IG不斷增加更多元的功能，像是IGTV和更多的介面標籤，這些功能使用戶對IG的黏著度持續上升，並花更多的時間使用IG平台。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>下列的IG廣告對於他們選定目標的方式以及文章的內容都是具有創意且富有策略性的。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>5. Coinbase</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>65%的美國民眾都是視覺性學習者，因此使用圖像性廣告展示給他們看會比花費長篇大論闡述你的產品有多好來的有效許多。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Coinbase就是一個成功例子。Coinbase是進行彼特幣、彼特幣現金、以太坊、以太經典和萊特幣的交易平台。他們成功利用簡單的圖像去解釋抽象的虛擬貨幣交易概念。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"width\":288,\"height\":512} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"https://lh4.googleusercontent.com/wJo-sdSbDTN1gthkeSbYYFq8_peu2IOuIXD5YYW__sbqcUdfoxLsMx6C72bEgPA48UCfLOCY7u9H6FThPJ1GNevTahvnccqhiZsYi5yfDYQUhHEO4m8nQbkiClroVCc_eENPHTTqjCQ0vbDZfA\" alt=\"Coinbase Instagram ad\" width=\"288\" height=\"512\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>這則廣告使用app當中的截圖讓他的受眾知道app大致的樣貌以及運作方式，並引起他們的興趣。透過這個方式，在用戶下載app之前，他們就能透過圖像預期使用app的體驗。 <br>即便你的產品是複雜難懂的，藉由早期提供app視覺圖進行行銷，你可以最小化使用者在使用產品時可能遇到的問題。消費者在面對新產品時都會有各自的學習曲線，透過早期的引導會使消費者在使用產品時更加舒適，這也代表你有更多的機會賣出商品。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>廣告小秘訣：提供你app運作的畫面作為廣告內容。與其他廣告進行比較，讓你的目標客群搶先看到商品的視覺形象以及感覺。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>6. Aspiration</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Aspiration是一間線上財務平台幫助使用者管理他們的銀行、投資、退休、慈善需求問題。Aspiration提供大量的教育內容去幫助他們的使用者使用產品或進行金融教育。 <br>Aspiration為了接觸他們的所有客群選擇在IG上投放不分世代的廣告。因為他們的使用者年齡分散實在是太分散了，讓Aspiration在選擇平台時決定使用受廣大使用者歡迎的平台投放廣告。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"width\":354,\"height\":512} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"https://lh3.googleusercontent.com/HiOO12Cb9eynMkcp2F_C74-IZSzUdNq2iDdAv_42c_FKH1Xj-MMV1VuWyinCDYDwmZyBKbgM5Cp3tXHWPOXI_4jkwcrfeAOgHOAieo_c0qkt42JQJVCd5GLwbrP5sGWB9t7XilnhYGebmDwv3g\" alt=\"Aspiration Instagram ad\" width=\"354\" height=\"512\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>這則廣告優異的點在於它向目標受眾展示了app內部頁面以及他們的顧客要如何透過這個app去存款。這則廣告透過秀出簡潔的資訊內容讓他們的受眾對於使用這個app後能如此簡單得到結果感到興奮。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>廣告小秘訣：使用廣告去示範你的商品實際運作的畫面。從最初開始展示，並秀出使用者會在最後得到那些回報。舉例來說：更多的存款、更多的經驗或其他使用者在使用其他產品會遇到的難關，用廣告說明你的商品會如何解決這些難題吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"cyan-bluish-gray\"} -->\n<p class=\"has-text-color has-cyan-bluish-gray-color\"><strong>Google Ads上的Fintech廣告</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>要說明google這個平台有多受歡迎從人們將「google」當作一個動詞使用便可見一斑。為了迎合他的使用者，google持續增加更多客製化的特色在他們的產品上。舉例來說：在Google ads中，使用者可以進行創建、分析並透過Google Sheets的幫助更快速地分享他們的成果。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這邊就讓我們來看看這些Fintech公司是如何利用Google ads去延伸他們的觸及群眾吧！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>7. Square Cash</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Square Cash，或稱為Cash App，是一個能讓使用者彼此間透過app寄送金錢的行動付款服務。Square Cash是一個相對新穎的服務但已經有超過700萬的使用者了。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://lh3.googleusercontent.com/MyJ589nuKBSUtkOwtp6AUUnsdTNC53U32KH5URe9YoATen5_kt2eor0dEoD8qqI9URI86MECI18YrUiEJS4jKjX8X2F4D-m3FmUh4abgZ9TcmnoCnPwhcR4e2z0xwiOahUnX9COTGqFLdp94vQ\" alt=\"Square Cash Google Ad\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>比起特別宣傳Cash App，Square投放的廣告更多是關於他們的核心產品：Square Reader、Square Stand和Square Register 。<br>這個方法會成功是因為這些產品都會連結到官網，並展示公司提供的其他軟體服務，當然包含Cash App。因為是對主要商品進行宣傳，因此這些app會被視為使商品服務更完善的使用者附加服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>廣告小秘訣：如果你已經有一個暢銷商品，將它與你的廣告進行連結，透過舊有的銷售管道向你的可能客戶宣傳你們有額外的商品可供選購。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>8. TransferWise</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>TransferWise是一個金錢轉換服務平台。他讓使用者可以從世界各地接受或寄出金錢。透過蒐集超過3億使用者的資料，TransferWise已經找出方法讓跨國金錢轉讓變得便宜、公平而且簡單。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>TransferWise最新的google廣告向他們的受眾傳達了關於他們的三個主要特色：<br>1.國際性金錢轉換<br>2.低費用<br>3.可以轉換成各式主流貨幣</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://lh6.googleusercontent.com/y-26Bk9MPTftlFYIciB_vGCgxKEWl5suZAgrWKLsdmrcxngFzCKLIsgcceJli1ZZ7CGmR-JCPMxovcvch2VDdf9kuHvagND9S6XmXYi6pa8nNgNAkutRcvCHk_fz9JNkAAcjuveIm4UwFy_b5Q\" alt=\"TransferWise Google Ad\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>這個方式成功的地方不光在於他強調了它的特色鼓勵閱聽者點擊去了解更多，而在於這個廣告給閱讀者額外的獎勵。使用者在體驗過相關功能後，踏們可以藉由告訴親朋好友TransferWise來轉取額外的金錢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>廣告小秘訣：致力用廣告向你的顧客宣傳他們能獲得的利益。一個成功的轉介程序能讓你在競爭者中脫穎而出並提供額外的價值。不論你是提供與競爭對手具差異性或是更完善商品或服務，將他放在廣告中讓使用者能從眾多競爭者中將你區分出來。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>準備好投放你自己的Fintech廣告了嗎？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金融其實並不是一個有趣、民眾願意談論的熱門話題，加上一般民眾聽到金融時若沒有特別理由不會進行更多思考，行銷者應該特別關注這些能使用各種方法創造熱門題材的Fintech公司。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>不論你身處何種產業，越來越多Fintech公司向大眾呈現出可以藉由FB、IG、google廣告這些大眾且令人舒適的平台去宣傳你的產品有多麼有趣或多麼吸引人。試著使用不同的社交平台去讓你的廣告觸及更多人，舉例來說FB的廣告針對某一刻群，同時IG廣告針對另一不同客群，要確保正確的受眾有機會可以檢視你的產品。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>千萬記住，投放廣告時要主動積極並採取不同策略以有趣的方式呈現這些資訊，現在就開始構想你的第一份廣告吧！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>原文連結：<a href=\"https://adespresso.com/blog/fintech-ads-examples/\">https://adespresso.com/blog/fintech-ads-examples/</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><br></p>\n<!-- /wp:paragraph -->', '2019-03-11 17:44:48', '【普匯觀點】(翻譯)八個Fintech廣告社群行銷方式(下)', 'article', 'publish', '2019-05-08 17:02:21', '', 0),
(3358, 1, '/upload/2019/03/未命名-2.png', NULL, '<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://lh4.googleusercontent.com/2-_SvRxnW-dj5IqiqQ7Sxr8SP3PV6vFbg4Nbn0ABWeX08TIgQ3AhgWwPpeWFOJkkriwJ3I7idffLYtTYwNbkcZBmBWQLB1RAf_n5H1N0mi9gT8lPyxg15QKIRV9vpUVYHMHBI_-WkkixQvky-A\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>借錢給別人最怕的就是對方不還錢，這在民法上稱為債務不履行，所謂債務不履行不單單只有借錢不還這個解釋，今天我們來看看債務不履行的定義和相關法條，並說一說普匯對於債務不履行應對措施吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>債務不履行種類</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在民法中大致將債務不履行分為<strong>三類</strong>：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.給付不能：指契約上有給付義務的一方，不能履行其應給付項目。舉例來說小普跟小匯買了50000元的機車並約定一個月後交貨，但過了一個月後要交貨的機車卻因為火災燒毀了，小匯無法交貨給小普，便是給付不能。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.給付延遲：指債務履行期到了，債務人並非給付不能，且是因其單方面可歸責的事由而未為給付。舉例來說小普跟小匯借了5000元並約定一個月後還款，但一個月後小普忘了這件事沒有進行支付，便是給付延遲。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.不完全給付：指債務人已經給付了，但是給付的內容不符合債之本旨。舉例來說小普跟小匯借了5000元並約定一個月後還款，而過了約定還款日後小普只還了3000元，便是不完全給付。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>民法上的債務不履行</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>民法226條：因可歸責於債務人之事由，致給付不能者，債權人得請求賠償損害。前項情形，給付一部不能者，若其他部分之履行，於債權人無利益時，債權人得拒絕該部之給付，請求全部不履行之損害賠償。民法227條：因可歸責於債務人之事由，致不完全給付者，債權人得依關於給付遲延或給付不能之規定行使其權利。因不完全給付而產生前項以外之損害者，債權人並得請求賠償。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>民法231條：債務人遲延者，債權人得請求其賠償因遲延而生之損害。前項債務人，在遲延中，對於因不可抗力而生之損害，亦應負責。但債務人證明縱不遲延給付，而仍不免發生損害者，不在此限。<br>民法上的條文都是在保護債權人的，而民法上最常用的賠償方式為損害賠償與強制執行。強制執行意即透過法院取得執行名義，對債務人名下的財產進行扣留、抵押等動作。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯的應對措施</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯平台約定還款日為每月的10號中午12點，並給予債務人七天寬限期(期間會打電話告知)，換句話說超過每月17號中午12點後平台系統便會將其記錄為逾期還款。逾期後債務人須繳納每天千分之一本金的延滯息給債權人(平台會寄發訊息告知債務人與債權人)，債務人需選擇對債務進行一次付清或產品轉換(減少每月還款金額並延長還款期間)，而兩者都必須支付給平台違約金與手續費(共為本金5%)，在完成這些動作後平台才會停止計算延滯息。若是遇到惡意積欠款項者，平台會提供債權人相關資料協助進行訴訟。普匯會盡全力維護每個使用者的權益，絕不姑息惡意違約者！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>延伸閱讀：<a href=\"https://www.pu-hey.com/%E3%80%90%E6%99%AE%E5%8C%AF%E8%A7%80%E9%BB%9E%E3%80%91%E6%B7%BA%E8%AB%87%E9%81%BF%E9%9A%AA/\">【普匯觀點】淺談避險</a></p>\n<!-- /wp:paragraph -->', '2019-03-12 17:24:09', '【普匯觀點】什麼是債務不履行？', 'article', 'publish', '2019-05-08 17:02:09', '', 0),
(3387, 1, '/upload/2019/03/未命名-2.jpg', NULL, '<!-- wp:paragraph -->\n<p>這幾年行動支付的崛起速度令人無法忽視，大家都知道中國人人都在使用支付寶，馬雲甚至說過：「連西湖邊的乞丐都用支付寶乞討了」可見其在中國影響力之大。但你絕對不會想到，竟然有人比中國更依賴行動支付！自2016年起，丹麥政府已經下令央行停止印刷紙鈔與鑄幣，據統計顯示，丹麥境內只有6%的交易是透過現金進行！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>無現金社會</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>其實除了丹麥之外，北歐的各國也逐漸採用行動支付的方式進行日常交易。在北歐境內能使用歐元的場合可以說是少之又少，目前只有醫院、藥房和郵局保留了現金服務，若是什麼都不知道便帶著滿滿的歐元現鈔到北歐自助旅行，可是會被商店的老闆拒收的喔！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>世界各國行動支付比例</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據金管會2016年「金融科技發展策略白皮書」中指出，台灣電子支付比率僅為26％，遠低於鄰近國家南韓的77％、香港的65％、中國的56％以及新加坡的53％，而推動無現金社會的北歐各國更是高達90%以上。台灣民眾對於行動支付的接受度仍然十分低迷，主要是因為下面幾項原因：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.超商、ATM的密集度高居全球之冠</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.信用卡普及</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.習慣舊有付款模式，不想做出改變</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.法規繁雜缺漏，相關業者推動困難<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>總結</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>簡單來說台灣因為地狹人稠，每個人都被服務的無微不至。而行動支付發展快速的國家可能具有現金不被信任(辛巴威)、國土過大(北歐諸國)、境內銀行手續費過高(中國)等等問題存在，因此這些國家主動尋找解決方式，而他們得到的解答便是走向無現金社會。台灣舊有的付款方式已經深植民心，若是沒有需要解決的相關問題且政府也不願意協助推廣，那麼行動支付要在台灣的普及可能還是需要一大段時間了。普匯平台也盡全力在推廣新型態的借貸方式，不像以往銀行貸款需要大量手續和等待時間，只要在家中便可完成申請，趕緊到我們的官網逛逛吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', '2019-03-15 17:29:36', '【時事分享】  印鈔已經成為歷史 –北歐無現金社會', 'article', 'publish', '2019-05-08 17:01:52', '', 0),
(3396, 1, '/upload/2019/03/1553046889052.jpg', 'https://www.youtube.com/embed/ki_6HAu-6RQ', '<!-- wp:core-embed/youtube {\"url\":\"https://www.youtube.com/watch?v=ki_6HAu-6RQ\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://www.youtube.com/watch?v=ki_6HAu-6RQ\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:paragraph -->\n<p>共享經濟，新的商業模式在近幾年席捲了全世界。隨著科技越來越發達，人類的消費模式越來越多樣化，商業經濟不再只侷限於傳統店面交易，「我需要使用它，但我不需要擁有它」這句話最能顯示出共享經濟的精神，以前背包客到世界各國旅遊時在住宿上總得花費不少，他鄉遇故知的機率少之又少，想省錢的人更是會直接找當地家庭借宿，安全堪憂，而現今小編想出國旅遊時，「Airbnb」都是學生及小資族的最愛，共享成為新的商業模式，讓我們的生活變得更多樣化。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>共享不等於租賃</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>共享經濟並不是租賃公司，它是一種去中介化、透過物聯網驅動的商業型態，利用社群網絡快速廣泛媒合，不再需要繁瑣的手續、實體的店面，不須再透過人與人之間的溝通，人們透過線上平台就能直接使用物品，交易成本下降對使用者來說無疑是最大的福音，同時市場資訊的透明度也能提高，消費者行為的數據也能累積更多，有助於未來的多元化發展。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>共享經濟需要三個驅動力的結合，社會、科技以及經濟。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>社會</strong>: 人們對於永續的觀念開始萌芽</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>科技</strong>: 物聯網、行動通訊以及社群媒體的技術提升</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>經濟</strong>: 新創產業崛起，將社會的閒置資源做有效規劃<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>消費者的行為轉變也是共享經濟發展的主要因子，</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li><strong>智慧型手機的普及</strong>: 手機不離手是時下一點也不足以為奇的畫面，APP Store史的平台能建置自己的服務，加上定位系統的優化，消費者在短短五分鐘內就能得到業者回應並即時取得物品，因此消費者開始都轉往線上取得服務。</li><li><strong>社群媒體的出現</strong>: Facebook以及IG的出現改變了資訊傳播的方式，以前大家都是用yahoo即時通做通訊，而現今fb及IG能夠掀起的社群感染力可說是極為強大，分享推文、限時動態都是共享經濟能大幅推廣的重要因素。</li><li><strong>線上購物以及評價系統的建立</strong>: 隨著電子商務的蓬勃發展，人們在網路線上購物的比例大幅增加，並建立了一個信任機制的評分系統，不僅消費者能為店家評分，店家同時也能評分消費者，讓雙方能透過信任進行交易，而這樣的系統也延伸到了共享經濟平台。</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>台灣共享平台</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Uber、Airbnb都是在台灣市場深耕已久的共享經濟平台，近年來也有許多新創業者如Pick one、潔客幫，共享經濟可以分成很多類型: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>汽車共享: Zipcar , Uber</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>單車共享: obike , ubike</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>車位共享: Uspace</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>房屋共享: Airbnb,共生公寓</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>物流共享: LaLamove<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以上都是大家耳熟能詳的共享經濟平台，但是蓬勃發展中勢必也衍生出許多問題，個人提供的服務品質不一，很難比對提供者的素質，各國法規限制也都不同，駕駛開車發生意外的新聞也層出不窮，儘管問題不斷浮上檯面不能否認的是，共享經濟的趨勢仍不可擋，科技的進步帶來新的產業，改變民眾的消費模式，變得更有效率、更加便利，而這也就是現今世代下人們所追求的！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>你，有跟上共享經濟的腳步嗎?<br></p>\n<!-- /wp:paragraph -->', '2019-03-19 17:00:17', '【普匯觀點】你有Catch到共享經濟嗎?!', 'video', 'publish', '2019-05-08 17:01:27', '', 0),
(3413, 1, '/upload/2019/03/未命名-1-1.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">在進入資本市場的殿堂前，我們先談談直接金融與間接金融是什麼？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">簡單來說我們可以從「籌錢」的角度出發:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">企業(資金需求者)透過發行有價證券(股票)，而直接向一般個人(資金提供者)募資，過程中不需要經過銀行中間機構，企業收到了錢，往後這些投資人有機會拿到分紅，這就是直接金融的概念，我們泛指「資本市場」。但台灣的文化與習性較難直接找到個別投資人販售證券，所以企業會通過承銷、證交所等管道讓大眾購買，與銀行不同的是，承銷商僅收取手續費和平台費用，並不會介入資金供需雙方間。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3420} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/03/image-5-300x127.png\" alt=\"\" class=\"wp-image-3420\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>國際資本市場走向</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">根據2017集思創意顧問公司的統計資料，英美、香港、新加坡等先進國家，直接金融比率較間接金融高出許多，足見直接金融的重要性，其除了助長創投之外，當市場出現重大變動時，依靠直接金融的企業資金斷炊風險較低。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3417} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/03/image-2.png\" alt=\"\" class=\"wp-image-3417\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"> <strong>為何企業要走入資本市場?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">每間企業會經過審慎評估與考量，決定是否邁入資本市場。上市櫃能夠提升企業市值、形象及競爭力，使籌資管道更多元，還有增加流動性使股性好等優勢。但凡事都有一體兩面，選擇未上市櫃，因資訊相對不透明，而有獲取高額價差機會。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>企業走入上市櫃的八大優勢:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3415} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/03/image-1.png\" alt=\"\" class=\"wp-image-3415\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>如何進入資本市場？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">從申請上市櫃，到各項籌資、增資行為，藉由資本市場的多元化籌資工具取得較低成本的資金，並持續拓展，透過併購、合資等方式擴張事業版圖，活絡資本市場。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3418} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/03/image-3.png\" alt=\"\" class=\"wp-image-3418\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>&nbsp;未來趨勢—跨境上市櫃、新經濟上市櫃</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">台灣監管機制較為嚴格，對於上市櫃要求公平、分散，還有限制單一認購人上限，這些侷限也使2016～2018上市櫃平均籌資金額與第一上市櫃數量逐年下降。反觀全球與亞太區，受惠於獨角獸而成長，且因投資文化的差異性，許多企業已轉往美、港發展。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">台灣近來力求突破與改革，著眼於新經濟，多元上市方案逐步推出，放寬了獲利條件，讓新創公司有更大誘因選擇台灣。新創公司如星星之火，是不容忽視的存在，期許台灣透過各項革新，再造繁盛景象。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3419,\"width\":452,\"height\":95} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"/upload/2019/03/image-4.png\" alt=\"\" class=\"wp-image-3419\" width=\"452\" height=\"95\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', '2019-03-22 17:07:06', '【金融趨勢】走入資本市場', 'article', 'publish', '2019-05-08 17:01:14', '', 0),
(3434, 1, '/upload/2019/03/未命名-1-4.png', NULL, '<!-- wp:paragraph -->\n<p>金融與科技加速融合，5年前，超過75%用戶還使用電腦或是各尋不同媒介做各式商業行為，這5年裡，脫離電腦、用手機支付幾乎是100%的用戶首選，移動互聯網的創新金融模式已席捲整個中國甚至蔓延到各國，第三方支付依託于智慧手機的普及崛起，支付寶、微信支付更是成長為巨無霸型的支付機構，極大改變了支付結算這百年以來最基礎的金融生態。以往我們想儲蓄、投資理財都會將錢放入銀行，但往往銀行的低利率再加上通膨率的扣除帳戶裡的錢不升反降，於是人們開始轉向線上理財平臺。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>為什麼會有互聯網理財?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當第三方支付越來越普及越來越便捷時，第三方支付平台裡的資金就像雪球般越滾越大，就像是一個資金池的概念，業者們利用這些龐大的資金流進行生息、周轉，由此產生的收入成為第三方支付重要的營利來源，而這樣的情況下必定產生非法集資的問題，因此第三方支付機構開始推出各項創新服務，客戶可用存款餘額進行各項理財服務，而這樣的業務推出後正式開啟了第三方支付進軍理財業務的新局面，同時也衝擊了銀行活期存款的穩定性。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據2018正規理財平台排名報告顯示，排名前五名依序為餘額寶、京東金融、工商銀行APP、活期通、如意寶。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>餘額寶是很多人選擇信賴的理財App，它在行業中的地位可說是佼佼者，用戶數超過1億，規模在最高峰時超過7000億元位列世界第二大貨幣基金。餘額寶不僅能夠得到收益，還能隨時消費支付和轉出，避免了想用錢越不能取出來的煩惱，轉入餘額寶的資金在第二個工作日由基金公司進行份額確認，在當今的市場上餘額寶仍是首屈一指的理財平臺。餘額寶的出現，被認為開創了中國互聯網理財的元年，成為普惠金融的典型代表，並極大激發了金融行業的創新熱情。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>網路信貸的興起，合規不合規?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>互聯網完成了對支付體系的改造後大舉進攻融資領域，以網絡平台為基礎，利用大數據分析技術發展網路貸款，對原有的銀行融資體系帶來極大衝擊。網路貸款發展其一就是去中介化的P2P模式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>P2P整合了閒置的資金，讓閒置資金流入有需要的人手上，同時提供資金的出資方也能獲得應有的回報，而這種回報往往高於銀行存款所生的存款利息。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(小編在之前有為大家試算投資報酬率喔!詳情可以點選&lt;大學生投資心法&gt;)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>經過幾年的發展，P2P網路借貸解決的資金市場效率不足的問題，透過科技及網絡運用，平台幫助小額投資者建立直接的渠道，促進普匯金融的發展，但同時大陸P2P業主卻也出現了亂象，有問題的平台占比高，各家網貸平台出現兌付的困難，老闆收錢跑路、平台清盤、偵查介入等各種現象層出不窮，短短不到半年的時間就有數百家網貸平台倒閉，因此各監管單位開始規範，小額信貸平台因而嶄露頭角。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>2018理財指數下滑？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據騰訊理財通、騰訊金融科技智庫、國家金融與發展實驗室發佈的《2018年互聯網理財指數報告》，2018年互聯網理財指數下降到了563點，相比2017年的695點，降幅高達23.45%。此報告顯示在強行監管以及行業洗牌的影響下，互聯網理財市場受到一定的衝擊，由於監管成本的提升，經營較差的互聯網金融平台尤其是較不受合規的P2P業者被迫退出市場，互聯網金融台平數量和理財規模迅速下降，理財指數因此走向下滑的趨勢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>“下跌的原因，首先在整個財富管理、資管大格局下，互聯網理財一定會下跌；其次是嚴格監管，特別是對P2P一些不太正規的理財平臺進行嚴監管。”                       殷劍峰表示。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但長遠看來，監管趨嚴對於互聯網理財平台的發展有正向的影響，平衡兼顧穩定發展與促進改革，同時加入風險管控。互聯網金融的根本仍然是服務客戶，提供全新的體驗創造更多的附加價值，金融服務的透明度提高、交易成本下降、操作程序上更優化……等。讓理財門檻大幅降低，人人都可享有小額理財的能力！<br></p>\n<!-- /wp:paragraph -->', '2019-03-28 17:00:58', '【金融趨勢】大陸互聯網理財面面談', 'article', 'publish', '2019-05-08 17:00:42', '', 0),
(3440, 1, '/upload/2019/03/新零售.png', 'https://www.youtube.com/embed/MxjZebzAOcc', '<!-- wp:core-embed/youtube {\"url\":\"https://www.youtube.com/watch?v=YO-BODCKYSw\\u0026feature=youtu.be\",\"type\":\"video\",\"providerNameSlug\":\"youtube\",\"className\":\"wp-embed-aspect-16-9 wp-has-aspect-ratio\"} -->\n<figure class=\"wp-block-embed-youtube wp-block-embed is-type-video is-provider-youtube wp-embed-aspect-16-9 wp-has-aspect-ratio\"><div class=\"wp-block-embed__wrapper\">\nhttps://www.youtube.com/watch?v=YO-BODCKYSw&amp;feature=youtu.be\n</div></figure>\n<!-- /wp:core-embed/youtube -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">現在的世代每個人人手一支手機，生活已邁向電商的世界，從銅板交易轉向電子支付，科技技術的應用汰換了傳統購物模式，傳統零售業者蛻變成「新零售」的運作型態，日常生活的網路購物也已經是再習以為常不過的事了。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>所以，新零售是什麼?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">以往我們進行的是以物易物的買賣交易，而後衍生出電子商務模式，但在未來的十年、二十年誕生的是新零售一說。                                                                                    新零售是甚麼? 用簡單的概念來解釋「以消費者體驗為中心的智慧新零售」再進一步的說明，新零售必須結合線上、線下以及物流才能誕生新形態，從消費者購買商品的各種情境出發去了解其需求，並連結起各大消費通路，串聯電商、實體商場以及倉儲物流，再透過數據及追蹤技術分析消費者消費型態，不再受地域及時間的限制全面性的掌握消費市場，把所有用戶都化為可識別、可服務的對象，抓住顧客需求並優化體驗，依據消費者行為的數據，新零售業者轉為先了解顧客的需求才決定銷售市場的樣貌，再決定商品的內容。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>發展策略</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">以往線上現下的業務型態有很大的區別，實體店面與網路線上平台的經營模式及價格都有所差異，但隨著數位化的浪潮，線上線下不再有如此大的差異性，藉由新型態的出現，業者能夠創新的空間也越多，不再以單一品項做販售，而是範圍更廣泛、更多元的零售模式。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong><em>新零售業者在未來的發展及策略可能有以下幾種：</em></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>提升行銷手段: 根據線上用戶的使用數據，發想個人化廣告，加強對用戶的掌握度，提升黏著度。 </li><li>開發新型場景消費模式</li><li>利用線上帶動線下績效表現</li><li>強化資料整合系統，根據不同客戶建立不同機制</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>搶攻商機</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">未來的科技相信會往更高端的方向走去，科技能使商業模式更具效率，若業者能掌握新興科技並發現市場痛點，搶攻廣大消費市場勢必能為產業闖出一片天。若導入區塊鏈技術，新零售業者就能發展出具有高附加價值的產業鏈，在眾多競爭者中找到屬於自己的定位並且多面向化的整合也是未來重要的策略，生活中各種細節都須留意，那麼擴大市場版圖就不是一件難事！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">舉一個例子，前不久全球第一大的美國零售業巨頭西往斯公司宣布破產，這家成立於1886年的百貨公司，被誰打敗了?是大家熟悉的沃爾瑪！過去十年，越來越多實體店開始線上線下進行轉型，而希爾斯的變革步伐明顯落後許多，正是這種對傳統零售的故步自封，讓它終將走向滅亡之路。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">任何企業若是不瞭解客戶、不深入研究市場變化，再高超的技術也不依定能打住消費者的心，現在是以消費者為本的世代，這也強調了掌握AI戰略變革的必要性！<br></p>\n<!-- /wp:paragraph -->', '2019-03-26 17:00:27', '【普匯觀點】創新遇上新零售 如何立於不敗之地?', 'video', 'publish', '2019-05-08 17:01:02', '', 0),
(3449, 1, '/upload/2019/03/未命名-3.png', NULL, '<!-- wp:paragraph -->\n<p>普匯金融科技是以金融為主、科技為輔的團隊，普匯的終極目標是創造一個無人化、人人能使用的線上借貸平台，今天要帶大家了解在普匯平台借貸所能得到的保障。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>借貸定義及相關法規</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據民法第474條，稱消費借貸者，謂當事人一方移轉金錢或其他代替物之所有權於他方，而約定他方以種類、品質、數量相同之物返還之契約。當事人之一方對他方負金錢或其他代替物之給付義務而約定以之作為消費借貸之標的者，亦成立消費借貸。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>借款者與投資人在普匯平台進行借貸與放款都會與平台簽訂契約，根據民法153條規定只要雙方默認時契約便已成立，而簽訂的契約會由普匯平台加密進行保存，未來若是發生紛爭(如惡意積欠債務、變更契約內容)，使用者都可以向普匯索取相關資料，並依據民法向法院提請調解或是訴訟，以法規確切保障每個平台使用者的權益。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>延伸閱讀：<a href=\"https://www.pu-hey.com/%E3%80%90%E6%99%82%E4%BA%8B%E5%88%86%E4%BA%AB%E3%80%91%E9%9F%93%E5%9C%8Bp2p%E5%80%9F%E8%B2%B8%E6%B3%95%E6%A1%88%E4%B8%8A%E8%B7%AF%EF%BC%81/\">【時事分享】韓國P2P借貸法案上路！</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯的利率設定</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據民法205條，約定年利率超過百分之二十者，債權人對於超過部分之利息，無請求權。另外根據刑法344條重利罪規定「乘他人急迫、輕率或無經驗貸以金錢或其他物品，而取得與原本顯不相當之重利者，處一年以下有期徒刑、拘役或科或併科一千元以下罰金」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯平台嚴格遵守各項法規，將借款年利率訂在5%~20%之間，借款人可以依照自己的還款能力設定利率，並且隨時隨地憑自己的意願進行借款，對借貸雙方都有保障！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>年輕人的借貸平台</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯的目標客群是年滿20歲的學生。沒有社會經驗的學生是很難跟銀行借到錢的，而且銀行規定個人在無擔保的情況下，個人最高總負債依規定不可逾月收入的22倍，銀行一般只會核貸10~15倍的金額給借貸者。學生多為打工族，在提供銀行月收入證明時會被認為是不穩定收入，會使銀行的放款意願降低。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>與此相比普匯秉持著幫助學生的精神，僅要求提供身分認證及簡易財務證明便可進行借款，而使用者提供的資料也會加密後保存在AWS上，以隱密、速度、貼心的原則站在使用者觀點提供服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯每天都在解決每個投資人和借款人的疑難雜症，並且建立數據分析使用者在各個階段可能會遇到的問題並提供解決方法，趕緊進入我們的官網了解更多吧！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>馬上試算： <a href=\"https://borrow.influxfin.com/\">https://borrow.influxfin.com/</a> </p>\n<!-- /wp:paragraph -->', '2019-03-29 17:04:18', '【普匯觀點】關於借貸，普匯是你最好的選擇', 'article', 'publish', '2019-05-08 17:00:29', '', 0),
(3454, 1, '/upload/2019/04/未命名-4-1.jpg', NULL, '<!-- wp:heading -->\n<h2>民法上的契約定義</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>民法153條第1項：「當事人互相表示意思一致者，無論其為明示或默示，契約即為成立」。這項條款簡易說明了契約成立的條件，只要雙方同意便可成立契約，不論是以書面或是口頭在法律上都會有相同的效力。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>口頭契約注意要點</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>雖然上面說到雙方同意便可以生成口頭契約，但仍然要注意下面幾個小地方，若是違反了即使雙方同意還是會被當作無效契約喔！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1. 不依法定方式者，無效。但法律另有規定者，不在此限。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2. 憲法保障之自由權利，若有妨礙他人自由、避免緊急危難、維持社會秩序，或增進公共利益者，得以法律限制之。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第二點節錄自憲法第23條，自然人在行使各種自由權利時，仍不得違反這些項目，憲法的位階高於民法，因此根據法律優位原則，即便民法說明只要雙方同意便可簽訂契約仍不能牴觸上面這些情形。簡單來說，若今天雙方口頭約定一起去搶銀行，基於維護社會秩序原則，這個契約在法律上是無效的。若是今天有一對熱戀中的情侶發誓說要一起白頭偕老，若有天他們分手了也不能以這個口頭契約要求另一方賠償，因為這明顯妨礙了他人的自由。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>延伸閱讀：<a href=\"https://www.pu-hey.com/%E3%80%90%E6%99%AE%E5%8C%AF%E8%A7%80%E9%BB%9E%E3%80%91%E4%BB%80%E9%BA%BC%E6%98%AF%E5%82%B5%E5%8B%99%E4%B8%8D%E5%B1%A5%E8%A1%8C%EF%BC%9F/\">【普匯觀點】什麼是債務不履行？</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>口頭契約舉證</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>為了防止民眾濫用口頭契約進行訴訟，我國民事訴訟法規定主張權利的一方要負舉證責任，需要以錄音檔的方式或是公正證人提供法院作為判斷依據，當然錄音檔有造假可能，證人有可能是串供的，就算提供了這些仍需經法官判定是否具有實質效力，因此口頭契約要訴訟成功還是存在著一定的難易度。若是真的有重要的契約還是以書面為主才能保障到雙方的權利喔！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>總結</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>雖然主張權利人需要負責舉證，但口頭契約在法律上還是具有相當的效力的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舉例來說若是今天A學生透過電話向便當店訂了100個便當，但卻沒有去拿，便當店若是有電話錄音紀錄甚至line通聯紀錄都能向法院提起訴訟，要求該學生履行契約或是進行損害賠償。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在知道口頭契約的效力後，以後在口頭允諾別人時時要特別注意，不要認為只是開個玩笑或是沒有書面資料就不需負任何法律責任，因為在雙方同意的當下，協議的內容便已經是一個口頭契約並且需要負法律上的責任了！</p>\n<!-- /wp:paragraph -->', '2019-04-01 16:42:49', '【普匯觀點】口頭約定算契約嗎?', 'article', 'publish', '2019-05-08 17:00:15', '', 0),
(3478, 1, '/upload/2019/04/decision-1697537_1280-.jpg', NULL, '<!-- wp:paragraph -->\n<p>普匯的債權投資和股票分別屬於低風險和高風險的投資商品，本篇的比較方式以加權指數做為參考依據，並無法完整代表所有投資人的狀況，請多加留意。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在開始計算前，我們先來了解影響股價的因素以及投資股票可能遭遇的風險。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>影響股價的因素</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">1. 公司運作情況：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>我們可以從四大財務報表(損益表、資產負債表、現金流量表、股東權益表)來檢視一家公司的構成和營運是否健康，與股價最相關的是損益表，損益表會反映公司前一段時間的營運情形，在財報出來後若是超出市場預期太多市場會對股價會進行修正，當然若是有內線消息的話在財報出來前便對股價進行修正的狀況也是很常見的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">2. 產業淡旺季：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>每個產業都有屬於自己的淡季和旺季，像是旅遊業在寒暑假的營業額一定會高於其他時段，而這些收益的差距便會影響財務報表，進而影響到股價。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">3. 法人進出：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>股價的變動與成交量有極大相關，相較於散戶，法人(外資、投信、自營商)能透過一次買進或賣出大量股票使股價變動，不少投資人在選擇股票時也會依法人近期進出做為參考依據。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">4. 總體經濟影響：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>景氣循環、政治因素還有其他投資商品利率的變動都是影響股價的因子，而且這些因素都是會直接對大盤產生影響，而不像前面幾樣只對個股產生影響喔。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>投資股票的風險</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>投資股票時會產生的風險主要分為系統性風險和非系統性風險。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>系統性風險是因為某種因素導致市場上所有證券都蒙受損失，主要分為以下四種：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.  政治風險：影響股價的相關政治因素或政策，如政黨輪替和加重進口關稅。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.經濟風險：影響股價的各式經濟因素，如景氣循環、匯率變動、物價等等。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.心理風險：投資人的預期心理造成的股價變化，如景氣不好時投資人預期未來景氣差而不願買股，導致股價低迷。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.技術風險：股票市場內部因技術性操作因素引起股價波動而產生的風險，包括投機者的投機性操作、股市的強弱變動趨勢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>非系統性風險是個股因自身內部問題可能產生的風險，主要分為：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.經營風險：因公司經營不善所帶來的風險，如營運策略不佳導致虧損、公司管理鬆散等等。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.財務風險：因為公司資金不足所造成的風險，常見的有資金週轉不良、缺乏資金挹注，都會使公司股價陷入不利。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>那麼投資普匯債權會遇到那些風險呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.逾期或是壞帳風險：借款人逾期未還款導致壞帳。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.公司倒閉風險：因天災或人為因素致使公司倒閉。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>比較兩者可能遇到的風險，在投資股市可能遇到的風險包括總體經濟影響以及個人因素影響，且多數的風險因素是投資人難以取得情報且無法以一己之力解決的。相較之下，投資普匯債權可能遇到的風險清楚明瞭，若是遇到壞帳我們也會協助您進行訴訟，若是公司不幸倒閉也會結清所有人的款項，確保投資人的權益不受損害！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">接下來我們來看看台灣股市過去10年的指數表現，以下是計算方式：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.以2017年的指數資料回推10年至2008年。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.資金為250萬元，一次性投入，若有盈餘下期會將盈餘與本金繼續投入。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.因為股市投資人進出不定(考量投資人在購買個股時低買高賣、高買低賣或平價賣出都有機率發生)，本算法每年進出一次，十年共計進出十次，並依年K線作為買進賣出參考，並分為下列兩種情形：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>   (a.) 紅K：依(當年開盤價-當年收盤價)*0.6列入盈餘</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>   (b.) 黑K：依(當年開盤價-當年收盤價)*0.6列入虧損</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.得到回收金額並計算投資報酬率</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>實際計算<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img src=\"https://lh6.googleusercontent.com/KknWJxjZS90eqbYlLXO7-bpqt6jiJ0kWIjprXQbHnhNK2bXOyQJeyxc5t1fhCekFiJ9oA3izd-bJ9a37fe-zp6YdnS-JIZjUCA9SABwwt2AIKTZ_jglr_TVXaw5SpuPzHj5FzTqbTAwAFnI0Tg\" alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":3479} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/04/image.png\" alt=\"\" class=\"wp-image-3479\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>第一年 250 * 73.46% = 183.65(萬元)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第二年 183.65 * 143.97% = 264.4</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第三年 264.4 * 105.47% = 278.86</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第四年 278.86 * 86.74% = 241.88</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第五年 241.88* 105.33% = 254.77</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第六年 254.77 * 106.77% = 272.02</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第七年 272.02 * 104.79% = 285.05</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第八年 285.05 * 93.88% = 267.6</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第九年 267.6 * 106.77% = 285.71</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第十年 285.71 * 108.98% = 311.37<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>十年總報酬率 = (311.37 - 250) / 250 = 24.55%</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>年化報酬率 = (1 + 24.55%) ^ (1/10) -1 = 2.22%<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>可以看到十年股市指數的報酬率大約是24.55%，經過年化後每年更只有2.22%，而多數投資人的操作績效不會優於大盤，因此投資報酬率可能會更低。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">再來我們看看普匯10年債權的投資報酬率，計算方式如下：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.每個月投入20000元</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.年利率設定10%，違約率0%(每期都能回收)。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.回款每期持續投入</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.不設定投資上限<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">收益計算方式：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.總回收=自第121期(即不再投入後)後的回款。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.總成本=每期投入的20000原以年利率10%折現到第一期後的加總</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.淨收益=總回收-總成本</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>4.投報率=淨收益/總成本</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>5.年化報酬率以有投入的期間(10年)計算<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>經過計算投資普匯債權的投資報酬率竟然可達近189%！年化報酬率更是高達11.2%！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2><strong>結論</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>大家都知道股市的獲利能力很高，但卻忽略了能在股市中穩定獲利的只有法人和不到1%的散戶，普匯債權採取小額且分散的投資方式，並且盡力解決投資人可能會遇到的各種風險，更重要的是長期持續投入獲利能力十分可觀！心動了嗎？現在就加入我們的會員並開始理財吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><br>點我看案件： <a href=\"https://lend.influxfin.com/welcome\">https://lend.influxfin.com/welcome</a> <a href=\"https://www.pu-hey.com/\"></a><a href=\"https://lend.influxfin.com/welcome\">h</a></p>\n<!-- /wp:paragraph -->', '2019-04-09 17:07:51', '【普匯觀點】普匯10年債權vs台灣股市', 'article', 'publish', '2019-05-08 17:00:03', '', 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(3498, 1, '/upload/2019/04/未命名-4.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">近來金融科技(Fintech)可謂當紅炸子雞，氣勢如虹轟動天下，Fintech不僅僅是技術、科技上的革新，還帶給人們全新的體驗，更貼近人民生活。說到台灣的農產業，它是整個國家經濟活動的基礎，從產地到批發市場，再到承銷人，卻面臨了諸多困難，那傳統農產交易如何運用Fintech技術發展生態金融呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-green-cyan-color\"><strong>台灣食農生態所面臨的現況</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3501,\"width\":587,\"height\":248} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"/upload/2019/04/image-3.png\" alt=\"\" class=\"wp-image-3501\" width=\"587\" height=\"248\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-green-cyan-color\"><strong>人類．飲食．農業</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">Fintech如何應用於生活並造福大眾呢？最顯著的體現就是在「食農生態」上了，農業是人類食物的最重要來源，而飲食活動基本上就是農業活動的一環，因此金融業漸漸意識到生態金融的重要性，紛紛加入改造傳統食農產業的行列，投注了極大的心血，將傳統果菜、漁產、肉品、花卉市場，進行一系列改革，繼而減少錯誤發生率並提高整體效率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-green-cyan-color\"><strong>改革腳步</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">將產值高達1800億的批發市場進行大規模改革是個大工程，需要考慮各面向，包含交易作業電腦化降低錯誤發生率、食材供應過程需要改良、經營模式要升級、承銷人拍賣系統翻新等等。因為農民居於弱勢，資訊不對稱問題嚴重，要解決長期困擾需要從三大方向著手：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>藉由公平、公開方式讓農民了解實際市場價格狀態</li><li>利用自動化取代人力資源，\n降低成本</li><li>協助承銷人融資</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">運用大數據清楚了解承銷人的營運狀況和交易慣性，得知應該以多少額度融資給他們，讓其不用再去使用其他管道調度金錢；對於農產供應鏈，運用大數據發展生態融資模型，提升農產附加價值。一顆普通高麗菜究竟能創造多少利潤？它不僅僅是台灣銷量最好，能儲存最久的葉菜，高麗菜心的抗癌功能更為其添增附加價值。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>inFlux</strong><strong>普匯金融科技–你的手機ATM</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">在食農產業，這些承銷人面臨籌資管道的窘境，受益於金融科技的帶來的革新。而普匯同樣秉持這樣的精神，希望能夠幫助年滿20歲，有借款需求的學生，提供他們一個平台，同時也給予投資人一個能夠定期投資的新選擇，以普惠金融為志業，希望落實傳統銀行無法提供的金融服務，申請方便、簡單操作、核貸快速，真正解決你的急用需要！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3500,\"width\":583,\"height\":261} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"/upload/2019/04/image-2.png\" alt=\"\" class=\"wp-image-3500\" width=\"583\" height=\"261\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>延伸閱讀：<a href=\"https://www.pu-hey.com/%E3%80%90%E6%99%AE%E5%8C%AF%E8%A7%80%E9%BB%9E%E3%80%91%E9%97%9C%E6%96%BC%E5%80%9F%E8%B2%B8%EF%BC%8C%E6%99%AE%E5%8C%AF%E6%98%AF%E4%BD%A0%E6%9C%80%E5%A5%BD%E7%9A%84%E9%81%B8%E6%93%87/\">關於借貸，普匯是你最好的選擇</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>金融科技的真諦：</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">金融科技並不是天上飛的東西，而是能夠實際解決人們需求，達成真正的「普惠」價值。</p>\n<!-- /wp:paragraph -->', '2019-04-12 17:00:23', '【金融趨勢】翻轉食農生態—生態金融', 'article', 'publish', '2019-05-13 09:19:18', '', 0),
(3553, 1, '/upload/2019/04/未命名-2-2.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>「不要把自己做小，勇敢創造新價值」</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>畢業於中興大學法學院。 現為中國政法大學法學博士候選人、IPBA環太平洋律師協會會員以及國際法律事務所主持律師的黃沛聲律師，選科系時因為家中親戚的法律專業背景，決定選擇法律系就讀。「進入法律系就讀後，我發現比起單純操作法律，我更喜歡做生意。」於是黃律師選擇以商業作為執業類別，不只做自己的生意，也發揮所長協助其他人做生意。<br><br></p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>結合商業與法律，跨越前後端火線支援</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「不僅是商業，我特別專精於其中的『投資』。一個公司要把商業做好，商業需要資本，以提高競爭對手的進入門檻，確保自己在市場上的地位。」要取得先行者優勢，或可透過技術、專業，但最重要的莫屬資本，因而需要引入資金，此時就要通盤考量商業性質、數額多寡、需要時間、由外部或是內部作為來源。若將商業律師的業務內容比喻為一場球賽，後端的法律支援便如同守備、前端的商業實務就好比攻擊，一定要了解商業行為本身，才能給予法律支援、協助商業成功。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>後端的支援又可圍繞著從事「商業律師」兩大核心：法庭活動、商業運作，律師只有了解法庭的運作機制，才有辦法先行預測狀況，並在事前合約的訂定預防不利結果，並讓當事人預期的結果透過上述機制實現才能有信服力；前端的商業部分，商業律師處理的便是將商業運作的規則化為文字、寫於明文規定，先行預想順利或不順利的情形分別該如何處理。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>黃律師於受訪時表示，「唯有了解商業，才能幫助商業成功。契約只有一半是法律，另一半都是商業條款，在學期間因為不了解商業而無法協助前端的運作。」以金融業為例，學生沒有辦過信用卡、買過保險或基金，可能只知道定存，這樣便無法將預想可能發生的狀況，不同的商業還會產生不一樣的類別，發展出更多商業形式。「臺灣的法律系學生問題在於『法律太會、商業太不會』，商業律師不懂得報價、蒐證、談判、異業結合、行銷宣傳這些細節，只專注在法律本身，白白流失可能性真的很可惜。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>「我確定」的勇氣，專業的背後往往背負著責任</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>作為一名商業律師，黃律師平日的業務最常擔任的是「法律顧問」，「法律顧問不管是商業或是法律的知識都很重要，要有相當的知識，才有人願意『雇』你來『問』嘛！」黃律師被問及平時業務時笑著表示。由於著重於投資領域，在公司設立時的設立章程的完備、創辦人間權利義務的約定，資金引入時的投資契約、營運時稅務的處理……無不需要對於各領域類別透徹的了解並加以活用，才能累積當事人的信賴。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「一般訴訟律師與商業非訟律師最大的不同，在於處理問題的時點，前者往往是問題發生後的救援，後者是洞燭先機避免後續的問題。」一般人找上訴訟律師，往往事實已成、問題已經發生，訴訟律師的工作就是停損甚至扭轉現局；相較之下，商業非訟律師在做就是「劃版圖」，如同前述的預知狀況、訂於明文預防紛爭。相較於訴訟律師壓力在法庭上的成敗，黃律師表示，非訟律師辛苦的地方便在於說出「我確定」背後的責任，「當事人在我們給完意見或是擬完契約後，常會問我們『這麼做沒問題吧？』『這樣沒風險吼？你確定嗎？』，最困難的就是肯定得做出『我確定』、『這樣沒問題』的判斷並告知當事人。」縱使壓力大，只要有相當的專業，就比較不會有負擔。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>黃律師補充道，專業是透過經驗積累而成，雖然每天都要被電話、Email 追著跑，但因為我喜歡、也從中累積了相當多的經驗，「因為做久了，我現在清楚知道怎麼做就沒有問題，所以敢果決得說出『我確定。』」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>多探索、找到站在巨人的肩膀看世界，拉大自己的視野與格局</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「無論是現在就讀法律系的學生，或是將來想選擇法律相關科系的高中生，都需要深入了解自己喜歡的是什麼。」黃律師認為臺灣的學生很少去思考自己真正所想要的，光是照著分數填志願，不僅可惜了教育資源，無法適得其所也待得很痛苦，「法律系無論訴訟或非訟，任何週邊的法律行為都圍繞著法庭活動，若不喜歡千萬不要勉強自己。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>延伸閱讀：<a href=\"https://www.pu-hey.com/%E3%80%90%E5%90%8D%E4%BA%BA%E5%A0%82%E3%80%91%E7%A2%81%E5%85%83%E6%9C%83%E8%A8%88%E5%B8%AB%E4%BA%8B%E5%8B%99%E6%89%80-%E5%8A%89%E7%A6%8F%E9%81%8B/\">多方發展的 π&nbsp;型人才－劉福運會計師</a> </p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>若決定投入法律領域，接著便是選定希望從事的類別（訴訟或非訟），盡可能把握機會累積相關知識，或能參與實習、課外活動，或是諮詢一個找一個能帶領著見識這些實務知識的人學習，都可以是不錯的方式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「法律系畢業的學生能做的事其實很多，受過訓練的思維到什麼領域都很受用。」法律人無論從事商業的創業、投資、國際貿易，或是投入政治領域當幕僚甚至親自從政，或進入新聞領域成為記者，因為有邏輯思考的訓練，將來的可能性非常多。「不要把自己做小，重點在於成為『你會，別人不會』的那個人。」黃律師在最後勉勵大家盡量拓展視野、勇於嘗試，創造更多有利於多數人的價值、也找到法律人更多可能。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#ae986c\"} -->\n<p style=\"color:#ae986c\" class=\"has-text-color\">採訪出處：THE Baton <a href=\"https://batonproject.com/2017/12/04/%E4%B8%8D%E8%A6%81%E6%8A%8A%E8%87%AA%E5%B7%B1%E5%81%9A%E5%B0%8F%EF%BC%8C%E5%8B%87%E6%95%A2%E5%89%B5%E9%80%A0%E6%96%B0%E5%83%B9%E5%80%BC%E2%94%80%E2%94%80%E9%BB%83%E6%B2%9B%E8%81%B2%E5%BE%8B%E5%B8%AB/\">https://batonproject.com/2017/12/04/%E4%B8%8D%E8%A6%81%E6%8A%8A%E8%87%AA%E5%B7%B1%E5%81%9A%E5%B0%8F%EF%BC%8C%E5%8B%87%E6%95%A2%E5%89%B5%E9%80%A0%E6%96%B0%E5%83%B9%E5%80%BC%E2%94%80%E2%94%80%E9%BB%83%E6%B2%9B%E8%81%B2%E5%BE%8B%E5%B8%AB/</a></p>\n<!-- /wp:paragraph -->', '2019-04-23 12:20:59', '【名人堂】立勤國際法律事務所－黃沛聲：不要把自己做小，勇敢創造新價值', 'article', 'publish', '2019-04-26 12:09:17', '', 0),
(3560, 1, '/upload/2019/04/未命名-3.jpg', NULL, '<!-- wp:paragraph {\"customTextColor\":\"#277bc0\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#277bc0\" class=\"has-text-color has-medium-font-size\"><strong>「</strong><strong>多方接觸發展，不要自我設限，成為具競爭優勢的π </strong><strong>型人才</strong><strong>」</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>碁元會計師事務所 劉福運所長<br>歷任上市公司財務長，創立會計師事務所，為多所大學講師，本著融會貫通的會計專業，加上多重產業背景經驗，結合多家資歷豐富中小企業公司及金融科技平台，以接地氣的方式扶持新創及中小企業。 </p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>劉福運會計師的人生經歷非常豐富有趣，在大學時期他就讀的是新聞系，理所當然畢業後進入報社工作，選擇工商時報證券新聞組記者作為他的第一份工作，隨著工作時間越來越久，漸漸發現新聞工作與他個人的理念及興趣相差甚多，就在劉會計師順利的考上東吳企管碩士後，便毅然決然的辭去記者的工作，轉換跑道，踏入企管的專業領域，為日後的發展奠定下良好的基礎。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>同時， 劉福運會計師還兼顧一份工作，因緣際會之下臨危受命接任當時公司的財務長一職，開啟了人生的另一扇窗，走向他最愛的會計專業。 劉福運會計師的求知欲非常強，他也深知會計是需要花時間去了解的一門學問，因此利用周末的時間去中國科大進修會計，在這期間，學長善用在東吳企管培養的企管人特質：靈活，將死版的會計融會貫通。他說「之前讀的會計都只知道運算過程而不知其原理，但在碩士班與進修班的學習，將會計的來龍去脈都了解得很透徹，幫助我更快吸收，清楚計算邏輯就不用辛苦的死讀書」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>用「熱忱」朝夢想前進</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>想必很多人都很好奇到底是怎樣的動力促使他不斷學習，他的答案很有趣，「愛讀會計猶如愛讀金庸，非要看到最後結果為止」，其實靠的無非就是過人的毅力與熱忱，還有追求「背後理由」的企管人特質，常研讀會計到三更半夜也不覺得累，會計對他的吸引力不言可喻。他非常嚮往成為一名會計師，更笑說「想像會計師的生活就是我繼續前進的動力」，他不僅有夢想，更努力將夢想實踐，經過多年的歷練，順利考取執照，當上夢想中的會計師，「這絕對是我人生的最大的意外驚喜」提到這段故事，臉上不由自主露出興奮的表情。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>給年輕人的建議</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在財務會計方面，劉會計師有很多他的學習心得，在工作繁忙之餘更投入教職，先後在東吳大學及中國科大教書，將財務的專業帶給莘莘學子，同時以他如此具說服力的人生經驗鼓勵更多學生學習會計專業。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>學生時期應培養的能力：</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1. 簡報力，作簡報對企管的同學來說已能駕輕就熟，即便技巧不是頂尖優秀但也能完整表達，反觀會計系的同學，這部分就相對弱，但簡報能力在企業內是時常需要用到的。\n</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2. 溝通力，會計出身的同學多數能掌握專業，但卻不能與同事、主管達到良好的溝通，而企管系的同學\n在這方面就比較常被訓練而做得比較好。\n</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3. 企管的同學對事情背後的原因也較積極去找答案，\n了解脈絡能幫助快速學習吸收。\n</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>延伸閱讀： <a href=\"https://www.pu-hey.com/%E3%80%90%E5%90%8D%E4%BA%BA%E5%A0%82%E3%80%91-ceo-%E8%A8%AA%E5%95%8F/\">普匯CEO專訪：路走對，就不怕遠了</a><br><br></p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p>最後，劉會計師鼓勵學弟妹不要只有一項專業， 應該多方接觸發展，不要自我設限，成為具競爭 優勢的「π 型人才」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#a39286\"} -->\n<p style=\"color:#a39286\" class=\"has-text-color\">採訪出處 :<a href=\"http://www2.scu.edu.tw/ba/alumni/mag/33/33-22%2023.pdf\">http://www2.scu.edu.tw/ba/alumni/mag/33/33-22%2023.pdf</a></p>\n<!-- /wp:paragraph -->', '2019-04-23 12:28:32', '【名人堂】碁元會計師事務所－劉福運：多方接觸發展，不自我設限，成為π 型人才', 'article', 'publish', '2019-04-26 12:08:47', '', 0),
(3606, 1, '/upload/2019/04/image-5.png', NULL, '<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">過去十年裡，世界各國的私營部門和政府債務都大幅增加，全球經濟面臨著科技變革和全球化帶來的一系列複雜挑戰，以及2008-2009年的金融危機在持續產生影響，這些都可能會破壞全球經濟的秩序。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>金融危機後，各國政府實行低利率的量化寬鬆政策，助漲政府部門債務規模</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">在美國，企業債務占GDP的比重正處於歷史最高水準；在歐洲部分國家，銀行過度持有政府債券；在中國，銀行盈利水准正在下降，中小銀行資本充足率仍然較低。根據國際貨幣基金組織（IMF）最新一期「全球金融穩定報告」，類似這樣的風險點在發達經濟體和新興市場經濟體都在新增。雖然還未觸發警報，但若繼續積累，尤其是在依舊寬鬆的金融條件之下，可能放大對全球經濟的衝擊效應，未來幾年全球經濟可能會出現嚴重下滑。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">在發達經濟體，企業債務上升，金融冒險行為新增，借款人信用惡化。自危機以來，美國和歐元區BBB評級的債券存量新增了三倍，投資級信貸存量新增了近一倍。若金融環境急劇收緊或經濟嚴重下滑，負債的公司可能更難以償還貸款，從而被迫减少投資或裁員，杠杆化貸款——向負債率較高的借款人放款的行為尤其值得擔憂。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">圖1&nbsp;&nbsp;&nbsp;評級為BBB（最低級）的投資級企業債佔總量的比重</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3628} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/04/image-5.png\" alt=\"\" class=\"wp-image-3628\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">數據來源：ICE債券指數、國際貨幣基金組織</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">&nbsp;十年前的金融浩劫還歷歷在目，全球數十萬億美元資產瞬間消失，現今持續擴大的債務帶來了新的風險，IMF認為現在應該謹慎對待債務：“多年來極低的利率，資產價格波動性風險的累積，以及金融系統的脆弱性，全球經濟增長面臨風險”。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>全球經濟活動在加速，但經濟擴張卻顯著減弱</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">一年前，全球幾乎所有地區的經濟活動都在加速，但一年後發生了很多變化，比如中美貿易緊張局勢升級，中國採取必要的信貸收緊政策，阿根廷和土耳其的總體經濟承受壓力，德國汽車產業出現中斷，大型發達經濟體貨幣政策正常化過程中出現收緊的趨勢，這些因素共同導致了全球經濟擴張的顯著减弱，尤其是2018年下半年尤為嚴重。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">鑒於這種疲弱態勢預計將延續至2019年上半年，IMF預測70%的全球經濟活動在2019年將減緩增長速度。全球增長在2018年放緩至3.6%，預計2019年將進一步降至3.3%。現在對2019年的增長預測相對於今年1月的預測下調了0.2個百分點，反映了一些主要經濟體（包括拉丁美洲、美國、英國、加拿大和澳大利亞）增長速度減緩的現狀。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">全球經濟正處於微妙時刻，前車覆，後車戒，如何减少可能面臨的損失將是未來每個人都要考慮的事情，在危機來臨之前選擇適合自己的投資、融資方式，利用先進的Fintech協助避險，是不二之選。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">普匯金融科技提供P2P金融服務，投資人出借閒置資金並獲得報酬，而借款人也能在平台的媒合下獲得資金，不僅提高社會資金利用率，也可以通過AI算法區分出信用良好、穩定還款的借款人，帶給投資人透明、穩定、高額的回報率，成為經濟下行時最令人安心的投資方式之一。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">金融危機的負面效應會從方方面面影響著所有人，傳統的投資標的價格往往會被“腰斬”，與其提心吊膽得查詢債券、基金、股票、衍生性金融商品的價格，不如轉換投資策略，不再拘泥於老舊的模式和方法，採用普匯金融科技提供的優質服務和先進技術，讓投資回報不再受制於宏觀經濟環境限制，一同面對經濟環境的不斷波動，準備好迎接即將到來的風暴。</p>\n<!-- /wp:paragraph -->', '2019-04-26 17:00:13', '【普匯觀點】全球金融危機又要來了嗎？IMF稱正處於微妙時刻', 'article', 'publish', '2019-05-08 16:58:13', '', 0),
(3662, 1, '/upload/2019/04/moneyholding-1.jpg', NULL, '<!-- wp:paragraph -->\n<p>無論是剛剛入學的學生，還是已經步入職場的白領，拿到獎學金和薪水的那一刻都是最幸福的，但隨即面臨一個問題：這麼一大筆現金該如何利用？除了犒賞自己之外，如何選擇投資標的享受“錢生錢”的樂趣？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>投資的選擇，最重要的當然是報酬率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>千萬不要小看投資報酬1%的差距，隨著時間的推移，長期複利下來總體報酬會相差非常多。普匯金融科技整理了最常見的幾種投資商品，包括一般銀行售賣的理財投資商品，還包括房地產、黃金等，一起來看看吧！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>1、銀行定存</h2>\n<!-- /wp:heading -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td><strong>投資商品類型</strong></td><td><strong>報酬率</strong></td></tr><tr><td>銀行–&nbsp;活期存款</td><td>0.2%</td></tr><tr><td>銀行–&nbsp;定期存款</td><td>1.0%</td></tr><tr><td>銀行–&nbsp;外幣定存(美金)</td><td>2.0%-2.5%</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>可以看到銀行的存款利率都比較低，如果一直把剩餘資金存在銀行中，長期下來會損失很多利息收入，因此除了少數生活費用以外，多的錢不要存入活期存款，而是盡可能只保留緊急預備金，其他的用在定存或投資，還有很多其他安全又高回報的投資工具可以選擇。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>另外，外幣定存報酬率雖然比較高，但匯率波動會極大影響報酬率，對於外匯不敏感的人群，或者沒時間關注外匯變化的人群，不推薦採用外幣定存的方式投資。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>2.債券投資</h2>\n<!-- /wp:heading -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td><strong>投資商品類型</strong></td><td><strong>報酬率</strong></td></tr><tr><td>債券&nbsp;–&nbsp;台灣10年期公債殖利率</td><td>0.85%</td></tr><tr><td>債券&nbsp;–&nbsp;美國10年期公債殖利率</td><td>2.9%</td></tr><tr><td>ETF –&nbsp;美國投資等級債券</td><td>3.6%</td></tr><tr><td>ETF –&nbsp;美債7-10年</td><td>4.0%</td></tr><tr><td>ETF –&nbsp;新興市場高收益債</td><td>4.3%</td></tr><tr><td>ETF –&nbsp;美債20+年(長期債)</td><td>6.0%</td></tr><tr><td>ETF –&nbsp;美國高收益公司債</td><td>6.2%</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>債券大多有固定的報酬率並且非常不易傷到本金，但是台灣債市交易量小，不太活躍，而且台灣的公債報酬率也很低，並不推薦投資。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有另一種跟債券相關的投資商品，也是當下很熱門的投資標的——債券型ETF，債券ETF簡單來說，就是一檔債券型基金，差別在於債券ETF是被動式的操作，經理人不做主觀決策，只根據事先決定好的規則特定的規則做進出買賣債券，並且可以用股票帳戶做交易買進，交易成本會比買基金低一些。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>但債券也會有損失的風險，收益越高的ETF，其違約風險也越高，例如新興市場、高收益債等。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>3.&nbsp;股票投資</h2>\n<!-- /wp:heading -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td><strong>投資商品類型</strong></td><td><strong>報酬率</strong></td></tr><tr><td>股票&nbsp;–&nbsp;台股中小型股</td><td>3.6%</td></tr><tr><td>股票&nbsp;–&nbsp;台股加權指數</td><td>9.5%</td></tr><tr><td>股神巴菲特報酬率(10年)</td><td>10.3%</td></tr><tr><td>股票&nbsp;– S&amp;P500指數</td><td>10.8%</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>股票投資是最有機會創造高報酬，但也最有可能讓人虧損的一種方式，</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>可以直接買進不同股票，持有一個股票投資組合。但缺點也很明顯，需要專業的知識、對市場靈敏的感覺、時刻盯盤的專注以及天賦。對於普通人來說並不是非常好的投資標的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>4.&nbsp;房地產</h2>\n<!-- /wp:heading -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td><strong>投資商品類型</strong></td><td><strong>報酬率</strong></td></tr><tr><td>房地產–&nbsp;台灣租金報酬率</td><td>1.0%-2.0%</td></tr><tr><td>房地產–&nbsp;房地產指數</td><td>2.4%</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>房地產分成賺租金或賺價差，台灣房價租金比目前是全球最高，房子很貴，好的投資機會需要花很多成本搜尋，如果只是單純收租金的話，每年報酬率跟銀行定期存款相似。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>5.&nbsp;其他常見投資標的(儲蓄險、黃金)</h2>\n<!-- /wp:heading -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>投<strong>資商品類型</strong></td><td><strong>報酬率</strong></td></tr><tr><td>儲蓄險</td><td>2.0%-3.5%</td></tr><tr><td>商品–&nbsp;黃金</td><td>3.5%-4%</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>儲蓄險可以理解為保險公司拿你的錢去買債券，差別在於儲蓄險是固定配息並且可拿回本金，但你買債券基金或債券ETF則是淨值會波動。儲蓄險作為投資標的，優點是沒風險，缺點是綁很久，低於6年幾乎報酬率很難超過2~3%以上。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>看完這些常見的投資商品，是不是覺得很多感覺上很賺錢的商品，其實並不是一直可以帶來穩定回報，尤其是對於時間少、工作壓力大、課業重的白領、學生一族，不妨考慮P2P這一投資方式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>作為撮合個人借貸的網路平台，省去了中間銀行的手續費，借款人的貸款利率更低，投資者的報酬率更高。對於投資人來說，不僅可以獲得穩定現金流，而且可以自由選擇投資標的，最高年利率接近20%，最低也有5%，如此省心省力的新方案，就在普匯金融科技。</p>\n<!-- /wp:paragraph -->', '2019-04-29 17:21:15', '【普匯觀點】你知道最賺錢的產品是什麼嗎？  5分鐘看懂各種投資商品報酬率', 'article', 'publish', '2019-05-08 16:57:42', 'investtonic', 0),
(3677, 1, '/upload/2019/04/普匯進駐-1.jpg', NULL, '<!-- wp:paragraph -->\n<p>普匯金融將於5月份進駐「金融科技創新園區FinTechSpace」，與其他8家新創團隊一同為金融科技注入活水，其中包含睿元國際、雲飛傳媒、雲想科技、博歐科技、塞特科技、全城地產、Formosa Financial及Caulis。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>領域涵蓋P2P網路借貸、資訊整合平台、支付、身分認證、資訊安全、智能理財與區塊鏈等，除了享有實體空間資源外，將藉由一站式的輔導接軌市場，協助新創於初期發展階段奠定堅實的基礎，逐步朝市場落地發展。目前共有77家進駐單位，包含來自美國、日本、新加坡、菲律賓、香港及瑞士等國際團隊。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>關於FinTechSpace</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>台灣第一座「金融科技創新園區FinTechSpace」於2018年9月18日開幕，這是國內首個金融科技實體共創空間，由金管會主導、金融總會主辦。金管會主委顧立雄表示，金融科技園區將打造一條龍式的金融科技新創服務，鼓勵新創團隊研發創新的金融科技應用。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>FinTechSpace涵蓋給新創團隊的兩大重點服務，分別是監理門診與數位沙盒，未來園區內將有監理人員常駐，建立新創團隊與政府非正式交流的平台，讓新創團隊不論就何種金融科技主題都可以「問到飽」；此外在園區內也會鼓勵金融機構提供相關數據、和金融服務模組API等資源共享共用，加速新創產品的推出。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>前行政院副院長施俊吉表示，在電子商務及Fintech的時代，數位經濟發展與金融市場密不可分，金融市場若能結合Fintech推出金融服務，將可帶動各個產業結構性的轉變。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>去年行政院通過金管會提出的「金融發展行動方案」，即欲運用金融科技作為關鍵策略，達到金融攜手產業、結合科技創新、進軍國際市場、普惠金融服務四大目標，而FinTechSpace的建置就是落實行動方案的具體措施。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>進駐金融科技園區</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯將於五月開始進駐園區，成為金管會輔導之新創團隊，未來將整合更多資源，也期待和其他優秀團隊共同交流，持續創新成長！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">中時報導： <a href=\"http://chinatim.es/DXP2H\">http://chinatim.es/DXP2H </a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯金融科技：<a href=\"https://www.pu-hey.com/\">https://www.pu-hey.com/</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', '2019-04-30 15:01:28', '普匯進駐金融科技創新園區－帶給你完整體驗', 'article', 'publish', '2019-04-30 15:51:27', '', 0),
(3701, 1, '/upload/2019/05/bitcoin-3732876_1920.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">還記得幾年前火紅的區塊鏈嗎？比特幣、以太幣和瑞波幣等加密貨幣的出現，全球的「礦工們」蓄勢待發，欲奪得先機。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0687e5\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0687e5\" class=\"has-text-color has-medium-font-size\"><strong>物以稀為貴</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">當很多人都想擁有這些加密貨幣，在市場供不應求的情況下，價格飛漲，如果直接購買價格昂貴實在吃不消，這時只好自己動手挖掘了。許多看好虛擬貨幣商機的人們加入挖礦大隊，投入大量硬體及運作成本，之後甚至出現「租借市場」，足見當時熱度破表。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0687e5\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0687e5\" class=\"has-text-color has-medium-font-size\"><strong>ICO</strong><strong>的出現–騙局說？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">ICO的全稱為(Initial Coin Offering)，我們稱為「首次代幣發行」，他的概念承襲自我們所熟悉的IPO(Initial Public Offering)：首次公開發行股票，所以ICO可說是區塊鏈版的IPO。此外ICO發行團隊會發布ICO白皮書，讓市場了解融資計畫與情形。但事情真的如想像中順利美好嗎？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0687e5\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0687e5\" class=\"has-text-color has-medium-font-size\"><strong>帶你快速識破ICO詐騙手段：</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>保證投資報酬</li><li>高度保證會退款</li><li>可以用信用卡付款</li><li>名人背書</li><li>獲得證管會批准</li><li>早鳥優惠價</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0687e5\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0687e5\" class=\"has-text-color has-medium-font-size\"><strong>ICO</strong><strong>的混亂與不確定性</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">根據ICO顧問公司統計，將近80%的案件是詐騙，另外4%募資失敗、3%計畫胎死腹中，僅有13%成功上市交易。詐騙、違法吸金、洗錢案件層出不窮，原本前景看好，為人吹捧的ICO，出現泡沫化問題，聲勢也直直落。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0687e5\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0687e5\" class=\"has-text-color has-medium-font-size\"><strong>代表國家監管態度</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>英美</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">今年英國金融行為監管局(FCA)對加密貨幣做出明確定義，以及適用的相關條例，並將其分為三大類，分別是交易型、證券型、功能型代幣，依據各種用途進行分類，從交易工具、投資到ICO募資做劃分，並嚴格調查各公司加密貨幣使用情形。目前英國市場還未真正壯大，FCA預見加密貨幣可能走向而進行嚴謹審查，以協助金融健康發展。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">至於美國態度認為ICO幾乎都有被稱為證券的資格，應該按照證券法的規則走，同時表達並不會讓ICO走註冊登記模式。此外大力宣導並推出Howey Test和虛擬ICO網站，就是為了防止民眾因資訊不對稱受騙，同時也嚴格管制ICO案件，將不合規定的案子進行法律訴訟程序。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>日本</strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">日本可謂加密貨幣重鎮，是最早階段認可比特幣合法化的國家之一，在監管方面也加強洗錢防制和投資人保護。雖然加密貨幣在日本法律上被認為是合法支付工具，但仍不具有法幣地位，而是將其歸類為加密資產，希望不要誤導民眾加密貨幣是由政府發行的法幣，。關於ICO管制可能透過修法方式加強監管，包含募資上限、商業計劃書等規定，目前並未說明具體方針，但整體監管是持相對開放態度。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><strong>大陸</strong><strong></strong></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">大陸曾是加密貨幣重要參與者，擁有廣大的人口和交易市場，原是期待欣欣向榮景況，然而違法洗錢、詐騙等層出不窮，近來以精進區塊鏈技術為主要方向。雖然大陸人民可以持有加密貨幣，但禁止進行匯兌、買賣等交易，大陸監管機構不認可加密貨幣具法幣和支付功能。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0687e5\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0687e5\" class=\"has-text-color has-medium-font-size\"><strong>台灣怎麼做？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">多數國家對ICO監管仍處於未定狀態，台灣早前並沒有實施明確法律規範。近期台灣金融監督管理委員會對於ICO管制的態度漸明朗，主委顧立雄表示：「ICO 涉有價證券募資與發行，管定了！」因為許多ICO以私募方式進行，有灰色地帶，有時難以控管，無法確保投資人權益。然而有些加密貨幣歸類為資產性質，很可能被劃分在管理範圍之外，台灣目前尚未提出有系統的完整政策，其實現今世界概況多半是「進行式」的監管狀態，法律難以快速因應區塊鏈的革新速度，甚至是整個金融科技發展。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">面臨加密貨幣改革大動亂的時代，許多投資人多年的心血可能一夕間葬送，普匯金融科技P2P平台讓你高枕無憂，我們提供你穩定且安心的環境，能夠做定期定額投資賺取報酬，每月小額投資為自己創造高品質生活。普匯不易受市場變動影響的特質，給你既安全又有保障的生活。歡迎你加入普匯大家庭，一起創造被動收入，使你提早達成財富自由。</p>\n<!-- /wp:paragraph -->', '2019-05-03 17:26:13', '【FinTech】剪不斷理還亂–ICO何去何從？', 'article', 'publish', '2019-05-16 22:01:17', '', 0),
(3708, 1, '/upload/2019/05/adeolu-eletu-38649-unsplash.jpg', NULL, '<!-- wp:paragraph {\"customTextColor\":\"#090909\"} -->\n<p style=\"color:#090909\" class=\"has-text-color\">5月初，各家主流金融科技企業的年報陸續出來了，從營收來看，P2P平臺兩極分化十分嚴重。一方面是360金融、樂信們以助貸為主營的企業財報亮眼，利潤增幅分別高達近十倍以及逾七倍；另一方面是宜人貸、投哪網等還在堅守P2P業務的頭部平臺在連續數年業績穩步提升後，出現了利潤的下滑。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\",\"backgroundColor\":\"vivid-cyan-blue\"} -->\n<p style=\"color:#000000\" class=\"has-text-color has-background has-vivid-cyan-blue-background-color\"><strong>理性看待</strong><strong>P2P</strong><strong>平臺的利潤增長</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">業績暴增的平臺有兩種類型，一種是“破罐子破摔”型，另一種是“積極轉型”。是因為去年6月份備案落空後，在下半年形成一股清退中小平臺之風，而中小平臺卻索性你死我活，已經完全放弃合規衝刺備案，把底層資產來了個大洗澡，把資金池對接暴利的超利貸資產。這種不受監管的平臺風險極大，隨時有暴雷的可能。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">而另一種業績增長的類型，可以視為積極轉型助貸業務的典範，比如360金融，其2018年第四季度撮合的貸款資金中有78%來自金融機構，樂信有近70%的新增借款來自金融機構，拍拍貸的機构資金占比達到20.4%。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">然而也有聲音質疑助貸業務，在P2P行業，一般認為助貸並非一個特別吸引人的買賣，尤其是近兩年銀保監會對於銀行助貸業務密集規範後。把精力放在類似業務上是P2P在“雙降”乃至“三降”要求下無奈的選擇，機构資金提供方相比較個人散戶而言，屬於典型的給錢很難、審批流程繁瑣、增信措施要求多，而考慮到增信成本後實際的機构資金成本可能又不會比線上零售資金便宜。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">轉型助貸業務的P2P平臺短期利潤無疑亮眼，然而帳面利潤可能虛高了：在早期的跑馬圈地中，助貸業務的利潤能够很快體現出來，但最後往往需要助貸推薦方背負的壞賬還沒有真實暴露出來。這種業務也具有相當程度的收益前置，風險後置特徵。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#010b0f\",\"backgroundColor\":\"vivid-cyan-blue\"} -->\n<p style=\"color:#010b0f\" class=\"has-text-color has-background has-vivid-cyan-blue-background-color\"><strong>認清P2P平臺的利潤下降原因</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">為何利潤會下降？主要有兩點：1、利潤下滑是因為監管政策大環境的問題；2、是具體業務經營上的問題。諸如投哪網這樣的平臺在過去幾年的利潤表現一直還算比較穩健，由此獲得了包括招商致遠、廣發信德、巨加網絡等實力股東的青睞。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">然而在“雙降”壓力下，2017年8月份高峰期的90.26億借貸餘額下降到2018年12月底的53.69億，規模壓縮幅度高達40%以上，所以導致利潤大幅下降。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">作為撮合出借人和借款人的資訊仲介平臺，P2P應當保持合理的利潤，但不應當是暴利行業。美國諸如Lending Club的興起並成功獲得監管背書，很重要的原因在於房地產次級貸款危機後社會各階層對於傳統金融體系很有意見，金融消費者在支付那麼高的仲介成本的同時還要承受這麼嚴重的金融機構道德風險損失。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">P2P在美國次貸危機後獲得西方社會的擁護正是因為它降低了仲介成本和仲介道德。而亞洲P2P也應該在持續降低出借人和借款人之間金融摩擦成本的基礎上體現自身的長遠商業價值，本身不應該過於追逐短期的商業利益。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\"} -->\n<p style=\"color:#000000\" class=\"has-text-color\">平臺利潤過高要不然是因為出借人未能獲得本應該獲得的出借收益，要不然意味著P2P平臺撮合服務的借款人本身風險較大、承擔的綜合借款遠高於出借人所獲取的收益。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#020a0d\"} -->\n<p style=\"color:#020a0d\" class=\"has-text-color\">普匯金融科技屬於P2P金融服務平臺，希望落實傳統銀行無法提供的金融服務，核心是金融服務，以滿足借款人和投資人需求為主要任務，並運用AI智慧科技與安全風控模組，透明公開得將專業金融產品透過逐筆撮合的模式搭起投資人與借款人的橋樑，極盡最大努力保護平台使用者的權益，讓你2019不再「難過」。</p>\n<!-- /wp:paragraph -->', '2019-05-08 17:00:25', '【普匯觀點】2018過得好嗎？P2P平臺兩極分化', 'article', 'publish', '2019-05-08 16:56:47', '', 0),
(3790, 1, '/upload/2019/05/oil.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">頁岩油開採議題在近幾年討論得沸沸揚揚，一直以來各產油國積極尋找接替能源，有些地區的石油儲藏量漸不足以供應未來的需求，渴望舒緩石油浩劫危機。根據美國統計，全球頁岩油蘊藏量甚至遠超越石油儲量，許多國家如俄羅斯、美國、中國、阿根廷、利比亞、澳洲、阿爾及利亞等都將是頁岩油開採重地。由於油頁岩屬成分複雜且為薄片狀層理的沉積岩，原油藏於緊密的岩層間，開採成本高昂，又難以在開採技術進行突破，故大部分持觀望態度。自從美國德州成功突破開採，轟動國際，開啟了頁岩油革命之路，帶來的不僅僅是經濟上的轉變，還是一場政治革命。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>頁岩油打破市場秩序，OPEC</strong><strong>繃緊神經</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">石油的交易進出口額，影響全球經濟脈動，而頁岩油產量的增加，撼動了國際貿易的流向。當美國透過水平鑽探技術及水力壓裂法，宣布頁岩油開採新技術，此震驚消息帶來的效應也快速反應在油價上，成為2014年國際油價崩跌的主因之一。    (見圖一WTI)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">長年來石油輸出國組織(OPEC)和產油大國俄羅斯合作，藉由縮減石油供應量以平衡市場機制，欲使油價回復預期水平。然而美國不斷提高頁岩油產量，甚至從以往的進口能源大國轉變為淨出口國，造成油價下行壓力，這是OPEC所不樂見的情況。近幾年也有輿論說美國聲勢不如以往，出現“美國衰落”一說，而美國似乎透過行動回擊，躍居成為產油霸主，再次拿回主動權。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>圖一</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3792,\"width\":457,\"height\":281} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"/upload/2019/05/image.png\" alt=\"\" class=\"wp-image-3792\" width=\"457\" height=\"281\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">資料來源：西德州原油期貨West Texas Intermediate (WTI)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>經濟政治關係</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">頁岩油革命所影響的層面包含經濟、政治、外交，和心理層面，美國可謂如虎添翼，在經濟發展方面，除了促進就業機會、平衡收支改善逆差問題、新稅收來源擴增，還有因技術革新而降低的開採成本，使得石油化工、鋁、鋼等能源密集性產業受惠，進而提高國際競爭力。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">至於政治影響更甚，美國近期透過石油制裁伊朗的核武計畫，也削弱委內瑞拉利用石油拉攏聯合國和小型國家的選票，油價重創也造成委內瑞拉經濟崩盤，內憂外患雙面夾擊下，要如何重新振作仍有待觀察；再來是削弱俄羅斯常威脅切斷供應迫使鄰國就範的能力。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>未來動向</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">根據國際能源署(IEA)估計，到2025年為止，全球大部分的原油和天然氣將由美國貢獻，未來五年，可能有將近66%～70%的產量來自美國。但2025年之後，美國頁岩油出口活絡度可能漸減，主動權將再度變化，可能回到OPEC手中，由OPEC滿足全球原油需求。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">未來趨勢多變，各國也自有盤算，加上綠色能源抬頭，將來會如何轉變？會不會衝擊整個經濟體？或許能夠顛覆依靠原油的產業生產模式與動力來源，大大翻轉舊有石油市場，諸多科技技術革新勢必對我們的未來造成革命性的改變。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">現今數據化、科技化的時代，許多產業面臨轉型衝擊，在「適者生存、不適者淘汰」如此競爭的世代，需要不斷革新與突破跟上腳步。金融同樣也面臨BANK 4.0金融常在，銀行不再的挑戰，普匯看見了市場的變化和走向，投入P2P網路借貸市場，服務銀行無法觸及的族群，以「普惠」金融為目標，打造貼心的客戶體驗。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">在全球政經局勢不穩定的情況下，如何穩紮穩打前進？面臨股市走向的不確定性，如何穩定獲利？普匯將成為你的好夥伴，只需要定期定額，進行小額投資，不怕血本無歸，給你高品質的生活。 </p>\n<!-- /wp:paragraph -->', '2019-05-10 17:32:12', '【金融時事】頁岩油革命—主動權爭奪戰', 'article', 'publish', '2019-05-13 09:17:47', '', 0),
(3805, 1, '/upload/2019/05/未命名2.png', NULL, '<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>受美國經濟持續走強的鼓舞，加之被中國的做法所激怒，不滿足於達成一項軟弱的協定，川普總統準備延長持續數月的貿易戰，並威脅要對價值2000億美元的中國商品新增關稅，從10%提高至25%，且擬另對價值3250億美元、尚未被加稅的中國產品，施以懲罰性關稅。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此後週一全球股市應聲下跌，投資者擔心貿易戰可能會無限期延長。股市開盤後大幅下跌，尤其是中國的滬深兩市，A股500多家公司宣告跌停鎖住，蒸發近3萬億市值。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>而後中國商務部週二下午宣佈，副總理劉鶴將與中國官員代表團一同前往華府，他們在週四和週五與特朗普政府展開進一步談判。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\",\"backgroundColor\":\"vivid-cyan-blue\"} -->\n<p style=\"color:#000000\" class=\"has-text-color has-background has-vivid-cyan-blue-background-color\"><strong>美股為何沒有大跌？</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>最新貿易戰火的影響，對華爾街而言，似乎稍縱即逝。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>高盛報告稱，想執行全面加稅，至少需要幾個月的時間，因為包含徵求公眾意見在內，必須先完成一整套正式流程，「我們認為，到那個時候，美中可能已經達成協議了。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>美股週一一開盤，道瓊指數暴跌417點，但旋即回升反彈，最終收盤時僅下跌66點，標準普爾500指數和納斯達克也火速反彈，僅下跌0.5%。從總體數字來看，關稅並沒有打擊美國太多，4月就業報告出爐，失業率降至3.6%，是近50年新低，第一季的經濟成長也從2018年底反彈，經季節性調整後，年化成長率達到3.2%，高於上一季度的2.2%。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>不確定性增加，但市場依然驚人的樂觀。雖然美國也有不少抱怨的聲音，部分行業的確感受到壓力了，例如中國對美國農產品課徵高關稅，並且停止購買大豆等美國主要出口品，美國的農業收入一直徘徊在10年前經濟衰退的最低水準。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#000000\",\"backgroundColor\":\"vivid-cyan-blue\"} -->\n<p style=\"color:#000000\" class=\"has-text-color has-background has-vivid-cyan-blue-background-color\"><strong>人民幣匯率貶值風險明顯消退，震盪行情難改</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>瑞銀財富管理評論指出，中美貿易談判破局的可能性仍不能排除，這種情景一旦發生，可能引發美國和中國股市大跌，兼且人民幣兌美元匯率跌破7.0大關。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「我們一直提請投資者注意，中美談判破局的可能性仍不能排除，我們認為這種風險情景發生的概率為30%。」該行CIO並稱，「在美國為明年的大選熱身之際，倘若雙方最近的交鋒導致談判停滯以及關稅提高，可能引發全球風險資產進一步下跌。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>瑞銀財富管理預測：在此風險情景下，美國企業獲利將衰退5%，中國股票則可能挫跌15-20%。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>全球經濟都會受到貿易戰的影響，無論是股市還是實體經濟，都將面臨一系列衝擊，除了要靈活運用期貨等衍生性商品避險之外，個人資產也要做好避險措施，儘量多的分散投資，不把雞蛋放在一個籃子裡。採用普匯金融科技提供的優質服務和先進技術，線上撮合交易，借款人輕鬆取得資金，投資者擁有「睡後收入」，不管川普説什麼，收益率都不受其影響。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\"><em>「睡後收入」——指睡一覺醒來就有的收入，投資人不用去做些什麼，收入就會持續增加，我們平時上班工作賺的錢，都叫做「主動收入」，一旦你停止幹活，收入也隨即停止了，而“睡後收入”屬於我們常說的「被動收入」。</em></p>\n<!-- /wp:paragraph -->', '2019-05-15 17:28:03', '【時事分享】川普準備延長貿易戰，匯率恐受影響', 'article', 'publish', '2019-05-17 16:56:03', '', 0),
(3826, 1, '/upload/2019/05/金管會開放.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">金管會5/14宣布將擴大開放銀行線上業務範圍，新增10項業務項目，包括新戶就可以直接在線上辦貸款，不用跑銀行就可以借到錢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>金管會開放銀行線上授信業務</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">金管會銀行局副局長莊琇媛表示，為因應金融科技快速發展，在符合身分確認機制及安全控管措施下，將新增10項可以在線上申辦的業務項目。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">其中授信業務新增六項，包括新戶線上成立貸款契約、線上取得企業戶同意查詢聯徵中心信用資料等。由於線上個人信貸業務快速成長，到今年3月底，已有5,140億元，這次「新戶線上成立貸款契約」的開放，最受關注。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">依規定，現在可以辦線上貸款的人，限於銀行既有存款戶、貸款戶、信用卡客戶，這次開放則是不限既有客戶，新戶也可以在線上申貸。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>線上核貸應嚴謹</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">線上就可申請貸款，銀行會否過度衝線上貸款業務，尤其是對年輕人宣稱幾分鐘「馬上貸」？莊琇媛表示，銀行徵授信審核不能少，透過線上提供證件、財力證明等，金管會也會觀察授信品質變化。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">另外線上個人信貸餘額5,000多億元，是從2015年開放至今才有到5,000億元，目前銀行整體授信品質還可以，未來對於線上核貸業務，可以考慮從一般授信中區分出來，觀察逾放比率等授信品質變化。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">這波線上金融業務的擴大，主要也是因應未來純網銀的開放，對銀行來說，商機最大的應是授信業務部分，可替銀行帶進收入。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯金融在借貸市場搶占先機</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">政府積極態度，金管會的大力支持，使線上借貸業務更加開放且蓬勃。普匯金融已在P2P網路借貸穩健開展，並已累積可觀的客戶量，我們領先於金融機構，提供服務給傳統銀行無法觸及的客群，協助其資金周轉，且效果顯著。從政府的態度走向來看，普匯已先在借貸市場站穩腳跟，不需要透過銀行中介，我們實現真正的直接金融服務！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>#金管會 #聯合新聞網</p>\n<!-- /wp:paragraph -->', '2019-05-16 17:36:07', '不用跑銀行就可借到錢 金管會將開放銀行線上業務 -普匯早已準備好了', 'article', 'publish', '2019-05-20 09:20:42', '', 0),
(3836, 1, '/upload/2019/05/trust1.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">金管會宣布將擴大開放銀行線上業務範圍，新增10項業務項目，包括新戶就可以直接在線上辦貸款，不用跑銀行就可以借到錢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>開放銀行線上授信業務</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">金管會為因應金融科技快速發展，在符合身分確認機制及安全控管措施下，將新增10項可以在線上申辦的業務項目，其中授信業務新增六項，包含新戶線上成立貸款契約、線上取得企業戶同意查詢聯徵中心信用資料等。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">依規定，目前可辦理線上貸款的人，限於銀行既有存款戶、貸款戶、信用卡客戶，這次開放則是不限既有客戶，新戶也可以在線上申貸。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>金管會密切注意銀行授信審核</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">線上就可申請貸款，銀行會否過度衝線上貸款業務，尤其是對年輕人宣稱幾分鐘「馬上貸」？金管會銀行局副局長莊琇媛表示，銀行徵授信審核不能少，透過線上提供證件、財力證明等，金管會也會觀察授信品質變化。未來對於線上核貸業務，可以考慮從一般授信中區分出來，觀察逾放比率等授信品質變化。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯金融導入AI</strong><strong>、區塊鏈技術 </strong><strong>審核嚴謹且快速 </strong><strong>又不怕個資外洩</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">普匯致力於P2P網路借貸且發展體質良好，目前會員數已逾20,000人，成交金額超過台幣三千萬元。我們透過AI人工智慧、區塊鏈先進技術，不僅帳號申請快速，風控審核也嚴謹，核貸速度快且準確撮合交易。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">此外運用高端生物辨識科技，保護個資隱私，導入人工智能交叉驗證風控系統，採用區塊鏈智能合約自動精準地執行每筆交易。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">我們秉持金融專業，堅持 「實現普惠金融」，提供你比一般銀行更完善、快速、嚴謹的服務，讓你借貸沒煩惱！\n</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">最快一小時放款，全程AI自動化審核，安全隱蔽，簡單快速                                     &gt;&gt;  https://www.influxfin.com/</p>\n<!-- /wp:paragraph -->', '2019-05-17 17:31:59', '【金管會將密切觀察銀行線上授信業務 普匯導入AI審核快速又安全】', 'article', 'publish', '2019-05-20 09:20:13', '', 0),
(3853, 1, '/upload/2019/05/dawn-1840298_1920.jpg', NULL, '<!-- wp:paragraph -->\n<p>一個學期的開始，原是令人振奮的時刻，但許多學生此刻卻眉頭緊皺“又要繳費了”家裡狀況一時無法提供資金協助，手頭緊暫時生不出錢，準備開啟一連串為申請就學貸款而奔波的日子。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>首先是視覺轟炸，複雜的申請條件落落長，看得眼花撩亂，終於弄懂手續流程，卻赫然發現自己並不符申請資格！以前身邊的同學也有這樣的窘境，有些人拖欠到學期中，甚至擱置到期末了，助教只好開始催繳費用，同學很累、助教也辛苦，學生們一個頭兩個大，求助無門的情況層出不窮。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>事實上，不只台灣，全球都面臨這樣的大問題，許多歐美國家念大學的學費極為高昂，學生們壓力更加沉重。摩根大通執行長Jamie Dimon甚至明確指出： 不斷增長的大學費用和學貸問題，甚至開始影響整個經濟。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>台灣就學貸款資格及條件繁複</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">一、申貸資格：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有戶籍登記之中華民國國民，就讀「國內」 （不含港、澳及大陸地區）公私立高級中等以上學校，具正式學籍及固定修業年限之在學學生。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">二、申貸條件：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>學生及其父母或法定代理人（學生已結婚者，為學生及其配偶）合計之家庭年收入經查符合本貸款中央主管機關教育部所訂定之「家庭年收入標準」（該標準由教育部逐年公告，目前規定之標準請詳閱問三）、或經就讀學校認定有申請就學貸款必要者。但縱不符合教育部公告之家庭年收入標準且未經就讀學校認定有申請就學貸款必要，惟其家中如有二位以上子女就讀「國內」\n（不含港、澳及大陸地區）公私立高級中等以上學校，亦可申請就學貸款，惟應自撥貸日起自行負擔利息。</li><li>學生及連帶保證人均具有中華民國國籍，並有戶籍登記者。駐外公務員在國外出生之子女返國就學後尚未取得戶籍登記者，得先以居留證及中華民國護照申請。</li><li>學生並未享有全部公費及全部學雜費減免，且未獲得政府主辦之其他無息助學貸款者。但如享有部分公費、部分學雜費減免及（或）教育補助費，而其應繳學雜費等費用（下稱學雜各費）於扣除部分公費、部分減免金額及教育補助費後仍有差額者，就該差額部分，亦可申請就學貸款。​</li><li>學生如有消費者債務清理條例之前置協商，惟於本行無債信不良紀錄者可申請貸款或展延；但有更生、清算等相關註記，即不得申請新撥就學貸款或緩繳本金。</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯看見你的需求</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有鑑於學貸申請無門案件日益增多，在普匯平台已有許多大學學子成功獲得貸款，我們希望落實傳統銀行無法提供的金融服務，秉持快速、簡易、安全，讓借款者能快速解決急用資金問題。從初期審核，到最後的放款階段，與同業相比是最快速的，而且平台的手續費也是最低，人工智能審核機制減少人為聯繫所耗費的等待時間，運用高端生物辨識科技，保護個資隱私，導入人工智能交叉驗證風控系統，採用區塊鏈智能合約自動精準地執行每筆交易，是一個讓你安心、放心的平台。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>簡單三步驟快速註冊 </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3855} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/05/image-2.png\" alt=\"\" class=\"wp-image-3855\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>讓我們來金援你: </strong><a href=\"https://www.pu-hey.com/我要借款/\">https://www.pu-hey.com/我要借款/</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', '2019-05-24 16:45:18', '【普匯觀點】學雜學貸這檔事', 'article', 'publish', '2019-06-13 12:00:25', '', 0),
(3871, 1, '/upload/2019/05/5G1.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">大家還記得3G轉變為4G的日子嗎？4G的來臨帶來更高的資料承載量和更低的時間延滯，還能運用於各領域，從醫療、交通到金融，提供更全面的服務，4G發展現已處成熟階段，幾乎遍布全台各地。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">那5G將會有什麼驚人的革新呢？市場人士分析指出，5G是全球科技發展的關鍵技術，比4G更快速且低延遲的無線通訊利於發展大數據、人工智慧、物聯網服務，不論是工業4.0、自駕車、遠距醫療、智慧程式、虛擬實境（VR）、擴增實境（AR）等領域皆可應用。反應時間更短，有利於導航、AI和直播等以即時和高效為目標的產業，5G將大幅提升速度品質。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>全球行動通訊標準3GPP</strong><strong>會議</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">3GPP (the 3rd Generation Partnership Project)成立於1998年，成員包含歐洲、北美、日本、中國大陸、韓國及印度等區域性電信標準組織，目標在於制訂全球性的行動通信系統規範。延續3G UMTS、4G LTE之成功發展，3GPP正致力於實現國際電信聯合聯盟(ITU)下一代行動通信(5G)願景之技術規格制訂。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">中華電信攜手聯發科、台灣資通產業標準協會和經濟部技術處5G辦公室主辦「台灣5G商用服務發展願景高峰會」，同時也贊助3GPP國際標準組織在台北舉辦的RAN1會議，推進5G標準制訂工作。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">據中華電信董事長鄭優表示，台灣資通訊產業在全球擁有重要地位，面對即將到來的5G新世代，中華電信已結合經濟部技術處5G辦公室、工研院、資策會共同發起成立『台灣5G產業發展聯盟-中華電信領航隊』，結盟國內外約45個機構及企業，建構端到端的5G產業鏈，發展創新應用服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>各國搶佔5G</strong><strong>先機–中、美、韓5G</strong><strong>技術角力戰</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">5G技術應用如能領先全球推出，將能掌握主導權，其影響不僅僅是成為全球網路平台領導者，甚至在AI人工智慧發展上也能搶佔先機。韓國及美國相繼在今年4/3號正式上線5G網路服務，而韓國又為全球首位5G商用國，三星公司打鐵趁熱推出首支5G信號手機Galaxy S10，宣稱網速最高可達1 Gbps。然而使用5G服務價格仍偏高，韓國SK電訊8GB流量收費約莫1485元台幣，美國威訊無線 (Verizon)雖不限流量，但只能觀看480p低畫質影片且收費要價2600元台幣，此外真實評測結果並不穩定，看來5G使用普及仍有一段路要走。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">至於中國的5G發展，根據法國新聞社報導，其極有可能已經主導了5G技術；另外德國專利資料庫公司IPlytics統計，中國5G專利數量占比達34%，其中又以對美國造成極大威脅的華為項目最多，總共注冊1529個與這一新技術有關的專利。一旦中國5G建構完整，更多連線裝置的出現將創造更多的數據，數據量增加讓演算法更完善，帶動AI進步，未來可能超越美國，躍升世界霸主。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>20</strong><strong>倍速神話？實測落差大</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">韓、美搶先推出的5G網路服務，實測情形令許多用戶感到失望，和預期主打速度為4G的20倍相比，落差很大，體驗者實際評測後表示，訊號不穩、時好時壞，與4G差異不大。目前5G覆蓋率極低，信號也微弱，即便手機能夠支援5G仍沒有太大意義，5G的拓展如當時3G邁向4G的陣痛期，需要經歷一段過渡時間才能廣泛運用，專家預測真正普及可能是2023年以後的事了。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>5G</strong><strong>＋</strong><strong>AI</strong><strong>人工智慧</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">5G將承載和傳輸海量的人工智慧數據，做完善的智能升級，從分析、決策到反饋，即時且高效；\n5G能夠因應更複雜的AI處理要求，實現人與人、人與物以及物與物的連接，達到萬物互聯的規模大躍進；AI應用層面多元，且深入人們生活，從AI身分驗證、對保到查核徵信，5G能囊括更多信息資料，加快審核速度，進而改善人類生活。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">普匯金融科技已在台灣P2P網路借貸領域搶得先機，5G的發展，能容納日益增長的客群，和處理龐大的資料數據，此外AI審核貸款速度會更迅速，平台使用更流暢，往後普匯金融將攜手5G帶給你全新的體驗與服務！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>關於普匯金融</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">我們服務銀行無法觸及的客群，提供P2P網路借貸服務，造福普羅大眾。只要你是年滿20歲，擁有大學以上身分，並備齊四大證件：身分證/健保卡/金融卡/學歷證件，就能註冊成為普匯會員。普匯幫助借款人圓夢，給予投資人理財新管道！ </p>\n<!-- /wp:paragraph -->', '2019-05-28 17:47:48', '【金融趨勢】5G有多快？各國5G技術角力戰', 'article', 'publish', '2019-06-13 11:59:58', '', 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(3894, 1, '/upload/2019/06/AI-business.jpg', NULL, '<!-- wp:paragraph -->\n<p>AI人工智慧的發展改變了既有的產業運作模式，帶來更快速且貼近客戶需求的服務與體驗。然而大勢AI卻也讓企業傷透了腦筋，多年建立起來的商業模式面臨衝擊，透過傳統經營模式獲取豐厚收益的企業，難以改變現有思維；想透過創新改革既有舊體質的企業又遇到相容性與整合困難。AI確實在各方面帶來突破性的轉變，但實際應用和企業適應性問題，仍是當今最大的難題，該如何讓企業著手計畫，了解自己所需，並成功加入AI，為其提升商業價值是重點，能掌握先機而成功結合的企業，將有機會獨占鰲頭。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>AI</strong><strong>人工智慧有多厲害？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>其實AI (Artificial Intelligence)並非近年才出現的新概念，簡單來說可以由一台電算機開始講起，從輸入指令、機器執行，到發展成學習人類思考模式，甚至能夠自我檢測、深度學習、進行演算等，過去因為研究與思維模式的限制，難以拓寬到運用層面。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>隨著科技大幅進步，運算效能的精進，漸漸將AI想法實際執行，近來也成為企業重視的議題，它將大幅增加產業競爭力。AI能夠執行以往只能由真人操作的工作，它能像人類一樣擁有解讀、研究和分析能力，我們通常稱為認知科技，根據Deloitte Global (德勤) 的研究，認知科技如同人們的五感包含視覺、聽覺、嗅覺、味覺和觸覺，除了情緒之外，幾乎能做得維妙維肖。而認知技術的運用包含機器人流程自動化(RPA)、機器學習、自然語言處理與生成、深度神經網路，目前較為成熟的運用是機器人流程自動化，企業可善用這些技術達到人機協作提高產能、即時快速及準確性的提升。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>企業與AI</strong><strong>合作的三大面向</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\"} -->\n<p class=\"has-text-color has-vivid-green-cyan-color\">1. 流程精準有效率</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>傳統企業經營模式以人為監督和人工操作為主，難以避免錯誤發生，機器人流程自動化(RPA)的導入帶來更加精確的判讀能力、高效率、即時迅速反饋等優勢，還能協助客戶滿足需求。流程機器人幫助企業降低營運成本，同時提升服務品質，可謂一舉兩得。\n</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\"} -->\n<p class=\"has-text-color has-vivid-green-cyan-color\">2. 服務價值創新</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以往企業會透過市調和既有客戶分析客群及消費模式，有時較難反應快速的市場變化，且有些產品可能已不符消費者需求。AI的結合，除了可以有系統的處理大量客戶資訊，還能優化消費者體驗，甚至能夠預測消費行為，推出的新產品也較貼合時下需要，讓企業事半功倍。AI的加入將帶給企業另一波成長，同時造福消費者，形成雙贏局面。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\"} -->\n<p class=\"has-text-color has-vivid-green-cyan-color\">3. 洞察能力</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>不同於以往單靠人力判斷市場走向，分析上有時會有主觀意見，可能影響決策結果。透過人機協作的方式，機器能提供客觀的分析且能同時處理多筆資料，有效協助人力，實際運用包括信用審核、舞弊偵查等。洞燭先機對一個企業至關重要，這也是認知科技對企業帶來極大效益的主因。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>跟上AI</strong><strong>潮流</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>AI的運用不僅影響企業營運模式，也成為社會進步的動力。AI應用層面廣泛，遍及各產業，從分析、審核、評估到生物辨識技術，帶來了極大便利性。近年來金融科技公司的成立，將AI做更多的發揮，提供消費者最低障礙和即時的服務，而普匯金融科技致力於互聯網金融產業的發展，在P2P網路借貸搶占先機，核貸速度快又準確，不僅幫助到銀行無法觸及的人群，也給予投資人新的理財管道，我們邀您一起加入普匯金融享受便捷貼心的借貸服務！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\"} -->\n<p class=\"has-text-color has-vivid-green-cyan-color\">註：互聯網金融&gt;&gt; 非金融機構透過網路科技從事金融業務活動。</p>\n<!-- /wp:paragraph -->', '2019-06-11 17:42:05', '【金融趨勢】當企業遇上AI–商業模式改造計畫', 'article', 'publish', '2019-06-13 11:58:58', '', 0),
(3900, 1, '/upload/2019/06/bright-chart-data-210607-1024x683-1.jpg', NULL, '<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>自川普政府上台以來，政策圍繞著「美國優先」、「讓美國再度偉大」的核心理念，不僅頻頻對中國發動各種貿易制裁，對於北美貿易國成員之一的墨西哥也揚言要課予關稅。但在前幾天川普卻宣佈暫緩對墨西哥課稅的計畫，市場認為有助於消緩川普政府貿易戰之疑慮，各國經濟市場受到鼓舞紛紛上揚。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">近期各國經濟數據表現佳</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>不僅美股S&amp;P&nbsp;指數漲幅1.1%，亞股受到川普暫緩關稅消息如日本、韓國、香港股市相繼上漲，也帶動國際原油價格上漲。外匯方面尤其是日幣，由於日幣在市場上常常被視為是熱門避險工具之ㄧ，在這次消息中日幣避險需求下降造成日幣略為貶值。各項反應都顯示市場對於貿易戰緩解有稍微增添信心。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">中美核心議題接受度不同&nbsp;G20有辦法能達成協議？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>由於6月底將舉行G20高峰會，美國總統川普表示將會就美中貿易議題與中國國家主席習近平再次談判。儘管他對於月底與習近平的會晤感到樂觀，然而中美貿易戰至今未解之關鍵因素，在於中國政府對於美國要求全面「開放市場」如Google等大型美國公司進駐中國，一直遲遲不願意讓步。其實不難理解中國政府之疑慮，由於開放後會對當局管控網路增加更多維護成本，再加上美國要求不能強制使外國雲端電腦公司把所有數據儲存在中國，種種措施都會讓中國更堅定貿易戰不會輕易妥協。即便上述美墨貿易戰的緩和，中美貿易戰核心利益與美墨不完全相同，因此市場上仍未消除對於中美貿易戰之疑慮。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">美國經濟數據放緩 加深對於市場之不信心</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>製造業PMI指數（採購經理人指數），是一個美國整體製造業狀況、就業及物價表現，是全球最受關注的經濟指標之一。在上月美國數據調查公司IHS Markit顯示指數持續下滑並創下自2009新低，來到50.5。其他指標如美國就業市場上薪資成長以及就業人口增長停滯、美債仍持續發生利率倒掛之現象，特別是利率倒掛在過去數據上皆能準確預測經濟衰退的來臨，再加上Fed主席Powell暗示必要時下半年可能採取寬鬆的貨幣政策，對於投資人來說在經濟市場上賺取報酬無疑是添加了更多風險。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">普匯提供更佳的投資選擇</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>隨著全球市場風險加劇以及美國經濟放緩，投資人對於市場上所追求之報酬往往會更加困難。普匯金融科技運用人工智慧以及大數據分析演算法，找出市場上適合大眾投資者之低風險高報酬投資標的，不僅讓投資人不用花費太多心思去關注市場變動，承受過大的經濟風險不確定性，透過普匯債權投資，只要簡單三步驟，就能讓投資人安心獲得穩定被動收入。</p>\n<!-- /wp:paragraph -->', '2019-06-12 16:23:04', '【金融趨勢】G20高峰會即將舉行 中美貿易戰有解決之道？', 'article', 'publish', '2019-06-13 12:05:06', '', 0),
(3918, 1, '/upload/2019/06/v211batch10-aum-91-blockchain_2.jpg', NULL, '<!-- wp:paragraph -->\n<p>金管會將在本月宣布證券型代幣STO監管法規以及發放純網銀執照，對於數位金融的發展，又跨出了一大步！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>其中金管會表示，只要民營STO交易所符合所要求之條件如實收資本額為一億元、營業保證金為1000萬元，就能申請自營商執照。況且金管會也不排除之後成立主要發展STO代幣交易的官方交易所，金管會此舉也告訴社會大眾作為官方主管機構對於數位金融的支持。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">數位金融在台發展</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>台灣對於數位金融發展至今，最大優勢在於優秀的人才與資金豐沛，再加上友善的創業環境，如果台灣相關的法規開放，很自然地能夠吸引世界各國團隊來台入駐，有助於台灣總體經濟的發展。再加上立法委員許毓仁發起兩岸區塊鏈聯盟，協助兩岸區塊鏈彼此產業交流合作，相信對於兩岸新創產業發展，有顯著的提升。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">台灣有望成為亞洲STO交易新興樞紐</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金管會所推之監管法規如果能符合大眾預期推行，台灣金融市場對於海外資金無疑是一個投資之好所在。由於成功推行後能創造先行者效應，台灣不但有機會成為亞洲區STO交易中心，更有機會成為「區塊鏈之島」。台灣自十幾年來自許成為亞洲金融中心，隨著STO交易與純網銀開放兩大新政，是否能達到願景此時正是最重要的關鍵。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">普匯致力提供最有溫度的服務給用戶</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯在台灣數位金融產業處於領先地位。我們團隊利用最新AI以及區塊鏈技術，讓所有用戶能夠透過平台使用P2P lending&nbsp;方式，快速地完成小額借貸或是從事投資。在以往傳統銀行無法提供的金融服務，透過普匯金融能讓用戶擁有不一樣的體驗!</p>\n<!-- /wp:paragraph -->', '2019-06-14 17:00:03', '【普匯觀點】金融法規逐漸開放 今夏數位金融有了不一樣道路', 'article', 'publish', '2019-06-14 15:05:17', '', 0),
(3933, 1, '/upload/2019/06/pexels-photo-356043.jpg', NULL, '<!-- wp:paragraph -->\n<p>由於近期香港特首與特區政府將修訂極具爭議性的《逃犯條例》，香港民間為此不但在網路串起了「612&nbsp;全港罷工罷市」的自主號召，也爆出逾百家企業紛紛響應。外界擔憂如果香港政府持續漠視民意不願意正面解決，恐激化市民使用更極端的示威手段，也將會進一步加深對於香港資本以及經濟市場的衝擊。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">台金融業與港緊密度高</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>我國金融業包括銀行、保險、證券等與香港緊緊相扣。根據金管會截至4月底統計，台資銀行在香港不僅擁有19家分行，在港總曝險金額共計高達6930億元。而保險業也不遑多讓，在港總曝險金額也有將近2092億元。再加上證券在港也有約為816億元的曝險部位，總計台灣整體金融業在香港曝險金額高達約9838億元。此曝險金額占行政院主計處所預估台灣整體名目GDP 181859億高達約5.4%！數據也告訴我們一旦示威持續擴大，台灣金融業必定會遭受嚴重衝擊。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舉例來說，前陣子金管會公布台灣銀行海外分支機構獲利情形，特別指出本國銀行在香港市場成長最為迅速與強勁，成長約32%。尤其是上海商業銀行香港子行，在2018狂賺137億元，成為獲利王。而老行庫如華南、合作金庫銀行、土地銀行等，皆透過香港分行子行取得獲利佳績。因此以上數據也顯示，台灣金融業靠著香港市場賺取不少獲利。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">美方表示此舉將影響香港特殊地位</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>美國政府在1992年制定法律，不僅承認香港是不同於中國之非主權實體，並且也給予了香港相關特殊待遇涵蓋如簽證、引渡及投資等，一旦中國政府修改了法律，華府將可能會重新審查此法律概述的美國與香港關係之內容。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據外媒福布斯(Forbes)報導，如果香港特區政府執意通過送中條例，華盛頓可能考慮不再給予香港特殊商業地位。一旦如此，美國將可能不在視香港為獨立的關稅區域。這也意味著時下中美貿易戰美國對於中國商品的關稅，也將會適用於自香港出口之商品，影響港幣兌美元之匯率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">經濟市場風險波動 普匯為你提供小額風險投資</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>近年來中美貿易戰加上各地域性之政治風險(如香港)，投資人在市場上容易承受波動劇烈的風險，投資報酬率極為不穩定。普匯金融提供債權轉讓投資，最大之好處在於如果此債權投資報酬率您不甚滿意的水平，也可以及時做轉讓，降低承擔虧損的機會。也因為透過普匯的小額投資，我們可以讓用戶的小額資金投資發揮複利的效果！</p>\n<!-- /wp:paragraph -->', '2019-06-17 17:00:59', '【時事分享】香港「反送中」衝突若擴大 我國金融業將遭受波及', 'article', 'publish', '2019-06-17 17:04:19', '', 0),
(3963, 1, '/upload/2019/06/franck-v-516603-unsplash.jpg', NULL, '<!-- wp:paragraph -->\n<p>作為全世界最大一家提供通訊設備與技術的公司，近年以來，華為受到美國貿易制裁禁令影響，在世界各國紛紛遭受打壓。儘管如此，華為不但沒有因此停下發展的腳步，還準備跨足AI市場！根據外媒報導，華為正在與各家車商討論合作關係，目的在尋求共同開發自駕車，並且進入AI市場。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>華為表示，此項合作他們將會替汽車製造商提供自己開發的AI軟體，其中包含知名車商奧迪、豐田等。此外，也會與當地中國汽車製造商如中國車廠北汽集團和長安汽車，一起合作來開發中國自駕車市場。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">自駕車市場在中國發展潛力大</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據華為首席戰略架構師黨文栓表示，考量到中國市場目前的規模以及活動力，在未來中國非常有機會成為全球自駕車產業的龍頭，發展AI後將讓中國車廠在連網汽車和自駕相關的關鍵技術突飛猛進。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>該公司也預計最快2021年推出自家與車商合作生產的自駕車，並且先在中國與歐洲市場銷售。此外，該自駕車將會採用自動化程度第二高標準的Level 4。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">華為跨足AI動作不斷</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在華為走進AI產業之前，前陣子才花了5000萬美元(約台幣15.7億)購併了國際老牌安防廠商Vocord，並且收購了該企業臉部辨識技術專利及開發團隊。此舉也顯示當今AI技術的「含金量」非常驚人，大企業不惜成本也要取得進入AI市場的門票。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\">普匯AI技術走在業界前端</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯金融一直以來金融願景目標是利用AI、區塊鏈等最新科技應用，重新定義金融業服務模式。我們平台導入生物系統辨識技術，讓整個審核過程都無需人工核貸，同時導入人工智能信評機制，以科技技術把關民眾的信用評等程度，不同於傳統金融業的人工審核，高端科技讓你我在未來透過我們申貸資金將會更便利、更快速！</p>\n<!-- /wp:paragraph -->', '2019-06-19 17:00:32', '【時事分享】人工智慧技術夯 華為也來參一咖', 'article', 'publish', '2019-06-19 14:00:46', '', 0),
(3967, 1, '/upload/2019/06/年輕人投資理財.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">年輕時的思維通常是將所得金錢享受人生，對於老年生活規劃鮮少深思過，其實北歐等先進國家十分注重國民投資理財教育，需要如何有效管理金錢，該如何依自身情況做資產配置，能夠清楚知道自己在幾歲退休時，可以累積多少財富以達成老年生活無虞的目標。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>社會結構改變 </strong><strong>打破傳統思維</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">養兒防老的舊觀念要漸漸被導正，從年輕的時候，我們應該思考未來自己想要的生活模式，愈早開始起步，愈能見證時間帶來的驚人力量，審視自己的生活方式與態度，將金錢最合理安排，在投資和消費達成平衡，可見打理自己的財產至關重要。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>每天省下一百塊 </strong><strong>滾出驚人效果</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">至於時間將見證投資的力量，股神巴菲特曾說過：「Life is like a snowball. The important thing is finding wet snow and\na really long hill.」這條長而遠的坡道就是時間，而每個人活在世上的時間都是有限而難以預料的，故盡早進行投資理財為最佳方法。適合年輕人的方式以小額投資理財負擔最低也最簡單，如果我們每天省下100塊錢的費用，一個月就能投入將近3000元，每個月小額投資3000元，以投報率5%進行月複利，20年後也有約莫124萬的收入，比起毫無作為的人，可看出巨大的差別，「The most powerful\nforce in the universe is compound interest」，從愛因斯坦對複利的闡述中，在時間洪流裡，更能見證效果。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">了解自己的需要、理解理財的真諦、相信時間終將帶來效益，規劃人生藍圖，懂得掌握和管理金錢，Design your life with wise investment!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>P2P</strong><strong>成為小額投資理財新管道</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">以前大家對於P2P(Peer to Peer Lending)網路借貸的了解甚少；有些人對於網路借貸感到懼怕，認為沒有保障，違法的可能性高；此外中國大陸P2P市場紊亂，詐騙叢生，一連串的倒閉潮加深人民對於P2P的排斥，種種因素導致P2P被醜化與誤解。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">其實網路借貸市場給予投資端與借款端一個便捷快速的管道，同時也是適合新鮮人和小資族的投資選擇，每個月小額投入3000～5000元也能累積可觀財富，而普匯金融科技是P2P網路借貸平台，結合AI無人自動化審核機制，相較於人工方式，能夠安全且有效率完成審核，此外申請借款人利率期數可依偏好選擇，而投資者也能依此法選擇欲投入的標的，好處是其不像股票易受市場波動影響，可能偶發事件就有崩盤的危機，不葬送每個月投入的心血，還能創造被動收入，提早享受退休生活。 </p>\n<!-- /wp:paragraph -->', '2019-06-21 14:56:06', '【普匯觀點】年輕人也要投資理財–提早享受退休人生', 'article', 'publish', '2019-06-21 14:56:07', 'investtonic', 0),
(3984, 1, '/upload/2019/06/AI人臉辨識.jpg', NULL, '<!-- wp:paragraph -->\n<p>AI人工智能時代的來臨，給世界帶來劇變，打破了舊有的產業運作模式，為停滯的技術帶來革新，而近年來也颳起AI臉部辨識熱潮，對人類可說是一種全新又方便快速的高效體驗。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>告別帳號密碼時代</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>日常生活當中，不論是網購還是各大電商app，甚至近期各大銀行推出的網銀服務等，均需要帳號密碼，但同一個人在各個網站留下的帳號密碼可能都不同，每次登入總是記不清一直輸入錯誤，十分耗時。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>AI人臉辨識技術的出現，以更簡易直觀的方法，拋棄以往帳密模式，憑一張臉即能確認使用者身分，隨者AI技術進步，精確度及速度都能有效提升，根據市場研究服務和商業顧問公司MarketsandMarkes報告指出，人臉辨識的全球市場，將從2017年的40.5億美元，成長到2022年的77.6億美元，暗示其有極大商機和高獲利前景。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>AI</strong><strong>人臉辨識全方面應用</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>AI人臉辨識應用範圍廣泛，它深入人們生活周遭，面臨身分辨別問題，舉凡考生身分檢查、犯罪偵防、銀行交易，到居家照護和醫療都能應用此技術，不僅簡化繁瑣人為審核，也不用提交資料文件，但許多人還是擔心其發展仍不成熟，對其精準度和安全性抱持懷疑態度。現在研發中的臉部辨識技術可做到分析表情與視線，人臉細節辨識再提升，除了提高辨識品質外，還有助於企業應用，企業端甚至不需知道你的身分信息，如零售業數位看板的精準行銷，提高企業的服務與商品表現。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>2020</strong><strong>年東京奧運將展現AI</strong><strong>人臉辨識技術</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>萬眾矚目的2020年東京奧運，將創下歷來奧運首例，運用日本電氣(NEC)研發的AI人臉辨識系統，自動檢查入場者臉部和事先登記在ID卡上的照片，比依靠人工判讀快上2.5倍，並宣稱0.3秒即能快速辨識成功，裝設於比賽會場、選手村、媒體中心的場所，用來識別30萬名參與者，包括運動員、志工、媒體、工作人員等，期望有效加速維安流程。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>AI</strong><strong>身分認證應用於P2P</strong><strong>網路借貸市場</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>台灣的P2P網路借貸市場處於起步階段，大部分P2P業者審核身分信息與相關資料仍以人工審查為主，作業時間較長，有時難以立即給予借款端協助，也會有人為疏失問題。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>為了給予客戶全新體驗，普匯金融科技是透過AI無人自動化審核的P2P平台，不同於一般人為審核模式，一小時即可通過審核並簽約撥款，提供借款端最低障礙和即時的服務；獨有的無人照會機制，透過借款人所提供的資料、證件及持正自拍，從實名認證、身分驗證、金融帳號認證到社交認證，都能自動辨識，帳戶系統方面，銀行、普匯、客戶三方公開透明，讓借款人及投資人都安心。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>期望未來AI臉部辨識技術更臻成熟，廣泛運用於各產業，提升安全、效率、品質，也能造福長照醫療族群，達到友善世界。</p>\n<!-- /wp:paragraph -->', '2019-06-27 16:19:59', '【金融趨勢】全球大勢–AI人臉辨識大進擊', 'article', 'publish', '2019-06-27 16:27:59', '', 0),
(3993, 1, '/upload/2019/06/money-2724241_1920.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>股票高報酬神話？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「股神」巴菲特一直是許多投資客的偶像，看似穩賺不賠，幾乎沒有投資失利的時候，坐擁20% 報酬率，但巴菲特真的那麼神嗎？答案是不可能的！人們往往報喜不報憂，成功值得嘉許，而大肆宣揚，他們上各大平台，或撰寫文章闡述獲利方法，洋洋灑灑寫了一堆股市分析和獨到見解，怎麼賺、如何賺？<strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有的40歲就達到財富自由，甚至有的神人30歲就賺夠一輩子的錢，退休環遊世界了。然而這些成功例子又有多少，遇賠錢時常常隻字未提，況且誰又願意提供自己失敗的例子當負面教材，讓家醜外揚；又有誰會將自己的獨門絕活公諸於世，讓其他人也分一杯羹？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>就因為人人都有發財夢，因著這個利基，這些成功人士的理念得以快速傳揚，人們開始競相仿效其投資模式，但真正分到大餅的又有幾位？多數失敗落寞退場，甚至傾家蕩產的都大有人在。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>或許這樣還沒能說服你，讓我舉真實的案例給你聽：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有次身邊的親戚突然開心的說他投資股票賺了14萬，我當下腦中立刻浮現「他之前到底賠了多少錢？」果真深究下去，前幾個月累計損失金額也是14萬左右，僅是打平而已，這可能已經算是幸運的案例了。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>跟著成功者就能致富嗎？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在阿里巴巴勢頭正旺的時期，馬雲的股票投資策略一時為人所吹捧，甚至有一說：「跟者馬雲走，不怕坑隊友」，馬雲投資什麼產業，哪個產業就會火，許多散戶開始跟風，幾乎將自己的資產都倒了進去，但他們可曾細想過，馬雲的資本額有多少？他能承受的虧損有多大？他可以為了扶植自己的阿里影業，即便淨虧損達到了17億人民幣，仍持續注資，這哪裡是一般投資客所能承擔的風險？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>真心話告訴你</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>報章雜誌上常常說投資股票的報酬率很高，但現實又是如何呢？其實很少有人能夠從股市中獲取高額報酬，而且當中還有很多人是認賠出場，最後一點錢都沒賺到，或許你也常在電視上看到投資人被斷頭的新聞。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>原因到底是為什麼呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>因為這些投資標的風險並沒有得到重視。不管是股票或是ETF，波動都不算小，而股市行情又常常是讓人摸不著頭緒的，所以才會出現電視上所謂分析師教大家要在什麼時候買進賣出，真的只要照單全收就能獲利嗎？那麼多人跟著分析師操作，賠錢卻仍是占多數。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>事實上，沒有人能真正看清什麼時候是高點，多數時候都只是盲從或是覺得自己的想法很對就下手，盲目進場的後果，就是被股市上沖下洗、搞得頭昏眼花，因為股市的波動永遠比投資人想像的複雜和難以捉摸。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯優勢</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>你有想過一種投資方式，它不需要憂心進場時機，利率又比銀行更具競爭力，還能獲利穩定的方式嗎？和市場上基金、股票比較，我們報酬穩定，不像基金股票易受市場波動影響，雖然行情好能夠大賺，但遇突發事件或金融海嘯時，也可能瞬間化為烏有。普匯P2P網路借貸平台讓你每月固定小額投資，不用提心吊膽一天到晚盯盤，見證時間帶來的驚人效果！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Design your life with wise investment! 相信普匯，聰明的投資理財，給你美好人生，盡早達到財富自由。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":3994} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/06/image-5.png\" alt=\"\" class=\"wp-image-3994\"/></figure>\n<!-- /wp:image -->', '2019-06-28 17:16:47', '【普匯觀點】普匯，創造財富的利器', 'article', 'publish', '2019-06-28 17:20:57', 'investtonic', 0),
(4000, 1, '/upload/2019/07/未命名-4.png', NULL, '<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>出門買東西還帶著各家信用卡嗎？早上上班上課太匆忙，不小心忘記帶錢包，身上沒有現金、沒有卡片讓人驚慌失措，整天魂不守舍嗎？行動支付的發展大大改善了這些問題，在手機不離身的時代，可謂現行最便民的方式。不過大家是否想過有天你兩手空空出門，就能進行支付消費，車票也不用買實體票券或持有卡片，不再需要擔心老是忘在家裡的門禁卡……</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-green-cyan-color\"><strong>NFC是什麼？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>NFC全稱為Near Field Communication，為近距離無線通訊，原理是使用單一晶片，利用點對點功能，進行約莫20公分的短距離資料傳輸、感應、交易存取等功能，通常結合感應讀卡器和感應式卡片。近年來NFC已成為多家廠商提供支援的標準。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-green-cyan-color\">晶片植入在瑞典大受歡迎</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>目前已有4000多名瑞典人選擇在手部虎口處植入晶片，僅約米粒般大小，價格約為台幣5000元，植入完成後，用戶只需在智慧手機上通過相關的NFC應用，對晶片訊息進行個人化設置，就能實現大部分功能了。晶片使用的就是NFC技術，其具有現金、車票、門禁卡等功用，還能透過手機讀取並確認個人信息。在瑞典能夠風行的原因與瑞典人樂於嘗試新技術，對於個人信息持開放態度，不避諱資訊互通有無的想法有很大的關聯。植入晶片的好處不只是讓生活更加便捷，在醫療方面也提供相當大的助益，瑞典創投公司「Biohax」創辦人兼「生物黑客」喬文·奧斯朗德（JowanOsterlund）更說：「當你失去知覺時，醫護人員就可以利用晶片確認你的身份及知道你的身體情況。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-green-cyan\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-green-cyan-color\">秉持革新精神優化客戶體驗</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>從現金交易，到卡片興起，再到手機支付，甚至近年來的皮下植入，都可以看見技術與服務方式再進化，以更快速、方便的形式滲入到我們生活當中。使用障礙愈少愈能吸引民眾前往嘗試，普匯致力發展P2P網路借貸市場，希望如行動支付普及於眾人般，讓一般人民也能享有借貸的權利，普匯之後將推出自家APP，屆時將給客戶全新而親民的體驗，也一同為金融科技發展而努力。</p>\n<!-- /wp:paragraph -->', '2019-07-03 17:58:24', '【金融趨勢】兩手空空也能支付消費–NFC應用再升級', 'article', 'publish', '2019-07-11 10:14:59', '', 0),
(4116, 1, '/upload/2019/07/圖片-1-1.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-red\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-red-color\">7月2日中職選秀會風光落幕，創造了多項輝煌紀錄</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>第一次選秀會冠名贊助－明台產物保險冠名贊助</li><li>參與選拔人數高達145人創歷年紀錄</li><li>連續三年由高中生當選選秀狀元</li><li>第五隊味全龍加入，選了28輪節結束指名，共選進32名選手</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-red-color\">7月3日桃園Lamigo傳出轉賣球隊</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>         Lamigo球團7年內拿下5度中華職棒總冠軍，並於近期拿下上半球季冠軍，同時也在去年成功輸出交易中職招牌球星「柏融大王」，至日本職棒人氣球隊－北海道火腿鬥士隊，然而終究抵不過經營危機，球團老闆劉保佑曾向蔡英文總統表示：「站在企業經營角度，沒有人想經營賠錢事業，桃猿隊16年來賠了16億。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>同時也在去年成功輸出交易中職招牌球星「柏融大王」，至日本職棒人氣球隊－北海道火腿鬥士隊，然而終究抵不過經營危機，球團老闆劉保佑曾向蔡英文總統表示：「站在企業經營角度，沒有人想經營賠錢事業，桃猿隊16年來賠了16億。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-red-color\">台灣的國球「不是棒球，是贏球」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>無論多數中職的母企業是否有曾經明確表示，在球迷心中認為多數球團的球隊經營方針，總是把目標放在於「企業社會責任」，而非透過商業經營角度打造。Lamigo球團相較之下，創新求變，其引進啦啦隊、打造偶像名星形象、塑造專屬球風、厚實一二軍實力、提升訓練品質與環境、努力經營主場甚至與桃園當地政府合作，積極落實屬地主義，仍然敵不過巨額虧損的事實。<br>經營方針、方法、理念與對國球形象的塑造，將成為未來中職的其他球隊所要面臨與克服的重要關卡，期許未來球團們可以更加用心經營，帶起國民的體育文化，讓球迷不再只有國際賽時對棒球有短暫的激情，而能夠讓體育落實在日常生活當中。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-red-color\">打造球隊＝建構投資組合？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>電影魔球中，經理人透過大數據分析，依據想要打造的球隊風格，在海量的球員市場中，找尋適合的成員，以低成本塑造出高競爭力的球隊，甚至衝擊總冠軍，創造了傳奇的扉頁。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當我們以球隊的建造類比成投資組合portfolio，在投資學上我們會依據投資人的風險偏好，舉凡CAPM或是MM理論去建構或是效仿beta係數，再透過beta去預估投資組合報酬率，同時從股海中挑選個股、債券或是其他標的物去建構目標投資組合。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在塑造球隊的過程中，我們也可以先擬定目標，塑造想要表現的球風與形象，再根據球員市場，假想球員為標的物，利用數據分析挑選適合的球員，依據所需的屬性打造符合期待的團隊。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-red-color\">普匯influx投資端app提供投資人建構屬於自己的投資組合<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯投資app簡單、方便、快速，投資人可輕易上手，其中小額投資的特性，即使本金只有10000元，對於股市來說只能購買少數零股，然而在普匯投資 app，最小單位1000元的投資量就可以參與其中，10000元的本金最多可以分散成10檔投資，「小額投資同時有效分散風險」，還等什麼，建構portfolio再也不是有錢人的專利，大家趕緊依據自己的風險偏好在普匯建構屬於自己的投資組合吧。</p>\n<!-- /wp:paragraph -->', '2019-07-11 10:13:14', '【時事分享】震撼！中職模範生5年4冠桃猿Lamigo隊驚 傳易主！！', 'article', 'publish', '2019-07-30 10:27:25', '', 0),
(4137, 1, '/upload/2019/07/圖片-13.png', NULL, '<!-- wp:paragraph -->\n<p>        普匯金融科技與東吳大學攜手發展「AI金融科技平台」，串接金融機構、政府單位及各學術單位，跨國際連結Fintech，打造環境培育台灣金融科技人才，朝向商轉企業化發展！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>    「普匯金融科技」今年6月25首推出的手機APP提供金融科技之投融資服務平台，全新、快速、方便、簡易的操作介面，提供超過25000個會員們，能夠隨時、隨地滿足借貸與投資需求，智能AI化的認證系統與獨特的風險控制模組更是APP與系統的最大特色。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>        普匯投資端提供會員們可以進行最低1000元小額分散投資，並且根據自身的風險偏好，選擇適合自己的風險報酬，建構一個屬於自己最適的投資組合，有自己的Portfolio再也不是遙不可及的夢想，讓投資這件事不再是高階經理人的專利，而是大家都可以親自參與。「借款端」普匯除了過去傳統金融審核的項目，新增了學校、社交等認證方式，從龐大的Data Base&nbsp;資料庫，進行大數據分析，讓借款人可以得到客觀評分的信用評等，取得更方便、更合理的借款利率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":4139,\"align\":\"center\",\"width\":648,\"height\":442} -->\n<div class=\"wp-block-image\"><figure class=\"aligncenter is-resized\"><img src=\"/upload/2019/07/圖片-13.png\" alt=\"\" class=\"wp-image-4139\" width=\"648\" height=\"442\"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>       普匯長期與學術界交流合作，在今年6月21日普匯與南部第一大校高科大共同舉辦「高雄盃」程式交易競賽，更在7月16日與東吳大學攜手發展「AI金融科技平台」。為將來共組金融科技公司暖身，普匯執行長姚木川表示：「希望透過實務與學界的交流，提供20年的金融經驗，接軌國際，孕育台灣未來的金融科技人才，期許與東吳大學合作，替下一代打造更美好的金融服務環境。」</p>\n<!-- /wp:paragraph -->', '2019-07-16 16:52:47', '普匯金融科技攜手東吳大學發展「AI金融科技平台」', 'article', 'publish', '2019-07-30 10:26:23', '', 0),
(4149, 1, '/upload/2019/07/圖片-123.png', NULL, '<!-- wp:paragraph {\"customFontSize\":18} -->\n<p style=\"font-size:18px\"><strong>普匯理念－金融科技的價值就是服務</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>透過各大銀行的公開資訊比較可以知道，普匯無論是「年化還款利率」、「手續費」、以及「風險評估模組」相較於過去的傳統金融業者，無論是放貸的價格或是程序，都來的更加的便宜與便利。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>還款利息不僅有別於傳統金融用名目利率計算使用IRR讓借款人反映真實價值，同時風險評估模組也有別於傳統金融審核的項目，新增了學校、社交等認證方式，從龐大的Data Base&nbsp;資料庫，進行大數據分析，讓借款人可以得到客觀評分的信用評等，取得更方便、更合理的借款利率，快速的放款速度也提供借款人在融通資金時可以更有效率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>除此之外，普匯的小額學生貸，提供學生們可以透過產品進行求學規劃與進修投資，普匯執行長曾表示：「希望透過普匯，幫助到更多傳統銀行服務不到的客戶，落實普惠金融。」</p>\n<!-- /wp:paragraph -->', '2019-07-20 00:29:10', '初期發展不被眾人看好的普匯萌芽期', 'article', 'publish', '2019-07-30 10:25:03', '', 0),
(4153, 1, '/upload/2019/07/71a5k6c1544566v9v5e7.jpg', NULL, '<!-- wp:paragraph -->\n<p>普匯是台灣最專業、安全的金融科技公司，我們的團隊成員有豐富的金融背景，特別是執行長姚木川先生於兩岸金融機構服務超過二十年，普匯團隊將以豐富的金融經驗，提供客戶最優質的金融借貸服務，此外普匯也積極尋求創新發展的機會，先後接受了「金管會、工研院」輔導，以及進駐了「Fintech Space」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>普匯進駐</strong><strong>Fintech Space&nbsp;</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Fintech Space是由金管會匯集國內、外金融科技產、官、學、研多方資源，所建立的國內第一座「金融科技創新園區」。在園區內，將提供金融科技領域相關的輔導，如「數位沙盒」與「監理門診」，幫助新創團隊進行創新研發與接軌市場。普匯金融在2019年5月進駐了Fintech Space，在園區內不斷進行更多創新、優化，希望帶給使用者更加的服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>普匯接受金管會、工研院輔導</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>為支持金融科技創新，鼓勵新創及科技業者與金融機構合作，共同打造數位化金融環境，金管會於2016年開始尋找具潛力的金融科技相關團隊，並進行輔導，同時普匯金融在2019年初以台灣最穩健優質的平台，接受金管會輔導；另一個重點輔導單位，工研院臺灣創新快製媒合中心，旨在協助媒合新創事業與快製聯盟業者，促成雙方合作。普匯定期接受工研院在技術、行銷、市場方面的輔導，讓產品逐步邁向穩健成長之路。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯金融科技是台灣最具潛力的新創團隊之一，積極尋求各方的合作機會，並與金管會、工研院等政府機關配合，希望以科技創新力量真正提台灣實現普惠金融！</p>\n<!-- /wp:paragraph -->', '2019-07-21 14:39:44', '普匯金融-台灣第一個接受金管會與工研院輔導的P2P平台，是您借貸的優先選擇！', 'article', 'publish', '2019-07-30 10:24:37', '', 0),
(4164, 1, '/upload/2019/07/DSC_0240.jpg', NULL, '<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>普匯積極深入校園，成為各大校金融科技顧問</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯積極深入校園，希望能與各校大專院校的同學建立深厚的連結，我們積極走訪淡江、東吳等各大專院校進行演講，將金融科技最新知識分享給同學們，此外，普匯更是接受了東吳大學、國立北商業大學、政大與高科大等學校的邀請，成為該校金融科技專業顧問，希望結合校方的力量，與同學一起掀開金融科技這層神秘的面紗。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>普匯於高科大舉辦的「高雄盃程式交易競賽」擔任評審</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯與國立高雄科技大學AI金融科技中心，於6月21日在高雄金融科技創新園區舉辦的「高雄盃程式交易競賽」擔任評審，普匯長期與學術界交流合作，促進金融科技在證券市場的應用與發展，發掘且培養有潛力的交易模型及撰寫策略程式之人才，一同攜手共創金融科技未來，為台灣的金融科技創新注入新活力，鼓勵創新動能！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":4165} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/07/134715-1024x722.jpg\" alt=\"\" class=\"wp-image-4165\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>普匯與東吳攜手發展「AI</strong><strong>金融科技平台」</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯長期致力於學界的交流與合作，於7月16日與東吳大學共同攜手發展「AI金融科技平台」，為將來共組金融科技公司暖身，普匯團隊透過數十年的金融經驗，回饋於學生與社會，希望透過實務與學界的交流，幫助學生接軌國際，孕育台灣未來的金融科技人才，期許與東吳大學合作，替下一代打造更美好的金融服務環境，為台灣的金融科技創新注入新活力。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯相信台灣的大學生充滿著改變的力量，希望能將金融科技的新知帶給同學們，並邀請同學們跟我們普匯金融一起用金融科技的力量改變台灣、改變世界！</p>\n<!-- /wp:paragraph -->', '2019-07-22 18:37:48', '普匯回饋，學界看得見', 'article', 'publish', '2019-07-22 18:39:23', '', 0),
(4171, 1, '', NULL, '<!-- wp:paragraph -->\n<p>出國遊學不再是夢想，也不再是富二代的代名詞，更不是城鄉差距的枷鎖。普匯提供有心學習的同學們出國遊學的機會，幫助學生走出台灣拓展視野、增廣見聞並培養國際觀，提供一個安全快速的遊學借貸方式，分期小額的還款大大地減輕學生們的負擔，讓學生們可以透過普匯自主完成支付遊學期間之學雜費與生活開銷等費用。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　普匯金融科技除了針對學生、上班族提供借款服務外，我們也希望將服務拓展到台灣的中小企業主，第一步將針對台灣占比相當大的產業-外匯車商，專為他們提供一套進口在庫車及代購車之融資方案，並且訂立此外匯車融資業務辦法，希望能幫助中小企業車商解決資金上的需求，並幫助他們拓展業務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　普匯更積極與通信行合作，推出分期付款超市服務，無論學生或是上班族，在普匯InFlux的APP中就可以選擇想要無卡分期的手機廠牌型號，透過與通信行的對接，消費者無須親自至店面即可獲得手機報價，投資者也僅需在線上投標為用戶買單，申貸流程快速，輕鬆滿足急需換新機的消費者需求。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　普匯致力與各產業相互交流合作，不斷創造服務機會，除了與凱基銀行進行廣告合作，共同為推廣Open Banking而努力，也與通訊行一同打造便利的分期付款超市，更不斷的規劃執行與外匯車商及留遊學廠商之合作，只為創造更優質的金融科技服務，普匯的用心你看得見。</p>\n<!-- /wp:paragraph -->', '2019-07-23 19:10:57', '普匯與產業合作無邊界', 'article', 'publish', '2019-07-30 10:29:48', '', 0),
(4174, 1, '/upload/2019/07/圖片-1234.png', NULL, '<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>普匯的成長歷程﻿</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>2018年8月普匯金融科技第一個產品正式上線，倒數24小時即將滿一週年，回顧來路： <br>→堅持「專業金融是核心、高端科技是助力，年輕族群是主力」 <br>→主動接受政府單位金管會、工研院及亞洲矽谷輔導，致力於創立真正的金科技平台 <br>→多方產學合作，協助學界與金融產業無縫接軌，不僅擔任高科大顧問及競賽評審、更邀約金融機構參與東吳大學發展AI金融科技平台共同作育英才、協助國立臺北商業大學產品商轉對接銀行 <br>→創立「分期付款超市」，協助提升實體經濟景氣，從通訊行到外匯車… <br>→時至今日 普匯更貼近市場，提供更多更快速便利的資金，讓萬物皆可分期，民眾享受更好的金融科技服務。</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>普匯會員數提升大幅提升</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>感謝年輕人的信賴，普匯會員數一路攀升，即將突破30 ,000人，憑藉「小額、快速」成交數千筆，金額突破6000萬，協助數千位年輕人渡過壞生活，普匯會持續提升科技能量，推出更多專業的普惠金融商品。。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>Web平台升級APP</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>為了提供數萬會員更高品質的體驗感，inFlux即將停止web系統服務，全面轉換APP，請會員速速下載「普匯inFlux」以及「普匯投資」兩支APP喔！<strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>普匯與你同在</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯始終陪伴你，讓你的青春在金融科技不留白，不管信貸、各種消費分期貸…，隨時善用你我的「手機ATM」，普匯inFlux！</p>\n<!-- /wp:paragraph -->', '2019-07-24 17:13:36', '普匯週年慶', 'article', 'publish', '2019-07-24 17:13:38', '', 0),
(4205, 1, '/upload/2019/08/圖片-3.png', NULL, '<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>近年台商出現「鮭魚返鄉」潮，今年累計已達約4441億，預計在八月底就能達成政府年初設立的5000億目標，而行政院政務委員龔明鑫也表示預計2019 年底回流的資金可能破台幣7,000 億元。後續在排隊回台投資的台商也還有四十、五十家。</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph -->\n<p><strong>大環境</strong><strong>-</strong><strong>中美貿易戰</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>中美貿易戰越演越烈，上個月初美國宣布課徵中國部分商品25%關稅，雖然中國極力希望能重啟談判，但目前態勢仍不明朗。對於在中國設廠的台商來說，要面臨到更嚴苛的考驗，因為大多數台商為製造業且以代工為主，在毛利已經偏低的情況下，再課徵25%的關稅可說是直接導致關廠。因此，多數選擇回台灣投資設廠，如此一來即可以保持出口的彈性，同時兼顧美中兩國的營收，台灣對中國比重為38%，對美國為14%均為非常高的比例，只要放棄任一邊的市場都是非常嚴重的損失。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>政府獎勵</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>台灣政府為了因應中美貿易展使台商回流的局勢，行政院推動「歡迎台商回台投資行動方案」，實施期程為3年（108年至110年），以企業需求為導向，提供客製化單一窗口服務，整合土地、水電、人力、稅務與資金等政策措施。以下簡述政策內容。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(一)滿足用地需求：進駐經濟部開發工業區前2年免租金，且預估新增產業用地873公傾。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(二)充裕產業人力：針對投資達一定金額，或擴廠打一訂標準的台商放寬外勞門檻，可以Extra制提高原先15％，以及享有外勞預先核配、1年內免定期查核。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(三)協助快速融資：匡列5,000億元額度，委由銀行以自有資金辦理專案貸款。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(四)穩定供應水電：專人協助加速用水計畫申請，同時積極辦理各項水資源建設管理工作；透過單一窗口及專案控管，加速用電取得。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(五)稅務專屬服務：財政部各地區國稅局設立聯繫窗口，提供稅務法規諮詢服務；國稅局成立專責小組與台商諮商，有效處理稅務疑義。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>普匯觀點：對台灣的影響</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>台灣最重要的是把握這次的機會，再創下一波大幅度的經濟成長，從勞工角度來看，能否利用這波產業回流，創造就業機會或薪資翻轉，解決長久以來低薪問題。從廠商角度來看，能否從單一廠商拓展到整個產業鍊，創造更龐大經濟效益。從產業角度探討，這波回流是否能創造整個產業的轉型，從原本代工導向轉為品牌生產，創造更高的毛利同時也帶動整體國家的經濟成長。普匯迎著這波趨勢，未來希望可以把借款端拓展到台灣中小企業上，希望能為這波回流的台商提供一股穩定的資金來源，也為台灣發展盡一份心力！</p>\n<!-- /wp:paragraph -->', '2019-08-02 15:55:53', '台商大回流！累積投資金額達4441億元！', 'article', 'publish', '2019-08-02 15:55:54', '', 0),
(4212, 1, '/upload/2019/08/1234.png', NULL, '<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>七月三十日金管會宣布三家純網銀申請者，將來商業銀行、連線商業銀行，與樂天國際商業銀行均通過審核，破除原本認為的3取2情形。主因是基於普惠金融以及科技創新，希望透過純網銀帶來的新金融服務使民眾更便利，以及帶動傳統金融的數位轉型。</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0075d4\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0075d4\" class=\"has-text-color has-medium-font-size\">台灣三家純網銀簡介</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>將來商業銀行:</strong>由中華電信持股41.9%，兆豐銀行25.1%、新光集團14%、全聯實業9.9%、凱基銀行7%、關貿網路2.1%。主要希望能服務被傳統銀行忽略的客群，如小微企業、信用小白等。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>連線商業銀行:</strong>Line持股49.9%，台北富邦銀行25.1%、中信銀5%、聯邦銀行5%、渣打銀行5%、台灣大哥大5%、遠傳電信5%。目標客群為LINE用戶，主打金融服務與LINE上相關服務做一站式整合。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>樂天國際商業銀行:</strong>由樂天持股51%、國票金49%。主要的客群為35～50歲白領客戶、樂天集團既有客戶，並主打台日O2O與台日雙幣數位存款。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0376a7\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0376a7\" class=\"has-text-color has-medium-font-size\">對消費者的影響-照顧更多族群，實現普惠金融</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>現階段台灣約有3400家實體銀行，加上隨處可見的ATM，金融服務相當普及，然而純網銀所要提供給消費者的是一個全新且便利性更佳的服務，利用App操作介面的便利性、金融服務的流暢度等，對於傳統金融那種抽號碼牌後坐著乾等的服務模式，產生劃世代的革新！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此外，純網銀也將服務到一些傳統銀行服務照顧不到的族群，例如在台灣瘩東南亞移工，這類客群礙於工作時間、信用評等等因素，而無法充分使用到傳統金融服務，如果純網銀可以補足服務到現有銀行接觸不到的客群，將會是差異化的最大亮點。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0479ab\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0479ab\" class=\"has-text-color has-medium-font-size\">對傳統銀行的影響-發揮鯰魚效應</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據麥肯錫的報告，2018年亞洲銀行業者的股東平均淨資產收益率（ROAE）平均從12.4％，下降到去年的10.1％。主要原因除了總體經濟的衰退外，就屬fintech業者的興起，然而銀行面對現在數位化的趨勢應該要更積極尋求轉型，用更開闊的胸襟擁抱數位金融，並透過自動化、AI和數據分析的創新來減少營運成本，同時讓顧客跟方便的使用金融服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0372a2\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#0372a2\" class=\"has-text-color has-medium-font-size\">普匯觀點</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>三家純網銀通過申請，代表台灣將進入線上金融的新世代，在這個現金流動快速的未來，最不能缺少的就是線上借貸服務，我們普匯金融的出現，就是希望能夠滿足社會上每個人對於借貸、投資的需求，我們對於服務對象不作過多的限制，甚至是還未出社會的學生族群都是我們的服務對象，此外，我們也極力將申貸與投資的流程精簡化，讓每個人能方便快速地上手，實現普惠金融。</p>\n<!-- /wp:paragraph -->', '2019-08-05 17:33:54', '純網銀時代來臨，三家申請者均通過', 'article', 'publish', '2019-08-05 17:36:27', '', 0),
(4220, 1, '/upload/2019/08/螢幕快照-2019-08-07-下午3.35.47.png', NULL, '<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>什麼是OIN組織？<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>OIN是一家智慧財產權公司，不同於其他支持開放原始碼軟體（FOSS）的非營利組織，由IBM、Red Hat、Novell、Sony、NEC、Philips 等六家公司共同合資在 2005 年 11 月成立，除了以上公司所投入之專利權外，也會於市場收購其他專利權。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>OIN的使命是提供創意與成果共享的平台<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>開源改變了我們的發明方式，有別於過去的同業商業競爭、傳統的專利權爭奪戰，以及異業合作時高額的專利費收取，OIN鼓勵共享專利、行動自由、分享新思想，甚至是新的發明，與開源社區一樣依賴於共享與自由，同時授權於全球防禦性專利池和專利非侵略性社區，可以在Linux中實現行動自由，已換取不侵略的承諾，成為其會員可在範圍裡共享資源，目前已有超過2000個組織加入OIN，免費獲得OIN專利，並獲得免版稅訪問其他OIN參與者Linux專利系統的權利。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>OIN無償交流不等於免費</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>越來越多的公司加入OIN，其共享資源背後也隱含著將會有權益上的損失，根據加入OIN合約的條款內容，成員間必須無償授權使用Linux系統相關應用專利，這意味著在獲得免專利費使用他人成果時，同時也失去本身收取專利費的權利，因此在共享的資源中，會失去對專利權的控制以及創意上的發想，甚至是產品的成果。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>新創是否皆應該加入OIN？以普匯為例<br> </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>隨著越來越多的大型企業加入，微軟Microsoft甚至把超過60,000項專利以免權利金的方式授權給OIN成員們使用，希望藉此吸引更多人加入這個龐大且日趨成熟的聯盟。新創公司在此可以得到豐富且多元的資源，包含企業參與的技術創新，以及產品開發的部分，然而是否大家「皆有需要」加入這個大家庭呢？在簽署合約之前是需要嚴加考量的範疇。以「普匯」為例，程式碼模型以business model為主要架構，搭配獨特的風控模組進行信用評等估算，並透過專業人士的經驗法則下決定風險評估因子，建構評等標準。因此普匯透OIN資源共享平台上，能取得其他產業資源相對有限，然而加入後卻可能喪失公司重要的know how與金融科技創新上的獨特性，間接導致失去市場優勢與商業價值。<br>因此我們知道未來新創公司是否無條件的加入OIN需要事先衡量市場定位，以及其產業特色、目標導向與專業能力，避免公司未來發展受限，謹慎評估方能建構雙贏之局面。</p>\n<!-- /wp:paragraph -->', '2019-08-07 15:39:37', '開放原始碼組織OIN（Open Invention  Network）     共享專利，創造「新創新契機」', 'article', 'publish', '2019-08-07 15:40:32', '', 0),
(4266, 1, '/upload/2019/08/螢幕快照-2019-08-15-下午2.23.04.png', NULL, '<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>金融科技正是時代的趨勢，新創公司如雨後春筍般冒出，每個團隊都希望能夠創造出改變世界的產品，而今天將要介紹幾間國內外的金融科技獨角獸！看看他們是如何利用金融科技解決傳統金融的痛點。</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#bc533b\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#bc533b\" class=\"has-text-color has-medium-font-size\"><strong>TransferWise跨國P2P匯兌服務平台</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>TransferWise創立於2010年，希望建立一個P2P的跨國匯款平台，讓換匯這件事不需要經過銀行端，這樣使用者就不會被迫接受一個相對差的匯率，甚至還要繳交高額手續費。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>TransferWise主打的就是方便、透明、安全、價格公道，在使用上可以用電腦和手機App，隨時隨地進行換匯，且網站上的申請流程相對簡潔清楚，只需要簡單幾個按鍵就可以輕鬆完成，所使用的匯率也是中立匯率，跟手續費一起公開透明給使用者看。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如此方便的服務也讓TransferWise成為歐洲炙手可熱的金融獨角獸，目前的市值高達16億美元，且他們也在2018年進駐香港，持續將服務拓展到亞洲，未來的前景十分看好。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#bc533b\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#bc533b\" class=\"has-text-color has-medium-font-size\"><strong>BREX- 新創的信用卡</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>成立於2017年初，在短短兩年間已經成為了估值26億美元的獨角獸，主要提供新創公司“企業信用卡”的服務，申請人僅需要銀行帳戶有超過 100,000 美元的存款即可。目前BREX推出了以下三個金融商品。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第一個產品是針對新創公司的企業卡，提供新創公司各種企業活動的各種回饋計畫，如搭Uber、Lyft可有7%回饋、差旅機票住宿4%回饋、餐廳3%優惠、訂等。第二個產品，是針對E-Commerce電商企業的企業卡，提供零利息的60天還款的刷卡服務，讓電商企業無需辛苦的融資或貸款也能夠應付企業週轉。創辦人不需要以個人信用額度來擔保，額度為每月營收的50%到100%，最高500萬美金。第三個產品，是針對生命科學公司的企業卡，包括製藥、生技、美妝品公司，提供採購實驗室設備3%回饋、會議門票7%回饋、差旅4%回饋。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#bc533b\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#bc533b\" class=\"has-text-color has-medium-font-size\"><strong>普匯-最值得信賴的金融科技公司</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯金融科技是台灣的金融科技公司，運用最新AI與區塊鏈技術，將金融產品線上化，我們公開所有交易訊息，提供給借貸雙方一個透明化且自由度高的交易平台，同時我們也注重個人隱私保護我們平台上每一個使用者。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>而普匯金融的理念就是希望能夠真正的在台灣達成普惠金融，讓每一個人都能以簡單、方便的方式將金融服務變成生活的一環，同時補足傳統金融無法提供的服務，因此我們的對象包含了學生、社會新鮮人等等的信用小白，提供他們方便快速又安全的借貸服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>現在普匯積極跟學界、產業界進行合作，也不斷優化我們的平台，希望能將p2p這個嶄新的金融服務帶進台灣，為台灣金融科技盡一份心力！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', '2019-08-15 14:26:01', 'Fintech逐漸主宰時代，這些新創報你知 !!!', 'article', 'publish', '2019-08-15 14:42:48', '', 0),
(4283, 1, '/upload/2019/08/圖片-1.png', NULL, '<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>在今年3月蘋果春季發表會上，蘋果發佈了Apple Card，由美國蘋果公司與高盛合作發行的網路銀行信用卡。</p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#a36611\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#a36611\" class=\"has-text-color has-medium-font-size\"><strong>結合Apply Pay，只要用iPhone就能申請</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Apple Card存放在Apple Wallet裡，iPhone用戶可以直接透過App線上申辦，而申請流程十分簡單也快速，核准後就能在iPhone上就能看到虛擬Apple Card，以此可於全球所有支援Apple Pay的商家消費。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#a36611\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#a36611\" class=\"has-text-color has-medium-font-size\"><strong>Wallet</strong><strong>使用介面設計</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Apple Card在Wallet上就能直接看到自己的消費記錄，透過容易理解的實時交易視圖，多少現金回饋、本月累計消費金額等都會直接列出，而Apple Map會清楚標記商家名稱和交易的位置，另外也會將購買自動匯總並以不同顏色編碼分類，使用者將可以更清楚管理自己的財務支出狀況。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#a36611\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#a36611\" class=\"has-text-color has-medium-font-size\"><strong>現金回饋每日結算！最高回饋3%！</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Apple Card中的Daily Cash回饋，是採用即時且無上限的方式，也就是在每筆消費後，就能立刻看見回饋入帳且立即使用。關於現金回饋，購買蘋果產品可以獲得3%的回饋，使用Apple Pay產品可以獲得2%的回饋，使用實體卡購物可以獲得1%的回饋，另外由於Apple Card屬於萬事達信用卡聯盟，所以萬事達的相關優惠用它也能得到喔。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#a36611\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#a36611\" class=\"has-text-color has-medium-font-size\"><strong>不收</strong><strong>取</strong><strong>年費、海外消費手續費等額外費用</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Apple Card不收取任何費用，包括境外交易費、年費、餘額轉帳費等，如果逾期繳款用戶也不必繳納滯納金或懲罰利息，但會有額外的利息累積到未付清餘額中，信用額度也會受到影響。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#a36611\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#a36611\" class=\"has-text-color has-medium-font-size\"><strong>用戶隱私與消費安全</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>相較於傳統信用的CVV安全碼（Card Verification Value）、消費者簽名及有效期限來提供身份驗證及防護，Apply Card使用的特殊安全芯片，創建出唯一的卡號，透過Face ID或Touch ID和一次性的動態安全碼授權，提高盜刷的困難度。另外這些資料並不會上傳蘋果資料庫，也就是說蘋果不會獲得用戶的信用卡消費記錄，而合作的高盛銀行也承諾，不會將用戶的消費記錄提供給第三方作為行銷或廣告用途。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#a36611\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#a36611\" class=\"has-text-color has-medium-font-size\"><strong>實體卡極簡設計</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Apple Card是MasterCard的鈦金卡等級，以鈦金屬為本體，全金屬全白底的高質感，正面有Apple Logo、持卡人姓名、晶片，背面有發行機構高盛銀行，跟發卡組織Mastercard的Logo，也因為跟Wallet結合，所以不用在卡面上印上卡號、日期、安全碼，外觀設計上十分簡潔，走一個極簡風。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#a36611\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#a36611\" class=\"has-text-color has-medium-font-size\"><strong>Apple\nCard</strong><strong>的推出，開</strong><strong>啟</strong><strong>行動支付的戰國時代</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金融科技的崛起，讓第三方支付以其平台優勢，跨越金融機構之間的聯繫，跳過金融體系直接與客戶連接，過去幾年間不斷跨足金融科技，尋求突破。而普匯App的各服務項目如同Apple Card，申辦簡單快速，介面設計除了簡單大方的風格，在使用方面也利於大家查看資料，另外在用戶隱私與安全也運用高端生物辨識科技及將資料於儲存亞馬遜AWS雲端來保護個資隱私，讓大眾都可輕易上手。普匯致力發展P2P網路借貸市場，希望如行動支付普及於眾人般，讓一般人民也都能享有借貸的權利。</p>\n<!-- /wp:paragraph -->', '2019-08-19 11:09:21', '一起來開箱APPLE CRAD～!', 'article', 'publish', '2019-08-21 14:38:09', '', 0),
(4311, 1, '/upload/2019/08/螢幕快照-2019-08-21-下午2.22.09.png', NULL, '<!-- wp:paragraph {\"customTextColor\":\"#3900a3\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#3900a3\" class=\"has-text-color has-medium-font-size\"><strong>美國學貸vs台灣學貸風氣</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　「讀書吃不好，出了社會更難溫飽。」台灣有約38％的學生背負著學貸，平均負債約台幣30萬；然而比起台灣，美國過六成大學生皆肩負龐大的就學貸款，平均負債約台幣100萬，對還沒找到工作的新鮮人來說，是極為驚人的數目，導致美國光學貸市場就累積了破兆美元的債款，而這些欠債的學生們一出社會，平均每個月都要還款超過一萬台幣，為降低學生們的還款負擔，各國愈來愈多P2P學生貸平台崛起，主打低於銀行貸款的利率，提供學生更優惠的貸款服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#3900a3\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#3900a3\" class=\"has-text-color has-medium-font-size\"><strong>SoFi</strong><strong>學生貸模式</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>　</strong>　SoFi即Social Finance，2011年創立於美國舊金山，以線上學生貸服務起家，主要客戶為千禧世代的大學生，透過學生貸款再融資模式，將學生貸款打包成債券在投資市場上出售，這讓學生們不像過往必須從銀行貸款，透過SoFi平台上網填寫基本資料，上傳證件，數日過後就能得知信用紀錄、現金流狀況及學校相關表現是否通過申請，一來省去銀行申辦的繁雜程序，便可以從私人方獲得資金，並且快速累積信用紀錄；二來，由於貸款利率遠低於銀行貸款，加上免手續費與年費，因此在去年五月，SoFi的會員數突破50萬，放貸金額更高達250億美元。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#3900a3\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#3900a3\" class=\"has-text-color has-medium-font-size\"><strong>SoFi</strong><strong>學生貸成功關鍵，「差別化低息＋資產證券化＋社群金融」</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　SoFi的成功絕非偶然，其客群定位與大部分P2P平台不同，大部分P2P平台通常將客群定位在無法於銀行借到錢的人，而SoFi將自己打造成「高學歷專業人才」值得信賴的金融服務公司，提供低於市場上其他貸款管道更低的利率，專注在為高信用、高學歷的高材生，提供與他們信用風險匹配的利率貸款，並透過社交媒體操作進一步降低違約率，因此學生貸服務推出後，就達到貸款違約率幾乎等於零的奇蹟；同時，它是全球第一家完成資產證券化的P2P公司，透過資產證券化獲取低成本金，別於以往次級房貸證券化的不堪過往，因有安全的信用評級把關，所以能吸引投資人或投資機構願意前往投資高材生<strong>；</strong>最後，SoFi透過很強勢的線上線下宣傳和營銷，成功建立在學生貸款領域最知名的品牌知名度，並透過建立一個對在學學生及校友都有益的社群，結合校友融資貸款模式，將貸款的學生與校友及機構投資者用社群聯繫起來，使得在校生有更廣闊的人脈資源，校友便可以幫助在校生與未來失業的畢業生找到適合的工作，達到「不只借錢，更交朋友」，因此，有高達98％的用戶會將SoFi的學生貸服務推薦給朋友，這對借貸平台來說是極高的評價。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#3900a3\",\"fontSize\":\"medium\"} -->\n<p style=\"color:#3900a3\" class=\"has-text-color has-medium-font-size\"><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　綜合以上，SoFi的成功不是機遇，其中一大關鍵為，美國學生貸風氣十分盛行，多數學生若未透過貸款就很難完成學位，因此SoFi透過本身的利率、社交、便利優勢，得以使它蓬勃發展；反觀台灣，由於社會風氣、家長及學生對貸款的觀念普遍保守，相較於美國的大學學費也並非那麼昂貴，學生貸的需求就不如美國。普匯金融科技洞察台灣學生需求，透過P2P線上借貸平台，我們服務不求多，只求能提供小額貸款，供需要的學生應急之用，透過全線上快速審核放款的程序，讓投資人的閒置資金，得以對急需用款的大學生有所助益，達到雙方互惠之效。<br></p>\n<!-- /wp:paragraph -->', '2019-08-21 14:27:43', '從美國SoFi學貸用戶數大增，看普匯P2P借貸平台發展', 'article', 'publish', '2019-08-21 14:29:39', '', 0),
(4325, 1, '/upload/2019/08/螢幕快照-2019-08-27-上午11.38.30.png', NULL, '<!-- wp:image -->\n<figure class=\"wp-block-image\"><img alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#7198b7\"} -->\n<p style=\"color:#7198b7\" class=\"has-text-color\"><strong>丹麥的金融環境走在時代的尖端 </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>坐公車沒帶零錢，出門忘了帶錢包，一堆硬幣充斥著口袋，這些煩惱從 2016 年起再也不是丹麥人的夢靨了，丹麥政府宣布於 2016 年 1 月起，進行無紙鈔交易，並且同時推動全國數位付款方式，多數店面取消了收銀機，改由電子支付、信用卡以及其他電子貨幣服務為主，便利、快速、好上手的金融科技服務充斥在民眾的日常生活。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#7198b7\"} -->\n<p style=\"color:#7198b7\" class=\"has-text-color\"><strong>日德蘭銀行(Jyske Bank A/S)</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><br>全丹麥第三大的銀行-日德蘭銀行(Jyske Bank A/S)，總部位於滑雪勝地錫爾克堡，在丹麥、法國、德國、直布羅陀、荷蘭、瑞士以及英國都設有分支。目前日德蘭銀行在丹邁的市佔率 7%，它同時也是哥本哈根證交所第二大銀行， 以及總部設在哥本哈根以外的最大銀行。 日德蘭銀行聲稱自己有別於其他銀行的非正式儲蓄方法，為了營照出開放的儲蓄環境，從 1998 年開始即放棄了傳統的銀行櫃台形式，取而代之的是每四位到八位業務員環做於圓桌的形式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#7198b7\"} -->\n<p style=\"color:#7198b7\" class=\"has-text-color\"><strong>日德蘭銀行推出負利率房貸 </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>日德蘭銀行推出全球第一個負利率房貸，提供了年利率為 -0.5 的十年期房貸，日德蘭銀行表示:「這項負利率房貸的借款人，將像往常一樣每月還款，但未償還房貸每月減少的金額將超過貸款人支付的金額。」同時日德蘭銀行的房地產經濟學家何伊夫(Mikkel Høegh)說:「我們不會直接把錢送到你手上，但你的債務每月所減少的金額將超過償還的金額。」 <br>實際上，負利率房貸實為名目利率，何伊夫表示透過房貸的申辦依然有手續費，貸款人仍然需要透過申請房貸而付費，因此不會如字面上的解讀而賺錢。負利率政策的實施，通常顯示放款銀行對於市場未來趨於謹慎的態度，因此藉由零利率、低利率甚至負利率來降低與規避風險。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#7198b7\"} -->\n<p style=\"color:#7198b7\" class=\"has-text-color\"><strong>普匯投資讓你不必擔心房貸 </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>透過普匯的投資端，投資者可以提前規劃房屋頭期款，定期定額的小額投資規劃，可根據自我的風險偏好選擇適合的投資標設立投資組合，再透過長時間的複利滾存，買房將不再是奢侈的夢想，「在家靠父母、出外靠朋友、借貸靠普匯」，讓安全、快速、信任度高的普匯投資平台當你的父母，陪你一起成為首購族。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->', '2019-08-27 11:47:59', '房貸負利率!丹麥日德蘭銀行成為全球首例', 'article', 'publish', '2019-08-27 11:48:00', '', 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(4447, 1, '/upload/2019/09/螢幕快照-2019-09-04-下午6.07.24.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><strong>4G</strong><strong>改變生活，</strong><strong>5G</strong><strong>改變社會</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　還在滿足於4G吃到飽嗎？想像一下未來馬路上不再出現壅塞交通，隨處可見自駕車在路上行駛，或是透過VR裝置，便可以即時體驗Live演唱會。因應物聯網及大數據日漸普及，新時代產物5G技術已被開發問世，不論是發燒話題自動駕駛、遠程醫療，智慧城市，或是人們愈來愈依賴的人工智能，都有仰5G技術得以實踐遠大目標。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　而5G技術是什麼？簡單來說，它能提供更快的網速、更大的容量，及更高的穩定性，其可連接的終端多達100萬台/千米，比起4G，它的傳輸速度可達每秒數十Gb，是4G的幾十倍、甚至百倍快，延遲率不超過一毫秒，行動數據量較4G亦增加了千倍，覆蓋率也明顯增強，4G到5G其跨度可相當於1G到4G，因此，5G技術正式上線，其帶來的影響不單單只是技術上的變革，而是大多數產業都可能透過5G有了大突破，屆時不只產業，人與人，人與物、物與物都真正進入萬物互聯的時代。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><strong>&nbsp;B2B</strong><strong>優先取得商機</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>　　</strong>雖然5G的優勢多不勝數，但布建5G基礎建設所需成本極其高，是4G的好幾倍，因此可以預知，電信業者的資費，以及5G智慧型手機的製作成本，對消費者來說都是相對昂貴，這對5G技術進入B2C市場形成較高門檻。然而在B2B市場的應用，很可能在5G技術上線初期就帶來商機，透過B2B企業的整合應用，估計會先對供應鏈業者發展帶來快速的成長。因此電信商在推動5G前期，理應會先著重在B2B市場的目標客群，而非價格敏感度高的B2C市場。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><strong>5G</strong><strong>開啟金融科技新時代</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　從電信業營收來源來看，金融業的佔比一直以來相對較低，但從1G到4G，金融業的進化是有目共睹，而4G時代，互聯網及金融科技漸漸興起，打破了傳統金融的格局，掀起了金融科技的浪潮，如今5G時代即將來臨，5G的優勢就是能快速處理海量數據、交互即時低時延，而近幾年金融數位化趨勢，愈來愈多金融業者特別注重行動裝置的移動服務推廣，5G的快速不僅能提升交易效率、簡化交易流程，更因為交互延遲、網路堵塞的狀況大幅減低，所以能讓用戶時刻保持在線，不怕網路因使用人數過多而當機，操作上也無須耗時等待；再者，有效掌管安全漏洞問題，能優化金融服務以提升顧客體驗，當金融科技有辦法嵌入到人們的生活，讓顧客隨時隨地享有不間斷高質量的智能服務，亦代表人類又往Bank 4.0前進一大步。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><strong>普匯觀點</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>　　初步預計2020年左右，5G技術將帶來大規模商轉，有更多的5G商用網路應用出現，雖然5G技術在各行各業的布局尚未明朗，5G技術在台灣也未正式上線，普匯金融科技現已提供顧客快速驗證、媒合放款的服務，別於傳統金融業，普匯的服務皆於線上申辦，倘若未來基礎建設及終端產品製造成本降低後，待5G技術漸漸普及於大眾，消費者就有辦法在手機介面完成更快速、更安全的交易，普匯金融科技在當前已將AI技術運用在智能辨識、信用風控等層面，期待未來能達到零時差的服務，不只快，還要快得安全又實在！<br></p>\n<!-- /wp:paragraph -->', '2019-09-04 18:09:38', '5G跑起來，商轉快快來', 'article', 'publish', '2019-09-04 18:09:39', 'investtonic', 0),
(4582, 1, '/upload/2019/09/messageImage_1568096805726-1.jpg', NULL, '<!-- wp:paragraph {\"customTextColor\":\"#386b83\"} -->\n<p style=\"color:#386b83\" class=\"has-text-color\"><strong>API</strong><strong>的本質是一種服務</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>為什麼今天我們只需要輸入簡單的關鍵字就可以下訂單、訂位呢？這些其實都是因為有API的技術，API就像是我們今天所有app和網頁運作的幕後功臣。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>API是Application Programming Interface的簡稱，即是「應用程式介面」，負責擔任應用程式和應用程式之間的橋樑，換言之，API即是在接收了你的指令訊息，而後告訴系統你要做甚麼，然後再把系統的回應傳回給你。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如此抽象的API到底是什麼呢？我們以一般便利商店的「補貨」機制等動作做比方。通常，一間便利商店的「商品區」和「庫存區」的擺設配置是不同的，今天一位負責補貨的員工，他必須要先知道擺放的規則，可能是要配合商店的行銷企劃、商品日期，也有可能是要擺的整整齊齊、一目了然等等，都是他的工作內容。在這個「補貨」的例子裡，商品可以換化成是數據，庫存區就像是資料庫，商品區則好比是我們平常使用的網頁或是APP，而負責把商品從庫存區擺放到商品區的員工就是扮演著 API 的角色，依照某種規則和規律運作著。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#386b83\"} -->\n<p style=\"color:#386b83\" class=\"has-text-color\"><strong>API</strong><strong>經濟帶來的便利和效率</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>API經濟是Fintech中的一種應用，興起關鍵主要來自於大資料庫的出現，當大型數據被規模性的使用時，API的串接就顯得更有價值，因為其大幅縮減了過去漫長的搜尋、比對的時間等等，而今天我們幾乎人人都有手機，透過手機和API的技術，讓很多生活相關的操作都可以快速完成，如下以機票訂票為例。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#386b83\"} -->\n<p style=\"color:#386b83\" class=\"has-text-color\"><strong>機票價格比較</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>一般來說，訂票除了可以直接透過航空公司的網站來操作，但更多的人會為了貨比三家，而改利用線上的旅遊服務中心去查詢各家的票價。以航空公司的網站來說，使用者可以直接填選表單以進行訂票的動作，在這過程中，會直接讀取航空公司內部的資料庫，提供滿足需求的資訊。然而，若從線上的旅遊服務中心來查詢了話，他們則會先透過網路技術匯集各家航空公司的資料庫，再利用API技術將使用者的需求指令傳送給各網站，再將網站回傳的資料傳送回使用者，以利方便比較。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#386b83\"} -->\n<p style=\"color:#386b83\" class=\"has-text-color\"><strong>Open API </strong><strong>帶來新的金融世界</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>API的使用之所以普及，來自於雲端技術的發展，有些企業採用自己開發的介面，然而大多則是由開發商直接提供，例如Google Maps API，如此的分工讓社會成為一個互補型的模式，有效增強企業間的合作關係。其次，還有一些電子商務平台會提供API給開發商，開發商就能夠在平台上提供商品檢索，雙方互利開啟了更多營收的管道，這樣雙贏的方式也是一種Open API經濟。如今，Open API經濟已經越來越普及，Google、亞馬遜等公司每天通過API處理的交易都是數十億筆以上，這樣的改變已不可小覷。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#386b83\"} -->\n<p style=\"color:#386b83\" class=\"has-text-color\"><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在這個凡事講求效率的社會裡，企業經營應跳脫內部資源的封閉模式，採行開放的API策略，以最簡化的方式達到目標，除可降低成本，也可以強化垂直產業的合作關係，也就是說金融產業從傳統走向Fintech是勢在必行，而普匯即是在這樣的趨勢下提供給使用者最完善也最安全的服務。</p>\n<!-- /wp:paragraph -->', '2019-09-10 14:23:39', 'API新經濟 帶你去看更大的世界', 'article', 'publish', '2019-09-10 14:32:53', '', 0),
(4692, 1, '/upload/2019/09/圖片-1.png', NULL, '<!-- wp:quote -->\n<blockquote class=\"wp-block-quote\"><p>萊爾富搶搭Open Banking(開放銀行)商機，宣布與彰化銀行合作，推出「App扣繳信用卡費」服務，民眾可以免拿帳單直接透過萊爾富App的繳費，目前已串聯五家銀行，成為台灣首家推出此服務的超商業者。 </p></blockquote>\n<!-- /wp:quote -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0e6838\"} -->\n<p style=\"color:#0e6838\" class=\"has-text-color\"><strong>台灣「開放銀行」分三階段開放</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>目前台灣金管會「開放銀行」採三階段開放，依序是商品資訊、客戶資訊及交易資訊，第一階段是第三方公司可透過共通的應用程式介面(API)，介接各銀行的商品資訊；第三方公司的客戶透過App可輕鬆比較各銀行商品資訊，例如房貸利率、信用卡利率。至於第二階段是開放客戶資訊，意即在客戶同意下，第三方公司提供帳戶整合服務。第三階段是開放交易資訊，指第三方公司進行帳戶整合後，進一步透過App直接連結帳戶扣款，也可調整帳戶間資金。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0e6838\"} -->\n<p style=\"color:#0e6838\" class=\"has-text-color\"><strong>隨時在線上快速繳費</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有別以往民眾繳費需透過實體銀行、限定超商或登入各銀行網路銀行操作帳戶扣款繳費，加上台灣金管會今年加速推動「開放銀行」措施，將過去民眾到銀行開戶、利用帳戶存提款、轉帳或投資各種金融商品，資料只侷限在銀行端擁有，如今只要取得用戶同意，即可讓銀行把資料釋放出來，將資料使用權還給用戶，也就是透過開放API與API結合，可隨時在線上即時繳納信用卡、水、電信與停車費等一般日常生活費用，達到繳費與日常生活結合之目的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>目前萊爾富首波已開通中國信託、花旗、樂天、星展與台灣中小企銀等五家銀行，後續仍會積極洽談其他銀行業者加入，迎戰傳統網路銀行，攜手彰銀API串接金流合作，預計帶動整體代收業績成長逾30%。未來彰銀也將持續增加繳費項目，並運用大數據分析會員不同層級的繳費特性，持續優化服務功能，讓會員可以體驗更多元、安全的金融服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#0e6838\"} -->\n<p style=\"color:#0e6838\" class=\"has-text-color\"><strong>全</strong><strong>方位</strong><strong>打造的普匯</strong><strong>金融科技</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯致力與各產業相互交流合作，不斷創造服務機會，除了與凱基銀行進行廣告合作，共同為推廣Open Banking而努力，也與通訊行一同打造便利的分期付款超市，更不斷的規劃執行與外匯車商及留遊學廠商之合作，以達到普惠金融，讓每個人都能以簡單快速的方式，將金融服務變成生活的一環，同時補足傳統金融業無法提供的服務，如同彰化銀行與萊爾富的合作，簡單、快速、客製化是目前金融科技的整體大方向，如同普匯想帶給大家的服務，朝這個方向邁進，相信未來還會有更多創新的金融服務可以為大家服務。</p>\n<!-- /wp:paragraph -->', '2019-09-19 12:00:39', '彰銀攜手萊爾富 推出超商API繳費應用', 'article', 'publish', '2019-09-19 12:00:40', '', 0),
(4770, 1, '/upload/2019/10/school.png', NULL, '<!-- wp:paragraph -->\n<p>近年來金融市場環境變化多端莫測，資訊科技的快速轉變，導致金融市場掀起了一波金融科技革命，而雲端運算、行動載具、社群網路等新興科技掀起浪潮，持續衝擊各行各業，尤其金融科技（Financial Technology）更直接為金融業的服務、產品、組織帶來改變。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「AI金融科技聯盟｣是由六大名校- 臺灣大學、政治大學、臺北商業大學、臺北大學、高雄科技大學，東吳大學，及普匯金融科技結盟，與金融機構合作，專注扎根金融科技與人工智慧所需之技術核心人才培育及中小企業金融市場服務。預計於2019年10月 30日於臺北商業大學盛大舉辦結盟記者會，產官學權威人士齊聚一堂，共同為台灣金融科技的未來發展盡一份心力!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>而為鼓勵青年學子實現對金融科技之創新創意，發掘人才並提供產學交流機會，本聯盟將於2019年度12月20日首度共同主辦「 AI金融科技創新創意大賽」，並計畫成為每年定期舉辦之大專院校活動。本比賽獲獎團隊，除獲得獎金之外，將接受主辦單位輔導，對接贊助金融機構，獲得實習，及優先受聘加入金融機構、金融科技公司之機會。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>除舉辦比賽之外，本聯盟更著重於後續的培育及輔導，知名學校、金融機構、加上金融科技專家的豐沛資源，相信這一定會是讓所有有志金融科技發展的同好交流成長的絕佳平台，期待大家一起加入號召，扶持更多台灣金融科技新秀早日登上世界舞台!</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>報名連結??? <a href=\"https://aifinpitchtw.com/\">https://aifinpitchtw.com/</a></p>\n<!-- /wp:paragraph -->', '2019-10-17 09:47:46', '台灣最具號召力【 Ai 金融科技聯盟】蔚然成形-六大名校與普匯金融科技結盟', 'article', 'publish', '2019-10-17 09:47:48', '', 0),
(4779, 1, '/upload/2019/10/123-1.jpg', NULL, '<!-- wp:paragraph -->\n<p>近年比特幣引發的熱潮持續吸引全球的目光，連帶引起世人、產業界與學術界對此領域的專研與關注。從應用領域來說，區塊鏈產業面又可分成「幣圈、礦圈、鏈圈」三大領域，那我們常說利用挖礦來得幣，到底挖礦是在挖什麼呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#006da3\"} -->\n<p style=\"color:#006da3\" class=\"has-text-color\"><strong>那些流通在市面上的比特幣從何而來？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>首要，需要先了解礦圈與幣圈二者是息息相關的，而比特幣之於人類文明歷史的意義，就是比特幣是人類文明史上出現的第一套「全球權威帳本」，這套金融帳本是唯一的，帳本上的數據不能被竄改，也不能被否認，每個電腦時時刻刻都保持著帳本的同步性與對照性。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>區塊鏈的世界裡有一群人被稱作「節點」，這些節點可以自由選擇要不要成為「礦工」，礦工負責「出產」虛擬貨幣，以比特幣來說，為了吸引節點們成為礦工以協助建構及運作比特幣區塊鏈，中本聰提供給礦工數枚不等的比特幣作為獎勵，隨著比特幣的價值越來越高，也出現越來越多人投入礦圈，爭相開採比特幣。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#006da3\"} -->\n<p style=\"color:#006da3\" class=\"has-text-color\"><strong>當礦圈人滿為患，到底最終誰能挖到比特幣？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>每一台礦機在每一個「區塊」要產出前，都需要競相解一道非常非常複雜的數學題目，這道題目的困難度根本不是人腦可以解決的，甚至連礦機都只能用湊答案的方式解出來，此時最先解出來的礦工就能獲得一筆固定數量的比特幣，就是所謂的「贏者全拿」！而近年，有越來越多人為了提升計算能力，會改為組團隊，來提升獲得獎勵的可能性。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此外，礦工們往往為了擁有更好的效率，會改裝自己的電腦，常見的方式像是改用 CPU（電腦處理器）、GPU（遊戲用顯示卡）、ASIC（特殊應用積體電路），上述這些效能更好的硬體工具，我們俗稱「礦機」。通常礦機須持續24小時維持運作，過程中會耗費大量的電力並產生大量的熱，所以需要搭配風扇以助散熱。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#006da3\"} -->\n<p style=\"color:#006da3\" class=\"has-text-color\"><strong>創意的挖礦方式，人人都可以參與 </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>近年發展出不是只有擁有高等的礦機，或是消耗大量的電能，才能夠挖礦，有幾種新穎的挖礦模式，讓更多的人能夠進入這個市場，一探究竟。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>交易挖礦</strong><strong>：</strong>指交易即挖礦，將手續費返還給消費者，此概念為交易所提出。為了促進用戶交易虛擬貨幣，用戶交易依據累進的交易手續費，獲得相對應的平台幣。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>社交挖礦</strong><strong>：</strong>主打當用戶於社群平台上發表了珍貴、自創的內容時，便可以透過社交挖礦賺取獎勵。用戶獲得的獎勵與其內容在網路上的影響力呈現正相關。簡單來說，當用戶為平台帶來越多流量，便能夠獲取越多的獎勵。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>按讚挖礦</strong><strong>：</strong>主張用戶創作內容所獲得的按讚數，應獲得相對應的獎勵。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#006da3\"} -->\n<p style=\"color:#006da3\" class=\"has-text-color\"><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>FinTech是將先端科技應用在金融領域上，以改善服務效率的概念。在這樣的角度下，區塊鏈所帶來的加解密簽帳技術，為每一個人帶來更安全、低成本且更有效率的金融服務，而普匯正這樣的產業，普匯擁有的是最頂尖的金融團隊，運用最新AI金融科技、區塊鏈等技術，將複雜金融產品線上化，讓所有用戶都能「隨時隨地、簡單、快速、隱私、安全的取得生活所需資金，並可獲得最大投資報酬率效益」。</p>\n<!-- /wp:paragraph -->', '2019-10-22 14:32:54', '新穎的挖礦方式，每個人都可以成為礦工！', 'article', 'publish', '2019-10-31 13:51:28', 'investtonic', 0),
(4796, 1, '/upload/2019/10/5566-2.png', NULL, '<!-- wp:paragraph {\"align\":\"center\",\"backgroundColor\":\"very-light-gray\",\"customFontSize\":27} -->\n<p style=\"font-size:27px;text-align:center\" class=\"has-background has-very-light-gray-background-color\"><strong>台灣金融科技獨角獸哪裡找?</strong>﻿</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\">胡潤研究院，是英國記者及註冊會計師胡潤(Rupert Hoogewerf)成立的專門研究編排百富榜的機構</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>為何獨角獸企業這麼受到關注?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p style=\"text-align:left\">獨角獸一詞最早來自2013年一位美國知名風險投資者，同時也是創投公司<br>「Cowboy Venture」創辦人Aileen Lee女士所提出，獨角獸企業為全球估值10美元以上的科技初創企業，這些企業創辦都不超過10年，獲得私募投資且都未上市，在2019年的統計中， 近500間獨角獸企業散落在全球24個國家與118座城市，其中坐擁全球一半GDP的中國與美國就佔了8成以上的的獨角獸企業。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>世界各國獨角獸公司的比較</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>中國超過美國，以206比203領先，中美兩國擁有世界八成多獨角獸公司</li><li>印度和英國排名第三第四，各有21家和13家；德國和以色列並列第五，各有7家；前五大國家占全球獨角獸九成多</li><li>矽谷以102家（21%）的獨角獸企業引領全球；粵港澳大灣區共30家獨角獸上榜，占全球6%</li><li>電子商務和金融科技以68家和56家獨角獸公司領先，其次是雲計算和人工智慧，分別有44家和40家</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:table {\"className\":\"is-style-regular\"} -->\n<table class=\"wp-block-table is-style-regular\"><tbody><tr><td><strong>全球獨角獸總部</strong></td><td>\n  　\n  </td><td>\n  　\n  </td><td>\n  　\n  </td></tr><tr><td>\n  <strong>排名</strong>\n  </td><td>\n  <strong>國家</strong>\n  </td><td>\n  <strong>獨角獸數量</strong>\n  </td><td>\n  <strong>排名</strong>\n  </td><td>\n  <strong>國家</strong>\n  </td><td>\n  <strong>獨角獸數量</strong>\n  </td></tr><tr><td>\n  1\n  </td><td>\n  中國\n  </td><td>\n  206\n  </td><td>\n  12\n  </td><td>\n  新加玻\n  </td><td>\n  2\n  </td></tr><tr><td>\n  2\n  </td><td>\n  美國\n  </td><td>\n  203\n  </td><td>\n  15\n  </td><td>\n  西班牙\n  </td><td>\n  1\n  </td></tr><tr><td>\n  3\n  </td><td>\n  印度\n  </td><td>\n  21\n  </td><td>\n  15\n  </td><td>\n  澳大利亞\n  </td><td>\n  1\n  </td></tr><tr><td>\n  4\n  </td><td>\n  英國\n  </td><td>\n  13\n  </td><td>\n  15\n  </td><td>\n  哥倫比亞\n  </td><td>\n  1\n  </td></tr><tr><td>\n  5\n  </td><td>\n  德國\n  </td><td>\n  7\n  </td><td>\n  15\n  </td><td>\n  愛爾蘭\n  </td><td>\n  1\n  </td></tr><tr><td>\n  5\n  </td><td>\n  以色列\n  </td><td>\n  7\n  </td><td>\n  15\n  </td><td>\n  芬蘭\n  </td><td>\n  1\n  </td></tr><tr><td>\n  7\n  </td><td>\n  韓國\n  </td><td>\n  6\n  </td><td>\n  15\n  </td><td>\n  阿根廷\n  </td><td>\n  1\n  </td></tr><tr><td>\n  8\n  </td><td>\n  印尼\n  </td><td>\n  4\n  </td><td>\n  15\n  </td><td>\n  馬爾他\n  </td><td>\n  1\n  </td></tr><tr><td>\n  8\n  </td><td>\n  法國\n  </td><td>\n  4\n  </td><td>\n  15\n  </td><td>\n  菲律賓\n  </td><td>\n  1\n  </td></tr><tr><td>\n  8\n  </td><td>\n  巴西\n  </td><td>\n  4\n  </td><td>\n  15\n  </td><td>\n  愛沙尼亞\n  </td><td>\n  1\n  </td></tr><tr><td>\n  11\n  </td><td>\n  瑞士\n  </td><td>\n  3\n  </td><td>\n  15\n  </td><td>\n  盧森堡\n  </td><td>\n  1\n  </td></tr><tr><td>\n  12\n  </td><td>\n  日本\n  </td><td>\n  2\n  </td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph {\"align\":\"right\"} -->\n<p style=\"text-align:right\"></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"right\"} -->\n<p style=\"text-align:right\"><em>來源:《2019胡潤全球獨角獸榜》</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table {\"className\":\"is-style-regular\"} -->\n<table class=\"wp-block-table is-style-regular\"><tbody><tr><td>\n  　\n  </td><td>\n  <strong>國家</strong>\n  </td><td>\n  <strong>獨角獸佔全球比重</strong>\n  </td><td>\n  <strong>GDP</strong><strong>佔全球比重</strong>\n  </td></tr><tr><td>\n  1\n  </td><td>中國</td><td>\n  42%\n  </td><td>\n  16%\n  </td></tr><tr><td>\n  2\n  </td><td>\n  美國\n  </td><td>\n  41%\n  </td><td>\n  24%\n  </td></tr><tr><td>\n  3\n  </td><td>\n  印度\n  </td><td>\n  4.30%\n  </td><td>\n  3.2%\n  </td></tr><tr><td>\n  4\n  </td><td>\n  英國\n  </td><td>\n  2.60%\n  </td><td>\n  3.3%\n  </td></tr><tr><td>\n  5\n  </td><td>\n  德國\n  </td><td>\n  1.40%\n  </td><td>\n  4.7%\n  </td></tr><tr><td>\n  5\n  </td><td>\n  以色列\n  </td><td>\n  1.40%\n  </td><td>\n  0.4%\n  </td></tr><tr><td>\n  　\n  </td><td>\n  全球其他國家\n  </td><td>\n  7%\n  </td><td>\n  48%\n  </td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph {\"align\":\"right\"} -->\n<p style=\"text-align:right\"><em>來源:《2019胡潤全球獨角獸榜》</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>獨角獸都來自那些企業?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>電子商務和金融科技佔全球獨角獸企業的31%，其次是雲計算與AI人工智能，這裡歸納了一些重點公司的整理</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>\n  <strong>排名</strong>\n  </td><td>\n  <strong>行業</strong>\n  </td><td>\n  <strong>獨角獸數量</strong>\n  </td><td>\n  <strong>佔總市值比例</strong>\n  </td><td>\n  <strong>排名</strong>\n  </td><td>\n  <strong>獨角獸數量</strong>\n  </td><td>\n  <strong>佔總市值比例</strong>\n  </td></tr><tr><td>\n  1\n  </td><td>\n  電子商務\n  </td><td>\n  68\n  </td><td>\n  9%\n  </td><td>\n  14\n  </td><td>\n  房地產科技\n  </td><td>\n  2%\n  </td></tr><tr><td>\n  2\n  </td><td>\n  金融科技\n  </td><td>\n  56\n  </td><td>\n  22%\n  </td><td>\n  15\n  </td><td>\n  消費品\n  </td><td>\n  4%\n  </td></tr><tr><td>\n  3\n  </td><td>\n  雲計算\n  </td><td>\n  44\n  </td><td>\n  7%\n  </td><td>\n  16\n  </td><td>\n  新零售\n  </td><td>\n  1%\n  </td></tr><tr><td>\n  4\n  </td><td>\n  人工智能\n  </td><td>\n  40\n  </td><td>\n  5%\n  </td><td>\n  16\n  </td><td>\n  區塊鏈\n  </td><td>\n  2%\n  </td></tr><tr><td>\n  5\n  </td><td>\n  物流\n  </td><td>\n  34\n  </td><td>\n  6%\n  </td><td>\n  18\n  </td><td>\n  新能源\n  </td><td>\n  1%\n  </td></tr><tr><td>\n  6\n  </td><td>\n  健康科技\n  </td><td>\n  27\n  </td><td>\n  3%\n  </td><td>\n  19\n  </td><td>\n  遊戲\n  </td><td>\n  1%\n  </td></tr><tr><td>\n  7\n  </td><td>\n  媒體與娛樂\n  </td><td>\n  24\n  </td><td>\n  8%\n  </td><td>\n  20\n  </td><td>\n  網路安全\n  </td><td>\n  1%\n  </td></tr><tr><td>\n  8\n  </td><td>\n  共享經濟\n  </td><td>\n  22\n  </td><td>\n  11%\n  </td><td>\n  21\n  </td><td>\n  即時通訊\n  </td><td>\n  &lt;1%\n  </td></tr><tr><td>\n  9\n  </td><td>\n  軟件與服務\n  </td><td>\n  21\n  </td><td>\n  2%\n  </td><td>\n  22\n  </td><td>\n  機器人\n  </td><td>\n  1%\n  </td></tr><tr><td>\n  10\n  </td><td>\n  生命科學\n  </td><td>\n  18\n  </td><td>\n  3%\n  </td><td>\n  23\n  </td><td>\n  虛擬與增強現實\n  </td><td>\n  1%\n  </td></tr><tr><td>\n  10\n  </td><td>\n  大數據\n  </td><td>\n  18\n  </td><td>\n  2%\n  </td><td>\n  23\n  </td><td>\n  3D印刷\n  </td><td>\n  &lt;1%\n  </td></tr><tr><td>\n  12\n  </td><td>\n  新能源汽車\n  </td><td>\n  15\n  </td><td>\n  2%\n  </td><td>\n  23\n  </td><td>\n  航天\n  </td><td>\n  2%\n  </td></tr><tr><td>\n  12\n  </td><td>\n  教育科技\n  </td><td>\n  15\n  </td><td>\n  2%\n  </td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph {\"align\":\"right\"} -->\n<p style=\"text-align:right\"><em>來源:《2019胡潤全球獨角獸榜》</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>美中對於獨角獸企業的發展政策</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>美中兩大國雖然在獨角獸企業佔比數極為接近，但兩國發展獨角獸的模式極為不同，美國支持主張開放競爭下的科技創新，而中國則透過國家政策全力扶持既定新技術與新科技的發展，希望透過政府的支持加速的拉近兩國之間的科技實質差距，其中中國最具知名的4大獨角獸BATJ(百度、阿里巴巴、騰訊、京東)，在海外的美國、香港等地掛牌上市為其代表，這些企業正透過中國可轉換股票存託憑證機制(CDR)回歸中國A股市場。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\">中國存託憑證(ChineseDepositoryReceipt,CDR)指在境外上市公司部分已發行上市的股票託管在當地保管銀行，由中國境內的存託銀行發行，在境內A股市場上市，以人民幣交易結算，供國內投資者買賣的存託憑證，從而實現股票的異地買賣</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯觀點</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在上述排名中可以看到全球在金融科技業不管是獨角獸數量還是總市值排名都是業界的翹楚。普匯致力於發展台灣金融科技，台灣自2017三讀通過金融監理沙盒後成為世界上第5個擁有監理沙盒的國家，而普匯在金管會FinTechSpace特選市值破億團隊，有望成為台灣新創公司中的獨角獸。</p>\n<!-- /wp:paragraph -->', '2019-10-24 09:53:21', '胡潤研究院發佈《2019胡潤全球獨角獸榜》', 'article', 'publish', '2019-10-31 13:51:23', '', 0),
(4898, 1, '/upload/2019/10/binance.jpg', NULL, '<!-- wp:paragraph {\"customTextColor\":\"#6a099e\"} -->\n<p style=\"color:#6a099e\" class=\"has-text-color\"><strong>幣安美國（Binance US）開放註冊！</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>總部位於舊金山的幣安美國（Binance.US），是大型加密貨幣交易所幣安美國所(Binance.com)設置的分部。於日前9月23日晚上開放註冊和存款，此外，幣安美國將首先支援六種主流的加密貨幣：比特幣（BTC）、以太幣（ETH）、瑞波幣（XRP）、比特幣現金（BCH）、萊特幣（LTC）以及穩定幣（USDT）。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>而在Binance.US的公告中，添加了幣安交易所的原生代幣「幣安幣（BNB）」成為第七種上架的加密貨幣，其實，幣安執行長 CZ 趙長鵬稍早也利用推特暗示，幣安幣（BNB）將會成為美國的功能型代幣。此外，關於幣安幣的相關訊息，官方也在公告中，特別直接寫出幣安美國在零手續費優惠結束後，將按每筆交易，收取固定 10％ 的交易手續費，而這有別於一般交易所會按照交易金額大小，調整收取費用的比例。而公告也指出，在幣安交易所裡有註冊的用戶，他們的帳戶資金並不會自動轉移到幣安美國交易所中，因為幣安美國是獨立運營的實體。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#6a09a8\"} -->\n<p style=\"color:#6a09a8\" class=\"has-text-color\"><strong>幣安美國(Binance.US)解決過去幣安(Binance)封鎖的美國人 </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>今年六月國際反洗錢組織 FATF 出台最新的「加密貨幣指導規範」，這是一個全球超過200國監管機構必須遵守的指導，面對這樣的趨勢，幣安交易所改變了經營策略。當時數據顯示幣安交易所正在收緊平台下的「低度驗證帳戶」，將要「重新審查部分用戶帳戶」，不符資格的用戶將會被禁止於該交易平台交易，也就是說，未通過風控的用戶只能拿來使用管理錢包與資金，而無法在Binance上進行交易、充值。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>面對上述新的規範，協議最後指出，幣安幣不再為美國人服務，正式的宣告幣安將封鎖美國市場。然而，值得注意的是封鎖全美市場的同時，該公司也正與另一間公司合作協議專門為美國市場量身訂做的案子，即是「幣安美國」。此次幣安美國的上線，即是為了服務大量該國用戶的解決方案，但是由於當地監管，註冊服務仍排除包含紐約在內共的13個州。對於幣安幣無法在美國全面使用，幣安美國的首席執行長\n凱瑟琳·科萊（Catherine Coley）在今年七月份曾提及：「雖然幣安美國無法在我個人生長的州提供服務，是令我十分沮喪的，但是請各位放心，我們的使命就是把服務帶到那些你們稱之為家的地方。」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#6a09a8\"} -->\n<p style=\"color:#6a09a8\" class=\"has-text-color\"><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如今快速進步的市場，若停滯不前，經濟必然遭到更多的國家超越，而其中無論是關於虛擬貨幣或是金融科技，都將採取漸進的方式推行業務，讓更多的人能夠接受與參與其中，正如普匯金融科技公司的創辦理念—「將金融服務普及大眾」，我們的使命正是將金融服務帶到那些你們稱之為家的地方。</p>\n<!-- /wp:paragraph -->', '2019-10-29 10:16:58', '幣安美國（Binance US）正式上線，開始美國的法幣交易', 'article', 'publish', '2019-10-31 13:51:17', '', 0),
(4917, 1, '/upload/2019/10/1030-1.jpg', NULL, '<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image -->\n<figure class=\"wp-block-image\"><img alt=\"\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>FinTech金融科技華麗登場！AI人工智慧的突飛猛進，改變了你我的生活型態，更為金融產業帶來爆炸式創新革命與新契機！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>今年集結最多大學及金融機構的產學合作，於2019年10月30日在臺北商業大學誓師聯盟。包括：國立臺灣大學、國立政治大學、國立臺北商業大學、國立臺北大學、國立高雄科技大學，東吳大學等六所名校，以及新光銀行、康和證券、上海銀行、遠東銀行、凱基銀行等金融機構的熱情贊助，與獲得金管會輔導、金總加持的普匯金融科技，首發產學結盟合作，成立「AI金融科技聯盟｣，挹注充沛專業技術、實務資源與經驗，將金融科技的發展推向世界!　</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>為培育台灣AI金融科技人才，「AI金融科技聯盟｣成立三部曲：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第一步將於2019年12月20日舉辦「全國大專院校AI金融科技創新創意大賽」，鼓勵青年學子積極參與金融科技發展，提供最佳年輕創意舞台！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第二部曲，串起「產學合作交流」，透過實務金融合作，讓師生深度參與研發金融科技過程，為金融科技領域開發更多具創新效率、更安全的金融服務環境！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第三部曲，持續不斷的各種創新創意競賽互動，及深度多方產學合作，藉由實務操作「培育台灣金融科技人才，衍生企業商機」，邁向世界舞台。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>與會貴賓，也是金融產業的大家長- 金管會張傳章副主委表示，「AI金融科技聯盟｣能夠集結六所頂尖大學，與產業界合作，組成人工智慧及金融科技的聯盟，而且未來還會舉辦AI金融科技創新創意大賽，同時發掘校園的創意，相信對國內產業提升一定有助力，金管會樂見這種有益金融科技產學合作的活動，一定會予以支持。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金融總會吳當傑秘書長表示，「AI金融科技聯盟｣是產創學跨域結合的平台，對金管會推動的金融科技人才培育是相當好的服務及幫助。在金管會的指導下，金融總會的任務是培育人才、金融科技園區、及金融科技大展。而聯盟代表的普匯金融科技是金融科技園區的團隊，而贊助的金融機構也是園區合作的廠商，因此金融總會跟聯盟的目標是一致的，未來有兩點期許: 1. 希望聯盟與金融總會進一步合作，在金融科技園區的場域可以有更多合作空間。2. 「AI金融科技聯盟｣難得的集結了這麼多頂尖學校跟金融機構，希望未來有更多學校及金融機構加入，發揮更多綜效。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>日益水乳交融的AI人工智慧與Fintech金融科技的結合，雖為傳統金融帶來巨大衝擊，卻為人類日常生活帶來了更多便利！這一場科技創新的洗禮正席捲全球，AI金融科技發展適逢趨勢潮流起飛，台灣絕對不能缺席。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>「AI金融科技聯盟」相信未來參與聯盟的有志人士會越來越多，期望串起全國大專院校、金融或金融科技業者，一起為台灣AI金融科技發展打拼！</p>\n<!-- /wp:paragraph -->', '2019-10-31 10:27:26', '「AI金融科技聯盟｣台灣首發最大產學結盟記者會 －   六大名校與普匯金融科技結盟', 'article', 'publish', '2019-11-27 16:51:52', '', 0),
(4939, 1, '/upload/2019/11/LINE-BANK.jpg', NULL, '<!-- wp:paragraph -->\n<p>金管會於七月底揭露了純網銀的核准名單，總共3間業者獲得設立許可，包括將來商業銀行、樂天國際商業銀行、以及將主打LINE Bank的連線商業銀行。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在台灣擁有龐大2,100萬活躍用戶的LINE，不僅佔了9成的台灣人口數，其涉獵領域橫跨通訊、社群以及金融，其中LINE Pay用戶更在今年五月突破600萬，數目相當可觀。而野心勃勃的LINE更打算攻占台灣金融市場，為用戶打造虛擬的純網路銀行LINE Bank，讓用戶生活的每一個細節與LINE緊緊相扣。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#9830ad\"} -->\n<p style=\"color:#9830ad\" class=\"has-text-color\"><strong>打造一目了然的整合性金融服務</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>LINE Bank籌備處執行長黃以孟在LINE Converge大會上，提出了LINE Bank的理想架構。他指出台灣擁有眾多競爭激烈的金融機構，每個人手上可能都有滿滿來自不同銀行的存摺、保單或投資產品，卻沒有一個平台可以將這些資料整合，讓用戶可以在單一地方一次掌握所有個人資產狀況。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>LINE Bank打算以Open banking的概念，創造一個開放銀行，透過Open API與所有銀行對接。讓用戶只需要開立一個銀行帳戶、與一間銀行往來，就能一目了然存款、消費金融、保險、基金投資等整合服務概況。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#9830ad\"} -->\n<p style=\"color:#9830ad\" class=\"has-text-color\"><strong>創造年輕人的個人信用評比機制</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>LINE Bank另一個目的為補足傳統銀行的缺陷，善用大量的消費者數據，開發新的信用評等模式，讓傳統銀行不願意貸款的年輕人對象，有另一個建立信用評等的地方。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>事實上，LINE Bank的母公司LINE Financial在日本就已經開發出一個個人信用評等系統LINE Score。LINE也與瑞穗銀行、東方公司（Orient）合作，透過投資由LINE Financial全額出資成立的子公司LINE Credit，提供消費者無擔保借貸服務，因此LINE早已具備數位資訊下的信用評等經驗。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>相較同時期被獲准成立的另外兩個純網銀來說，LINE擁有更龐大的使用者數據，包括將近全台灣數位使用者的社群行為模式，在資料庫上算是具備一些優\n勢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#9830ad\"} -->\n<p style=\"color:#9830ad\" class=\"has-text-color\"><strong>資安問題成為隱憂</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>由於近來LINE被爆出個資安全不夠可靠的負面消息，將可能影響日後LINE Bank在台灣的推廣。對此，董事長黃仁埈聲明，LINE會將銀行客戶的所有資料放在台灣，以化解金管會以及部分使用者的疑慮。不過在資安問題漏洞百出的現代，開放銀行的概念能不能順利進行，還有待觀察。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"customTextColor\":\"#9830ad\"} -->\n<p style=\"color:#9830ad\" class=\"has-text-color\"><strong>普匯觀點:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>周全的信用評比機制</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯會利用貸款申請人的學歷、社群媒體以及過去的財務訊息等做為信用評比的參考機制，並配合大數據資料來建立每個申請者的信用評比額度，讓投資者放心，並減少許多不必要的風險。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>完善的資安保護</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>使用者的個人資訊保護，向來是普匯最重視的工作之一。所有的資料都經過加密保護，並且使用Amazon Web Services雲端服務平台，同時員工也在嚴格的訓練和規範之下，保證個資決不外洩。</p>\n<!-- /wp:paragraph -->', '2019-11-04 11:20:07', '致力於成為「全民的銀行」，LINE Bank 強勢進入台灣金融市場', 'article', 'publish', '2019-11-07 12:21:19', '', 0),
(4943, 1, '/upload/2019/11/123.png', NULL, '<!-- wp:paragraph -->\n<p>現在機器人逐漸的走進了人們的生活，可以在很多領域代替人們從事工作，有的可以打掃衛生、陪伴孩子和家人、有的送餐迎賓等等</p>\n<!-- /wp:paragraph -->\n\n<h2 class=\"has-text-color has-vivid-cyan-blue-color\"><strong>什麼是迎賓機器人?</strong></h2>\n\n<!-- wp:paragraph -->\n<p>迎賓機器人是集語音識別技術和智能運動技術於一身的仿人型高科技產品，顧客可自主選擇想要互動的介面，機器人帶有豐富的表情，時常靈活的動作這些都能提升顧客的興趣從而達到一個好的效果。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>機器人可按設定的路線自主行走，並且能自主的躲避障礙物，遇到障礙物時，機器人能發出語音提醒，感應性強，可根據語音控制行走引導，導遊接大堂經理功能兼備。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>迎賓機器人整體設計思路按照人性化的思路，主要應用了語音識別技術、圖像識別技術、電機控制技術、計算機通信技術、單片機應用技術、機械設計技術、材料成型技術，涉及到訊息、機械、材料、美學等多學科。</p>\n<!-- /wp:paragraph -->\n\n<h2 class=\"has-text-color has-vivid-cyan-blue-color\"><strong>AI機器人在未來將取代重複性高之工作</strong></h2>\n\n<!-- wp:paragraph -->\n<p>技術升級及產業鏈完善，為迎賓機器人的發展創造了很優越的條件，國際機器人協會（IFR： International Federation of Robotics）於今年9月發佈的最新報告指出，自2013年以來，全球服務型機器人市場規模年均增長率為23.5%，預估2019到2022年將以年均40%的複合成長率急速增長，國內銀行業已陸續有迎賓機器人的推出，但在證券業，兆豐證券是第一家推出迎賓機器人服務的券商。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>兆豐證券董事長陳佩君表示，面對少子化、高齡化的社會趨勢下，可預見未來金融業在客戶服務、作業流程中重複性質較高的工作，導入機器人的應用會愈來愈普遍。通過影像辨識、語音及語意分析、深度學習、大數據等金融科技的應用，既可以解決傳統客服占線待機的問題，在分公司的實體場域透過真人與機器人的協作，也能作為迎賓接待、客戶服務、商務導覽、整合行銷的輔助工具。因此，兆豐證券在進行長時間的服務場景規劃及行為編輯後，推出這項創新的Fintech服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>近年台灣產業不只是關注在高科技產業的發展，金融科技的發展也是如火如荼，將來AI不單單只是一個抽象概念而已，生活周遭也開始出現了越來越多的自動化機器人，網銀的出現也大幅地降低了人力成本，雖然很多可重複性的工作會被替代，但也同時會有許多新穎的工作出現，所以人們如何駕馭機器人是台灣未來發展的重點之一。</p>\n<!-- /wp:paragraph -->', '2019-11-06 17:28:19', '兆豐證券首創具備AI語音的迎賓機器人 – 小貝', 'article', 'publish', '2019-11-13 14:09:42', '', 0),
(4947, 1, '/upload/2019/11/003-1.png', NULL, '<h2 class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>什麼是P2P網路借貸?</strong></h2>\n\n<!-- wp:paragraph -->\n<p>P2P ( Peer to Peer )可以翻譯成為「網路直接借貸」，意思就是網路用戶直接貸款給借款人，就是個人對個人的直接金融，屬於民間借貸的一種。<br> P2P網路直接借貸因為去除了銀行金融仲介的費用，不僅借款利率低，從投資人角度看也是有利的，可大幅拉高投資報酬，不用一定要把錢借給銀行。</p>\n<!-- /wp:paragraph -->\n\n<h2 class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>投資P2P有什麼該注意的?</strong></h2>\n\n<!-- wp:paragraph -->\n<p><strong>團隊背景 :</strong> 要求創始人或高階主管必須至少有一位在國內外傳統或知名金融機構有過貸款、理財或投資的經驗。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>資金來源 : </strong>小額信用貸款、個人抵押貸款、小型企業貸款等等小額分散風險投資。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>合法合規與否</strong><strong> : </strong>依台灣現行法規，P2P業者提供進行眾籌（crowd funding）的平台，在借貸活動上，僅能擔任媒合的中立角色，不能有債權債務、保本和自融自放等金融行為。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>資金託管模式 : </strong> 隨著通道模式暴露出來的資金池問題越來越嚴重，一些第三方支付平台開始推出了『資金託管』服務。用戶（投資人和借款人）需在第三方支付機構創辦虛擬獨立帳戶，投資人先把錢充到其在第三方支付上的帳戶裡，當進行投資交易時，第三方支付把投資人的錢直接撥到借款人帳戶，用戶的資金全程由第三方支付負責託管，並開設獨立專用帳戶，實現專款專用，與平台自由資金完成分離，避免資金池的風險。</p>\n<!-- /wp:paragraph -->\n\n<h2 class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>當我投資P2P後違約率上升該怎麼辦?</strong></h2>\n\n<!-- wp:paragraph -->\n<p>資訊中介業者違約風險由投資人自行承擔，風險分散在所有投資人身上，不會集中在平台。即使違約飆升，平台也不須因賠付而承擔債務。雖說風險分散在投資人身上，但因為P2P是小額分散投資，因此當違約率受到系統性風險而增加時，對投資人的影響也僅僅是淨報酬下降而已。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯做為一家新創P2P公司，用戶使用後有什麼評價?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":4955} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/11/001-1.png\" alt=\"\" class=\"wp-image-4955\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":4956} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/11/003-1.png\" alt=\"\" class=\"wp-image-4956\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":4950} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/11/002.png\" alt=\"\" class=\"wp-image-4950\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":4952} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/11/004.png\" alt=\"\" class=\"wp-image-4952\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯做為一家P2P新創公司，在使用者的推廣下大受好評，普匯不只是驗證快速，在金管會的嚴格監控下普匯合法性更是無庸置疑，在等待機會進入投資的您還在等什麼呢?</p>\n<!-- /wp:paragraph -->', '2019-11-07 11:53:38', '投資P2P真的安全嗎?幫您介紹新穎投資方法', 'article', 'publish', '2019-11-13 14:06:10', 'investtonic', 0),
(4979, 1, '/upload/2019/11/66144.jpg', NULL, '<span style=\"color: #000000;\">傳統金融科技式微，現今金融科技的發展與應用不容小覷，而 P2P投資與網路借貸正是現今火熱的一項趨勢。只要上網搜尋P2P投資、P2P借貸或P2P借貸Ptt，都會有很多介紹，但是複雜的文字還是讓我們對於P2P很陌生。因此，此篇文章將帶大家深入了解P2P借貸問題與p2p相關資訊。</span>\n<h2><span style=\"color: #000000;\">P2P是什麼？網路借貸相關知識大解密</span></h2>\n<h2><img class=\"alignnone  wp-image-4985\" src=\"/upload/2019/11/unnamed.png\" alt=\"\" width=\"694\" height=\"301\"></h2>\n<span style=\"color: #000000;\">P2P是一種全球性的金融創新服務，P2P是英文 Peer to Peer的縮寫，意即「個人對個人」，是一種金融民主化的精神。但是在台灣並沒有很多人真正了解P2P到底功用何在，聽到的也都有可能只是片面的解釋，然而在現今大數據的時代下，使用區塊鏈技術，發展網路交易是一大趨勢。</span>\n\n<span style=\"color: #000000;\">P2P就是一個無形的網路平台，當現在有個需要資金的借款人提出借貸申請後，平台就會運用區塊鏈的技術，開始尋找可以媒合的投資人進行配對、搓合，再用更簡單的話來講，P2P就像是一個中間媒介，為借貸雙方進行金錢的流動處理，因此我們可以是借款人也可以是提供資金的投資人。因為去除了銀行金融仲介的費用，不僅借款利率低，從投資人角度看也是有利的，可大幅拉高投資報酬，不用一定要把錢借給銀行。</span>\n<h2>&nbsp;</h2>\n<h2><span style=\"color: #000000;\">借貸風險高嗎？帶你快速掌握普匯金融P2P借貸問題</span></h2>\n<span style=\"color: #000000;\"><img class=\"alignnone wp-image-4986\" src=\"/upload/2019/11/66144.jpg\" alt=\"大學生借貸平台\" width=\"471\" height=\"471\">&nbsp;</span>\n\n<span style=\"color: #000000;\">大家一定會想關於P2P借貸問題與風險，在這種網路交易的平台上我的資金及隱私是否安全、是否在借錢出去後能安全收回款項？</span>\n\n<span style=\"color: #000000;\">台灣的P2P借貸相較於各國起步晚，但透過「經濟部」的監管以及金管會等相關機關的管控，除了規定P2P業者不得吸金、融資，亦不可以發行理財商品，再加上與金管會合作建立的「金融監理沙盒機制」，台灣 P2P 的發展漸趨成熟。</span>\n\n<span style=\"color: #000000;\">其實台灣的金融信用體制是很完善的，我們擁有國家級的信用資料庫機構去判斷個人或企業的信用分數，裡面的資訊不僅完整更在多數P2P平台中使用。用較淺顯易懂的話語來說，假設今日我要進行貸款，當我填寫好所有資料以及進行相關認證後，P2P平台就會依據個人過去的工作或是收支狀況計算出一個分數，系統再依照分數尋找適合的投資人！加上媒合的期間個人資料都不會被公布在平台上，因此發生風險的機率也大大降低。</span>\n\n<span style=\"color: #0000ff;\"><a style=\"color: #0000ff;\" href=\"https://www.influxfin.com/\" target=\"_blank\" rel=\"noopener\">普匯金融</a></span><span style=\"color: #000000;\">為台灣第一個接受金管會與工研院輔導的P2P借貸平台，並與許多大學及金融機構進行產學合作。包括：國立臺灣大學、國立政治大學、國立臺北商業大學等多所名校；以及新光銀行、上海銀行等金融機構的熱情贊助，並獲得金管會輔導、金總加持的普匯金融科技，成立「AI金融科技聯盟｣，挹注充沛專業技術、實務資源與經驗，在安全、隱私上都擁有領先的科技技術保護，是網路借貸的優先選擇！　</span>\n<h2><span style=\"color: #000000;\">大學生借貸平台｜普匯金融P2P借貸好方便</span></h2>\n<span style=\"color: #000000;\">學生及沒有社會經驗的年輕人是很難跟銀行借到錢的，而且銀行規定個人在無擔保的情況下，個人最高總負債依規定不可逾月收入的22倍，銀行一般只會核貸10~15倍的金額給借貸者。學生多為打工族，在提供銀行月收入證明時會被認為是不穩定收入，會使銀行的放款意願降低，且年輕人初步入社會的過渡期，因為沒有金融信用累積，更降低了在銀行取得貸款的機率。</span>\n\n<span style=\"color: #000000;\">因此</span><span style=\"color: #0000ff;\"><a style=\"color: #0000ff;\" href=\"https://www.influxfin.com/\" target=\"_blank\">普匯金融</a></span><span style=\"color: #000000;\">秉持著幫助年輕人的精神，僅要求提供身分認證及簡易財務證明便可進行借款，而使用者提供的資料也會加密後保存在AWS上，以隱密、速度、貼心的原則站在使用者觀點提供服務。讓普匯不論在各大社群媒體上，以及網路論壇 (google搜尋 “P2P借貸Ptt” )，都有大量的討論及許多正面的迴響。</span>\n<h2>&nbsp;</h2>\n<h2><span style=\"color: #000000;\">公開透明的p2p投資、借貸，首選普匯</span></h2>\n<span style=\"color: #000000;\">普匯作為一家P2P網路借貸新創公司，同時也是知名的</span><span style=\"color: #0000ff;\"><a style=\"color: #0000ff;\" href=\"https://www.influxfin.com/%E6%88%91%E8%A6%81%E5%80%9F%E6%AC%BE/\" target=\"_blank\" rel=\"noopener\">大學生借貸平台</a></span><span style=\"color: #000000;\">，我們每天都在解決每個投資人和借款人的疑難雜症，並且建立數據分析使用者在各個階段可能會遇到的問題並提供解決方法，在使用者的推廣下大受好評，普匯不只是驗證快速，在金管會的嚴格監控下普匯合法性更是無庸置疑，還在觀望P2P借貸的您，還在等什麼呢？ 趕緊進入</span><span style=\"color: #0000ff;\"><a style=\"color: #0000ff;\" href=\"https://www.influxfin.com/\" target=\"_blank\">普匯官網</a></span><span style=\"color: #000000;\">了解更多吧！</span>', '2019-11-20 18:41:23', 'P2P借貸是什麼？為您介紹時下年輕人最常使用借貸方式', 'article', 'publish', '2019-11-20 18:55:59', '', 0),
(5509, 1, '/upload/2019/11/123.jpg', NULL, '<!-- wp:paragraph -->\n<p>金管會9月6日公布，核准國泰人壽與易遊網合作試辦保險電子商務，即運用API技術串接易遊網與國壽的網路投保平台，可一站完成投保旅行平安險，預計最快12月正式進沙盒實驗，最長實驗一年，這也是首宗保險監理沙盒成功獲准。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>國壽數位發展部分析，國人出國旅遊頻率持續增加，2018年國人平均每人出國0.71次，年輕數位族群在進入職場後旅遊頻率更隨之飆高，國壽網路投保每年約6.4萬件保單，其中有93％是旅平險。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>就目前網路投保規定，如果消費者出國要投保只能在保險公司所設的網站上投保旅平險，而旅行業應專業經營，也不能兼營保經代業務，即不能銷售保單，所以消費者若在旅行業網頁上買了機票跟飯店，同時想加保旅平險，就得連接回保險公司的網站，重新填寫資料，但經過如此多重繁瑣步驟，或許會消耗消費著的耐心，進而影響投保意願。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>國泰人壽發現此問題，因此從客戶體驗為出發點，攜手國內自由行服務平台首選 ezTravel 易遊網合作，讓用戶可以直接在易遊網上使用已註冊的會員身分，直接投保國壽的旅平險，運用Open API串接，程序也縮短至僅需四道，耗時1分鐘，就能達成旅遊一站式的服務體驗，將投保選項整合於行程規劃中，保證客戶在最具保險需求意識的當下就能夠被滿足，也讓使用體驗更加簡化與流暢。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在金融科技技術面，國泰人壽以微服務模式將投保流程拆分為多個模組，透過 Open API 將保險服務延伸至易遊網，並引進 API Management 平台進行維運管理。對於網路服務中最嚴謹的資安部分更是不敢輕忽，落實 5 大層級管理機制，包括網路安全機制、惡意軟體監控、裝置媒體控管、電子商務安全、及時監控機制，讓民眾可以安心在易遊網進行投保。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在安全與隱私的部分，普匯運用最新AI與區塊鏈技術，將金融產品線上化，我們公開所有交易訊息，提供給借貸雙方一個透明化且自由度高的交易平台，同時也運用高端生物辨識科技及將資料於儲存亞馬遜AWS雲端來保護個資隱私，保護我們平台上每一個使用者。現今普匯積極學界、產業界進行合作，也不斷優化我們的平台，希望能將p2p這個嶄新的金融服務帶進台灣，為台灣金融科技盡一份心力！</p>\n<!-- /wp:paragraph -->', '2019-11-22 10:33:27', '國壽攜手易遊網推旅遊保險一站式服務', 'article', 'publish', '2019-12-17 16:07:32', '', 0),
(5535, 1, '/upload/2019/11/shutterstock_1126117115.jpg', NULL, '<!-- wp:paragraph -->\n<p>生活中難免會遇到急需一筆現金的時候，不管是換新手機、裝潢房屋、每年報稅，或是修車，甚至旅遊等等，通常這筆費用並不高，但或許你無法在短時間內湊出這筆資金，因此當你在急需這筆現金的時候，你不需緊張，因為你有個很簡單的選擇，就是快速小額貸款，那現在就帶大家來好好了解線上小額貸款 。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>在ptt上也能看到許多人會在Loan板上討論線上小額貸款的資訊 (可搜尋關鍵字「小額信貸ptt」)，以下就讓普匯小幫手來幫你做整理。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>小額信貸是什麼?</h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>從字面上來看，小額信貸就是<strong>金額不高的信貸</strong>，而他們之間的差異只有貸款額度而已，小額信用貸款通常是以個人或家庭為核心的經營類貸款，其主要的服務對象為廣大工商個體戶、小作坊、小業主，一般而言通常為10萬以下小額信貸。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>借款人不需要提供擔保，其特徵就是債務人無需提供抵押品或第三方擔保僅憑自己的信譽就能取得貸款，並以借款人信用程度作為還款保證的。由於這種貸款方式風險較大，一般要對借款方的經濟效益、經營管理水平、發展前景等情況進行詳細的考察，以降低風險。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>線上小額信用貸款好嗎？</h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>1. 貸款範圍較廣：普匯針對大眾<strong>短時間內的資金周轉</strong>，提供小金額、無需擔保品的貸款。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>2. 申辦方式超便利：普匯透過完全線上，讓申請流程更加簡單、快速且便利化。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>3. 貸款方案選擇多：由於申貸金額不高，除了申請條件簡單，渠道也多，不管是在普匯、銀行或是貸款機構，都有機會找到適合自己的方案。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>抵押貸款vs信用貸款</h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>1. 方式不同：申請信用貸款是借款人憑個人信用申請的貸款，無需提供抵押物作擔保，只要有穩定的工作和收入，具備按時足額償還貸款本息的能力，即可提出申請。而抵押貸款是借款人提供符合貸款機構要求的抵押物作抵押申請的貸款。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>2. 要求不同：申請信用貸款必須擁有良好的信用記錄，以及穩定的收入和工作。而申請抵押貸款時，只要抵押物符合要求，貸款機構對信用情況及收入的要求可以適當放寬。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>3. 放款時間不同：抵押貸款在申請時需要對抵押物進行評估和辦理抵押登記等手續，所以放款速度較慢，一般需要十五個工作日左右;而信用貸款只需對借款人的資質情況作考察，所以兩三天就能放款。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>公開透明的小額信貸｜首選普匯</h2>\n<!-- /wp:heading --><!-- wp:image {\"id\":3931} -->\n\n<p><img alt=\"小額信貸\" src=\"/upload/2019/06/04.png\" /></p>\n\n<p>&nbsp;</p>\n<!-- /wp:image --><!-- wp:paragraph -->\n\n<p><a href=\"https://www.influxfin.com/\" target=\"_blank\">普匯金融</a>運用AI智能科技與安全風控模組，將專業金融產品與線上簡易方式搭起投資人與借款人的橋梁，以5大特色，安全、簡單、快速、隱私、低風險高報酬為主，尤其在快速小額貸款的部分，主張「快速三小時內核貸，最快一小時放款」，另外在金融科技發展下，我們的產品採用「全自動線上審核系統」，完全替代了以前人工審核的無效率，普匯就是快。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>普匯觀點｜線上小額貸款好方便</h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>小額借款靈活提供了現代的資金需求，緩解急需用錢的困難，促進其走向自我生存和發展。它既是一種金融服務的創新，又是一種扶貧的重要方式。無論是選擇抵押貸款還是信用貸款，每個貸款產品適合不同的客戶群體，但主要還是要看貸款的額度、利率、難易程度、放款速度等多方面權衡考慮，進而選擇適合自身的借款產品呦！</p>\n<!-- /wp:paragraph -->', '2019-11-26 11:07:35', '小額信貸｜快速又合法的申辦管道帶你快速掌握', 'article', 'publish', '2021-01-18 08:18:15', NULL, 1),
(5878, 1, '/upload/2019/11/東.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><strong>東協(Association of Southeast Asian Nations，英文簡稱ASEAN)</strong>，<strong>是集合東南亞區域國家的一個政府性國際組織，現有10個國家，創始國 : 印尼、馬來西亞、菲律賓、泰國、新加坡，後面加入的有汶萊、柬埔寨、寮國、緬甸、越南。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>新加坡主導融資，是東協金融科技公司的第一大區域基地</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>自2014年以來，對東協的投資增長了30倍以上，截至2019年9月達到了11.4億美元的新高。隨著越來越多的投資者和金融科技公司將目光投向了東協強大的經濟基礎，金融科技的發展逐漸加快。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>儘管其他東協國家加快了其國內金融科技行業的發展，但新加坡擁有相比其它成員更成熟的金融科技領域，仍繼續吸引東協內最多的資金。至今，在2019年的總資金中，超過一半（51％）的金額流入新加坡，顯示新加坡仍然是金融科技公司的首選基地。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>東協各地金融科技公司的融資成熟度各不相同</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>根據調查結果，在東協的金融科技公司普遍對未來的資金需求持樂觀態度，幾乎一半的受訪者有信心在下一輪融資中籌到超過1000萬美元的資金。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>新加坡金融科技公司的資金也分佈在所有金融科技類別中，其中保險科技，支付和個人理財是分布最多的地方。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>在印尼，由於該國人數不斷壯大的中產階級對信貸的需求很大，投資者將目光投向了其他貸款公司。投資者在馬來西亞金融科技公司尋求機會，以開發支付，財務和會計解決方案；在泰國，投資者將資金投入了InsurTech和投資技術公司。在越南，投資者偏愛支付金融科技，這在發展金融科技行業的早期階段對於經濟體來說很普遍。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>隨著數位銀行的到來，金融科技公司看到了更多的商機</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>東協金融科技領域的一項進展是開放了數位銀行的許可。雖然擁有數位銀行許可證將使金融科技公司能夠擴大其提供的服務範圍，但只有21％接受調查的金融科技公司表示有興趣申請許可證。儘管如此，幾乎有三分之二（65％）的金融科技公司認為，他們將能夠抓住機會提供創新的解決方案，並積極與該地區新興的數位銀行進行合作。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>人才問題仍然是金融科技區域擴張的一大挑戰</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>對於一半以上接受調查的金融科技公司來說，人才缺少仍然是一個挑戰，阻礙了它們在區域性擴張方面的發展。東協金融科技公司中有十分之六的公司從一開始就具有地區野心，因此人才問題是他們在計劃擴張以確保更大成功機會時需要考慮的問題。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>從該地區的經濟潛力和監管環境到投資者對東協的濃厚興趣，很明顯的，該地區對世界各地的金融科技公司都充滿了希望。成功在該地區擴張的關鍵將在於金融科技公司如何應對東協內部的多種文化，監管環境和商業習慣。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\"><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>在東南亞急速發展金融科技下，亞洲地區的發展已逐漸追上歐美地區，而台灣普遍金融科技公司還正在萌芽的階段下，普匯致力於提供最新穎的金融新創產品，且我們願意把資金花在資訊人才培育上，台灣一直都不缺乏培育人才的資源而是缺少了投資人對於金融科技的信心，我們有自信在往後的日子能夠追趕上其他國家的腳步。</strong></p>\n<!-- /wp:paragraph -->', '2019-11-28 12:26:10', '東協金融科技的發展', 'article', 'publish', '2019-11-28 12:26:11', '', 0),
(5937, 1, '/upload/2019/12/shutterstock_1081849367-2.jpg', NULL, '<h2><span style=\"font-weight: 400;\">過去申請貸款的流程</span></h2>\n<span style=\"font-weight: 400;\">首先確認資格，需年滿20歲以上，無信用瑕疵之本國國民；第二步，準備相關文件，如身分證、工作證明（勞保卡或在職證明）、所得證明（薪轉存摺或扣繳憑單）；第三步，客戶就可以提出申請貸款；第四步，銀行人員徵信，會先與客戶口頭洽談，初步了解借款用途、所需金額、借款期限、償還來源、還款方式及擔保條件等以及辦理徵信、授信審核後再決定是否核准貸款。第五步，過件者進行對保、撥款，經銀行核准的案件，會洽請客戶辦理對保、簽約等手續，如果貸款案件有徵提擔保品時，還會請客戶辦妥抵押權設定及投保火險、地震險等各項手續後再辦理撥款。</span>\n\n<span style=\"font-weight: 400;\">&nbsp;</span>\n<h2><span style=\"font-weight: 400;\">快速輕鬆線上貸款｜最新潮流方式大公開</span></h2>\n<span style=\"font-weight: 400;\">2018年</span><a href=\"https://www.influxfin.com/\"><span style=\"font-weight: 400;\">inFlux普匯金融</span></a><span style=\"font-weight: 400;\">建構全台第一個AI無人化投融資科技平台，以結合｢金融專業｣與｢高端科技｣，提供獨有的線上金融服務模式，架構雙向投資融資，資金流通更為便捷，讓所有用戶都能隨時隨地、簡單、快速、隱私、安全的取得生活所需資金，並可獲得最大投資報酬率效益，相較於以往需要親自備齊文件到銀行辦理，普匯的線上申貸服務，省下親自到銀行申貸時間且無須手填表格，讓您輕輕鬆鬆也能在家線上貸款。</span>\n<h2><span style=\"font-weight: 400;\">線上對保意思？｜注意事項不藏私</span></h2>\n<span style=\"font-weight: 400;\">對保是貸款核準後到撥款前最後一個流程，借款方需要攜帶身分證去銀行進行面對面保證契約簽訂，以及確認契約內容的借款金額、貸款年限、貸款利率等，此動作我們稱作對保。</span>\n\n<span style=\"font-weight: 400;\">就是確認借款人或保證人本人簽訂借款或保證契約的程序，主要目的是要確認簽署人是否為本人，另外我們也有義務在借款方簽署銀行借據或本票時，告知借款方是否可以提前清償，有無限制或違約金的部分等等，對所簽的借款條款做一個說明。如今透過線上就能自助簽約，無須帶著證件印章特地到現場辦理，既省時又方便。</span>\n\n<span style=\"font-weight: 400;\">&nbsp;</span>\n<h2><span style=\"font-weight: 400;\">普匯觀點</span></h2>\n<img class=\"alignnone  wp-image-3931\" src=\"/upload/2019/06/04.png\" alt=\"線上申貸失敗\" width=\"470\" height=\"430\">\n<h3><span style=\"font-weight: 400;\">普匯為了你一直走在最前端</span></h3>\n<span style=\"font-weight: 400;\">普匯金融在無論是在申貸、審核、對保的部分全面線上化，對於線上申貸失敗時，我們也將會快速的通知客戶，方便客戶直接修改資料，以利後續的申貸流程，完全替代了以前人工審核較低的效率，並且主打5大特色，安全、簡單、快速、隱私、低風險高報酬，普匯就是快。</span>\n\n<a href=\"https://www.influxfin.com/\"><span style=\"font-weight: 400;\">普匯金融</span></a><span style=\"font-weight: 400;\">服務最受年輕人的信賴，是台灣唯一同時接受「金管會、金融總會、工研院、亞洲矽谷及資策會等五個政府單位輔導的金融科技公司，也是台灣最高知名的學生貸平台，而我們志在貼近市場需求，提供更高品質體驗，客戶隨時隨地享有安全、簡單、快速，又不失溫度的平台服務，讓借貸投資流程全線上化，滿足需要資金的客戶需求。</span>', '2019-12-02 15:36:59', '線上申貸｜幫你解決短期週轉的煩惱', 'article', 'publish', '2020-02-18 15:09:02', '', 0),
(5942, 1, '/upload/2019/12/shutterstock_619031555.jpg', NULL, '<span style=\"font-weight: 400;\">許多人在貸款前，避免自己冒險、受騙，都會想先聽聽大家的意見或是使用心得，所以有不少人會上論壇，像PTT Loan-貸款、現金卡、代償討論專版(信用貸款ptt)或是mobile 101上詢問信用貸款試算或貸款條件查詢等相關貸款條件。關於信用貸款的問題，普匯金融科技在這裡幫您整理出一系列的信用貸款問答，讓您可以知己知彼，百戰百勝。</span>\n<h2><span style=\"font-weight: 400;\">信用貸款是什麼?&nbsp;</span></h2>\n<span style=\"font-weight: 400;\">信用貸款（credit loans）是指以該借款人的信譽來做依據而發放的貸款，借款人不需要提供任何擔保，僅憑自己個人的信用就能取得貸款，並以「借款人信用程度」作為還款保證。信用貸款是銀行長期以來的主要放款方式，因為無抵押風險較大，一般銀行要對借款人的還款收入來源、職業類型、個人名下債信狀況等情況進行詳細的考察確定風險後，才予以放款。</span>\n<h2><span style=\"font-weight: 400;\">信貸年利率是如何計算的?</span></h2>\n<span style=\"font-weight: 400;\">大部份使用信貸的還款方式都是本息均攤分期付款的方式，而每個月繳的額度都是一樣的，在借款的時候會根據借款人的條件與信用評分來決定利率及額度，普匯金融科技在自動評分借款人信用的技術使用了有別於一般銀行的人工辨別方式，全程採取線上自動進行，大大降低了借款人時間上的不確定性。</span>\n<h2><span style=\"font-weight: 400;\">沒有時間去銀行申辦，怎麼辦？｜線上貸款超方便</span></h2>\n<span style=\"font-weight: 400;\">不要擔心，</span><a href=\"https://www.influxfin.com/\"><span style=\"font-weight: 400;\">普匯金融</span></a><span style=\"font-weight: 400;\">秉持著金融專業、強調AI無人化，打造「你的手機ATM」，因此所有流程都是線上申辦喔！您可以透過app線上貸款，最快1個小時就可以到帳，讓您快速方便借錢！</span>\n<h2><span style=\"font-weight: 400;\">學生線上貸款｜財務自主、培養好信用</span></h2>\n<span style=\"font-weight: 400;\">學生身份一般在銀行除了就學貸款之外是沒辦法借到資金的，急需用錢時往往不是找家人就是身旁的親朋好友，</span><a href=\"https://www.influxfin.com/\"><span style=\"font-weight: 400;\">普匯金融</span></a><span style=\"font-weight: 400;\">特別為了學生推出了【學生貸】及【學生手機貸】的方案，讓未來畢業後具有良好還款能力或現階段有打工的學生也能在就學時期借到資金，普匯能解決銀行所不能辦到的問題。</span>\n<h2><span style=\"font-weight: 400;\">我沒有學過理財，該如何開始呢？</span></h2>\n<img class=\"alignnone  wp-image-5944\" src=\"/upload/2019/12/貸款條件_Pic1.jpg\" alt=\"信用貸款ptt\" width=\"337\" height=\"729\">\n\n<img class=\"alignnone  wp-image-5946\" src=\"/upload/2019/12/貸款條件_Pic2.png\" alt=\"信用貸款ptt\" width=\"402\" height=\"411\">\n\n<span style=\"font-weight: 400;\">若您不清楚可以使用普匯的甚麼產品，普匯金融的</span><a href=\"https://www.influxfin.com/\"><span style=\"font-weight: 400;\">官網</span></a><span style=\"font-weight: 400;\">與app都有簡單明確的資料，可以做貸款條件查詢喔！就連利息也不用自己算，不管您是借款人還是投資人，普匯App內有提供信用貸款試算，僅需輸入您欲借金額、還款月數、年利率，就可以知道了，讓您輕鬆做出自己的理財規劃。</span>\n\n<span style=\"font-weight: 400;\">&nbsp;</span>&nbsp;<span style=\"font-weight: 400;\">&nbsp;&nbsp;</span>\n<h2><span style=\"font-weight: 400;\">信用貸款好嗎？</span></h2>\n<span style=\"font-weight: 400;\">簡單來說，只要您過去信用狀況良好，那麼信用貸款就是個好選擇，可以讓您迅速解決資金需求，只需要關心利率與額度是否是您想要的就好。</span>\n\n<span style=\"font-weight: 400;\">但是，對於信用狀況有瑕疵的人來說，可能就會有一點風險，因為您有可能金額數超過信用貸款試算的結果，而無法申辦成功，所以信用狀況對於信用貸款來說是非常重要的。</span>\n<h2><span style=\"font-weight: 400;\">普匯觀點</span></h2>\n<img class=\"alignnone  wp-image-3931\" src=\"/upload/2019/06/04.png\" alt=\"信用貸款是什麼\" width=\"441\" height=\"403\">\n\n<span style=\"font-weight: 400;\">在傳統觀念中，一般人提到貸款，總是第一個想到負債、欠錢；但你知道嗎？債務也有好壞之分，如果是一昧的利用刷卡來滿足自己的吃喝玩樂慾望，久而久之變成卡奴，導致收支入不敷出，帶來沉重的經濟負擔和心理壓力，這就是不好的債務；相反的，若透過貸款，可以讓申貸人增加現金流，帶來收益的投資，雖然每個月需償還本金利息，但若理財的當，還可以創造更多收益。</span>\n\n<span style=\"font-weight: 400;\">因此，無論您是想要買一台好車、辦一場夢幻的婚禮、好好享受一趟出國旅行或是完成夢想，那麼您現在就可以趕快打開</span><a href=\"https://www.influxfin.com/\"><span style=\"font-weight: 400;\">普匯金融</span></a><span style=\"font-weight: 400;\">的app，我們提供安全且便利、容易申請，最快24小時內就能核貸的服務，快速申辦信用貸款，讓您輕鬆解決燃眉之急！</span>', '2019-12-02 15:48:39', '你知道你的信用值多少錢嗎？貸款條件報你知！', 'article', 'publish', '2020-02-18 15:09:34', '', 0),
(6278, 1, '/upload/2019/12/dfoj.png', NULL, '<!-- wp:quote -->\n<blockquote>\n<p>隨着技術假得愈來愈真，一些影視業者開始以此作藝術創作。在假新聞透過社交媒體迅速散播的當下，不少學者擔心Deepfake會進一步模糊真相，破壞社會秩序。</p>\n</blockquote>\n<!-- /wp:quote --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>BuzzFeed曾經在YouTube發布一支以FakeApp製成的「深度造假」影片，畫面分成左右兩半，片中西裝筆挺的美國前總統歐巴馬坐對着鏡頭發表演說，右方為演員Jordan Peele，他一舉一動都與左方的歐巴馬表情、動作、口徑一致。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>Deepfake深度造假，是一系列造假技術的統稱，由Deep learning深度學習與Fake偽造的混合詞，是人工智能的一種形式。利用生成對抗網絡（Generative Adversarial Network, GAN）的機器學習技術，將已有的圖像和影片疊加至目標圖像或影片上，例如換臉、像傀儡般的同步動作、偽造聲帶、唇形同步等。在GAN中有兩個機器學習模型，一個扮演造假者，在數據集上訓練後生成假視頻；另一個則扮演檢測者，不斷地檢測這些假視頻，一直到它再也不能檢測出結果是假的，當用於訓練的數據集越大，做出的Deepfake視頻也越真實。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>深度學習技術一直被視為只為人工智能研究社群所用，直至2017年末，一位網民在人氣論壇Reddit上發布了偽造的色情影片，把Gal Godot的樣貌「換」到色情片女演員身上，另外假新聞、惡意惡作劇和金融欺詐也是經常透過深度造假兒發生的事。自此，Deepfake就代表了利用人工智能自動生成、幾可亂真、或許立心不良的偽造影片。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"customTextColor\":\"#952f2f\"} -->\n\n<p><strong>懸賞1000萬美元，打假AI換臉，Facebook發起Deepfakes檢測挑戰賽</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>由Facebook領銜的一群學校和公司，決定共同創建Deepfake視頻檢測挑戰賽，Deepfake Detection Challenge，壕擲1000萬美元，懸賞能最好檢測出利用Deepfake技術製造的假視頻，包括微軟、MIT、牛津大學，加州大學伯克利分校等業界學界聯合支持。臉書首席技術官表示，挑戰的目標是&ldquo;創造出每個人都可以使用的技術，以便更好地檢測AI何時被用來改變視頻以誤導觀眾。&rdquo;Deepfake檢測挑戰賽將包括一個數據集和排行榜，以及撥款和獎勵，以刺激行業創造新的方式來檢測和防止通過AI操縱的媒體被用來誤導他人。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"customTextColor\":\"#952f2f\"} -->\n\n<p><strong>普匯觀點：</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>強大的AI技術，也將伴隨著犯罪的高風險，Deepfake在如今的趨勢下，需要的相關數據越來越少，將會導致未來創造內容真假難辨。雖然說這樣的技術若能實現更大突破，未來將會有機會被應用於電子遊戲、電影、電視等領域，對於特效行業來說，這樣的技術會降低不少成本。但是隨著反作弊技術的進步，對於這些偽造影片的判斷也會更加精準，現階段觀察並討論它的適用範圍是我們更應思考的事情。</p>\n<!-- /wp:paragraph -->', '2019-12-16 15:38:12', '潘多拉魔盒中的Deepfake，完美偽造', 'article', 'inherit', '2021-01-15 06:42:07', NULL, 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(6372, 1, '/upload/2019/12/image2-1.jpg', NULL, '<!-- wp:paragraph -->\n<p>當你有資金需求時，你會向誰借錢？爸媽、朋友，還是銀行？以上這些是目前最常見的借貸管道，不過如果你今天有筆緊急的小額借貸，不想讓爸媽、朋友知道，但跟銀行借貸又很麻煩，也許你可以嘗試新穎的民間借貸方法。本文將為你講解借貸意思、借貸契約的重要性，以及新興的借貸方式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>首先，借貸定義是什麼？</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><a rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\" href=\"https://zh.wikipedia.org/wiki/%E5%80%9F%E8%B4%B7\" target=\"_blank\">借貸</a>，是指債權人向債務人讓渡資金使用權的一種金融融資行為。而放貸人（銀行或一般大眾）無須有指定的貸款額，則可在借款人的戶口存款，按照銀行或借貸雙方所協議的條款歸還債務，若債務人未能還款則可能會有法律責任，同時借貸也是銀行的主要獲利模式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>掌握借貸契約的重要性，降低借貸風險</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>談到借貸，大家都知道「白紙黑字」的重要性，不過如果遇到借款人是自己的朋友、親戚，你是否會不好意思訂立借貸契約呢？其實，借貸契約之所以重要，是因為萬一債務人無法按時還款，憑藉著借貸契約，你便能行使法律上的權力，不用擔心這筆欠款收不回來。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>借貸契約中，你不可不知的個人信貸條件</h2>\n<!-- /wp:heading -->\n\n<!-- wp:image {\"id\":7258} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/12/image2-1.jpg\" alt=\"女孩在看借貸廣告\" class=\"wp-image-7258\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>以銀行為例，在個人信貸申請過程中，通常會有以下4幾點重要的審核條件：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>23~63歲之中華民國國民</li><li>年收入達25萬以上且有穩定收入來源</li><li>無信用不良紀錄</li><li>依個別條件檢附財力證明文件</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>申請個人信貸的同時，你也需要準備以下3種資料：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1. 身分證正反面影印本</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2. 職業證明文件：請依您的職業類別提供相關證明。以下為最常見的3種職業類別所需繳交之證明：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>專業人士 : 考試院核發之及格證書、主管機關核發之專業證照或執業執照等影本。</li><li>公教軍警人員 : 教師聘（證）書、教師證、公保卡、派令、在職證明、識別證、軍人身分證、警察證等影本。</li><li>上班族 : 名片、勞保異動明細表、現職公司之薪資證明文件(如：扣繳憑單、薪資單、薪資轉帳等..)、人事資料(合約)、在職證明、企業識別證等影本。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>3. 財力證明文件：包含所得扣繳憑單、綜合所得稅各類所得資料清單、綜合所得稅納稅證明書、綜合所得稅結算申報所得資料『參考』清單、金融機構薪資轉帳存摺明細（含網路銀行資料）等收入證明文件。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>借貸不只能找銀行，你還可以考慮小額借貸！</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>如果你今天突然需要一筆資金，金額不大但卻很緊急，又沒辦法從家人及朋友手中借到錢的話該怎麼辦？或是借貸廣告到處都看得到，但卻不知道如何選擇？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>現在有一種新穎的借貸方式：P2P借貸平台，不像銀行需要辦一堆繁雜的手續，而且還可能礙於學生身分而被拒絕。以往小部份的人可能會鋌而走險去向非法行業借錢，但從金融科技出身的P2P借貸平台就是為了解決這類問題而誕生。P2P是一種民間借貸，藉由媒介平台使資金供給方（投資人）與資金需求方（借款人）撮合，以<strong>線上的方式</strong>對借款人做信用評比來決定借貸利率後進行媒合，是一種比銀行更有效率的新選擇，且此種借貸在台灣是完全合法的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>個人信貸成為p2p借貸ptt上熱門話題</h2>\n<!-- /wp:heading -->\n\n<!-- wp:image {\"id\":6375} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/12/借貸意思-1024x316.png\" alt=\"借貸意思\" class=\"wp-image-6375\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>談到這裡，你可能會好奇P2P民間借貸到底有多少人用呢？PTT為現在鄉民們討論日常事務常用的電子佈告欄平台，我們搜集近一年在P2P借貸PTT、 Loan板上討論的文章共有5973篇，而其中標題為「個人信貸」有2181篇、「房貸」有1909篇、「車貸」有182篇、「其他」1701篇，可見在PTT裡面<strong>信貸</strong>與<strong>房貸</strong>在借款項目中佔了各1/3的討論部分，也足以看出個人信貸成為現今流行的借貸方式之一。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>想在大學時買股票？就讓P2P借貸平台幫助你</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>商科的學生有時遇到一個問題，平常學到的投資學到底能不能實際應用呢？有些教授會建議學生們趁年輕時進入股市熟悉一下。不過如果不買零股的話，一張低成本的股票也要上萬元，就算有在打工也須存一段時間才有辦法入手，而學生身分在銀行又沒辦法借到小額貸款，此時P2P就扮演了一個很重要的角色。P2P小額借貸提供一個新穎的借貸平台，讓想投入股市裡做投資理財卻沒有資金的大學生一個融資管道。過往，人們總覺得在外面公司融資好像多少有一點非法的影子，但如今金融科技的發展，又伴隨著監理沙盒監督，P2P平台的合法性是毋庸置疑的。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>看完以上介紹，相信你對於民間借貸方式有了更深入的了解，以下推薦大家一個快速且合法的大學生借貸平台。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>大學生借貸平台新選擇：普匯金融，快速借貸安全又放心&nbsp;</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>現在民眾借貸的方向已經不只是向銀行求助，越來越多的金融科技公司為了學生族群、新鮮人，推出小額貸款，因應龐大借貸供需，同時也漸漸替代傳統銀行所觸及不到的族群。<a href=\"https://www.influxfin.com/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\">普匯金融</a>看見這些學生、新鮮人市場，進而提供服務，普匯金融的借貸系統都是線上自動審核過件，手機APP簡單三步驟：註冊、認證、撥款，最快1小時內可以核准並進行貸放，讓你可以應付緊急資金需求。還在煩惱要去哪裡小額借貸嗎？效率快的普匯金融就是你的好選擇！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>想了解更多關於小額信貸資訊，請參考：<a href=\"https://www.influxfin.com/%e5%bf%ab%e9%80%9f%e5%b0%8f%e9%a1%8d%e8%b2%b8%e6%ac%be/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\">小額信貸｜快速又合法的申辦管道帶你快速掌握</a></p>\n<!-- /wp:paragraph -->', '2019-12-18 18:23:06', '借貸契約總整理，教你如何小額借貸，靈活運用資金', 'article', 'publish', '2020-02-18 15:08:32', '', 0),
(6403, 1, '/upload/2019/12/image3.jpg', NULL, '<!-- wp:paragraph -->\n<p>急需資金時不少人會選擇向銀行提出信用貸款的需求，但信貸是什麼你真的了解嗎？信用貸款PTT版上有許多網友因為沒有擔保人，或因本身為SOHO族而無法成功貸款，其實現在有許多機構，能讓你以更便捷的方式快速到手所需的資金。本文將把信貸好處和風險一一列出，教你如何輕鬆申請個人信貸！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2> 個人信用貸款是什麼？ </h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>「個人信用貸款」的最大的特色在於只憑藉申貸人的信譽就可以申請貸款，無須提供抵押品或第三方擔保。個人信譽包括個人的信用背景（聯徵紀錄及銀行貸款次數）和財務狀況（年收入及工作性質）。其中聯徵紀錄是指聯合信用徵信中心在個人資料庫裡所建檔的信用紀錄，若聯徵次數過多，會讓金融機構對申貸人的還款能力抱持懷疑的態度。可利用中心提供的<a href=\"https://www.jcic.org.tw/main_ch/docDetail.aspx?uid=170&amp;pid=168&amp;docid=412\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\"線上查閱 (在新分頁中開啟)\">線上查閱</a>服務查詢自己的聯徵紀錄。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>信用貸款好嗎？ 從各類個人信貸了解好處和風險 </h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>在台灣，最主要的個人信貸就分為三種 : 傳統銀行信貸、P2P信貸、以及代辦管道。  </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<table style=\"width: 100%; border=\" 2\";\"=\"\">\n<tbody>\n<tr>\n<td style=\"width: 200px; text-align: center;\">\n<p></p><p><span style=\"font-weight: 400;\">種類</span></p>\n</td>\n<td style=\"width: 600px; text-align: center;\">\n<p></p><p><span style=\"font-weight: 400;\">好處</span></p>\n</td>\n<td style=\"width: 400px; text-align: center;\">\n<p></p><p><span style=\"font-weight: 400;\">風險</span></p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 82px; text-align: center;\">\n<p></p><p><span style=\"font-weight: 400;\">傳統銀行</span></p>\n</td>\n<td style=\"width: 260px; text-align: center;\">\n<p></p><p><span style=\"font-weight: 400;\">利率低</span></p>\n</td>\n<td style=\"width: 1300px;\">\n<p></p><p style=\"text-align:center; justify;\"><span style=\"font-weight: 400;\">會另外收取高額費用，如手續費、信用查詢費、開辦費等等。</span></p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 82px; text-align: center;\">\n<p><span style=\"font-weight: 400;\">代辦管道</span></p>\n</td>\n<td style=\"width: 260px; text-align: center;\">\n<p></p><p><span style=\"font-weight: 400;\">鬆綁申請條件，貸款容易</span></p>\n</td>\n<td style=\"width: 262px; text-align: center;\">\n<p></p><p><span style=\"font-weight: 400;\">個資被洩漏的機率高</span></p>\n</td>\n</tr>\n<tr>\n<td style=\"width: 82px; text-align: center;\"><br>\n<p><span style=\"font-weight: 400;\">P2P信貸</span></p>\n</td>\n<td style=\"width: 800px; text-align: center;\">\n<p></p><p><span style=\"font-weight: 400;\">效率高，手續費低，適合急需資金的人</span></p>\n</td>\n<td style=\"width: 262px; text-align: center;\">\n<p></p><p><span style=\"font-weight: 400;\">可信賴的平台少</span></p>\n</td>\n</tr>\n</tbody>\n</table>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>1. 傳統銀行信貸 </h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>傳統銀行的信貸年利率大約介於0.5% – 3%之間（信用卡則可能高達10%以上），手續費的範圍則大多落在3,000-8,000新台幣，所以即便利率低，高昂的手續費其實計算下來也不容小覷。尤其對某些小額貸款人來說，可能只是急需1萬元的資金，卻需要付跟借款金額差不多的手續費，其實相當不划算。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>2. 代辦管道 </h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>在網路不夠發達的時代，許多找銀行申貸無門的人就會去找代辦公司幫忙，但其實這樣的風險是非常高的。首先，代辦手續費非常高（往往高達10-20%），而代辦公司的水準良莠不齊，個資曝光的風險很大。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":3} -->\n<h3>3. P2P信貸</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p style=\"text-align:left\"> P2P就是「Peer to Peer （個人對個人）」的英文縮寫，顧名思義即為讓借款人與放款人可以直接對接；少掉了銀行中間人，則大大省下了手續費（通常不到5%），對於急需要小額借款的人而言非常適合。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>信用貸款試算，評估還款能力 </h2>\n<!-- /wp:heading -->\n\n<!-- wp:image {\"id\":7244} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/12/image2.jpg\" alt=\"個人信貸ptt參考後試算\" class=\"wp-image-7244\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p> 不過，文字敘述永遠比不上數字的說服力，我們就來個簡單的信用貸款試算比較吧！ 以一個2萬元的小額貸款，分為24期還款來舉例： 以某大知名銀行為例，最低貸款利率0.68%，每月還款額為$839，然而需支付$5,999的手續費。實際上等於每個月都多了$250的還款額，平均一個月還款$1,089。 而若使用普匯，雖然年利率為5%，每期還款金額為$878，但手續費僅有本金的3%，以2萬元貸款的話就是$600，相當於每個月多還款$25銅板而已，一個月還款$903。 簡單比較下來，在銀行貸款的平均每月還款額整整比普匯高出1.2倍，再加上許多條件限制和作業流程所花費的時間，很明顯普匯會是對於小額借貸者最適合的選擇。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>揭密！個人信貸年利率APR計算方式</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p> 信貸年利率又稱為「總費用年利率」（Annual Percentage Rate）APR，也就是將貸款利率、信用查詢費用、開辦費等各項相關支出和貸款金額，平均分攤在需繳款的期限數內的利率。所以，在選擇個人信貸方案時，信貸年利率才是最終每個期數要繳交的金額，若只參考優惠利率，反而會落入金融機構的利率陷阱中。但是P2P信貸平台則是根據個人的信用評分和條件決定利率及額度，較不會產生多餘的費用需要支出。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>4大個人信貸條件</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>搞清楚了個人信用貸款後，想要申請個人信貸又有哪些條件呢？普遍來說，各大金融機構都會用以下4大條件，當作放貸的依據： </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>23~63歲之中華民國國民</li><li>年收入達25萬以上且有穩定收入來源</li><li>無信用不良紀錄</li><li>依個別條件檢附財力證明文件</li><li></li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>（更多詳細內容請參考 ：<a href=\"https://www.influxfin.com/wp-admin/post.php?post=6403&amp;action=edit\">一篇搞懂個人信貸好處和風險，找出最划算的信貸管道！</a> ）</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>由於個人信貸是「無擔保性放款」，在借款人沒有提供相關擔保品的情況下，其實對於金融業者或銀行而言風險是相對高的，所以對於個人信貸條件的評估方式就會更為審慎。如沒有工作收入的學生、或剛踏入社會，所得和工作紀錄都還不充足的新鮮人，這些族群要向銀行申貸往往會面臨重重困難，最後只好轉向民間借貸機構。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n<h2>P2P信貸平台讓資金運用更方便</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p> 針對信貸財力較難證明的自營商和社會新鮮人，許多銀行都難以申請個人信用借貸，P2P平台提供了既方便又快速的融資空間。以普匯金融科技的上班族借貸為例，只要年滿20歲以上且大學畢業，準備以下6項文件就可以線上申請貸款，利用AI自動評分信用的技術，大大降低借款人不知何時可以收到款項的不確定性，最快1小時就能拿到所需資金。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":6417} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/12/個人信貸條件-1024x261.png\" alt=\"個人信貸條件\" class=\"wp-image-6417\"/><figcaption>普匯上班族個人信貸條件</figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading -->\n<h2> 信用貸款推薦：普匯金融科技 </h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://www.influxfin.com/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\"普匯金融 (在新分頁中開啟)\">普匯金融</a>科技借貸媒合平台在<a rel=\"noreferrer noopener\" aria-label=\"監理沙盒 (在新分頁中開啟)\" href=\"https://stli.iii.org.tw/article-detail.aspx?no=57&amp;tp=5&amp;i=6&amp;d=8091\" target=\"_blank\">監理沙盒</a>的監督下，於各大信用貸款推薦論壇上面受到許多年輕族群的佳評（可搜尋信用貸款ptt、個人信貸ptt），提供借款人快速的核貸機制，不僅可以做個人信貸，也可以當作投資人藉由放款賺取利息收入；透過AI自動化審核機制，為使用者打造最便捷快速的借貸經驗，同時給投資者最值得信任的信貸評比，再搭配24小時的AI智能LINE客服，幫助使用者在使用過程中順暢無煩惱！</p>\n<!-- /wp:paragraph -->', '2019-12-18 18:40:33', '一篇搞懂個人信貸好處和風險，找出最划算的信貸管道！', 'article', 'publish', '2020-02-18 15:08:00', '', 0),
(6451, 1, '/upload/2020/01/image2.png', NULL, '<!-- wp:paragraph -->\n<p>由於金融科技的進步，許多小額借款平台漸漸興起，快速核貸以及低利率的特性，讓越來越多人開始使用小額借款。不過在進行私人小額借款前，你了解小額借款嗎？根據小額借款ptt上的網友分享，我們整理出一些有關小額借款的問題。接著就跟普匯一起，了解小額借款是什麼，以及小額借貸的風險和好處。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>首先，小額借款是什麼？</h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>對一般人來說，借錢管道可以分為銀行借款與私人小額借款，而私人小額借款又可分為低利的P2P借款與高利的民間借款。對於小額借款的人來說，多半可能就只是當季或當月資金上有一些小缺口，又不想向親友伸手，而找銀行所花的時間成本又太多、太麻煩也不一定能夠成功核貸，若冒上高風險地找上民間高利貸，可能會因此為高利息而後悔莫及。以下普匯整理了小額借款與其他兩種借貸方式的比較：</p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":6453} -->\n\n<p><img alt=\"小額借款比較\" src=\"/upload/2019/12/小額借款比較-1024x445.png\" /></p>\n<!-- /wp:image --><!-- wp:heading -->\n\n<h2>5大小額借款ptt上熱門話題</h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>一般借款人最常遇到的疑問，大概有以下五個：</p>\n<!-- /wp:paragraph --><!-- wp:list {\"ordered\":true} -->\n\n<ol>\n	<li>我可以借多少錢 ?</li>\n	<li>每期利息是多少 ?</li>\n	<li>我什麼時候可以拿到借款 ?</li>\n	<li>有負債、信用瑕疵，還可以借錢嗎 ?</li>\n	<li>哪裡的借款方式最符合我的需求 ?</li>\n</ol>\n<!-- /wp:list --><!-- wp:paragraph -->\n\n<p>然而，若您遇到了以下這四種狀況，您可能在借錢上遇到了大麻煩。</p>\n<!-- /wp:paragraph --><!-- wp:list {\"ordered\":true} -->\n\n<ol>\n	<li>急用錢，承受不起銀行不核貸的風險</li>\n	<li>無資產或薪資證明，擔憂跟銀行借不到錢</li>\n	<li>想知道哪間銀行有推優惠，哪家利率最低</li>\n	<li>申辦貸款被銀行退件，找不到解決的方法</li>\n</ol>\n<!-- /wp:list --><!-- wp:paragraph -->\n\n<p>如果遇到以上情況，也不用緊張。根據小額借款ptt網友分享，大部分人會選擇P2P借貸，放款快速，對於急需用錢的借款人來說，是一個不錯的選擇。接下來讓我們來了解私人小額借款的好處有哪些。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>私人小額借款3大好處：便利、迅速、選擇多</h2>\n<!-- /wp:heading --><!-- wp:image {\"id\":7231,\"align\":\"center\"} -->\n\n<p><img alt=\"女孩成功申貸小額借款\" src=\"/upload/2020/01/image3.jpg\" /></p>\n<!-- /wp:image --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>為了讓大家更了解小額貸款，以下列出3項私人小額借款特色：</p>\n<!-- /wp:paragraph --><!-- wp:heading {\"level\":3} -->\n\n<h3>小額借款好處一：申請便利</h3>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>在申請小額借款的過程中，因為借款人不用透過銀行，所以不會被銀行抽取存貸間的利差，除了節省金錢，同時也不須經過銀行繁瑣的核貸流程，而借款人也可以取得更低的利率。</p>\n<!-- /wp:paragraph --><!-- wp:heading {\"level\":3} -->\n\n<h3>小額借款好處二：放款時間迅速</h3>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>目前，大部分P2P借貸的系統平台為全面AI線上化，讓借款人透過線上審核、媒合等動作，快速過濾，<strong>在24小時內</strong>安全、快速借到錢。</p>\n<!-- /wp:paragraph --><!-- wp:heading {\"level\":3} -->\n\n<h3>小額借款好處三：借款方案多</h3>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>由於私人小額借款方案多，還款方式也不同。借款人可根據自身的需求、償還債務能力，選擇不同利率、分期時間去借款。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>除了知道小額借款好處，關於網路上大家最關心的身分證借款風險，以下也將詳細敘述。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>借款人疑問：身分證小額借款有風險嗎？</h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>有些P2P借貸標榜只須身分證便能借款，然而這樣的借款方式卻存在一些風險。而關於這點，如果您選擇<a href=\"https://www.fsc.gov.tw/ch/index.jsp\" target=\"_blank\">金管會</a>核准的借貸平台，則可以不用擔心，因為借款的合法性與風險都受到政府機關的保障。另外，P2P借貸早已在歐美成功運作十多年，就連台灣首富郭台銘都在投資P2P網路借貸呢！因此借款人可以放心將雙證件交予合法的借貸平台，不用擔心風險與合法性問題。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>申請小額借款的2項資格，3步驟輕鬆拿取款項</h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>一般來說，任何貸款都需要<strong>借款人年滿20歲</strong>，且<strong>有工作證明</strong>才能申請貸款，小額貸款也不例外。而以普匯為例，普匯提供專屬學生、上班族快速申辦的專案，透過金融科技平台，提前建立財務信用。無論是學生小額借款，或是身分證小額借款，人人只要有一支手機，透過簡單三步驟：註冊會員、進行認證以及立即撥款，就可以順利拿到錢了喔！</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2>學生小額借款首選：普匯金融</h2>\n<!-- /wp:heading --><!-- wp:image {\"id\":7233} -->\n\n<p><img alt=\"學生小額借款平台\" src=\"/upload/2020/01/image2.png\" /></p>\n<!-- /wp:image --><!-- wp:paragraph -->\n\n<p>如果您是學生、上班族，卻還在煩惱私人小額借款該選哪一個，那麼普匯將是您的新選擇。<a href=\"https://www.influxfin.com/\" target=\"_blank\">普匯</a>的宗旨在「速度、紀律、績效」，擁有完善的金融科技技術，包含：反詐欺反洗錢系統、競標即時撮合系統、 風控信評/線上對保以及自動撥貸/貸後管理等等，搭配二十多年的金融專業經驗與視覺化簡潔好用的操作介面，運用先進科技與AWS 安全系統，保護您的個資絕不外洩，讓您透過小額借款，在圓夢的旅程中，既安全又放心。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>想了解更多關於P2P借貸資訊，請參考：<a href=\"https://www.influxfin.com/articlepage?q=knowledge-4979\" target=\"_blank\">P2P借貸是什麼？為您介紹時下年輕人最常使用借貸方式</a></p>\n<!-- /wp:paragraph -->', '2019-12-20 15:58:41', '想了解小額借款就讀這一篇！普匯帶您3分鐘看更實惠的借款方式', 'article', 'publish', '2021-01-18 08:30:23', NULL, 1),
(6458, 1, '/upload/2019/12/image-from-rawpixel-id-550436-original-1.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">一、Netflix 的崛起</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":6459} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/12/NE.png\" alt=\"\" class=\"wp-image-6459\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\">補充說明：串流媒體（streaming media）指在網路上即時傳輸影音以供觀賞的一種技術與過程，此技術使得資料封包得以像流水一樣傳送；如果不使用此技術，就必須在使用前下載整個媒體檔案。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">二、核心競爭力</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>以競爭優勢環境模式分析Netflix的核心競爭力</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>競爭優勢環境模式指出，在企業所擁有的「資源」相仿的前提之下，必須透過「不同的使用方式」來利用資源建立企業的核心競爭力。 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>（一）長期累積的顧客數據發展出精準演算法 </strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Netflix因有先進者優勢，在影音租借市場累積龐大的會員數量，並據此數據來優化使用，再加上演算法能夠推算出消費者在意的因素，進而去改進使用者體驗，此為其他原先不在租借市場的競爭者所無法取代的。化程度可以讓同一部影集，在介面選單上為不同用戶族群自動生成各種差異化海報封面，用顧客偏好的演員來吸引點閱。播出節目只是產品販售，準確滿足用戶的娛樂需求才是價值。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>（二）「顧客體驗至上」的價值觀</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p> Netflix的所有策略都採顧客導向，最重視消費者的整體的體驗過程，也非常了解當今環境下的消費者行為模式為何，故在策略上除了讓顧客滿意，更可以提高客戶黏著度及移轉成本。(eg. 一鍵退訂) </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>（三）售後管理</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Netflix 透過蒐集大數據，包括觀眾每一次中斷收視的情況、搜尋偏好、&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 觀看後的主動評價，負面使用經驗能即時回饋，在下一次影片推薦時改善。每一次的收看選擇都是 A/B test，都能即時修正，不需要等到消費者用下個月退訂才被教訓。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">三、產品競爭力</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(一)<strong>影片題材看重長尾效應</strong>: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Netflix的核心主軸是留住客戶，所以評估內容成效的關鍵指標是 「每小時觀看成本」，亦即 「這個內容是否能在一定的成本內，最大化用戶觀看時數」。由此可見，Netflix 想的並不是 「這個作品是否是最優質的內容」 或 「這個作品是否能吸引最多的觀眾數」，而是 「只要這個作品，能吸引一定數量級的觀眾群，並且符合每小時觀看成本的門檻」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(二)<strong>在地化自創影集提高認同感</strong>:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>觀眾對演員和題材認同度越高，收視也就越黏著。印度和中國是網飛近年關注的高成長市場，但這些市場需要更多在地題材與演員。網飛近年陸續針對印度和中國推出專屬劇集，韓國，台灣市場亦然。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\">e.g. 在台灣引起轟動的自製劇《你的孩子不是你的孩子》，在網飛的全球播放獲得好評，就是本地結合全球的雙贏案例。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(三)<strong>內容開發精準</strong>:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>從內容開發者的角度，他能夠精算何種特質整合及估算合理成本，來創造成功的戲劇，所以小眾、另類題材的類型題材就可以有固定數量的鐵粉支持下長期出現，進一提升整體營收表現。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\">(四)<strong>特殊演算法擊敗最大的競爭對手：</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\"} -->\n<p style=\"text-align:center\"><strong>睡眠</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>矽谷出身的網飛在演算技術下足功夫，從觀眾一點選影片開始播放，就有 6-15 種演算法，估算如何推薦下一部影片。包括收看長度、時段選擇、中斷時機、過去收看清單、演員偏好、類型、年份、使用載具等都是演算法的判別因素。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} -->\n<p class=\"has-text-color has-vivid-cyan-blue-color\">四、普匯觀點</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這幾年來不管是在電商還是在影視產業處處可以見到金融科技的影子，在Netflix、Youtube上常見的訂閱經濟制度，Amazone、FB的商品推薦系統，都是應用大數據及機器學習分析下的產物，台灣近期因應著這股風潮從Uber最短路程運算、街口金融支付等等的新穎商機也紛紛竄出，普匯金融以普惠大眾為初衷，幫助銀行無法觸及到的需求面，首創台灣第一家以全自動線上審核的P2P系統，嚴格把關借方與貸方的資訊橋梁，未來在台灣Fintech上普匯也會極力的持續推廣更豐富更科技化的借貸商品給有資金需求的大眾!</p>\n<!-- /wp:paragraph -->', '2019-12-19 16:56:50', 'Fintech訂閱經濟 – Netflix', 'article', 'publish', '2020-04-28 12:25:53', '', 0),
(6613, 1, '/upload/2019/12/image2.png', NULL, '<!-- wp:paragraph -->\n<p>當你在申辦貸款或者申請信用卡和現金卡時，是否記得銀行要求你簽屬一份聯徵信用同意書，但你因不確定聯徵信用的確切意圖而遲遲不敢下筆。到底什麼是聯徵報告呢？擁有聯徵報告會使我們在申請信貸時更有利嗎？以下透過普匯小幫手的說明，解開你的疑惑。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h2><strong>聯徵是什麼？</strong></h2>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>「聯徵」是銀行瞭解個人償債能力和償債表現的途徑，以確保信用交易安全。個人或者企業都有獨自專屬的聯徵報告，由財團法人金融聯合徵信中心（簡稱：聯徵中心）提供。<a rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\" href=\"https://www.jcic.org.tw/main_ch/index.aspx\" target=\"_blank\">聯徵中心</a>是全台唯一政府設立的「跨金融機構」信用報告機構，透過蒐集個人或者企業與銀行的所有往來紀錄，包括信用卡有沒有遲繳或是卡循等等，來產生一個信用評分（詳細評分細則請見下文），信用評分的分數高低會影響你的貸款成功機率以及利率高低。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h2><strong>聯徵中心的個人信用報告內容</strong></h2>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>以下為聯徵中心提供給銀行的個人信用報告內容主要項目：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>借款人基本資料</li><li>銀行貸款項目明細與金額</li><li>信用卡繳款紀錄</li><li>是否有逾期、催收、呆帳、退票或銀行 拒絕等紀錄</li><li>過往曾跟銀行申請過的工作職稱、收入、年資、學歷等報送資料</li><li>聯徵被查詢次數</li><li>借款人自己查聯徵</li><li>現階段的整體信用評分（包含一個<a rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\" href=\"https://ezloan991.com.tw/%E8%81%AF%E5%BE%B5%E5%88%86%E6%95%B8/\" target=\"_blank\">聯徵分數</a>）</li><li></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading -->\n</br>\n<h2><strong>聯徵中心信用評分高低，決定你的貸款利率</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>個人信用評分是銀行決定是否貸款的關鍵指標，主要依據聯徵中心信用評分決定，聯徵分數界於200分至800分之間，分數的高低是貸款的先決條件之一，會影響貸款的過件機率。分數越高代表信用越好，在貸款時能夠得到較優惠的利率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h3><strong>聯徵中心的個人信用資料</strong><strong>評分依據</strong></h3>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>信用評分主要以聯徵中心的個人信用資料為評分依據：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<table class=\"wp-block-table\"><tbody><tr><td width=\"100\">\n信用分數比重\n</td><td width=\"80\">\n名目\n</td><td width=\"100\">\n說明\n</td></tr><tr><td>\n30%\n</td><td>\n信用卡可借和已借額度的比例\n</td><td>\n避免已借額度逼近可借額度。當已借額度&gt;可借額度的50%，分數會降低。\n</td></tr><tr><td>\n15%\n</td><td>\n信用紀錄使用長短\n</td><td>\n隨著使用信用卡時間增長，根據貸款人良好的還款記錄，貸款額度會增加。\n</td></tr><tr><td>\n10%\n</td><td>\n信用種類\n</td><td>\n分為Installment（像是房貸、車貸、學生貸款等等）、Revolving（像是信用卡、商店卡）等等，但這不表示多開不同的戶頭對信用分數一定有幫助，需視情況而定。\n</td></tr><tr><td>\n10%\n</td><td>\n近期聯徵查詢次數\n</td><td>\n如果你近期有申請信用卡或貸款，會因為銀行查詢信用資料而導致你的聯徵查詢次數上升，聯徵分數「短時間」內降低。但是，如果是你自己查詢你自己的信用分數、或者那些銀行要寄給你Pre-Approved 信用卡申請的公司所做的查詢，理應不會對聯徵分數造成影響。\n</td></tr></tbody></table>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>因此，如果想要提高自己的信用評分分數，可以參考以上評分依據。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h2><strong>聯徵報告怎麼查詢？可以線上查詢嗎？</strong></h2>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n</br>\n<h3><strong>個人查詢</strong></h3>\n<!-- /wp:html -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>線上查閱<ol><li>使用個人電腦，在<a href=\"https://apply.jcic.org.tw/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\">聯徵中心官網</a>使用自然人憑證，或以銀行、證券、保險業等金融機構核發的軟體金融憑證，即可快速查閱個人信用狀況。</li><li>使用智慧型手機或平板電腦等行動裝置，透過App Store或Google Play，搜尋「TWID投資人行動網」，安裝App並完成憑證申請，即可以隨時隨地查閱個人信用狀況。</li></ol></li><li>書面查詢<ol><li>郵局代收辦理&nbsp;：全國各地郵局儲匯窗口均可受理代收代驗「當事人綜合信用報告」及「金融機構債權人清冊」服務。</li><li>親臨聯徵中心櫃台辦理：本人或本人委託親友至服務櫃台辦理。</li><li>郵寄申請：寄件地址—10044台北市重慶南路一段2號16樓。</li></ol></li></ol>\n<!-- /wp:list -->\n\n<!-- wp:html -->\n</br>\n<h3><strong>企業查詢</strong></h3>\n<!-- /wp:html -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>負責人親自至聯徵中心櫃台辦理</li><li>負責人委託他人至聯合徵信中心服務櫃檯辦理</li><li>郵寄申請</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:html -->\n</br>\n<h2><strong>普匯貸款｜申請低利率小額貸款免用聯徵報告</strong></h2>\n</br>\n<!-- /wp:html -->\n\n<!-- wp:image {\"id\":7252,\"width\":324,\"height\":701} -->\n<figure class=\"wp-block-image is-resized\"><img src=\"/upload/2019/12/image2-473x1024.png\" alt=\"聯徵中心的個人信用資料\" class=\"wp-image-7252\" width=\"324\" height=\"701\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>「聯徵資訊」是普匯決定是否貸款的因素之一，如果希望以低利率的方式最速小額貸款，可以透過普匯的線上app「普匯inFlux」授權個人徵信報告。在一般情況下，千大企業員工免聯徵報告、畢業一年內的的社會新鮮人免聯徵報告，我們會以最快的速度進行審查。但若提供聯徵報告，能有效增加核貸額度喔！快去申請一份來看看自己的信用價值值多少吧，普匯是你最佳的選擇！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><a href=\"https://www.influxfin.com/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\">普匯金融</a>以金融服務為核心，先進科技為輔具，提供專業金融APP。擁有20年金融專業，幫助人們解決遇到的金融困難，為社會大眾打造更好的生活。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>該如何選擇個人信貸的管道呢？請參考：<a href=\"https://www.influxfin.com/%e5%80%8b%e4%ba%ba%e4%bf%a1%e8%b2%b8/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\">一篇搞懂個人信貸好處和風險，找出最划算的信貸管道！</a></p>\n<!-- /wp:paragraph -->', '2019-12-24 10:28:25', '聯徵報告是什麼？評定個人信用高低的關鍵', 'article', 'publish', '2020-02-18 15:06:56', '', 0),
(6626, 1, '/upload/2019/12/image2-3.jpg', NULL, '<!-- wp:paragraph -->\n<p>在面臨資金需求的時候，很多人都會選擇透過貸款來解決，但對於沒有信用紀錄的人來說，要取得貸款並沒有那麼容易。由於沒有讓銀行評估風險的數據，多數銀行對於小白貸款的態度都是比較不友善的，那麼究竟信用空白該如何申請信貸？需要具備什麼條件？跟著本文一次了解小白貸款完整攻略。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h2><strong>信用小白意思是什麼？</strong></h2>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>在了解小白貸款的方法之前，要先了解信用小白的意思。簡單來說，信用小白就是「信用空白」，指個人與銀行從來沒有財務往來紀錄，沒有辦過信用卡、現金卡、持卡未滿3個月或是沒有申請過任何一項貸款，聯徵上沒有累積任何的信用分數，就是我們所謂的信用小白。對於銀行來說，沒有可以評估風險的依據，需要承擔的風險就會比較大。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":7266} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/12/image1-4.jpg\" alt=\"信用空白如何申請信貸\" class=\"wp-image-7266\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:html -->\n</br>\n<h2><strong>信用小白要怎麼累積信用紀錄？</strong></h2>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>以下為3個累積信用紀錄的方法：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>累積公司薪轉紀錄：長期且穩定的薪資收入證明可以累積良好的信用。</li><li>良好的存款紀錄：不當月光族，穩定累積戶頭金額，至少存放2～3萬的存款。</li><li>申辦第一張信用卡：使用信用卡時，養成定時繳款的好習慣，不拖欠並一次付清。</li><li></li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h2><strong>信用空白如何申請信貸？3步驟取得小白小額信貸</strong></h2>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>相信許多人存在著這樣的迷思：「我從來沒有借過錢，沒有使用信用卡，更不可能有遲繳的問題，因此我的信用好得很！」然而，事情卻與你想像的不同，銀行並非那麼歡迎「信用小白」。信用空白因為沒有任何信用紀錄，通常也被認為沒有信用，因而無法順利地申辦貸款。但是不用擔心，還有一些銀行特別為這些族群設立了——小白小額信貸。接下來以<a href=\"https://www.influxfin.com/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\">普匯</a>為例，一起來看以下的小白小額信貸3步驟，讓您也可以順利拿到小白貸款10萬！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>延伸閱讀：<a href=\"https://www.influxfin.com/%e5%80%9f%e8%b2%b8%e5%a5%91%e7%b4%84/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\">借貸契約總整理，教你如何小額借貸，靈活運用資金</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h3><strong>step 1 有份穩定的工作</strong></h3>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>借貸最需要的信任即是您未來有能力支付還款，因此當我們看到您存摺有薪轉收入或是提供勞工保險資料，都會了解到顧客有持續且穩定的收入，不見得一定要是公教人員或是上市櫃公司的職員，基本上只要有固定的工作就可以了！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h3><strong>step 2 存摺保持一定的額度</strong></h3>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>切記不要當月光族喔！一領到薪水後就全部提領出來然後花掉，借方可能對於你的還款能力就會提出質疑，想要借錢就比較不容易了。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h3><strong>Step 3 來普匯申辦「小白貸款」</strong></h3>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>下載普匯inFlux app並申請案件，一天內就可以輕鬆擺脫信用小白，你將成為各金融機構心目中的好客戶哦！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h2><strong>有抵押貨款可以增加小白貸款成功率</strong></h2>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>除了上述所說的條件之外，如果在申請貸款的時候，申請人可以提供抵押物，例如房子或是車子等，申請貸款的成功率會比較高。但相對的，當借款人無法負擔貸款時，銀行或債權人就有權力將抵押物沒收，這樣的方式對銀行來說比較有保障。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<h2><strong>最佳的小白信貸推薦給您，普匯金融科技</strong></h2>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>普匯金融科技是您最貼心的貸款顧問，同時透過全面線上、AI等流程優化，讓您快速解決燃眉之急。有什麼相關問題，歡迎透過【普匯app線上客服】或是 line加入<a href=\"https://line.me/R/ti/p/%40kvd1654s\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\">@influxfin</a>來諮詢，我們將提供您專業的貸款諮詢服務，那麼，心動不如馬上行動，跟著普匯一起擺脫信用小白！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>延伸閱讀：<a href=\"https://www.influxfin.com/%e8%b2%b8%e6%ac%be%e6%a2%9d%e4%bb%b6/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\">你知道你的信用值多少錢嗎？貸款條件報你知！</a></p>\n<!-- /wp:paragraph -->', '2019-12-24 10:31:30', '如何申請小白貸款？信用小白貸款攻略一次告訴你！', 'article', 'publish', '2020-02-18 15:06:14', '', 0),
(6870, 1, '/upload/2019/12/image2-2.jpg', NULL, '<!-- wp:paragraph -->\n<p>在ptt上也能看到許多人在<a rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\" href=\"https://www.ptt.cc/bbs/Loan/search?page=13&amp;q=%E5%B0%8F%E9%A1%8D%E4%BF%A1%E8%B2%B8\" target=\"_blank\">Loan板</a>上討論線上小額貸款的相關資訊，以下就讓普匯小幫手來整理快速小額貸款的管道，以及教你如何小額貸款，救急現金！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<p>小額貸款指通過非正式途徑向微型和小型企業、貧困者或貧困者家庭提供融資服務，以擴大這些群體的選擇範圍，提高其生產性投資，從而增加這些群體的收入和就業機會的金融活動。這種形式不同於一般意義上的商業銀行融資。</p></br>\n<!-- /wp:html -->\n\n<!-- wp:heading -->\n<h2><strong>小額借貸申辦資格？</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"textColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-very-dark-gray-color\"><strong>1.年滿２０歲</strong><br>不管是哪一個合約，簽訂人都需要年滿２０歲才具有法律效益。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<p class=\"has-text-color has-very-dark-gray-color\"><strong>2.有穩定工作</strong><br>貸款最重要的不是借，而是還，要有還款能力別人才願意借錢給你。工作是貸款的第一道防線，如果沒有穩定工作與穩定收入，無論申請任何一項貸款都會相對困難。</p></br>\n<!-- /wp:html -->\n\n<!-- wp:heading -->\n<h2><strong>快速小額貸款不僅方便，更有6大優點</strong></h2>\n<!-- /wp:heading -->\n\n<p></p>\n<p></p>\n\n<!-- wp:heading {\"level\":3} -->\n<h3><strong>1.程序簡單、放貸過程快、手續簡便。</strong></h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>申辦小額貸款手續簡便，申請後快速放貸。</p>\n<!-- /wp:paragraph -->\n\n<p></p>\n<p></p>\n\n<!-- wp:heading {\"level\":3} -->\n<h3><strong>2.還款方式靈活</strong></h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>按月等額還本付息、按季結息到期還本、到期一次還本付息或分兩次還本付息等多種靈活的還款付息方式。</p>\n<!-- /wp:paragraph -->\n\n<p></p>\n<p></p>\n\n<!-- wp:heading {\"level\":3} -->\n<h3><strong>3.貸款範圍較廣</strong></h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>小額貸款公司服務對象主要是中小企業、個體工商戶、農戶等。&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<p></p>\n<p></p>\n\n<!-- wp:heading {\"level\":3} -->\n<h3><strong>4.營銷模式靈活</strong></h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>小額貸款公司在風險可控下實行不評級、不授信的營銷形式，打破了長期以來商業銀行等正規金融機構的經營方式，具有方式簡便、高效快捷的特點，有利於中小企業及時獲得信貸支持，緩解中小企業及個體工商戶的短期融資困難，一定程度上彌補了銀行貸款和民間借貸之間的不足。&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<p></p>\n<p></p>\n\n<!-- wp:heading {\"level\":3} -->\n<h3><strong>5.小額貸款公司貸款質量高</strong></h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>小額貸款公司貸款質量高，是因為小額貸款公司貸出資金幾乎全部是股東的自有資金,所以對貸款項目的審查就更為謹慎；由於小額貸款公司是私人經營,主要在當地放款,對借款人及用途能充分的了解，所以對風險控制有一定好處。&nbsp;</p>\n<!-- /wp:paragraph -->\n\n<p></p>\n<p></p>\n\n<!-- wp:heading {\"level\":3} -->\n<h3><strong>6.小額貸款社會風險小</strong></h3>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<p>小額貸款公司不非法集資、不放高利貸、不用社會閒散人員收貸。其集資、放貸、收貸都有自己的執行辦法，而且只貸不存，不涉及公眾存款問題，社會風險小。</p></br>\n<!-- /wp:html -->\n\n<!-- wp:heading -->\n<h2><strong>小額借貸跟一般信用貸款有什麼差別？</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>小額借貸也是屬於信用貸款的一部分，差別在小額借貸的對象一般是個人，根據銀行的不同額度大約在幾千到幾十萬左右，可以做為留學、生意成本、買車的用途。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<p>一般信用貸款對象可以是個人也可以是公司行號，設限額度通常大於小額借貸，額度可以到幾百萬元，大致用途為買房、生意周轉…等等用途。</p></br>\n<!-- /wp:html -->\n\n<!-- wp:heading -->\n<h2><strong>普匯金融科技，線上小額信貸的新助手</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:image {\"id\":7263} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2019/12/image2-2.jpg\" alt=\"小額貸款五萬\" class=\"wp-image-7263\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>普匯金融運用AI智能科技與安全風控模組，將專業金融產品與線上簡易方式搭起投資人與借款人的橋梁，以5大特色，安全、簡單、快速、隱私，快速小額貸款的部分也主張「快速三小時內核貸，最快一小時放款」，最特別的是普匯鼓勵「自我學習」「生活需求」的小額貸款，也有提供學生貸款，只要年滿20歲在學之學生，透過身分證/健保卡，以及學生證，即符合申請資格可進行借貸。在金融科技發展下，我們推出的<a href=\"https://www.influxfin.com/\" target=\"_blank\" rel=\"noreferrer noopener\" aria-label=\"貸款平台 (在新分頁中開啟)\">貸款平台</a>，採用「全自動線上審核系統」，完全替代了以前人工審核的無效率，隨時都能高效率的滿足需要資金的客戶需求！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><br>想了解更多小額貸款相關知識總整理，請參考：<a rel=\"noreferrer noopener\" aria-label=\" (在新分頁中開啟)\" href=\"https://www.influxfin.com/%e5%80%9f%e8%b2%b8%e5%a5%91%e7%b4%84/\" target=\"_blank\">借貸契約總整理，教你如何小額借貸，靈活運用資金</a></p>\n<!-- /wp:paragraph -->', '2019-12-30 17:18:58', '小額貸款新選擇！3小時核貸，1小時放款，合法安全線上辦', 'article', 'publish', '2021-01-18 08:12:37', NULL, 1),
(7084, 1, '/upload/2020/01/image3-1.jpg', NULL, '<!-- wp:paragraph -->\n<p>求學路上要打工又要念書，能夠工作的時間有限，但好不容易領了薪水，繳了學費、社費、系學會費等等，結果發現幾乎存不了什麼錢，偏偏這時候突然間電腦壞了、機車拋錨，或是又要繳一筆畢業製作費或是畢旅費，因此你開始上網在學生借錢ptt或是dcard上找找有什麼便利的好方法來借錢，希望最好動動手指就能順利拿到錢，在這裡告訴您一個好消息，Dcard 熱搜 『大學生最愛線上借貸平台』&mdash;&mdash; 「普匯」幫你圓夢！</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>為什麼申辦</strong><strong>大學生小額借款</strong><strong>無法成功？</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>多數大學生借錢都以小額約1~20萬左右、短期約3~6個月的借款需求，然而銀行多半會拒絕學生貸款，理由來自於學生收入不穩定，也沒有累積的信用資料可供聯徵參考，銀行無法判斷您是否會依約準時還款，因此多數學生無法在銀行上申辦成功。試想，若是毫無風險控管及聯徵資料參考地借錢給學生，就會像中國曾經火紅流行一陣子的借錢管道，短短8年讓中國信貸呆帳翻漲了11倍，銀行怎麼會願意承擔這樣的風險呢？也因此學生在銀行很難順利借款成功。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>大學生借不到錢怎麼辦？</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>相信許多學生是為了能繼續生活、完成學業，可能就差這一年的關鍵時間來完成，但生活卻遇上了變數，無可奈何而走上了借錢，又因銀行無法成功申辦，致使焦頭爛額。但其實，選擇合法的民間貸款來申辦大學生借錢會讓您更有彈性且更為方便。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>然而，許多人會將民間貸款與高利貸錢莊聯想在一起，但事實上兩者是完全不同的東西，民間貸款依法成立，當然就有法律約束最高利率限制來保障民眾，而且民間貸款的條件可依自身還款能力及信用評價來調整，因此可有效降低您無法負荷的可能性。注意在接洽民間的學生貸款時，請先了解對方是否完整說明利率及各項手續費用，避免有任何隱藏費用，此外，申辦通過前，請勿繳交任何費用，也請不要抵押證件、印章或是存褶給對方，以免自身個資被遭到惡意利用。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>當然，借款最重要的還是依照自己的還款能力及需求，找到適合且安全的貸款方式，來解決金錢問題。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>最好用的</strong><strong>學生借錢平台</strong><strong>在普匯！</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>普匯學生貸款是Dcard熱搜大學生最愛的線上借貸平台，成功幫助11,075筆交易解決生活急需或是達成自己的夢想，我們提供最方便且直觀的借款app，借款再也不用害怕被騷擾或是被他人得知，免出門跑遍各大銀行、也不需擔心開口借錢，只要在手機上動動手指，就可以完成申辦，同時普匯借款平台透過線上自動媒合的方式放款，最快一小時就可以順利到帳，快速解決您的燃眉之急！</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>普匯學生貸款，讓錢輕鬆在指尖上完成！</p>\n\n<p>立即體驗&rarr;<a href=\"https://event.influxfin.com/R/url?p=web\">最快速的學生貸款</a></p>\n<!-- /wp:paragraph -->', '2020-01-09 16:50:53', '學生借錢不再煩惱！合法借錢平台快速貸款，解決你的用錢之急', 'article', 'publish', '2021-01-19 01:41:10', NULL, 1),
(7100, 1, '/upload/2020/01/image2.jpg', NULL, '<!-- wp:paragraph -->\n<p><strong>線上借款平台</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>隨著科技技術的發展，現代生活形式的改變，許多臨櫃的銀行業務已轉為線上化，不僅能夠提升速度，更能增加效率，免去出門或者親自跑一趟的時間，而<strong>「網路借貸」</strong><strong>正是這個趨勢下的時代潮流</strong>。網路借貸的年齡層橫跨廣泛，從大學新鮮人至而立之人，只要透過手機操作便有機會得到周轉資金，快速解決生活中的資金需求。網路借貸最關鍵的步驟在於找到<strong>合法安全的線上借款平台</strong>，借款人在選擇時須識別平台的真偽，並排除高利貸或地下錢莊，才不會掉入不良業者的陷阱裡。目前<strong>inFlux普匯金融科技</strong>是全台第一個同時受金管會、金融總會、資策會、工研院、亞洲矽谷輔導的AI網路借貸平台，以結合｢金融專業｣與｢高端科技｣，提供獨有的線上金融服務模式，架構雙向投資融資，讓資金流通更為簡單、快速、安全與隱私。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>網路借貸可以免除向親友借錢的人力壓力，或者前往銀行臨櫃的尷尬感，此外inFlux普匯更主打四大優勢，分別是簡單、快速、安全、隱私，全程借款申請採用線上化手機操作，讓您躺在家裡就能快速取得金源，最快一小時到帳！此外，普匯嚴格遵守「個人資料保護法」，未經同意及授權，絕不提供他人使用，資料均存於全球最大的亞馬遜AWS雲端伺服器，並且使用無人化AI驗證技術，撇除人為的主觀操作。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>目前inFlux普匯提供網路借貸的顧客可以分為<strong>「學生借款平台」</strong>以及「上班族借款平台」，主要依據身份來區分，20歲以上在校生申請學生貸；35歲以下大學以上畢業生則申請上班族貸，此外，無卡也能分期買手機，每月$499起，享有最優惠的空機價，普匯AI系統會依據您提供資訊的完整性給予信用評等分數，此分數將決定您的借貸額度以及借貸利率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>網路借貸平台，解決您的短期小額資金需求，普匯您的金錢小幫手，隨時online照顧您的需求！<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n</br>\n<!-- /wp:html -->', '2020-01-09 16:47:39', '線上借款平台', 'article', 'publish', '2020-02-18 14:57:23', '', 0),
(7152, 1, 'https://lh5.googleusercontent.com/ZxSjNNVN5AvMnZoP4DpU0kjZOnmQDckLVFr2MMQjnAgOIOiEHKGoQTPmTS6iMYxd6xG5Uziw3aZhe1UKkMu13o_MN81AENfW16O-qSwliXU7-7abeQsw5gnta8N8J_oM0fl1EZ9n', NULL, '<!-- wp:paragraph -->\n<p><strong>就學貸款</strong>是最常聽到的學生貸款之一，貸款聽起來很可怕，卻是短期內幫助你規劃手頭資金的方法之一，只要善用資源，學貸還款不但很輕鬆，甚至還能利用政府的緩繳期政策，趁畢業前存下一筆錢！接下來本文將會告訴你如何申請學生貸款、學貸利率多少，推薦友善的學生借貸平台，減輕經濟負擔。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2><strong>學生借貸如何申請？</strong></h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>大多數人會在讀書之餘打工貼補生活費，但身為學生除了要繳學費，如果要換手機、買機車、和同學旅遊甚至是創業，也需要一筆不小的開銷，所以有些人會選擇向金融借貸機構申請學生借貸，以利周轉。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>以學生貸款最常見的就學貸款為例，目前各大銀行皆有辦理就學貸款的服務，申請條件、何時可申貸、申貸所需文件等資訊，請參考表格內的資訊：</p>\n<!-- /wp:paragraph --><!-- wp:table -->\n\n<table border=\"1\">\n	<tbody>\n		<tr>\n			<td style=\"width:100px\">\n			<p>申請</p>\n			</td>\n			<td>為中華民國國民，且就讀國內公立高級中等以上學校並具有正式學籍，本身與法定代理人的收入符合<a href=\"https://dep.mohw.gov.tw/DOSAASW/cp-572-5035-103.html\">中低收入家庭標準</a>者，可分為以下三類資格：<br />\n			1. 家庭年收入所得114萬元以下，就學時利息由政府全額補助2. 家庭年收入所得114萬至120萬元，就學時利息政府負擔半額，自付半額3. 家庭年收入所得120萬以上，但家中有2位以上子女就讀高級中等以上學校者，需檢附其中一位在學證明資料，利息自付全額</td>\n		</tr>\n		<tr>\n			<td>何時申請</td>\n			<td>每年8月1日至9月底、每年1月15日至2月底</td>\n		</tr>\n		<tr>\n			<td>所需文件</td>\n			<td>學生本人與保證人之身分證、印章（第一次申貸需要保證人一同前往）註冊繳費單申請撥款通知書三聯單三個月內戶籍謄本信保費100元</td>\n		</tr>\n		<tr>\n			<td>申貸時間</td>\n			<td>需經過人工查核，所以時間大概會拉長至一周</td>\n		</tr>\n	</tbody>\n</table>\n<!-- /wp:table --><!-- wp:paragraph -->\n\n<p>如果是未成年學生需要申請就學貸款，過去需要父母雙方到場辦理申請，但民國105年已<a href=\"https://www.cy.gov.tw/News_Content.aspx?n=124&amp;sms=8912&amp;s=7936\">修正法案</a>，改由法定代理人其中一位，或一位適當之成年人陪同辦理即可，解決了不少單親家庭的煩惱。</p>\n<!-- /wp:paragraph --><!-- wp:heading {\"level\":3} -->\n\n<h3><strong>學生貸款流程</strong></h3>\n<!-- /wp:heading --><!-- wp:image -->\n\n<p><img alt=\"學生借貸\" src=\"https://lh4.googleusercontent.com/XZY_vtBA8Ee-hfA7LSQyYpqxI3vrIRbkKNmNC6ion2ap5clUrlJXckjUO6YIfNZp_RHu2-MYRGorE8r0ChynNM-KMV1j3_FcFA9OVhNhf4vRJ1og7Tp1ltWJRTgsXBzT2lkyXpfv\" style=\"height:323px; width:200px\" /></p>\n<!-- /wp:image --><!-- wp:paragraph -->\n\n<p>雖然只有四個步驟，但申請文件繁瑣，需要辦理的時間也長，還要另外和保證人約定時間，其中的人力和時間成本，都會讓生活步調繁忙的現代人來說，造成另外一種困擾。而且撥款時間依照程序快慢時間不等，需要時刻注意款項是否已撥入帳戶。</p>\n<!-- /wp:paragraph --><!-- wp:heading {\"level\":3} -->\n\n<h3><strong>就學貸款額度</strong></h3>\n<!-- /wp:heading --><!-- wp:list -->\n\n<ul>\n	<li>高中職最高50萬元</li>\n	<li>高中職實習、書籍費1000元</li>\n	<li>大專院校、學士後碩博士學程皆為100萬元</li>\n	<li>醫學系最高為150萬元</li>\n	<li>大專實習、書籍費3000元</li>\n	<li>住宿費依校內宿舍費的最高額度為借款上限</li>\n	<li>每學期低收入戶者生活費最高4萬元、中低收入戶者最高2萬元</li>\n	<li>海外研修和保險費每年最高為44萬元</li>\n	<li>電腦及網路通訊費全額</li>\n</ul>\n<!-- /wp:list --><!-- wp:heading {\"level\":3} -->\n\n<h3><strong>學貸利率、就學貸款還款期限</strong></h3>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p><strong>學貸利率：</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>普匯小幫手來幫你整理學貸還款的資料，目前學貸利率最低為台銀、台北富邦與高雄銀行1.15%。若為其他銀行可參考台銀的<a href=\"https://www.tbb.com.tw/TBB-ContentUtils-portlet/html/app-11/doc/%E8%B2%B8%E6%AC%BE%E6%94%A4%E9%82%84%E8%A8%88%E7%AE%97%E5%BC%8F%E8%AA%AA%E6%98%8E.pdf\">就學貸款攤還計算方式</a>，也可利用<a href=\"https://rate.bot.com.tw/trial/t05\">台銀的消費者貸款試算</a>得知。除此之外，政府在民國107年時提出<a href=\"https://www.edu.tw/News_Content.aspx?n=9E7AC85F1954DDA8&amp;s=5E57C530BE471E52\">「只繳息不還本」和「放寬繳款門檻」的政策</a>，讓貸款人於畢業後最多有4年的只繳息期間和緩繳期。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>就學貸款還款</strong><strong>時間：</strong></p>\n<!-- /wp:paragraph --><!-- wp:list -->\n\n<ul>\n	<li>應於教育階段學業完成後滿一年之次日起開始攤還本息。</li>\n	<li>參加教育實習者，應自實習期滿後滿一年之次日起開始攤還本息。</li>\n	<li>退學或休學的就學者，應自休、退學日後滿一年之次日起開始攤還本息。</li>\n	<li>出國留學、出國定居或出國就業者應於出國前一次償還。</li>\n</ul>\n<!-- /wp:list --><!-- wp:paragraph -->\n\n<p><strong>學貸還款期限：</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>貸款一學期就有一年的償還期間，得在完成學業後一年內以每月償還，依此類推。例如：借八學期者，則借款金額會合併成一筆，分八年（96期）平均償還。但是如果沒有經濟上的負擔，也可提早還款。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>逾期未還款：</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>將會由承貸銀行依法追繳，並將資料送請財團法人金融聯合徵信中心建檔，列為金融債信不良往來戶，直到貸款完全償還為止；已償還者，由承貸銀行通知財團法人金融聯合徵信中心註銷紀錄。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2><strong>申請就學貸款期限過了，但急需用錢怎麼辦？</strong></h2>\n<!-- /wp:heading --><!-- wp:image -->\n\n<p><img alt=\"學生借貸\" src=\"https://lh5.googleusercontent.com/ZxSjNNVN5AvMnZoP4DpU0kjZOnmQDckLVFr2MMQjnAgOIOiEHKGoQTPmTS6iMYxd6xG5Uziw3aZhe1UKkMu13o_MN81AENfW16O-qSwliXU7-7abeQsw5gnta8N8J_oM0fl1EZ9n\" style=\"height:200px; width:300px\" /></p>\n<!-- /wp:image --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>銀行是每個人第一時間會想到的借款管道，但銀行借款與否會從個人收入以及過去信用紀錄作為判斷，因為學生收入不穩定，沒有累積信用資料可供聯徵參考，導致銀行無法判斷你是否會依約準時還款，進而無法放款。於是轉而選擇線上學生借貸平台，但借貸前要注意合法性和安全性，選擇經過政府合法立案的管道。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n\n<h2><strong>學生借貸平台推薦：普匯金融科技</strong></h2>\n<!-- /wp:heading --><!-- wp:paragraph -->\n\n<p>普匯金融科技經過政府立案，提供學生借貸平台，截至目前為止普匯已成功幫助11,075名同學解決生活急需與達成夢想，普匯未來也將推出助學貸款，提供給中低收入戶、單親、績優生們更好的生活環境與品質。</p>\n<!-- /wp:paragraph --><!-- wp:image -->\n\n<p><img alt=\"學生借貸\" src=\"https://lh6.googleusercontent.com/DAhQyzUMG6OUlm5sZQZ1hWuRfloeEqBmA68BJLxJupKN0dWjzHGZ0_udKkd8hGPTCHSRBVrPLiDvb81cSJUGnn3-GshdX6CnmtOLXfCQPQW79lLMvozYpiyrYNUJRg1Psk8bXP4R\" style=\"height:174px; width:600px\" /></p>\n<!-- /wp:image --><!-- wp:paragraph -->\n\n<p>學生借貸申請流程秉持著<strong>普匯的特色：簡單快速</strong>，任何<strong>年滿20歲的大學以上在校生</strong>，只需提供<strong>身分證件、金融帳號、常用社群、學生身份</strong>即可<a href=\"https://event.influxfin.com/R/url?p=web\">申請借貸</a>。簡單三步驟就可以獲得需要的資金，尊重個人隱私，不會被家人和朋友知道。想了解更多線上借貸的詳細介紹嗎？來<a href=\"https://www.influxfin.com/collegeLoan\">普匯金融科技</a>解決你的燃眉之急吧！</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>更多學生貸款資訊：</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><a href=\"https://www.influxfin.com/articlepage?q=knowledge-7084\">學生借錢不再煩惱！合法借錢平台快速貸款，解決你的用錢之急</a></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><a href=\"https://www.influxfin.com/articlepage?q=knowledge-2893\">【普匯報你知】只知道就學貸款? 還有這項超方便學生貸款!</a></p>\n<!-- /wp:paragraph -->', '2020-01-17 10:20:20', '學生貸款好麻煩？一篇搞懂如何申請、學貸還款利率', 'article', 'publish', '2021-01-20 09:29:53', NULL, 1),
(7287, 1, '/upload/2020/01/image2-3.jpg', NULL, '<!-- wp:paragraph -->\n<p>人生難免有突發狀況，可能是您的寵物急需開刀，又或是學期末要交報告了，電腦卻突然壞掉故障，然而這時候銀行不過借，親友也避不見面，這時候的你急得像熱鍋上的螞蟻，到處尋找急需借錢的方法，但又害怕遇到高利貸，到底該如何是好呢？<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯在這邊幫您整理以下三種安全借錢的好方法！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>1.保單貸款</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>保單貸款是利用自己過去購買的保單來申請貸款，一般可借到保單現金價值的八九成左右，年利率大概落在4~6%，按日計息，借多少天就算多少天利息這樣的利率比銀行來的更低，因此更吸引人，當然，若您事前沒有保保險或者保單的現金價值少，就用不上了。而一張保單，最長可以借款6個月，到期後可以再次申請，且貸款期間，保險責任並不會受到影響。總體而言，因為保單貸款的利息低且線上就可申辦，是越來越受到大家的歡迎。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2.私人借錢</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>民間私人貸款公司是許多人在銀行借款不成後的第一想法，理由無他，就是放款速度夠快，許多業者甚至能夠當天撥款，不但能滿足短期小額資金周轉需求，更解決許多人因為信用紀錄有瑕疵而被銀行拒於門外的窘境，最重要的是，有任何問題都能提供免費證詢服務，然而私人借錢的利息多半較高，且多數人也會害怕店家是否為高利貸業者，因此在選擇私人借錢時，一定要事前確定對方是合法營業以及保障自己的個資喔！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>3.P2P線上借貸</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>保單貸款這樣的方法對於兩種人不適用，急需借錢者，沒有時間等待公司流程審核，亦或是您沒有保險或是保單現值太少，因此您轉向搜尋私人借款，但您可能又會擔心，是否會遇到詐騙公司變成警示帳戶？還是會不會以各種名義多次要求付款，甚至玩文字遊戲以貸款合約為由強詐錢財？這裡可以很肯定的告訴你來到合法經營的P2P借貸公司，這些杞人憂天通通不會發生！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>inFlux普匯金融科技公司建構全台第一個Al無人化投融資科技平台，結合「金融專業」與「高端科技」提供獨有的安全線上借貸服務，只需一隻手機就可以完成申辦，順利借到錢，同時透過P2P自動媒合的方式最快一小時就可以輕松到帳，且利率最低只要5%起。普匯讓借錢變得更為便捷、安全且隱私，給急需借錢救急的您最佳的借錢方式！<br></p>\n<!-- /wp:paragraph -->', '2020-01-31 10:31:29', '快速借錢方法一次看透透！', 'article', 'publish', '2020-02-18 15:00:22', '', 0),
(7372, 1, '/upload/2020/02/數位金融趨勢2020.jpg', NULL, '<script type=\"application/ld+json\">{\"@context\":\"https://schema.org\",\"@type\":\"FAQPage\",\"mainEntity\":[{\"@type\":\"Question\",\"name\":\"數位金融是什麼？\",\"acceptedAnswer\":{\"@type\":\"Answer\",\"text\":\"數位金融（Digital Finance）是指，消費者透過網路或行動裝置就可以享受各種金融服務，消費者不必跑到臨櫃，也不受限於銀行的營業時間，便可以線上完成金融相關業務。\"}},{\"@type\":\"Question\",\"name\":\"金融科技是什麼？\",\"acceptedAnswer\":{\"@type\":\"Answer\",\"text\":\"金融科技（Fintech）的重點是「科技」，將本質由人類執行的工作內容改由科技來執行，降低成本，更能夠用科技幫顧客打造客製化的服務。\"}},{\"@type\":\"Question\",\"name\":\"2020數位金融趨勢是什麼？\",\"acceptedAnswer\":{\"@type\":\"Answer\",\"text\":\"「2020年科技發展趨勢的核心在於『以人為本的智慧空間』，也是現今科技發展最重要的面向之一。」將為金融科技帶來全新的變革，包含區塊鏈及AI人工智慧等技術，讓數位金融的使用體驗更加簡單、快速、安全與隱私。\"}},{\"@type\":\"Question\",\"name\":\"數位金融怎麼選擇？\",\"acceptedAnswer\":{\"@type\":\"Answer\",\"text\":\"普匯金融科技為台灣數位金融邁向金融科技的先驅，提供全台第一個完全線上貸款的AI無人化融資平台，致力於提供學生和上班族輕鬆貸款，追求簡單、快速、安全與隱私。\"}}]}</script>\n\n<b>數位金融是什麼？</b>\n\n<span style=\"font-weight: 400;\">相信許多人早上起床的第一件事，便是查看股市行情並選擇多買進或者賣出；接者上班的途中想順手買一份早餐，於是掏出手機打開支付app快速嗶一下；下午接到家人電話希望幫忙代繳一筆款項，於是你操作線上繳費3分鐘完成。以上一連串的金融服務，你知道自己「數位金融」幾項了嗎？別懷疑，以上全部都是，而你也已經成功成為「數位金融」的熟客戶了。</span>\n\n<span style=\"font-weight: 400;\">數位金融（Digital Finance）係指，隨著科技的發展，消費者透過網路或行動裝置就可以享受各種金融服務，藉由虛擬通路，消費者不必跑到臨櫃，也不受限於銀行的營業時間，便可以線上完成金融相關業務，舉凡轉帳、買外幣、繳卡費，數位金融即是把銀行數位化的過程，讓顧客跨越場景享受銀行服務。</span>\n\n<b>別再把</b><b>數位金融和fintech</b><b>搞混，你還傻傻分不清嗎？</b>\n\n<span style=\"font-weight: 400;\">許多人習慣將數位金融（Digital Finance）和金融科技（Fintech）兩個名詞混著使用，然而它們所代表的金融商業模式卻差異甚大。數位金融主要指銀行為了加速作業流程，提供顧客不同的場景金融而發展出的線上化服務模式，舉例而言：上一段所指的轉帳、買外幣、繳卡費，又或者保單交易、信用貸款、購買基金等等，只要是現況下銀行已提供的金融服務結合科技變成線上作業，就屬於數位金融的範疇。</span>\n\n<span style=\"font-weight: 400;\">金融科技（Fintech）則指稱本質由人類執行的工作內容改由科技來執行。不僅能夠降低成本，更能夠達成普惠金融，用科技幫顧客打造客製化的服務，哪怕資本額小的顧客都可以獲得良好的金融服務，打破以往服務客群專限於富裕階層。</span>\n\n<span style=\"font-weight: 400;\">當今</span><span style=\"font-weight: 400;\">數位金融趨勢</span><span style=\"font-weight: 400;\">是朝著金融科技發展，從Digital Finance邁入Fintech，期待能夠透過科技簡化業務流程和後勤作業，並且落實普惠金融，替社會上每一個人提供金融服務。市場研究機構Gartner進行</span><span style=\"font-weight: 400;\">數位金融調查</span><span style=\"font-weight: 400;\">並發表2020年應該關注的十大科技趨勢：</span>\n<ol>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">邊緣運算</span></li>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">區塊鏈應用</span></li>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">高自動化</span></li>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">結合行動與社群加擴增實境（AR）的多重體驗</span></li>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">科技民主化</span></li>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">人類機能增進</span></li>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">科技透明化與可追蹤化</span></li>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">分散是雲端</span></li>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">自動化裝置</span></li>\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">AI人工是會安全防護</span></li>\n</ol>\n<span style=\"font-weight: 400;\">台灣數位金融邁向金融科技的先驅—普匯金融科技，全台第一個完全線上貸款的AI無人化融資平台，致力於提供學生和上班族貸款輕鬆貸款，追求簡單、快速、安全與隱私。運用人工智慧進行大數據分析，整合會員用戶資料及還款紀錄，以系統進行風險管控，自動完成授信及續約的程序，讓使用者只要有一支手機在極短的時間內就可以完成申貸手續，達成現代人希望簡單、方便、尊重隱私的生活需求。</span>\n\n<span style=\"font-weight: 400;\">普匯提供網路借貸的顧客可以分為</span><b>「</b><span style=\"font-weight: 400;\">學生借款平台</span><b>」</b><span style=\"font-weight: 400;\">以及「上班族借款平台」，主要依據年紀來區分，大學以上在校生申請學生手機貸；35歲以下大學以上畢業生則申請上班族手機貸，普匯會依據顧客提供資訊透過風險評價模型給予信用評等分數，此分數將決定顧客的借貸額度以及借貸利率。普匯身為P2P產業數位金融的領航者，金融科技的開拓者，致力於提供顧客最佳的金融體驗。</span><!--FAQPage Code Generated by https://saijogeorge.com/json-ld-schema-generator/faq/-->', '2020-02-07 16:56:50', '您今天「數位金融」了嗎？', 'article', 'publish', '2020-02-18 15:04:04', '', 0),
(7399, 1, '/upload/2020/02/工程師貸款推薦.jpg', NULL, '<script type=\"application/ld+json\">{\"@context\":\"https://schema.org\",\"@type\":\"FAQPage\",\"mainEntity\":[{\"@type\":\"Question\",\"name\":\"普匯工程師貸款跟一般的信貸有什麼不同呢？\",\"acceptedAnswer\":{\"@type\":\"Answer\",\"text\":\"普匯金融科技致力科技人才的培養，資訊背景的借款人都會享有一些利率上的優惠。針對擁有專業技能的工程師提供借款額度高達20萬、利率減免優惠5%起，且超快速半小時審核過件。\"}},{\"@type\":\"Question\",\"name\":\"申請普匯工程師貸款有什麼條件限制？\",\"acceptedAnswer\":{\"@type\":\"Answer\",\"text\":\"年齡須年滿20歲，資訊軟體相關系所在校生，或畢業上班族從事資訊科技類相關的從業人員，也需準備申請所需文件。\"}},{\"@type\":\"Question\",\"name\":\"申請普匯工程師貸款需要多少手續費？\",\"acceptedAnswer\":{\"@type\":\"Answer\",\"text\":\"平台僅收取本金的3%，最低為500元，作為平台服務費。\"}},{\"@type\":\"Question\",\"name\":\"普匯的系統安全嗎?\",\"acceptedAnswer\":{\"@type\":\"Answer\",\"text\":\"普匯嚴格遵守「個人資料保護法」，未經同意及授權，絕不提供他人使用。資料均存於全球最大的亞馬遜AWS雲端伺服器。\"}},{\"@type\":\"Question\",\"name\":\"普匯工程師貸款審核時間要多久？  \",\"acceptedAnswer\":{\"@type\":\"Answer\",\"text\":\"一般銀行標榜著線上驗證但是通常還是要客戶跑一趟公司，普匯工程師信貸採用App全自動線上進行審核與貸放，最快核貸1小時可完成，通常1～3天，實際時間需視投資人的認購意願。\"}}]}</script>\n\n<span style=\"font-weight: 400;\">不管你是剛畢業的大學生還是已經工作一段時間的上班族，生活上難免會遇到需要資金卻無法向人開口的時候，向銀行借款已經不是唯一的一條選擇，在資訊科技迅速發展下，現今靠著寫程式謀生的學生與上班族越來越多，不過好的電腦動輒4、5萬，更好的規格可能需要上到6萬多元，所以當就讀資工系時設備就是一項很重要的投資產品，再者畢業後如果只想靠單純的寫程式來賺錢的話著實是一種很辛苦的過程，現在人們主要都靠著被動式收入來迅速增加自己的資產，一般人單單只靠薪水是無法買車買房的，一定得自己學習一些投資理財的知識，其實現今已經有很多大學生很早就已經投入股票市場，投資市場不一定要有很大的資本才可以進入，以普匯自己的數據庫來看發現大學生借款端在「投資理財」的筆數有明顯越來越高的趨勢，如果您也是這幾種類型的學生or上班族，</span>\n\n<span style=\"font-weight: 400;\">這裡為您介紹普匯金融科技P2P的</span><span style=\"font-weight: 400;\">工程師借貸</span><span style=\"font-weight: 400;\">。</span>\n<ul>\n 	<li>工程師信用貸款:</li>\n</ul>\n<b>使用工程師貸款跟一般的信貸有什麼不同呢？</b>\n\n<span style=\"font-weight: 400;\">普匯金融科技致力於幫助那些銀行所觸及不到的人們，並且本身也是金融科技公司，所以我們很注重科技人才的培養，只要是資訊背景的借款人都會享有一些利率上的優惠！</span>\n\n<b>年齡限制：</b>\n\n<span style=\"font-weight: 400;\">學生</span><span style=\"font-weight: 400;\"> : 年滿20歲資訊軟體相關系所的大學以上在校生。</span><span style=\"font-weight: 400;\">\n</span><span style=\"font-weight: 400;\">上班族</span><span style=\"font-weight: 400;\">：年滿20以上資訊軟體相關系所畢業上班族，並從事資訊科技類相關的從業人員。</span>\n\n<b>申請所需文件 :</b>\n\n<b>身分證件</b><b>常用社群</b><b>最高學歷證明</b><b>工作證明</b><b>金融帳號</b><b>聯徵報告</b>\n\n<span style=\"font-weight: 400;\">   </span><b>工程師專業證照</b>\n<ul>\n 	<li>普匯手續費</li>\n</ul>\n<span style=\"font-weight: 400;\">平台僅收取本金的3%，最低為500元，作為平台服務費。</span>\n\n<span style=\"font-weight: 400;\">Ex：若您申請20000元，服務費即為600元。</span>\n<ul>\n 	<li>普匯的系統安全嗎?</li>\n</ul>\n<span style=\"font-weight: 400;\">普匯嚴格遵守「個人資料保護法」，未經同意及授權，絕不提供他人使用。資料均存於全球最大的亞馬遜AWS雲端伺服器。</span>\n<ul>\n 	<li>審核時間 :</li>\n</ul>\n<span style=\"font-weight: 400;\">一般銀行標榜著線上驗證但是通常還是要客戶跑一趟公司，普匯</span><span style=\"font-weight: 400;\">工程師信用借貸</span><span style=\"font-weight: 400;\">採用App全自動線上進行審核與貸放，最快核貸1小時可完成，通常1～3天，實際時間需視投資人的認購意願。</span>\n<ul>\n 	<li>使用工程師貸款的好處</li>\n</ul>\n<span style=\"font-weight: 400;\">普匯針對擁有專業技能的Techie們提供更優惠的專案，借款額度高達20萬、利率減免優惠5%起，且超快速半小時審核過件。</span>\n<ul>\n 	<li>為什麼要選擇普匯?</li>\n</ul>\n<span style=\"font-weight: 400;\">普匯金融在無論是在申貸、審核、對保的部分全面線上化，對於線上申貸失敗時，我們也將會快速的通知客戶，方便客戶直接修改資料，以利後續的申貸流程，完全替代了以前人工審核較低的效率，並且主打5大特色，安全、簡單、快速、隱私、低風險高報酬，使用普匯借款分期是投資自己的時間，讓財務管理配置更靈活！</span>\n\n<!--FAQPage Code Generated by https://saijogeorge.com/json-ld-schema-generator/faq/-->', '2020-02-07 16:42:57', '寫程式有辦法寫出第一桶金嗎？', 'article', 'publish', '2020-02-18 15:02:36', '', 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(7419, 1, '/upload/2020/02/mimi-thian-vdXMSiX-n6M-unsplash.jpg', NULL, '<!-- wp:paragraph -->\n<p>隨著AI人工智慧的發展，許多人為的工作漸漸被取代，相關從業人員和技術勞工面臨轉型的挑戰，在麥肯錫的全球研究報告（McKinsey Global Institute）〈未來工作：自動化、就業與生產力〉中指出，在近六成的職業中，至少有30%的工作內容將被人工智慧取代。以下列舉了10大容易被取代的職務，你/妳十年後還能在現今工作崗位中屹立不搖嗎？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>隨著</strong><strong>AI</strong><strong>的發展，面臨轉型的十大工作</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>會計：面對繁雜的數字和報表，AI能夠快速地進行計算和分析，不僅能提高效率和準確性，同時能降低舞弊的可能性，提高訊提的價值，因此傳統審計和基礎會計人員查帳工作將被取代。</li><li>醫生：科幻影片中的機器醫生已成為可能，隨著醫療技術和機器醫生的發展，目前機器人能完成如膝關節和視力矯正的手術。此外，目前正在發展聊天機器人，提供病人問診的服務。</li><li>保全人員：機器保全透過高分辨率攝影鏡頭看守整棟大樓，並配備定向麥克風與紅外線感測器，隨時監控任何可疑的聲音和動作。機器保全目前已被應用於許多商業大樓當中。</li><li>營銷員：人工智慧依靠數據收集和分析，在面對形形色色的消費者時，能夠根據他們的消費喜好、購買能力、消費習慣發送相對應的個人訊息和廣告投遞。</li><li>軍人：在美國和伊拉克的戰爭當中，美國已將無人機應用於軍方暗殺上。</li><li>接待員：接待機器人能夠識別超過20種語言，以及顧客的身高、年齡、性別等，提供顧客客製化的服務，目前比利時已有兩家醫院使用接待機器人取代接待員的工作。</li><li>工人：建築業和製造業廣泛使用機器人替代人力，提升速度和確保商品品質和規格統一性，在經濟發達的國家尤其明顯。隨著工業化和經濟實力提升，在不久的將來幾乎所有企業皆會使用機器人。</li><li>快遞員：飛行或陸地交通的無人機與機器人正漸漸取代快遞員，現今我們常使用的uberOOOO、foodOOOOO、戶O送未來將有機會透過小型機器人進行短程客戶投遞外賣。</li><li>公交車司機：隨著特斯拉的半自動駕駛技術發展，未來有望應用在公共交通工具與出租車領域。</li><li>農民：日本京都使用機器人農民收割農作物，完全由機器人運營生菜農場，負責種植、灌溉以及收割農作物。</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p><strong>如何不被</strong><strong>AI</strong><strong>取代？您必須具備的能力</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>台積電創辦人張忠謀先生在談「台灣IT產業的過去、現在和外來」的講座中提到，面對AI技術的強勢來襲，職場人員是否具備「數據分析能力」成為關鍵。現在蘋果、亞馬遜、臉書、Google等大公司都在搜集大量數據進行顧客分析，發展商業行為，數據是冰冷的資料，需要藉由專業人員進行決策，因此如果將以上的十項會被取代的工作從技術層面發展至管理或決策層面，例如：管理會計人員、病症分析醫師、生態農場規劃人員等等，皆是不容易被AI取代的職位，也是未來從業人員專精的方向。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此外，五大最不可能被AI取代的工作包括：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>作家/創作者：無形的靈感是每個人獨一無二的先天能力，創作源來自不同經歷的家世背景、思維模式、涉略情境，作家和創作者的作品依賴人類情感，而情感正是AI所缺法的感官。</li><li>體育/舞蹈教練：教練針對運動員的弱點進行補強和修正，AI無法理解人類產生肢體動作的意涵或者藝術性。</li><li>人力資源經理：涉略到管理和人力分配管理的工作難以被自動化。人力資源經理時常要調停員工內部紛爭，這需具備良好的溝通表達能力來化解衝突和誤會，良好的情緒理解能力是AI無法學會的。</li><li>律師：律師需要溝通辯論技巧，幫助顧客在法庭上進行一來一往的溝通和辯論進而說服法官。目前研發一款能夠與人類辯論的機器人尚在初淺的階段，機器人的應變能力和臨場感應能力遠遠低於人類。</li><li>神職人員：同理心、情感、信念是許多教徒心中的依歸。神職人員的話語和開導會無法被冷冰冰的機器人取代，因為機器人不具情感。</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>歸結以上的五項工作，在未來凡是涉略情感、管理、溝通的職位皆存在發展性和不可取代性，因為機器人無法學會人類與人類的相處模式。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>AI</strong><strong>可以如何提升金融服務？</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>AI具備深度學習的能力，不僅能完成部分金融工作自動化；還具有精實作業流程、提高企業生產力的潛力，金融業涉及大量的數字，因此AI與金融產業的結合必定能夠大大提升效益。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據財團法人國家政策發展研究基金會，AI在金融業已有許多應用，隨著金融科技（FinTech）興起，目前在智慧金融、智慧保險取得成就。智慧金融包括<strong>財富管理（理財機器人）、智慧客服、信用風險管制等</strong>。理財機器人透過大數據及計量模型，結合專家智慧，能依據使用者之風險屬性提供自動化及客製化的投資組合財富管理服務，並向使用者收取一定比例的佣金，理財機器人的服務規模和人數遠大於理專數十倍。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>德國安聯集團於2017年發表的調查報告，台灣平均每人的金融資產淨值約為342萬台幣，名列亞洲第二和全球第五名，顯示台灣財富管理市場的未來發展潛力無窮。目前許多券商和銀行紛紛引進理財機器人並與理專做搭配，因為財富管理是許多銀行的主要獲利來源。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此外，AI在金融業的智慧客服應用。AI提供客戶24小時的線上文字對談，解決顧客的疑難雜症，並運用智能語音、智能圖像及生物特徵識別等AI技術進行迎賓導覽與語音互動的工作，以及線上查詢信用卡可用額度、消費明細、繳款紀錄、帳單金額、紅利點數、補寄電子帳單等服務，讓金融服務更便利有趣，減少客服人力。在信用風險管制應用方面，包括AI核貸模型、反詐欺、反洗錢、信用評估、保險定價已上線，可藉此提升業績及降低違約及洗錢等風險。AI在保險業的核保、出單、理賠應用也能發揮成效，提升業績。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>普匯觀點</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>別緊張，培養AI無法取代的能力成為關鍵。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以會計從業人員為例，雖然傳統的審計人員和基礎會計人員漸漸被AI取代，但只要是涉及主觀判斷的會計知識仍需由專業知識人員進行，例如：企業進行合併重組、融資上市、跨國合作等。此外，管理會計（Management Accounting）應用會計學收集來的企業數據和報表，須經由專業人員主觀判斷以及進行數據解讀，才能提供給予公司內部管理人參考和監控企業活動的建議，幫助公司作出決策及修訂。因此，正在考取會計師資格的你不需害怕丟了飯碗，或者畢業即失業，而是要提升自己的專業知識水平，讓自己不再只是基礎會計人員，而是AI無法替代的會計數據分析師。</p>\n<!-- /wp:paragraph -->', '2020-02-07 15:10:51', 'AI浪潮來襲，如何保住自己的飯碗？', 'article', 'publish', '2020-02-11 09:04:56', '', 0),
(7422, 1, '/upload/2020/02/dhaofhi.png', NULL, '<!-- wp:paragraph -->\n<p><strong>什麼是債權轉讓？</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>當投資人出借後，於借貸合約未到期的期間內，可以選擇賣出債權，取回全部/部分本金，也就是所謂的債權轉讓，債權將由第三方受讓者出資接收。債權轉讓的轉讓方式主要依取得的金額可以區分為三種：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>假設轉讓一筆剩餘本金$10,000元的債權，應收利率為14%，債權至到期日剩餘3個月（2020/2/10~2020/5/10），且債轉需支出本金餘額0.5%轉讓手續費從價金中扣除。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>債權總額＝債權剩餘本金＋預期最高可收利息。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>平價：以債權剩餘本金轉讓。（取得$10,000 - $10,000 x 0.5% = $9,950元）                                              </li><li>折價：轉讓價格介於債權總額的八折至債權剩餘本金之間。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>（取得$10,228 x 0.8 = $8,184 ~ $9,999 元之間）</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>溢價：最高取得債權總額的金額。（取得$10,001 ~ $10,228 元之間）</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>                         剩餘本金&nbsp;&nbsp; 每期固定收回本金&nbsp;&nbsp; 本期應得利息</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第0期（2/10） 10,000&nbsp;&nbsp;\n</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第1期（3/10） &nbsp;6,667&nbsp;&nbsp;&nbsp;&nbsp; 10,000/3 = $3,333&nbsp;&nbsp;&nbsp; 10,000x14%x(29/366)=$111</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第2期（4/10）&nbsp; 3,334&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;            3,333&nbsp;&nbsp;&nbsp; 6,667x14%x(31/366)=$79</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>第3期（5/10）&nbsp;&nbsp;&nbsp;&nbsp; 0&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;                3,334&nbsp;&nbsp; &nbsp;3,334x14%x(30/366)=$38</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>總額&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;        10,000&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;                                           $228</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此試算表格採2020/02/10~05/10，每月10日為利息支付日，以認購一個月以上開始可開始收取利息。若債權轉讓日不同，則式算金額會變動。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>債權轉讓的好處？</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>一、出讓者</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>債權轉讓改變資金的周期，可以短時間內實現資金變現，提高<strong>債權流動性</strong>，利用變現的現金進行其他偏好的投資，讓理財更具<strong>靈活性</strong>。此外，對於逾期債權而言，出讓者透過折價讓其它投資人承接債權，降低風險和損失。普匯投資提供線上債權轉讓平台，開放給平台上其餘投資者認購，讓您不僅安心投資更能快速變現。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>二、受讓者</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>受讓者因承接出讓者的債權，所以享有<strong>期數較短的優勢</strong>，可以讓資金更具彈性。受讓者在選擇標的時可以查看還款人前期的還款紀錄，是否有逾期的情況，若是已有還款紀錄的債權相對具有安全性，可供售讓者從中評估投資的風險是否與期望相符，再對有興趣的標的進行認購。此外，受讓者在認購逾期債權前，能夠評估預期收回的債權，並以較低的價格購入，用風險換取可能的的高收益。普匯投資app提供「智能投資」，方便受讓者篩選適合自己的債權，提高投資效率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>如何進行債權轉讓？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>投資人登入「普匯投資」app，在初始頁面右下角「我的」中選取「我的資產」，再挑選預轉讓的債權，投資人可以選擇分批轉讓債權，單筆債權須全額轉讓；也可以選擇一次轉讓所有債權，打包優良資產包出讓，讓沒時間挑選標的出借投資人支付部分金額，達到小額分散投資目的。此外，出讓方須注意要支付債權本金剩餘的0.5%予平台，作為轉讓手續費。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>債權轉讓的對象是誰？</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>無限制購買對象，只需是普匯投資平台的用戶即可。若出讓者想要指定債權轉讓對象，雙方可約定一組轉讓密碼輸入。受讓者不需支付任何手續費。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>普匯投資/債權轉讓</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯債權轉讓使您輕鬆投資無負擔，債權轉讓提高債權流動性，增加理財靈活度，更是提供受讓者短期數且明確的還款者還款紀錄。</p>\n<!-- /wp:paragraph -->', '2020-02-07 15:26:23', '「普匯/債權轉讓」好處多', 'article', 'publish', '2020-02-11 09:04:53', '', 0),
(7557, 1, '/upload/2020/02/截圖-2020-02-07-下午5.18.26.png', NULL, '<!-- wp:paragraph -->\n<p>投資不知道如何下手，想諮詢理專服務的最低門檻存款資產卻最低要80萬元。沒有理專只得自己研究，然而許多小資族上班加班就累癱，哪來這麼多時間研究盯盤？想要投資賺錢卻只敢做夢不敢去做。但2008年開始的機器人理財服務，引進人工智慧（AI）系統，輕鬆幫你投資，小資族也能輕鬆用千元投資創造被動收入！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>什麼是機器人理財？</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>理財機器人又稱為智能理財，是一種線上的財富管理服務，引進人工智慧（AI）系統，運用強大的演算法建構而成的投資平台，隨著大數據及演算法日益成熟，提供自動化的投資規劃與資產管理，並以演算法為基礎，提供客戶投資組合建議。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>自從金管會通過開放自動化投資顧問服務（Robo-Advisor），至今已有10多家銀行推出服務，透過數位平台打破時間與地點的限制，小資族再也不怕沒錢沒閒。理財機器人依照顧客的需求來推薦適合的投資方法，像是客戶想要在幾年內獲得多少錢，以及風險屬性（對風險的承受能力）來推薦適合的投資配置（例如：股票與債券的比例）；此外，由於機器人理財依據演算法提供投資組合建議，系統穩定投資，可以避免投資新手因為情緒波動而失手，加上低資產門檻就能進場，十分友善投資小白。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>國外如美國的Betterment、WealthFront與Personal Capital、英國的Nutmeg與Scalable Capital，以及日本的Money Design與Wealth Navi都是透過人工智慧等技術提供智能理財服務。而台灣也早在2017年前，就有王道銀行首先推出機器人理財服務，王道銀行數位理財部副總經理朱挺豪表示，機器人運作是靠深度學習，透過比對一張張線形，預判接下來各資產類別的走勢往上或往下，所以學習內容的品質、內容能否不斷更新，是決定機器人是否有聰明AI（人工智慧）腦的關鍵因素。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>投資小白、小資族照過來，普匯幫您整理好最易進場的6家機器人理財！</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>也有不少金融機構的機器人理財要數十萬元起跳，然而普匯已經幫您搜尋好門檻最低、最友善新鮮人和大學生的方案如下：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td><strong>&nbsp;</strong></td><td>王道銀行O!My Robot!</td><td>中國信託智動Go</td><td>台新Richart&nbsp;銅板投資</td><td>國泰世華智能投資</td><td>富邦奈米投</td><td>永豐ibrAin</td><td>普匯P2P債權投資&nbsp;</td></tr><tr><td>投資標的</td><td>基金股票</td><td>基金</td><td>基金</td><td>基金</td><td>全球ETF</td><td>ETF</td><td>債權</td></tr><tr><td>投資門檻</td><td>定期定額新台幣1,000元、單筆新台幣1,000元</td><td>定期定額新台幣1,000元、單筆新台幣10,000元</td><td>定期定額&nbsp;新台幣10&nbsp;元單筆&nbsp;新台幣10&nbsp;元&nbsp;</td><td>定期定額新台幣5,000元、單筆新台幣30,000元</td><td>定期定額100美元、單筆100美元</td><td>定期定額3,000元、單筆投資新台幣30,000元</td><td>新台幣1000元，可選擇對不同借款人投資不同金額<strong>&nbsp;</strong><strong>&nbsp;</strong><strong>&nbsp;</strong><strong>&nbsp;</strong></td></tr><tr><td>特色</td><td>最新最成熟、介面活潑與部落客合作</td><td>自動化再平衡機制，智能系統直接幫客戶調整比率</td><td>你沒看錯的銅板價投資，試玩好選擇</td><td>經客戶同意使用動態平衡「賣出高價資產、買入低價資產」</td><td>規模大、英國團隊「人機合作」</td><td>最客製化、首創「歷史模擬」回測功能假設重大事件</td><td>報酬率最高、風險最低。介面最好用、最人性化，投資最高利率10%！還能幫助新鮮人及大學生的教育生活費&nbsp;&nbsp;&nbsp;&nbsp;</td></tr><tr><td>推出時間</td><td>2017.04</td><td>2017.05</td><td>2018.05</td><td>2018.10</td><td>2019.04</td><td>2019.08</td><td>2018.08</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<p class=\"has-medium-font-size\"><strong>普匯觀點：</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>自從低門檻的機器人理財問世，即使不了解投資策略也能進場，創造被動收入。想要投資卻不知道從何開始的投資小白，也能藉由機器人投資的配置策略為開端，查詢、了解機器人提供的標的，並且主動追蹤。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>賺錢最快的方法是用錢滾錢，連有錢人都在借錢投資賺更多錢！雖然機器人理財最低只要10元起，但若真的想獲利，專家們仍建議至少以千元為單位。大學生或是新鮮人若想湊足數千元做完整的投資，就找普匯金融科技！我們是由金管會、金融總會、亞洲矽谷、資策會、工研院輔導，台灣最高知名的投融資平台，借貸投資流程全線上化，只要年滿20歲，提供身分證件、金融帳號、常用社群、學生身份即可<a href=\"https://www.influxfin.com/%e5%ad%b8%e7%94%9f%e8%b2%b8%e6%ac%be/\">申請借貸</a>。簡單三步驟就可以獲得需要的資金，尊重個人隱私，幫助你輕鬆賺入人生第一桶金！</p>\n<!-- /wp:paragraph -->', '2020-02-10 09:13:24', '小資族輕鬆投資 機器人幫您理財最低10元起！', 'article', 'publish', '2020-02-11 09:04:32', '', 0),
(7565, 1, '/upload/2020/02/pp.jpg', NULL, '<!-- wp:paragraph {\"align\":\"center\",\"customBackgroundColor\":\"#ffbb00\",\"fontSize\":\"large\"} -->\n<p style=\"background-color:#ffbb00;text-align:center\" class=\"has-background has-large-font-size\"><strong>綠色金融</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>亞洲金融科技發展的持續性與綠色金融科技?</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>隨著澳大利亞的叢林大火和雅加達的嚴重洪災，氣候變化的影響正嚴重打擊亞太地區。世界再也不能否認它給我們的星球造成的損失。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>而去年，我們看到了對亞洲社會負責任投資(responsible investing)的高度重視，新加坡金融管理局在新加坡金融科技節上宣布啟動一項20億美元的計劃，以支持新加坡<strong>綠色金融</strong>的發展。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\">什麼是綠色金融(永續金融)投資?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>超越了股票債券以及私募股權、房地產、礦產等另類投資一直以來的傳統類型的投資理念範疇，被視為解決全球暖化，森林破壞等環境問題的有效對策，同時也是綜合考量了各類社會問題的先進投資理念。綠色投資的具體投資對象涉及股票、債券、私募股權、房地產、基礎設施、森林植樹、農業農地等領域。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\">綠色金融投資的對象包括</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(1)重視環境保護以及可持續性發展企業的股票 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(2)上述企業的債券 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(3)再生能源或新型能源企業的私募股份 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(4)節能型綠色房地產</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(5)節能型交通設施，供給可再生能源的基礎設施 </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>(6)能夠有效吸收溫室氣體的森林植樹投資，可持續性農地/農業投資</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":7566} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2020/02/ss.jpg\" alt=\"\" class=\"wp-image-7566\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>亞太地區（包括日本）的綠色債券發行量也創下紀錄，2019年價值近480億美元，這是因為越來越多的投資者開始考慮環境，社會和治理（ESG）等等的社會問題，而不只是考慮本身的投資營利。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>有趣的是，中國在2019年引領全球綠色債券，掌握了大部份全球綠色債券市場。但是，在推進亞洲的金融發展仍存在許多挑戰。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>以台灣來說，日月光為亞洲綠債先驅，曾於2014年推出亞洲第一檔由製造業發行的綠色債券，也是台灣首家發行綠色債券的企業，在2019年10月初也發行了3億美元的綠色債券，成功開啟台灣國際債券市場的綠能新頁。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>而金融科技技術也在綠色金融上扮演重要的角色，較受矚目的仍是UNEP Inquiry引領的綠色數位金融聯盟(GDFA)以及WePower(基於區塊鏈的垃圾能源融資和交易平台)方案。GDFA的主要參與者螞蟻金服，已提出一種應用程式服務，能為用戶提供碳帳戶，作為信用及儲蓄帳戶。這使該公司在中國的4.5億用戶能夠對其碳足跡進行基準測試，而後經過分析該些用戶之金融交易歷程，而計算出該些用戶「綠色能源」信用帳戶餘額的增減。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在瑞典，參與了「綠色金融科技網路」(GFN)的Fintech公司Trime則藉由建置一個允許民間投資者參與配置及投資新興國家太陽能光電計畫的數位投資平台，以終結後進國家「能源貧窮」課題。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"backgroundColor\":\"luminous-vivid-amber\",\"fontSize\":\"large\"} -->\n<p style=\"text-align:center\" class=\"has-background has-large-font-size has-luminous-vivid-amber-background-color\"><strong>亞洲數位金融</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>使用人工智能來使金融持續發展</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>關於亞洲可持續未來金融的首份報告指出，亞洲最大公司的目標和實際行動之間存在相當大的差距。超過60％的亞洲公司製定了減少排放的政策，但只有三分之一的公司制定了具體的減排策略以支持其目標。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>人工智能和機器學習的進步可以幫助緩解這一大挑戰，即量化和衡量可持續性發展的成功機率。這些工具可以更輕易的量化持續性金融的數據，並提供工具來分析隨時間而產生實際變化的影響。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>亞洲打擊金融犯罪的意願</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>近年來，隨著亞洲新興市場競相發展其數位經濟和基礎設施，如何有效地進入新的數位時代是2020年各地區金融服務業面臨的最大挑戰之一。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>去年，部份地區的金融形勢令人振奮，例如在香港和新加坡引入了數位銀行牌照，但這種轉變也帶來了新的風險。網路領域的風險正變得越來越高，組織嚴密且復雜的網路罪犯使用網路隱藏身份，如果任其隨意遊走在法律漏洞，最後可能會通過銀行來取得大量又無法追蹤的不法資金。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>實際上，Refinitiv(一家跨國大眾媒體及資訊提供商)的2019年年度金融犯罪報告發現，在過去12個月中，亞太地區有75％的企業受到金融犯罪的影響。人們認為，一些公司在加入新客戶，供應商和合作夥伴時，貪圖方便而疏於調查客戶的行為給意圖犯罪的人製造了機會。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯預計，在擁有更好的數據和分析工具的支持下，這一趨勢將持續到2021年，以在亞洲建立更強大，更具前瞻性的數位經濟，我們有P2P產業內最先進的AI技術與線上自動化系統，在遏止犯罪上貢獻了極大的幫助，我們的目標是能夠幫助到銀行所無法觸及到的地方，金融科技不能只是為富人而服務，而是要為了解決大眾的問題而產生。</p>\n<!-- /wp:paragraph -->', '2020-02-11 09:13:43', '2020年亞洲金融服務的趨勢', 'article', 'publish', '2020-02-11 09:13:45', '', 0),
(7616, 1, '/upload/2020/02/image.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-red\",\"fontSize\":\"small\"} -->\n<p class=\"has-text-color has-small-font-size has-vivid-red-color\"><strong>2015年1月，WeLab宣布完成2000萬美元A輪融資，紅杉中國、TOM集團、DST、Iconiq Capital投資。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\",\"fontSize\":\"small\"} -->\n<p class=\"has-text-color has-small-font-size has-vivid-red-color\"><strong>2016年1月，完成1.6億美元B輪融資，投資方為阿里巴巴、瑞士信貸、中國建設銀行、國際金融公司。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\",\"fontSize\":\"small\"} -->\n<p class=\"has-text-color has-small-font-size has-vivid-red-color\"><strong>2019年12月，香港的Fintech獨角獸WeLab宣布完成1.56億美元的C輪融資，成為今年兩岸新創企業最大筆金融科技融資，讓這間公司的估值達人民幣70億元（約合新台幣305億元），而阿里巴巴創業者基金、建銀國際等5家原有投資方繼續跟投。</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>從香港起家，主打學生手機貸款市場</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2013年，一位來自香港金融圈，曾任職花旗銀行與渣打銀行管理階層共15年的金融老將，成立了一家現在人稱金融科技獨角獸的公司－WeLab。WeLab員工數量約200人，中國員工約170人，而香港員工約30人。員工來自支付寶、騰訊以及百度等科技公司與花旗銀行、高盛集團等金融機構。其產品在香港以WeLend品牌經營，在中國使用的產品則是稱作「我來貸」。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":7618} -->\n<figure class=\"wp-block-image\"><img src=\"/upload/2020/02/image.png\" alt=\"\" class=\"wp-image-7618\"/><figcaption><br></figcaption></figure>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>學生族群在銀行信用卡裡因審核嚴格，額度又不高，因此產生龐大需求，WeLab起家時瞄準了大學生群體。2014年9月推出「我來貸APP」，專門貸款給中國大學生，最低1500元人民幣至最高6000元人民幣，學生只要在App中填寫個人基本資料（包括教育與聯繫人資訊）把學生證和身分證拍照上傳，系統24小時內就會完成審核並放貸。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>15秒就能審批貸款利率，1小時完成放款</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>WeLab並非P2P借貸平台，而是網路貸款公司，WeLend領有香港放債人牌照，是政府許可的私人貸款公司。由金融機構提供資金，然後放款給貸款人，WeLab賺取其中間利差。在香港雖然這種類型的公司並不少，但大多數還是還是必須讓借款人到實體公司做開戶並且面對面審核。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>貸款人申請時，只需要基本個人資料，不需要大量文件，就可以在網路申請，WeLend還強調15秒就能批核貸款利率，比起一般銀行或財務公司更有效率。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>剛完成C輪融資，看2020年後的三大目標</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><strong>目標一：加強B2B技術，瞄準隱私計算</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>WeLab所推出B2B服務品牌“天冕大數據實驗室”，是一家憑藉自身運營經驗為合作夥伴輸出一站式金融科技解決方案的服務商，而另一項重要的技術目標則是，將隱私計算作為重點技術解決方案，並在2020年下半年開始輸出給機構合作夥伴。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>龍沛智表示，WeLab在2018年投入到隱私計算領域，包括雲端計算<a href=\"#_ftn1\">[1]</a>、邊緣計算<a href=\"#_ftn2\">[2]</a>，並已獲得了邊緣計算的相關專利，其專利邊緣計算解決方案將在高效計算的基礎上確保更安全精簡的數據傳輸。在2019年，WeLab全面上線了高維向量檢索——ANN算法<a href=\"#_ftn3\">[3]</a>，也將運用這一算法進行高效的檢測欺詐行為。<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\"><a href=\"#_ftnref1\"><em>[1]</em></a><em> </em><strong><em>聯邦計算</em></strong><em>: 雲端運算（英語：cloud computing），是一種基於網際網路的運算方式，通過這種方式，共享的軟硬體資源和資訊可以按需求提供給電腦各種終端和其他裝置，使用服務商提供的電腦基建作運算和資源。</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\"><a href=\"#_ftnref2\"><em>[2]</em></a><em> </em><strong><em>邊緣計算</em></strong><em>: 是一種分散式運算的架構，將應用程式、數據資料與服務的運算，由網路中心節點，移往網路邏輯上的邊緣節點來處理。</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontSize\":\"small\"} -->\n<p class=\"has-small-font-size\"><a href=\"#_ftnref3\"><em>[3]</em></a><em> </em><strong><em>ANN算法</em></strong><em>: 人工神經網路（英語：Artificial Neural Network，ANN），簡稱神經網路（Neural Network，NN）或類神經網路，在機器學習和認知科學領域，是一種模仿生物神經網路（動物的中樞神經系統，特別是大腦）的結構和功能的數學模型或計算模型。</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><strong>目標二：虛擬銀行將於2020年初開始上線運行</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>2019年4月，WeLab獨資獲得香港金融管理局頒發的虛擬銀行牌照，將虛擬銀行命名為WeLab Bank（匯立銀行）。在最近的發布會上，集團表示WeLab Bank（匯立銀行）將在2020年初開業。龍沛智認為傳統銀行局限於周一至周五開業，但虛擬銀行可於每周七天二十四小時開業，且可在短時間內開戶，而且不需與借款人面對面，更省下了人力成本。虛擬銀行的業務開展也將運用到WeLab過去七年在香港市場所積累的客戶數據和經驗；未來將立足於銀行賬戶，持續完善銀行的金融服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><strong>目標三：兩年內開拓另一個東南亞市場</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在過去兩年時間裡，WeLab的出海戰略首先踏足印尼市場。其在國內印尼品牌Maucash上線一年獲得超過60萬註冊用戶。 WeLab也於2019年9月獲印尼金融服務監管局（OJK）頒發金融科技牌照，成為當地13家持牌運營者之一；並與當地大型綜合集團Astra合資成立了金融科技公司AWDA(Astra WeLab Digital Art)。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>新創公司初期擁有的資源相對少，WeLab草創時比數據量，贏不了螞蟻金服，背後更沒有金融集團當靠山。所以，龍沛智選擇直接跟銀行借錢，再轉借貸給徵信資料少的年輕人，避開跟實體銀行的衝突。普匯創辦的理念亦是如此，金融科技不只是有錢人的專利，我們也要為了讓大眾們也能夠用低廉的成本享有各式金融服務，而如何觸及到台灣所有未被照顧到的群體，就是普匯技術上的挑戰。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\",\"backgroundColor\":\"cyan-bluish-gray\"} -->\n<p class=\"has-text-color has-background has-vivid-red-color has-cyan-bluish-gray-background-color\"><strong>普匯手機貸</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>而與WeLab相同的是，普匯也同樣擁有學生手機貸款的服務，學生不是買不起手機，而是不想沒事多一筆開銷，甚至覺得舊手機好像還可以使用，不過一但手機摔壞了，高額維修費的負擔不如直接買一台新的手機，普匯手機貸特色在於除了可以立即取得商品外還享有免費使用30天後再付款的服務，讓您馬上可以「貸」回家！</p>\n<!-- /wp:paragraph -->', '2020-02-15 12:12:52', '香港Fintech借貸平台獨角獸　－　Welab', 'article', 'publish', '2020-02-15 12:12:54', '', 0),
(7671, 1, '/upload/2020/02/cdc-_HvUN5xlv7I-unsplash.jpg', NULL, '<!-- wp:paragraph -->\n<p>中國武漢爆發的新型冠狀病毒（COVID-19，簡稱武漢肺炎）疫情持續延燒，根據中國（不包含香港和澳門）網站公布的最新病例統計，至今（2020/02/15）已達63,866人確診，1,381人死亡，全球總計共64,472人確診，1,384人死亡。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>中國面對龐大的確診人數，以及高達2%的死亡率，在如此嚴峻的情況下，醫療服務和金援成為民眾急需的資源。中國第三方支付龍頭「螞蟻金服」旗下的「相互寶」醫療互助平台，主打不需要事先繳納保險費，而是在確診感染時，由所有平台上的參與者共同分攤補助支出，平台至今已累計1.04億的參與人數，官方宣稱最高武漢肺炎的補助金額為10萬人民幣（約45萬台幣）。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>面對醫療壓力的民眾，區塊鏈伸出援手</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>相互寶的運作模式仰賴區塊鏈的去中心化技術和零信任基礎的特性，確認生病的申請人透過App提交公立醫院的資料和就醫情況，由相互寶的審核團隊進行互助評估。若申請人在App中看見「該案件已上鏈」的文字，說明案件申請的審核過程已全部儲存在區塊鏈上，供成員查看。此外，當申請者發現案件被審核完拒絕支付補助金時，可以發起陪審團申訴，由所有用戶作為陪審團進行投票表決是否理賠。螞蟻區塊鏈表示，區塊鏈驗證節點包含法院、公證處、司法鑑定等公信機構，資料存證立基於區塊鏈的不可篡改功能，獲得民眾的信任感。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>武漢肺炎理賠困境</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>相互寶在2020年1月30日起將武漢肺炎列入「相互寶新冠肺炎特殊保障」，最高理賠10萬人民幣，全額由相互寶公司支付，用戶不需要分攤。然而在二月的所有武漢肺炎病患申請中，沒有顯示成功獲得補助金的案例。根據相互寶的說法，考量到調查人員的生命安全因素，目前部分地區的調查作業暫停，因此審核進度才會受影響。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>這不禁讓用戶產生好奇，主打使用區塊鏈技術的相互寶平台聲稱：每秒能處理1,000筆資料，不需要人工參與，縮短後端驗證及文書作業，舒緩醫療服務上的壓力。面對外界的質疑：相互寶針對武漢肺炎推出的新特殊保障條款是否有實現的可能性？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>醫療互助平台未來的挑戰</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>站在全民共同分攤確診病人的醫療費用勢必面對不少爭議和挑戰，隨著參與人數快速上升，符合救助規則的成員數增加，理賠數也隨著增多，全部成員的分攤費從原先一個月的兩三分錢人民幣漲到3元人民幣以上，未來若成員數持續上升，人均分攤勢必持續提高。同時，當越來越多人申請理賠，審核時間拉長，許多人在確診後的三、四個月才能獲得醫療補助，若面對急迫性的病症則降低了補助效益。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>相互寶存在一定的不確定性，因其身分不是保險業務（保險本質具法律效力，一旦簽訂合同則不得隨意更改內容），保障內容會面臨更改的風險，例如：申請互助的條件、互助金額等等。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>普匯觀點：壽險產業的發展</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>台灣雖然沒有發展線上互保的醫療互助平台，但自身的保險服務和保單類型十分多元完整。壽險公會在2019年5月開始測試「保險區塊鏈聯盟科技運用共享平台」，聯合八大壽險公司發展保單電子化存摺，用戶可以在單一平台檢視多家保單，並且舉證的資料直接與保險公司的理賠系統串連，免去多次申請的作業流程。同時，金管會預計在2020年11月底將壽險公會推動的保險區塊鏈「上鏈」，屆時電子保單、保單存摺、理賠和保全四大功能正式上線運作。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>醫療與科技的結合提升醫療作業流程以及保單的審核系統，更免除病患與保險人員面對面的傳染風險，此外，運用區塊鏈上鏈的病患資訊更具安全性和可追溯性，提升檢視和病史追蹤的方便性。</p>\n<!-- /wp:paragraph -->', '2020-02-20 14:58:26', '武漢肺炎，中國人的荷包挺得住醫療費用嗎？', 'article', 'publish', '2020-02-26 11:04:49', '', 0),
(7674, 1, '/upload/2020/02/stanislaw-zarychta-vfuTnGb6-e8-unsplash.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"very-light-gray\",\"backgroundColor\":\"very-dark-gray\"} -->\n<p class=\"has-text-color has-background has-very-light-gray-color has-very-dark-gray-background-color\"><em>區塊鏈最一開始被熟知是因為比特幣的應用，具備公開透明、公平競爭的特性。隨後，世界開始探索區塊鏈的各種場景應用，嘗試與金融業、供應鏈、政府機關、保險業、資安業做結合。</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>在談區塊鏈之前，向讀者介紹幾個相關的專有名詞以及其性質和應用，到底什麼是公有鏈、私有鏈、聯盟鏈呢？又要如何區分它們的差異？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>公有鏈（</strong><strong>Public Blockchain</strong><strong>）：</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>公有鏈譯自Public，指對全世界任何人公開，所有人都可以參與共識過程的區塊鏈（共識過程指決定哪個區塊可以被添加到區塊鏈中），公有鏈被視為「完全去中心化」，其主要特性為以下三點：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>用戶具有自主性。公有鏈的程序開發者無權干涉用戶。</li><li>訪問門檻低。任何擁有足夠技術能力的人都可以訪問。</li><li>匿名公開。每個參與者者可以看到所有帳戶的剩餘餘額和其交易活動。</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>目前公有鏈面臨最大的困境是「初期建置成本高」。因為仰賴分散式帳本的共識決，所以更新迭怠慢，若自行開發需耗費高昂的成本費用。大眾最耳熟能詳的應用是在比特幣、乙太坊。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>私有鏈（</strong><strong>Private Blockchain</strong><strong>）：</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>私有鏈相對於公有鏈，只對單獨的個人或實體開放。寫入權限僅在一個組織手裡的區塊鏈。讀取權限和開放對象被一定程度的限制，被視為「部份去中心化」，部份去中心化最大的憂慮來自若私有鏈發行代幣，價格和代碼可以被操控的，存在一定的風險。然而，私有鏈依然具有以下三項優點：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list {\"ordered\":true} -->\n<ol><li>交易速度快。節點之間具高度信任，免去繁瑣的節點驗證交易過程。</li><li>保護隱私。數據不會被擁有網路連結的任何人取得，且成員需要進行身分認證，降低惡意攻擊的可能性。</li><li>交易成本極低。私有鏈的交易只需要受到普遍認可的高算力節點確認即可，其交易成本與公有鏈相比極低。</li></ol>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>主要應用在企業內部的辦公審批、財務審計、政府行業的預算和執行。部分業主認為私有鏈的應用具爭議性，因為私有鏈完全由一個組織掌握，所有礦工都是組織人員，其存在的目的和必要性與分散式資料庫相似，就像有個權限中心分配每個會員的使用權，唯一的好處是難以篡改，成本卻比分散式資料庫高。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>聯盟鏈（</strong><strong>Consortium Blockchain</strong><strong>）：</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>聯盟鏈的架構是使用者、礦工、管理者所有鏈聯盟成員協商共同決定安排，成員間地位大多相互平行。聯盟鏈和私有鏈相似，開放程度和去中心化程式是受限制的，它們最大的差別在於聯盟鏈是一個聯盟（ex: 一個行業）；私有鏈是一個組織（ex: 一家公司）。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>聯盟鏈介於公有鏈和私有鏈之間，優劣也是介於兩者之間。節點相對於公有鏈較少，因此交易速度快，但同時容易出現權力集中和安全疑慮。主要應用在銀行間支付、結算、清算，將各家銀行的相關節點當成記帳節點。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>區塊鏈的必要性</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>私有鏈和聯盟鏈的開發成本遠低於公有鏈許多，且具有不可篡改、可追溯性以及隱私性等優點，但對於企業而言投資和應用區塊鏈技術依舊是一筆龐大的開銷，因此近期對於區塊鏈的熱門討論即是「是否需要使用區塊鏈？」</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>隨著JP Morgan的美金穩定幣JPM\nCoin開始讓企業申請試用，Facebook全力促成Libra的發行，Amazon成熟的區塊鏈服務平台，區塊鏈相關應用已如火如荼的展開，然而回歸企業最初的評估立場，「是否要導入區塊鏈」，到底是議題炒作，亦或者產業發展呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>根據Deloitte 2019 Global Bloakchain\nResearch，報告內容調查1,000位企業高階主管：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>84%位主管認為區塊鏈未來將成為主流</li><li>40%位主管認為公司會投資超過500萬美元</li><li>41%位主管認為該技術被過度炒作，膨脹優點、忽略成本</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>支持者認為：區塊鏈撇除價格昂貴，對於金融產業可謂百利無一害，許多銀行目前仍依靠大量人力和時間來驗證、審批分散且不透明的交易資訊，若將這些資訊改成「一套帳本」，可以省去大量的記帳和結算成本，並可以更有效率的達到紀錄溯源和資料真偽的分辨。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>懷疑者主要站在「沒有區塊鏈也能做得到！」的立場，認為若參與者不多，將因導入成本過高，而無法短期內達成經濟效益，以及若存在便宜可信的第三方，則沒有需要建立額外的信任基礎。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>普匯觀點</strong><strong></strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>對於未來科技的發展，沒有人能夠準確預測區塊鏈是否能夠成為主流，以及估計為人們帶來的經濟效益。區塊鏈依舊面對不少挑戰，例如：如何加強民眾的認知和信心？如何兼顧法規和安全性？如何讓參與者達成共識和協議等等。以普匯小幫手的觀點，台灣的環境目前最適合發展聯盟鏈，比如數家銀行成立自有聯盟鏈、多家醫院聯手建構聯盟鏈等等，甚至進一步達成跨產業合作的聯盟鏈，此舉能夠達成資源共享免去繁瑣的資料核對和對接過程。相較之下，台灣的公有鏈和私有鏈發展缺乏誘因，主要因為欠缺龐大且具世界影響力的財團和經濟實力，所以發展受到限制。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>此外，面對日新月異的技術，人們面對加密世界尚須思考：這種變革是不是好的方向？如果是，則受益者又是誰？如果否，那替代的解決方案該如何進行？</p>\n<!-- /wp:paragraph -->', '2020-02-21 17:48:02', '區塊鏈入門：你不得不知道公有鏈、私有鏈、聯盟鏈的差別', 'article', 'publish', '2020-02-26 11:05:22', '', 0),
(7684, 1, '/upload/2020/02/LL.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><em>LendingClub Corp週二同意以價值1.85億美元的現金加股票交易方式收購美國數位銀行Radius Bank，因為它希望獲得更便宜的資金來源並提供更廣泛的銀行服務。</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>Lending Club簡介</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>金融海嘯後初期，傳統銀行對個人借貸的審核更加嚴謹，個人借貸尤在無擔保品的情況下更是難以成功獲取貸款。LendingClub的成立是為了改變銀行系統，使信貸更容易獲得融資且投資方能得到更多收益。因此，互聯網借貸平台藉此機會搶佔市場。P2P借貸又稱互聯網借貸，經營互聯網借貸業者的角色通常是金融資訊仲介平台，為供給與需求雙方實現借貸而提供資訊蒐集、資訊公布、信用評估、借貸媒合等服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lending Club 是全球第三家成立互聯網借貸平台的公司，前面二家公司分別為英國的 Zopa，以及美國的 Prosper Marketplace，但目前知名度以 Lending Club 為最高。Lending Club 位於美國舊金山，現在市值1,159,824,871美元 (截至2020/02/18)，股價平均落在13塊。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>互聯網借貸平台主要的業務是媒合資金供需雙方，但 Lending Club 其實是沒有銀行牌照的，不能發放貸款，所以必須找到發行銀行（Issuing Bank），借貸平台媒合資金供需產生的貸款都由發行銀行發放貸款，目前 Lending Club 的無擔保個人貸款和汽車貸款的發行銀行是 WebBank，而教育和醫療財務貸款的發行銀行為 NBT Bank 和 Comenity Capital Bank。當貸款發放完成後，發行銀行將貸款及相關權利移轉給 Lending Club，同時也喪失追索權，發行銀行得到 Lending Club 在借貸平台取得資金供給方的投資資金，並向 Lending Club 支付交易服務費（Transaction fees），相當於幫銀行找到了資金需求面。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>Lending Club能夠成功的關鍵</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>利用網路蒐集資料及大數據分析，建立線上即時借貸風險評估機制，包括個人信用記錄、就業數據和網上瀏覽紀錄，更加準確地預測用戶的付款行為和壞帳率。過去，傳統金融機構在徵信用體系，難以通過大數據驅動技術的革命性創新，數據的量、深度、廣度都不足夠。</li><li>創造三贏局面，改善金融活動中效率問題，使閑置資金的有效分配，同時滿足微型企業和信用空白個人的融資需求，不與傳統金融業形成直接競爭。</li><li>美國 P2P Lending 發展，其中 FICO 機制才是Lending Club 成功的關鍵，由FICO建構個人金融資料庫並將信用程度分數化，並且開放給P2P業者直接查詢。台灣目前雖將個人金融資料存放金資中心，但僅讓金融業查詢使用。未來，可能開放給銀行或銀行與P2P 業者合作方式，並允許銀行自行設立 P2P，透過銀行協助 P2P 業者解決資金來源、貸放經驗與徵信資料不足等問題，也有助銀行因應金融科技發展。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>Lending Club 2020年的一大突破　－　併購</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Radius是一家先進的網路銀行，成立於1987年，總部位於馬薩諸塞州波士頓，擁有超過14億美元的多元化資產。它以屢屢獲獎的「無分支數位銀行平台」而聞名，該平台將最新技術結合了支票帳戶和儲蓄賬戶，為消費者和小型企業提供卓越的銀行體驗。它的平台提供便捷的功能，例如支票存款，賬單支付，銀行卡管理和個人財務管理儀表板，以及開放的API，可以為金融科技公司提供“銀行即服務”（BaaS）功能。此外，該公司還為企業提供商業貸款選擇，並為養老基金、工會、市政當局和非營利組織提供資金管理服務。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p class=\"has-text-color has-vivid-red-color\"><em>LendingClub首席執行官Scott Sanborn表示：“這是一次使我們能夠成功轉型的併購方式，我們能夠以有別於傳統做法和系統的方式重新構想銀行業務，通過與Radius的結合確保本公司與客戶們都能夠獲利。</em></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Lending\nClub的併購策略預計能帶來的好處:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li>提供低成本，穩定的資金來源，增強經濟周期中的應變能力。</li><li>能夠與主要監管機構直接往來而獲得監管之合法性。</li><li>通過增加利用LendingClub的營銷實力來吸引新會員。</li><li>通過BaaS提供新產品、服務和資源，同時提高Radius的交叉銷售能力讓客戶更有意願申購貸款產品。</li><li>增加現有LendingClub成員的參與度，以幫助他們管理現金流量並節省開支，同時產生更多數據以告知承銷商，並幫助消費者改善財務狀況。</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p class=\"has-text-color has-medium-font-size has-vivid-cyan-blue-color\"><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>現今採用P2P借貸商業模式的新創公司已有越來越多的趨勢，雖然在中國大陸的市場爆發了很多的災情，但主因還是法律規範太慢的關係，但如果金融科技在適當的法律規範下就可以有無限的可能，普匯金融科技是進入金融監理沙盒的公司，並且進駐了新創園區，同時估值破億元，未來也將有機會與本地銀行有更多的合作機會，有望成為未來的獨角獸。</p>\n<!-- /wp:paragraph -->', '2020-02-26 11:10:24', '美國第一家收購銀行的P2P借貸金融科技公司 － Lending Club', 'article', 'publish', '2020-02-26 11:10:26', '', 0),
(7697, 1, '/upload/2020/02/2-1.png', NULL, '<p><!-- wp:paragraph --></p>\n<p><strong>至今普匯投資統計違約率約0.63%~1.87%(M6以上)</strong></p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>相比元大50穩健型的ETF為例，追逐買進台灣最大的50檔一籃子股票，如果您也是屬於低風險型投資者，今天將資金分散投資在台、清、交、成、政的同學身上，您會不會覺得這是一種穩賺不賠的投資生意呢？</p>\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">比ETF更好的報酬！立即下</a><a style=\"color: #ff0000;\" href=\"https://reurl.cc/L1VOla\">載賺錢去</a></strong></h2>\n<p> </p>\n<p><!-- /wp:paragraph --><!-- wp:image {\"id\":7698,\"width\":883,\"height\":540} --></p>\n<figure><img src=\"/upload/2020/02/3-1.png\" alt=\"普匯投資\" width=\"883\" height=\"540\" /></figure>\n<p><!-- /wp:image --><!-- wp:paragraph --></p>\n<p>普匯投資方的年利率平均約略在12%左右，五大（台大、清大、交大、政大、成大）、四中（中正、中央、中興、中山）的同學利率大約落在6% ~ 10%。以8%來看，假如今天某A從25歲開始定期定額投資這些同學，並且需要在50歲時賺得1000萬，那只要每個月投入1萬元左右，於普匯投資購買債權（可自行選擇投資標的，普匯投資以$1000為單位，達到您分散風險的效果），並且要在每個月回款時持續投入進去(類似現金股利再投入概念)，用這種風險最低的投資法直到50歲時可以存到1000萬。但人的薪水是會漸漸提高的，這種投資方法會隨著投資標的物利率、月投入本金的提高而更早達到目的，操作甚至比投資ETF還要更具有彈性。</p>\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">還在等什麼？點我跟著大戶賺錢去</a></strong></h2>\n<p> </p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p><strong>投資股票真的有比較好賺嗎?</strong></p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>不知道正在看文章的您，是否有接觸過股票市場？   </p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>投資股市因其波動率較高，投資人有機會依靠大幅度的短期波動而賺取到高報酬，而市場的本益比(P/E ratio)不可能維持不變。這時就出現了兩難：為了精準預測股票的市場報酬率，我們不僅要知道當下的股息殖利率，並預估每股盈餘的成長率。同時，我們還必須推測其他投資人會如何投機，要尋找的往往不是自認覺得好的標的物，而是要預測出大盤的喜好，所以有人就嘲諷到說，股市就是看誰掌握最多的內部(內線)消息，誰就是勝者。這種猜謎遊戲消耗太多投資人的精力，以至於我們忘記了股息殖利率和每股盈餘成長率的基本價值，卻把心力耗損在不斷地猜測投資人的心思是看漲還是看跌。</p>\n<p><!-- /wp:paragraph --><!-- wp:image {\"id\":7699,\"width\":922,\"height\":371} --></p>\n<figure><img src=\"/upload/2020/02/1-1-1024x413.png\" alt=\"\" width=\"922\" height=\"371\" /></figure>\n<p><!-- /wp:image --><!-- wp:paragraph --></p>\n<p>而也有多數風險趨避的投資人喜歡以投資ETF為標的來進行理財，上圖為近6年台灣前10大配息ETF現金殖利率一覽，可以發現殖利率平均落在3.2%左右，最高平均不超過5%，而台灣央行對物價通膨的警戒線是2%，也就是以2%的風險來看這個報酬率其實是更縮水的(雖然比郵局定存好很多了)。</p>\n<p><!-- /wp:paragraph --><!-- wp:image {\"id\":7700,\"width\":915,\"height\":440} --></p>\n<figure><img src=\"/upload/2020/02/2-1.png\" alt=\"\" width=\"915\" height=\"440\" /></figure>\n<p><!-- /wp:image --><!-- wp:paragraph --></p>\n<p><strong>股市與債市的關係</strong></p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>相對於股市關注供需市場而言，債市比較像是買一個人的信譽，一個統一、成熟的債券市場可以為全社會的投資者和籌資者提供低風險的投融資工具；債券的收益率曲線是社會經濟中一切金融商品收益水平的基準，因此債券市場也是傳導中央銀行貨幣政策的重要載體。債券作為一種重要的金融工具。</p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>股票和債券是本質相反的有價證券，債券投資人永遠是貸方，而股票投資人通常為借方，二者走勢也幾乎是背離的。由於資金的逐利性，投資者總是會追尋具有較高收益率/回報率的資產，因此在經濟繁榮時期，投資者便會湧入回報率較高/風險較大的股市，從而推升股價，資金從債市流出（收益率上漲）；相反經濟低迷時期，國家不斷收緊流動性，股市也表現疲軟，此時投資者便會湧入回報率相對較低但安全性更高的債市（收益率下跌）。</p>\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">還在等什麼？立即下載賺錢去</a></strong></h2>\n<h2>延伸閱讀&gt;&gt;&gt;<a style=\"color: #000080;\" href=\"https://reurl.cc/vDDVZl\">退休後如何實現</a><a style=\"color: #000080;\" href=\"https://reurl.cc/vDDVZl\">月領五萬</a><a style=\"color: #000080;\" href=\"https://reurl.cc/vDDVZl\">？</a></h2>\n<p><!-- /wp:paragraph --></p>', '2020-02-27 15:33:28', '如何透過普匯投資存到人生第一桶金?', 'article', 'publish', '2020-04-17 09:58:08', '', 0),
(7717, 1, '/upload/2020/03/image-4.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p><em>勞保局根據內政部公告全國簡易生命表為準，計算平均餘命，目前請領勞退新制年齡為60歲起算，可以領至85歲。至於利率則是採勞動基金運用局公告勞退新制全年平均保證收益率前三年（即2015年至2018年）平均數為準，即1.1843%。</em></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>許多人從年輕的時候沒有好好的理財，導致老年退休的時候有了嚴重的資金缺口，而其中醫療也占了大部分的花用，如果自已的小孩沒辦法照顧自己甚至還要花錢請佣人。退休金若只單單依靠勞退的話亦是遠遠不夠退休時的花用的，且其保證收益率甚至還低於央行認定的2%通貨膨脹，等於是把錢「越存越薄」，且最多也只能提繳月薪的6%，所以想要享受優質的退休生活，勢必要做更有效率的投資資產配置。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p><strong>債權投資，比股市風險小得多</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>在上一篇<a href=\"https://www.influxfin.com/%e5%a6%82%e4%bd%95%e9%80%8f%e9%81%8e%e6%99%ae%e5%8c%af%e6%8a%95%e8%b3%87%e5%ad%98%e5%88%b0%e4%ba%ba%e7%94%9f%e7%ac%ac%e4%b8%80%e6%a1%b6%e9%87%91/\"><strong>如何透過普匯投資存到人生第一桶金裡</strong></a>有提到，投資股市裡的風險是遠遠高於債權投資的，尤其散戶更是容易被大盤套牢，想利用股票賺到退休金可能不是一個非常理想的方式。而普匯統計現在年輕人心中的最佳退休月領金額是5 ~ 6萬元，現在就教您該如何利用普匯的產品來達成目標！</p>\n<h3><a href=\"https://www.influxfin.com/%e5%a6%82%e4%bd%95%e9%80%8f%e9%81%8e%e6%99%ae%e5%8c%af%e6%8a%95%e8%b3%87%e5%ad%98%e5%88%b0%e4%ba%ba%e7%94%9f%e7%ac%ac%e4%b8%80%e6%a1%b6%e9%87%91/\">延伸閱讀&gt;&gt;&gt;如何透過普匯投資存到人生第一桶金?</a></h3>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":7719} -->\n<figure><img src=\"/upload/2020/03/EXCEL-1.jpg\" alt=\"退休如何月領五萬\" width=\"1124\" height=\"511\" /><br />\n<figcaption></figcaption>\n</figure>\n<!-- /wp:image --><!-- wp:list -->\n<ul>\n<li>以每月投入1萬元購買債權為例，選定在IRR約10%左右的標的，在第216期(18年)的時候可以達到終生財富自由，總投入本金為2,160,000，且還不計入每年因薪資成長而增加的額外投入(每期投入更高即可更快達成目標)，這種投資方法可以在18年後退休時每月從回款裡面提領5萬元，剩下的回款繼續再投入普匯購買債權，而每月現金流可以達到約91萬左右，然後會繼續逐年增加，以趨勢圖來看會呈現如下圖(橫軸單位為年)，可以發現這種方法也是一種永續年金的概念。</li>\n</ul>\n<!-- /wp:list --><!-- wp:image {\"id\":7720} -->\n<figure>\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">聰明投資新選擇！立即下載開始你的第一桶金</a></strong></h2>\n</figure>\n<figure></figure>\n<figure><img src=\"/upload/2020/03/CASE1.jpg\" alt=\"月領五萬\" width=\"1068\" height=\"757\" /></figure>\n<!-- /wp:image --><!-- wp:list -->\n<ul>\n<li>若在第18年後不想繼續複投了，則在第19年可以拿回所有本金與利息，由excel表可以算出大約有<strong>$6,181,223</strong>元的總資產(將上圖紅框處線條加總)。</li>\n</ul>\n<!-- /wp:list --><!-- wp:image {\"id\":7721} -->\n<figure><img src=\"/upload/2020/03/CAES2.jpg\" alt=\"退休月領五萬\" width=\"1068\" height=\"757\" /></figure>\n<!-- /wp:image --><!-- wp:list -->\n<ul>\n<li>假若您是想要在退休後慢慢把資本都領完，另一種領取的方式則是每月月領55,000，剩餘回款持續複投，則這種領法可以在第23年半剛好領完，以趨勢圖來看會呈現如上圖(橫軸單位為年)。</li>\n</ul>\n<!-- /wp:list --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">還在等什麼？現在就開始輕鬆賺得退休金</a></strong></h2>\n<p><strong>小額分散投資能夠更有效的分散風險</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>債權投資相對於股票的好處是更容易把投資標的做更細的拆分，股票一次動輒上萬元，小資族沒辦法再做更細的風險控管，但普匯債權投資可以把一萬元拆分成十個千元債權，此種投資方法更能有效的分散風險！</p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":7722} -->\n<figure><img src=\"/upload/2020/03/複利滾存.jpg\" alt=\"複利滾存月賺五萬\" width=\"1184\" height=\"668\" /></figure>\n<!-- /wp:image --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p><strong>普匯每月提供對帳單，讓結算更輕鬆！</strong></p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":7723} -->\n<figure><img src=\"/upload/2020/03/對帳單.jpg\" alt=\"\" width=\"1277\" height=\"683\" /></figure>\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">最簡單、透明的債權投資APP，立即創造你的被動收入</a></strong></h2>\n<p> </p>\n<h3><a href=\"https://www.influxfin.com/%e5%a6%82%e4%bd%95%e9%80%8f%e9%81%8e%e6%99%ae%e5%8c%af%e6%8a%95%e8%b3%87%e5%ad%98%e5%88%b0%e4%ba%ba%e7%94%9f%e7%ac%ac%e4%b8%80%e6%a1%b6%e9%87%91/\">延伸閱讀&gt;&gt;&gt;如何透過普匯投資存到人生第一桶金?</a></h3>\n<!-- /wp:image --><!-- wp:list --><!-- /wp:list --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"backgroundColor\":\"very-light-gray\",\"fontSize\":\"medium\"} --><!-- /wp:paragraph -->', '2020-03-13 15:31:02', '普匯教您如何退休後還能月月領5萬', 'article', 'publish', '2020-04-17 09:57:02', '', 0),
(7772, 1, '/upload/2020/03/截圖-2020-03-18-上午10.32.28.png', NULL, '<!-- wp:paragraph -->\n<p>現代醫美科技發達，台灣人常常指韓國是整形大國，殊不知我們自己的台北車站、東區的街道也充斥著醫美診所，想要預約還得等上大半個月，才知道原來台灣的醫美需求這麼高！不然怎麼有這麼多醫美診所賺進大把鈔票呢？雖然醫美還稱不算普世價值，但身邊一定有很多人正在偷偷做！（不然東區這麼高的房租，診所怎麼付得起呢XD?）</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯並不鼓勵大家「過度」整形，迷失自我。但若是適當的改善自身困擾、重獲自信，對自己絕對是加分行為！雖然每個人都有自己獨特的美麗，但每個人心中也有自己的審美標準，如果只改變一小處就能獲得更大的自信、或是能達到和化妝後一樣的效果，不用再提早一小時起床，可以省下時間和精力更認真上班工作，那麼何樂而不為呢？更何況自信是最難用金錢買到的！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>醫美也分為「想要」和「需要」，像是：咬合不正影響進食、臉上大面積胎記不敢素顏，或是因為意外事故導致顏面需要修復等等⋯⋯這些人都是「需要」而不僅僅是「想要」，如果投資人借他們這筆醫美費用，一定會是筆值得的投資！因為根據研究顯示，相貌更好看的人，確實在求職、升遷上更為順利， 那他們的還款能力必定會比其他人更好囉！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>近年來越來越盛行牙齒矯正，小時候同學戴牙套還會被笑「鋼牙妹」，但現在大家恨不得存一筆錢讓自己牙齒變整齊，變成所謂的「鋼牙妹」！世代改變之快，當醫美變成日常需求，如果矯正牙齒能讓你咬合更好、減少蛀牙，而且吃芭樂或珍珠波霸不再困擾，又可以自信的露齒笑，絕對是一筆值得的投資！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>然而牙齒矯正至少需要準備10萬元，而且許多診所要求現金付款，頭期款至少得付上一半，對於小資族來說確實是一筆不小的金額。即使有穩定工作及收入，但一次付這麼多錢可能會讓身上沒有存款和緊急備用金！如果能夠分期付款有多好呢？你的煩惱，普匯都知道！若是確定能夠每月歸還，可以上普匯金融科技，使用時下最新的P2P技術，不需要銀行照會、不用和別人解釋是要裝牙套，只要上app輕鬆點幾下，現金就到帳！注重隱私的你肯定會喜歡！每個月只要還幾千元，就能擁有10萬元的矯正療程。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>舉例來說，假設牙齒矯正總共需要10萬元，上普匯P2P線上借貸，輕鬆輸入幾筆資料，不需要調銀行聯徵、不需要照會家長，就能即時拿到這筆錢囉！分為24期，如果以5%年利率計算，每期只要還款4388元，就可以有一口完美笑容。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>還有臉上的胎記、痘痘、痘疤、雀斑、曬斑，每天都要提早一小時起床，在臉上抹上防曬乳、遮瑕液、粉底液，再用蜜粉定妝⋯⋯像是小編已經有雙眼皮了，卻因為內雙而受各種限制，連眼線眼影都不能盡情發揮，常常想著如果能變成大外雙該有多好？而且其實改變也不大，畢竟本來就有雙眼皮嘛！每天貼雙眼皮貼調整內雙，貼到都快鬆弛了，好想要一勞永逸！就像漫畫「女神降臨」的朱靜一樣，平常像女神但是卸妝差很大！難道要一輩子避著男友，不讓他看見化妝品下的自己嗎⋯⋯？皮秒雷射等治療，都可以讓你重拾自信！不用提早起床也能像韓劇「愛的迫降」中的尹世理一樣美麗，剛起床也能很正的素顏女神！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>年滿20歲，身為合法公民的你，做這些醫美療程並不需要父母的同意書簽名，但卻需要普匯的資金給你勇氣。你的需求，普匯比你更清楚，普匯的投資人也願意幫助你獲得自信和美麗。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>普匯希望能給妳自信，素顏出門不用遮遮掩掩，背後的流言蜚語也不怕，不再看著卸妝後的自己洩氣。妳已經很漂亮，妳只是想更漂亮、只是想多睡一點、只是不想再倚賴化妝品、只是想讓自己24小時都有自信，不受限於打扮後的自己。其實妳缺少的不是自信，而是接受我們服務的勇氣！</p>\n<!-- /wp:paragraph -->', '2020-03-19 09:22:17', '每天化妝好累？想要一起床就美？普匯還你漂漂拳', 'article', 'publish', '2020-03-21 10:35:08', '', 0),
(7784, 1, '/upload/2020/03/QQ.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p><strong><em>3</em></strong><strong><em>月9</em></strong><strong><em>日美股開盤暴跌，標普500</em></strong><strong><em>開盤跌7%</em></strong><strong><em>觸發熔斷機制，暫停交易15</em></strong><strong><em>分鐘。</em></strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n<p><strong><em>3月12日暴跌，道瓊斯工業平跌幅為9.99%，標準普爾500種股票指數跌幅為9.51%，納斯達克綜合指數跌幅為9.43%。期間觸發熔斷機制，暫停交易15分鐘。</em></strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p><strong>什麼是美股熔斷?</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>熔斷機制(Circuit breaker / Trading curb)又名自動停盤機制，指的是當股票指數波動達到某一個點時，自動停止交易一段時間，以控制風險。因為同“保險絲”在電流過大時自動熔斷起到的保護作用相似而得名。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>1988年10月19日，美國商品期貨交易委員會與證券交易委員會批准了紐約股票交易所和芝加哥商業交易所的熔斷機制。此後，熔斷機制一直持續至今。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>最開始美國熔斷機制的基準指數是道瓊斯工業指數，後來修改為標準普爾500指數。在美股交易時段，熔斷機制可以分為三級。一級市場熔斷，是指市場下跌達到7%；二級市場熔斷，是指市場下跌達到13%；三級市場熔斷，是指市場下跌達到20%。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>如果觸發一級或者二級市場熔斷，且時間是在：美東時間9：30 - 15：25(含)之間，全市場所有股票暫停交易15分鐘。美東時間15：25之後，不暫停交易。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>另外，如果該交易日為半天交易，則時間分界點為12：25。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>在全天任意交易時段，如果觸發三級市場熔斷，全股市停止交易，直至下個交易日開盤。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p><strong>疫情導致美股單月3度熔斷，連川普也無力回天</strong></p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":7786} -->\n<figure><img src=\"/upload/2020/03/image-5.png\" alt=\"\" /></figure>\n<!-- /wp:image --><!-- wp:paragraph -->\n<p>武漢肺炎（COVID-19，2019新型冠狀病毒）疫情延燒，加上國際油價崩盤，股市受到嚴重衝擊，美股單月觸發3次「熔斷機制」，3月9日、12日、16日，也創下美國股市新紀錄。美國總統川普近幾周祭出多項政策救市，卻依然不見成效，今16日開盤前川普也在推特發文「天佑美國」。</p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":7787} -->\n<figure><img src=\"/upload/2020/03/image-6.png\" alt=\"\" /></figure>\n<!-- /wp:image --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p> </p>\n<h1><a href=\"https://event.influxfin.com/R/url?p=webpost0321\"><strong>救救股市，危機入市，創造財富～普匯，貸給股民們，人生大財富！</strong></a></h1>\n<p> </p>\n<p> </p>\n<p><strong>菲律賓政府關閉股市、債市、匯市的影響</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>自2020年初以來，外國資金大量外流。自從冠狀病毒的擔憂開始蔓延以來的過去八周中，有550億美元從新興市場流出，流失量是2008年或2013年“緊縮恐慌”期間的兩倍。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>菲律賓政府3/17宣佈，由於新冠肺炎疫情嚴重影響民生與經濟金融市場安全穩定，為了維護金融秩序，菲律賓從17日開始無限期停止包括股票、債券、匯兌等各種金融交易市場，直到政府重新宣佈恢復運作為止。菲律賓是全球第一個因為新冠肺炎大流行全面關閉國內金融市場交易的國家。這是總統杜特蒂（Rodrigo Duterte）下令擴大隔離檢疫以遏止2019冠狀病毒疾病（COVID－19，武漢肺炎）疫情擴散的一環。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>彭博社報導，菲律賓股市從今年元月以來，下跌已經超過30%，跌幅歷來最大。由於菲律賓與美國股市連動密切，所以菲律賓可能是鑑於美股連日懸岸式重挫，因此決定關閉所有金融交易市場，避免國內因為金融市場激烈動盪，發生更不可預測的狀況。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>而危機發生時刻關閉市場極為罕見，但並非沒有先例。2001年發生911恐怖攻擊事件後，美國即停止股市交易將近一週時間；香港在1987年「黑色星期一」（Black Monday）股市崩跌後也停止交易；希臘也在2015年關閉股市將近5週時間。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p><strong>政府關閉交易所對經濟的影響？</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>菲律賓政府的做法只有一種最直接的解釋，即「只要不開盤，就不會跌了」，但用歷史資訊來看關閉交易所的影響</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"very-dark-gray\",\"backgroundColor\":\"luminous-vivid-amber\"} -->\n<p>希臘股市在關閉逾一個月後周一恢復交易，希臘ASE股票指數開盤暴跌23%，至615.61點。銀行股受創最深，Eurobank、希臘國家銀行、Piraeus Bank股價跌幅在30%左右，觸及跌停板。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>從這個事件來看，關閉交易所的目的是為了讓股民們冷靜，不要過度反應，但從此事件可以明顯看出，停止交易只是延後下跌而已，並且有可能會把股民緊張的情緒推得更高，讓波動更加劇烈造成恐慌性拋售。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"very-dark-gray\",\"backgroundColor\":\"luminous-vivid-amber\"} -->\n<p>1987年12月，香港證券交易所對國際投資者進行的一項調查一致認為，停業對交易所的國際聲譽產生了負面影響，並且至少在短期內削弱了對香港市場的信心。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>股民對於關閉交易所的恐懼點在於「流動性」，限制了人們的退場機制，使得公司債務無法履行債務契約，因為此時市場是失去訂價能力的，就算掌握了成千上萬張股票，只要沒辦法交易，在那段時間就會成了一堆沒有價值的「廢紙」，所以比起漲跌幅，更多時候看重的是其股票變現能力，而維持市場的秩序與彈性更是對開放自由市場必要的尊重，換個方向思考，如果交易所允許市值暴漲而不關閉市場叫人們冷靜，那怎能因為暴跌而關閉市場叫人別退出呢？且只要交易所一但失去了外資的信心，是很難在段時間內就挽救的回來的。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p><strong>個人投資股市，該如何自保？</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>在<a href=\"https://www.influxfin.com/%e5%a6%82%e4%bd%95%e9%80%8f%e9%81%8e%e6%99%ae%e5%8c%af%e6%8a%95%e8%b3%87%e5%ad%98%e5%88%b0%e4%ba%ba%e7%94%9f%e7%ac%ac%e4%b8%80%e6%a1%b6%e9%87%91/\"><strong>如何透過普匯投資存到人生第一桶金?</strong></a>這篇文章有提到，投資股市因其波動率較高，投資人有機會依靠大幅度的短期波動而賺取到高報酬相，而對於股市關注供需市場而言，債市比較像是買一個人的信譽，一個統一、成熟的債券市場可以為全社會的投資者和籌資者提供低風險的投融資工具，而要適度的做風險分散，在景氣較差時應把資金從股市移轉到債市，才不會讓單一事件把自己逼上絕路。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p><strong>普匯債權讓您可以輕鬆的做小額分散投資</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n<p>普匯債權擁有以下幾個優點</p>\n<!-- /wp:paragraph --><!-- wp:list -->\n<ul>\n<li>具有固定期限和固定利率</li>\n<li>債權可轉讓，具有靈活的流通性</li>\n<li>投資門檻低，回報高，且收益穩定</li>\n</ul>\n<!-- /wp:list --><!-- wp:paragraph -->\n<p>相對於股票，債權是購買債務人的「信用」，而債權不會因為公司倒了而血本無歸，只要契約成立的當下，追索期是遠遠長於股票的，在普匯做定期定額投資，就不需要怕因為天災人禍引起的大災情，對於人生的規劃更有保障，只要1000元就可以輕鬆體驗。</p>\n<!-- /wp:paragraph --><!-- wp:heading -->\n<h1><a href=\"https://event.influxfin.com/r/iurl?p=webpost0321invest\">還在等什麼？立即下載賺錢去&gt;&gt;&gt;</a></h1>\n<!-- /wp:heading -->', '2020-03-21 10:40:46', '普匯帶您了解股市最新時事 – 美股熔斷', 'article', 'publish', '2020-03-23 16:01:05', '', 0),
(7798, 1, '/upload/2020/03/圖片-1.png', NULL, '<!-- wp:paragraph -->\n<p>每天宅在家不敢出門人擠人網購買蝦皮、淘寶時，發現用卡結帳優惠比較多、也比較方便，也想享有這些優惠！但學生族還沒有固定薪資、扣繳憑證來和銀行申請信用卡⋯⋯</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>其實不用這麼麻煩！只要你年滿20歲，就可以申辦簽帳金融卡～不只是你的提款卡也能拿來刷，帳戶裡有多少就能刷多少，不怕刷爆卡變成學生卡奴！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>不同於信用卡的先享受後付款，簽帳金融卡也就是大家俗稱的Debit Card，屬於「有多少刷多少」的性質，可以避免金錢管理能力較弱的朋友們過度消費，而且也能享有不錯的回饋。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>市面上其實已經有多家銀行的VISA金融卡具備刷卡功能，像是許多同學從小就有的郵局卡。然而這種仍以提款卡為主要功能的卡別，回饋幾乎是0~0.5%而已，幾乎不痛不癢。因此，普匯幫你過濾了台灣的每一間銀行，幫你整理好回饋最高的簽帳金融神卡，讓你花小錢省大錢！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:table -->\n<table class=\"wp-block-table\"><tbody><tr><td>簽帳金融卡</td></tr><tr><td>卡片名稱</td><td>回饋</td><td>國際發卡組織</td><td>其他優惠</td></tr><tr><td>中國信託LINE Pay&nbsp;簽帳金融卡</td><td>一般1%、海外最高2.8%LINE Points回饋</td><td>VISA</td><td>LINE TV、LINE STORE、LINE TAXI、屈臣氏、Hotels.com…等享5~15%回饋&nbsp;</td></tr><tr><td>中國信託My Way簽帳金融卡</td><td>國內現金回饋1%海外消費現金回饋2.5%</td><td>MASTERCARD</td><td>日本旅遊最高享10%退稅+10%折扣</td></tr><tr><td>國泰世華KOKO簽帳金融卡&nbsp;</td><td>國內、外1.2%現金回饋&nbsp;</td><td>VISA</td><td>全台遠東SOGO百貨各店刷卡消費，<br>一般商品購物享正品9折優惠，部分商品享95折優惠。<br>每20元累積1點國泰世華紅利點數。</td></tr><tr><td>台新銀行Richart金融卡</td><td>一般0.5%、指定通路1%現金回饋</td><td>VISA</td><td>一般通路消費享&nbsp;0.5%&nbsp;無上限現金回饋；<br>指定數位通路（蝦皮、淘寶、momo、pchome等）消費加碼至&nbsp;1%&nbsp;現金回饋，<br>每月上限&nbsp;NT$500</td></tr><tr><td>王道銀行簽帳金融卡&nbsp;</td><td>國內1%、海外&nbsp;1%現金回饋&nbsp;</td><td>VISA、MasterCard</td><td>百種卡面任挑：消費金額之0.2%捐助指定公益團體的公益認同卡、<br>&nbsp;cama café及FunNow聯名卡、</td></tr><tr><td>玉山&nbsp;Pi&nbsp;拍兔&nbsp;Debit&nbsp;卡</td><td>國內外2.5% P幣回饋</td><td>MasterCard</td><td>使用Pi拍錢包綁定支付加碼1%回饋，搭配指定通路再加碼1% P幣回饋</td></tr><tr><td>永豐銀行國際學生證Debit卡</td><td>國內0.6%、海外&nbsp;2%現金回饋</td><td>MasterCard</td><td>海外提款3次免手續費、8次跨行交易免手續費優惠ISIC在全球130個國家超過15萬項學生優惠&nbsp;</td></tr><tr><td>永豐銀行大戶現金回饋Debit卡&nbsp;</td><td>國內0.6%、海外&nbsp;1.5%指定保費代繳1.1%</td><td>MasterCard</td><td>每月16次跨行提款及轉帳免收手續費</td></tr></tbody></table>\n<!-- /wp:table -->\n\n<!-- wp:paragraph -->\n<p>普匯觀點：</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>不管是電子支付、信用卡還是金融卡，只要有優惠就是好卡！大家可以根據平常的消費習慣來選擇最省錢的支付方式，從小錢開始省起，就是最基本的投資理財，同樣都要付出一定金額的生活費，為什麼不順手領個回饋金呢？</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>如果想要更進階的投資，股票、基金對新手來說仍舊有點難度，不妨考慮普匯債權投資，最低只要1000元，就能領到最高20%利息！保證比定存賺的還要多！還能幫助其他學生或上班族的教育及生活花費，一邊幫助人、一邊賺錢，輕鬆開始投資之路！</p>\n<!-- /wp:paragraph -->', '2020-03-25 16:37:09', '一邊刷卡還能一邊賺錢？學生也能辦的簽帳金融卡！', 'article', 'publish', '2021-01-18 08:15:39', NULL, 1);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(7810, 1, '/upload/2020/04/INVEST.jpg', NULL, '<p></p>\n\n<!-- wp:paragraph -->\n</p>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":7811} -->\n</p>\n<figure><strong style=\"color: #212121; font-family: Yantramanav, Helvetica, Arial, sans-serif; font-size: 21px; letter-spacing: 0em;\">普匯同時皆受產業</strong><strong style=\"color: #212121; font-family: Yantramanav, Helvetica, Arial, sans-serif; font-size: 21px; letter-spacing: 0em;\">、官方、學界、研究機構支持</strong>　</figure>\n<figure>普匯是全台第一個同時受<strong>金管會、金融總會、資策會、工研院、亞洲矽谷</strong>等輔導的AI無人化金融科技平台，並通過審核進駐在<strong>金融科技創新園區</strong>，以「普惠金融」為願景，致力於讓學生、新鮮人或青年創業家及中小企業家享受金融服務，建立<strong>互利互信</strong>的環境，讓投資人能安心線上下單進行小額投資同時分散風險！普匯與<strong>臺灣大學、政治大學、臺北大學、臺北商業大學、高雄科技大學、東吳大學</strong>攜手新光銀行、康和證券、上海商銀、凱基銀行、遠東商銀舉辦Ai金融科技創新創意大賽，培育金融科技人才，促進深度產學合作。</figure>\n<p>　</p>\n<figure></figure>\n<figure><strong style=\"color: #212121; font-family: Yantramanav, Helvetica, Arial, sans-serif; font-size: 21px; letter-spacing: 0em;\">王媽媽如何利用普匯投資賺得16%的年報酬率？</strong></figure>\n<p>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n</p>\n<p>退休小學教師——王媽媽的投資經驗，王媽媽從2019年8月開始使用普匯投資債權，第一個月投入20萬本金，分散成200筆1000元，每月回款穩定，並將收回的本利再繼續投資新債權，額外每月挹注資金，以錢滾錢，截至今天已累積517筆！投資標的信用評級約6～7級，年利率14~16%，這500多筆中逾期戶只有6位！風險比股票低數千倍！</p>\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">點我立即擁有16%的穩定報酬！</a></strong></h2>\n \n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":7812} -->\n</p>\n<figure><img src=\"/upload/2020/04/小學堂2-1024x988.jpg\" alt=\"\" /></figure>\n<p>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n</p>\n<p>王媽媽目前已投入的成本總金額為45萬元，8個月已經賺入33,391元的利息！即使退休金因政策而減少，卻在普匯賺得被動收入。王媽媽作為教師、身為兩個孩子的母親，深深理解教育的重要性，且體諒眾多學生為了教育及生活費所苦，所以她選擇在普匯投資！不只較股市風險低、報酬高，還能投資莘莘學子，贊助大學生的學費、生活費、研究所補習費，一邊助人、創造社會利益，還能換得現金，最符合教師與母親的心，輕鬆支應小孩的大學學費、研究所補習費還有給自己的母親節禮物。</p>\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">利用閒置資金獲得報酬，也幫助他人完成夢想！</a></strong></h2>\n<p> </p>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n</p>\n<h3><strong>那麼，究竟如何利用普匯投資輕鬆創造翻倍的資產？</strong></h3>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n</p>\n<p>我們為您試算出必勝公式，在首期先投入10萬元，並挑選年利率14%左右的12期 ~ 18期債權標的物，並且學習王媽媽將每月本息回款，外加上每月1萬元的新資金，再投入其他債權，以錢滾錢。5年後成本共70萬元（期初10萬+ (1萬x 60個月)），最後可領回約173萬元，若考慮風險違約的情況，抓2%的違約率也可以收回162萬元！只要5年就可以創造出翻倍的資產！</p>\n<p> </p>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n</p>\n<h3><strong>手中有點存款的您，讓普匯投資為您輕鬆創造翻倍資產</strong></h3>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n</p>\n<p>依照普匯內部蒐集問卷並整理出，投資人對於普匯的風險管控非常有信心。從2019年底到今年3月統計普匯所有申貸案件與真正上架案件中，真正通過審核上架媒合的只有10%！有9成的借款案件在不被我們嚴謹的AI風控模組通過。在普匯的把關下，只要記得做分散風險，就可以和王媽媽一樣，違約率不到1.16%（517筆中只有6筆）！提醒投資人勿把所有的雞蛋放在同一個籃子裡。</p>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n</p>\n<p>而普匯投資專員建議小資族群們，可以按照上面介紹的公式投資，並且謹記小額風散的原則，必定可以增加資產！</p>\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">立即讓自己的資產翻倍&gt;&gt;</a></strong></h2>\n<p> </p>\n<p>\n<!-- /wp:paragraph -->\n\n<p></p>', '2020-04-16 16:08:08', '資產翻倍術 - 普匯投資人實例分享', 'article', 'publish', '2020-05-05 18:25:05', '', 0),
(7861, 1, '/upload/2020/05/金總金管會大合照.jpg', NULL, '由普匯金融科技姚執行長（右六）主持的「AI金融科技創新創意競賽」記者會，於國立臺北商業大學盛大舉行，金管會張副主委（右七）、金融總會吳秘書長（左六）出席。\n<h2><strong>普匯同時皆受產業、官方、學界、研究機構支持</strong></h2>\n普匯是全台第一個同時受<strong>金管會、金融總會、資策會、工研院、亞洲矽谷</strong>等輔導的AI無人化金融科技平台，並通過審核進駐在<strong>金融科技創新園區</strong>，以「普惠金融」為願景，致力於讓學生、新鮮人或青年創業家及中小企業家享受金融服務，建立<strong>互利互信</strong>的環境，讓投資人能安心線上下單進行小額投資同時分散風險！普匯與<strong>臺灣大學、政治大學、臺北大學、臺北商業大學、高雄科技大學、東吳大學</strong>攜手新光銀行、康和證券、上海商銀、凱基銀行、遠東商銀舉辦Ai金融科技創新創意大賽，培育金融科技人才，促進深度產學合作。\n<h2><strong>股市賺錢不容易，散戶只能被大盤坑殺</strong></h2>\n是不是時常覺得自己學了很多技術指標，累積了很多『盤感』，報酬率卻一直上上下下很難賺到錢？今年爆發疫情以來，美國十年期公債在民眾避險情緒下殖利率創新低，在恐慌時期股市都是最先崩盤的，走向債市就是最佳策略。\n\n投資人在操作股票的時候常會忽略一項重要的因素：「時間成本」，不管是短期當沖還是中長期投資，投資人需要花很多的時間關注在股價的變動，更不用說那些股價不確定的因子往往使人更焦慮，想要贏過大盤的專職操盤手更是不可能，股市是零和遊戲，不會有所有人都是贏家的局面；而購買債權的好處在於不需要再額外花時間去關注已擁有之標的，只要選擇好風險利率，就只要像買公債一樣每月到期時收錢進口袋。\n<h2><strong>投資人該關心的是</strong><strong>「經濟利潤」而非「會計利潤」</strong></h2>\n會計利潤和經濟利潤的本質區別在於會計師與經濟學家看利潤的角度不同，最大的差別在於「隱性成本」，簡單來說就是機會成本的概念。今年從新春以來台股市值已經蒸發5.1兆，等於平均每位股民慘虧48萬，國內各類台股基金更是單月平均下跌25%以上；普匯金融科技債權統計至今平均年化內部報酬率落在10%~15%，如果把時間起點拉回到今年初，您當初手上的資金選擇購買了債權，除了不用在股市賠25%，還可以穩定賺到年利率15%，普匯就是您的資金最佳避風港。\n<h2><strong>遵循小額分散的要訣，讓風險降至最低</strong></h2>\n購買債權不必像股票一樣有最低1000股數限制(不論零股的話)，所以小資族只要是剛接觸股票市場至少都得先投入個幾萬元才能進場，而普匯的投資人針對每個借款標的投入金額最低限制1000元，若您手中剛好有1萬元資金，想小試身手的話只要將1萬元分別投給10位借款人各1000元，即可像上一篇王媽媽的投資心法一樣，小額分散風險降低又不需要再花額外時間看盤；風險趨避的投資者可以選擇低年利率的高學歷標的(台清交成政)名校學生，想要中高風險的投資人亦可以選擇年利率較高的借款標的，普匯金融科技擁有AI信用評估系統，且借款人的上架率只有20%不到，經過層層把關的風險才能有效的降至最低！\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">小額分散投資術 把風險降到最低&gt;&gt;</a></strong></h2>\n<h2><strong>普匯投資分析師推薦，3年賺10萬算法大公開</strong></h2>\n<table width=\"361\">\n<tbody>\n<tr>\n<td width=\"111\">每月投入本金</td>\n<td width=\"111\">選定放貸期數</td>\n<td width=\"37\">IRR</td>\n<td width=\"103\">月利(IRR/12)</td>\n</tr>\n<tr>\n<td width=\"111\">10000</td>\n<td width=\"111\">12</td>\n<td width=\"37\">14%</td>\n<td width=\"103\">0.011666667</td>\n</tr>\n</tbody>\n</table>\n推薦給想要快速存到一筆金額的你， 給自己3年的時間，把每個月的娛樂金1萬去購買普匯債權，並謹記風險分散切割成10個1000元，標的選擇級數約6級左右的評分，並持續復投，3年就可以創造出10萬的績效，報酬率達到130%，而詳細算法都列在下圖，有興趣的小夥伴可以參考看看！\n\n<img src=\"/upload/2020/05/資金避風港1.png\" alt=\"\" width=\"928\" height=\"742\">\n<h2><strong><a style=\"color: #ff0000;\" href=\"https://event.influxfin.com/r/iurl\">立即讓自己的資產翻倍&gt;&gt;</a></strong></h2>\n<strong>&nbsp;</strong>', '2020-05-05 17:53:58', '普匯分析師推薦，3年賺10萬算法大公開', 'article', 'publish', '2020-05-15 19:35:13', '', 0),
(7912, 1, '/upload/2020/05/k.png', NULL, '<!-- wp:paragraph -->\n<p>繼兩家純網銀公司將來商業銀行、連線商業銀行各別於2020/01/31、2020/03/31取得登記許可後，樂天國際商業銀行也於05月18日正式宣布取得經濟部商業司核准公司設立登記，三家純網銀目前只需再通過金管會核發營業執照，即可力拚於Q3正式營業，此舉意味著純網銀時代到來不遠了!</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>樂天國際商銀正式成立，首度揭露8名董監事名單</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>由第一金控前總經理簡明仁領軍擔任董事長，在7名董監事名單中台日比例為2 : 5，其中一席董事席位由國票金控董事長魏啓林拿下，另一則由前國票金控副董事長王東和擔任監察人。雖因疫情影響，樂天銀行的日籍相關員工無法來台，但樂天銀行樂觀表示，這段時間改採取每日視訊會議，相關設備建置與規劃皆會如期進行，希望藉由日本純網銀的成功經驗，為樂天國際商銀注入一劑強心針。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>純網銀將造成金融科技的顛覆式創新</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>台灣三家純網銀開業在即，相信許多人好奇純網銀能帶來多便利的金融體驗?</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>傳統商業銀行所拓展的數位銀行，是在原有銀行的基礎上發展，以互聯網輔助銀行業務，並利用實體和虛擬通路進行虛實整合。而純網銀的出現，無疑是對銀行業掀起一波滔天巨浪，整合科技、通訊、電商、社群媒體與金控公司，透過異業結盟以刺激金融服務創新，帶來1＋1＞2的綜效。除了前述傳統銀行業務(例如 : App 轉帳、提款、存款、貸款、基金等理財服務)皆可在網路上操作外，其最大的創新在於無實體分行，並主打7x24全年無休線上服務，對於不方便親臨實體銀行或高頻金融服務使用率的消費者，更加受到青睞。而實體分行的人事及營運費用，可直接用於支持網路營運，並以較低廉的存放款利率回饋給消費者。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>另外，隨著千禧世代的來臨，數位化早已成為生活核心，對網路依存度高、傾向以網路平台使用服務的年輕一代來說，純網銀挾帶著金融科技的優勢，在服務使用上尤其具有吸引力。以連線商銀為例，LINE目前在台灣的月活躍用戶數約有2100萬，滲透率高達91.3％。從個人、企業，甚至到政府都將LINE視為重要的即時溝通管道，連線純網銀可佐以此優勢輔助，嵌入消費者的生活，打造屬於LINE的金融生態圈。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>而在亞洲國家中，由日本純網銀Japan Net Bank於2000年率先開出第一槍，至2015年，騰訊集團旗下的微眾銀行為中國大陸首家開辦此業務的銀行，南韓由K Bank及KakaoBank首先於2017正式開業，香港則由眾安銀行擔任首家開辦的虛擬銀行。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>下圖為各國主要純網銀比較-以亞洲區為例:</p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":7914} -->\n\n<p><img alt=\"\" src=\"/upload/2020/05/oo.png\" /></p>\n<!-- /wp:image --><!-- wp:paragraph -->\n\n<p><strong>普匯金融科技深植你心</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>因應金融趨勢浪潮，未來如何將純網銀應用於各式服務場景，呈現更優秀的金融服務體驗並落實普惠金融，值得我們期待。而普匯金融科技亦是以落實普惠金融為理念，致力於提供透明、優質的P2P金融服務平台，採用AI無人自動化和區塊鏈技術進行精準審核與維持交易的隱私安全性，補充傳統銀行的不足，給大眾便利、信賴的投資與借貸服務。</p>\n<!-- /wp:paragraph -->', '2020-05-22 15:14:02', '樂天國際商銀取得公司登記許可，迎接純網銀最後一哩路', 'article', 'publish', '2020-07-06 09:59:41', NULL, 0),
(7971, 1, '/upload/2020/06/5566-300x200.jpg', NULL, '<p><!-- wp:paragraph {\"align\":\"right\"} --></p>\n<p style=\"text-align: right;\"><em>圖片來源：Unsplash</em></p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n<p><strong>（壹）香港國安法正式打破北京政府所承諾的一國兩制，其具體內容為何？</strong></p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>                溯及香港國安法的立法歷史，需要了解的是中國於2002年要求香港所立定的基本法第23條，為一條規定香港應自行立法維護中華人民共和國國家安全的法律條文。其用意是以法律禁止任何有損該國國家主權、領土完整、統一及國家安全的行為。但卻於2003年引發50萬香港人民參與七一大遊行反對立法，最終港府只能停止立法，至今都未曾再提案。</p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>  中國政府官員針對香港國安法簡介，表示由於一國兩制自2003年飽受威脅，基本法有被妖魔化、可能遭受長期立法擱置的風險，且反送中事件更加劇了當局立法的必要性，香港國安法將懲戒矛頭指向「顛覆國家政權」、「分裂國家」、「恐怖活動」、「外部勢力干預」等行為。當時基本法23條強調應該由香港自行立法，可如今的香港國安法卻是直接繞過香港中央法院，為中國人大直接為香港所制定的法律，示威者更是擔憂長久以來香港維持的一國兩制被破壞，此法也引起國際關注。</p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n<p><strong>（貳）香港國安法對於整體社會經濟會造成什麼影響？</strong></p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>                香港國安法的通過，香港人民的言論自由、司法自由都會受到影響，此立法並沒有明確地告訴大眾什麼事是被政府認為會危害國家安全，有鑒於先前反送中和香港民主派被中共迫害的慘痛案例，香港人民很難不去堪憂是否未來有可能因為批評政府當局或領導的言論而被違反國安法起訴，而罪名為：做出可能顛覆國家政權之行為，總結地說，香港國安法等於剝奪香港人民多年來享有的基本權利如：言論表意自由、人民團體結社自由、甚至信仰宗教自由等等，這對香港人民無疑是歷經逃犯條例後的第二次爆擊。</p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>而就經濟層面來說，美國總統川普於5月29日宣布取消香港特殊待遇，美國將取消長期對香港的關稅和簽證優惠政策，採取對中國大陸強硬的關稅壁壘和出口管制，學界對於美國此舉是否短期危及香港經濟普遍持相同意見，認為這對香港經濟短期影響不大：</p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>週四 (28 日)「港版國安法」通過之際，摩根大通發表報告認為，若美國撤銷香港特殊地位，預估對香港經濟直接的影響不大，就算美國作出反制，相信只會針對個人或個別實體。</p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>美國有線新聞網CNN分析，香港失去特殊地位，未必令外資大企業即時撤出香港這個國際金融中心，短期震盪有限，但長遠或削弱香港對外資的吸引力及競爭力。</p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>瑞銀投資銀行亞洲經濟研究主管兼首席中國經濟學家汪濤稱，目前未見觸發明顯走資，若美國撤銷香港在投資及貨幣等獨立地位，對香港造成重大打擊，但料機會較微，因為會對大量在港的美國金融機構造成影響。</p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n<p><strong>（參）</strong> <strong>普匯觀點</strong></p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>                普匯認為「香港國安法」的通過對香港經濟之直接衝擊有限。首先，香港去年對美出口額為392.2億美元，僅佔其整體對外出口總額的7.6%，且其中有超過9成是屬於轉口出口，而此些轉出口商品原本即未適用香港獨立關稅優惠，故儘管未來香港在貿易上被視為與中國同等對待，對於香港貨品出口的直接影響可能不大。此外，現階段在美營運的中資銀行僅有6家，且經測算此些銀行在美資產佔總資產比平均僅為0.7%，其中對美暴險規模最大的中國銀行其佔比亦僅為1.73%，若進一步考量資產變現和轉換空間，儘管美國未來宣布對凍結中資銀行在美資產，對中資銀行的直接影響或相對有限。</p>\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n<p>然而，隨著香港原先之高科技、敏感技術產品進口限制豁免被取消，未來含有美國科技的德國、日本產品將無法輸入香港，此除了將打擊長期仰賴香港轉口之中國高科技產業供應鏈外，還將對在香港設有生產基地的科技公司，如ASM太平洋等產生直接影響。此外，鑒於不排除美國總統Trump在未來繼續發布其他限制措施，此將一定程度打擊對港投資者之市場信心，故潛在衝擊資本外流風險可能明顯較高，值得持續關注。</p>\n<p><!-- /wp:paragraph --></p>', '2020-06-20 10:39:54', '北京政府強推「國安法」、美國取消香港國家自治地位，香港金融中心即將殞落？', 'article', 'publish', '2020-06-20 10:42:31', '', 0),
(7979, 1, '/upload/2020/07/789-300x200.jpg', NULL, '<p></p>\n\n<!-- wp:paragraph -->\n</p>\n<p>2017年一卡通票證公司取得電子支付業務之營業許可，並於隔年與台灣最大通訊軟體公司LINE旗下之第三方支付公司LINE Pay合作推出LINE Pay一卡通，運用其於台灣2200萬用戶的優勢搶佔市場。2019年悠遊卡股份有限公司亦申請取得許可，利用其龐大用戶基數，於2020年推出「悠遊付」之電子支付服務。</p>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n</p>\n<p>2015年《電子支付機構管理條例》立法通過以來，迄今有5家專營電支業者（街口、歐付寶、橘子支付、國際連、簡單行動支付）和23家兼營業者（銀行、中華郵政及電子票證發行機構），加上原有的第三方支付業者，使目前國內行動支付工具種類繁雜、業者眾多、市場競爭激烈、監管瑣碎複雜，民眾於眾多選擇前手足無措。有鑒於電子票證機構積極兼營電子支付業務，金管會為統一管理及規範，並讓驗證機制及資金移轉更加方便、提升普及率，遂擬整併於2009年1月23日公布之《電子票證發行管理條例》及於2015年2月4日公布之《電子支付機構管理條例》兩部專法。</p>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n</p>\n<p>本文上篇討論電子票證條例以及電子之條例之缺失，下篇則將討論兩專法未來整併之修改方向，兩法業務重疊與實收資本額設定之問題，首當應於兩法合併前進行討論與修正，以利未來兩法合一時相關問題能被清晰的區分、規範與改善。</p>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n</p>\n<p>兩部現行專法之主要缺失如下：</p>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n</p>\n<ul>\n<li><b>電子支付形同廣義之電子票證</b></li>\n</ul>\n<p>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n</p>\n<p>《電子票證發行管理條例》規範之業務範圍定義為以電子、磁力或光學形式儲存金錢價值，並含有資料儲存或計算功能之晶片、卡片、憑證或其他形式之債據，作為多用途支付使用之工具。而《電子支付機構管理條例》則著重於透過平台和網絡系統作為買賣雙方之媒介，並以代理收付實質交易款項、收受儲值款項、電子支付帳戶間款項移轉為其主要規範之業務範圍。從法條上觀之，兩法所規範之金融行為有顯著之不同，而兩者於使用者一般的使用情境亦極為不同。然依照電子票證條例第3條第5款第2目針對電子票證本身作為多用途支付使用時，立法者特意排除了以網路或電子支付平臺為中介，接受使用者註冊及開立電子支付帳戶，並利用電子設備以連線方式傳遞收付訊息，於使用者間收受儲值款項者，將電子支付之儲值服務排除於該法之適用範圍之外。由此可見，電子支付似可認定為廣義之電子票證形式，其主要差別僅在於可於電子支付平台設立虛擬帳戶並且進行帳戶間轉帳，於代理收付實質交易款項方面實質上屬於業務之重疊，僅因電子支付立法時間較晚，導致現今專法針對相同業務做出不同規範和標準，縱有轉帳功能之差異，仍欠妥適。</p>\n<p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n</p>\n<ul>\n<li><b>實收資本額之設定並不能成為穩定營運之保證</b></li>\n</ul>\n<p>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n</p>\n<p>於《電子票證發行管理條例》與《電子支付機構管理條例》皆有關於發行機構與電子支付機構之實收資本額設置。《電子票證發行管理條例》規定發行機構最低實收資本額為新臺幣3億元，而電子支付機構則依據其所欲提供之服務是否僅限於該法第3條第1項第1款之代收代付業務而有新臺幣5億元與新臺幣1億元之區別。其背後立法目的是為健全之財務狀況、完善之資訊安全管理及妥適之風險控管措施，以維持業務之穩定與安全運作，惟其對實收資本額及資本維持原則之正向關係顯有誤解。實收資本額為股東實際將現金或實物投入公司的資本，僅顯現該機構自成立以來獲得之投資金額，並且通常逐步上修，並無確保未來財務狀況之必然性，也無法達到風險管控、資訊管理的目的，更無法確保其具有專業經營能力、可提供穩健長期之服務，甚至保護大眾，故此二條例對於資本額之要求實際上根本無法達到立法目的所追求之意旨。兩專法中已有交付信託與獲得銀行十足履約之相關規定，似更能達成實收資本額相關規定所欲達成之效果。此外，實收資本額亦往往反映立法者對一個行業的管制強度，過高的實收資本額門檻將造成欲經營電子票證或電子支付業者於資力基礎方面更加缺乏，此立法實削減僅為單純經營代收付服務業者之機會。</p>\n<p>\n<!-- /wp:paragraph -->\n\n<p></p>', '2020-07-08 09:41:59', '電子支付、電子票證的監理整合（上）', 'article', 'publish', '2020-07-08 09:43:25', '', 0),
(7984, 1, '/upload/2020/07/7899-300x145.png', NULL, '<p>&nbsp;</p>\n<!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n\n<h2><strong>什麼是支付命令？</strong></h2>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>\n<!-- wp:paragraph -->\n\n<p>為債權人面臨貸款與款項無法收回時之債權保障法律手段。然因過往許多申請人濫用支付命令性質，現今支付命令不具確定判決效力，而僅得為執行名義。也就是說債務人對於已確定之支付命令不服者，除對債權人已聲請之強制執行提起債務人異議之訴，仍可針對該支付命令提起確認之訴以資救濟。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>\n<!-- wp:paragraph -->\n\n<h2><strong>支付命令的優點</strong></h2>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>\n<!-- wp:list {\"ordered\":true} -->\n\n<ol>\n	<li>聲請費用較低</li>\n	<li>流程簡易</li>\n	<li>更有效率</li>\n</ol>\n\n<p><!-- /wp:list --></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n<!-- wp:paragraph -->\n\n<p>支付命令的審查不須答辯，法院無需訊問債務人，只要債權人提交證明文件申請，透過書面審理就可逕行核發。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>\n\n<h2><strong>如何處理支付命令？</strong></h2>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>\n<!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n\n<p>支付命令函到達債務人時，無論該債權債務關係是否真實存在皆應積極處理。若對支付命令內容有疑義，應積極地依向法院提出異議，無需敘明理由，然後在獲得確定判決或法院核定的調解書後，需要盡快踐行判決書、調解書內容，否則會進入假扣押與強制執行流程。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n<!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n\n<p>債務人若未於二十日內提出異議，債權人得持支付命令及確定證明書向國稅局查調債務人的財產總歸戶，並向法院聲請對對薪資、動產、不動產等之假扣押與強制執行以進行求償。凍結薪資或是強制扣薪將會通過法院向債務人所任職的公司核發執行命令，極可能使債務人面臨被解雇、信用破產的窘境，而對於動產、不動產的查封拍賣，更是直接造成財產權、所有權的喪失。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n<!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n\n<h2><strong>如果已被強制執行怎麼辦？</strong></h2>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>\n<!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n\n<p>在踐行強制執行的過程中，債務人其實還有救濟的機會，債務人可以提起債務人異議之訴，但訴訟過程中法院並不會停止扣薪、查封、拍賣等等強制執行行為。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>\n\n<p>&nbsp;</p>\n<!-- wp:paragraph -->\n\n<p>如果想要停止執行需向法院申請，經取得法院之准許停止裁定，並提供「相當數額的保證金」，才能提示予執行處的承辦人員，所費不貲。而於查封、拍賣情形，債務人得於拍定前提出現款，聲請撤銷查封。因而必須在查封的財產被拍賣前，將足以清償全體債權人債權金額(含本金、利息、違約金及程序費用等)及執行費之現金至法院，向承辦人表示清償的意願，由承辦人開立繳款單，再以此至聯合服務中心繳款，程序瑣碎麻煩。</p>\n\n<p>綜上，收到支付命令後首要釐清是否有該債務存在，應盡快為給付或提出異議，以免後續繁瑣的強制執行程序，耗時傷財，更有可能影響自身聲譽、就職情況，形成無限輪迴的負面循環。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>', '2020-07-08 09:50:52', '【普匯小學堂】收到支付命令函不可置之不理', 'article', 'publish', '2021-02-17 09:00:42', NULL, 0),
(7994, 1, '/upload/2020/07/ZZ-300x135.png', NULL, '<p>&nbsp;</p>\n<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>取得銀行牌照，使金融服務能夠大範圍的觸及民眾</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>上次已經介紹過美國第一家收購銀行的P2P借貸金融科技公司 － Lending Club；而這次要介紹一家總部位於英國的P2P借貸公司，且已獲得監管部門的批准，將推出只有線上業務的Zopa銀行，根據AltFi，它將與現有的P2P借貸業務並駕齊驅。Zopa銀行將提供定期儲蓄帳戶，預計在未來幾個月內將推出信用卡。(AltFi專門為發展快速的替代性金融產品和金融科技提供新聞，觀點，見解和事件。)</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>這是P2P　Lending長期致力於進軍銀行業的成果。Zopa從2018年12月開始努力獲得牌照許可，而在當時它獲得了金融行為管理局（Financial Conduct Authority）的諸多限制。作為許可條款的其中一部分，Zopa被要求籌集額外的資金以緩解監管機構的擔憂，原因是擔心其金流不夠穩定而無法成為一家完整的銀行，而Zopa在2019年12月籌集了1.4億英鎊（1.83億美元），這筆資金在與獲得許可證的同一天到達，使Zopa在擴展核心業務之外，還能持續擴大金融服務範圍。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>多樣化的金融科技商品，能創造新的商機</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>作為英國首批成功的金融技術公司之一，Zopa擁有一個知名品牌。Zopa於2005年建立了平台，已為成千上萬的客戶提供金融服務。而且，Zopa能夠將新的銀行產品交叉銷售給現有的客戶，讓舊客戶也能夠享有新的銀行金融服務。這些因子將降低未來新成員的進入門檻，並且通過產品多樣化，Zopa能夠開拓新的收入來源並大幅增加利潤。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>但是，Zopa正在進入一個擁擠的金融市場，獲取新客戶可能不是那麼簡單。儘管線上化的體驗可能使Zopa在客戶體驗方面與現有銀行相比更能脫穎而出，但它將與諸如Monzo和N26之類的新銀行競爭，而後者在客戶服務方面早已樹立了更高的標準。而且，與市場上已有的產品相比，Zopa的計劃產品似乎有限：例如，Monzo和Starling Bank都提供了商業帳戶。 因此，接下來才是Zopa要面臨的大挑戰。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>普匯觀點</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>&nbsp;&nbsp;&nbsp; 英國與美國將P2P借貸歸類於證券交易，所以民眾們在P2P借貸平台上操作點對點債權交易系統早已行之有年，且非常有紀律，故就算大陸爆發許多不法行為，P2P借貸在歐美國家仍然是一項非常流行的小額借款平台，因其擁有去中心化的優點且應用金融科技的技術，資金的流通效率非常之高。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>&nbsp;&nbsp;&nbsp; 普匯是台灣第一家真正使用金融科技技術的P2P借貸公司，只要一台手機即可在普匯的平台上迅速的籌措資金，未來也將持續開發新的產品使其能夠觸及更多銀行所無法觸及的區域，使普匯金融能夠普惠大眾。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>', '2020-07-13 17:10:48', '【普匯小學堂】全球第一家P2P 借貸公司「Zopa」，已取得英國銀行牌照', 'article', 'publish', '2021-02-17 08:59:32', NULL, 0),
(8000, 1, '/upload/2020/07/Picture1-300x120.png', NULL, '<p><!-- wp:paragraph --></p>\n\n<blockquote>\n<p><a href=\"https://www.influxfin.com/%e6%a8%82%e5%a4%a9%e5%9c%8b%e9%9a%9b%e5%95%86%e9%8a%80%e5%8f%96%e5%be%97%e5%85%ac%e5%8f%b8%e7%99%bb%e8%a8%98%e8%a8%b1%e5%8f%af%ef%bc%8c%e8%bf%8e%e6%8e%a5%e7%b4%94%e7%b6%b2%e9%8a%80%e6%9c%80%e5%be%8c/\"><strong>上篇</strong></a>向大家說明了純網銀帶給大眾的便利服務及亞洲區各代表性純網銀的比較，想必大家對純網銀已經有初步的了解。但純網銀真的對金融業攻無不破嗎?今天，小編將繼續探討純網銀的營運模式與挑戰。</p>\n</blockquote>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>純網銀有哪幾種?</strong></p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>在國際上純網銀的成立模式可分為以下四種</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:table --></p>\n\n<table>\n	<tbody>\n		<tr>\n			<td>科技業創設</td>\n			<td>集團企業投資</td>\n			<td>集團企業與銀行業合資</td>\n			<td>金控公司投資</td>\n		</tr>\n		<tr>\n			<td>由具有金融科技背景的人設立，優勢為擁有創新的科技技術和營運模式</td>\n			<td>由集團企業跨業設立，優勢為擁有龐大客戶群基礎</td>\n			<td>由集團企業與銀行業合資設立，優勢為兼具企業客戶群及金融專業</td>\n			<td>由金控公司設立，優勢為擁有金融專業與客戶群</td>\n		</tr>\n	</tbody>\n</table>\n\n<p><!-- /wp:table --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>營運模式也可分為以歐美國家為主體的「科技創新及客戶體驗模式」與以亞洲國家為主體的「完整營運生態圈模式」，會有此分別是因為歐美國家金融普及率高，因此著重於給用戶價格優惠的創新金融服務以吸引客戶，而亞洲國家的純網銀公司多為各企業之結合，包括科技、通訊、電商、社群媒體與金控公司，可結合各產業的龐大用戶群，並結合物流、金流打造完整營運生態圈。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>純網銀的挑戰</strong></p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>純網銀為大家帶來便利的金融服務體驗，但此新興的金融科技服務，也將面臨許多挑戰。前金管會主委曾銘宗表示，台灣的純網銀發展將會面臨到三大問題:</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:list --></p>\n\n<ul>\n	<li>純網銀以高利率吸收存款、低利率放款方式進攻金融市場，然而金融市場需忍受純網銀帶來的市場價格破壞，且殺價競爭的方式，恐怕會讓銀行獲利大幅衰退，導致經營風險的提高</li>\n	<li>為因應純網銀的無實體化競爭，勢必產生鯰魚效應使國內大型銀行做出數位化的變革，但若沒有良好的配套措施，將會威脅台灣金融從業人員的工作權益</li>\n	<li>若國內大型銀行長期跟進純網銀低價競爭模式，後續將產生經營不善問題，最後仍可能是國庫或全民必須吸收銀行退場的成本</li>\n</ul>\n\n<p><!-- /wp:list --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>另外，因純網銀屬金融科技領域之服務，亦可能面臨銀行領域的六大風險，因監督不善導致的作業風險、因快速在不同帳戶間移轉資金導致的流動性風險、因法令意識薄弱導致的法遵風險、因違反個資保護法導致的個資保護風險、因各公司責任歸屬不清導致的委外風險以及因高度仰賴科技應用導致的資安風險等各式風險。這些都將成為純網銀的挑戰</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>純網銀會取代銀行分行嗎?</strong></p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>綜觀前述純網銀的優勢或挑戰，相信大家都很好奇，在未來純網銀會取代銀行分行嗎?</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>純網銀挾帶科技優勢，挑戰傳統銀行的業務發展，然而，國泰金控總經理李長庚表示: 「日本、美國發展純網銀十多年，也沒有完全取代實體銀行，並沒有那麼可怕」。純網銀視為金融科技領域的一種破壞式創新，一開始皆採用高利率吸收存款、低利率放款方式吸引用戶，勢必對台灣金融業造成衝擊，然而時間一久回到原本正常競爭下，純網銀未必能持續打擊國內大型銀行業務，加上純網銀存在些許風險，以及各國純網銀規模皆不大，未來若要對國內大型銀行產生強大威脅，還需視後續經營方針決定。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>根據金管會統計，至109年第一季為止，台灣有36家本國銀行，3404家分行支構，再加上外國銀行在臺分行、大陸地區銀行在臺分行、信用合作社、農會信用部、漁會信用部等貨幣機構，台灣國內貨幣機構總分支機構高達6201家，密度可說是相當之高，銀行ATM的密度更是世界第一，此數據顯示台灣的金融環境成熟，銀行競爭激烈。雖純網銀已在歐美國家運行行之有年，但以台灣金融生態來看，純網銀要如何在金融市場中搶占一席之地，仍值得關注。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>普匯觀點</strong></p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>身為關注最新金融趨勢並致力於提供最優質借貸服務的金融透明平台，普匯站穩腳跟，採用線上借貸服務，最低只要1000元，就能領到最5%-20%的利息，在風險方面，普匯採用優質風控模組，自動化審核各項認證，給予相當安全、保障的服務！普匯也和大家一樣期待純網銀的到來，在現今瞬息萬變的金融情勢下，迎接純網銀帶給大眾的震撼與挑戰。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p><!-- /wp:paragraph --></p>', '2020-07-22 17:42:41', '【普匯小學堂】純網銀知多少? 淺談你所不知道的純網銀挑戰', 'article', 'publish', '2021-02-17 08:59:26', NULL, 0),
(8014, 1, '/upload/2020/07/Picture1-300x113.jpg', NULL, '<!-- wp:paragraph -->\n<blockquote>\n<p>中國人民銀行宣布將發行「數位人民幣」，為全世界第一款數位貨幣，並定位為</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>中國法定貨幣之一。目前會先在少數網點進行內部封測，並涉及多個場景，預計將為虛擬貨幣領域帶來全新的面貌與影響。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>而數位人民幣的用途是什麼呢?小編帶你一探究竟。</p>\n</blockquote>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>數位人民幣是什麼?</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>數位人民幣指的是由政府央行發行的一種數位貨幣，以數位形式展現，他的地位與紙鈔相同，具有法定清償效力，表示任何人都不可拒絕使用數位人民幣付款、接收，數位人民幣與虛擬貨幣相似，但又不盡相同，一般的虛擬貨幣(如:比特幣)，特點是去中心化與有最終數量，但數位人民幣為中國政府掌控，管制與發放數量皆為政府控管，與比特幣形式難以相容。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>一張100元的紙鈔人民幣100元就等於數位人民幣100元。用戶只需要註冊數位人民幣錢包就可使用，並可隨時在日常交易中進行。它的特色在於採用「雙離線技術」，即使雙方手機皆沒有網路訊號，仍然可以使用，使用方式為雙方拿起手機碰一碰，即可完成一次交易。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>數位人民幣的特色</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>在數位人民幣的使用中，除了方便快速、節省紙鈔成本且價格穩定(由中國政府控管)外，另一項讓大家好奇的是:「我能使用多少的額度?」，針對這方面，中國政府提出:「APP錢包實名制程度愈高，能使用的額度就愈高」的方案，目前共分為四種層級，分別為強實名、較強實名、較弱實名、匿名，實名制的目的在於讓政府更有效的追蹤每一筆金流。在匿名使用方面，是因為中國政府為落實零售的使用，而開放讓數位人民幣准許以不用實名的方式進行，這種匿名方式交易模式，僅可追蹤錢包位置與商品交易紀錄，而無法查找到使用者，此一特色可使大眾保有日常消費隱私，又可打擊犯罪與貪腐。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>與支付寶、微信支付的差異</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>以支付型態來看，數位人民幣和支付寶、微信支付都是行動支付的一環，大家可能會很好奇，那數位人民幣與支付寶的差異在哪呢?</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>目前中國在支付寶以及微信支付的使用率可說是相當普及，據小編先前在上海生活數個月的經驗來看，中國民眾出門幾乎不帶現金，而是只帶一支手機即可出門生活一整天，就連百貨公司地下街的美食商場也不收現金。因此，數位人民幣的出現可說是非常對民眾的口味的。而數位人民幣與支付寶、微信支付最大的差異在於，他們皆是需要先綁定銀行卡，並申請手機號碼才可使用，且這兩方不可互轉現金，但數位人民幣不僅不須綁定銀行卡，且可在支付寶與微信之間流通自如，此為數位人民幣的一大益處。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>數位人民幣的隱憂</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>雖說數位人民幣訂定為中國法定貨幣之一，但若深入探究其細節，可想見其存在不少隱憂。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>以隱私性來看，平常使用現金交易時，可隨時花在任何想用的地方上，也不怕旁人知道自己的資金流向及花費領域，但若使用數位人民幣，只要國家想了解，個人的金流資料則完全隱藏不住，尤其是金額較大或是較敏感領域的交易，就容易讓隱私暴露在國家下，此美意恐怕會淪落為政府干預經濟的手段。依據此特性，我們可推測出數位人民幣可能會讓部分民眾避之唯恐不及，以至於數位人民幣黑市交易的產生，部分民眾會傾向將數位人民幣折價賣出以換取現金人民幣，以保持交易彈性與隱私，而黑市的出現又會造成貨幣炒作的情形發生，後續情況的惡化將顯而易見。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>另一隱憂為目前國家均偏向高齡化社會，中國若之後大量推行數位人民幣之使用，可能會造成老年人在交易上的使用不便。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>台灣的因應策略</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>數位人民幣發行計畫在中國如火如荼的進行，雖藏有隱憂，但其本質確實為促進國家貨幣發展的一大步。然而，看望著中國對數位貨幣的採用勢在必行，台灣方面又如何看待呢?</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>台灣央行總裁楊金龍對此發表看法，他表示台灣目前並未將數位貨幣作為首要發展目標，而是著重在強化電子支付系統，原因為法規及技術目前皆不成熟，而雖無急迫發展計畫，但央行方面有一直注意數位貨幣的趨勢，以適時擬定對策。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>數位人民幣的發展勢如破竹，代表著貨幣領域的新時代即將來臨，普匯亦走在金融科技的最尖端，採用全自動化線上平台進行審核與放貸，並將資料存放在AWS雲端伺服器中，保護投資人、借款人之隱私，提供給大眾安心的借貸服務。</p>\n<!-- /wp:paragraph -->', '2020-07-30 11:12:43', '【普匯小學堂】數位人民幣開啟新支付時代', 'article', 'publish', '2021-02-17 08:59:10', NULL, 0),
(8025, 1, '/upload/2020/08/123-300x200.png', NULL, '<!-- wp:paragraph -->\n<p>開放銀行(Open Banking)為目前國際間新興的金融科技詞彙，各國皆對開放銀行產生強烈興趣，隨著金融科技蓬勃發展，開放銀行這種全新的方式對大眾來說十分新穎，也可以使金融科技公司提供更多創新的服務，更是一種將科技運用效益極大化的方式。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>國際開放銀行概況</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>2007年底，歐盟開始進行支付服務指令(PSD)，目的為降低支付服務新進者的難度以活絡市場。2013年擴大適用範圍列入第三方支付服務公司(TPPs)，強化資安防護，英國並於2017年制定開放銀行相關應用程式介面(APIs)標準，授權開放API提供第三方業者使用，是全球第一個執行的國家。亞太地區則是新加坡和香港能見度最高，香港金融局劃分四階段實施，分別為產品資訊、消費者申請、帳戶資訊和交易資訊，在此模式下香港金融科技發展十分成功。另外，澳洲則著重於資料分享的規劃。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>台灣開放銀行概況</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>台灣近幾年來在金融科技領域的發展相當積極，2019年開放API平台正式上線，與香港模式大致相同，第一階段為「公開資料查詢」，內容以非金融交易的資訊為主，第二階段開放「消費者資訊查詢」，第三階段則開放「交易面資訊」。目前的實務案例是業務範圍為金融商品資訊(台外幣存款利率、匯率等)比較的麻布記帳(MoneyBook)和銀行合作，包括國泰世華、台北富邦等數家公司。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>開放銀行啟動之前後架構</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>依據財金資訊公司的資訊所示，在啟動開放銀行前TSP業者會與金融機構直接合作，而財金資訊公司處於協助角色，而此階段雙方合作所採用之應用程式介面(API)無統一規格，皆以雙方協定之規格執行。而在開放銀行起啟動之後，財金資訊公司將改成TSP業者與金融機構的中介角色，而屆時將會採用標準統一規格的API進行資訊交流。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>而在與消費者連結方面，因開放銀行啟動前TSP業者的所有洽談排程皆不穩定，包括系統需求、規格、資安等問題皆會產生，一來一往商談將耗費不少時間，因此此階段還是以金融機構來服務消費者。而在開放銀行啟動之後，因資料交換已使用統一的API規格，所以消費者可以自由選擇使用TSP業者或金融機構所提供的服務。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>普匯觀點</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>開放銀行希望以金融界的數據共享來推動銀行與金融科技公司的合作與創新，並追求消費者與社會的利益最大化，普匯亦認同此理念，也與凱基銀行進行廣告合作，期望以金融服務的轉型與創新，提供使用者方便快速的用戶體驗。</p>\n<!-- /wp:paragraph -->', '2020-08-04 16:30:47', '【普匯小學堂】開放銀行的發展', 'article', 'publish', '2021-02-17 08:58:58', NULL, 0),
(8029, 1, '/upload/2020/08/Picture1-300x157.png', NULL, '<!-- wp:paragraph {\"textColor\":\"pale-pink\"} -->\n<blockquote>\n<p>&nbsp;</p>\n\n<p>有募資經驗或是正再募資的新創企業創辦人，大多有一個共識：「台灣的投資人都相對其他國家的保守」。這些人的意思，其實是指其新創公司的估值沒有獲得投資人認同，甚至被過度低估了。可惜的是，新創企業的公司估值未被投資人接受的背後原因，常常是來自創辦者與團隊沒有將其創新技術與產品的市場潛力完全釋放出來，更甚至是沒有做好市場區隔，突顯自己的不同。所以換句話說，不但「台灣的投資人都很保守」，可能連「台灣新創企業的創業者也很保守」。</p>\n</blockquote>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>創業環境與新創經營現況</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>根據新創企業報告指出，創業者心目中在「創業文化」與「人才技能」方面表現較佳，但在「資金取得」與「法規環境」方面表現相對疲弱。我國其實在人才技能培育上是很完善的，但嚴格的法條限制了人們的想法，而台灣在2018年在金管會頒布「金融科技創新實驗管理辦法」開放業者申請沙盒實驗，協助金融服務業運用科技支援金融服務產業的發展，並建立與金融科技業的夥伴關係。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>下圖為台灣新創各項自評創業指標的統計圖，新創企業普遍風險與危機管理意識不足，也印證產品技術研發背景為主的創業者，除了募資以外，對於商業環境的掌握程度、風險拐裡意識皆需要再加強。</p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":8031} -->\n\n<p><img alt=\"\" src=\"https://www.influxfin.com/wp-content/uploads/2020/08/Picture1-1.png\" /></p>\n\n<p><br />\n來源：<br />\n2019 PWC<br />\n新創生態圈大調查<br />\n&nbsp;</p>\n<!-- /wp:image --><!-- wp:paragraph -->\n\n<p>去年調查發現，新創團隊的創業類型分布為：服務創新41%、產品創新26%、商業模式創新20%、技術創新13%。而新創企業營收現況則是19%尚未有營收、53%有營收但仍虧損中，僅28%已有營收且獲利。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>而受調查新創認為為開始獲利或者獲利不如預期的前四項原因包括：先期研發成本過高(21%)、缺乏有效的商業模式(18%)、相似產品或服務競爭者眾多(14%)以及製造成本高、尚未達經濟規模(11%)，顯示新創企業除了投入初期研發造成主要的成本壓力，對市場的了解有限，創新的獨特性不足以拉開競爭優勢，或者對商業模式的掌握偏弱。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>新創企業募資困難的問題癥結</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>去年7月、有一家發展小型人造衛星的日本新創企業，順利在A輪的募資獲得近日幣90億的創投資金。連同前面的種子輪與天使輪，這家成立僅一年五個月的日本新創企業，募得的創投資金累計約日幣110億、超過1億美元(約台幣30億)。據稱，是全球範圍中，最快的一家募資金額達1億美元的新創企業。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>但是，一般來說，類似日本這家發展小型衛星的新創企業，如果是在台灣創立，我們很難想像可以在成立不到一年半，就募到1億美元創投資金。探討其原因，就會像我們將大型魚種的幼苗放入大海成長，一年半後可以長到20公斤；將它留在內陸湖裡生長，同樣的期間過後、長成的體型相對很小的情形一樣。 所以，對這些未來的發展前景還充滿不確定性、只以台灣市場為發展範圍的新創企業，投資人會採取「很保守」的心態算是正常的。問題的癥結點，應該在於新創公司的創業團隊，如何讓投資人覺得值得投資。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>台灣新創籌資除了政府補助，還可以有新的選擇</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>新創企業往往最大的問題仍然是資金不足，普匯推出新產品　－　創業貸，新創企業除了向政府申請補助外，不足額的部分可以向普匯申請創業貸款，普匯金融科技開發完善線上對保技術，主要以中小企業、小微企業及新創企業為目標，免去面對面簽立契約的步驟之外也不需要占用借款人額外的時間做視訊面談對保，我們透過風險管控系統直接擷取用戶提供的資料進行線上交叉比對，可以更精確的估計新創產業的信用水準，進而加速審核，提升企業融資效率！</p>\n<!-- /wp:paragraph -->', '2020-08-06 09:18:28', '【普匯小學堂】台灣新創企業的困境', 'article', 'publish', '2021-02-17 08:58:43', NULL, 0),
(8052, 1, '/upload/2020/08/Picture1-2-300x239.png', NULL, '<p><!-- wp:paragraph --></p>\n\n<blockquote>\n<p><strong>是否每次遇到年利率、月利率、殖利率都不知道實際的算法是什麼?很多人都覺得年利率就一定是月利率 x 12，但其實在部分的情況下這個觀念是錯的！如果你經常用錯誤的算法去計算利率的話，很可能會造成財務損失。</strong></p>\n</blockquote>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>利率試算</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>購買過理財商品，或著是貸款過的朋友們一定常常會聽到年化利率、月利率等等的字眼，而這些利率到底是怎麼算出來的呢？其實大部分的借貸都是以複利率來計算，也就是會有利滾利的狀況，學習投資以前要先搞懂這些基本觀念，下面讓普匯小學堂來簡單做介紹：</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} --></p>\n\n<p><strong><em>單利</em></strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"backgroundColor\":\"cyan-bluish-gray\"} --></p>\n\n<p>每期的利息只以本金計算，不把前一期的利息加入本金計算</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\"} --></p>\n\n<p><em><strong>複利</strong></em></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"backgroundColor\":\"cyan-bluish-gray\"} --></p>\n\n<p>每期的利息是前一期的本利和計算</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>假如今天有一筆借貸的本金為100萬元，年利率為5%，會有以下兩種算法：</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p><strong>單利</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>一年後的本利和為100(萬元) + 100(萬元) x 5%(年利率) = 1,050,000(元)</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>十年後的本利和為100(萬元) + 100(萬元) x 5%(年利率) x 10(年) = 1,500,000(元)</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p><strong>複利</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>一年後的本利和為100(萬元) + 100(萬元) x 5%(年利率) = 1,050,000(元)</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>十年後的本利和為100(萬元) x ( 1 + 5% ) ^ 10 = 1,628,895(元)</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>[其中 ^ 為次方的意思]</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>這裡就可以了解到了複利的可怕，同樣一筆100萬的借款金額，以單利及複利各別計算十年就相差了1,628,895 - 1,500,000 = 128,895的利息支出，幾乎佔了期初的13%，當借款期間為1時，單利及複利的計算是相同的，一旦借款期間超過了1，複利就會開始起作用並且遠遠大於單利的計算。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>普匯借款利息採ＰＭＴ(固定利率等額分期付款金額)</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:list --></p>\n\n<ul>\n	<li><strong>什麼是</strong><strong>PMT?</strong></li>\n</ul>\n\n<p><!-- /wp:list --><!-- wp:paragraph --></p>\n\n<p>PMT（Returns the periodic payment for an annuity）其實就是年金(Annuity)的概念，指的是在某一特定期間內，定期支付或領回某一特定額度之現金流量(Payment)</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>PMT計算公式 :&nbsp;</p>\n\n<p><!-- /wp:paragraph --><!-- wp:image {\"id\":8054,\"align\":\"center\",\"width\":140,\"height\":43} --></p>\n\n<p><img alt=\"\" src=\"https://www.influxfin.com/wp-content/uploads/2020/08/Screen-Shot-2020-08-12-at-10.33.50-AM-300x108.png\" style=\"height:69px; width:191px\" /></p>\n\n<p><!-- /wp:image --><!-- wp:paragraph {\"align\":\"center\"} --></p>\n\n<p><strong>C：本金&nbsp;&nbsp; M : 月利率&nbsp; T : 期數</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>例子 :</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>借款金額3萬、年利率9%（月利率為9%/12）、借款期間6個月，則每月攤還金額試算如下</p>\n\n<p><!-- /wp:paragraph --><!-- wp:image {\"id\":8055,\"align\":\"center\",\"width\":249,\"height\":51} --></p>\n\n<p><img alt=\"\" src=\"https://www.influxfin.com/wp-content/uploads/2020/08/Screen-Shot-2020-08-12-at-10.33.58-AM-300x85.png\" style=\"height:85px; width:300px\" /></p>\n\n<p><!-- /wp:image --><!-- wp:paragraph {\"textColor\":\"vivid-red\"} --></p>\n\n<p>即可算出每個月的平均分期償還金額囉！</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>分期攤還試算</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>如果手邊沒有電腦可以試算的朋友也不用擔心，普匯App就已經幫您內建好了計算公式，只要動動手指就可以算出每個月平均攤還金額囉！趕快下載試試看</p>\n\n<p><!-- /wp:paragraph --><!-- wp:image {\"id\":8056,\"align\":\"center\"} --></p>\n\n<p><img alt=\"\" src=\"https://www.influxfin.com/wp-content/uploads/2020/08/image-2.png\" /></p>\n\n<p><!-- /wp:image --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>普匯貸款可分期償還，減輕年輕族群壓力</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>在傳統銀行核貸機制下，學生、社會新鮮人及青年創業家，由於缺乏與銀行往來資歷，往往被評估為違約風險高族群，因而銀行大多不願核貸或是僅願意提供高利息產品。普匯認為，學生、社會新鮮人及青年創業家等年輕族群在小額借貸上具備還款能力且有高度還款意願，建立在良好信用下的借貸行為其實是一種很常見的財務管理，槓桿理財也亦是一種以負債做操作的投資行為。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>如有違約狀況發生，普匯亦以提供無法償還之借款人工作機會和轉換產品的創新處理方式，來降低借款人違約風險。普匯認為只要借款人有還款意願，卻無法如期還款時，會盡力協助其避免違約，而非以尋求法律或其他途徑解決，不僅可以使投資人獲得全額本金與利息，學生也能安心借貸，不用擔心一時的財務困難，兼具投資人利益與學生尊嚴。</p>\n\n<p><!-- /wp:paragraph --></p>', '2020-08-12 10:12:36', '【普匯小學堂】普匯分期教您計算攤還利息', 'article', 'publish', '2021-02-17 08:58:31', NULL, 0),
(8079, 1, '/upload/2020/08/Picture1-3-300x223.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p>&nbsp;</p>\n\n<p><strong>科技進步改變大眾消費模式</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>隨著數位化、科技化時代的來臨，智慧型手機開始普及，因此間接帶動了大眾於各方面之行為模式，尤其於網路購物最為盛行，像是AC Nielsen在網路購物行為研究的資料顯示，2017年台灣網購的消費者透過行動裝置購物已超越了非行動裝置（包含桌上型及筆記型電腦）。網路購物的涵蓋範圍近至生活必需品，遠至旅遊之住宿、行程，如今皆能於一支智慧型手機中完成，再加上物聯網、行動裝置等各種科技的出現，使的人們開始改變支付模式，例如Line Pay、街口支付等，僅以QR Code或是App的支付便能完成一項交易。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>行動支付知多少</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>行動支付的廣泛定義為，舉凡以行動存取設備（如手機及平板電腦等）透過無線網路，採用語音、簡訊或近距離無線通訊等方式所啟動的支付行為均屬之。而臺灣主要的行動支付分為三類:電子票證、電子支付、第三方支付。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>電子票證:大眾最早熟知之行動支付類型，其代表業者有一卡通、悠遊卡。金流方式為儲值金、支付方式為RFID感應支付，可花費於交通(捷運、公車)、小額等多種消費，但不具有轉帳功能。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>電子支付:藉由綁定銀行帳戶及信用卡，來進行消費及扣款，代表業者有歐付寶。可以進行轉帳與儲值，更擁有提領功能。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>第三方支付:藉由第三方支付保障了消費者，先付款給第三方，消費者確認貨品沒問題後，才由第三方付款給賣家，其代表業者有Line Pay、GOMAJI。金流方式主要為信用卡，支付方式為掃碼支付，為實名制，且不得儲值，不得轉讓，但不限交易金額。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>除了這三種類型的行動支付外，近年來最為大眾熟知之行動支付應為三大Pay: Apple Pay、Google Pay、Samsung Pay。它們是藉由銀行和這三家公司合作，將他們的信用卡或金融卡複製到手機上作為載具，本質上和直接使用卡片消費無太大區別。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>未來行動支付之可能性與發展方向</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>行動支付所可能應用的範疇極為多元，像是近年來不斷嘗試的無人商店、自駕車、虛擬醫療照護(患者在透過行動支付後，與指定之醫生於手機與患者互動以進行醫療診斷)，都是各個業者所預測可能發展的方向。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>根據資策會MIC的研究，未來的行動支付將朝向跨國界、跨載具、跨情境以及跨虛實此四大跨界方向發展。跨國界，為業者為擴大市場版圖，藉此增加行動支付的使用次數，進而達到獲利更多之效果；跨載具，目前的行動支付皆是透過手機完成支付，而跨載具便是希望其他裝置，例如戒指、手錶等，亦能達到支付功能；跨情境，隨著行動支付的功能逐漸增加，業者為與其他業者做出區別，不斷增進行動支付所能解決之情境，像是捐款、繳費提款、借貸等；跨虛實，例如原先從僅支援實體行動支付與App付款，擴增到能支援網頁內支付或是線上網購付款。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>普匯觀點</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>行動支付如今對於大眾而言是不可或缺的支付方式，它已滲透入生活中的各個角落，因此我們期待未來行動支付的任何可能性，也不排斥與之合作。結合時代趨勢，走在金融科技的前端，並提供大眾最便利且舒適的服務，是我們公司一致的目標。</p>\n<!-- /wp:paragraph -->', '2020-08-19 17:38:27', '【普匯小學堂】行動支付時代的來臨', 'article', 'publish', '2021-02-17 08:58:15', NULL, 0),
(8103, 1, '/upload/2020/08/pexels-mentatdgt-1186886-1-300x200.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<blockquote>\n<p><strong>誰是Z世代？</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>廣泛來說，Z世代（Centennials）為於1997年至2005年間出生的消費者，擁有全球人口77億人中高達35%的占比。而1997年是Google註冊網域（Domain name）的一年，被視為人類發展上的轉折，開啟了數位原住民（Digital native）的時代。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>然而，很長一段時間以來，千禧世代（Millennials，1981年至1996年出生）消費者一直被視為現代企業主流的目標消費族群，而Z世代則因為其年紀及經濟能力因素被低估購買力。隨著Z世代逐漸接近成年之際，其全球消費能力預估高達1430億美元，對於供應方，現在正是成熟的時機轉移目標。</p>\n</blockquote>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>Z世代的中心特性</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>作為數位原住民，Z世代一出生即接受到網際網路普及化，他們不知道沒有網路的世界是什麼樣子，手機及各類電子產品不離手，因此數位顧客體驗成為了該世代消費者的重要考量，也是各大品牌巨頭的看點。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>有別於數位移民（Digital immigrant）的時代，Z世代在選擇商品及購物時更容易受到身邊的朋友及同事的影響而非傳統的廣告。因此，「個人意見」在社群上足以發揮極大的影響力。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>而Z世代最獨樹一幟的特性則是「開放」、「自由」與「接納多元」。認為每個人都是不同的，並且意見不同也應該表達出來。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>Z世代如何創造金融服務新商機</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>大部分的Z世代消費者正處於尚未開始使用金融服務的年紀，也意味著這一族群尚未被既有的傳統金融服務框架住。開放的思維讓Z世代對於各式各樣創新的金融服務有著很高的接納度，使得金融科技能夠展露頭角。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>根據BII（Business insider intellegence）調查，超過50%的Z世代每個月都會使用電子錢包，高達75%的人同時會使用電子支付及P2P（Peer to Peer Lending）應用程式。由此可知，該族群對於新型態的金融服務已經開始大幅度使用。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>金融服務如何與Z世代產生連結</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>對於Z世代消費者而言，他們期望金融服務的產品能夠富有「社交性」、「教育性」、「可靠性」以及適合「數位原住民」的四大特性。在數位轉型上，企業必須對於趨勢擁有良好的洞察力以及反應能力。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>如何迎合年輕一代的偏好成為了重要的課題，不論在行銷、產品策略或研發上都圍繞著是否足夠吸引該族群？是否符合他們的需求？Z世代所重視的特性都是企業發展產品上不可或缺的考量。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>Z世代的成年商機給予了金融科技良好的溫床，談到數位轉型時，金融產業都會被視為佔有一席的領導者，有賴於許多破壞式創新的團隊興起，同時也開啟了激烈的戰場，因此對於Z世代消費者，各家企業都必須發揮敏銳的洞察以及迅速的應對。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph -->', '2020-08-27 17:07:11', '【普匯小學堂】Z世代如何改變金融服務？', 'article', 'publish', '2021-02-17 08:58:06', NULL, 0),
(8117, 1, '/upload/2020/09/0908-300x207.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<blockquote>\n<p><strong>背景</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>在這個科技日新月異的時代，只要輸入關鍵字，便能於網路上找到大量的相關資訊，雖然這樣的資料暴露或許帶來了便利，但也使的人們擔憂自身的訊息無意間被洩漏。而金融科技業既為新興產業，為民眾帶來更多工作機會，又為以科技為重的產業，其中不乏數據化、人工智慧等的應用，因此亟需一項針對這樣的新興產業的法規，因時制宜，而非以舊制度規範之。</p>\n</blockquote>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>臺灣應對方法</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>金融監督管理委員會(金管會)於今年八月六日發布了「金融資安行動方案」，目的是為了加強金融業的資訊安全防護能力，以達到各企業安全且營運不被中斷。而此方案將以四年為期，分階段推行，每半年檢討成果，並隨當前資安發展趨勢做出調整。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>目前金融資安行動方案有五種推動做法:公私協力(政府&amp;各單位/公會)、國際合作(結合其他國家資安組織，因應駭客攻擊)、差異化管理(依業別、規模給予不同資安要求)、資源共享(建立情資分享、事件應變及監控體系)與激勵誘因(給予降低成本的誘因)。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>此外，還有四大策略因應之:強化資安監理、深化資安治理、精實金融韌性、發揮資安聯防。</p>\n<!-- /wp:paragraph --><!-- wp:table -->\n\n<table>\n	<tbody>\n		<tr>\n			<td>&nbsp;</td>\n			<td>強化資安監理</td>\n			<td>深化資安治理</td>\n			<td>精實金融韌性</td>\n			<td>發揮資安聯防</td>\n		</tr>\n		<tr>\n			<td>措施</td>\n			<td>- 型塑重視資安的組織文化<br />\n			- 完備資安規範<br />\n			- 強化資安監理職能</td>\n			<td>- 加強資安管理<br />\n			- 強化資安監控<br />\n			- 加強資安人才培育</td>\n			<td>- 增進營運持續管理量能<br />\n			- 加強資安演練<br />\n			- 建構資料保全避風港</td>\n			<td>- 資安情資分享與合作<br />\n			- 建立金融資安事件應變體系<br />\n			- 建立金融資安事件監控體系</td>\n		</tr>\n	</tbody>\n</table>\n<!-- /wp:table --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>普匯身為金融科技產業的一員，我們同樣重視用戶的個資安全，除了為確保用戶的信用而顯現出的基本訊息，我們絕不會透露用戶其他之私人信息。因此，對於金管會新推出的金融資安行動方案，我們將盡全力配合，以保護用戶個資安全，提供大眾值得信任的借貸服務。</p>\n<!-- /wp:paragraph -->', '2020-09-08 18:59:15', '【普匯小學堂】金融資安行動方案', 'article', 'publish', '2021-02-17 08:57:54', NULL, 0),
(8134, 1, './upload/article/上海-最新消息.jpg', NULL, '<p>在這個分秒必爭的時代，時間完全不容浪費，就連打開一個網頁，等待頁面只要多轉幾圈，就讓人心情煩躁。</p>\n\n<p>&nbsp;&nbsp;&nbsp; 試想一個情境: 某天你好不容易請一天假前往臨櫃開戶， 一進銀行人山人海，從抽號碼牌開始等待，好不容易等到你，繳交證件加一大堆文件簽屬搞得你頭昏眼花，辦完後抬頭一看，天色竟然暗了，只能帶著滿肚子怨氣離開</p>\n\n<p>這時心裡是否有一個聲音:「如果開戶時間能縮短那該有多好!!! 如果開戶地點更多元那該有多棒!!!」</p>\n\n<p>而數位帳戶也因此油然而生</p>\n\n<p>現在普匯正與上海商銀攜手合作推出金融服務。只要3步驟，立即完成<strong><a href=\"https://www.scsb.com.tw/content/cloudbank/index.html\">上海商銀Cloud Bank線上開戶</a></strong>，馬上體驗數位開戶的極速快感！</p>\n\n<h2><strong>【上海商銀Cloud Bank數位帳戶】六大優惠</strong></h2>\n\n<p><img alt=\"上海商銀\" src=\"upload/article/上海-六大優惠介紹.jpg\" style=\"height:419px; width:800px\" /></p>\n\n<p><strong>上開備註之詳細說明或活動相關限制，悉依</strong><strong>上海商銀</strong><strong>官網為準</strong></p>\n\n<h2>上海線上開戶的好處有哪些，小編帶你一探究竟</h2>\n\n<h3><strong>數位帳戶是什麼</strong></h3>\n\n<p>&nbsp;&nbsp;&nbsp; 數位帳戶顧名思義是因應網路世代所推出的帳戶服務，使用者能在線上利用短短幾步驟開戶，完全不用跑銀行，十分方便。開戶後即擁有數位帳戶，而消費者可以透過數位帳戶App查詢交易資料，或是管理信用卡、消費明細，大大縮減原有花費時間，數位帳戶還有另一個特點是沒有實體存摺，為地球盡一份環保心力。</p>\n\n<p>&nbsp;&nbsp;&nbsp; 另外，相信很多人都很想進行理財，而如何做出正確的理財策略，了解自己的財務狀況與對財務的認知相當重要，如果有一個帳戶能即時看到財務收支狀況、消費分析與總資產變化，且能靈活運用功能，是不是能加深你的理財觀念呢?</p>\n\n<p>&nbsp; &nbsp;&nbsp;數位帳戶正有此優勢，大多數人在意的財務運用功能，像是儲蓄、外匯、信用卡、投資、保險、貸款都被數位帳戶囊括其中。</p>\n\n<h3><strong>一戶在手，好處多多</strong></h3>\n\n<p><strong>活存利息高乎你的想像</strong></p>\n\n<p>&nbsp;&nbsp;&nbsp; 目前台灣銀行業的的台幣活儲利息大多介於0.15%-0.2%之間，而數位帳戶主打超高活存利息，以上海商業儲蓄銀行的Cloud Bank為例，其活存利息高達1.2%，足足是一般活儲的6倍，更罕見推出高額度50萬內，在目前數位帳戶的激烈市場中殺出一片天。</p>\n\n<p><strong>跨行提款/轉帳優惠次數多更多</strong></p>\n\n<p>&nbsp;&nbsp;&nbsp; 愈方便的生活自然愈受到大眾喜愛，目前台灣銀行業呈現百家爭鳴型態，每人手中有2、3張卡不足為奇，而這時如果有跨行轉帳免手續費，勢必完全打中目前消費者的需求，為用戶的一大福音。此一優惠由合作金庫銀行及國泰世華銀行所推出的「KOKO」優惠次數最高，而上海商銀雖沒有提供海量手續費優惠，其旗下的Cloud Bank為每月免費轉帳10次，但也足夠應付日常所需。</p>\n\n<p><strong>業務項目多元</strong></p>\n\n<p>&nbsp;&nbsp;&nbsp; 數位帳戶擁有儲蓄、外匯、信用卡、投資、保險、貸款等業務項目，完全不亞於傳統帳戶。</p>\n\n<p>&nbsp;&nbsp;&nbsp; 從數位帳戶App中，可清楚得知自身存款金額多少錢、收取利息多少錢，日常支出明細，或進行小額投資，甚至是其他項目，都能從數位帳戶中獲取，非常方便外，更能建立起自身的理財知識。</p>\n\n<h3><strong>普匯觀點</strong></h3>\n\n<p>數位帳戶十分方便，其使用率也逐漸升高，可見其熱門程度。普匯金融科技與上海商銀為台灣最具潛力金融科技明日之星與台灣最資深民營銀行，攜手合作共同為年輕人帶來更快速、更有效率的金融服務！</p>', '2020-09-11 14:33:56', '金融科技創新就是要這樣! 上海商銀聯手普匯金融，開啟台灣Fintech嶄新的一頁', 'article', 'publish', '2021-01-27 01:57:25', NULL, 0),
(8177, 1, '/upload/2020/10/Picture1-300x145.png', NULL, '<p>&nbsp;</p>\n<!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>淺談超主權數字貨幣Libra和數字人民幣DCEP的區別。超主權數字貨幣Libra vs. 數字人民幣DCEP vs. 台灣數位貨幣CBDC</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>&nbsp;如果說Libra是社交網路巨頭聯盟背書的數位貨幣，DCEP就能形容為電子化版本的人民幣。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>&nbsp;Libra由擁有27億全球用戶的臉書所引領，市場廣大屬於無國界貨幣，因此為了規避匯率波動的風險，Libra將一籃子的貨幣作為資產儲備基礎，以避免因某國匯率的波動而引起Libra的劇烈波動。而在Libra的底層技術中使用了區塊鏈的技術，更使用了智能合約語言來幫助金融應用。但Libra更偏向於私人商業化，倘若能被廣泛應用於未來，手續費將會成為很大部份的營收，且因監管問題沉重以及造成許多爭議，Libra的發行尚未明確。 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 而DCEP被形容為電子化版本的紙幣就是基於其直接將人民幣作為資產儲備的基礎，因其具有法償性，使用電子支付的地方就需要接受，和Libra不同的是DCEP主要的客群還是限於國內。雖說有使用到區塊鏈的應用，但其實只是抓取其精華，應用於P2P的轉帳功能。且其異於Libra較為商業化，DCEP推動的目的就是在於減少紙鈔和硬幣的使用量，DCEP具低成本、無限分割和隱私保護的優點。在推動方面也較Libra更為順利 。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"align\":\"center\",\"textColor\":\"pale-pink\"} --></p>\n\n<p>下表為Libra和DCEP的比較</p>\n\n<p><!-- /wp:paragraph --><!-- wp:table --></p>\n\n<table>\n	<tbody>\n		<tr>\n			<td>&nbsp;</td>\n			<td>Libra</td>\n			<td>DCEP</td>\n		</tr>\n		<tr>\n			<td>傳遞方式</td>\n			<td>區塊鏈</td>\n		</tr>\n		<tr>\n			<td>貨幣性質</td>\n			<td>超主權</td>\n			<td>主權</td>\n		</tr>\n		<tr>\n			<td>信用基礎</td>\n			<td>商業信用</td>\n			<td>國家信用</td>\n		</tr>\n		<tr>\n			<td>法償性</td>\n			<td>無</td>\n			<td>有</td>\n		</tr>\n		<tr>\n			<td>監管體系</td>\n			<td>尚未完善</td>\n			<td>成熟</td>\n		</tr>\n		<tr>\n			<td>應用範圍</td>\n			<td>無國界</td>\n			<td>國內</td>\n		</tr>\n		<tr>\n			<td>網路需求</td>\n			<td>需要</td>\n			<td>雙離線支付</td>\n		</tr>\n		<tr>\n			<td>資產儲備基礎</td>\n			<td>一籃子貨幣</td>\n			<td>人民幣</td>\n		</tr>\n		<tr>\n			<td>匯率波動</td>\n			<td>較大</td>\n			<td>較小</td>\n		</tr>\n	</tbody>\n</table>\n\n<p><!-- /wp:table --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>各國數位貨幣的持續發展，台灣CBDC也將加緊腳步。</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>CBDC即為央行數位貨幣，可分成通用型和批發型兩類，通用型為零售的支付工具，使用對象不限制，而批發行則是大額的交易工具。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:list --></p>\n\n<ul>\n	<li>通用型 CBDC：</li>\n</ul>\n\n<p><!-- /wp:list --><!-- wp:paragraph --></p>\n\n<p>主要提供使用者更加即時且安全的支付方式，且其可以分散風險，為民眾支付的新管道。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:list --></p>\n\n<ul>\n	<li>批發型 CBDC：</li>\n</ul>\n\n<p><!-- /wp:list --><!-- wp:paragraph --></p>\n\n<p>能夠提升交易效率和安全保障，故能進行清算的支付，因此不需要通過銀行進行交易，故能降低流動性風險，並能幫助央行監管金融交易。但較通用型CBDC來說批發型CBDC技術較不成熟。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>而台灣央行所發行的數位貨幣CBDC預計在2020年進入「通用型CBDC」概念的驗證，並於同年上半年度完成第一階段「批發型 CBDC」可行性的技術研究。因此，台灣央行將參考各國於CBDC研究的經驗，採用雙層式運作架構建置驗證平台，但目前台灣仍停留於理論階段，因CBDC本身的驗證結果亦於實驗階段上停擺，中國央行的DCEP也因此成為各國參考的樣本，但不同於DCEP發展，台灣因印鈔和運輸成本較低，CBDC較無急迫性，故發展前途較不明朗。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>數位支付貨幣的未來發展？</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>在全球疫情的推波助瀾下，數位支付貨幣成為了各國熱烈討論的議題，但雖支付型態隨著時代逐漸轉變，民眾仍十分注重貨幣的信任度和安全度，因此由央行所發行的DCEP和台灣CBDC較能被民眾信任，Libra相對不容易實現，因此Libra可能會先從監管較寬鬆的國家發展。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>在全球疫情的推波助瀾下，數位支付貨幣成為了各國熱烈討論的議題，但雖支付型態隨著時代逐漸轉變，民眾仍十分注重貨幣的信任度和安全度，因此由央行所發行的DCEP和台灣CBDC較能被民眾信任，Libra相對不容易實現，因此Libra可能會先從監管較寬鬆的國家發展。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>而同樣是由央行所發行，因台灣央行對於虛貨幣態度較為保守，且無發行CBDC的急迫性，因此台灣CBDC的發展又較DCEP不順利。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<p><strong>普匯觀點</strong></p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>數位時代的來臨，電子支付亦愈趨盛行，例如在台灣 LINE PAY和其他電子支付都很方便，因此不應再以較無急迫性為由，否則將跟不上金融科技發展的腳步，且中國DCEP推動迅速，台灣也應利用我國優化的電子支付基礎以及發展迅速的零售支付系統，以跟上數位時代的腳步。</p>\n\n<p><!-- /wp:paragraph --><!-- wp:paragraph --></p>\n\n<p>除了虛擬貨幣、數位貨幣的發展，金融服務的數位化也是當務之急。金管會亦於今年提出金融科技發展路徑圖，推動普惠、創新、永續等發展目標，而普匯也隨著路徑圖的發展，提供安全隱私又方便的P2P借貸服務。&nbsp;</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p>&nbsp;</p>', '2020-10-12 17:59:00', '【普匯小學堂】超主權數字貨幣Libra vs. 數字人民幣DCEP vs. 台灣數位貨幣CBDC', 'article', 'publish', '2021-02-17 08:57:44', NULL, 0),
(8185, 1, '/upload/2020/10/pexels-chris-schippers-427679-300x200.jpg', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p>&nbsp;</p>\n\n<p><strong>新冠肺炎如何影響銀行通路？</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>2019年底爆發的新冠肺炎，使得人心惶惶，口罩不離身以及足不出戶的情況比比皆是，對於各大產業皆產生莫大的影響。以英國的銀行產業為例，疫情加速了該產業趨勢的改變，實體分行紛紛進入暫時關閉的狀態，顧客對於現金的需求大幅降低，使得銀行業者重新思考在通路佈局的投資比重。而一般銀行業者所涉及的通路囊括實體分行、ATM及客服中心等等的實體佈局，近年來大勢興起的數位通路則是包含行動銀行及數位金融。</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>分支滲透策略不再受用？</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>傳統銀行在通路佈局上多以分支滲透（Branch Penetration）的策略進入市場，透過成立多家分行在市場上盡可能的去觸及消費者，達到快速擴散的目標。而在疫情期間，許多的實體分行宣布進入暫時關閉的狀態，間接導致顧客長期以來對於實體通路的需求，開始轉移至數位通路上，這樣的趨勢使得各家銀行重新思考分支滲透策略，逐漸將實體通路擴散的腳步放慢，減少在營運上的成本。</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>網路銀行的崛起震撼產業</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>近年來，網路銀行（Neobank）的興起迫使現有市場上的銀行業者開始步上數位佈局的道路，紛紛推出自己的數位產品，而疫情的爆發更是加劇顧客對於網路服務的依賴，使得手機裝置成為銀行提供金融服務的焦點，打開銀行產業的新戰場。英國，作為一個正步入無現金社會的國家，數位通路的興起壓縮了實體ATM的生存空間，也導致部分顧客認為這是對現金使用者不友善的情況。</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>客服中心短期內仍不會被取代</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>值得令人注意的是，雖然在疫情的肆虐及顧客偏好改變的情況下，許多實體通路遭受衝擊，實體的客服中心（Call Center）卻不減其重要性，仍然會持續運作，原因為大部分業者認為客服中心能夠針對習慣取得實體協助的顧客提供更好的金融服務。而客服中心將成為在疫情期間顧客與銀行間的第一前線，成為了金融服務要求的繁忙重地。</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>普匯觀點</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>「危機就是轉機」疫情的爆發使得人類面臨空前的危機，各大產業遭受衝擊陷入一片愁雲慘霧，卻可以看到在疫情的衝擊下，加速了部分產業的轉型，迫使銀行打造更方便完善的服務來因應，數位通路的開發，不僅提高銀行的服務效率，同時也滿足顧客的需求，可以說是在危機中的一線生機。</strong></p>\n<!-- /wp:paragraph -->', '2020-10-27 12:12:39', '【普匯小學堂】新冠肺炎如何影響銀行通路-以英國為例', 'article', 'publish', '2021-02-17 08:57:37', NULL, 0);
INSERT INTO `knowledge_article` (`ID`, `post_author`, `media_link`, `video_link`, `post_content`, `post_date`, `post_title`, `type`, `status`, `post_modified`, `category`, `order`) VALUES
(8215, 1, '/upload/2020/11/1110-300x168.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p><strong>開放金融DeFi</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>DeFi是decentralized finance的縮寫，以智能合約平臺為基礎架構的加密資產，屬於金融領域的智能協議，且其合約具組合性，因此DeFi又可被稱為貨幣樂高，但DeFi尚未完全的去中心化，僅屬於開放金融。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>而在傳統金融體系中，所有服務由政府與銀行主導，從中抽取手續費和管理費，但隨著區塊鏈技術的發展，不可竄改和加解密技術的生成，逐漸轉變為去中心化的金融系統，DeFi因此誕生。藉由區塊鏈技術，DeFi目標建構透明的金融體系，所有人不須經過第三方許可就能完成金融需求。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>DeFi與傳統金融的相互關係</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>DeFi和傳統金融相異的是，DeFi能通過資金池進行快速兌換，且因其將債權代幣化並流通，故能即時調整借貸利率，不受傳統借貸期限的框架，進而加速了資產的流通。且DeFi利用區塊鏈上的智能合約，省去中間流程，用戶能享有透明化的金融服務，並獲得更好的借貸利率。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 而雖說DeFi免去了傳統銀行的破產風險，但仍面臨加密貨幣可能在短時間劇烈下跌的問題，並有可能遭受駭客攻擊的疑慮。因此DeFi目前僅為部分人的需求。不過若能將DeFi與傳統金融的優點相互融合，保有傳統金融的資產規模以及DeFi的流動性，將能快速擴展於金融體系中。而USDC及為一個案例，它將傳統金融的貨幣帶入DeFi中，可以視為一種穩定幣，並透過DeFi的透明化和高流動最大化在金融體系的效益。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>DeFi與智能合約平臺 </strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>DeFi目前大部分項目皆為以乙太坊公鏈為基礎建構的，因比特幣雖市值高，但無法達到乙太坊的靈活度，因此不能建構過於複雜的智能合約。相對比特幣，乙太坊具有高度安全性、底層資產的價值，因此能做為建構基礎。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>而DeFi的運作規則中，流動性挖礦以及收益農場為核心概念。DeFi因無信用問題，因此需要使用代幣當資產以作為借貸擔保。為解決投資者對代幣價值的疑慮，當參與者使用代幣時即可獲得小額的代幣回饋，就是流動性挖礦的概念。而收益農場的概念即為藉由借錢所得之流動性獎勵和利息，將之放款再次獲得流動性獎勵與利息，並持續投入另一個資金池的循環，並從中獲利。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>DeFi的使用參與</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>DeFi為開放金融，任何人無須經過第三方許可皆能使用，而目前主要提供借貸、交易、支付以及期貨相關金融服務，但用戶通常為擁有Dai、USDC、ETH 等資產，因此對於沒有使用過加密貨幣系統的大部分民眾將成為一道難題。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p><strong>普匯觀點</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>雖然在科技快速變化的時代中，區塊鏈的應用和加密貨幣的盛行是必須跟進的技術，但目前對於一般民眾而言，加入門檻仍然偏高，因此應適時地與傳統金融結合，加入其資產規模的優勢，優化使用者體驗、簡化操作流程，並保有其去中心化以及透明的金融體系，金融科技於國內將愈趨盛行。</p>\n<!-- /wp:paragraph -->', '2020-11-10 15:23:43', '【普匯小學堂】加密重大突破-貨幣樂高DeFi', 'article', 'publish', '2021-02-17 08:57:29', NULL, 0),
(8222, 1, '/upload/2020/11/Picture1-300x122.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p>&nbsp;</p>\n\n<p><strong>區塊鏈的基礎架構區塊鏈的基礎架構</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>預言機為區塊鏈基礎架構之一，也是Chainlink的工作原理。預言機的產生在於區塊鏈並無主動獲取資訊的能力，導致數據缺失進而造成智能合約應用範圍遭受限制。其為建構在電腦上作為解答特定問題集合的計算機，能將建立成功的問題集合轉譯成目標的答案，因此可以被認作智能合約提供外部數據來源的系統，連結智能合約與區塊鏈外部的中間橋樑，替區塊鏈上的智能合約提供數據資訊。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>去中心化的預言機</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n\n<p>而Chainlink可以解釋為一個去中心化的預言機項目，是為解決時間差、程式錯誤、意外停機時，產生漏洞導致智能合約失效、資料庫被攻擊，最終資產受損的狀況所產生，它的作用是用最安全的方式提供區塊鏈來自外部產生的數據，且Chainlink的預言機需要Link Token的轉帳以觸發，進而產生經濟效益並建立循環系統，既能保障數據的完整又能維持去中心化，解決系統單點失效的問題，維持區塊鏈和真實世界的數據真實性。</p>\n\n<p>Chainlink的功能亦可說是「智能合約的智能合約」，先由多個預言機從外界多方獲取資料後，上傳至區塊鏈，藉由Chainlink在區塊鏈上的智能合約整合，再進行最後處理，將結果交還數據需求者。簡而言之其原理就是先將數據上鏈整合後，再發還數據需求者。如此方式具有以下優點，能維護Chainlink節點的系統，藉由區塊鏈不可竄改的特性保存各預言機連結後的數據，且二次過濾篩選的程序增強了數據的可行性。</p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>預言機主要應用場景-DeFi</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>在區塊鏈的領域，只要與鏈下互動都需要預言機，例如借貸平台、保險、衍生性金融商品交易平台，而其中最主要的應用場景即為DeFi。因乙太幣的價格隨時都在波動，導致各個節點從API回報的價格不同，進而造成系統混亂，且DeFi去中心化的特性，它使用的數據需從外部提供，故DeFi需要預言機的使用，Chainlink即可說是預言機替DeFi進行鏈上鏈下傳輸數據的樞紐。而Link Token為Chainlink系統中的獎勵機制，給予數據驗證的節點發行代幣作為獎勵，且智能合約的使用者會補償使用Link Token的數據提供者。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>普匯觀點</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>過往因外部資料無法順利傳遞至區塊鏈，因鮮少公司會使用公鏈，但隨著私有鏈以及聯盟鏈的興起，大幅優化了區塊鏈的效率，因此消除企業或人民對於資訊安全的隱憂，區塊鏈的應用更加廣泛，而Chainlink亦將跟隨這股區塊鏈浪潮遍布於我們的日常生活中。例如2020年7月，LINK資產上漲了超 400％，市值排名甚至超越 EOS，綜觀上述，可以發現Chainlink的影響力將不容小覷。</p>\n<!-- /wp:paragraph -->', '2020-11-19 11:06:52', '【普匯小學堂】鏈結智能合約與生活-預言機Chainlink', 'article', 'publish', '2021-02-17 08:57:22', NULL, 0),
(8230, 1, '/upload/2020/12/Screen-Shot-2020-12-01-at-3.22.27-PM-300x170.png', NULL, '<p><!-- wp:paragraph {\"textColor\":\"vivid-red\",\"fontSize\":\"medium\"} --></p>\n\n<blockquote>\n<h2><strong>前言</strong></h2>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p><strong>在新冠疫情的影響之下，許多上班族在工作不順遂之餘，會萌生想自己出來當老闆的念頭。但創業絕非一件容易的事，除了找到產業方向之外，更重要的是如何跨過資金障礙。具備完整良好的資金籌組模式，有利於資金流轉、創造商機並提升企業產值。目前台灣政府有推出許多補助創業家的獎勵方案，本篇文章將整理於台灣創業之相關補助，並介紹新創團隊資金借貸的選擇。</strong></p>\n</blockquote>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<h2><strong>政府補助計畫</strong></h2>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>｜台北市產業局 &mdash; 產業奬勵補助</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>台北市產業局提供給新創團隊許多創業補助，積極扶植新創企業，只要貴團隊公司已完成登記設立、公司登記在台北市且成立時間在一年內，即可以申請創業補助。 惟申請程序較為繁雜，須通過以下各項審核程序始能成功領取補助。目前通過率大約為34%，若有意申請者，需備齊計畫書及各項文件，並落實團隊自我檢核表各項檢核基準，才能增加審議委員會審核通過的機會！</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:list --></p>\n\n<ul>\n	<li>詳細會計科目審核準則請參見產業獎勵補助計畫官方網站： <a href=\"https://www.industry-incentive.taipei\">https://www.industry-incentive.taipei</a></li>\n</ul>\n\n<p><!-- /wp:list --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>｜Startup@Taipei 創業台北 &mdash; 創業服務輔導</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>創業台北提供創業門診的服務，協助創業者審核相關資料，無論你有任何天馬行空的業務範疇，都可以與創業台北的窗口接洽，協助相關審核資料的籌備，增加通過台北市產業局各項補助準則的機率。此外，創業台北也有提供創業團隊輔導計畫，整合中央與地方資源，提供業師輔導、募資媒合、國際鏈結等資源，以持續性創業輔導機制，協助臺北市創業團隊改善新創營運體質。&nbsp;</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:list --></p>\n\n<ul>\n	<li>官方網站：<a href=\"https://startup.taipei/index.php\">https://startup.taipei/index.php</a></li>\n</ul>\n\n<p><!-- /wp:list --></p>\n\n<p><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<h2><strong>創業資金借貸</strong></h2>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>｜臺北市融資貸款申辦網（Financing &amp; Loan in Taipei）</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>臺北市政府為協助臺北市青年創業，提供20-45歲市民創業者申請，最高為新臺幣200萬元，曾參加經產業局認可之獲獎獎項申請人，其額度最高300萬元。此服務提供年輕人創業導入時期相關融資服務。申請條件為設籍本市一年以上，且年齡為二十歲以上四十五歲以下之中華民國國民。貸款應由申請人檢具相關書表文件後向產業局提出申請，受理申請後將由市府委派專人，偕同顧問針對營業現況、設備情形、產品狀況等進行貸款前現場訪視。</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:list --></p>\n\n<ul>\n	<li>官方網站： <a href=\"http://www.easyloan.taipei/index.php\">http://www.easyloan.taipei/index.php</a></li>\n</ul>\n\n<p><!-- /wp:list --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>｜勞動部發展署 &mdash; 微型創業鳳凰（微型創業協助計畫）</p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>鳳凰創業貸款為勞動部貸款方案，主要幫助資源相對少的弱勢族群，如婦女、離島居民以及45歲以上的中高年齡層。除了協助資金借貸，此計畫亦提供創業陪伴輔導（包括指派創業顧問、辦理創業諮詢座談、經營管理系列課程）、創業經營管理培訓（包括創業入門班、進階班及創業精進班）及相關配套措施（成立創業同儕成長互助社群，協助建置商品行銷網路）。因應疫情影響，各項規定及借貸利息都有放寬，是申請門檻較低、CP值極高的政府貸款方案，若有心想創業且符合相關條件的創業家可以優先考慮微型創業鳳凰借貸方案。</p>\n\n<p>&nbsp;</p>\n\n<ul>\n	<li>官方網站：<a href=\"https://beboss.wda.gov.tw/\">https://beboss.wda.gov.tw/</a></li>\n</ul>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} --></p>\n\n<h2><strong>普匯觀點</strong></h2>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p><strong>政府補助方案可以幫助年輕創業者紓緩經濟壓力，但並不能夠協助創業者跨越資金障礙。在緊急需要資金周轉時，既有之借貸平台需要繁複且漫長的審核程序，無法提供臨時急迫性的協助。普匯金融科技提供快速的借貸服務平台，隨到隨審，透過AI技術自動化審核，最快一個小時內能夠完成資金媒合，讓您能夠迅速拿到款項，不耽誤您圓夢的時間。結合普匯借貸系統以及政府補助資源，規劃完善的資金籌措計畫，能夠有效的確保創業初期有充沛的資金，落實創業理念並創造商機。</strong></p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<ul>\n	<li>參考資料：</li>\n</ul>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>台北市政府產業發展局 <a href=\"https://www.doed.gov.taipei/News.aspx?n=CC12E50F84DFBA69&amp;sms=DEC29AD21CA09561\">https://www.doed.gov.taipei/News.aspx?n=CC12E50F84DFBA69&amp;sms=DEC29AD21CA09561</a>新南向政策 <a href=\"https://newsouthboundpolicy.trade.gov.tw\">https://newsouthboundpolicy.trade.gov.tw</a></p>\n\n<p><!-- /wp:paragraph --></p>\n\n<p><!-- wp:paragraph --></p>\n\n<p>&nbsp;</p>\n\n<p><!-- /wp:paragraph --></p>', '2020-12-01 15:22:58', '【普匯小學堂】想要一圓老闆夢？創業先從籌備資金開始', 'article', 'publish', '2021-02-17 08:57:13', NULL, 0),
(8239, 1, '/upload/2020/12/Picture1-300x265.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p>&nbsp;</p>\n\n<p><strong>PayPal跨足加密貨幣</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>支付龍頭PayPal於10/21發出重磅消息，宣布將開放加密貨幣的買賣服務，PayPal與加密貨幣公司 Paxos 合作，預計自2021年開始，消費者就可以使用比特幣等加密貨幣進行支付，而PayPal也已取得紐約州金融服務廳(NYDFS)的BitLicense 執照許可，在未來可直接在全球2600萬戶的商家使用。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>PayPal聲稱此舉可以提升加密貨幣的使用率，而加密貨幣可視為PayPal 數位錢包中的一種資金來源，用戶可使用加密貨幣進行購買、出售或持有。而PayPal會自動協助消費者將加密貨幣轉換成法定貨幣，並以PayPal所設置的當前匯率為主。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>日前，PayPal已開放大部分用戶使用，且不收取手續費，消息一出，比特幣水漲船高，外界也一致看好，PayPal也將成為全球最大的線上支援加密貨幣的公司，站穩加密貨幣先驅位置。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>加密貨幣風險令人卻步</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>PayPal此舉令人質疑，加密貨幣波動性大，在風險方面令人卻步，且成本也是一大問題，若要將加密貨幣作為消費者的交易工具，效益有限，以目前來看，並不足以讓人放心，而為何PayPal卻逆勢而行，毅然跨足加密貨幣領域呢?</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>PayPal此舉令人質疑，加密貨幣波動性大，在風險方面令人卻步，且成本也是一大問題，若要將加密貨幣作為消費者的交易工具，效益有限，以目前來看，並不足以讓人放心，而為何PayPal卻逆勢而行，毅然跨足加密貨幣領域呢?</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>為此，PayPal的CEO Dan Schulman表示貨幣是不可避免的轉向數位形式，而在數位形式上，可以讓支付系統在效率、速度和彈性上都有所提升，因此，領先的技術實為為加密貨幣帶來成長和茁壯。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>普匯觀點</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>PayPal強勢跨足加密貨幣領域，開啟加密貨幣的支付功能，此舉吸引許多新用戶慕名而來，也鞏固舊用戶在其心中的地位，間接提升了品牌忠誠度，PayPal也表示在未來會擴充加密貨幣的應用方式，以提供更完整的支付服務。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>但如此大膽的舉動也必然存在風險，以普羅大眾角度來看，多數人都認為加密貨幣為一種投資工具，擁有後基本上不想賣出，但PayPal的主旨是以加密貨幣作為交易工具，因此，目前問題在於一般用戶大致皆不會想花掉加密貨幣，而是想持有以賺取利潤，此矛盾還端看後續大眾反應與PayPal的企業策略。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>PayPal作為大名鼎鼎的第三方支付公司，加入加密貨幣服務後，有助於加深政府或大型企業對加密貨幣的重視，更是激勵政府正視比特幣的相關法案，普匯亦是秉持相同理念，加速大眾對金融科技的認識與了解，以提升金融服務使用效率。</p>\n<!-- /wp:paragraph -->', '2020-12-08 12:01:09', '【普匯小學堂】PayPal擁抱加密貨幣', 'article', 'publish', '2021-02-17 08:57:02', NULL, 0),
(8247, 1, '/upload/2020/12/Picture1-1-300x159.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<p>&nbsp;</p>\n\n<p><strong>四個工作揭示金融科技產業明年之發展目標</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>台灣為了真正落實金融科技的發展，2020年11月19日正式成立金融科技共創平台，整合金融圈、科技圈、金管會等單位，透過意見交流的方式，以利制定策略、推動各項措施。其中，更細分為4大執行小組，分別是能力建構組、數據治理組、監理科技組、廣宣交流組。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>此共創平台由金融總會擔任總窗口，同時，更設立秘書處，讓他們負責4大執行小組的經費、衝突之處等問題，另外，整理小組每次討論所得出的決議、未來規劃，將呈報最終結果給金管會。而共創平台及執行小組所需之業務經費將透過金融總會的金融科技發展基金予以之處理。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>各小組預計達成的目標</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>一、能力建構組</strong>：由金融研訓院擔任召集人，培養金融科技人才期望完成知識體系的完整架構，規劃出金融科技證照機制、跨產業訓練、產學合作工作坊等項目，預計明年8月底可完成。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n\n<ul>\n	<li>成員：包含了金管會、證基會、保發中心、會基會、各金融公會、金融科技創新園區等。</li>\n</ul>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n\n<ul>\n	<li>三大工作方向：</li>\n</ul>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>&nbsp;</p>\n\n<ol>\n	<li>針對金融業者、科技業者、監理人員及大眾了解相關金融科技的知識。</li>\n</ol>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>2.不同群體裡，需有不同知識能量。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>3.不同年齡，在金融科技的能力掌握尚須思考哪方便的知識須與國際接軌。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>二、數據自理組</strong>：由聯徵中心擔任召集人，預計將第二資料庫提供給金融科技業者</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>期望提供一個平台揭露金融機構與第三方服務業者的合作資訊給大眾參考，明年亦將研議訂定金融控股公司客戶的資料共享機制，減少大量重複輸入資料的時間成本，其中，更是積極推動金融行動身分識別標準化機制，提升網路金融的便利及安全性。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>(1)成員：金管會、中央存保、聯卡中心、財金資訊公司、證交所、期交所、集保公司、各金融公會等單位。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>(2)工作項目：負責收集業者對跨領域機構、市場數據的需求、籌備金融行動身分別聯盟、金融機構與第三方業者資訊揭露機制、分級、分類資料以選擇一些適合的資料給FinTech公司。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p><strong>三、監理科技組</strong>：由集保所擔任召集人</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>期望新進金融機構可應用於全新之管理機制，日前，集保已設立純網銀流動性風險數位監理機制，未來將配合純網銀開業正式上線時間，此外，亦將金融機構加入監理數位轉型，目前預計六月能夠上線票券業數位監理機制</p>\n<!-- /wp:paragraph --><!-- wp:list -->\n\n<ul>\n	<li>成員：包含金管會、中央存保、保發中心、證交所、期交所、櫃買中心、聯徵中心、各金融公會、創新園區、臺網公司等單位</li>\n	<li>工作項目：負責協助數位監理申報機制，加入監理自動化、數位化、智慧化等流程，另外，擬定法規調適最佳實務原則以及研討數位金融管理相關規範。</li>\n</ul>\n<!-- /wp:list --><!-- wp:paragraph -->\n\n<p><strong>四、廣宣組</strong>：由金融總會代表擔任召集人</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>期望能夠辦理以創新為主題之金融科技競賽亦或邀請政府單位、國際創投等跨領域的評審團出適合的隊伍前往海外發展。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>(1)成員：金管會、證交所、期交所、櫃買中心、保發中心、集保公司、金融研訓院、證基會、各金融公會等單位。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>(2)工作項目：負責推動金融科技相關競賽，面試選擇國際隊、金融科技宣導業務、連接海外資源等相關事項。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n\n<p>&nbsp;</p>\n\n<p><strong>普匯觀點：</strong></p>\n\n<p>&nbsp;</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>由上文可見金管會積極希望能實現「普惠」金融的目標，藉由與金融機構及新創團隊、校園的合作推出金融科技共創平台，發展全新的數位金融與創新監理科技。</p>\n<!-- /wp:paragraph -->', '2020-12-14 18:31:16', '【普匯小學堂】台灣啟動金融科技共創平台', 'article', 'publish', '2021-02-17 08:56:53', NULL, 0),
(8260, 1, '/upload/2020/12/Picture1-2-300x224.png', NULL, '<!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<h2><strong>簡介場景金融：開放數據是關鍵</strong></h2>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>強調數位轉型的今日，金融服務橫跨時間與空間，不侷限於「實體銀行」場域，而是能即時嵌入生活，在正確的時間與地點提供消費者所需的金融服務，融入消費者所身處的時間與場景。而要有成功的場景金融設置，背後是需要金融科技與大數據等要素所支撐。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>金融數據的開放，使得銀行得以透過API與第三方業者串連，共享金融數據。連結超商、賣場、電信業與醫療業，串接不同服務場景的數據，分析消費者的屬性與金融需求，更精確地將金融產品分級，並衡量消費者所能承擔的風險與信用評級，<strong>提供消費者「因地制宜」、「因人而異」的金融產品與服務</strong></p>\n<!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-red\"} -->\n\n<p>同時，銀行能透過過去所蒐集的數據，建立新產品與商業模式以進一步拓展新通路與開發新客戶，<strong>這將是在銀行數位戰略之下，能超前部署與搶得先機的關鍵。</strong></p>\n\n<hr /><!-- /wp:paragraph --><!-- wp:paragraph {\"textColor\":\"vivid-cyan-blue\",\"fontSize\":\"medium\"} -->\n<h2><strong>場景金融的商機案例</strong></h2>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>平安集團的企業使命：成為科技型金融百貨公司，提供「一站式」的全方位服務。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>平安集團結合金融科技與生態，聚焦「大金融資產」與「大醫療產業」兩大產業，透過提供保險、銀行與投資三大核心金融服務，將產品服務的應用場景擴展至客戶的生活中，致力於打造金融服務、醫療健康、汽車服務、房產服務與智慧城市形成五大互聯網的金融生態圈。</p>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>除了「平安好醫師」外，平安集團也針對各式使用者接觸的場景不斷開發新服務與技術，包括陸金所、平安醫保科技與金融壹帳通等提供客製化服務的平台。</p>\n\n<p><img alt=\"\" src=\"upload/article/5.png\" style=\"height:704px; width:941px\" /></p>\n<!-- /wp:paragraph --><!-- wp:image {\"id\":8261} -->\n\n<hr />\n<h2><strong>如何搶下場景金融先機？</strong></h2>\n\n<p>在bank 4.0趨勢之下，提升客戶用戶體驗已成為數位金融發展與重視的面向，同時，銀行的角色須轉換成串連消費者生活各個場景的經營者。這也意味著，<strong>銀行業者若能在開放數據資源下，規劃出越完善的客戶旅程，即可越貼進消費者的金融需求。</strong><img alt=\"\" src=\"https://www.influxfin.com/wp-content/uploads/2020/12/Picture3.png\" /></p>\n\n<p><img alt=\"\" src=\"upload/article/9.png\" style=\"height:550px; width:941px\" /></p>\n\n<hr /><!-- /wp:paragraph --><!-- wp:paragraph --><!-- /wp:paragraph --><!-- wp:image {\"id\":8262} --><!-- /wp:image --><!-- wp:paragraph {\"fontSize\":\"medium\"} -->\n<h2><strong>普匯觀點</strong></h2>\n<!-- /wp:paragraph --><!-- wp:paragraph -->\n\n<p>場景金融為銀行即服務最佳的應用，從客戶的需求出發，<strong>除了與原有的商家企業合作外，更應透過現有數據預測未來有商機之通路與服務，積極與主流外部場景、新創服務合作並搶先同業取得商機</strong>，讓消費者在尚未察覺自己需要哪些金融產品與服務時，已滲透到客戶的生活中，提供「一站式」的完整金融服務，這將成為場景金融能否長遠獲利的關鍵。</p>\n<!-- /wp:paragraph -->', '2020-12-23 11:35:08', '【普匯小學堂】跨越網域、系統與瀏覽器的場景金融，如何形塑未來的金融服務？', 'article', 'publish', '2021-02-17 08:56:41', NULL, 0),
(8262, 1, './upload/article/golden-bitcoin-mail-hands.jpg', NULL, '<blockquote>\n<p>隨著區塊鍊的發展，各國開始陸續研擬推行「數位化」法定貨幣。這些央行所推行的數位貨幣和現金一樣擁有交易功能、法定償還性質，但不再以實體的形式存在，可進行更有效透明的金流追蹤，也有利於政府直接管控、施行貨幣政策。</p>\n\n<p>2020年，美國宣布限制中企在美國掛牌上市、取消香港特殊經貿地位，聚焦金融戰的情況越發明顯。而數位貨幣象徵未來金融科技的主要交易代幣，更可能會牽動總體貿易市場，因此可能成為下一階段雙方角力的戰場。</p>\n</blockquote>\n\n<hr />\n<h2><strong>美國：美元地位可能動搖，研擬推動數位貨幣</strong></h2>\n\n<p>美元在二次大戰後，就成為國際間最重要的交易貨幣。根據國際貨幣基金組織（IMF）2020第三季統計，美元占全球主要儲備貨幣交易比重60%，歐元20.5%，而人民幣僅2%。但當未來國際間使用數位貨幣交易就能跳過美元，美元的強勢地位將可能被動搖，因此美國展開了數位美元的推行。</p>\n\n<p>2020年4月，針對疫情所推出的紓困方案，將數位美元納入草案研議範圍。初步構想是民眾可以在聯準會（FED）開立數位錢包帳戶，而FED可將新發行的數位美元直接匯入民眾帳戶，且每一筆消費紀錄皆可追蹤管理。但該構想是採用集中式管理的中心化帳本，僅為單純的「債務註記」，與未來可全球流通的數位貨幣仍有一段距離。</p>\n\n<p>而由數位美元基金會啟動的「數位美元計畫」，則在5月發布了第一份「數位美元白皮書」，說明未來將以分散式帳本技術（Distributed Ledger Technology, DLT）與雙層架構為基礎，打造全球適用與穩定的數位美元系統。DLT技術就是所有人都擁有一本完整且即時更新的帳本副本，透過同時驗證每筆交易、省去中介機構，加快貨幣流通速度與降低成本；雙層架構則沿用現有的金融機構體系，先由央行發行貨幣給商業銀行，再由商業銀行分發給一般民眾與企業使用。</p>\n\n<p>以上兩項計畫，都表明了美國對於數位貨幣的重視，一方面可遏阻不法金錢流向，一方面是為了更鞏固美元的地位，美國必須也在數位貨幣面都取得主導性，面對跨境交易、清算、貿易政策等才會握有更多的控制權。</p>\n\n<p><img alt=\"\" src=\"upload/article/6.png\" style=\"height:391px; width:952px\" /></p>\n\n<hr />\n<h2><strong>中國大陸：發行數位人民幣，致力打造亞洲的新金融系統</strong></h2>\n\n<p>據世界銀行資訊，若以購買力平價（PPP）計算，中國自2017年起已超越美國成為全球最大的經濟體，為全球主要的跨國貿易對象。但人民幣作為儲備貨幣的交易比重卻僅占2%，促使中國將投注更多心力加速人民幣國際化。</p>\n\n<p>&nbsp;</p>\n\n<p>人民銀行在2020年4月宣布在深圳、蘇州等地區及未來的2022年北京冬奧場景，對官方的數位貨幣電子支付系統進行內部封測。目前試驗發行的數位人民幣也採用了雙層架構與分散式帳本技術，但交易資料的透明性僅屬於政府。除了人們已熟知的掃碼支付、線上轉帳外，數位人民幣具備「雙離線交易」的功能，即兩人都處於無網路狀態，只要在同一個空間將手機進行近距離感應，便能進行交易，使用體驗上與現金更為接近。</p>\n\n<p>目前跨境交易所仰賴的環球銀行金融電信協會系統受美國控制，中國期待透過數位貨幣的快速發展，與俄羅斯、北韓、伊朗等聯手，串聯一帶一路及大東亞地區與中國貿易頻繁的國家，打造一個新的亞洲金融系統，藉此擺脫美國的經濟制裁與威脅。</p>\n\n<p><img alt=\"\" src=\"upload/article/7.png\" style=\"height:481px; width:685px\" /></p>\n\n<hr />\n<h2><strong>普匯觀點</strong></h2>\n\n<p>短期而言，美國在數位貨幣的推展上採取守勢，避免在數位貨幣上落後中國，並以鞏固美元優勢為目的；中國則以攻勢為主，強化與亞洲各國的經貿關係並拉攏其他國家，試圖提升人民幣的國際地位。</p>\n\n<p>長期而言，推行數位貨幣已是全球趨勢，未來政府與民眾在貨幣上的互動關係將產生改變。第一、未來政府的掌控權會提升，實施貨幣政策時央行能直接面對民眾，對貨幣的管控也能更即時與精確；第二，金流追蹤力增強，每筆交易都會被立即記錄且無法竄改，有效遏止不法金流；第三，便利性大幅提升，民眾無須持有實體現金即可交易。</p>\n\n<p>但數位貨幣需要面對資訊安全與個人隱私的兩難，政府能介入的範圍與可公開共享的領域都是需權衡的。數位貨幣的發展可能擠壓實體貨幣的使用場域，對於沒有行動裝置、難以進行身分驗證的人而言，數位化並不能帶來預期的便利性，因此「數位平權」是數位貨幣的推行上值得關注的重要議題。</p>\n\n<p>&nbsp;</p>', '2021-01-12 02:46:08', '【普匯小學堂】繼科技戰後的美中角力新戰場—數位貨幣', 'article', 'publish', '2021-02-17 08:56:33', NULL, 0),
(8264, 1, './upload/article/20210115-1.png', NULL, '<h2><strong>一、CBDC架構比較</strong></h2>\n\n<p>1.單層式：由央行直接向使用者發行，無須透過中介機構。</p>\n\n<p>此架構最大的優點即是設計上較為簡單，但同時也須面對「金融去中介化」風險。另外，由中央直接面對民眾將造成市場壟斷，導致經營策略上缺乏彈性與創新能力，最終未必能如金融中介機構設計出較為貼近使用者需求的產品。</p>\n\n<p>因此，國際間的CBDC一般而言都採用雙層式架構設計保留金融中介機構的角色。</p>\n\n<p><img alt=\"\" src=\"upload/article/20210115-2.png\" style=\"height:704px; width:941px\" /></p>\n\n<p>2.雙層式：由央行向中介機構發行，再由中介機構提供給使用者。</p>\n\n<p>此模式較符合傳統貨幣體系，仰賴中央發行，並倚靠中介機構擔任CBDC的分配者，讓大眾透過便利的方式取得現金。此模式下能避免金融去中介化風險，公私合作模式下亦能維持金融中介機構創新競爭的活力。</p>\n\n<p>雙層式CBDC已是國際間執行的共同方向，但大部分國家的CBDC皆屬於試驗階段，目前尚未有央行提出確切的發行時間。</p>\n\n<h2><img alt=\"\" src=\"upload/article/20210115-3.png\" style=\"height:704px; width:941px\" /></h2>\n\n<hr />\n<h2><strong>二、台灣中央銀行CBDC的發展</strong></h2>\n\n<p>台灣在2019年5月投入CBBC的研究，並接著成立「CBDC研究計畫專案小組」，交由其下的「CBDC工作小組」負責規劃與執行。另外，透過與學術單位協作的方式，今年6月已完成「批發型CBDC可行性技術研究」，批發型CBDC雖擁有DLT（分佈式帳本）多點同步儲存資料的優點，但在電子支付服務交易速度上仍缺乏效率，顯示批發型CBDC無法同時滿足電子支付兼顧消費者隱私與交易速度的需求。</p>\n\n<p>為了解決上述問題，央行在今年9月底開始執行「通用型CBDC（央行數位貨幣）試驗計畫」，納入國際間CBDC基本原則與核心特徵。為了達成滿足各種支付需求的目標，此階段將試驗各種CBDC的應用場景，期望更貼近消費者的使用需求。</p>\n\n<hr />\n<h2><strong>三、普匯觀點：CBDC仍有許多問題待解決</strong></h2>\n\n<p>CBDC在法律相關議題上就面臨了幾個爭議，首先，CBDC是否如現金一樣具有法償效力，任何人都不得拒絕接受。再者，以台灣發展雙層式架構的CBDC而言，在提供給民眾CBDC的過程中，涉及許多金融中介機構，央行與金融機構之間的權利義務關係該如何釐清與確立亦是重要的議題。另外，最重要的即是須確保CBDC得以保障消費者保護、資料安全及保護隱私。</p>\n\n<p>國內業者也期望能盡快推動CBDC的執行，期望能發揮「數位現金」的功能，擴大支付的流通性，另外也需考量商家在CBDC支付系統下，能否降低電子支付過程中商家收單與整合成本等問題。同時，也因CBDC錢包不需綁定信用卡、金融卡與銀行帳戶，將能大幅降低使用門檻，發揮更大的經濟效益。</p>\n\n<p>快捷支付發展在國際上算健全的台灣，也許發展CBDC並不是當務之急，但可發現社會大眾對於CBDC的發展與執行抱有一定程度的期待，期望其能徹底解決電子支付的痛點，使台灣支付市場能更符合數位經濟的未來需求。</p>', '2021-01-15 06:46:36', '【普匯小學堂】一次看懂央行數位貨幣(CBDC)單層與雙層架構異同！', 'article', 'publish', '2021-02-17 08:56:20', NULL, 0),
(8265, 1, './upload/article/20210120-1.png', NULL, '<blockquote>\n<p>瑞典在2017年春季開始著手研發中央銀行數位貨幣--電子克朗，從2020年2月也展開為期一年的試驗，<strong>期望透過試驗的場景確保Ekrona可以滿足民眾簡單、易於使用與安全性保障等需求</strong>。測試過程包含用戶在電子錢包中使用電子貨幣，並透過手機app進行付款、存款和提款，亦可藉由穿戴裝置進行付款。</p>\n</blockquote>\n\n<p>一、Ekrona（電子克朗）發展的背景：<br />\n&nbsp;</p>\n\n<p>1.瑞典使用現金的比例很低</p>\n\n<p><img alt=\"\" src=\"upload/article/20210120-2.png\" style=\"height:554px; width:899px\" /></p>\n\n<ol>\n	<li>早在2013年，瑞典官方取消了最大面值的實體鈔票，導致紙鈔使用量大幅下降，銀行等機構也開始停止對現金的交易，這也使得數位支付的使用率上升。但同時這也意味著瑞典央行漸漸喪失對貨幣發行與控制的權利，因此<strong>Riksbank期望推出法定數字貨幣E-krona拿回核心主導權。</strong></li>\n	<li>Ikea熱狗實驗：提供支付現金的顧客一份免費的熱狗。實驗顯示根據門市人員觀察，每1000位顧客中，只有大約1.2人以現金支付，顯現民眾使用現金的意願低落。然而，雖然只有1%的顧客用現金，但員工卻需要花費一天工作15%的時間，去記錄、整理以及核對現金，<strong>藉由這項實驗，IKEA希望凸顯使用現金額外產生的成本。</strong></li>\n</ol>\n\n<p>2.數位行動支付對瑞典社會而言並不是個理想的選擇</p>\n\n<p>首先，目前瑞典國內的行動支付都被少數商業銀行主導甚至是寡頭壟斷，長期而言將導致支付市場的競爭性受到侷限，亦使得支付系統面對危機時變得脆弱。再者，若以邁向無現金社會為目標，瑞典目前仍有民眾未能接受並習慣行動支付的情境，也許是受限於裝置與綁定實體帳戶等問題。對此，<strong>央行Riksbank認為未來的E-krona須設計出能讓社會大眾接受且符合未來數位經濟的支付方式。</strong></p>\n\n<hr />\n<p>二、現行的瑞典Ekrona的架構：雙層式架構設計</p>\n\n<p><strong><img alt=\"\" src=\"upload/article/20210120-3.png\" style=\"height:704px; width:941px\" /></strong></p>\n\n<hr />\n<p>三、疑慮與隱憂:無現金社會對瑞典真的好嗎？</p>\n\n<p>1.使用客群的限制</p>\n\n<p>E-krona若能順利推行，瑞典將變為無現金經濟的社會，雖然能讓部分交易成本降低提升交易效率，但須考量<strong>瑞典有將近35%的民眾為55歲以上的高齡人口</strong>。雖在疫情之下已推升高齡人口的行動支付使用率，然而，從行動支付到E-krona的使用之間仍有所差距。另外，遊客對於瑞典零售業的貢獻不可小覷，<strong>若無法使用現金支付，可能對於遊客而言極為不方便</strong>，觀光業產值對於整體經濟的貢獻將一定程度的下降。</p>\n\n<p>2.隱私問題</p>\n\n<p>無現金支付累積大量的數據，社會大眾皆難以控制自身數據的交換、使用與傳遞。因此，對於消費者而言，等同於將消費行為乃至於日常生活完全暴露給數據持有者與政府，<strong>這對於民眾的消費者隱私缺乏相對應的保障。</strong></p>\n\n<hr />\n<p>四、普匯觀點：</p>\n\n<p>瑞典央行目前也未明確說明E-krona測試結束後，是否會正式推出以及推出的時程安排等等。</p>\n\n<p>瑞典與中國發行央行數位貨幣情況類似，<strong>同樣都是期望在由少數支付公司寡頭壟斷的時代中，拿回中央銀行對於貨幣的核心控制權</strong>，以及面對民間行動支付使用便利的現況下，期望將CBDC定位成補充現金消失的缺口或替代現金的選項之一。<strong>但仍須注意， 瑞典的人口結構與中國並不相同，瑞典中央銀行仍須評估E-krona對於國內高齡人口造成的影響，並進一步納入未來的設計規劃中。</strong></p>\n\n<p>雖然瑞典相較於其他國而言，在E-krona的設計與進程上都居於領先地位，但近期瑞典央行也逐漸放慢腳步，去審慎思考E-krona對於整體社會的影響，而這也是各國在爭先恐後發行央行數位貨幣的路程上，所必經的道路。</p>', '2021-01-20 07:56:28', '【普匯小學堂】世界上最不依賴現金的國家！瑞典央行數位貨幣E-krona介紹！', 'article', 'publish', '2021-02-17 08:56:10', NULL, 0),
(8266, 1, './upload/article/20210127-1.png', NULL, '<p>一、現況簡介</p>\n\n<p><strong>金融科技創新對於傳統銀行而言，既是考驗同時也是機會</strong>。從Bank 1.0不斷進化到Bank 4.0的現今，傳統銀行持續在創新趨勢下找尋生存的方式。近幾年來Fintech與Bigtech以驚人的速度成長與發展，傳統銀行勢必更努力加緊腳步找出應對之道。</p>\n\n<p><img alt=\"\" src=\"upload/article/20210127-2.png\" style=\"height:529px; width:941px\" /></p>\n\n<p>金融科技公司資本額多半不高，在此情況下難以與傳統銀行擁有的顧客與規模相抗衡，因此，對於傳統銀行而言，最理想的經營轉變即為與金融科技公司合作互補，<strong>由傳統銀行提供龐大的客戶作為基礎，並由金融科技公司提供創新技術讓傳統銀行擁有更多發展可能。</strong></p>\n\n<hr />\n<p>二、傳統銀行可能的角色轉變方向</p>\n\n<p>傳統銀行的關鍵資源在於<strong>擁有大量的客戶資訊作為基礎</strong>，如何進行業務調整與轉型將為重要的課題。傳統銀行可依自身的強項選擇以下幾種模式之一，專精提供一種服務或是成為全能型銀行。</p>\n\n<p>&nbsp;</p>\n\n<p>1.成為產品研發與提供者</p>\n\n<p><strong>由銀行開發金融產品，委託第三方業者進行配銷服務</strong>。此對於傳統銀行而言，是一項艱鉅的任務，<strong>因須面對金融科技公司高度競爭的產品開發能力</strong>，但如同上文所述，銀行擁有大量用戶數據以及規模龐大的優勢，若能善用數據分析與運用的能力，由銀行自身開發產品將能最貼近消費者的需求。</p>\n\n<p>&nbsp;</p>\n\n<p>2.轉變為特定數位介面角色</p>\n\n<p>銀行從過去提供、配銷商品與服務的角色，轉變為<strong>專精於配銷金融商品與特定服務之平台</strong>，提供第三方業者（如金融科技公司）銷售產品的介面，以<strong>提供平台服務為主要業務，賺取平台服務費</strong>。在此模式下，銀行須有能力即時整合第三方業者所建立的生態系，並善用客戶數據資料為第三方業者打造有效的銷售管道。</p>\n\n<p>&nbsp;</p>\n\n<p>3.擔任整合入口網角色</p>\n\n<p>與擔任特定數位介面平台的角色有所不同，整合入口網為替<strong>銀行業務的供應商與其特定數位介面提供整合入口，而銀行已提供整合入口服務賺取收入，</strong>例如提供其他業者使用其支付管道、基本客戶辨識與洗錢防制檢核等等。</p>\n\n<p>&nbsp;</p>\n\n<p>4.進化成全能型銀行</p>\n\n<p>由<strong>銀行負責研發、提供以及配銷商品，簡單來說，就是成為「全能型銀行」與金融科技公司競爭</strong>。這將考驗銀行自身金融科技、大數據運用與開發能力，能否有效利用用戶的數據資料，以場景金融能力貼近客戶金融服務日常。對於銀行而言，擁有大量客戶數據是一大利基，<strong>但能否從中萃取精要的資訊</strong>，以利銀行調整產品與服務售價、制定行銷決策與提升用戶體驗等等，此為全能型銀行做出市場差異化之關鍵。</p>\n\n<p><img alt=\"\" src=\"upload/article/20210127-3.png\" style=\"height:529px; width:941px\" /></p>\n\n<hr />\n<p>三、普匯觀點</p>\n\n<p>傳統銀行與金融科技公司之間的關係並非絕對性互斥，事實上，傳統銀行在金融科技公司強勢崛起之下，仍能運用自身優勢在金融科技快速變遷的時代下生存。首先，提升用戶體驗絕對是關鍵，<strong>透過實體與虛擬銀行雙向並行</strong>，<strong>並與金融科技公司進行策略性合作</strong>，滿足消費者生活全面的金融服務需求。另外，透過原有的通路資源、品牌價值與客戶資訊，<strong>提升服務滿意度</strong>。此外，在人才訓練方面，<strong>應以金融科技相關能力訓練為方向</strong>，進一步提升公司整體的競爭力。</p>\n\n<p>同時，亦須注意在銀行轉型與角色轉變時，新的商業模式可能帶來的相關風險，銀行須及早有相應措施應對。另外，消費者隱私相關保護更是開放銀行趨勢下所須特別重視的。</p>', '2021-01-27 06:13:26', '【普匯小學堂】傳統銀行該如何轉變其角色與定位?', 'article', 'publish', '2021-02-17 08:55:58', NULL, 0),
(8267, 1, './upload/article/20210129-1.png', NULL, '<blockquote>\n<p>在科技的蓬勃發展下，金融部門卻成為網絡詐騙的熱門目標，數據顯示光是2020年7月，德國網路單月受到駭客攻擊的次數就高達100,000次，且攻擊對象涉及各種規模的公司，由下圖可知目前全球數字密碼鎖遇到的問題，可見新型態的密碼勢在必行。</p>\n</blockquote>\n\n<p>因此，金融和信貸機構採取了新型的保護措施&mdash;FIDO（Fast IDentity Online 線上快速身分驗證服務）&mdash;運用生物特徵辨識或FIDO安全金鑰，來線上驗證使用者身分，目前Windows裝置、iPhone手機、華為手機皆支援此驗證方式。</p>\n\n<p><img alt=\"\" src=\"upload/article/20210129-2.png\" style=\"height:487px; width:866px\" /></p>\n\n<hr />\n<p><strong>FIDO</strong><strong>怎麼運作：</strong></p>\n\n<p>FIDO認證模式的重點為&mdash;將身分「驗證」與「識別」拆開，因此<strong>伺服器端不須再保管密碼</strong>，並採公私鑰架構來保障安全（使用者的驗證加密後成為私鑰，以私鑰進入FIDO認證，認證通過獲得公鑰，再以公鑰解開目標應用程式）因此在FIDO伺服器上，<strong>只保存公鑰不保存密碼或私鑰</strong>、達到不存在密碼的效用。</p>\n\n<p>另外，FIDO驗證較嚴謹&mdash;當用戶移除App或手機重置時，在FIDO架構之下，需重新註冊；相較僅使用FaceID驗證的客戶仍會記憶登入資訊。</p>\n\n<p><img alt=\"\" src=\"upload/article/20210129-4.png\" style=\"height:487px; width:866px\" /></p>\n\n<hr />\n<p><strong>國際上的發展：</strong></p>\n\n<p>2015年美國銀行（Bank of America）是全球首家導入FIDO驗證的金融業者，其將FIDO驗證導入行動銀行App，提供用戶安全簡便的驗證方式。</p>\n\n<p>而以各國政府而言，FIDO目前已獲得12國政府（美國、臺灣、加拿大、香港、馬來西亞、挪威、韓國、瑞典、泰國、英國、澳洲、捷克）導入&mdash;作為新的線上身分識別技術，並應用於政府電子服務中。例如：韓國用於對民眾的稅務服務、馬來西亞規畫應用於政府、市議會、銀行、電信與交通等特定行業的身分驗證與交易簽名。</p>\n\n<p><strong>臺灣的發展：</strong></p>\n\n<p>2018年中國信託銀行成為臺灣首家導入FIDO驗證的金融業者，旗下的「Home Bank」App的用戶達360萬人，其中有75%是使用FIDO機制登入，可見用戶對此機制所帶來的便利性接收度很高。接著2020年，國泰世華銀行也導入FIDO驗證&mdash;應用於「國泰世華行動銀行」與「KOKO」App，目前已有超過120萬客戶在使用。</p>\n\n<p>金管會也預計於2021年8月完成F FIDO籌組&mdash;將銀行、證券期貨、保險等機構作規格的統一，期望能達到<strong>跨機構互通</strong>，例如：在A銀行開立數位帳戶後，在B銀行只要透過人臉辨識、指紋辨識等方式，便能直接開立數位帳戶；也希望達到客戶使用同一支手機或行動裝置，只要經過<strong>一次身分驗證，即可登入不同銀行、證券機構的帳戶</strong><strong>App</strong>，不需再依據不同機構而分別使用不同的帳號與密碼。</p>\n\n<p><strong>普匯觀點：</strong></p>\n\n<p>目前臺灣的金融機構，雖然已經有提供相較過去更多元的身分識別方法，如：自然人憑證、信用卡、存款帳戶等非簽名的方式，但通常仍會在事後要求用戶再進行一次實體身分認證、或輔以視訊資料的方式補足資訊。但透過FIDO驗證，FIDO本身的認證過程就有涵蓋生物辨識部分，因此能夠更加精簡認證程序，且延伸至跨單位的整合，可大幅縮減人力作業的時間。</p>\n\n<p>而好比普匯的反詐欺系統&mdash;持件自拍（生物辨識）與雙證件辨識，亦可作為降低風險的系統，且由於普匯是使用APP進行借貸操作，因此在資安方面也較網頁安全&mdash;因為手機的行動IP有電信綁定、容易追朔回源頭。</p>\n\n<p>因此FIDO認證不僅在時間上具有效率，更能降低資安風險（用戶每次的登入都需經過生物辨識），雖然目前較多應用在金融產業，但勢必是未來各產業作人員與用戶認證時必備的科技。</p>', '2021-01-29 07:49:56', '【普匯小學堂】數字密碼已不再安全，FIDO身份驗證將成為主流', 'article', 'publish', '2021-02-17 09:08:49', NULL, 0),
(8268, 1, './upload/article/20210205-1.png', NULL, '<blockquote>\n<p>所有的金融交易都需從KYC（Know Your Customer，認識你的客戶）開始，因此第一步為&mdash;核實客戶身分，但傳統審查流程的痛點太多，如：手動填寫資料十分耗時、上傳證件難辨是否為本人、自拍難以確認人物真實性、人工審查耗時耗力。因此提供更加快速又便利的識別方式勢在必行，現行的兩種生物辨識有FIDO認證與持證自拍：</p>\n</blockquote>\n\n<h2><strong>FIDO</strong><strong>認證：</strong></h2>\n\n<p>使用安全密鑰、臉部、指紋或語音來作為辨識，將個資進行加密處理。有關FIDO認證更詳細的內容，可至【普匯講堂】數字密碼已不再安全，FIDO身份驗證將成為主流，有更詳細的講解。</p>\n\n<h2><strong>持證自拍：</strong></h2>\n\n<p>用戶作身分認證時，需手持身分證件與臉部自拍，先採用OCR（Optical Character Recognition，光學字元辨識）&mdash;辨識靜態的文字掃描；接著再透過進階技術作臉部照片的比對分析，以利機構作「人證合一」的確認。</p>\n\n<hr />\n<p><strong>FIDO認證 vs. 持證自拍：</strong></p>\n\n<h2><strong><img alt=\"\" src=\"upload/article/20210205-2.png\" style=\"height:487px; width:866px\" />FIDO</strong><strong>認證</strong><strong>+</strong><strong>持證自拍：</strong></h2>\n\n<p>在FIDO認證與持證自拍的比較中，由於兩者是屬於<strong>不同使用階段的應用：</strong><strong>FIDO認證用於登入窗口、持證自拍用於人證一致性的辨識</strong>，因此若能搭配使用，才是未來金融產業做線上與行動服務時必備的工具。</p>\n\n<p><img alt=\"\" src=\"upload/article/20210205-3.png\" style=\"height:487px; width:866px\" /></p>\n\n<hr />\n<h2><strong>普匯觀點：</strong></h2>\n\n<p>普匯目前的線上申辦提供「持證自拍」，並透過後台的OCR的文字與影像分析&mdash;了解是否為本人申辦，且資料儲存於AWS（Amazon Web Services，亞馬遜網路服務公司），提供用戶更高的安全性。</p>\n\n<p>在臺灣目前的金融業中，只有「中國信託銀行」與「國泰世華銀行」達到「真正的FIDO認證」，而其他機構多半僅是連結Face ID（iPhone的臉部解鎖）或Touch ID（iPhone的指紋解鎖）來作為快速登入的方式，與「真正的FIDO認證」仍有差距。</p>\n\n<p>而在持證自拍方面，目前部分銀行與金融機構已有提供此服務，但大多數的機構仍僅提供「證件上傳」&mdash;即便顧客已在線上填寫所有文件，但事後仍會被要求前往臨櫃再作一次身份確認後，才能通過審查；<strong>但「持證自拍」不僅可在線上直接完成身份的辨識、且相較</strong><strong>FIDO</strong><strong>認證更能達到真正的線上真人身份辨識</strong>，例：FIDO無法同步確認用戶使用其指紋登入時，是否知情或為他人代按。因此這部分是未來金融業者仍可持續優化的部分，以利提供用戶更便捷的金融服務。</p>', '2021-02-05 08:35:00', '【普匯小學堂】都是生物辨識，FIDO認證 vs. 持證自拍', 'article', 'publish', '2021-02-17 08:55:32', NULL, 0),
(8269, 1, './upload/article/20210217-1.png', NULL, '<blockquote>\n<p>先前我們在【數字密碼已不再安全，FIDO身份驗證將成為主流】提到，FIDO認證的機制最先由美國銀行（Bank of America）導入作為APP的登入、而台灣目前有也中國信託銀行與國泰世華銀行，跟隨其腳步同樣導入<strong>作為</strong><strong>APP的登入</strong>，應用上使用FIDO認證中的<strong>UAF（通用身份驗證框架）</strong>規格，而FIDO中尚有不同的機制：</p>\n</blockquote>\n\n<p><img alt=\"\" src=\"upload/article/20210217-2.png\" style=\"height:610px; width:1085px\" /><img alt=\"\" src=\"upload/article/20210217-3.png\" style=\"height:616px; width:1095px\" /></p>\n\n<hr />\n<h2><strong>Google</strong><strong>的應用：</strong></h2>\n\n<p>Google在2011年開始提供使用<strong>U2F</strong><strong>（通用第二因素）認證</strong>，目的為：使用者在輸入密碼後，還會被要求<strong>輸入從手機收到的驗證碼</strong>，因為駭客多是在遠處作業，因此第二因素的認證可使駭客攻擊更加困難。</p>\n\n<p>另外，Google也推出Security Key來作為安全登入的USB裝置，只在是真實網站時才會運作；若用戶連上假冒的Google網站，Security Key便不會運作。而插入Security Key後，用戶無需輸入任何密碼，只需依Chrome所出現的提示按下確認即可。</p>\n\n<hr />\n<h2><strong>台灣使用</strong><strong>FIDO</strong><strong>的應用：</strong></h2>\n\n<p>除在【數字密碼已不再安全，FIDO身份驗證將成為主流】中提到，中國信託銀行與國泰世華銀行的應用外，政府日前亦啟用──Taiwan FidO（臺灣行動身分識別），應用於：房屋稅、牌照稅、綜合所得稅的繳納。而過去身分認證的方式有四種：「健保卡+註冊密碼」、「自然人憑證」、「電子憑證」、「戶口名簿戶號+查詢碼」；而後新增了Taiwan FidO方式後，國人可透過手機上的生物辨識機制來驗證身分。且除了提供更便民的服務，連政府機關內部人員使用的作業系統也導入FIDO認證，可見FIDO認證正擴及國內多項政府服務。</p>\n\n<hr />\n<h2><strong>FIDO</strong><strong>認證在台灣的困境：</strong></h2>\n\n<p>自Taiwan FidO推出後，已有萬人註冊使用，但<strong>需綁定自然人憑證，來作確認註冊人身分的依據，成為推廣上的挑戰</strong><strong>&mdash;</strong>已有自然人憑證的民眾，需使用讀卡機及自然人憑證，並輸入PIN碼進行驗證後，才能完成TW FidO的註冊；還未有自然人憑證的民眾，需先跑一趟戶政事務申請，並領取憑證IC卡後，才能接續後面的動作，也因此Taiwan FidO的<strong>實際使用率仍不高</strong>。</p>\n\n<hr />\n<h2><strong>普匯觀點：</strong></h2>\n\n<p>目前FIDO已從1.0發展至2.0，而台灣政府也開始將FIDO大量導入政府的服務中，但<strong>受限於仍需要「申請自然人憑證」才能完成註冊</strong>，時間上的效益仍不足，無法達到真正的快速認證。而相較普匯的持證自拍，透過分析證件與面部特徵，可有效的作到遠端認證，提供用戶更便利的服務。</p>\n\n<p>至於FIDO 2.0，在技術上也<strong>未有大幅度的進步，僅是將安全性提升</strong>&mdash;增加手機與電腦間的認證聯動，期望未來能夠看到<strong>更多元的</strong><strong>FIDO</strong><strong>認證方式與更開放的態度</strong><strong>&mdash;</strong><strong>政府可將要求自然人憑證，改為參考類似普匯的持證自拍方式</strong>，來做到真正的效率認證<strong>。</strong></p>', '2021-02-17 08:55:14', '【普匯小學堂】FIDO認證的延伸應用', 'article', 'publish', '2021-02-17 08:55:14', NULL, 0),
(8270, 1, './upload/article/20210223-1.png', NULL, '<blockquote>\n<p><strong>興起背景：</strong></p>\n\n<p>2008年金融海嘯後，各國紛紛推出不同的法規，使全球進入了新的監管時代；加上2010年人工智慧與Fintech（金融科技）的蓬勃發展下，大量的金融創新提供了更便捷的金融服務，但也產生許多法律問題，因而促成Regtech（Regulation Technology，監管科技、法遵科技）的服務受到重視。</p>\n</blockquote>\n\n<p>&nbsp;</p>\n\n<p><strong>Regtech的應用範圍：</strong></p>\n\n<p><img alt=\"\" src=\"upload/article/20210223-2.png\" style=\"height:487px; width:866px\" /></p>\n\n<hr />\n<p><strong>市場狀況：</strong></p>\n\n<p>自2015年以來短短四年的期間，全球RegTech的投資年增至80億美元以上，下圖為2015年至2019年間（尚未有2020年的統計資料），全球投資者在RegTechg的投資額：</p>\n\n<p><img alt=\"\" src=\"upload/article/20210223-3.png\" style=\"height:487px; width:866px\" /></p>\n\n<ul>\n	<li>自2015年至2019年，投資在RegTech的947筆交易中，就注入超過170億美元的資金。</li>\n	<li>交易量在2019年創下新紀錄&mdash;共有317項的RegTech交易，相較2018年增加了93.3％；且投資額也創下新高&mdash;達到850億美元，相較2018年成長了52.9％。</li>\n</ul>\n\n<hr />\n<p>下圖為統計自2015至2019年，投資者在RegTech價值鏈上的資金分配：</p>\n\n<p><img alt=\"\" src=\"upload/article/20210223-4.png\" style=\"height:487px; width:866px\" /></p>\n\n<ul>\n	<li>獲得投資前三大的項目分別為：「網絡安全/信息安全」、「合規性管理」與「識別/背景檢查」，合計比例佔所有交易的56.1％。</li>\n	<li>網絡安全公司提供專門解決如：KYC（Know Your Customer，認識你的客戶&mdash;為企業確認客戶身份的程序）和GDPR（General Data Protection Regulation，《一般資料保護規範》，歐盟法律中對個人資料保護和隱私的規範）相關的應用程式和解決方案。</li>\n	<li>補充：「合規性管理」項目佔RegTech總交易額的17.7％，而最大的交易公司為Rubrik（美國的雲端數據管理公司）&mdash;因其提供符合GDPR要求的雲端數據管理服務。</li>\n</ul>\n\n<hr />\n<p><strong>普匯觀點：</strong></p>\n\n<p>在人工智慧與區塊鏈的快速發展下，RegTech更是需要投入發展的領域&mdash;對用戶數據的保護與數據運用的合法性息息相關。</p>\n\n<p>臺灣也在2018年時，成立「數位監理申報機制小組」，運用API（Application Programming Interface，應用程式介面，是多個軟體的中介橋梁&mdash;可以應用於軟體間的呼叫或請求）、資料倉儲、大數據分析、互動式儀表板等科技，來自動產出監理資訊、以利提升監理效能；更於2019年成為全球金融創新聯盟(Global Financial Innovation Network，GFiN)的會員，並參與跨境沙盒試驗與監理科技的工作小組。</p>\n\n<p>因此，可見RegTech不僅是近五年國際間的重點發展項目，臺灣也積極跟進不同產業在監管領域的投入&mdash;特別是在需要高度安全監控的金融產業。</p>', '2021-02-23 02:35:48', '【普匯小學堂】大家都聽過Fintech，那您有聽過Regtech嗎?', 'article', 'publish', '2021-02-23 02:35:48', NULL, 0),
(8271, 1, './upload/article/20210224-1.png', NULL, '<blockquote>\n<p>雖然目前台灣三家網銀業者都未直接揭露自己的系統，但可以從像LINE BANK的工程團隊聯合線上招募會中一窺所使用的相關技術及未來發展方向。</p>\n</blockquote>\n\n<p>&nbsp;</p>\n\n<p><img alt=\"\" src=\"upload/article/20210224-2.png\" style=\"height:579px; width:1458px\" /></p>\n\n<hr />\n<p>App開發團隊表示除了基本的核心服務之外，還會結合Line的信評分數（Line Score）與Line生態系，讓服務更多元。在App開發上採用Design Thinking（設計思維）來探索用戶需求與創意發想，並與資料團隊合作，進行市面上產品研究，蒐集用戶回饋與意見來做設計糾正。目前常用的框架是用MVVM Design pattern。Android版用Kotlin語言，iOS版則以Swift語言進行開發。</p>\n\n<p>Web開發團隊則分為前端、後端、QA三組團隊。前端開發團隊的開發流程會以敏捷式開發為主，從產品提案開始，前端工程師就會全程參與討論，開發過程中以專案管理服務JIRA來控管任務，來進行程式碼審查、Unit test等，內部也有自訂Git Flow規範，最後是透過一套Pipeline流程，將應用部署到相關伺服器，來上線產品。對於前端工程師的技術要求，包括最基礎的ES6+,DOM,CSS preprocessor,RWD,Git，更希望有前端框架開發經驗。由於前後端常有資料交換，因此也要能熟悉Restful API與HTTP Networking。</p>\n\n<p>後端開發團隊負責後端伺服器的程式開發與系統維運，串接核心系統資料來提供給前端UI跟App使用。目前要求聚焦在Java技術，還有Spring Boot、Spring Framework等框架的經驗。在對API開發者的要求上，也需具備像Restful API或API Test Framework的相關經驗。</p>\n\n<p>QA團隊依照手動/自動化測/App/Web測試來分工，使用的測試工具也不一樣。例如：自動化測試中，底層API自動化測試是採用Robot Framework這套架構；App測試則採用Appium這套開源自動化工具；Web測試則用Selenium這套測試工具。</p>\n\n<p><img alt=\"\" src=\"upload/article/20210224-3.png\" style=\"height:381px; width:906px\" /></p>\n\n<p>核心系統團隊開出系統分析師、應用系統架構師、系統開發人員等職缺，共同條件都是需要熟悉Java技術，這顯示Line Bank使用Java作為主要開發語言。</p>\n\n<p>營運支援系統團隊以技術需求簡單分類需要e KYC系統人員、支付應用人員、客服系統人員、風控系統人員、資料倉儲人員、QA品管人員，來負責系統開發與維運。不只Java，連C#、.Net都是主要開發環境。未來IT發展將聚焦生態圈的各種應用場景， AI、Open API、大數據以及區塊鏈等都會應用納入未來方向之一。</p>\n\n<hr />\n<p>普匯觀點</p>\n\n<p>目前台灣三間純網銀皆不約而同的開出技術開發及系統測試維護等相關職缺，並且也都紛紛表示未來想與更多業者合作，提供大眾更便利的生活金融服務。從這些背後的科技發展以及對於未來生活的想像可以感受到越來越多人關注金融科技的議題！LINE BANK公開的招募內容，對於相關科系的學生們也更可以了解未來在實務上會應用到技術需求。而未來如何打造更緊密的金融服務生態，筆者認為會是目前純網銀及其他第三方業者努力發展的方向。</p>', '2021-02-24 03:50:25', '【普匯小學堂】Line Bank純網銀的系統架構', 'article', 'publish', '2021-02-24 03:50:25', NULL, 0),
(8272, 1, './upload/article/20210303-1.png', NULL, '<blockquote>\n<p>過去我們曾在【數字密碼已不再安全，FIDO身份驗證將成為主流】提到，FIDO認證有四種識別方式：安全密鑰、臉部、指紋、語音，本篇會深入討論語音（聲紋）識別：</p>\n</blockquote>\n\n<hr />\n<p><strong>聲紋識別（</strong><strong>Acoustic Fingerprint</strong><strong>）：</strong></p>\n\n<p>聲紋是生物特徵的一種，指從人發出的音頻中提取聲紋特徵，把聲信號轉換為電信號，再由演算法進行識別。人聲中包含：內容、身份、情感、年齡及健康狀況等豐富資訊，且由於聲音的產生需經人體中樞與發音器官復雜的作用而生成，因此<strong>聲紋就如同指紋一樣具有唯一性和獨特性</strong>。</p>\n\n<p><img alt=\"\" src=\"upload/article/20210303-2.png\" style=\"height:639px; width:1135px\" /></p>\n\n<p><strong>聲紋識別的優勢：</strong></p>\n\n<p><strong>目前的應用：</strong></p>\n\n<ul>\n	<li><strong>遠傳</strong>於2020年8月推出「聲紋核資」服務，將聲音做為遠傳的通行密碼，取代傳統一問一答核對身分的方式，成為目前<strong>唯一</strong>提供聲紋辨識服務的電信業者。</li>\n	<li><strong>Apple</strong>將 Siri加入聲紋辨識系統，當使用者在說出「Hey,Siri」時，Siri可透過分析聲紋來辨識是否為主人在呼喊他。</li>\n</ul>\n\n<p><strong>未來展望：</strong></p>\n\n<p>整合聲紋識別與NLP（文字探勘）技術，可應用於會議記錄&mdash;透過聲紋識別後分辨不同的說話人，並針對其交談內容進行理解與問答，將聲紋識別與閱讀理解作作整合、提供更具實用性的應用。而有關NLP系統的內容，我們將在下一篇【NLP文字探勘是什麼?】中進行深度討論。</p>\n\n<hr />\n<p><strong>普匯觀點：</strong></p>\n\n<p>雖然聲紋辨識相較臉部辨識，在設備需求與實用上更具經濟效益，但市面上的產品（手機或平板等載具）品質參差不齊，因此亦可能影響辨識的結果。因此雖然辨識的準確率極高，但未來發展上，仍需<strong>考慮載具的影響</strong>；且若用戶喉嚨發生問題或嚴重沙啞時，系統亦可能無法辦法辨識，因此這些問題都是聲紋辨識發展中需克服的問題，以利往更普及的道路前進。&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n\n<p>先前在【耳朵也能作為生物辨識?】中提到，<strong>耳朵辨識相較臉部辨識具有更高的準確性</strong>，而從本文中又得知&mdash;<strong>聲紋辨識相較耳朵辨識又具有更高的準確性。</strong></p>\n\n<p><strong>普匯</strong>目前除運用OCR（光學字元辨識）作反詐欺偵測外，也規劃與聲紋辨識專業團隊&mdash;共同開發辨識系統，相信在<strong>聲紋辨識相較其他辨識系統，更具獨特性、加密性、方便採集性與辨識快速性的特點</strong>下，能提供用戶更加安全的科技使用環境，同時達到更好的KYC（Know Your Customer，辨別你的顧客）。而有關OCR系統的內容，我們將在下篇【OCR光學字元辨識是什麼?】中進行討論。</p>', '2021-03-03 07:34:09', '【普匯小學堂】聲紋識別 - 聲音也能當作密碼?', 'article', 'publish', '2021-03-03 07:34:27', NULL, 0);
INSERT INTO `media` (`ID`, `media`, `date`, `title`, `link`, `content`, `imgSrc`) VALUES
(1, 'YAHOO!新聞', '2019-04-26', '金融科技創新園區9家廠商加入 企業實驗室首波六大主題公布', 'https://tw.money.yahoo.com/%E9%87%91%E8%9E%8D%E7%A7%91%E6%8A%80%E5%89%B5%E6%96%B0%E5%9C%92%E5%8D%809%E5%AE%B6%E5%BB%A0%E5%95%86%E5%8A%A0%E5%85%A5-%E4%BC%81%E6%A5%AD%E5%AF%A6%E9%A9%97%E5%AE%A4%E9%A6%96%E6%B3%A2%E5%85%AD%E5%A4%A7%E4%B8%BB%E9%A1%8C%E5%85%AC%E5%B8%83-083826449.html', '<p>金融科技創新園區 FinTechSpace 為打造金融科技跨域的共創生態，目前共有 9 家金融機構與科技廠商加入「金融共創」的行列，設立創新實證或創新人才發展的主題實驗室，首波六大主題今天出爐。</p>\n\n<p><br />\n金管會為推動國內金融科技發展，指示金融總會設立國內第一座聚焦金融科技產業生態的實體共創空間 -「金融科技創新園區 FinTechSpace」，並委託資策會負責維運，營運至今已逾半年，已舉辦監理門診、法規健檢、資安健檢、財會輔導、新創講座、國際媒合、產業工坊、研討會等近 200 場活動。</p>\n\n<p><br />\n企業實驗室首波六大主題分別為：華南銀行 -「開放帳戶」實驗室、中國信託 -「跨域資料整合、打擊金融犯罪」實驗室、上海商銀 -「校園創新」實驗室、遠東商銀 -「個人社群銀行 (Bankee)」實驗室、台灣微軟 -「AB Lab (Ai+Blockchain)」與永豐銀行 -「Partner APIs」實驗室，各機構針對企業主軸進行創新議題實證與發展，預計將於年底進行成果發表。</p>\n\n<p><br />\n金融科技創新園區 5 月將新增 9 家新創團隊進駐，包含普匯金融、睿元國際、雲飛傳媒、雲想科技、博歐科技、塞特科技、全城地產、Formosa Financial 及 Caulis，領域涵括借貸、資訊整合平台、支付、身分認證、資訊安全、智能理財與區塊鏈等，除享有實體空間資源外，將藉由一站式的輔導接軌市場，協助新創於初期發展階段奠定堅實的基礎，逐步朝市場落地發展。</p>\n\n<p><br />\n目前金融科技創新園區已經共有 77 家進駐單位，包含來自美國、日本、新加坡、菲律賓、香港及瑞士等國際團隊。</p>', 'a645f762048f5f425d0f0a8486f34612.png'),
(3, '工商時報', '2019-04-26', '金融科技創新園區 六大企業實驗室出爐！', 'https://m.ctee.com.tw/livenews/aj/a95645002019042615564198', '<p>金融科技創新園區（FinTechSpace）26日指出，為打造金融科技跨域之共創生態，目前共有9家金融機構與科技廠商加入「金融共創」的行列，設立創新實證或創新人才發展的主題實驗室，首波主題公布包括：華南銀行「開放帳戶」實驗室、中國信託-「跨域資料整合、打擊金融犯罪」實驗室、上海商銀-「校園創新」實驗室、遠東商銀-「個人社群銀行(Bankee)」實驗室、台灣微軟-「AB&nbsp;Lab&nbsp;(Ai+Blockchain)」與永豐銀行-「Partner&nbsp;APIs」實驗室，各機構針對企業主軸進行創新議題實證與發展，預計將於年底進行成果發表。</p>\n\n<p>&nbsp;</p>\n\n<p>永豐銀行電子金融處副總經理黃品睿指出，以消費支付、生活繳費、身分認證與金融資訊服務，共計四大類推出專屬API組合。Partner&nbsp;APIs透過分類再聚焦，將可加速永豐與企業溝通的腳步，也有助開發多元創新應用。</p>\n\n<p>&nbsp;</p>\n\n<p>永豐銀行表示，若企業主打消費支付應用，如支付業者導入相關API後，永豐銀行客戶可直接綁定帳戶，消費、儲值都能一指搞定，目前每年消費支付類串接的金流已達10億元；若企業有定期性收款需求或提供繳費服務，即可運用生活繳費API，支援多達34項生活帳單及捐款服務。</p>\n\n<p>&nbsp;</p>\n\n<p>若企業需要身分認證功能，例如，證券商於開戶功能導入相關API，民眾開立證券戶後也能直接線上驗證銀行帳戶，免跑分行或券商，省時又便利；若企業經營旅遊或跨國購物相關服務，也可導入金融資訊服務相關API，提供即時金融匯率資訊，讓民眾有更好的使用者體驗。同時，四大類API將持續進化；永豐銀行舉例，身分認證除傳統的金融卡或帳號、密碼外，也將加入生物辨識技術，讓企業與消費者同享安全及便利。</p>\n\n<p>&nbsp;</p>\n\n<p>FinTechSpace5月份將新增9家新創團隊進駐，包含普匯金融、睿元國際、雲飛傳媒、雲想科技、博歐科技、塞特科技、全城地產、Formosa&nbsp;Financial及Caulis，領域涵括借貸、資訊整合平台、支付、身分認證、資訊安全、智能理財與區塊鏈等，除享有實體空間資源外，將藉由一站式的輔導接軌市場，協助新創於初期發展階段奠定堅實的基礎，逐步朝市場落地發展。目前共有77家進駐單位，包含來自美國、日本、新加坡、菲律賓、香港及瑞士等國際團隊。</p>', 'logo300_90.png'),
(4, '財訊新聞', '2019-04-26', '金融科技創新園區9家廠商加入 企業實驗室首波六大主題公布', 'https://www.wealth.com.tw/home/articles/20567', '<p>金融科技創新園區&nbsp;FinTechSpace&nbsp;為打造金融科技跨域的共創生態，目前共有9&nbsp;家金融機構與科技廠商加入「金融共創」的行列，設立創新實證或創新人才發展的主題實驗室，首波六大主題今天出爐。</p>\n\n<p>&nbsp;</p>\n\n<p>金管會為推動國內金融科技發展，指示金融總會設立國內第一座聚焦金融科技產業生態的實體共創空間&nbsp;-「金融科技創新園區&nbsp;FinTechSpace」，並委託資策會負責維運，營運至今已逾半年，已舉辦監理門診、法規健檢、資安健檢、財會輔導、新創講座、國際媒合、產業工坊、研討會等近&nbsp;200&nbsp;場活動。</p>\n\n<p>&nbsp;</p>\n\n<p>企業實驗室首波六大主題分別為：華南銀行&nbsp;-「開放帳戶」實驗室、中國信託&nbsp;-「跨域資料整合、打擊金融犯罪」實驗室、上海商銀&nbsp;-「校園創新」實驗室、遠東商銀&nbsp;-「個人社群銀行&nbsp;(Bankee)」實驗室、台灣微軟&nbsp;-「AB&nbsp;Lab&nbsp;(Ai+Blockchain)」與永豐銀行&nbsp;-「Partner&nbsp;APIs」實驗室，各機構針對企業主軸進行創新議題實證與發展，預計將於年底進行成果發表。</p>\n\n<p>&nbsp;</p>\n\n<p>金融科技創新園區&nbsp;5&nbsp;月將新增&nbsp;9&nbsp;家新創團隊進駐，包含普匯金融、睿元國際、雲飛傳媒、雲想科技、博歐科技、塞特科技、全城地產、Formosa&nbsp;Financial&nbsp;及&nbsp;Caulis，領域涵括借貸、資訊整合平台、支付、身分認證、資訊安全、智能理財與區塊鏈等，除享有實體空間資源外，將藉由一站式的輔導接軌市場，協助新創於初期發展階段奠定堅實的基礎，逐步朝市場落地發展。</p>\n\n<p>&nbsp;</p>\n\n<p>目前金融科技創新園區已經共有&nbsp;77&nbsp;家進駐單位，包含來自美國、日本、新加坡、菲律賓、香港及瑞士等國際團隊。</p>\n\n<p>&nbsp;</p>\n\n<p>（本文由「鉅亨網」授權轉載）</p>', 'logo-red.png'),
(5, '鉅亨網', '2019-01-11', '普匯金融科技 CEO：觀念、態度、努力 決定你的人生', 'https://news.cnyes.com/news/id/4267004', '<p>Arthur&nbsp;Yao&nbsp;姚木川，於兩岸金融機構服務超過二十年，曾任大眾（現併入元大）、台新、復華（今元大）、中租等集團之專業經理人，期間亦陸續投資服飾、餐飲、通訊、進出口、演藝、唱片、娛樂等產業，近年毅然決然卸下南粵銀行總經理的職位創業，放棄千萬年薪投入台灣金融科技發展，現為&nbsp;普匯金融科技&nbsp;CEO／亞設國際投資&nbsp;CEO。</p>\n\n<p>Q：為什麼想創立普匯？</p>\n\n<p>顧名思義，普匯希望能夠幫助更多銀行服務不到的客戶。</p>\n\n<p>加上科技產業正在快速發展，結合本身豐富的金融歷練，決定投入&nbsp;Fintech&nbsp;創業，自我挑戰、讓自己活得更精彩！</p>\n\n<p>&nbsp;</p>\n\n<p>Q：對於台灣金融科技發展的看法？</p>\n\n<p>台灣曾歷經數次金融風暴，應該有足夠的基礎與韌性發展&nbsp;Fintech。如果能一改台灣金融產業長年累積的保守風氣，搭配法規上的調整，利用科技快速推進，台灣的&nbsp;Fintech&nbsp;會發展得很好。</p>\n\n<p>&nbsp;</p>\n\n<p>Q：為何在金融科技產業之中選擇了&nbsp;P2P&nbsp;Lending？</p>\n\n<p>由於過往在金融機構主要負責法金企業放款項目，不只對相關運作模式與規範有一定了解，更能確保普匯不存在吸金、觸法等問題。</p>\n\n<p>另外，借貸衍生支付、支付衍生其他金融行為；未來也會繼續探尋&nbsp;Fintech&nbsp;中的其他可能性，只是目前要先打穩基礎，才能向上發展。</p>\n\n<p>&nbsp;</p>\n\n<p>Q：如何同時兼顧多項投資事業？</p>\n\n<p>睡眠時間比別人短，維持正常的生活起居，養成運動習慣訓練體力。</p>\n\n<p>懂得自我約束，搭配時間管理，就能達成目標。</p>\n\n<p>&nbsp;</p>\n\n<p>Q：擔任高階主管、甚至創業，需要什麼特質？</p>\n\n<p>比別人更努力，比別人更細心，保持虛心不斷學習。</p>\n\n<p>心態要正面積極、樂觀進取，做事要捨我其誰、當仁不讓。</p>\n\n<p>&nbsp;</p>\n\n<p>Q：給予想進入金融業的年輕人一些建議？</p>\n\n<p>若想進入金融業，需要花額外的時間鑽研相關知識，加上產業特性，圈子較為封閉、狹小，因此一旦選擇了金融業，就要下定決心。</p>\n\n<p>至於金融科技業，則得拋棄金融體制內的固有想法，若能畢業後直接進入&nbsp;Fintech，也許可以學習得更快、更沒有包袱。</p>\n\n<p>但總體來說，建議年輕人一定要有夢想、有野心，勇於挑戰、衝刺、冒險，在各行各業都會成功。</p>', 'logo-cn-yes-1.png'),
(6, '中時電子報', '2018-08-08', '小資金實現大夢想　InFlux普匯P2P借貸平台實現3小時核貸、1小時放款', 'https://act.chinatimes.com/market/content.aspx?AdID=6585&chdtv', '<p>InFlux普匯推出P2P借貸平台，展現金融科技更創新彈性的金融服務</p>\n\n<p>金融科技Fintech被視為新一波產業革命應用，為了解決企業融資、借貸的不透明及不便，讓企業順利獲得資金並拓展業務，InFlux普匯金融科技日前推出結合｢金融專業｣跟｢高端科技｣的P2P&nbsp;Lending服務模式：全台第一個完全線上貸款，無人化融資平台。藉由架構雙向投資，提供便捷、安全與隱私的流程，創造新型態的借貸金融服務。</p>\n\n<p>InFlux普匯金融科技於2018年8月3號，假臺北文創正式對外公開這項服務。執行長姚木川指出「普匯金融願景目標是利用AI、區塊鏈等科技應用，重塑金融產業，建立一個真正有效能的透明金融平台。」</p>\n\n<p>&nbsp;</p>\n\n<p>Fintech投資創新高　P2P借貸平台翻轉傳統金融</p>\n\n<p>傳統銀行核貸機制，學生、社會新鮮人及青年創業家，由於缺乏與銀行往來資歷，往往被評估為違約風險高族群。普匯認為，當前年輕族群大多無法取得借貸商品，但透過InFlux借貸平台，投資人可提供資金助人圓夢，還可獲得合理報酬。對借貸方來說，受惠彈性快速的資金調度，可望為事業獲得成長機會。</p>\n\n<p>&nbsp;</p>\n\n<p>3小時核貸、1小時放款　實現所有借貸人夢想</p>\n\n<p>姚木川表示，InFlux普匯金融科技不是銀行，而是提供互信互利的平台，將借款人與投資者聯繫起來，共創雙贏機會。平台是由具備風險管控模組、金融產品設計經驗的金融團隊組成，目的建立互利互信制度，創造有溫度的金融服務體驗。InFlux平台最大特色是申貸過程在網路線上即可辦理，無需與人聯繫保護個人隱私資訊。這項產品的背後另一推手是TRIPLE（台灣創新快製媒合中心)，由工研院產業服務中心執行的政府計畫。平台更使用生物系統辨識技術，無需人工核貸，同時導入人工智能信評機制，手機讓申貸資金便利，亦可成為台灣培養新創事業競爭力的一股活水。</p>', 'logo-chinatimes2019-1200x635.png'),
(7, '今日新聞', '2018-08-07', 'AI自動核貸無人化融資平台　打造金融科技新型態', 'https://www.nownews.com/news/20180807/2798010/', '<p>金融科技Fintech被視為新一波產業革命應用，為了解決企業融資、借貸的不透明及不便，讓企業順利獲得資金並拓展業務，InFlux普匯金融科技日前推出結合｢金融專業｣跟｢高端科技｣的P2P&nbsp;Lending服務模式：全台第一個完全線上貸款，無人化融資平台。藉由架構雙向投資，提供便捷、安全與隱私的流程，創造新型態的借貸金融服務。</p>\n\n<p>&nbsp;</p>\n\n<p>InFlux普匯金融科技於2018年8月3號，假臺北文創正式對外公開這項服務。執行長姚木川指出「普匯金融願景目標是利用AI、區塊鏈等科技應用，重塑金融產業，建立一個真正有效能的透明金融平台。」</p>\n\n<p>&nbsp;</p>\n\n<p>傳統銀行核貸機制，學生、社會新鮮人及青年創業家，由於缺乏與銀行往來資歷，往往被評估為違約風險高族群。普匯認為，當前年輕族群大多無法取得借貸商品，但透過InFlux借貸平台，投資人可提供資金助人圓夢，還可獲得合理報酬。對借貸方來說，受惠彈性快速的資金調度，可望為事業獲得成長機會。</p>\n\n<p>&nbsp;</p>\n\n<p>姚木川表示，InFlux普匯金融科技不是銀行，而是提供互信互利的平台，將借款人與投資者聯繫起來，共創雙贏機會。平台是由具備風險管控模組、金融產品設計經驗的金融團隊組成，目的建立互利互信制度，創造有溫度的金融服務體驗。</p>\n\n<p>&nbsp;</p>\n\n<p>InFlux平台最大特色是申貸過程在網路線上即可辦理，無需與人聯繫保護個人隱私資訊。這項產品的背後另一推手是TRIPLE（台灣創新快製媒合中心），由工研院產業服務中心執行的政府計畫。平台更使用生物系統辨識技術，無需人工核貸，同時導入人工智能信評機制，手機讓申貸資金便利，亦可成為台灣培養新創事業競爭力的一股活水。</p>', 'Now_News_logo.png'),
(8, '蕃薯藤', '2018-08-03', '關注台灣年輕族群　InFlux普匯推出有溫度的P2P借貸平台', 'http://n.yam.com/Article/20180803346949', '<p>【台北訊】金融科技Fintech被視為新一波產業革命應用，為了解決企業融資、借貸的不透明及不便，讓企業順利獲得資金並拓展業務，InFlux普匯金融科技日前推出結合｢金融專業｣跟｢高端科技｣的P2P&nbsp;Lending服務模式：全台第一個完全線上貸款，無人化融資平台。藉由架構雙向投資，提供便捷、安全與隱私的流程，創造新型態的借貸金融服務。</p>\n\n<p>&nbsp;</p>\n\n<p>InFlux普匯金融科技於2018年8月3號，假臺北文創正式對外公開這項服務。執行長姚木川指出「普匯金融願景目標是利用AI、區塊鏈等科技應用，重塑金融產業，建立一個真正有效能的透明金融平台。」</p>\n\n<p>&nbsp;</p>\n\n<p>Fintech投資創新高　P2P借貸平台翻轉傳統金融</p>\n\n<p>傳統銀行核貸機制，學生、社會新鮮人及青年創業家，由於缺乏與銀行往來資歷，往往被評估為違約風險高族群。普匯認為，當前年輕族群大多無法取得借貸商品，但透過InFlux借貸平台，投資人可提供資金助人圓夢，還可獲得合理報酬。對借貸方來說，受惠彈性快速的資金調度，可望為事業獲得成長機會。</p>\n\n<p>&nbsp;</p>\n\n<p>3小時核貸、1小時放款　實現所有借貸人夢想</p>\n\n<p>姚木川表示，InFlux普匯金融科技不是銀行，而是提供互信互利的平台，將借款人與投資者聯繫起來，共創雙贏機會。平台是由具備風險管控模組、金融產品設計經驗的金融團隊組成，目的建立互利互信制度，創造有溫度的金融服務體驗。InFlux平台最大特色是申貸過程在網路線上即可辦理，無需與人聯繫保護個人隱私資訊。這項產品的背後另一推手是TRIPLE（台灣創新快製媒合中心)，由工研院產業服務中心執行的政府計畫。平台更使用生物系統辨識技術，無需人工核貸，同時導入人工智能信評機制，手機讓申貸資金便利，亦可成為台灣培養新創事業競爭力的一股活水。</p>', '1JHY1QOLYPF0.png');
